'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import React, { useState } from 'react';
import { useForm, useFieldArray } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { Button } from '@ghxstship/ui/components/Button';
import { Input } from '@ghxstship/ui/components/Input';
import { Textarea } from '@ghxstship/ui/components/Textarea';
import { Select } from '@ghxstship/ui/components/Select';
import { Badge } from '@ghxstship/ui/components/Badge';
import { Card } from '@ghxstship/ui/components/Card';
import { useToastContext } from '@ghxstship/ui/components/Toast';
import { ApprovalsService } from './lib/approvalsService';
import { CreateApprovalPolicySchema, type CreateApprovalPolicy } from './types';

interface CreatePolicyClientProps {
  open: boolean;
  onClose: () => void;
  onSuccess: () => void;
  organizationId: string;
  initialData?: Partial<CreateApprovalPolicy>;
}

export function CreatePolicyClient({
  open,
  onClose,
  onSuccess,
  organizationId,
  initialData
}: CreatePolicyClientProps) {
  const [loading, setLoading] = useState(false);
  const [currentStep, setCurrentStep] = useState(1);
  const { toast } = useToastContext();
  const approvalsService = new ApprovalsService();

  const form = useForm<CreateApprovalPolicy>({
    resolver: zodResolver(CreateApprovalPolicySchema),
    defaultValues: {
      organization_id: organizationId,
      name: '',
      description: '',
      conditions: {},
      approval_steps: [
        {
          step: 1,
          role: 'manager',
          description: 'Manager approval',
          conditions: {}
        }
      ],
      is_active: true,
      ...initialData
    }
  });

  const { fields, append, remove } = useFieldArray({
    control: form.control,
    name: 'approval_steps'
  });

  // Add new approval step
  const addApprovalStep = () => {
    const nextStep = fields.length + 1;
    append({
      step: nextStep,
      role: 'admin',
      description: `Step ${nextStep} approval`,
      conditions: {}
    });
  };

  // Remove approval step
  const removeApprovalStep = (index: number) => {
    if (fields.length > 1) {
      remove(index);
      // Reorder remaining steps
      const currentSteps = form.getValues('approval_steps');
      currentSteps.forEach((step, idx) => {
        form.setValue(`approval_steps.${idx}.step`, idx + 1);
      });
    }
  };

  // Handle form submission
  const onSubmit = async (data: CreateApprovalPolicy) => {
    setLoading(true);
    try {
      const { data: newPolicy, error } = await approvalsService.createPolicy(data);

      if (error) {
        toast.error('Failed to create policy', error);
        return;
      }

      toast.success('Approval policy created successfully');
      form.reset();
      onSuccess();
    } catch (error) {
      console.error('Error creating policy:', error);
      toast.error('Failed to create policy');
    } finally {
      setLoading(false);
    }
  };

  if (!open) return null;

  return (
    <div className="fixed inset-0 z-50 overflow-hidden">
      <div className="absolute inset-0 bg-black/50" onClick={onClose} />
      <div className="absolute right-0 top-0 h-full w-full max-w-4xl bg-background shadow-xl">
        <div className="flex h-full flex-col">
          {/* Header */}
          <div className="flex items-center justify-between border-b p-6">
            <div>
              <h2 className="text-lg font-semibold">Create Approval Policy</h2>
              <p className="text-sm text-muted-foreground">
                Configure approval workflows for procurement requests
              </p>
            </div>
            <Button variant="ghost" size="sm" onClick={onClose}>
              <X className="h-4 w-4" />
            </Button>
          </div>

          {/* Progress Steps */}
          <div className="border-b p-6">
            <div className="flex items-center space-x-4">
              {[
                { step: 1, title: 'Basic Info' },
                { step: 2, title: 'Conditions' },
                { step: 3, title: 'Approval Steps' },
                { step: 4, title: 'Review' }
              ].map(({ step, title }) => (
                <div key={step} className="flex items-center">
                  <div
                    className={`flex h-8 w-8 items-center justify-center rounded-full text-sm font-medium ${
                      currentStep >= step
                        ? 'bg-primary text-primary-foreground'
                        : 'bg-muted text-muted-foreground'
                    }`}
                  >
                    {step}
                  </div>
                  <span className="ml-2 text-sm font-medium">{title}</span>
                  {step < 4 && <div className="ml-4 h-px w-8 bg-border" />}
                </div>
              ))}
            </div>
          </div>

          {/* Content */}
          <div className="flex-1 overflow-y-auto p-6">
            <form className="space-y-6">
              {/* Step 1: Basic Information */}
              {currentStep === 1 && (
                <div className="space-y-6">
                  <div>
                    <label className="text-sm font-medium">Policy Name *</label>
                    <Input
                      {...form.register('name')}
                      placeholder="Enter policy name"
                      error={form.formState.errors.name?.message}
                    />
                  </div>

                  <div>
                    <label className="text-sm font-medium">Description</label>
                    <Textarea
                      {...form.register('description')}
                      placeholder="Describe when this policy applies"
                      rows={3}
                    />
                  </div>

                  <div className="flex items-center space-x-2">
                    <input
                      type="checkbox"
                      {...form.register('is_active')}
                      className="rounded border-border"
                    />
                    <label className="text-sm font-medium">Active Policy</label>
                  </div>
                </div>
              )}

              {/* Step 2: Conditions */}
              {currentStep === 2 && (
                <div className="space-y-6">
                  <div>
                    <h3 className="text-lg font-medium mb-4">Policy Conditions</h3>
                    <p className="text-sm text-muted-foreground mb-6">
                      Define when this approval policy should be applied to procurement requests.
                    </p>
                  </div>

                  <Card className="p-4">
                    <h4 className="font-medium mb-4">Amount Threshold</h4>
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <label className="text-sm font-medium">Minimum Amount</label>
                        <Input
                          type="number"
                          min="0"
                          step="0.01"
                          placeholder="0.00"
                          onChange={(e) => {
                            const conditions = form.getValues('conditions') || {};
                            conditions.min_amount = parseFloat(e.target.value) || 0;
                            form.setValue('conditions', conditions);
                          }}
                        />
                      </div>
                      <div>
                        <label className="text-sm font-medium">Maximum Amount</label>
                        <Input
                          type="number"
                          min="0"
                          step="0.01"
                          placeholder="No limit"
                          onChange={(e) => {
                            const conditions = form.getValues('conditions') || {};
                            conditions.max_amount = parseFloat(e.target.value) || undefined;
                            form.setValue('conditions', conditions);
                          }}
                        />
                      </div>
                    </div>
                  </Card>

                  <Card className="p-4">
                    <h4 className="font-medium mb-4">Categories</h4>
                    <div className="space-y-2">
                      {['equipment', 'supplies', 'services', 'materials', 'software', 'maintenance'].map(category => (
                        <div key={category} className="flex items-center space-x-2">
                          <input
                            type="checkbox"
                            className="rounded border-border"
                            onChange={(e) => {
                              const conditions = form.getValues('conditions') || {};
                              const categories = conditions.categories || [];
                              if (e.target.checked) {
                                categories.push(category);
                              } else {
                                const index = categories.indexOf(category);
                                if (index > -1) categories.splice(index, 1);
                              }
                              conditions.categories = categories;
                              form.setValue('conditions', conditions);
                            }}
                          />
                          <label className="text-sm capitalize">{category}</label>
                        </div>
                      ))}
                    </div>
                  </Card>

                  <Card className="p-4">
                    <h4 className="font-medium mb-4">Departments</h4>
                    <Input
                      placeholder="Enter departments (comma-separated)"
                      onChange={(e) => {
                        const conditions = form.getValues('conditions') || {};
                        conditions.departments = e.target.value.split(',').map(d => d.trim()).filter(Boolean);
                        form.setValue('conditions', conditions);
                      }}
                    />
                  </Card>
                </div>
              )}

              {/* Step 3: Approval Steps */}
              {currentStep === 3 && (
                <div className="space-y-6">
                  <div className="flex items-center justify-between">
                    <h3 className="text-lg font-medium">Approval Steps</h3>
                    <Button type="button" variant="outline" onClick={addApprovalStep}>
                      <Plus className="h-4 w-4 mr-2" />
                      Add Step
                    </Button>
                  </div>

                  {fields.map((field, index) => (
                    <Card key={field.id} className="p-4">
                      <div className="flex items-center justify-between mb-4">
                        <h4 className="font-medium">Step {index + 1}</h4>
                        {fields.length > 1 && (
                          <Button
                            type="button"
                            variant="ghost"
                            size="sm"
                            onClick={() => removeApprovalStep(index)}
                          >
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        )}
                      </div>

                      <div className="grid grid-cols-1 gap-4 md:grid-cols-2">
                        <div>
                          <label className="text-sm font-medium">Approver Role *</label>
                          <Select {...form.register(`approval_steps.${index}.role`)}>
                            <option value="manager">Manager</option>
                            <option value="admin">Admin</option>
                            <option value="owner">Owner</option>
                            <option value="finance">Finance Team</option>
                            <option value="procurement">Procurement Team</option>
                          </Select>
                        </div>

                        <div>
                          <label className="text-sm font-medium">Step Order</label>
                          <Input
                            type="number"
                            min="1"
                            value={index + 1}
                            disabled
                            className="bg-muted"
                          />
                        </div>

                        <div className="md:col-span-2">
                          <label className="text-sm font-medium">Description *</label>
                          <Input
                            {...form.register(`approval_steps.${index}.description`)}
                            placeholder="Describe this approval step"
                          />
                        </div>
                      </div>

                      {/* Step-specific conditions */}
                      <div className="mt-4 p-3 bg-muted rounded">
                        <h5 className="text-sm font-medium mb-2">Step Conditions (Optional)</h5>
                        <div className="grid grid-cols-2 gap-4">
                          <div>
                            <label className="text-xs text-muted-foreground">Min Amount for this step</label>
                            <Input
                              type="number"
                              min="0"
                              step="0.01"
                              placeholder="0.00"
                              onChange={(e) => {
                                const currentSteps = form.getValues('approval_steps');
                                currentSteps[index].conditions = {
                                  ...currentSteps[index].conditions,
                                  min_amount: parseFloat(e.target.value) || 0
                                };
                                form.setValue('approval_steps', currentSteps);
                              }}
                            />
                          </div>
                          <div>
                            <label className="text-xs text-muted-foreground">Required</label>
                            <div className="flex items-center mt-2">
                              <input
                                type="checkbox"
                                defaultChecked={true}
                                className="rounded border-border"
                                onChange={(e) => {
                                  const currentSteps = form.getValues('approval_steps');
                                  currentSteps[index].conditions = {
                                    ...currentSteps[index].conditions,
                                    required: e.target.checked
                                  };
                                  form.setValue('approval_steps', currentSteps);
                                }}
                              />
                              <span className="ml-2 text-xs">Required step</span>
                            </div>
                          </div>
                        </div>
                      </div>
                    </Card>
                  ))}
                </div>
              )}

              {/* Step 4: Review */}
              {currentStep === 4 && (
                <div className="space-y-6">
                  <h3 className="text-lg font-medium">Review Policy</h3>

                  {/* Basic Info Summary */}
                  <Card className="p-4">
                    <h4 className="font-medium mb-4">Basic Information</h4>
                    <div className="grid grid-cols-2 gap-4 text-sm">
                      <div>
                        <span className="text-muted-foreground">Name:</span>
                        <p className="font-medium">{form.watch('name')}</p>
                      </div>
                      <div>
                        <span className="text-muted-foreground">Status:</span>
                        <Badge variant={form.watch('is_active') ? 'success' : 'secondary'}>
                          {form.watch('is_active') ? 'Active' : 'Inactive'}
                        </Badge>
                      </div>
                    </div>
                    {form.watch('description') && (
                      <div className="mt-4">
                        <span className="text-muted-foreground">Description:</span>
                        <p className="text-sm mt-1">{form.watch('description')}</p>
                      </div>
                    )}
                  </Card>

                  {/* Conditions Summary */}
                  <Card className="p-4">
                    <h4 className="font-medium mb-4">Conditions</h4>
                    <div className="space-y-2 text-sm">
                      {form.watch('conditions.min_amount') && (
                        <p>â€¢ Minimum amount: ${form.watch('conditions.min_amount')}</p>
                      )}
                      {form.watch('conditions.max_amount') && (
                        <p>â€¢ Maximum amount: ${form.watch('conditions.max_amount')}</p>
                      )}
                      {form.watch('conditions.categories')?.length > 0 && (
                        <p>â€¢ Categories: {form.watch('conditions.categories').join(', ')}</p>
                      )}
                      {form.watch('conditions.departments')?.length > 0 && (
                        <p>â€¢ Departments: {form.watch('conditions.departments').join(', ')}</p>
                      )}
                    </div>
                  </Card>

                  {/* Approval Steps Summary */}
                  <Card className="p-4">
                    <h4 className="font-medium mb-4">Approval Workflow ({fields.length} steps)</h4>
                    <div className="space-y-3">
                      {form.watch('approval_steps')?.map((step, index) => (
                        <div key={index} className="flex items-center justify-between p-3 bg-muted rounded">
                          <div>
                            <p className="font-medium">Step {step.step}: {step.description}</p>
                            <p className="text-sm text-muted-foreground">
                              Role: {step.role} â€¢ 
                              {step.conditions?.min_amount && ` Min: $${step.conditions.min_amount} â€¢`}
                              {step.conditions?.required !== false ? ' Required' : ' Optional'}
                            </p>
                          </div>
                          <Badge variant="outline">
                            {step.role}
                          </Badge>
                        </div>
                      ))}
                    </div>
                  </Card>
                </div>
              )}
            </form>
          </div>

          {/* Footer */}
          <div className="border-t p-6">
            <div className="flex items-center justify-between">
              <div className="flex space-x-2">
                {currentStep > 1 && (
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => setCurrentStep(currentStep - 1)}
                  >
                    Previous
                  </Button>
                )}
              </div>

              <div className="flex space-x-2">
                {currentStep < 4 ? (
                  <Button
                    type="button"
                    onClick={() => setCurrentStep(currentStep + 1)}
                  >
                    Next
                  </Button>
                ) : (
                  <Button
                    type="button"
                    onClick={form.handleSubmit(onSubmit)}
                    disabled={loading}
                    className="flex items-center gap-2"
                  >
                    <Save className="h-4 w-4" />
                    Create Policy
                  </Button>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import { Plus, BookOpen, Search, Filter, Grid, List, Columns, Calendar, BarChart3, Download, Upload, Trash2, Edit, Eye, RefreshCw, Settings, X } from "lucide-react";
import { useState, useEffect, useCallback } from 'react';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { 
  Button, 
  UnifiedInput, 
  Card, 
  Badge, 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Checkbox,
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
} from '@ghxstship/ui';

// Import types and views
import type { 
  CatalogItem, 
  CatalogFilters, 
  CatalogSort, 
  CatalogViewMode, 
  CatalogStats,
  BulkAction 
} from './types';
import { CatalogService } from './lib/catalogService';
import CatalogGridView from './views/CatalogGridView';
import CatalogListView from './views/CatalogListView';
import CatalogTableView from './views/CatalogTableView';
import CatalogKanbanView from './views/CatalogKanbanView';
import CatalogDashboardView from './views/CatalogDashboardView';
import CreateCatalogItemClient from './CreateCatalogItemClient';

export default function CatalogClient({ orgId }: { orgId: string }) {
  const t = useTranslations('procurement');
  const sb = createBrowserClient();

  // State management
  const [catalogItems, setCatalogItems] = useState<CatalogItem[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedItems, setSelectedItems] = useState<string[]>([]);
  const [viewMode, setViewMode] = useState<CatalogViewMode>('grid');
  const [showFilters, setShowFilters] = useState(false);
  const [stats, setStats] = useState<CatalogStats | null>(null);

  // Filters and sorting
  const [filters, setFilters] = useState<CatalogFilters>({
    search: '',
    type: 'all',
    status: 'all',
  });
  const [sort, setSort] = useState<CatalogSort>({
    field: 'created_at',
    direction: 'desc',
  });

  // Pagination
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 50,
    total: 0,
    hasMore: false,
  });

  const catalogService = new CatalogService();

  // Load catalog items with filters and pagination
  const loadCatalogItems = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      const result = await catalogService.getCatalogItems(
        orgId,
        filters,
        sort,
        pagination.page,
        pagination.limit
      );

      setCatalogItems(result.items);
      setPagination(prev => ({
        ...prev,
        total: result.total,
        hasMore: result.hasMore,
      }));

      // Load stats if on dashboard view
      if (viewMode === 'dashboard') {
        const statsResult = await catalogService.getCatalogStats(orgId);
        setStats(statsResult);
      }
    } catch (error) {
      console.error('Error loading catalog items:', error);
      setError(error instanceof Error ? error.message : 'Failed to load catalog items');
    } finally {
      setLoading(false);
    }
  }, [orgId, filters, sort, pagination.page, pagination.limit, viewMode]);

  // Load data on mount and when dependencies change
  useEffect(() => {
    loadCatalogItems();
  }, [loadCatalogItems]);

  // Handle filter changes
  const handleFilterChange = useCallback((newFilters: Partial<CatalogFilters>) => {
    setFilters(prev => ({ ...prev, ...newFilters }));
    setPagination(prev => ({ ...prev, page: 1 })); // Reset to first page
  }, []);

  // Handle sort changes
  const handleSortChange = useCallback((newSort: CatalogSort) => {
    setSort(newSort);
    setPagination(prev => ({ ...prev, page: 1 })); // Reset to first page
  }, []);

  // Handle view mode changes
  const handleViewModeChange = useCallback((mode: CatalogViewMode) => {
    setViewMode(mode);
    setSelectedItems([]); // Clear selection when changing views
  }, []);

  // Handle item selection
  const handleSelectionChange = useCallback((itemIds: string[]) => {
    setSelectedItems(itemIds);
  }, []);

  // Handle individual item actions
  const handleItemClick = useCallback((item: CatalogItem) => {
    // Navigate to item detail or open drawer
    console.log('Item clicked:', item);
  }, []);

  const handleEditItem = useCallback((item: CatalogItem) => {
    // Open edit drawer
    console.log('Edit item:', item);
  }, []);

  const handleDeleteItem = useCallback((item: CatalogItem) => {
    // Show confirmation dialog and delete
    console.log('Delete item:', item);
  }, []);

  const handleViewItem = useCallback((item: CatalogItem) => {
    // Open view drawer
    console.log('View item:', item);
  }, []);

  // Handle bulk actions
  const handleBulkAction = useCallback(async (action: BulkAction) => {
    try {
      setLoading(true);
      await catalogService.bulkUpdateCatalogItems(orgId, action, 'current-user-id');
      await loadCatalogItems(); // Reload data
      setSelectedItems([]); // Clear selection
    } catch (error) {
      console.error('Bulk action failed:', error);
      setError(error instanceof Error ? error.message : 'Bulk action failed');
    } finally {
      setLoading(false);
    }
  }, [orgId, loadCatalogItems]);

  // Handle status change (for Kanban view)
  const handleStatusChange = useCallback(async (item: CatalogItem, newStatus: string) => {
    try {
      await catalogService.updateCatalogItem(
        orgId,
        item.id,
        item.type,
        { status: newStatus as unknown },
        'current-user-id'
      );
      await loadCatalogItems(); // Reload data
    } catch (error) {
      console.error('Status change failed:', error);
      setError(error instanceof Error ? error.message : 'Status change failed');
    }
  }, [orgId, loadCatalogItems]);

  // Render view based on current mode
  const renderView = () => {
    const commonProps = {
      items: catalogItems,
      loading,
      selectedItems,
      onSelectionChange: handleSelectionChange,
      onItemClick: handleItemClick,
      onEditItem: handleEditItem,
      onDeleteItem: handleDeleteItem,
      onViewItem: handleViewItem,
    };

    switch (viewMode) {
      case 'grid':
        return <CatalogGridView {...commonProps} />;
      case 'list':
        return <CatalogListView {...commonProps} />;
      case 'table':
        return (
          <CatalogTableView
            {...commonProps}
            sort={sort}
            onSortChange={handleSortChange}
          />
        );
      case 'kanban':
        return (
          <CatalogKanbanView
            {...commonProps}
            onStatusChange={handleStatusChange}
          />
        );
      case 'dashboard':
        return (
          <CatalogDashboardView
            items={catalogItems}
            loading={loading}
            stats={stats || undefined}
            onItemClick={handleItemClick}
          />
        );
      default:
        return <CatalogGridView {...commonProps} />;
    }
  };

  if (error) {
    return (
      <Card className="p-xl text-center">
        <div className="text-destructive mb-md">
          <BookOpen className="h-12 w-12 mx-auto mb-md opacity-50" />
          <h3 className="text-lg font-medium mb-sm">Error Loading Catalog</h3>
          <p className="text-sm">{error}</p>
        </div>
        <Button onClick={loadCatalogItems} variant="outline">
          <RefreshCw className="h-4 w-4 mr-sm" />
          Try Again
        </Button>
      </Card>
    );
  }

  return (
    <div className="space-y-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-sm">
          <BookOpen className="h-5 w-5" />
          <h3 className="text-lg font-semibold">Procurement Catalog</h3>
          <Badge variant="secondary">
            {catalogItems.length} item{catalogItems.length !== 1 ? 's' : ''}
          </Badge>
        </div>
        <div className="flex items-center gap-sm">
          <CreateCatalogItemClient orgId={orgId} onItemCreated={loadCatalogItems} />
          <Button variant="outline" size="sm">
            <Upload className="h-4 w-4 mr-sm" />
            Import
          </Button>
          <Button variant="outline" size="sm">
            <Download className="h-4 w-4 mr-sm" />
            Export
          </Button>
        </div>
      </div>

      {/* Filters and Search */}
      <Card className="p-md">
        <div className="flex flex-col lg:flex-row gap-md">
          {/* Search */}
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <UnifiedInput
              placeholder="Search catalog items..."
              value={filters.search || ''}
              onChange={(e) => handleFilterChange({ search: e.target.value })}
              className="pl-10"
            />
          </div>

          {/* Type Filter */}
          <Select
            value={filters.type || 'all'}
            onValueChange={(value) => handleFilterChange({ type: value as unknown })}
          >
            <SelectTrigger className="w-[140px]">
              <SelectValue placeholder="All Types" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Types</SelectItem>
              <SelectItem value="product">Products</SelectItem>
              <SelectItem value="service">Services</SelectItem>
            </SelectContent>
          </Select>

          {/* Status Filter */}
          <Select
            value={filters.status || 'all'}
            onValueChange={(value) => handleFilterChange({ status: value as unknown })}
          >
            <SelectTrigger className="w-[140px]">
              <SelectValue placeholder="All Status" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Status</SelectItem>
              <SelectItem value="active">Active</SelectItem>
              <SelectItem value="inactive">Inactive</SelectItem>
              <SelectItem value="discontinued">Discontinued</SelectItem>
            </SelectContent>
          </Select>

          {/* Advanced Filters Toggle */}
          <Button
            variant="outline"
            size="sm"
            onClick={() => setShowFilters(!showFilters)}
          >
            <Filter className="h-4 w-4 mr-sm" />
            Filters
          </Button>
        </div>

        {/* Advanced Filters */}
        {showFilters && (
          <div className="mt-md pt-md border-t border-border">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-md">
              {/* Category Filter */}
              <div>
                <label className="text-sm font-medium mb-sm block">Category</label>
                <UnifiedInput
                  placeholder="Filter by category"
                  value={filters.category || ''}
                  onChange={(e) => handleFilterChange({ category: e.target.value })}
                />
              </div>

              {/* Supplier Filter */}
              <div>
                <label className="text-sm font-medium mb-sm block">Supplier</label>
                <UnifiedInput
                  placeholder="Filter by supplier"
                  value={filters.supplier || ''}
                  onChange={(e) => handleFilterChange({ supplier: e.target.value })}
                />
              </div>

              {/* Price Range */}
              <div>
                <label className="text-sm font-medium mb-sm block">Price Range</label>
                <div className="flex gap-sm">
                  <UnifiedInput
                    type="number"
                    placeholder="Min"
                    value={filters.priceRange?.min || ''}
                    onChange={(e) => handleFilterChange({ 
                      priceRange: { 
                        ...filters.priceRange, 
                        min: e.target.value ? Number(e.target.value) : undefined 
                      } 
                    })}
                  />
                  <UnifiedInput
                    type="number"
                    placeholder="Max"
                    value={filters.priceRange?.max || ''}
                    onChange={(e) => handleFilterChange({ 
                      priceRange: { 
                        ...filters.priceRange, 
                        max: e.target.value ? Number(e.target.value) : undefined 
                      } 
                    })}
                  />
                </div>
              </div>
            </div>
          </div>
        )}
      </Card>

      {/* View Mode Tabs */}
      <Tabs value={viewMode} onValueChange={(value) => handleViewModeChange(value as CatalogViewMode)}>
        <div className="flex items-center justify-between">
          <TabsList>
            <TabsTrigger value="grid">
              <Grid className="h-4 w-4 mr-sm" />
              Grid
            </TabsTrigger>
            <TabsTrigger value="list">
              <List className="h-4 w-4 mr-sm" />
              List
            </TabsTrigger>
            <TabsTrigger value="table">
              <Columns className="h-4 w-4 mr-sm" />
              Table
            </TabsTrigger>
            <TabsTrigger value="kanban">
              <Columns className="h-4 w-4 mr-sm" />
              Kanban
            </TabsTrigger>
            <TabsTrigger value="dashboard">
              <BarChart3 className="h-4 w-4 mr-sm" />
              Dashboard
            </TabsTrigger>
          </TabsList>

          {/* Bulk Actions */}
          {selectedItems.length > 0 && (
            <div className="flex items-center gap-sm">
              <span className="text-sm text-muted-foreground">
                {selectedItems.length} selected
              </span>
              <Button
                variant="outline"
                size="sm"
                onClick={() => handleBulkAction({ type: 'delete', itemIds: selectedItems })}
              >
                <Trash2 className="h-4 w-4 mr-sm" />
                Delete
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={() => setSelectedItems([])}
              >
                <X className="h-4 w-4 mr-sm" />
                Clear
              </Button>
            </div>
          )}
        </div>

        <TabsContent value={viewMode} className="mt-md">
          {renderView()}
        </TabsContent>
      </Tabs>

      {/* Pagination */}
      {pagination.total > pagination.limit && (
        <div className="flex items-center justify-between">
          <div className="text-sm text-muted-foreground">
            Showing {((pagination.page - 1) * pagination.limit) + 1} to{' '}
            {Math.min(pagination.page * pagination.limit, pagination.total)} of{' '}
            {pagination.total} items
          </div>
          <div className="flex items-center gap-sm">
            <Button
              variant="outline"
              size="sm"
              disabled={pagination.page === 1}
              onClick={() => setPagination(prev => ({ ...prev, page: prev.page - 1 }))}
            >
              Previous
            </Button>
            <Button
              variant="outline"
              size="sm"
              disabled={!pagination.hasMore}
              onClick={() => setPagination(prev => ({ ...prev, page: prev.page + 1 }))}
            >
              Next
            </Button>
          </div>
        </div>
      )}
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useState } from 'react';
import { Card, Badge, Button, Checkbox } from '@ghxstship/ui';
import type { CatalogItem } from '../types';
import { formatCurrency, formatDate, getStatusColor, getTypeColor } from '../types';

interface CatalogGridViewProps {
  items: CatalogItem[];
  loading?: boolean;
  selectedItems: string[];
  onSelectionChange: (itemIds: string[]) => void;
  onItemClick?: (item: CatalogItem) => void;
  onEditItem?: (item: CatalogItem) => void;
  onDeleteItem?: (item: CatalogItem) => void;
  onViewItem?: (item: CatalogItem) => void;
}

export default function CatalogGridView({
  items,
  loading = false,
  selectedItems,
  onSelectionChange,
  onItemClick,
  onEditItem,
  onDeleteItem,
  onViewItem,
}: CatalogGridViewProps) {
  const [hoveredItem, setHoveredItem] = useState<string | null>(null);

  const handleItemSelection = (itemId: string, checked: boolean) => {
    if (checked) {
      onSelectionChange([...selectedItems, itemId]);
    } else {
      onSelectionChange(selectedItems.filter(id => id !== itemId));
    }
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      onSelectionChange(items.map(item => item.id));
    } else {
      onSelectionChange([]);
    }
  };

  const getTypeIcon = (type: string) => {
    return type === 'product' ? Package2 : Wrench;
  };

  if (loading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
        {Array.from({ length: 8 }).map((_, index) => (
          <Card key={index} className="p-md">
            <div className="animate-pulse">
              <div className="flex items-center justify-between mb-sm">
                <div className="h-4 bg-muted rounded w-3/4"></div>
                <div className="h-6 bg-muted rounded-full w-16"></div>
              </div>
              <div className="h-3 bg-muted rounded w-1/2 mb-sm"></div>
              <div className="h-3 bg-muted rounded w-full mb-sm"></div>
              <div className="h-3 bg-muted rounded w-2/3 mb-md"></div>
              <div className="flex justify-between items-center">
                <div className="h-5 bg-muted rounded w-20"></div>
                <div className="flex gap-xs">
                  <div className="h-8 w-8 bg-muted rounded"></div>
                  <div className="h-8 w-8 bg-muted rounded"></div>
                </div>
              </div>
            </div>
          </Card>
        ))}
      </div>
    );
  }

  if (items.length === 0) {
    return (
      <Card className="p-xl text-center">
        <Package2 className="h-12 w-12 mx-auto mb-md text-muted-foreground opacity-50" />
        <h3 className="text-lg font-medium mb-sm">No catalog items found</h3>
        <p className="text-muted-foreground">
          No items match your current filters. Try adjusting your search criteria.
        </p>
      </Card>
    );
  }

  const allSelected = items.length > 0 && selectedItems.length === items.length;
  const someSelected = selectedItems.length > 0 && selectedItems.length < items.length;

  return (
    <div className="space-y-md">
      {/* Selection header */}
      {items.length > 0 && (
        <div className="flex items-center gap-sm p-sm bg-muted/30 rounded-lg">
          <Checkbox
            checked={allSelected}
            indeterminate={someSelected}
            onCheckedChange={handleSelectAll}
          />
          <span className="text-sm text-muted-foreground">
            {selectedItems.length > 0 
              ? `${selectedItems.length} of ${items.length} items selected`
              : `Select all ${items.length} items`
            }
          </span>
        </div>
      )}

      {/* Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
        {items.map((item) => {
          const TypeIcon = getTypeIcon(item.type);
          const isSelected = selectedItems.includes(item.id);
          const isHovered = hoveredItem === item.id;

          return (
            <Card
              key={item.id}
              className={`relative transition-all duration-200 cursor-pointer hover:shadow-md ${
                isSelected ? 'ring-2 ring-primary' : ''
              }`}
              onMouseEnter={() => setHoveredItem(item.id)}
              onMouseLeave={() => setHoveredItem(null)}
              onClick={() => onItemClick?.(item)}
            >
              {/* Selection checkbox */}
              <div className="absolute top-sm left-sm z-10">
                <Checkbox
                  checked={isSelected}
                  onCheckedChange={(checked) => handleItemSelection(item.id, checked as boolean)}
                  onClick={(e) => e.stopPropagation()}
                />
              </div>

              {/* Action buttons */}
              {isHovered && (
                <div className="absolute top-sm right-sm z-10 flex gap-xs">
                  {onViewItem && (
                    <Button
                      size="sm"
                      variant="secondary"
                      onClick={(e) => {
                        e.stopPropagation();
                        onViewItem(item);
                      }}
                    >
                      <Eye className="h-3 w-3" />
                    </Button>
                  )}
                  {onEditItem && (
                    <Button
                      size="sm"
                      variant="secondary"
                      onClick={(e) => {
                        e.stopPropagation();
                        onEditItem(item);
                      }}
                    >
                      <Edit className="h-3 w-3" />
                    </Button>
                  )}
                  {onDeleteItem && (
                    <Button
                      size="sm"
                      variant="destructive"
                      onClick={(e) => {
                        e.stopPropagation();
                        onDeleteItem(item);
                      }}
                    >
                      <Trash2 className="h-3 w-3" />
                    </Button>
                  )}
                </div>
              )}

              <div className="p-md pt-lg">
                {/* Header */}
                <div className="flex items-start justify-between mb-sm">
                  <div className="flex items-center gap-sm min-w-0 flex-1">
                    <TypeIcon className="h-4 w-4 text-muted-foreground flex-shrink-0" />
                    <h4 className="font-medium truncate">{item.name}</h4>
                  </div>
                  <Badge variant={getStatusColor(item.status)} className="ml-sm">
                    {item.status}
                  </Badge>
                </div>

                {/* Type and category */}
                <div className="flex items-center gap-sm mb-sm">
                  <span className={`px-sm py-xs rounded-full text-xs font-medium ${
                    item.type === 'product' 
                      ? 'bg-primary/10 text-primary' 
                      : 'bg-success/10 text-success'
                  }`}>
                    {item.type}
                  </span>
                  {item.category && (
                    <span className="text-xs text-muted-foreground uppercase tracking-wide">
                      {item.category}
                    </span>
                  )}
                </div>

                {/* Description */}
                {item.description && (
                  <p className="text-sm text-muted-foreground line-clamp-2 mb-sm">
                    {item.description}
                  </p>
                )}

                {/* Price */}
                <div className="flex items-center justify-between mb-sm">
                  <div className="text-lg font-semibold">
                    {formatCurrency(item.price || item.rate || 0, item.currency)}
                    {item.type === 'service' && item.unit && (
                      <span className="text-sm text-muted-foreground font-normal">
                        /{item.unit}
                      </span>
                    )}
                  </div>
                </div>

                {/* Supplier and SKU */}
                <div className="space-y-xs text-xs text-muted-foreground">
                  {item.supplier && (
                    <div>
                      <strong>Supplier:</strong> {item.supplier}
                    </div>
                  )}
                  {item.sku && (
                    <div>
                      <strong>SKU:</strong> {item.sku}
                    </div>
                  )}
                </div>

                {/* Tags */}
                {item.tags && item.tags.length > 0 && (
                  <div className="flex flex-wrap gap-xs mt-sm">
                    {item.tags.slice(0, 3).map((tag, index) => (
                      <span
                        key={index}
                        className="px-xs py-xs bg-muted rounded text-xs"
                      >
                        {tag}
                      </span>
                    ))}
                    {item.tags.length > 3 && (
                      <span className="px-xs py-xs bg-muted rounded text-xs">
                        +{item.tags.length - 3} more
                      </span>
                    )}
                  </div>
                )}

                {/* Footer */}
                <div className="flex items-center justify-between mt-md pt-sm border-t border-border">
                  <span className="text-xs text-muted-foreground">
                    {formatDate(item.created_at)}
                  </span>
                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={(e) => {
                      e.stopPropagation();
                      // Show more options menu
                    }}
                  >
                    <MoreHorizontal className="h-3 w-3" />
                  </Button>
                </div>
              </div>
            </Card>
          );
        })}
      </div>
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useState } from 'react';
import { Card, Badge, Button } from '@ghxstship/ui';
import type { CatalogItem } from '../types';
import { formatCurrency, formatDate, getStatusColor } from '../types';

interface CatalogKanbanViewProps {
  items: CatalogItem[];
  loading?: boolean;
  selectedItems: string[];
  onSelectionChange: (itemIds: string[]) => void;
  onItemClick?: (item: CatalogItem) => void;
  onEditItem?: (item: CatalogItem) => void;
  onDeleteItem?: (item: CatalogItem) => void;
  onViewItem?: (item: CatalogItem) => void;
  onStatusChange?: (item: CatalogItem, newStatus: string) => void;
}

const statusColumns = [
  { key: 'active', label: 'Active', color: 'bg-success/10 border-success/20' },
  { key: 'inactive', label: 'Inactive', color: 'bg-warning/10 border-warning/20' },
  { key: 'discontinued', label: 'Discontinued', color: 'bg-destructive/10 border-destructive/20' },
];

export default function CatalogKanbanView({
  items,
  loading = false,
  selectedItems,
  onSelectionChange,
  onItemClick,
  onEditItem,
  onDeleteItem,
  onViewItem,
  onStatusChange,
}: CatalogKanbanViewProps) {
  const [draggedItem, setDraggedItem] = useState<CatalogItem | null>(null);

  const getTypeIcon = (type: string) => {
    return type === 'product' ? Package2 : Wrench;
  };

  const handleDragStart = (e: React.DragEvent, item: CatalogItem) => {
    setDraggedItem(item);
    e.dataTransfer.effectAllowed = 'move';
  };

  const handleDragEnd = () => {
    setDraggedItem(null);
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';
  };

  const handleDrop = (e: React.DragEvent, status: string) => {
    e.preventDefault();
    if (draggedItem && draggedItem.status !== status) {
      onStatusChange?.(draggedItem, status);
    }
    setDraggedItem(null);
  };

  const getItemsByStatus = (status: string) => {
    return items.filter(item => item.status === status);
  };

  if (loading) {
    return (
      <div className="flex gap-md h-[600px]">
        {statusColumns.map((column) => (
          <div key={column.key} className="flex-1">
            <Card className={`h-full ${column.color}`}>
              <div className="p-md border-b border-border">
                <div className="h-6 bg-muted rounded w-20 animate-pulse"></div>
              </div>
              <div className="p-md space-y-md">
                {Array.from({ length: 3 }).map((_, index) => (
                  <Card key={index} className="p-md">
                    <div className="animate-pulse space-y-sm">
                      <div className="flex items-center gap-sm">
                        <div className="h-4 w-4 bg-muted rounded"></div>
                        <div className="h-4 bg-muted rounded w-3/4"></div>
                      </div>
                      <div className="h-3 bg-muted rounded w-1/2"></div>
                      <div className="h-3 bg-muted rounded w-full"></div>
                      <div className="h-5 bg-muted rounded w-16"></div>
                    </div>
                  </Card>
                ))}
              </div>
            </Card>
          </div>
        ))}
      </div>
    );
  }

  return (
    <div className="flex gap-md h-[600px] overflow-x-auto">
      {statusColumns.map((column) => {
        const columnItems = getItemsByStatus(column.key);

        return (
          <div key={column.key} className="flex-1 min-w-[300px]">
            <Card 
              className={`h-full ${column.color} flex flex-col`}
              onDragOver={handleDragOver}
              onDrop={(e) => handleDrop(e, column.key)}
            >
              {/* Column header */}
              <div className="p-md border-b border-border flex-shrink-0">
                <div className="flex items-center justify-between">
                  <h3 className="font-medium">{column.label}</h3>
                  <Badge variant="secondary">
                    {columnItems.length}
                  </Badge>
                </div>
              </div>

              {/* Column content */}
              <div className="flex-1 p-md space-y-md overflow-y-auto">
                {columnItems.length === 0 ? (
                  <div className="text-center py-lg text-muted-foreground">
                    <div className="text-sm">No {column.label.toLowerCase()} items</div>
                  </div>
                ) : (
                  columnItems.map((item) => {
                    const TypeIcon = getTypeIcon(item.type);
                    const isSelected = selectedItems.includes(item.id);
                    const isDragging = draggedItem?.id === item.id;

                    return (
                      <Card
                        key={item.id}
                        className={`cursor-pointer transition-all duration-200 hover:shadow-md ${
                          isSelected ? 'ring-2 ring-primary' : ''
                        } ${isDragging ? 'opacity-50 rotate-2' : ''}`}
                        draggable
                        onDragStart={(e) => handleDragStart(e, item)}
                        onDragEnd={handleDragEnd}
                        onClick={() => onItemClick?.(item)}
                      >
                        <div className="p-md">
                          {/* Header */}
                          <div className="flex items-start justify-between mb-sm">
                            <div className="flex items-center gap-sm min-w-0 flex-1">
                              <TypeIcon className="h-4 w-4 text-muted-foreground flex-shrink-0" />
                              <h4 className="font-medium text-sm truncate">{item.name}</h4>
                            </div>
                            <div className="flex items-center gap-xs ml-sm">
                              {onViewItem && (
                                <Button
                                  size="sm"
                                  variant="ghost"
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    onViewItem(item);
                                  }}
                                  className="h-6 w-6 p-0"
                                >
                                  <Eye className="h-3 w-3" />
                                </Button>
                              )}
                              {onEditItem && (
                                <Button
                                  size="sm"
                                  variant="ghost"
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    onEditItem(item);
                                  }}
                                  className="h-6 w-6 p-0"
                                >
                                  <Edit className="h-3 w-3" />
                                </Button>
                              )}
                              {onDeleteItem && (
                                <Button
                                  size="sm"
                                  variant="ghost"
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    onDeleteItem(item);
                                  }}
                                  className="h-6 w-6 p-0"
                                >
                                  <Trash2 className="h-3 w-3" />
                                </Button>
                              )}
                            </div>
                          </div>

                          {/* Type and category */}
                          <div className="flex items-center gap-sm mb-sm">
                            <span className={`px-sm py-xs rounded-full text-xs font-medium ${
                              item.type === 'product' 
                                ? 'bg-primary/10 text-primary' 
                                : 'bg-success/10 text-success'
                            }`}>
                              {item.type}
                            </span>
                            {item.category && (
                              <span className="text-xs text-muted-foreground">
                                {item.category}
                              </span>
                            )}
                          </div>

                          {/* Description */}
                          {item.description && (
                            <p className="text-xs text-muted-foreground line-clamp-2 mb-sm">
                              {item.description}
                            </p>
                          )}

                          {/* Price */}
                          <div className="flex items-center justify-between mb-sm">
                            <div className="font-semibold text-sm">
                              {formatCurrency(item.price || item.rate || 0, item.currency)}
                              {item.type === 'service' && item.unit && (
                                <span className="text-xs text-muted-foreground font-normal">
                                  /{item.unit}
                                </span>
                              )}
                            </div>
                          </div>

                          {/* Supplier */}
                          {item.supplier && (
                            <div className="text-xs text-muted-foreground mb-sm">
                              <strong>Supplier:</strong> {item.supplier}
                            </div>
                          )}

                          {/* SKU */}
                          {item.sku && (
                            <div className="text-xs text-muted-foreground mb-sm">
                              <strong>SKU:</strong> {item.sku}
                            </div>
                          )}

                          {/* Tags */}
                          {item.tags && item.tags.length > 0 && (
                            <div className="flex flex-wrap gap-xs mb-sm">
                              {item.tags.slice(0, 2).map((tag, index) => (
                                <span
                                  key={index}
                                  className="px-xs py-xs bg-muted rounded text-xs"
                                >
                                  {tag}
                                </span>
                              ))}
                              {item.tags.length > 2 && (
                                <span className="px-xs py-xs bg-muted rounded text-xs">
                                  +{item.tags.length - 2}
                                </span>
                              )}
                            </div>
                          )}

                          {/* Footer */}
                          <div className="flex items-center justify-between pt-sm border-t border-border text-xs text-muted-foreground">
                            <span>{formatDate(item.created_at)}</span>
                            <div className="flex items-center gap-xs">
                              <div className="w-2 h-2 rounded-full bg-current opacity-50"></div>
                              <span>{item.id.slice(-6)}</span>
                            </div>
                          </div>
                        </div>
                      </Card>
                    );
                  })
                )}
              </div>
            </Card>
          </div>
        );
      })}
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useState } from 'react';
import { Card, Badge, Button, Checkbox } from '@ghxstship/ui';
import type { CatalogItem } from '../types';
import { formatCurrency, formatDate, getStatusColor } from '../types';

interface CatalogListViewProps {
  items: CatalogItem[];
  loading?: boolean;
  selectedItems: string[];
  onSelectionChange: (itemIds: string[]) => void;
  onItemClick?: (item: CatalogItem) => void;
  onEditItem?: (item: CatalogItem) => void;
  onDeleteItem?: (item: CatalogItem) => void;
  onViewItem?: (item: CatalogItem) => void;
}

export default function CatalogListView({
  items,
  loading = false,
  selectedItems,
  onSelectionChange,
  onItemClick,
  onEditItem,
  onDeleteItem,
  onViewItem,
}: CatalogListViewProps) {
  const [expandedItems, setExpandedItems] = useState<Set<string>>(new Set());

  const handleItemSelection = (itemId: string, checked: boolean) => {
    if (checked) {
      onSelectionChange([...selectedItems, itemId]);
    } else {
      onSelectionChange(selectedItems.filter(id => id !== itemId));
    }
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      onSelectionChange(items.map(item => item.id));
    } else {
      onSelectionChange([]);
    }
  };

  const toggleExpanded = (itemId: string) => {
    const newExpanded = new Set(expandedItems);
    if (newExpanded.has(itemId)) {
      newExpanded.delete(itemId);
    } else {
      newExpanded.add(itemId);
    }
    setExpandedItems(newExpanded);
  };

  const getTypeIcon = (type: string) => {
    return type === 'product' ? Package2 : Wrench;
  };

  if (loading) {
    return (
      <Card>
        <div className="divide-y divide-border">
          {Array.from({ length: 6 }).map((_, index) => (
            <div key={index} className="p-md">
              <div className="animate-pulse flex items-center gap-md">
                <div className="h-4 w-4 bg-muted rounded"></div>
                <div className="h-4 w-4 bg-muted rounded"></div>
                <div className="flex-1 space-y-sm">
                  <div className="h-4 bg-muted rounded w-1/3"></div>
                  <div className="h-3 bg-muted rounded w-2/3"></div>
                </div>
                <div className="h-6 bg-muted rounded-full w-16"></div>
                <div className="h-4 bg-muted rounded w-20"></div>
              </div>
            </div>
          ))}
        </div>
      </Card>
    );
  }

  if (items.length === 0) {
    return (
      <Card className="p-xl text-center">
        <Package2 className="h-12 w-12 mx-auto mb-md text-muted-foreground opacity-50" />
        <h3 className="text-lg font-medium mb-sm">No catalog items found</h3>
        <p className="text-muted-foreground">
          No items match your current filters. Try adjusting your search criteria.
        </p>
      </Card>
    );
  }

  const allSelected = items.length > 0 && selectedItems.length === items.length;
  const someSelected = selectedItems.length > 0 && selectedItems.length < items.length;

  return (
    <div className="space-y-md">
      {/* Selection header */}
      {items.length > 0 && (
        <div className="flex items-center gap-sm p-sm bg-muted/30 rounded-lg">
          <Checkbox
            checked={allSelected}
            indeterminate={someSelected}
            onCheckedChange={handleSelectAll}
          />
          <span className="text-sm text-muted-foreground">
            {selectedItems.length > 0 
              ? `${selectedItems.length} of ${items.length} items selected`
              : `Select all ${items.length} items`
            }
          </span>
        </div>
      )}

      {/* List */}
      <Card>
        <div className="divide-y divide-border">
          {items.map((item) => {
            const TypeIcon = getTypeIcon(item.type);
            const isSelected = selectedItems.includes(item.id);
            const isExpanded = expandedItems.has(item.id);

            return (
              <div key={item.id} className={`transition-colors ${isSelected ? 'bg-primary/5' : ''}`}>
                {/* Main row */}
                <div className="p-md hover:bg-muted/30 transition-colors">
                  <div className="flex items-center gap-md">
                    {/* Selection checkbox */}
                    <Checkbox
                      checked={isSelected}
                      onCheckedChange={(checked) => handleItemSelection(item.id, checked as boolean)}
                    />

                    {/* Expand/collapse button */}
                    <Button
                      size="sm"
                      variant="ghost"
                      onClick={() => toggleExpanded(item.id)}
                      className="p-0 h-auto"
                    >
                      {isExpanded ? (
                        <ChevronDown className="h-4 w-4" />
                      ) : (
                        <ChevronRight className="h-4 w-4" />
                      )}
                    </Button>

                    {/* Type icon */}
                    <TypeIcon className="h-5 w-5 text-muted-foreground flex-shrink-0" />

                    {/* Main content */}
                    <div 
                      className="flex-1 min-w-0 cursor-pointer"
                      onClick={() => onItemClick?.(item)}
                    >
                      <div className="flex items-center gap-sm mb-xs">
                        <h4 className="font-medium truncate">{item.name}</h4>
                        <span className={`px-sm py-xs rounded-full text-xs font-medium ${
                          item.type === 'product' 
                            ? 'bg-primary/10 text-primary' 
                            : 'bg-success/10 text-success'
                        }`}>
                          {item.type}
                        </span>
                        <Badge variant={getStatusColor(item.status)}>
                          {item.status}
                        </Badge>
                      </div>

                      <div className="flex items-center gap-md text-sm text-muted-foreground">
                        {item.category && <span>Category: {item.category}</span>}
                        {item.supplier && <span>Supplier: {item.supplier}</span>}
                        {item.sku && <span>SKU: {item.sku}</span>}
                        <span>Created: {formatDate(item.created_at)}</span>
                      </div>
                    </div>

                    {/* Price */}
                    <div className="text-right">
                      <div className="font-semibold">
                        {formatCurrency(item.price || item.rate || 0, item.currency)}
                        {item.type === 'service' && item.unit && (
                          <span className="text-sm text-muted-foreground font-normal">
                            /{item.unit}
                          </span>
                        )}
                      </div>
                    </div>

                    {/* Actions */}
                    <div className="flex items-center gap-xs">
                      {onViewItem && (
                        <Button
                          size="sm"
                          variant="ghost"
                          onClick={(e) => {
                            e.stopPropagation();
                            onViewItem(item);
                          }}
                        >
                          <Eye className="h-4 w-4" />
                        </Button>
                      )}
                      {onEditItem && (
                        <Button
                          size="sm"
                          variant="ghost"
                          onClick={(e) => {
                            e.stopPropagation();
                            onEditItem(item);
                          }}
                        >
                          <Edit className="h-4 w-4" />
                        </Button>
                      )}
                      {onDeleteItem && (
                        <Button
                          size="sm"
                          variant="ghost"
                          onClick={(e) => {
                            e.stopPropagation();
                            onDeleteItem(item);
                          }}
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      )}
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={(e) => {
                          e.stopPropagation();
                          // Show more options menu
                        }}
                      >
                        <MoreHorizontal className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                </div>

                {/* Expanded details */}
                {isExpanded && (
                  <div className="px-md pb-md ml-[4.5rem] border-l-2 border-muted">
                    <div className="bg-muted/30 rounded-lg p-md space-y-md">
                      {/* Description */}
                      {item.description && (
                        <div>
                          <h5 className="font-medium mb-xs">Description</h5>
                          <p className="text-sm text-muted-foreground">{item.description}</p>
                        </div>
                      )}

                      {/* Specifications */}
                      {item.specifications && (
                        <div>
                          <h5 className="font-medium mb-xs">Specifications</h5>
                          <p className="text-sm text-muted-foreground whitespace-pre-wrap">
                            {item.specifications}
                          </p>
                        </div>
                      )}

                      {/* Tags */}
                      {item.tags && item.tags.length > 0 && (
                        <div>
                          <h5 className="font-medium mb-xs">Tags</h5>
                          <div className="flex flex-wrap gap-xs">
                            {item.tags.map((tag, index) => (
                              <span
                                key={index}
                                className="px-sm py-xs bg-background border rounded text-xs"
                              >
                                {tag}
                              </span>
                            ))}
                          </div>
                        </div>
                      )}

                      {/* Additional details */}
                      <div className="grid grid-cols-2 gap-md text-sm">
                        <div>
                          <h5 className="font-medium mb-xs">Details</h5>
                          <div className="space-y-xs text-muted-foreground">
                            <div>Type: {item.type}</div>
                            <div>Status: {item.status}</div>
                            {item.category && <div>Category: {item.category}</div>}
                            {item.supplier && <div>Supplier: {item.supplier}</div>}
                            {item.sku && <div>SKU: {item.sku}</div>}
                            {item.unit && <div>Unit: {item.unit}</div>}
                          </div>
                        </div>
                        <div>
                          <h5 className="font-medium mb-xs">Pricing</h5>
                          <div className="space-y-xs text-muted-foreground">
                            <div>
                              {item.type === 'product' ? 'Price' : 'Rate'}: {' '}
                              {formatCurrency(item.price || item.rate || 0, item.currency)}
                            </div>
                            <div>Currency: {item.currency}</div>
                            {item.type === 'service' && item.unit && (
                              <div>Unit: {item.unit}</div>
                            )}
                          </div>
                        </div>
                      </div>

                      {/* Timestamps */}
                      <div className="flex justify-between text-xs text-muted-foreground pt-sm border-t border-border">
                        <span>Created: {formatDate(item.created_at)}</span>
                        <span>Updated: {formatDate(item.updated_at)}</span>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </Card>
    </div>
  );
}

'use client';

import { Package, Search, Filter, Grid, List, Columns, BarChart3, Calendar, Download, Upload, Trash2, Edit, Eye, RefreshCw, Settings, X, Plus } from "lucide-react";
import { useState, useEffect, useCallback } from 'react';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { 
  Button, 
  UnifiedInput, 
  Card, 
  Badge,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Checkbox,
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
} from '@ghxstship/ui';

// Import types and views
import type { 
  ProcurementOrder, 
  OrderFilters, 
  OrderSort, 
  OrderViewMode, 
  OrderStats,
  OrderBulkAction 
} from './types';
import { OrderService } from './lib/orderService';
import OrderGridView from './views/OrderGridView';
import OrderTableView from './views/OrderTableView';
import OrderKanbanView from './views/OrderKanbanView';
import OrderDashboardView from './views/OrderDashboardView';
import CreateOrderClient from './CreateOrderClient';

export default function OrdersClient({ orgId }: { orgId: string }) {
  const t = useTranslations('procurement');
  const sb = createBrowserClient();

  // State management
  const [orders, setOrders] = useState<ProcurementOrder[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedOrders, setSelectedOrders] = useState<string[]>([]);
  const [viewMode, setViewMode] = useState<OrderViewMode>('grid');
  const [showFilters, setShowFilters] = useState(false);
  const [stats, setStats] = useState<OrderStats | null>(null);

  // Filters and sorting
  const [filters, setFilters] = useState<OrderFilters>({
    search: '',
    status: 'all',
    priority: 'all',
  });
  const [sort, setSort] = useState<OrderSort>({
    field: 'created_at',
    direction: 'desc',
  });

  // Pagination
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 50,
    total: 0,
    hasMore: false,
  });

  const orderService = new OrderService();

  // Load orders with filters and pagination
  const loadOrders = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      const result = await orderService.getOrders(
        orgId,
        filters,
        sort,
        pagination.page,
        pagination.limit
      );

      setOrders(result.orders);
      setPagination(prev => ({
        ...prev,
        total: result.total,
        hasMore: result.hasMore,
      }));

      // Load stats if on dashboard view
      if (viewMode === 'dashboard') {
        const statsResult = await orderService.getOrderStats(orgId);
        setStats(statsResult);
      }
    } catch (error) {
      console.error('Error loading orders:', error);
      setError(error instanceof Error ? error.message : 'Failed to load orders');
    } finally {
      setLoading(false);
    }
  }, [orgId, filters, sort, pagination.page, pagination.limit, viewMode]);

  // Load data on mount and when dependencies change
  useEffect(() => {
    loadOrders();
  }, [loadOrders]);

  // Handle filter changes
  const handleFilterChange = useCallback((newFilters: Partial<OrderFilters>) => {
    setFilters(prev => ({ ...prev, ...newFilters }));
    setPagination(prev => ({ ...prev, page: 1 })); // Reset to first page
  }, []);

  // Handle sort changes
  const handleSortChange = useCallback((newSort: OrderSort) => {
    setSort(newSort);
    setPagination(prev => ({ ...prev, page: 1 })); // Reset to first page
  }, []);

  // Handle view mode changes
  const handleViewModeChange = useCallback((mode: OrderViewMode) => {
    setViewMode(mode);
    setSelectedOrders([]); // Clear selection when changing views
  }, []);

  // Handle order selection
  const handleSelectionChange = useCallback((orderIds: string[]) => {
    setSelectedOrders(orderIds);
  }, []);

  // Handle individual order actions
  const handleOrderClick = useCallback((order: ProcurementOrder) => {
    // Navigate to order detail or open drawer
    console.log('Order clicked:', order);
  }, []);

  const handleEditOrder = useCallback((order: ProcurementOrder) => {
    // Open edit drawer
    console.log('Edit order:', order);
  }, []);

  const handleDeleteOrder = useCallback((order: ProcurementOrder) => {
    // Show confirmation dialog and delete
    console.log('Delete order:', order);
  }, []);

  const handleViewOrder = useCallback((order: ProcurementOrder) => {
    // Open view drawer
    console.log('View order:', order);
  }, []);

  // Handle bulk actions
  const handleBulkAction = useCallback(async (action: OrderBulkAction) => {
    try {
      setLoading(true);
      await orderService.bulkUpdateOrders(orgId, action, 'current-user-id');
      await loadOrders(); // Reload data
      setSelectedOrders([]); // Clear selection
    } catch (error) {
      console.error('Bulk action failed:', error);
      setError(error instanceof Error ? error.message : 'Bulk action failed');
    } finally {
      setLoading(false);
    }
  }, [orgId, loadOrders]);

  // Handle status change (for Kanban view)
  const handleStatusChange = useCallback(async (order: ProcurementOrder, newStatus: string) => {
    try {
      await orderService.updateOrder(
        orgId,
        order.id,
        { status: newStatus as unknown },
        'current-user-id'
      );
      await loadOrders(); // Reload data
    } catch (error) {
      console.error('Status change failed:', error);
      setError(error instanceof Error ? error.message : 'Status change failed');
    }
  }, [orgId, loadOrders]);

  // Render view based on current mode
  const renderView = () => {
    const commonProps = {
      orders,
      loading,
      selectedOrders,
      onSelectionChange: handleSelectionChange,
      onOrderClick: handleOrderClick,
      onEditOrder: handleEditOrder,
      onDeleteOrder: handleDeleteOrder,
      onViewOrder: handleViewOrder,
    };

    switch (viewMode) {
      case 'grid':
        return <OrderGridView {...commonProps} />;
      case 'table':
        return (
          <OrderTableView
            {...commonProps}
            sort={sort}
            onSortChange={handleSortChange}
          />
        );
      case 'kanban':
        return (
          <OrderKanbanView
            {...commonProps}
            onStatusChange={handleStatusChange}
          />
        );
      case 'dashboard':
        return (
          <OrderDashboardView
            orders={orders}
            loading={loading}
            stats={stats || undefined}
            onOrderClick={handleOrderClick}
          />
        );
      default:
        return <OrderGridView {...commonProps} />;
    }
  };

  if (error) {
    return (
      <Card className="p-xl text-center">
        <div className="text-destructive mb-md">
          <Package className="h-12 w-12 mx-auto mb-md opacity-50" />
          <h3 className="text-lg font-medium mb-sm">Error Loading Orders</h3>
          <p className="text-sm">{error}</p>
        </div>
        <Button onClick={loadOrders} variant="outline">
          <RefreshCw className="h-4 w-4 mr-sm" />
          Try Again
        </Button>
      </Card>
    );
  }

  return (
    <div className="space-y-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-sm">
          <Package className="h-5 w-5" />
          <h3 className="text-lg font-semibold">Purchase Orders</h3>
          <Badge variant="secondary">
            {orders.length} order{orders.length !== 1 ? 's' : ''}
          </Badge>
        </div>
        <div className="flex items-center gap-sm">
          <CreateOrderClient orgId={orgId} onOrderCreated={loadOrders} />
          <Button variant="outline" size="sm">
            <Upload className="h-4 w-4 mr-sm" />
            Import
          </Button>
          <Button variant="outline" size="sm">
            <Download className="h-4 w-4 mr-sm" />
            Export
          </Button>
        </div>
      </div>

      {/* Filters and Search */}
      <Card className="p-md">
        <div className="flex flex-col lg:flex-row gap-md">
          {/* Search */}
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <UnifiedInput
              placeholder="Search orders..."
              value={filters.search || ''}
              onChange={(e) => handleFilterChange({ search: e.target.value })}
              className="pl-10"
            />
          </div>

          {/* Status Filter */}
          <Select
            value={filters.status || 'all'}
            onValueChange={(value) => handleFilterChange({ status: value as unknown })}
          >
            <SelectTrigger className="w-[140px]">
              <SelectValue placeholder="All Status" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Status</SelectItem>
              <SelectItem value="draft">Draft</SelectItem>
              <SelectItem value="pending">Pending</SelectItem>
              <SelectItem value="approved">Approved</SelectItem>
              <SelectItem value="ordered">Ordered</SelectItem>
              <SelectItem value="delivered">Delivered</SelectItem>
              <SelectItem value="cancelled">Cancelled</SelectItem>
            </SelectContent>
          </Select>

          {/* Priority Filter */}
          <Select
            value={filters.priority || 'all'}
            onValueChange={(value) => handleFilterChange({ priority: value as unknown })}
          >
            <SelectTrigger className="w-[140px]">
              <SelectValue placeholder="All Priority" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Priority</SelectItem>
              <SelectItem value="low">Low</SelectItem>
              <SelectItem value="medium">Medium</SelectItem>
              <SelectItem value="high">High</SelectItem>
              <SelectItem value="urgent">Urgent</SelectItem>
            </SelectContent>
          </Select>

          {/* Advanced Filters Toggle */}
          <Button
            variant="outline"
            size="sm"
            onClick={() => setShowFilters(!showFilters)}
          >
            <Filter className="h-4 w-4 mr-sm" />
            Filters
          </Button>
        </div>

        {/* Advanced Filters */}
        {showFilters && (
          <div className="mt-md pt-md border-t border-border">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-md">
              {/* Vendor Filter */}
              <div>
                <label className="text-sm font-medium mb-sm block">Vendor</label>
                <UnifiedInput
                  placeholder="Filter by vendor"
                  value={filters.vendor_name || ''}
                  onChange={(e) => handleFilterChange({ vendor_name: e.target.value })}
                />
              </div>

              {/* Amount Range */}
              <div>
                <label className="text-sm font-medium mb-sm block">Amount Range</label>
                <div className="flex gap-sm">
                  <UnifiedInput
                    type="number"
                    placeholder="Min"
                    value={filters.amount_range?.min || ''}
                    onChange={(e) => handleFilterChange({ 
                      amount_range: { 
                        ...filters.amount_range, 
                        min: e.target.value ? Number(e.target.value) : undefined 
                      } 
                    })}
                  />
                  <UnifiedInput
                    type="number"
                    placeholder="Max"
                    value={filters.amount_range?.max || ''}
                    onChange={(e) => handleFilterChange({ 
                      amount_range: { 
                        ...filters.amount_range, 
                        max: e.target.value ? Number(e.target.value) : undefined 
                      } 
                    })}
                  />
                </div>
              </div>

              {/* Date Range */}
              <div>
                <label className="text-sm font-medium mb-sm block">Order Date Range</label>
                <div className="flex gap-sm">
                  <UnifiedInput
                    type="date"
                    value={filters.date_range?.start || ''}
                    onChange={(e) => handleFilterChange({ 
                      date_range: { 
                        ...filters.date_range, 
                        start: e.target.value || undefined 
                      } 
                    })}
                  />
                  <UnifiedInput
                    type="date"
                    value={filters.date_range?.end || ''}
                    onChange={(e) => handleFilterChange({ 
                      date_range: { 
                        ...filters.date_range, 
                        end: e.target.value || undefined 
                      } 
                    })}
                  />
                </div>
              </div>
            </div>
          </div>
        )}
      </Card>

      {/* View Mode Tabs */}
      <Tabs value={viewMode} onValueChange={(value) => handleViewModeChange(value as OrderViewMode)}>
        <div className="flex items-center justify-between">
          <TabsList>
            <TabsTrigger value="grid">
              <Grid className="h-4 w-4 mr-sm" />
              Grid
            </TabsTrigger>
            <TabsTrigger value="table">
              <Columns className="h-4 w-4 mr-sm" />
              Table
            </TabsTrigger>
            <TabsTrigger value="kanban">
              <Columns className="h-4 w-4 mr-sm" />
              Kanban
            </TabsTrigger>
            <TabsTrigger value="dashboard">
              <BarChart3 className="h-4 w-4 mr-sm" />
              Dashboard
            </TabsTrigger>
          </TabsList>

          {/* Bulk Actions */}
          {selectedOrders.length > 0 && (
            <div className="flex items-center gap-sm">
              <span className="text-sm text-muted-foreground">
                {selectedOrders.length} selected
              </span>
              <Button
                variant="outline"
                size="sm"
                onClick={() => handleBulkAction({ type: 'delete', orderIds: selectedOrders })}
              >
                <Trash2 className="h-4 w-4 mr-sm" />
                Delete
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={() => setSelectedOrders([])}
              >
                <X className="h-4 w-4 mr-sm" />
                Clear
              </Button>
            </div>
          )}
        </div>

        <TabsContent value={viewMode} className="mt-md">
          {renderView()}
        </TabsContent>
      </Tabs>

      {/* Pagination */}
      {pagination.total > pagination.limit && (
        <div className="flex items-center justify-between">
          <div className="text-sm text-muted-foreground">
            Showing {((pagination.page - 1) * pagination.limit) + 1} to{' '}
            {Math.min(pagination.page * pagination.limit, pagination.total)} of{' '}
            {pagination.total} orders
          </div>
          <div className="flex items-center gap-sm">
            <Button
              variant="outline"
              size="sm"
              disabled={pagination.page === 1}
              onClick={() => setPagination(prev => ({ ...prev, page: prev.page - 1 }))}
            >
              Previous
            </Button>
            <Button
              variant="outline"
              size="sm"
              disabled={!pagination.hasMore}
              onClick={() => setPagination(prev => ({ ...prev, page: prev.page + 1 }))}
            >
              Next
            </Button>
          </div>
        </div>
      )}
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useState } from 'react';
import { Card, Badge, Button, Checkbox } from '@ghxstship/ui';
import type { ProcurementOrder } from '../types';
import { formatCurrency, formatDate, getStatusColor, getPriorityColor, getPaymentStatusColor } from '../types';

interface OrderGridViewProps {
  orders: ProcurementOrder[];
  loading?: boolean;
  selectedOrders: string[];
  onSelectionChange: (orderIds: string[]) => void;
  onOrderClick?: (order: ProcurementOrder) => void;
  onEditOrder?: (order: ProcurementOrder) => void;
  onDeleteOrder?: (order: ProcurementOrder) => void;
  onViewOrder?: (order: ProcurementOrder) => void;
}

export default function OrderGridView({
  orders,
  loading = false,
  selectedOrders,
  onSelectionChange,
  onOrderClick,
  onEditOrder,
  onDeleteOrder,
  onViewOrder,
}: OrderGridViewProps) {
  const [hoveredOrder, setHoveredOrder] = useState<string | null>(null);

  const handleOrderSelection = (orderId: string, checked: boolean) => {
    if (checked) {
      onSelectionChange([...selectedOrders, orderId]);
    } else {
      onSelectionChange(selectedOrders.filter(id => id !== orderId));
    }
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      onSelectionChange(orders.map(order => order.id));
    } else {
      onSelectionChange([]);
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'delivered':
      case 'received':
        return <CheckCircle className="h-3 w-3" />;
      case 'ordered':
        return <Truck className="h-3 w-3" />;
      case 'cancelled':
      case 'rejected':
        return <AlertCircle className="h-3 w-3" />;
      default:
        return <Package className="h-3 w-3" />;
    }
  };

  const getPriorityIcon = (priority: string) => {
    switch (priority) {
      case 'urgent':
        return 'ðŸ”´';
      case 'high':
        return 'ðŸŸ¡';
      case 'medium':
        return 'ðŸŸ¢';
      case 'low':
        return 'ðŸ”µ';
      default:
        return 'âšª';
    }
  };

  const isOverdue = (order: ProcurementOrder) => {
    if (!order.expected_delivery) return false;
    const now = new Date();
    const expectedDate = new Date(order.expected_delivery);
    return expectedDate < now && !['delivered', 'received', 'cancelled'].includes(order.status);
  };

  if (loading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
        {Array.from({ length: 8 }).map((_, index) => (
          <Card key={index} className="p-md">
            <div className="animate-pulse">
              <div className="flex items-center justify-between mb-sm">
                <div className="h-4 bg-muted rounded w-3/4"></div>
                <div className="h-6 bg-muted rounded-full w-16"></div>
              </div>
              <div className="h-3 bg-muted rounded w-1/2 mb-sm"></div>
              <div className="h-3 bg-muted rounded w-full mb-sm"></div>
              <div className="h-3 bg-muted rounded w-2/3 mb-md"></div>
              <div className="flex justify-between items-center">
                <div className="h-5 bg-muted rounded w-20"></div>
                <div className="flex gap-xs">
                  <div className="h-8 w-8 bg-muted rounded"></div>
                  <div className="h-8 w-8 bg-muted rounded"></div>
                </div>
              </div>
            </div>
          </Card>
        ))}
      </div>
    );
  }

  if (orders.length === 0) {
    return (
      <Card className="p-xl text-center">
        <Package className="h-12 w-12 mx-auto mb-md text-muted-foreground opacity-50" />
        <h3 className="text-lg font-medium mb-sm">No orders found</h3>
        <p className="text-muted-foreground">
          No orders match your current filters. Try adjusting your search criteria.
        </p>
      </Card>
    );
  }

  const allSelected = orders.length > 0 && selectedOrders.length === orders.length;
  const someSelected = selectedOrders.length > 0 && selectedOrders.length < orders.length;

  return (
    <div className="space-y-md">
      {/* Selection header */}
      {orders.length > 0 && (
        <div className="flex items-center gap-sm p-sm bg-muted/30 rounded-lg">
          <Checkbox
            checked={allSelected}
            indeterminate={someSelected}
            onCheckedChange={handleSelectAll}
          />
          <span className="text-sm text-muted-foreground">
            {selectedOrders.length > 0 
              ? `${selectedOrders.length} of ${orders.length} orders selected`
              : `Select all ${orders.length} orders`
            }
          </span>
        </div>
      )}

      {/* Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
        {orders.map((order) => {
          const isSelected = selectedOrders.includes(order.id);
          const isHovered = hoveredOrder === order.id;
          const overdue = isOverdue(order);

          return (
            <Card
              key={order.id}
              className={`relative transition-all duration-200 cursor-pointer hover:shadow-md ${
                isSelected ? 'ring-2 ring-primary' : ''
              } ${overdue ? 'border-destructive/50' : ''}`}
              onMouseEnter={() => setHoveredOrder(order.id)}
              onMouseLeave={() => setHoveredOrder(null)}
              onClick={() => onOrderClick?.(order)}
            >
              {/* Selection checkbox */}
              <div className="absolute top-sm left-sm z-10">
                <Checkbox
                  checked={isSelected}
                  onCheckedChange={(checked) => handleOrderSelection(order.id, checked as boolean)}
                  onClick={(e) => e.stopPropagation()}
                />
              </div>

              {/* Overdue indicator */}
              {overdue && (
                <div className="absolute top-sm right-sm z-10">
                  <Badge variant="destructive" className="text-xs">
                    Overdue
                  </Badge>
                </div>
              )}

              {/* Action buttons */}
              {isHovered && !overdue && (
                <div className="absolute top-sm right-sm z-10 flex gap-xs">
                  {onViewOrder && (
                    <Button
                      size="sm"
                      variant="secondary"
                      onClick={(e) => {
                        e.stopPropagation();
                        onViewOrder(order);
                      }}
                    >
                      <Eye className="h-3 w-3" />
                    </Button>
                  )}
                  {onEditOrder && (
                    <Button
                      size="sm"
                      variant="secondary"
                      onClick={(e) => {
                        e.stopPropagation();
                        onEditOrder(order);
                      }}
                    >
                      <Edit className="h-3 w-3" />
                    </Button>
                  )}
                  {onDeleteOrder && (
                    <Button
                      size="sm"
                      variant="destructive"
                      onClick={(e) => {
                        e.stopPropagation();
                        onDeleteOrder(order);
                      }}
                    >
                      <Trash2 className="h-3 w-3" />
                    </Button>
                  )}
                </div>
              )}

              <div className="p-md pt-lg">
                {/* Header */}
                <div className="flex items-start justify-between mb-sm">
                  <div className="flex items-center gap-sm min-w-0 flex-1">
                    <div className="p-sm bg-primary/10 rounded-lg">
                      <Package className="h-4 w-4 text-primary flex-shrink-0" />
                    </div>
                    <div className="min-w-0 flex-1">
                      <h4 className="font-medium truncate">{order.order_number}</h4>
                      <p className="text-sm text-muted-foreground truncate">{order.vendor_name}</p>
                    </div>
                  </div>
                  {!overdue && (
                    <Badge variant={getStatusColor(order.status)} className="ml-sm">
                      {getStatusIcon(order.status)}
                      <span className="ml-xs capitalize">{order.status}</span>
                    </Badge>
                  )}
                </div>

                {/* Priority and Payment Status */}
                <div className="flex items-center gap-sm mb-sm">
                  <div className="flex items-center gap-xs">
                    <span className="text-sm">{getPriorityIcon(order.priority)}</span>
                    <Badge variant={getPriorityColor(order.priority)} className="text-xs">
                      {order.priority}
                    </Badge>
                  </div>
                  <Badge variant={getPaymentStatusColor(order.payment_status)} className="text-xs">
                    {order.payment_status.replace('_', ' ')}
                  </Badge>
                </div>

                {/* Description */}
                {order.description && (
                  <p className="text-sm text-muted-foreground line-clamp-2 mb-sm">
                    {order.description}
                  </p>
                )}

                {/* Amount */}
                <div className="flex items-center justify-between mb-sm">
                  <div className="text-lg font-semibold">
                    {formatCurrency(order.total_amount, order.currency)}
                  </div>
                </div>

                {/* Dates */}
                <div className="space-y-xs text-xs text-muted-foreground mb-sm">
                  <div className="flex items-center gap-xs">
                    <Calendar className="h-3 w-3 flex-shrink-0" />
                    <span>Ordered: {formatDate(order.order_date)}</span>
                  </div>
                  {order.expected_delivery && (
                    <div className="flex items-center gap-xs">
                      <Truck className="h-3 w-3 flex-shrink-0" />
                      <span className={overdue ? 'text-destructive' : ''}>
                        Expected: {formatDate(order.expected_delivery)}
                      </span>
                    </div>
                  )}
                  {order.actual_delivery && (
                    <div className="flex items-center gap-xs">
                      <CheckCircle className="h-3 w-3 flex-shrink-0 text-success" />
                      <span>Delivered: {formatDate(order.actual_delivery)}</span>
                    </div>
                  )}
                </div>

                {/* Tags */}
                {order.tags && order.tags.length > 0 && (
                  <div className="flex flex-wrap gap-xs mb-sm">
                    {order.tags.slice(0, 3).map((tag, index) => (
                      <span
                        key={index}
                        className="px-xs py-xs bg-muted rounded text-xs"
                      >
                        {tag}
                      </span>
                    ))}
                    {order.tags.length > 3 && (
                      <span className="px-xs py-xs bg-muted rounded text-xs">
                        +{order.tags.length - 3} more
                      </span>
                    )}
                  </div>
                )}

                {/* Approval status */}
                {order.approval_required && (
                  <div className="mb-sm">
                    {order.approved_by ? (
                      <div className="flex items-center gap-xs text-xs text-success">
                        <CheckCircle className="h-3 w-3" />
                        <span>Approved</span>
                      </div>
                    ) : (
                      <div className="flex items-center gap-xs text-xs text-warning">
                        <AlertCircle className="h-3 w-3" />
                        <span>Approval Required</span>
                      </div>
                    )}
                  </div>
                )}

                {/* Tracking */}
                {order.tracking_number && (
                  <div className="text-xs text-muted-foreground mb-sm">
                    <strong>Tracking:</strong> {order.tracking_number}
                    {order.carrier && <span> ({order.carrier})</span>}
                  </div>
                )}

                {/* Footer */}
                <div className="flex items-center justify-between mt-md pt-sm border-t border-border">
                  <span className="text-xs text-muted-foreground">
                    {formatDate(order.created_at)}
                  </span>
                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={(e) => {
                      e.stopPropagation();
                      // Show more options menu
                    }}
                  >
                    <MoreHorizontal className="h-3 w-3" />
                  </Button>
                </div>
              </div>
            </Card>
          );
        })}
      </div>
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useState } from 'react';
import { Card, Badge, Button } from '@ghxstship/ui';
import type { ProcurementOrder } from '../types';
import { formatCurrency, formatDate, getStatusColor, getPriorityColor } from '../types';

interface OrderKanbanViewProps {
  orders: ProcurementOrder[];
  loading?: boolean;
  selectedOrders: string[];
  onSelectionChange: (orderIds: string[]) => void;
  onOrderClick?: (order: ProcurementOrder) => void;
  onEditOrder?: (order: ProcurementOrder) => void;
  onDeleteOrder?: (order: ProcurementOrder) => void;
  onViewOrder?: (order: ProcurementOrder) => void;
  onStatusChange?: (order: ProcurementOrder, newStatus: string) => void;
}

const statusColumns = [
  { key: 'draft', label: 'Draft', color: 'bg-secondary/10 border-secondary/20' },
  { key: 'pending', label: 'Pending', color: 'bg-warning/10 border-warning/20' },
  { key: 'approved', label: 'Approved', color: 'bg-success/10 border-success/20' },
  { key: 'ordered', label: 'Ordered', color: 'bg-primary/10 border-primary/20' },
  { key: 'delivered', label: 'Delivered', color: 'bg-success/10 border-success/20' },
  { key: 'cancelled', label: 'Cancelled', color: 'bg-destructive/10 border-destructive/20' },
];

export default function OrderKanbanView({
  orders,
  loading = false,
  selectedOrders,
  onSelectionChange,
  onOrderClick,
  onEditOrder,
  onDeleteOrder,
  onViewOrder,
  onStatusChange,
}: OrderKanbanViewProps) {
  const [draggedOrder, setDraggedOrder] = useState<ProcurementOrder | null>(null);

  const handleDragStart = (e: React.DragEvent, order: ProcurementOrder) => {
    setDraggedOrder(order);
    e.dataTransfer.effectAllowed = 'move';
  };

  const handleDragEnd = () => {
    setDraggedOrder(null);
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';
  };

  const handleDrop = (e: React.DragEvent, status: string) => {
    e.preventDefault();
    if (draggedOrder && draggedOrder.status !== status) {
      onStatusChange?.(draggedOrder, status);
    }
    setDraggedOrder(null);
  };

  const getOrdersByStatus = (status: string) => {
    return orders.filter(order => order.status === status);
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'delivered':
      case 'received':
        return <CheckCircle className="h-3 w-3" />;
      case 'ordered':
        return <Truck className="h-3 w-3" />;
      case 'cancelled':
      case 'rejected':
        return <AlertCircle className="h-3 w-3" />;
      default:
        return <Package className="h-3 w-3" />;
    }
  };

  const getPriorityIcon = (priority: string) => {
    switch (priority) {
      case 'urgent':
        return 'ðŸ”´';
      case 'high':
        return 'ðŸŸ¡';
      case 'medium':
        return 'ðŸŸ¢';
      case 'low':
        return 'ðŸ”µ';
      default:
        return 'âšª';
    }
  };

  const isOverdue = (order: ProcurementOrder) => {
    if (!order.expected_delivery) return false;
    const now = new Date();
    const expectedDate = new Date(order.expected_delivery);
    return expectedDate < now && !['delivered', 'received', 'cancelled'].includes(order.status);
  };

  if (loading) {
    return (
      <div className="flex gap-md h-[600px]">
        {statusColumns.map((column) => (
          <div key={column.key} className="flex-1">
            <Card className={`h-full ${column.color}`}>
              <div className="p-md border-b border-border">
                <div className="h-6 bg-muted rounded w-20 animate-pulse"></div>
              </div>
              <div className="p-md space-y-md">
                {Array.from({ length: 3 }).map((_, index) => (
                  <Card key={index} className="p-md">
                    <div className="animate-pulse space-y-sm">
                      <div className="flex items-center gap-sm">
                        <div className="h-4 w-4 bg-muted rounded"></div>
                        <div className="h-4 bg-muted rounded w-3/4"></div>
                      </div>
                      <div className="h-3 bg-muted rounded w-1/2"></div>
                      <div className="h-3 bg-muted rounded w-full"></div>
                      <div className="h-5 bg-muted rounded w-16"></div>
                    </div>
                  </Card>
                ))}
              </div>
            </Card>
          </div>
        ))}
      </div>
    );
  }

  return (
    <div className="flex gap-md h-[600px] overflow-x-auto">
      {statusColumns.map((column) => {
        const columnOrders = getOrdersByStatus(column.key);

        return (
          <div key={column.key} className="flex-1 min-w-[320px]">
            <Card 
              className={`h-full ${column.color} flex flex-col`}
              onDragOver={handleDragOver}
              onDrop={(e) => handleDrop(e, column.key)}
            >
              {/* Column header */}
              <div className="p-md border-b border-border flex-shrink-0">
                <div className="flex items-center justify-between">
                  <h3 className="font-medium">{column.label}</h3>
                  <Badge variant="secondary">
                    {columnOrders.length}
                  </Badge>
                </div>
              </div>

              {/* Column content */}
              <div className="flex-1 p-md space-y-md overflow-y-auto">
                {columnOrders.length === 0 ? (
                  <div className="text-center py-lg text-muted-foreground">
                    <div className="text-sm">No {column.label.toLowerCase()} orders</div>
                  </div>
                ) : (
                  columnOrders.map((order) => {
                    const isSelected = selectedOrders.includes(order.id);
                    const isDragging = draggedOrder?.id === order.id;
                    const overdue = isOverdue(order);

                    return (
                      <Card
                        key={order.id}
                        className={`cursor-pointer transition-all duration-200 hover:shadow-md ${
                          isSelected ? 'ring-2 ring-primary' : ''
                        } ${isDragging ? 'opacity-50 rotate-2' : ''} ${
                          overdue ? 'border-destructive/50' : ''
                        }`}
                        draggable
                        onDragStart={(e) => handleDragStart(e, order)}
                        onDragEnd={handleDragEnd}
                        onClick={() => onOrderClick?.(order)}
                      >
                        <div className="p-md">
                          {/* Header */}
                          <div className="flex items-start justify-between mb-sm">
                            <div className="flex items-center gap-sm min-w-0 flex-1">
                              <Package className="h-4 w-4 text-muted-foreground flex-shrink-0" />
                              <div className="min-w-0 flex-1">
                                <h4 className="font-medium text-sm truncate">{order.order_number}</h4>
                                <p className="text-xs text-muted-foreground truncate">{order.vendor_name}</p>
                              </div>
                            </div>
                            <div className="flex items-center gap-xs ml-sm">
                              {overdue && (
                                <Badge variant="destructive" className="text-xs">
                                  Overdue
                                </Badge>
                              )}
                              {onViewOrder && (
                                <Button
                                  size="sm"
                                  variant="ghost"
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    onViewOrder(order);
                                  }}
                                  className="h-6 w-6 p-0"
                                >
                                  <Eye className="h-3 w-3" />
                                </Button>
                              )}
                              {onEditOrder && (
                                <Button
                                  size="sm"
                                  variant="ghost"
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    onEditOrder(order);
                                  }}
                                  className="h-6 w-6 p-0"
                                >
                                  <Edit className="h-3 w-3" />
                                </Button>
                              )}
                            </div>
                          </div>

                          {/* Priority and Payment */}
                          <div className="flex items-center gap-sm mb-sm">
                            <div className="flex items-center gap-xs">
                              <span className="text-xs">{getPriorityIcon(order.priority)}</span>
                              <Badge variant={getPriorityColor(order.priority)} className="text-xs">
                                {order.priority}
                              </Badge>
                            </div>
                          </div>

                          {/* Description */}
                          {order.description && (
                            <p className="text-xs text-muted-foreground line-clamp-2 mb-sm">
                              {order.description}
                            </p>
                          )}

                          {/* Amount */}
                          <div className="flex items-center justify-between mb-sm">
                            <div className="font-semibold text-sm">
                              {formatCurrency(order.total_amount, order.currency)}
                            </div>
                          </div>

                          {/* Dates */}
                          <div className="space-y-xs text-xs text-muted-foreground mb-sm">
                            <div className="flex items-center gap-xs">
                              <Calendar className="h-3 w-3 flex-shrink-0" />
                              <span>{formatDate(order.order_date)}</span>
                            </div>
                            {order.expected_delivery && (
                              <div className="flex items-center gap-xs">
                                <Truck className="h-3 w-3 flex-shrink-0" />
                                <span className={overdue ? 'text-destructive' : ''}>
                                  {formatDate(order.expected_delivery)}
                                </span>
                              </div>
                            )}
                          </div>

                          {/* Approval status */}
                          {order.approval_required && (
                            <div className="mb-sm">
                              {order.approved_by ? (
                                <div className="flex items-center gap-xs text-xs text-success">
                                  <CheckCircle className="h-3 w-3" />
                                  <span>Approved</span>
                                </div>
                              ) : (
                                <div className="flex items-center gap-xs text-xs text-warning">
                                  <AlertCircle className="h-3 w-3" />
                                  <span>Needs Approval</span>
                                </div>
                              )}
                            </div>
                          )}

                          {/* Footer */}
                          <div className="flex items-center justify-between pt-sm border-t border-border text-xs text-muted-foreground">
                            <span>{formatDate(order.created_at)}</span>
                            <div className="flex items-center gap-xs">
                              <div className="w-2 h-2 rounded-full bg-current opacity-50"></div>
                              <span>{order.id.slice(-6)}</span>
                            </div>
                          </div>
                        </div>
                      </Card>
                    );
                  })
                )}
              </div>
            </Card>
          </div>
        );
      })}
    </div>
  );
}

'use client';

import { Plus, FileText, CheckCircle, XCircle, Clock, AlertTriangle, Search, Download, Upload, RefreshCw, Eye, Edit, Trash2, Grid3X3, List, LayoutDashboard, Calendar, BarChart3, Filter } from "lucide-react";
import { useState, useEffect, useCallback } from 'react';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { 
  Button, 
  Badge, 
  Card,
  Input,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
  Checkbox
} from '@ghxstship/ui';
import AppDrawer from '@/app/_components/shared/AppDrawer';
import { RequestsService } from './lib/requestsService';
import { CreateRequestClient } from './CreateRequestClient';
import type { ProcurementRequest, RequestFilters, RequestSortOptions } from './types';

interface RequestsClientProps {
  className?: string;
  organizationId?: string;
}

// View Types
type RequestViewMode = 'dashboard' | 'grid' | 'list' | 'kanban' | 'calendar';

export default function RequestsClient({ className, organizationId = 'default-org' }: RequestsClientProps) {
  const t = useTranslations();
  const supabase = createBrowserClient();

  // State management
  const [loading, setLoading] = useState(false);
  const [requests, setRequests] = useState<ProcurementRequest[]>([]);
  const [selectedRequest, setSelectedRequest] = useState<ProcurementRequest | null>(null);
  const [selectedRequests, setSelectedRequests] = useState<string[]>([]);
  const [viewMode, setViewMode] = useState<RequestViewMode>('dashboard');
  const [showCreateDrawer, setShowCreateDrawer] = useState(false);
  const [showRequestDrawer, setShowRequestDrawer] = useState(false);

  // Filters and sorting
  const [filters, setFilters] = useState({
    search: '',
    status: 'all',
    priority: 'all',
    category: 'all'
  });
  const [sort, setSort] = useState<RequestSortOptions>({ field: 'created_at', direction: 'desc' });

  const requestsService = new RequestsService();

  // Field configuration for procurement requests
  const fieldConfig: FieldConfig[] = [
    {
      key: 'title',
      label: 'Request Title',
      type: 'text',
      required: true,
      sortable: true,
      searchable: true
    },
    {
      key: 'requester',
      label: 'Requester',
      type: 'text',
      sortable: true,
      render: (value: unknown) => value?.name || 'Unknown'
    },
    {
      key: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'draft', label: 'Draft' },
        { value: 'submitted', label: 'Submitted' },
        { value: 'under_review', label: 'Under Review' },
        { value: 'approved', label: 'Approved' },
        { value: 'rejected', label: 'Rejected' },
        { value: 'cancelled', label: 'Cancelled' },
        { value: 'converted', label: 'Converted' }
      ],
      filterable: true,
      sortable: true,
      render: (value: string) => {
        const statusConfig = {
          draft: { color: 'gray' as const, icon: FileText },
          submitted: { color: 'blue' as const, icon: Clock },
          under_review: { color: 'yellow' as const, icon: AlertTriangle },
          approved: { color: 'green' as const, icon: CheckCircle },
          rejected: { color: 'red' as const, icon: XCircle },
          cancelled: { color: 'gray' as const, icon: XCircle },
          converted: { color: 'purple' as const, icon: CheckCircle }
        };
        const config = statusConfig[value as keyof typeof statusConfig] || statusConfig.draft;
        const Icon = config.icon;
        return (
          <Badge variant={config.color} className="flex items-center gap-1">
            <Icon className="h-3 w-3" />
            {value.charAt(0).toUpperCase() + value.slice(1).replace('_', ' ')}
          </Badge>
        );
      }
    },
    {
      key: 'priority',
      label: 'Priority',
      type: 'select',
      options: [
        { value: 'low', label: 'Low' },
        { value: 'medium', label: 'Medium' },
        { value: 'high', label: 'High' },
        { value: 'urgent', label: 'Urgent' }
      ],
      filterable: true,
      sortable: true,
      render: (value: string) => {
        const priorityColors = {
          low: 'gray' as const,
          medium: 'blue' as const,
          high: 'yellow' as const,
          urgent: 'red' as const
        };
        return (
          <Badge variant={priorityColors[value as keyof typeof priorityColors] || 'gray'}>
            {value.charAt(0).toUpperCase() + value.slice(1)}
          </Badge>
        );
      }
    },
    {
      key: 'category',
      label: 'Category',
      type: 'select',
      options: [
        { value: 'equipment', label: 'Equipment' },
        { value: 'supplies', label: 'Supplies' },
        { value: 'services', label: 'Services' },
        { value: 'materials', label: 'Materials' },
        { value: 'software', label: 'Software' },
        { value: 'maintenance', label: 'Maintenance' },
        { value: 'other', label: 'Other' }
      ],
      filterable: true,
      sortable: true
    },
    {
      key: 'estimated_total',
      label: 'Estimated Total',
      type: 'currency',
      sortable: true,
      render: (value: number, record: DataRecord) => {
        const currency = record.currency || 'USD';
        return new Intl.NumberFormat('en-US', {
          style: 'currency',
          currency: currency
        }).format(value || 0);
      }
    },
    {
      key: 'project',
      label: 'Project',
      type: 'text',
      sortable: true,
      render: (value: unknown) => value?.name || 'No Project'
    },
    {
      key: 'requested_delivery_date',
      label: 'Requested Delivery',
      type: 'date',
      sortable: true
    },
    {
      key: 'created_at',
      label: 'Created',
      type: 'date',
      sortable: true
    },
    {
      key: 'updated_at',
      label: 'Updated',
      type: 'date',
      sortable: true
    }
  ];

  // Load data function
  const loadData = useCallback(async () => {
    setLoading(true);
    try {
      const { data: requests, error } = await requestsService.getRequests(
        organizationId,
        filters,
        sortConfig
      );

      if (error) {
        toast.error('Failed to load requests', { description: error });
        return;
      }

      // Transform data for DataViews
      const transformedData: DataRecord[] = requests.map(request => ({
        id: request.id!,
        ...request,
        record_type: 'request'
      }));

      setData(transformedData);
    } catch (error) {
      console.error('Error loading requests:', error);
      toast.error('Failed to load requests');
    } finally {
      setLoading(false);
    }
  }, [organizationId, filters, sortConfig]);

  // Load data on mount and when dependencies change
  useEffect(() => {
    loadData();
  }, [loadData]);

  // Handle record selection
  const handleRecordSelect = (record: DataRecord) => {
    setSelectedRecord(record);
    setDrawerOpen(true);
  };

  // Handle record actions
  const handleRecordAction = async (action: string, record: DataRecord) => {
    try {
      switch (action) {
        case 'submit':
          if (record.status === 'draft') {
            await requestsService.submitRequest(record.id);
            toast.success('Request submitted for approval');
            loadData();
          }
          break;
        case 'approve':
          if (record.status === 'submitted' || record.status === 'under_review') {
            // In a real app, this would check permissions
            await requestsService.approveRequest(record.id, 'current-user-id');
            toast.success('Request approved');
            loadData();
          }
          break;
        case 'reject':
          if (record.status === 'submitted' || record.status === 'under_review') {
            await requestsService.rejectRequest(record.id, 'current-user-id', 'Rejected by user');
            toast.success('Request rejected');
            loadData();
          }
          break;
        case 'convert':
          if (record.status === 'approved') {
            const { data: result, error } = await requestsService.convertToPurchaseOrder(record.id);
            if (error) {
              toast.error('Failed to convert to purchase order', { description: error });
            } else {
              toast.success(`Converted to purchase order: ${result?.orderId}`);
              loadData();
            }
          }
          break;
        case 'delete':
          await requestsService.deleteRequest(record.id);
          toast.success('Request deleted');
          loadData();
          break;
      }
    } catch (error) {
      console.error('Error performing action:', error);
      toast.error(`Failed to ${action} request`);
    }
  };

  // Handle search
  const handleSearch = (query: string) => {
    // In a real implementation, this would update filters
    console.log('Search:', query);
  };

  // Handle filter changes
  const handleFilter = (newFilters: FilterConfig[]) => {
    const requestFilters: RequestFilters = {};

    newFilters.forEach(filter => {
      switch (filter.field) {
        case 'status':
          requestFilters.status = filter.value as unknown[];
          break;
        case 'priority':
          requestFilters.priority = filter.value as unknown[];
          break;
        case 'category':
          requestFilters.category = filter.value as unknown[];
          break;
      }
    });

    setFilters(requestFilters);
  };

  // Handle sort changes
  const handleSort = (sorts: SortConfig[]) => {
    if (sorts.length > 0) {
      const sort = sorts[0];
      setSortConfig({
        field: sort.field as unknown,
        direction: sort.direction
      });
    }
  };

  // Handle export
  const handleExport = (data: DataRecord[], format: string) => {
    console.log('Export:', data.length, 'records in', format, 'format');
    toast.success(`Exported ${data.length} requests as ${format.toUpperCase()}`);
  };

  // Handle import
  const handleImport = (data: unknown[]) => {
    console.log('Import:', data.length, 'records');
    toast.success(`Imported ${data.length} requests`);
    loadData();
  };

  // DataView configuration
  const dataViewConfig: DataViewConfig = {
    id: 'procurement-requests',
    name: 'Procurement Requests',
    viewType: 'grid',
    defaultView: 'grid',
    fields: fieldConfig,
    data: data,
    loading: loading,
    onRecordSelect: handleRecordSelect,
    onRecordAction: handleRecordAction,
    onSearch: handleSearch,
    onFilter: handleFilter,
    onSort: handleSort,
    onRefresh: loadData,
    onExport: handleExport,
    onImport: handleImport,
    actions: [
      {
        id: 'submit',
        label: 'Submit',
        icon: 'send',
        variant: 'primary',
        condition: (record) => record.status === 'draft'
      },
      {
        id: 'approve',
        label: 'Approve',
        icon: 'check',
        variant: 'success',
        condition: (record) => ['submitted', 'under_review'].includes(record.status)
      },
      {
        id: 'reject',
        label: 'Reject',
        icon: 'x',
        variant: 'destructive',
        condition: (record) => ['submitted', 'under_review'].includes(record.status)
      },
      {
        id: 'convert',
        label: 'Convert to PO',
        icon: 'arrow-right',
        variant: 'secondary',
        condition: (record) => record.status === 'approved'
      },
      {
        id: 'delete',
        label: 'Delete',
        icon: 'trash',
        variant: 'destructive',
        condition: (record) => ['draft', 'cancelled'].includes(record.status)
      }
    ]
  };

  return (
    <div className={className}>
      <DataViewProvider config={dataViewConfig}>
        <div className="space-y-6">
          {/* Header */}
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-2xl font-semibold text-foreground">Procurement Requests</h1>
              <p className="text-sm text-muted-foreground">
                Manage purchase requisitions and approval workflows
              </p>
            </div>
            <Button 
              onClick={() => setCreateDrawerOpen(true)}
              className="flex items-center gap-2"
            >
              <Plus className="h-4 w-4" />
              New Request
            </Button>
          </div>

          {/* Data Views */}
          <DataViews />

          {/* Request Details Drawer */}
          <UniversalDrawer
            open={drawerOpen}
            onClose={() => setDrawerOpen(false)}
            record={selectedRecord}
            fields={fieldConfig}
            mode="view"
            title="Request Details"
            tabs={[
              {
                key: 'details',
                label: 'Details',
                content: selectedRecord ? (
                  <div className="space-y-4">
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <label className="text-sm font-medium text-muted-foreground">Title</label>
                        <p className="text-sm">{selectedRecord.title}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-muted-foreground">Status</label>
                        <div className="mt-1">
                          {fieldConfig.find(f => f.key === 'status')?.render?.(selectedRecord.status, selectedRecord)}
                        </div>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-muted-foreground">Priority</label>
                        <div className="mt-1">
                          {fieldConfig.find(f => f.key === 'priority')?.render?.(selectedRecord.priority, selectedRecord)}
                        </div>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-muted-foreground">Category</label>
                        <p className="text-sm">{selectedRecord.category}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-muted-foreground">Estimated Total</label>
                        <p className="text-sm">
                          {fieldConfig.find(f => f.key === 'estimated_total')?.render?.(selectedRecord.estimated_total, selectedRecord)}
                        </p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-muted-foreground">Requester</label>
                        <p className="text-sm">{selectedRecord.requester?.name}</p>
                      </div>
                    </div>
                    {selectedRecord.description && (
                      <div>
                        <label className="text-sm font-medium text-muted-foreground">Description</label>
                        <p className="text-sm mt-1">{selectedRecord.description}</p>
                      </div>
                    )}
                    {selectedRecord.business_justification && (
                      <div>
                        <label className="text-sm font-medium text-muted-foreground">Business Justification</label>
                        <p className="text-sm mt-1">{selectedRecord.business_justification}</p>
                      </div>
                    )}
                  </div>
                ) : null
              },
              {
                key: 'items',
                label: 'Items',
                content: selectedRecord?.items ? (
                  <div className="space-y-4">
                    {selectedRecord.items.map((item: any, index: number) => (
                      <div key={index} className="border rounded-lg p-4">
                        <div className="grid grid-cols-2 gap-4">
                          <div>
                            <label className="text-sm font-medium text-muted-foreground">Item Name</label>
                            <p className="text-sm">{item.name}</p>
                          </div>
                          <div>
                            <label className="text-sm font-medium text-muted-foreground">Category</label>
                            <p className="text-sm">{item.category}</p>
                          </div>
                          <div>
                            <label className="text-sm font-medium text-muted-foreground">Quantity</label>
                            <p className="text-sm">{item.quantity} {item.unit}</p>
                          </div>
                          <div>
                            <label className="text-sm font-medium text-muted-foreground">Estimated Price</label>
                            <p className="text-sm">
                              {new Intl.NumberFormat('en-US', {
                                style: 'currency',
                                currency: selectedRecord.currency || 'USD'
                              }).format(item.estimated_total_price || 0)}
                            </p>
                          </div>
                        </div>
                        {item.description && (
                          <div className="mt-2">
                            <label className="text-sm font-medium text-muted-foreground">Description</label>
                            <p className="text-sm mt-1">{item.description}</p>
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="text-sm text-muted-foreground">No items found</p>
                )
              },
              {
                key: 'activity',
                label: 'Activity',
                content: (
                  <div className="space-y-4">
                    <p className="text-sm text-muted-foreground">Activity log will be implemented here</p>
                  </div>
                )
              }
            ]}
          />

          {/* Create Request Drawer */}
          <CreateRequestClient
            open={createDrawerOpen}
            onClose={() => setCreateDrawerOpen(false)}
            onSuccess={() => {
              setCreateDrawerOpen(false);
              loadData();
            }}
            organizationId={organizationId}
          />
        </div>
      </DataViewProvider>
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useState } from 'react';
import { Card, Badge, Button, Checkbox } from '@ghxstship/ui';
import Link from 'next/link';
import type { TrackingItem } from '../types';
import { formatCurrency, formatDate, getStatusColor, getPriorityColor, getPerformanceColor, calculateDeliveryPerformance } from '../types';

interface TrackingGridViewProps {
  items: TrackingItem[];
  loading?: boolean;
  selectedItems: string[];
  onSelectionChange: (itemIds: string[]) => void;
  onItemClick?: (item: TrackingItem) => void;
  onEditItem?: (item: TrackingItem) => void;
  onViewItem?: (item: TrackingItem) => void;
  onTrackPackage?: (item: TrackingItem) => void;
}

export default function TrackingGridView({
  items,
  loading = false,
  selectedItems,
  onSelectionChange,
  onItemClick,
  onEditItem,
  onViewItem,
  onTrackPackage,
}: TrackingGridViewProps) {
  const [hoveredItem, setHoveredItem] = useState<string | null>(null);

  const handleItemSelection = (itemId: string, checked: boolean) => {
    if (checked) {
      onSelectionChange([...selectedItems, itemId]);
    } else {
      onSelectionChange(selectedItems.filter(id => id !== itemId));
    }
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      onSelectionChange(items.map(item => item.id));
    } else {
      onSelectionChange([]);
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'delivered':
        return <CheckCircle className="h-4 w-4 text-success" />;
      case 'shipped':
      case 'in_transit':
      case 'out_for_delivery':
        return <Truck className="h-4 w-4 text-primary" />;
      case 'cancelled':
      case 'returned':
        return <AlertCircle className="h-4 w-4 text-destructive" />;
      default:
        return <Package className="h-4 w-4 text-muted-foreground" />;
    }
  };

  const getPriorityIcon = (priority: string) => {
    switch (priority) {
      case 'urgent':
        return 'ðŸ”´';
      case 'high':
        return 'ðŸŸ¡';
      case 'medium':
        return 'ðŸŸ¢';
      case 'low':
        return 'ðŸ”µ';
      default:
        return 'âšª';
    }
  };

  const isOverdue = (item: TrackingItem) => {
    if (!item.expected_delivery) return false;
    const now = new Date();
    const expectedDate = new Date(item.expected_delivery);
    return expectedDate < now && !['delivered', 'cancelled'].includes(item.status);
  };

  const getDaysUntilDelivery = (item: TrackingItem) => {
    if (!item.expected_delivery) return null;
    const now = new Date();
    const expectedDate = new Date(item.expected_delivery);
    const diffTime = expectedDate.getTime() - now.getTime();
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return diffDays;
  };

  if (loading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
        {Array.from({ length: 8 }).map((_, index) => (
          <Card key={index} className="p-md">
            <div className="animate-pulse">
              <div className="flex items-center justify-between mb-sm">
                <div className="h-4 bg-muted rounded w-3/4"></div>
                <div className="h-6 bg-muted rounded-full w-16"></div>
              </div>
              <div className="h-3 bg-muted rounded w-1/2 mb-sm"></div>
              <div className="h-3 bg-muted rounded w-full mb-sm"></div>
              <div className="h-3 bg-muted rounded w-2/3 mb-md"></div>
              <div className="flex justify-between items-center">
                <div className="h-5 bg-muted rounded w-20"></div>
                <div className="flex gap-xs">
                  <div className="h-8 w-8 bg-muted rounded"></div>
                  <div className="h-8 w-8 bg-muted rounded"></div>
                </div>
              </div>
            </div>
          </Card>
        ))}
      </div>
    );
  }

  if (items.length === 0) {
    return (
      <Card className="p-xl text-center">
        <Package className="h-12 w-12 mx-auto mb-md text-muted-foreground opacity-50" />
        <h3 className="text-lg font-medium mb-sm">No tracking items found</h3>
        <p className="text-muted-foreground">
          No items match your current filters. Try adjusting your search criteria.
        </p>
      </Card>
    );
  }

  const allSelected = items.length > 0 && selectedItems.length === items.length;
  const someSelected = selectedItems.length > 0 && selectedItems.length < items.length;

  return (
    <div className="space-y-md">
      {/* Selection header */}
      {items.length > 0 && (
        <div className="flex items-center gap-sm p-sm bg-muted/30 rounded-lg">
          <Checkbox
            checked={allSelected}
            indeterminate={someSelected}
            onCheckedChange={handleSelectAll}
          />
          <span className="text-sm text-muted-foreground">
            {selectedItems.length > 0 
              ? `${selectedItems.length} of ${items.length} items selected`
              : `Select all ${items.length} items`
            }
          </span>
        </div>
      )}

      {/* Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
        {items.map((item) => {
          const isSelected = selectedItems.includes(item.id);
          const isHovered = hoveredItem === item.id;
          const overdue = isOverdue(item);
          const daysUntilDelivery = getDaysUntilDelivery(item);
          const performance = calculateDeliveryPerformance(item.expected_delivery, item.actual_delivery);

          return (
            <Card
              key={item.id}
              className={`relative transition-all duration-200 cursor-pointer hover:shadow-md ${
                isSelected ? 'ring-2 ring-primary' : ''
              } ${overdue ? 'border-destructive/50' : ''}`}
              onMouseEnter={() => setHoveredItem(item.id)}
              onMouseLeave={() => setHoveredItem(null)}
              onClick={() => onItemClick?.(item)}
            >
              {/* Selection checkbox */}
              <div className="absolute top-sm left-sm z-10">
                <Checkbox
                  checked={isSelected}
                  onCheckedChange={(checked) => handleItemSelection(item.id, checked as boolean)}
                  onClick={(e) => e.stopPropagation()}
                />
              </div>

              {/* Overdue indicator */}
              {overdue && (
                <div className="absolute top-sm right-sm z-10">
                  <Badge variant="destructive" className="text-xs">
                    Overdue
                  </Badge>
                </div>
              )}

              {/* Action buttons */}
              {isHovered && !overdue && (
                <div className="absolute top-sm right-sm z-10 flex gap-xs">
                  {item.tracking_number && onTrackPackage && (
                    <Button
                      size="sm"
                      variant="secondary"
                      onClick={(e) => {
                        e.stopPropagation();
                        onTrackPackage(item);
                      }}
                      title="Track package"
                    >
                      <MapPin className="h-3 w-3" />
                    </Button>
                  )}
                  {onViewItem && (
                    <Button
                      size="sm"
                      variant="secondary"
                      onClick={(e) => {
                        e.stopPropagation();
                        onViewItem(item);
                      }}
                      title="View details"
                    >
                      <Eye className="h-3 w-3" />
                    </Button>
                  )}
                  {onEditItem && (
                    <Button
                      size="sm"
                      variant="secondary"
                      onClick={(e) => {
                        e.stopPropagation();
                        onEditItem(item);
                      }}
                      title="Edit item"
                    >
                      <Edit className="h-3 w-3" />
                    </Button>
                  )}
                </div>
              )}

              <div className="p-md pt-lg">
                {/* Header */}
                <div className="flex items-start justify-between mb-sm">
                  <div className="flex items-center gap-sm min-w-0 flex-1">
                    <div className="p-sm bg-primary/10 rounded-lg">
                      {getStatusIcon(item.status)}
                    </div>
                    <div className="min-w-0 flex-1">
                      <h4 className="font-medium truncate">{item.order_number}</h4>
                      <p className="text-sm text-muted-foreground truncate">{item.vendor_name}</p>
                    </div>
                  </div>
                  {!overdue && (
                    <Badge variant={getStatusColor(item.status)} className="ml-sm">
                      <span className="capitalize">{item.status.replace('_', ' ')}</span>
                    </Badge>
                  )}
                </div>

                {/* Priority and Performance */}
                <div className="flex items-center gap-sm mb-sm">
                  <div className="flex items-center gap-xs">
                    <span className="text-sm">{getPriorityIcon(item.priority)}</span>
                    <Badge variant={getPriorityColor(item.priority)} className="text-xs">
                      {item.priority}
                    </Badge>
                  </div>
                  {item.actual_delivery && (
                    <Badge variant={getPerformanceColor(performance)} className="text-xs">
                      {performance.replace('_', ' ')}
                    </Badge>
                  )}
                </div>

                {/* Description */}
                {item.description && (
                  <p className="text-sm text-muted-foreground line-clamp-2 mb-sm">
                    {item.description}
                  </p>
                )}

                {/* Amount */}
                <div className="flex items-center justify-between mb-sm">
                  <div className="text-lg font-semibold">
                    {formatCurrency(item.total_amount, item.currency)}
                  </div>
                </div>

                {/* Tracking Information */}
                {item.tracking_number && (
                  <div className="mb-sm p-xs bg-muted/30 rounded text-xs">
                    <div className="flex items-center gap-xs">
                      <Truck className="h-3 w-3" />
                      <span className="font-medium">{item.tracking_number}</span>
                    </div>
                    {item.shipping_carrier && (
                      <div className="text-muted-foreground mt-xs">
                        via {item.shipping_carrier}
                      </div>
                    )}
                  </div>
                )}

                {/* Dates */}
                <div className="space-y-xs text-xs text-muted-foreground mb-sm">
                  <div className="flex items-center gap-xs">
                    <Calendar className="h-3 w-3 flex-shrink-0" />
                    <span>Ordered: {formatDate(item.order_date)}</span>
                  </div>
                  {item.expected_delivery && (
                    <div className="flex items-center gap-xs">
                      <Clock className="h-3 w-3 flex-shrink-0" />
                      <span className={overdue ? 'text-destructive' : ''}>
                        Expected: {formatDate(item.expected_delivery)}
                        {daysUntilDelivery !== null && daysUntilDelivery > 0 && (
                          <span className="ml-xs">({daysUntilDelivery} days)</span>
                        )}
                      </span>
                    </div>
                  )}
                  {item.actual_delivery && (
                    <div className="flex items-center gap-xs">
                      <CheckCircle className="h-3 w-3 flex-shrink-0 text-success" />
                      <span>Delivered: {formatDate(item.actual_delivery)}</span>
                    </div>
                  )}
                </div>

                {/* Tags */}
                {item.tags && item.tags.length > 0 && (
                  <div className="flex flex-wrap gap-xs mb-sm">
                    {item.tags.slice(0, 3).map((tag, index) => (
                      <span
                        key={index}
                        className="px-xs py-xs bg-muted rounded text-xs"
                      >
                        {tag}
                      </span>
                    ))}
                    {item.tags.length > 3 && (
                      <span className="px-xs py-xs bg-muted rounded text-xs">
                        +{item.tags.length - 3} more
                      </span>
                    )}
                  </div>
                )}

                {/* Footer */}
                <div className="flex items-center justify-between mt-md pt-sm border-t border-border">
                  <span className="text-xs text-muted-foreground">
                    {formatDate(item.created_at)}
                  </span>
                  {item.tracking_number && (
                    <Button
                      size="sm"
                      variant="ghost"
                      onClick={(e) => {
                        e.stopPropagation();
                        onTrackPackage?.(item);
                      }}
                      className="text-xs"
                    >
                      <ExternalLink className="h-3 w-3 mr-xs" />
                      Track
                    </Button>
                  )}
                </div>
              </div>
            </Card>
          );
        })}
      </div>
    </div>
  );
}

'use client';
