  const [viewMode, setViewMode] = useState<VendorViewMode>('grid');
  const [showFilters, setShowFilters] = useState(false);
  const [stats, setStats] = useState<VendorStats | null>(null);

  // Filters and sorting
  const [filters, setFilters] = useState<VendorFilters>({
    search: '',
    status: 'all',
    business_type: 'all',
  });
  const [sort, setSort] = useState<VendorSort>({
    field: 'created_at',
    direction: 'desc',
  });

  // Pagination
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 50,
    total: 0,
    hasMore: false,
  });

  const vendorService = new VendorService();

  // Load vendors with filters and pagination
  const loadVendors = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      const result = await vendorService.getVendors(
        orgId,
        filters,
        sort,
        pagination.page,
        pagination.limit
      );

      setVendors(result.vendors);
      setPagination(prev => ({
        ...prev,
        total: result.total,
        hasMore: result.hasMore,
      }));

      // Load stats if on dashboard view
      if (viewMode === 'dashboard') {
        const statsResult = await vendorService.getVendorStats(orgId);
        setStats(statsResult);
      }
    } catch (error) {
      console.error('Error loading vendors:', error);
      setError(error instanceof Error ? error.message : 'Failed to load vendors');
    } finally {
      setLoading(false);
    }
  }, [orgId, filters, sort, pagination.page, pagination.limit, viewMode]);

  // Load data on mount and when dependencies change
  useEffect(() => {
    loadVendors();
  }, [loadVendors]);

  // Handle filter changes
  const handleFilterChange = useCallback((newFilters: Partial<VendorFilters>) => {
    setFilters(prev => ({ ...prev, ...newFilters }));
    setPagination(prev => ({ ...prev, page: 1 })); // Reset to first page
  }, []);

  // Handle sort changes
  const handleSortChange = useCallback((newSort: VendorSort) => {
    setSort(newSort);
    setPagination(prev => ({ ...prev, page: 1 })); // Reset to first page
  }, []);

  // Handle view mode changes
  const handleViewModeChange = useCallback((mode: VendorViewMode) => {
    setViewMode(mode);
    setSelectedVendors([]); // Clear selection when changing views
  }, []);

  // Handle vendor selection
  const handleSelectionChange = useCallback((vendorIds: string[]) => {
    setSelectedVendors(vendorIds);
  }, []);

  // Handle individual vendor actions
  const handleVendorClick = useCallback((vendor: Vendor) => {
    // Navigate to vendor detail or open drawer
    console.log('Vendor clicked:', vendor);
  }, []);

  const handleEditVendor = useCallback((vendor: Vendor) => {
    // Open edit drawer
    console.log('Edit vendor:', vendor);
  }, []);

  const handleDeleteVendor = useCallback((vendor: Vendor) => {
    // Show confirmation dialog and delete
    console.log('Delete vendor:', vendor);
  }, []);

  const handleViewVendor = useCallback((vendor: Vendor) => {
    // Open view drawer
    console.log('View vendor:', vendor);
  }, []);

  // Handle bulk actions
  const handleBulkAction = useCallback(async (action: VendorBulkAction) => {
    try {
      setLoading(true);
      await vendorService.bulkUpdateVendors(orgId, action, 'current-user-id');
      await loadVendors(); // Reload data
      setSelectedVendors([]); // Clear selection
    } catch (error) {
      console.error('Bulk action failed:', error);
      setError(error instanceof Error ? error.message : 'Bulk action failed');
    } finally {
      setLoading(false);
    }
  }, [orgId, loadVendors]);

  // Handle status change (for Kanban view)
  const handleStatusChange = useCallback(async (vendor: Vendor, newStatus: string) => {
    try {
      await vendorService.updateVendor(
        orgId,
        vendor.id,
        { status: newStatus as unknown },
        'current-user-id'
      );
      await loadVendors(); // Reload data
    } catch (error) {
      console.error('Status change failed:', error);
      setError(error instanceof Error ? error.message : 'Status change failed');
    }
  }, [orgId, loadVendors]);

  // Render view based on current mode
  const renderView = () => {
    const commonProps = {
      vendors,
      loading,
      selectedVendors,
      onSelectionChange: handleSelectionChange,
      onVendorClick: handleVendorClick,
      onEditVendor: handleEditVendor,
      onDeleteVendor: handleDeleteVendor,
      onViewVendor: handleViewVendor,
    };

    switch (viewMode) {
      case 'grid':
        return <VendorGridView {...commonProps} />;
      case 'table':
        return (
          <VendorTableView
            {...commonProps}
            sort={sort}
            onSortChange={handleSortChange}
          />
        );
      case 'kanban':
        return (
          <VendorKanbanView
            {...commonProps}
            onStatusChange={handleStatusChange}
          />
        );
      case 'dashboard':
        return (
          <VendorDashboardView
            vendors={vendors}
            loading={loading}
            stats={stats || undefined}
            onVendorClick={handleVendorClick}
          />
        );
      default:
        return <VendorGridView {...commonProps} />;
    }
  };

  if (error) {
    return (
      <Card className="p-xl text-center">
        <div className="text-destructive mb-md">
          <Building className="h-12 w-12 mx-auto mb-md opacity-50" />
          <h3 className="text-lg font-medium mb-sm">Error Loading Vendors</h3>
          <p className="text-sm">{error}</p>
        </div>
        <Button onClick={loadVendors} variant="outline">
          <RefreshCw className="h-4 w-4 mr-sm" />
          Try Again
        </Button>
      </Card>
    );
  }

  return (
    <div className="space-y-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-sm">
          <Building className="h-5 w-5" />
          <h3 className="text-lg font-semibold">Procurement Vendors</h3>
          <Badge variant="secondary">
            {vendors.length} vendor{vendors.length !== 1 ? 's' : ''}
          </Badge>
        </div>
        <div className="flex items-center gap-sm">
          <CreateVendorClient orgId={orgId} onVendorCreated={loadVendors} />
          <Button variant="outline" size="sm">
            <Upload className="h-4 w-4 mr-sm" />
            Import
          </Button>
          <Button variant="outline" size="sm">
            <Download className="h-4 w-4 mr-sm" />
            Export
          </Button>
        </div>
      </div>

      {/* Filters and Search */}
      <Card className="p-md">
        <div className="flex flex-col lg:flex-row gap-md">
          {/* Search */}
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <UnifiedInput
              placeholder="Search vendors..."
              value={filters.search || ''}
              onChange={(e) => handleFilterChange({ search: e.target.value })}
              className="pl-10"
            />
          </div>

          {/* Status Filter */}
          <Select
            value={filters.status || 'all'}
            onValueChange={(value) => handleFilterChange({ status: value as unknown })}
          >
            <SelectTrigger className="w-[140px]">
              <SelectValue placeholder="All Status" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Status</SelectItem>
              <SelectItem value="active">Active</SelectItem>
              <SelectItem value="inactive">Inactive</SelectItem>
              <SelectItem value="pending">Pending</SelectItem>
              <SelectItem value="suspended">Suspended</SelectItem>
            </SelectContent>
          </Select>

          {/* Business Type Filter */}
          <Select
            value={filters.business_type || 'all'}
            onValueChange={(value) => handleFilterChange({ business_type: value as unknown })}
          >
            <SelectTrigger className="w-[140px]">
              <SelectValue placeholder="All Types" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Types</SelectItem>
              <SelectItem value="individual">Individual</SelectItem>
              <SelectItem value="company">Company</SelectItem>
              <SelectItem value="agency">Agency</SelectItem>
            </SelectContent>
          </Select>

          {/* Advanced Filters Toggle */}
          <Button
            variant="outline"
            size="sm"
            onClick={() => setShowFilters(!showFilters)}
          >
            <Filter className="h-4 w-4 mr-sm" />
            Filters
          </Button>
        </div>

        {/* Advanced Filters */}
        {showFilters && (
          <div className="mt-md pt-md border-t border-border">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-md">
              {/* Category Filter */}
              <div>
                <label className="text-sm font-medium mb-sm block">Category</label>
                <UnifiedInput
                  placeholder="Filter by category"
                  value={filters.primary_category || ''}
                  onChange={(e) => handleFilterChange({ primary_category: e.target.value })}
                />
              </div>

              {/* Rating Filter */}
              <div>
                <label className="text-sm font-medium mb-sm block">Min Rating</label>
                <UnifiedInput
                  type="number"
                  min="1"
                  max="5"
                  step="0.1"
                  placeholder="Min rating"
                  value={filters.rating_min || ''}
                  onChange={(e) => handleFilterChange({ 
                    rating_min: e.target.value ? Number(e.target.value) : undefined 
                  })}
                />
              </div>

              {/* Rate Range */}
              <div>
                <label className="text-sm font-medium mb-sm block">Hourly Rate Range</label>
                <div className="flex gap-sm">
                  <UnifiedInput
                    type="number"
                    placeholder="Min"
                    value={filters.hourly_rate_min || ''}
                    onChange={(e) => handleFilterChange({ 
                      hourly_rate_min: e.target.value ? Number(e.target.value) : undefined 
                    })}
                  />
                  <UnifiedInput
                    type="number"
                    placeholder="Max"
                    value={filters.hourly_rate_max || ''}
                    onChange={(e) => handleFilterChange({ 
                      hourly_rate_max: e.target.value ? Number(e.target.value) : undefined 
                    })}
                  />
                </div>
              </div>
            </div>
          </div>
        )}
      </Card>

      {/* View Mode Tabs */}
      <Tabs value={viewMode} onValueChange={(value) => handleViewModeChange(value as VendorViewMode)}>
        <div className="flex items-center justify-between">
          <TabsList>
            <TabsTrigger value="grid">
              <Grid className="h-4 w-4 mr-sm" />
              Grid
            </TabsTrigger>
            <TabsTrigger value="table">
              <Columns className="h-4 w-4 mr-sm" />
              Table
            </TabsTrigger>
            <TabsTrigger value="kanban">
              <Columns className="h-4 w-4 mr-sm" />
              Kanban
            </TabsTrigger>
            <TabsTrigger value="dashboard">
              <BarChart3 className="h-4 w-4 mr-sm" />
              Dashboard
            </TabsTrigger>
          </TabsList>

          {/* Bulk Actions */}
          {selectedVendors.length > 0 && (
            <div className="flex items-center gap-sm">
              <span className="text-sm text-muted-foreground">
                {selectedVendors.length} selected
              </span>
              <Button
                variant="outline"
                size="sm"
                onClick={() => handleBulkAction({ type: 'delete', vendorIds: selectedVendors })}
              >
                <Trash2 className="h-4 w-4 mr-sm" />
                Delete
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={() => setSelectedVendors([])}
              >
                <X className="h-4 w-4 mr-sm" />
                Clear
              </Button>
            </div>
          )}
        </div>

        <TabsContent value={viewMode} className="mt-md">
          {renderView()}
        </TabsContent>
      </Tabs>

      {/* Pagination */}
      {pagination.total > pagination.limit && (
        <div className="flex items-center justify-between">
          <div className="text-sm text-muted-foreground">
            Showing {((pagination.page - 1) * pagination.limit) + 1} to{' '}
            {Math.min(pagination.page * pagination.limit, pagination.total)} of{' '}
            {pagination.total} vendors
          </div>
          <div className="flex items-center gap-sm">
            <Button
              variant="outline"
              size="sm"
              disabled={pagination.page === 1}
              onClick={() => setPagination(prev => ({ ...prev, page: prev.page - 1 }))}
            >
              Previous
            </Button>
            <Button
              variant="outline"
              size="sm"
              disabled={!pagination.hasMore}
              onClick={() => setPagination(prev => ({ ...prev, page: prev.page + 1 }))}
            >
              Next
            </Button>
          </div>
        </div>
      )}
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useState } from 'react';
import { Card, Badge, Button, Checkbox } from '@ghxstship/ui';
import type { Vendor } from '../types';
import { formatCurrency, formatDate, getStatusColor, getBusinessTypeColor, formatAddress } from '../types';

interface VendorGridViewProps {
  vendors: Vendor[];
  loading?: boolean;
  selectedVendors: string[];
  onSelectionChange: (vendorIds: string[]) => void;
  onVendorClick?: (vendor: Vendor) => void;
  onEditVendor?: (vendor: Vendor) => void;
  onDeleteVendor?: (vendor: Vendor) => void;
  onViewVendor?: (vendor: Vendor) => void;
}

export default function VendorGridView({
  vendors,
  loading = false,
  selectedVendors,
  onSelectionChange,
  onVendorClick,
  onEditVendor,
  onDeleteVendor,
  onViewVendor,
}: VendorGridViewProps) {
  const [hoveredVendor, setHoveredVendor] = useState<string | null>(null);

  const handleVendorSelection = (vendorId: string, checked: boolean) => {
    if (checked) {
      onSelectionChange([...selectedVendors, vendorId]);
    } else {
      onSelectionChange(selectedVendors.filter(id => id !== vendorId));
    }
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      onSelectionChange(vendors.map(vendor => vendor.id));
    } else {
      onSelectionChange([]);
    }
  };

  const renderStars = (rating?: number) => {
    if (!rating) return null;

    return (
      <div className="flex items-center gap-xs">
        {[1, 2, 3, 4, 5].map((star) => (
          <Star
            key={star}
            className={`h-3 w-3 ${
              star <= rating ? 'text-warning fill-current' : 'text-muted-foreground'
            }`}
          />
        ))}
        <span className="text-xs text-muted-foreground ml-xs">({rating.toFixed(1)})</span>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
        {Array.from({ length: 8 }).map((_, index) => (
          <Card key={index} className="p-md">
            <div className="animate-pulse">
              <div className="flex items-center justify-between mb-sm">
                <div className="h-4 bg-muted rounded w-3/4"></div>
                <div className="h-6 bg-muted rounded-full w-16"></div>
              </div>
              <div className="h-3 bg-muted rounded w-1/2 mb-sm"></div>
              <div className="h-3 bg-muted rounded w-full mb-sm"></div>
              <div className="h-3 bg-muted rounded w-2/3 mb-md"></div>
              <div className="flex justify-between items-center">
                <div className="h-5 bg-muted rounded w-20"></div>
                <div className="flex gap-xs">
                  <div className="h-8 w-8 bg-muted rounded"></div>
                  <div className="h-8 w-8 bg-muted rounded"></div>
                </div>
              </div>
            </div>
          </Card>
        ))}
      </div>
    );
  }

  if (vendors.length === 0) {
    return (
      <Card className="p-xl text-center">
        <Building className="h-12 w-12 mx-auto mb-md text-muted-foreground opacity-50" />
        <h3 className="text-lg font-medium mb-sm">No vendors found</h3>
        <p className="text-muted-foreground">
          No vendors match your current filters. Try adjusting your search criteria.
        </p>
      </Card>
    );
  }

  const allSelected = vendors.length > 0 && selectedVendors.length === vendors.length;
  const someSelected = selectedVendors.length > 0 && selectedVendors.length < vendors.length;

  return (
    <div className="space-y-md">
      {/* Selection header */}
      {vendors.length > 0 && (
        <div className="flex items-center gap-sm p-sm bg-muted/30 rounded-lg">
          <Checkbox
            checked={allSelected}
            indeterminate={someSelected}
            onCheckedChange={handleSelectAll}
          />
          <span className="text-sm text-muted-foreground">
            {selectedVendors.length > 0 
              ? `${selectedVendors.length} of ${vendors.length} vendors selected`
              : `Select all ${vendors.length} vendors`
            }
          </span>
        </div>
      )}

      {/* Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
        {vendors.map((vendor) => {
          const isSelected = selectedVendors.includes(vendor.id);
          const isHovered = hoveredVendor === vendor.id;

          return (
            <Card
              key={vendor.id}
              className={`relative transition-all duration-200 cursor-pointer hover:shadow-md ${
                isSelected ? 'ring-2 ring-primary' : ''
              }`}
              onMouseEnter={() => setHoveredVendor(vendor.id)}
              onMouseLeave={() => setHoveredVendor(null)}
              onClick={() => onVendorClick?.(vendor)}
            >
              {/* Selection checkbox */}
              <div className="absolute top-sm left-sm z-10">
                <Checkbox
                  checked={isSelected}
                  onCheckedChange={(checked) => handleVendorSelection(vendor.id, checked as boolean)}
                  onClick={(e) => e.stopPropagation()}
                />
              </div>

              {/* Action buttons */}
              {isHovered && (
                <div className="absolute top-sm right-sm z-10 flex gap-xs">
                  {onViewVendor && (
                    <Button
                      size="sm"
                      variant="secondary"
                      onClick={(e) => {
                        e.stopPropagation();
                        onViewVendor(vendor);
                      }}
                    >
                      <Eye className="h-3 w-3" />
                    </Button>
                  )}
                  {onEditVendor && (
                    <Button
                      size="sm"
                      variant="secondary"
                      onClick={(e) => {
                        e.stopPropagation();
                        onEditVendor(vendor);
                      }}
                    >
                      <Edit className="h-3 w-3" />
                    </Button>
                  )}
                  {onDeleteVendor && (
                    <Button
                      size="sm"
                      variant="destructive"
                      onClick={(e) => {
                        e.stopPropagation();
                        onDeleteVendor(vendor);
                      }}
                    >
                      <Trash2 className="h-3 w-3" />
                    </Button>
                  )}
                </div>
              )}

              <div className="p-md pt-lg">
                {/* Header */}
                <div className="flex items-start justify-between mb-sm">
                  <div className="flex items-center gap-sm min-w-0 flex-1">
                    <div className="p-sm bg-primary/10 rounded-lg">
                      <Building className="h-4 w-4 text-primary flex-shrink-0" />
                    </div>
                    <div className="min-w-0 flex-1">
                      <h4 className="font-medium truncate">{vendor.display_name}</h4>
                      <p className="text-sm text-muted-foreground truncate">{vendor.business_name}</p>
                    </div>
                  </div>
                  <Badge variant={getStatusColor(vendor.status)} className="ml-sm">
                    {vendor.status}
                  </Badge>
                </div>

                {/* Business type and category */}
                <div className="flex items-center gap-sm mb-sm">
                  <span className={`px-sm py-xs rounded-full text-xs font-medium ${getBusinessTypeColor(vendor.business_type)}`}>
                    {vendor.business_type}
                  </span>
                  {vendor.primary_category && (
                    <span className="text-xs text-muted-foreground uppercase tracking-wide truncate">
                      {vendor.primary_category}
                    </span>
                  )}
                </div>

                {/* Rating */}
                {vendor.rating && (
                  <div className="mb-sm">
                    {renderStars(vendor.rating)}
                  </div>
                )}

                {/* Bio/Description */}
                {vendor.bio && (
                  <p className="text-sm text-muted-foreground line-clamp-2 mb-sm">
                    {vendor.bio}
                  </p>
                )}

                {/* Hourly rate */}
                {vendor.hourly_rate && (
                  <div className="flex items-center justify-between mb-sm">
                    <div className="text-lg font-semibold">
                      {formatCurrency(vendor.hourly_rate, vendor.currency)}/hr
                    </div>
                  </div>
                )}

                {/* Contact info */}
                <div className="space-y-xs text-xs text-muted-foreground mb-sm">
                  {vendor.email && (
                    <div className="flex items-center gap-xs">
                      <Mail className="h-3 w-3 flex-shrink-0" />
                      <span className="truncate">{vendor.email}</span>
                    </div>
                  )}
                  {vendor.phone && (
                    <div className="flex items-center gap-xs">
                      <Phone className="h-3 w-3 flex-shrink-0" />
                      <span className="truncate">{vendor.phone}</span>
                    </div>
                  )}
                  {vendor.website && (
                    <div className="flex items-center gap-xs">
                      <Globe className="h-3 w-3 flex-shrink-0" />
                      <span className="truncate">Website</span>
                    </div>
                  )}
                  {vendor.address && (
                    <div className="flex items-center gap-xs">
                      <MapPin className="h-3 w-3 flex-shrink-0" />
                      <span className="truncate">{formatAddress(vendor.address)}</span>
                    </div>
                  )}
                </div>

                {/* Skills */}
                {vendor.skills && vendor.skills.length > 0 && (
                  <div className="flex flex-wrap gap-xs mb-sm">
                    {vendor.skills.slice(0, 3).map((skill, index) => (
                      <span
                        key={index}
                        className="px-xs py-xs bg-muted rounded text-xs"
                      >
                        {skill}
                      </span>
                    ))}
                    {vendor.skills.length > 3 && (
                      <span className="px-xs py-xs bg-muted rounded text-xs">
                        +{vendor.skills.length - 3} more
                      </span>
                    )}
                  </div>
                )}

                {/* Stats */}
                {(vendor.total_projects || vendor.total_reviews) && (
                  <div className="flex items-center justify-between text-xs text-muted-foreground mb-sm">
                    {vendor.total_projects && (
                      <span>{vendor.total_projects} projects</span>
                    )}
                    {vendor.total_reviews && (
                      <span>{vendor.total_reviews} reviews</span>
                    )}
                  </div>
                )}

                {/* Footer */}
                <div className="flex items-center justify-between mt-md pt-sm border-t border-border">
                  <span className="text-xs text-muted-foreground">
                    {formatDate(vendor.created_at)}
                  </span>
                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={(e) => {
                      e.stopPropagation();
                      // Show more options menu
                    }}
                  >
                    <MoreHorizontal className="h-3 w-3" />
                  </Button>
                </div>
              </div>
            </Card>
          );
        })}
      </div>
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useState } from 'react';
import { Card, Badge, Button } from '@ghxstship/ui';
import type { Vendor } from '../types';
import { formatCurrency, formatDate, getStatusColor, getBusinessTypeColor } from '../types';

interface VendorKanbanViewProps {
  vendors: Vendor[];
  loading?: boolean;
  selectedVendors: string[];
  onSelectionChange: (vendorIds: string[]) => void;
  onVendorClick?: (vendor: Vendor) => void;
  onEditVendor?: (vendor: Vendor) => void;
  onDeleteVendor?: (vendor: Vendor) => void;
  onViewVendor?: (vendor: Vendor) => void;
  onStatusChange?: (vendor: Vendor, newStatus: string) => void;
}

const statusColumns = [
  { key: 'active', label: 'Active', color: 'bg-success/10 border-success/20' },
  { key: 'pending', label: 'Pending', color: 'bg-warning/10 border-warning/20' },
  { key: 'inactive', label: 'Inactive', color: 'bg-secondary/10 border-secondary/20' },
  { key: 'suspended', label: 'Suspended', color: 'bg-destructive/10 border-destructive/20' },
];

export default function VendorKanbanView({
  vendors,
  loading = false,
  selectedVendors,
  onSelectionChange,
  onVendorClick,
  onEditVendor,
  onDeleteVendor,
  onViewVendor,
  onStatusChange,
}: VendorKanbanViewProps) {
  const [draggedVendor, setDraggedVendor] = useState<Vendor | null>(null);

  const handleDragStart = (e: React.DragEvent, vendor: Vendor) => {
    setDraggedVendor(vendor);
    e.dataTransfer.effectAllowed = 'move';
  };

  const handleDragEnd = () => {
    setDraggedVendor(null);
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';
  };

  const handleDrop = (e: React.DragEvent, status: string) => {
    e.preventDefault();
    if (draggedVendor && draggedVendor.status !== status) {
      onStatusChange?.(draggedVendor, status);
    }
    setDraggedVendor(null);
  };

  const getVendorsByStatus = (status: string) => {
    return vendors.filter(vendor => vendor.status === status);
  };

  const renderStars = (rating?: number) => {
    if (!rating) return null;

    return (
      <div className="flex items-center gap-xs">
        {[1, 2, 3, 4, 5].map((star) => (
          <Star
            key={star}
            className={`h-3 w-3 ${
              star <= rating ? 'text-warning fill-current' : 'text-muted-foreground'
            }`}
          />
        ))}
        <span className="text-xs text-muted-foreground ml-xs">({rating.toFixed(1)})</span>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="flex gap-md h-[600px]">
        {statusColumns.map((column) => (
          <div key={column.key} className="flex-1">
            <Card className={`h-full ${column.color}`}>
              <div className="p-md border-b border-border">
                <div className="h-6 bg-muted rounded w-20 animate-pulse"></div>
              </div>
              <div className="p-md space-y-md">
                {Array.from({ length: 3 }).map((_, index) => (
                  <Card key={index} className="p-md">
                    <div className="animate-pulse space-y-sm">
                      <div className="flex items-center gap-sm">
                        <div className="h-4 w-4 bg-muted rounded"></div>
                        <div className="h-4 bg-muted rounded w-3/4"></div>
                      </div>
                      <div className="h-3 bg-muted rounded w-1/2"></div>
                      <div className="h-3 bg-muted rounded w-full"></div>
                      <div className="h-5 bg-muted rounded w-16"></div>
                    </div>
                  </Card>
                ))}
              </div>
            </Card>
          </div>
        ))}
      </div>
    );
  }

  return (
    <div className="flex gap-md h-[600px] overflow-x-auto">
      {statusColumns.map((column) => {
        const columnVendors = getVendorsByStatus(column.key);

        return (
          <div key={column.key} className="flex-1 min-w-[300px]">
            <Card 
              className={`h-full ${column.color} flex flex-col`}
              onDragOver={handleDragOver}
              onDrop={(e) => handleDrop(e, column.key)}
            >
              {/* Column header */}
              <div className="p-md border-b border-border flex-shrink-0">
                <div className="flex items-center justify-between">
                  <h3 className="font-medium">{column.label}</h3>
                  <Badge variant="secondary">
                    {columnVendors.length}
                  </Badge>
                </div>
              </div>

              {/* Column content */}
              <div className="flex-1 p-md space-y-md overflow-y-auto">
                {columnVendors.length === 0 ? (
                  <div className="text-center py-lg text-muted-foreground">
                    <div className="text-sm">No {column.label.toLowerCase()} vendors</div>
                  </div>
                ) : (
                  columnVendors.map((vendor) => {
                    const isSelected = selectedVendors.includes(vendor.id);
                    const isDragging = draggedVendor?.id === vendor.id;

                    return (
                      <Card
                        key={vendor.id}
                        className={`cursor-pointer transition-all duration-200 hover:shadow-md ${
                          isSelected ? 'ring-2 ring-primary' : ''
                        } ${isDragging ? 'opacity-50 rotate-2' : ''}`}
                        draggable
                        onDragStart={(e) => handleDragStart(e, vendor)}
                        onDragEnd={handleDragEnd}
                        onClick={() => onVendorClick?.(vendor)}
                      >
                        <div className="p-md">
                          {/* Header */}
                          <div className="flex items-start justify-between mb-sm">
                            <div className="flex items-center gap-sm min-w-0 flex-1">
                              <Building className="h-4 w-4 text-muted-foreground flex-shrink-0" />
                              <div className="min-w-0 flex-1">
                                <h4 className="font-medium text-sm truncate">{vendor.display_name}</h4>
                                <p className="text-xs text-muted-foreground truncate">{vendor.business_name}</p>
                              </div>
                            </div>
                            <div className="flex items-center gap-xs ml-sm">
                              {onViewVendor && (
                                <Button
                                  size="sm"
                                  variant="ghost"
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    onViewVendor(vendor);
                                  }}
                                  className="h-6 w-6 p-0"
                                >
                                  <Eye className="h-3 w-3" />
                                </Button>
                              )}
                              {onEditVendor && (
                                <Button
                                  size="sm"
                                  variant="ghost"
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    onEditVendor(vendor);
                                  }}
                                  className="h-6 w-6 p-0"
                                >
                                  <Edit className="h-3 w-3" />
                                </Button>
                              )}
                              {onDeleteVendor && (
                                <Button
                                  size="sm"
                                  variant="ghost"
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    onDeleteVendor(vendor);
                                  }}
                                  className="h-6 w-6 p-0"
                                >
                                  <Trash2 className="h-3 w-3" />
                                </Button>
                              )}
                            </div>
                          </div>

                          {/* Business type and category */}
                          <div className="flex items-center gap-sm mb-sm">
                            <span className={`px-sm py-xs rounded-full text-xs font-medium ${getBusinessTypeColor(vendor.business_type)}`}>
                              {vendor.business_type}
                            </span>
                            {vendor.primary_category && (
                              <span className="text-xs text-muted-foreground truncate">
                                {vendor.primary_category}
                              </span>
                            )}
                          </div>

                          {/* Rating */}
                          {vendor.rating && (
                            <div className="mb-sm">
                              {renderStars(vendor.rating)}
                            </div>
                          )}

                          {/* Bio */}
                          {vendor.bio && (
                            <p className="text-xs text-muted-foreground line-clamp-2 mb-sm">
                              {vendor.bio}
                            </p>
                          )}

                          {/* Hourly rate */}
                          {vendor.hourly_rate && (
                            <div className="flex items-center justify-between mb-sm">
                              <div className="font-semibold text-sm">
                                {formatCurrency(vendor.hourly_rate, vendor.currency)}/hr
                              </div>
                            </div>
                          )}

                          {/* Contact info */}
                          <div className="space-y-xs text-xs text-muted-foreground mb-sm">
                            {vendor.email && (
                              <div className="flex items-center gap-xs">
                                <Mail className="h-3 w-3 flex-shrink-0" />
                                <span className="truncate">{vendor.email}</span>
                              </div>
                            )}
                            {vendor.phone && (
                              <div className="flex items-center gap-xs">
                                <Phone className="h-3 w-3 flex-shrink-0" />
                                <span className="truncate">{vendor.phone}</span>
                              </div>
                            )}
                          </div>

                          {/* Skills */}
                          {vendor.skills && vendor.skills.length > 0 && (
                            <div className="flex flex-wrap gap-xs mb-sm">
                              {vendor.skills.slice(0, 2).map((skill, index) => (
                                <span
                                  key={index}
                                  className="px-xs py-xs bg-muted rounded text-xs"
                                >
                                  {skill}
                                </span>
                              ))}
                              {vendor.skills.length > 2 && (
                                <span className="px-xs py-xs bg-muted rounded text-xs">
                                  +{vendor.skills.length - 2}
                                </span>
                              )}
                            </div>
                          )}

                          {/* Stats */}
                          {(vendor.total_projects || vendor.total_reviews) && (
                            <div className="flex items-center justify-between text-xs text-muted-foreground mb-sm">
                              {vendor.total_projects && (
                                <span>{vendor.total_projects} projects</span>
                              )}
                              {vendor.total_reviews && (
                                <span>{vendor.total_reviews} reviews</span>
                              )}
                            </div>
                          )}

                          {/* Footer */}
                          <div className="flex items-center justify-between pt-sm border-t border-border text-xs text-muted-foreground">
                            <span>{formatDate(vendor.created_at)}</span>
                            <div className="flex items-center gap-xs">
                              <div className="w-2 h-2 rounded-full bg-current opacity-50"></div>
                              <span>{vendor.id.slice(-6)}</span>
                            </div>
                          </div>
                        </div>
                      </Card>
                    );
                  })
                )}
              </div>
            </Card>
          </div>
        );
      })}
    </div>
  );
}

