        <div className="w-10 h-10 rounded-full bg-primary/10 flex items-center justify-center">
          {item.icon && <item.icon className="w-5 h-5 text-primary" />}
        </div>
        <div>
          <p className="font-medium">{item.label}</p>
          <p className="text-sm text-muted-foreground">{item.value}</p>
        </div>
      </div>
      {item.status && (
        <Badge variant={item.status === 'complete' ? 'success' : 'warning'}>
          {item.status}
        </Badge>
      )}
    </div>
  </Card>
));

ProfileItemRow.displayName = 'ProfileItemRow';

interface ProfileOptimizedClientProps {
  orgId: string;
  userId: string;
}

export default function ProfileOptimizedClient({ orgId, userId }: ProfileOptimizedClientProps) {
  const [loading, setLoading] = useState(true);
  const [profileData, setProfileData] = useState<ProfileData | null>(null);
  const [activeSection, setActiveSection] = useState('overview');
  const [searchQuery, setSearchQuery] = useState('');
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerMode, setDrawerMode] = useState<'view' | 'edit'>('view');
  const { toast } = useToast();
  const supabase = createBrowserClient();

  // Optimized data fetching with pagination
  const fetchProfileData = useCallback(async (section?: string) => {
    try {
      setLoading(true);

      // Use pagination for large datasets
      const PAGE_SIZE = 50;
      const query = supabase
        .from('users')
        .select(`
          *,
          memberships!inner(
            organization_id,
            role,
            status
          )
        `)
        .eq('auth_id', userId)
        .range(0, PAGE_SIZE - 1);

      if (section && section !== 'overview') {
        // Fetch only specific section data
        query.select(`${section}_data`);
      }

      const { data, error } = await query.single();

      if (error) throw error;

      setProfileData(data);
    } catch (error) {
      console.error('Error fetching profile:', error);
      toast({
        title: 'Error',
        description: 'Failed to load profile data',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  }, [userId, supabase, toast]);

  // Lazy load sections
  useEffect(() => {
    fetchProfileData(activeSection);
  }, [activeSection, fetchProfileData]);

  // Memoized filtered items
  const filteredItems = useMemo(() => {
    if (!profileData) return [];

    const items = Object.entries(profileData)
      .filter(([key, value]) => {
        if (!searchQuery) return true;
        return key.toLowerCase().includes(searchQuery.toLowerCase()) ||
               String(value).toLowerCase().includes(searchQuery.toLowerCase());
      })
      .map(([key, value]) => ({
        id: key,
        label: key.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase()),
        value: String(value),
        icon: User,
        status: value ? 'complete' : 'incomplete'
      }));

    return items;
  }, [profileData, searchQuery]);

  // Module configuration for template
  const moduleConfig = useMemo(() => ({
    id: 'profile',
    name: 'Profile',
    description: 'User profile management',
    icon: User,
    baseRoute: '/profile',
    tabs: PROFILE_SECTIONS.map(section => ({
      id: section.id,
      label: section.label,
      icon: section.icon,
      route: `/profile/${section.id}`,
      component: section.component,
    })),
    permissions: {
      view: ['profile:view'],
      create: ['profile:create'],
      update: ['profile:update'],
      delete: ['profile:delete'],
    },
    features: {
      search: true,
      filter: true,
      sort: true,
      export: true,
      import: false,
      bulkActions: false,
      realtime: true,
    },
    views: ['grid', 'list', 'kanban'],
    defaultView: 'list',
  }), []);

  if (loading && !profileData) {
    return (
      <div className="space-y-4">
        {[...Array(5)].map((_, i) => (
          <Skeleton key={i} className="h-20 w-full" />
        ))}
      </div>
    );
  }

  return (
    <StateManagerProvider>
      <DataViewProvider
        config={{
          module: 'profile',
          defaultView: 'list',
          features: moduleConfig.features,
        }}
      >
        <div className="space-y-6">
          {/* Header with optimized actions */}
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold">Profile Management</h1>
              <p className="text-muted-foreground">
                Manage your profile information and settings
              </p>
            </div>
            <div className="flex items-center gap-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => fetchProfileData(activeSection)}
              >
                Refresh
              </Button>
              <Button
                size="sm"
                onClick={() => {
                  setDrawerMode('edit');
                  setDrawerOpen(true);
                }}
              >
                Edit Profile
              </Button>
            </div>
          </div>

          {/* Optimized tabs with lazy loading */}
          <Tabs value={activeSection} onValueChange={setActiveSection}>
            <TabsList className="grid grid-cols-5 w-full">
              {PROFILE_SECTIONS.map((section) => (
                <TabsTrigger key={section.id} value={section.id}>
                  <section.icon className="w-4 h-4 mr-2" />
                  {section.label}
                </TabsTrigger>
              ))}
            </TabsList>

            {PROFILE_SECTIONS.map((section) => (
              <TabsContent key={section.id} value={section.id} className="mt-6">
                <Card className="p-6">
                  <div className="space-y-4">
                    {/* Search and filters */}
                    <DataActions
                      onSearch={setSearchQuery}
                      searchPlaceholder={`Search ${section.label.toLowerCase()}...`}
                      showViewSwitcher={false}
                      showExport={true}
                      showFilter={true}
                    />

                    {/* Virtual list for performance */}
                    <VirtualProfileList 
                      items={filteredItems} 
                      height={600}
                    />
                  </div>
                </Card>
              </TabsContent>
            ))}
          </Tabs>

          {/* Optimized drawer */}
          <UnifiedDrawer
            open={drawerOpen}
            onOpenChange={setDrawerOpen}
            title={drawerMode === 'edit' ? 'Edit Profile' : 'View Profile'}
            size="lg"
          >
            <div className="space-y-6">
              {/* Drawer content with lazy loading */}
              {drawerMode === 'edit' ? (
                <ProfileEditForm 
                  data={profileData} 
                  onSave={async (data) => {
                    // Save logic
                    setDrawerOpen(false);
                    await fetchProfileData(activeSection);
                  }}
                />
              ) : (
                <ProfileViewDetails data={profileData} />
              )}
            </div>
          </UnifiedDrawer>
        </div>
      </DataViewProvider>
    </StateManagerProvider>
  );
}

