'use client';

import { User, Mail, Phone, MapPin, Briefcase, Calendar, Globe, Edit, Eye, Download } from "lucide-react";
import { 
  Card, 
  Avatar, 
  Badge, 
  Button,
  Skeleton,
} from '@ghxstship/ui';
import type { UserProfile } from '../types';

interface ProfileCardViewProps {
  profile: UserProfile | null;
  loading: boolean;
  onEdit?: () => void;
  onView?: () => void;
  onExport?: () => void;
  compact?: boolean;
}

export default function ProfileCardView({
  profile,
  loading,
  onEdit,
  onView,
  onExport,
  compact = false,
}: ProfileCardViewProps) {
  if (loading) {
    return (
      <Card className="p-6">
        <div className="flex items-start gap-4">
          <Skeleton className="h-16 w-16 rounded-full" />
          <div className="flex-1 space-y-2">
            <Skeleton className="h-4 w-48" />
            <Skeleton className="h-3 w-32" />
            <Skeleton className="h-3 w-40" />
          </div>
        </div>
      </Card>
    );
  }

  if (!profile) {
    return (
      <Card className="p-6 text-center">
        <User className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
        <h3 className="text-lg font-semibold mb-2">No Profile Found</h3>
        <p className="text-muted-foreground">
          Profile information is not available.
        </p>
      </Card>
    );
  }

  const formatDate = (dateString?: string) => {
    if (!dateString) return 'Not specified';
    return new Date(dateString).toLocaleDateString();
  };

  const getCompletionColor = (percentage: number) => {
    if (percentage >= 80) return 'text-green-600';
    if (percentage >= 60) return 'text-yellow-600';
    return 'text-red-600';
  };

  return (
    <Card className="p-6">
      <div className="flex items-start justify-between mb-6">
        <div className="flex items-start gap-4">
          <Avatar className="h-16 w-16">
            {profile.avatar_url ? (
              <img 
                src={profile.avatar_url} 
                alt="Profile" 
                className="h-full w-full object-cover" 
              />
            ) : (
              <User className="h-8 w-8" />
            )}
          </Avatar>

          <div className="space-y-2">
            <div className="flex items-center gap-2">
              <h3 className="text-lg font-semibold">
                {profile.job_title || 'No Title'}
              </h3>
              <Badge variant={profile.status === 'active' ? 'default' : 'secondary'}>
                {profile.status}
              </Badge>
            </div>

            <div className="flex items-center gap-4 text-sm text-muted-foreground">
              {profile.department && (
                <div className="flex items-center gap-1">
                  <Briefcase className="h-4 w-4" />
                  <span>{profile.department}</span>
                </div>
              )}

              {profile.employment_type && (
                <Badge variant="outline" className="text-xs">
                  {profile.employment_type}
                </Badge>
              )}
            </div>

            <div className={`text-sm font-medium ${getCompletionColor(profile.profile_completion_percentage)}`}>
              Profile {profile.profile_completion_percentage}% complete
            </div>
          </div>
        </div>

        <div className="flex items-center gap-2">
          {onView && (
            <Button variant="ghost" size="sm" onClick={onView}>
              <Eye className="h-4 w-4" />
            </Button>
          )}
          {onEdit && (
            <Button variant="ghost" size="sm" onClick={onEdit}>
              <Edit className="h-4 w-4" />
            </Button>
          )}
          {onExport && (
            <Button variant="ghost" size="sm" onClick={onExport}>
              <Download className="h-4 w-4" />
            </Button>
          )}
        </div>
      </div>

      {!compact && (
        <div className="space-y-4">
          {/* Contact Information */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {profile.phone_primary && (
              <div className="flex items-center gap-2 text-sm">
                <Phone className="h-4 w-4 text-muted-foreground" />
                <span>{profile.phone_primary}</span>
              </div>
            )}

            {(profile.city || profile.country) && (
              <div className="flex items-center gap-2 text-sm">
                <MapPin className="h-4 w-4 text-muted-foreground" />
                <span>
                  {[profile.city, profile.country].filter(Boolean).join(', ')}
                </span>
              </div>
            )}

            {profile.hire_date && (
              <div className="flex items-center gap-2 text-sm">
                <Calendar className="h-4 w-4 text-muted-foreground" />
                <span>Hired {formatDate(profile.hire_date)}</span>
              </div>
            )}

            {profile.nationality && (
              <div className="flex items-center gap-2 text-sm">
                <Globe className="h-4 w-4 text-muted-foreground" />
                <span>{profile.nationality}</span>
              </div>
            )}
          </div>

          {/* Bio */}
          {profile.bio && (
            <div className="pt-4 border-t">
              <p className="text-sm text-muted-foreground leading-relaxed">
                {profile.bio}
              </p>
            </div>
          )}

          {/* Skills and Languages */}
          <div className="pt-4 border-t space-y-3">
            {profile.skills && profile.skills.length > 0 && (
              <div>
                <h4 className="text-sm font-medium mb-2">Skills</h4>
                <div className="flex flex-wrap gap-1">
                  {profile.skills.slice(0, 6).map(skill => (
                    <Badge key={skill} variant="secondary" className="text-xs">
                      {skill}
                    </Badge>
                  ))}
                  {profile.skills.length > 6 && (
                    <Badge variant="outline" className="text-xs">
                      +{profile.skills.length - 6} more
                    </Badge>
                  )}
                </div>
              </div>
            )}

            {profile.languages && profile.languages.length > 0 && (
              <div>
                <h4 className="text-sm font-medium mb-2">Languages</h4>
                <div className="flex flex-wrap gap-1">
                  {profile.languages.map(language => (
                    <Badge key={language} variant="outline" className="text-xs">
                      {language}
                    </Badge>
                  ))}
                </div>
              </div>
            )}
          </div>

          {/* Links */}
          {(profile.linkedin_url || profile.website_url) && (
            <div className="pt-4 border-t">
              <div className="flex gap-4">
                {profile.linkedin_url && (
                  <a
                    href={profile.linkedin_url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-sm text-blue-600 hover:underline"
                  >
                    LinkedIn
                  </a>
                )}
                {profile.website_url && (
                  <a
                    href={profile.website_url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-sm text-blue-600 hover:underline"
                  >
                    Website
                  </a>
                )}
              </div>
            </div>
          )}

          {/* Metadata */}
          <div className="pt-4 border-t text-xs text-muted-foreground">
            <div className="flex justify-between">
              <span>Updated: {formatDate(profile.updated_at)}</span>
              <span>ID: {profile.employee_id || 'Not assigned'}</span>
            </div>
          </div>
        </div>
      )}
    </Card>
  );
}

'use client';

import { Camera, Save, User, Plus, X, Eye, EyeOff } from "lucide-react";
import { useState } from 'react';
import { 
  Button, 
  Input, 
  Textarea, 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Card, 
  Avatar, 
  Badge,
  Skeleton,
} from '@ghxstship/ui';
import type { UserProfile, FieldConfig } from '../types';
import { PROFILE_FIELD_CONFIG } from '../types';

interface ProfileFormViewProps {
  profile: UserProfile | null;
  loading: boolean;
  saving: boolean;
  onSave: (data: Partial<UserProfile>) => Promise<void>;
  onFieldVisibilityChange?: (fieldKey: string, visible: boolean) => void;
  visibleFields?: Set<string>;
  editableFields?: Set<string>;
}

export default function ProfileFormView({
  profile,
  loading,
  saving,
  onSave,
  onFieldVisibilityChange,
  visibleFields = new Set(PROFILE_FIELD_CONFIG.map(f => f.key)),
  editableFields = new Set(PROFILE_FIELD_CONFIG.map(f => f.key)),
}: ProfileFormViewProps) {
  const [formData, setFormData] = useState<Partial<UserProfile>>(profile || {});
  const [newTag, setNewTag] = useState('');
  const [activeTagField, setActiveTagField] = useState<string | null>(null);

  const handleInputChange = (field: keyof UserProfile, value: unknown) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleTagAdd = (field: 'languages' | 'skills', tag: string) => {
    if (!tag.trim()) return;

    const currentTags = (formData[field] as string[]) || [];
    if (!currentTags.includes(tag.trim())) {
      handleInputChange(field, [...currentTags, tag.trim()]);
    }
    setNewTag('');
    setActiveTagField(null);
  };

  const handleTagRemove = (field: 'languages' | 'skills', tag: string) => {
    const currentTags = (formData[field] as string[]) || [];
    handleInputChange(field, currentTags.filter(t => t !== tag));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    await onSave(formData);
  };

  const renderField = (fieldConfig: FieldConfig) => {
    if (!visibleFields.has(fieldConfig.key)) return null;

    const isEditable = editableFields.has(fieldConfig.key);
    const value = formData[fieldConfig.key];

    const fieldWrapper = (content: React.ReactNode) => (
      <div key={fieldConfig.key} className="space-y-2">
        <div className="flex items-center justify-between">
          <label className="text-sm font-medium">
            {fieldConfig.label}
            {fieldConfig.required && <span className="text-red-500 ml-1">*</span>}
          </label>
          {onFieldVisibilityChange && (
            <Button
              variant="ghost"
              size="sm"
              onClick={() => onFieldVisibilityChange(fieldConfig.key, false)}
              className="h-6 w-6 p-0"
            >
              <EyeOff className="h-3 w-3" />
            </Button>
          )}
        </div>
        {content}
      </div>
    );

    if (!isEditable) {
      return fieldWrapper(
        <div className="px-3 py-2 bg-muted/50 rounded-md text-sm">
          {Array.isArray(value) ? value.join(', ') : value || 'Not specified'}
        </div>
      );
    }

    switch (fieldConfig.type) {
      case 'select':
        return fieldWrapper(
          <Select
            value={value as string || ''}
            onValueChange={(newValue) => handleInputChange(fieldConfig.key, newValue)}
          >
            <SelectTrigger>
              <SelectValue placeholder={`Select ${fieldConfig.label.toLowerCase()}`} />
            </SelectTrigger>
            <SelectContent>
              {fieldConfig.options?.map(option => (
                <SelectItem key={option.value} value={option.value}>
                  {option.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        );

      case 'textarea':
        return fieldWrapper(
          <Textarea
            value={value as string || ''}
            onChange={(e) => handleInputChange(fieldConfig.key, e.target.value)}
            placeholder={fieldConfig.placeholder}
            maxLength={fieldConfig.validation?.maxLength}
            rows={3}
          />
        );

      case 'tags':
        const tags = (value as string[]) || [];
        return fieldWrapper(
          <div className="space-y-2">
            <div className="flex flex-wrap gap-2">
              {tags.map(tag => (
                <Badge
                  key={tag}
                  variant="secondary"
                  className="cursor-pointer hover:bg-destructive hover:text-destructive-foreground"
                  onClick={() => handleTagRemove(fieldConfig.key as 'languages' | 'skills', tag)}
                >
                  {tag}
                  <X className="ml-1 h-3 w-3" />
                </Badge>
              ))}
            </div>
            {activeTagField === fieldConfig.key ? (
              <div className="flex gap-2">
                <Input
                  value={newTag}
                  onChange={(e) => setNewTag(e.target.value)}
                  placeholder={`Add ${fieldConfig.label.toLowerCase()}`}
                  onKeyPress={(e) => {
                    if (e.key === 'Enter') {
                      e.preventDefault();
                      handleTagAdd(fieldConfig.key as 'languages' | 'skills', newTag);
                    }
                  }}
                  autoFocus
                />
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => handleTagAdd(fieldConfig.key as 'languages' | 'skills', newTag)}
                >
                  Add
                </Button>
                <Button
                  type="button"
                  variant="ghost"
                  onClick={() => {
                    setActiveTagField(null);
                    setNewTag('');
                  }}
                >
                  Cancel
                </Button>
              </div>
            ) : (
              <Button
                type="button"
                variant="outline"
                size="sm"
                onClick={() => setActiveTagField(fieldConfig.key)}
                className="w-fit"
              >
                <Plus className="mr-1 h-3 w-3" />
                Add {fieldConfig.label.slice(0, -1)}
              </Button>
            )}
          </div>
        );

      default:
        return fieldWrapper(
          <Input
            type={fieldConfig.type}
            value={value as string || ''}
            onChange={(e) => handleInputChange(fieldConfig.key, e.target.value)}
            placeholder={fieldConfig.placeholder}
            minLength={fieldConfig.validation?.minLength}
            maxLength={fieldConfig.validation?.maxLength}
            pattern={fieldConfig.validation?.pattern}
          />
        );
    }
  };

  if (loading) {
    return (
      <div className="space-y-6">
        <div className="flex flex-col items-center space-y-4">
          <Skeleton className="h-24 w-24 rounded-full" />
          <Skeleton className="h-4 w-32" />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {Array.from({ length: 8 }).map((_, i) => (
            <div key={i} className="space-y-2">
              <Skeleton className="h-4 w-24" />
              <Skeleton className="h-10 w-full" />
            </div>
          ))}
        </div>
      </div>
    );
  }

  const groupedFields = PROFILE_FIELD_CONFIG.reduce((acc, field) => {
    if (!acc[field.section]) acc[field.section] = [];
    acc[field.section].push(field);
    return acc;
  }, {} as Record<string, FieldConfig[]>);

  return (
    <form onSubmit={handleSubmit} className="space-y-8">
      {/* Avatar Section */}
      <div className="flex flex-col items-center space-y-4">
        <div className="relative">
          <Avatar className="h-24 w-24">
            {formData.avatar_url ? (
              <img 
                src={formData.avatar_url} 
                alt="Profile" 
                className="h-full w-full object-cover" 
              />
            ) : (
              <User className="h-12 w-12" />
            )}
          </Avatar>
          <Button
            type="button"
            variant="outline"
            size="sm"
            className="absolute -bottom-2 -right-2 rounded-full h-8 w-8 p-0"
          >
            <Camera className="h-4 w-4" />
          </Button>
        </div>

        {profile && (
          <div className="text-center space-y-2">
            <Badge variant={profile.status === 'active' ? 'default' : 'secondary'}>
              {profile.status}
            </Badge>
            <div className="text-sm text-muted-foreground">
              Profile {profile.profile_completion_percentage}% complete
            </div>
          </div>
        )}
      </div>

      {/* Form Sections */}
      {Object.entries(groupedFields).map(([section, fields]) => (
        <Card key={section} className="p-6">
          <h3 className="text-lg font-semibold mb-4 capitalize">
            {section === 'basic' ? 'Basic Information' : 
             section === 'contact' ? 'Contact Information' : 
             section === 'professional' ? 'Professional Information' : 
             'Metadata'}
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {fields.map(renderField)}
          </div>
        </Card>
      ))}

      {/* Hidden Fields */}
      {onFieldVisibilityChange && (
        <Card className="p-6">
          <h3 className="text-lg font-semibold mb-4">Hidden Fields</h3>
          <div className="flex flex-wrap gap-2">
            {PROFILE_FIELD_CONFIG
              .filter(field => !visibleFields.has(field.key))
              .map(field => (
                <Button
                  key={field.key}
                  type="button"
                  variant="outline"
                  size="sm"
                  onClick={() => onFieldVisibilityChange(field.key, true)}
                  className="h-8"
                >
                  <Eye className="mr-1 h-3 w-3" />
                  {field.label}
                </Button>
              ))}
          </div>
        </Card>
      )}

      {/* Save Button */}
      <div className="flex justify-end">
        <Button type="submit" disabled={saving}>
          <Save className="mr-2 h-4 w-4" />
          {saving ? 'Saving...' : 'Save Changes'}
        </Button>
      </div>
    </form>
  );
}

'use client';

import { User, Edit, Eye, Download, ArrowUpDown, MoreHorizontal } from "lucide-react";
import { useMemo } from 'react';
import { 
  Card, 
  Avatar, 
  Badge, 
  Button,
  Skeleton,
} from '@ghxstship/ui';
import type { UserProfile, ProfileSort } from '../types';

interface ProfileTableViewProps {
  profiles: UserProfile[];
  loading: boolean;
  selectedItems: string[];
  onSelectItem: (id: string, selected: boolean) => void;
  onSelectAll: (ids: string[], selected: boolean) => void;
  onSort?: (sort: ProfileSort) => void;
  onEdit?: (profile: UserProfile) => void;
  onView?: (profile: UserProfile) => void;
  onExport?: (profile: UserProfile) => void;
  currentSort?: ProfileSort;
}

export default function ProfileTableView({
  profiles,
  loading,
  selectedItems,
  onSelectItem,
  onSelectAll,
  onSort,
  onEdit,
  onView,
  onExport,
  currentSort,
}: ProfileTableViewProps) {
  const allSelected = profiles.length > 0 && profiles.every(profile => selectedItems.includes(profile.id));
  const someSelected = profiles.some(profile => selectedItems.includes(profile.id));

  const handleSelectAll = () => {
    const profileIds = profiles.map(profile => profile.id);
    onSelectAll(profileIds, !allSelected);
  };

  const handleSort = (field: keyof UserProfile) => {
    if (!onSort) return;

    const direction = currentSort?.field === field && currentSort?.direction === 'asc' ? 'desc' : 'asc';
    onSort({ field, direction });
  };

  const getSortIcon = (field: keyof UserProfile) => {
    if (currentSort?.field !== field) return <ArrowUpDown className="h-3 w-3 opacity-50" />;
    return <ArrowUpDown className={`h-3 w-3 ${currentSort.direction === 'desc' ? 'rotate-180' : ''}`} />;
  };

  const formatDate = (dateString?: string) => {
    if (!dateString) return 'Not specified';
    return new Date(dateString).toLocaleDateString();
  };

  const getCompletionColor = (percentage: number) => {
    if (percentage >= 80) return 'text-green-600';
    if (percentage >= 60) return 'text-yellow-600';
    return 'text-red-600';
  };

  if (loading) {
    return (
      <Card className="p-6">
        <div className="space-y-4">
          <Skeleton className="h-8 w-full" />
          {Array.from({ length: 5 }).map((_, i) => (
            <div key={i} className="flex items-center gap-4">
              <Skeleton className="h-4 w-4" />
              <Skeleton className="h-8 w-8 rounded-full" />
              <Skeleton className="h-4 flex-1" />
              <Skeleton className="h-4 w-20" />
              <Skeleton className="h-4 w-16" />
            </div>
          ))}
        </div>
      </Card>
    );
  }

  if (profiles.length === 0) {
    return (
      <Card className="p-12 text-center">
        <User className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
        <h3 className="text-lg font-semibold mb-2">No Profiles Found</h3>
        <p className="text-muted-foreground">
          No profiles match the current filters.
        </p>
      </Card>
    );
  }

  return (
    <Card className="p-6">
      <div className="overflow-x-auto">
        <table className="w-full">
          <thead>
            <tr className="border-b">
              <th className="text-left p-2">
                <input
                  type="checkbox"
                  checked={allSelected}
                  ref={(input) => {
                    if (input) input.indeterminate = someSelected && !allSelected;
                  }}
                  onChange={handleSelectAll}
                  className="h-4 w-4 rounded border-border"
                />
              </th>
              <th className="text-left p-2">Profile</th>
              <th className="text-left p-2">
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => handleSort('job_title')}
                  className="h-auto p-0 font-semibold"
                >
                  Position
                  {getSortIcon('job_title')}
                </Button>
              </th>
              <th className="text-left p-2">
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => handleSort('department')}
                  className="h-auto p-0 font-semibold"
                >
                  Department
                  {getSortIcon('department')}
                </Button>
              </th>
              <th className="text-left p-2">
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => handleSort('employment_type')}
                  className="h-auto p-0 font-semibold"
                >
                  Type
                  {getSortIcon('employment_type')}
                </Button>
              </th>
              <th className="text-left p-2">
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => handleSort('profile_completion_percentage')}
                  className="h-auto p-0 font-semibold"
                >
                  Completion
                  {getSortIcon('profile_completion_percentage')}
                </Button>
              </th>
              <th className="text-left p-2">
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => handleSort('status')}
                  className="h-auto p-0 font-semibold"
                >
                  Status
                  {getSortIcon('status')}
                </Button>
              </th>
              <th className="text-left p-2">
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => handleSort('updated_at')}
                  className="h-auto p-0 font-semibold"
                >
                  Updated
                  {getSortIcon('updated_at')}
                </Button>
              </th>
              <th className="text-left p-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {profiles.map((profile) => {
              const isSelected = selectedItems.includes(profile.id);

              return (
                <tr 
                  key={profile.id} 
                  className={`border-b hover:bg-muted/50 ${
                    isSelected ? 'bg-muted/30' : ''
                  }`}
                >
                  <td className="p-2">
                    <input
                      type="checkbox"
                      checked={isSelected}
                      onChange={(e) => onSelectItem(profile.id, e.target.checked)}
                      className="h-4 w-4 rounded border-border"
                    />
                  </td>

                  <td className="p-2">
                    <div className="flex items-center gap-3">
                      <Avatar className="h-8 w-8">
                        {profile.avatar_url ? (
                          <img 
                            src={profile.avatar_url} 
                            alt="Profile" 
                            className="h-full w-full object-cover" 
                          />
                        ) : (
                          <User className="h-4 w-4" />
                        )}
                      </Avatar>
                      <div>
                        <div className="font-medium text-sm">
                          {profile.employee_id || 'No ID'}
                        </div>
                        <div className="text-xs text-muted-foreground">
                          {profile.nationality || 'Not specified'}
                        </div>
                      </div>
                    </div>
                  </td>

                  <td className="p-2">
                    <div className="text-sm">
                      {profile.job_title || 'No Title'}
                    </div>
                  </td>

                  <td className="p-2">
                    <div className="text-sm">
                      {profile.department || 'Unassigned'}
                    </div>
                  </td>

                  <td className="p-2">
                    {profile.employment_type && (
                      <Badge variant="outline" className="text-xs">
                        {profile.employment_type}
                      </Badge>
                    )}
                  </td>

                  <td className="p-2">
                    <div className={`text-sm font-medium ${getCompletionColor(profile.profile_completion_percentage)}`}>
                      {profile.profile_completion_percentage}%
                    </div>
                    <div className="w-16 bg-muted rounded-full h-1 mt-1">
                      <div
                        className="bg-primary h-1 rounded-full transition-all"
                        style={{ width: `${profile.profile_completion_percentage}%` }}
                      />
                    </div>
                  </td>

                  <td className="p-2">
                    <Badge variant={profile.status === 'active' ? 'default' : 'secondary'}>
                      {profile.status}
                    </Badge>
                  </td>

                  <td className="p-2">
                    <div className="text-sm text-muted-foreground">
                      {formatDate(profile.updated_at)}
                    </div>
                  </td>

                  <td className="p-2">
                    <div className="flex items-center gap-1">
                      {onView && (
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => onView(profile)}
                          className="h-6 w-6 p-0"
                        >
                          <Eye className="h-3 w-3" />
                        </Button>
                      )}
                      {onEdit && (
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => onEdit(profile)}
                          className="h-6 w-6 p-0"
                        >
                          <Edit className="h-3 w-3" />
                        </Button>
                      )}
                      {onExport && (
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => onExport(profile)}
                          className="h-6 w-6 p-0"
                        >
                          <Download className="h-3 w-3" />
                        </Button>
                      )}
                    </div>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </Card>
  );
}

'use client';

import { Award, Plus, Search, Filter, Download, RefreshCw, Settings, List, Grid3X3, Table, BarChart3, Save, X, Calendar, ExternalLink, Trash2 } from "lucide-react";
import { useState, useEffect, useCallback, useMemo, type ChangeEvent, type FormEvent } from 'react';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { 
  Button, 
  Input,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Card, 
  Badge,
  Textarea,
  UnifiedInput,
} from '@ghxstship/ui';

import type { 
  Certification as CertificationType, 
  CertificationFilters, 
  CertificationStats, 
  CertificationAnalytics, 
  ViewType,
  CertificationSort,
  CertificationFormData as FormDataType,
} from './types';
import { 
  VIEW_CONFIG, 
  QUICK_FILTERS, 
  createEmptyCertification,
  createEmptyCertificationStats, 
  createEmptyCertificationAnalytics,
  validateCertificationForm,
} from './types';

import CertificationListView from './views/CertificationListView';
import CertificationGridView from './views/CertificationGridView';
import CertificationTableView from './views/CertificationTableView';
import CertificationAnalyticsView from './views/CertificationAnalyticsView';

interface CertificationsClientProps {
  orgId: string;
  userId: string;
}

const viewIcons: Record<ViewType, React.ComponentType<{ className?: string }>> = {
  list: List,
  grid: Grid3X3,
  table: Table,
  analytics: BarChart3,
};

export default function CertificationsClient({ orgId, userId }: CertificationsClientProps) {
  const t = useTranslations('profile');
  const supabase = useMemo(() => createBrowserClient(), []);

  const [certifications, setCertifications] = useState<CertificationType[]>([]);
  const [stats, setStats] = useState<CertificationStats>(createEmptyCertificationStats());
  const [analytics, setAnalytics] = useState<CertificationAnalytics>(createEmptyCertificationAnalytics());
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [analyticsLoading, setAnalyticsLoading] = useState(false);
  const [view, setView] = useState<ViewType>('list');
  const [filters, setFilters] = useState<CertificationFilters>({});
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedItems, setSelectedItems] = useState<string[]>([]);
  const [sort, setSort] = useState<CertificationSort>({ field: 'created_at', direction: 'desc' });
  const [total, setTotal] = useState(0);
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingCertification, setEditingCertification] = useState<CertificationType | null>(null);
  const [formData, setFormData] = useState<FormDataType>(() => createEmptyCertification() as FormDataType);
  const [formErrors, setFormErrors] = useState<Record<string, string>>({});

  const fetchCertifications = useCallback(async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams();

      params.append('user_id', userId);
      if (filters.status && filters.status !== 'all') {
        params.append('status', filters.status);
      }
      if (filters.issuing_organization) params.append('issuing_organization', filters.issuing_organization);
      if (filters.expiry_status && filters.expiry_status !== 'all') {
        params.append('expiry_status', filters.expiry_status);
      }
      if (filters.search || searchQuery) params.append('search', filters.search || searchQuery);
      if (filters.date_from) params.append('date_from', filters.date_from);
      if (filters.date_to) params.append('date_to', filters.date_to);

      const response = await fetch(`/api/v1/profile/certifications?${params.toString()}`);
      if (!response.ok) throw new Error('Failed to fetch certifications');

      const data = await response.json();
      setCertifications(data.certifications || []);
      setStats(data.stats || createEmptyCertificationStats());
      setTotal(data.total || 0);
    } catch (error) {
      console.error('Error fetching certifications:', error);
      setCertifications([]);
      setStats(createEmptyCertificationStats());
    } finally {
      setLoading(false);
    }
  }, [userId, filters, searchQuery]);

  const fetchAnalytics = useCallback(async () => {
    try {
      setAnalyticsLoading(true);
      const params = new URLSearchParams();
      params.append('user_id', userId);
      params.append('period', '30d');
      params.append('granularity', 'day');

      const response = await fetch(`/api/v1/profile/certifications/analytics?${params.toString()}`);
      if (!response.ok) throw new Error('Failed to fetch analytics');

      const data = await response.json();
      setAnalytics(data);
    } catch (error) {
      console.error('Error fetching analytics:', error);
      setAnalytics(createEmptyCertificationAnalytics());
    } finally {
      setAnalyticsLoading(false);
    }
  }, [userId]);

  useEffect(() => {
    fetchCertifications();
  }, [fetchCertifications]);

  useEffect(() => {
    if (view === 'analytics') {
      fetchAnalytics();
    }
  }, [view, fetchAnalytics]);

  const handleSave = async (certificationData: FormDataType) => {
    const errors = validateCertificationForm(certificationData);
    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    try {
      setSaving(true);
      const url = editingCertification 
        ? `/api/v1/profile/certifications?user_id=${userId}&certification_id=${editingCertification.id}`
        : `/api/v1/profile/certifications?user_id=${userId}`;

      const method = editingCertification ? 'PUT' : 'POST';

      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(certificationData),
      });

      if (!response.ok) throw new Error('Failed to save certification');

      setShowAddForm(false);
      setEditingCertification(null);
      setFormData(createEmptyCertification());
      setFormErrors({});
      await fetchCertifications();
    } catch (error) {
      console.error('Error saving certification:', error);
    } finally {
      setSaving(false);
    }
  };

  const handleDelete = async (certification: CertificationType) => {
    if (!confirm('Are you sure you want to delete this certification?')) return;

    try {
      const response = await fetch(`/api/v1/profile/certifications?user_id=${userId}&certification_id=${certification.id}`, {
        method: 'DELETE',
      });

      if (!response.ok) throw new Error('Failed to delete certification');

      await fetchCertifications();
    } catch (error) {
      console.error('Error deleting certification:', error);
    }
  };

  const handleSelectItem = (id: string, selected: boolean) => {
    setSelectedItems(prev => {
      if (selected) {
        return prev.includes(id) ? prev : [...prev, id];
      }
      return prev.filter(itemId => itemId !== id);
    });
  };

  const handleSelectAll = (ids: string[], selected: boolean) => {
    setSelectedItems(selected ? Array.from(new Set([...selectedItems, ...ids])) : []);
  };

  const handleExport = (certification?: CertificationType) => {
    const certsToExport = certification ? [certification] : 
      selectedItems.length > 0 ? certifications.filter(c => selectedItems.includes(c.id)) : certifications;

    const csvRows = [
      ['Name', 'Organization', 'Number', 'Issue Date', 'Expiry Date', 'Status', 'Verification URL'],
      ...certsToExport.map(c => [
        c.name,
        c.issuing_organization,
        c.certification_number || '',
        c.issue_date || '',
        c.expiry_date || '',
        c.status,
        c.verification_url || '',
      ])
    ];

    const csvContent = csvRows.map(row => row.map(cell => `"${cell}"`).join(',')).join('\n');
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `certifications-${new Date().toISOString().split('T')[0]}.csv`;
    link.click();
    URL.revokeObjectURL(url);
  };

  const handleRefresh = () => {
    fetchCertifications();
    if (view === 'analytics') {
      fetchAnalytics();
    }
  };

  const handleEdit = (certification: CertificationType) => {
    setEditingCertification(certification);
    setFormData({
      name: certification.name || '',
      issuing_organization: certification.issuing_organization || '',
      certification_number: certification.certification_number || '',
      issue_date: certification.issue_date || '',
      expiry_date: certification.expiry_date || '',
      verification_url: certification.verification_url || '',
      attachment_url: certification.attachment_url || '',
      notes: certification.notes || '',
      status: certification.status,
    });
    setShowAddForm(true);
  };

  const filterValue = filters.status ?? 'all';

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col gap-4 lg:flex-row lg:items-center lg:justify-between">
        <div>
          <h1 className="text-2xl font-bold">Certifications</h1>
          <p className="text-muted-foreground">
            Manage your professional certifications and track expiry dates
          </p>
        </div>
        <div className="flex items-center gap-2">
          <Button variant="outline" size="sm" onClick={handleRefresh}>
            <RefreshCw className="mr-2 h-4 w-4" />
            Refresh
          </Button>
          <Button variant="outline" size="sm" onClick={() => handleExport()}>
            <Download className="mr-2 h-4 w-4" />
            Export
          </Button>
          <Button onClick={() => setShowAddForm(true)}>
            <Plus className="mr-2 h-4 w-4" />
            Add Certification
          </Button>
          <div className="flex items-center rounded-lg border p-1">
            {(Object.keys(VIEW_CONFIG) as ViewType[]).map(option => {
              const Icon = viewIcons[option];
              return (
                <Button
                  key={option}
                  variant={view === option ? 'default' : 'ghost'}
                  size="sm"
                  onClick={() => setView(option)}
                  className="h-8 w-8 p-0"
                >
                  <Icon className="h-4 w-4" />
                </Button>
              );
            })}
          </div>
        </div>
      </div>

      {/* Filters */}
      <Card className="p-4">
        <div className="flex flex-col gap-4 lg:flex-row lg:items-center lg:justify-between">
          <div className="flex flex-1 items-center gap-4">
            <div className="relative flex-1 max-w-sm">
              <Search className="absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 text-muted-foreground" />
              <Input
                placeholder="Search certifications..."
                value={searchQuery}
                onChange={event => setSearchQuery(event.target.value)}
                className="pl-9"
              />
            </div>

            <Select
              value={filterValue}
              onValueChange={value =>
                setFilters(prev => ({ ...prev, status: value === 'all' ? undefined : value as unknown }))
              }
            >
              <SelectTrigger className="w-48">
                <SelectValue placeholder="Filter by status" />
              </SelectTrigger>
              <SelectContent>
                {QUICK_FILTERS.map(option => (
                  <SelectItem key={option.value} value={option.value}>
                    {option.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div className="flex items-center gap-2">
            {selectedItems.length > 0 && (
              <div className="flex items-center gap-2 rounded-lg border bg-muted/50 px-3 py-2 text-sm">
                <span>{selectedItems.length} selected</span>
                <div className="flex items-center gap-1">
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => handleExport()}
                  >
                    Export
                  </Button>
                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={() => setSelectedItems([])}
                  >
                    Clear
                  </Button>
                </div>
              </div>
            )}

            <Badge variant="secondary">
              {total} total certifications
            </Badge>
          </div>
        </div>
      </Card>

      {/* Add/Edit Form Modal */}
      {showAddForm && (
        <Card className="p-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold">
              {editingCertification ? 'Edit Certification' : 'Add New Certification'}
            </h3>
            <Button
              variant="ghost"
              size="sm"
              onClick={() => {
                setShowAddForm(false);
                setEditingCertification(null);
                setFormData(createEmptyCertification());
                setFormErrors({});
              }}
            >
              <X className="h-4 w-4" />
            </Button>
          </div>

          <form
            onSubmit={(e) => {
              e.preventDefault();
              handleSave(formData);
            }}
            className="space-y-4"
          >
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <UnifiedInput
                label="Certification Name *"
                value={formData.name || ''}
                onChange={(e: ChangeEvent<HTMLInputElement>) => 
                  setFormData(prev => ({ ...prev, name: e.target.value }))
                }
                error={formErrors.name}
                placeholder="AWS Solutions Architect"
              />

              <UnifiedInput
                label="Issuing Organization *"
                value={formData.issuing_organization || ''}
                onChange={(e: ChangeEvent<HTMLInputElement>) => 
                  setFormData(prev => ({ ...prev, issuing_organization: e.target.value }))
                }
                error={formErrors.issuing_organization}
                placeholder="Amazon Web Services"
              />

              <UnifiedInput
                label="Certification Number"
                value={formData.certification_number || ''}
                onChange={(e: ChangeEvent<HTMLInputElement>) => 
                  setFormData(prev => ({ ...prev, certification_number: e.target.value }))
                }
                placeholder="AWS-SAA-123456"
              />

              <UnifiedInput
                label="Issue Date"
                type="date"
                value={formData.issue_date || ''}
                onChange={(e: ChangeEvent<HTMLInputElement>) => 
                  setFormData(prev => ({ ...prev, issue_date: e.target.value }))
                }
              />

              <UnifiedInput
                label="Expiry Date"
                type="date"
                value={formData.expiry_date || ''}
                onChange={(e: ChangeEvent<HTMLInputElement>) => 
                  setFormData(prev => ({ ...prev, expiry_date: e.target.value }))
                }
                error={formErrors.expiry_date}
              />

              <UnifiedInput
                label="Verification URL"
                value={formData.verification_url || ''}
                onChange={(e: ChangeEvent<HTMLInputElement>) => 
                  setFormData(prev => ({ ...prev, verification_url: e.target.value }))
                }
                error={formErrors.verification_url}
                placeholder="https://verify.aws.com/..."
              />

              <UnifiedInput
                label="Attachment URL"
                value={formData.attachment_url || ''}
                onChange={(e: ChangeEvent<HTMLInputElement>) => 
                  setFormData(prev => ({ ...prev, attachment_url: e.target.value }))
                }
                error={formErrors.attachment_url}
                placeholder="https://example.com/cert.pdf"
              />
            </div>

            <Textarea
              placeholder="Additional notes about this certification..."
              value={formData.notes || ''}
              onChange={(e: ChangeEvent<HTMLTextAreaElement>) => 
                setFormData(prev => ({ ...prev, notes: e.target.value }))
              }
              rows={3}
            />

            <div className="flex gap-2">
              <Button type="submit" disabled={saving}>
                <Save className="mr-2 h-4 w-4" />
                {saving ? 'Saving...' : editingCertification ? 'Update' : 'Add'} Certification
              </Button>
              <Button
                type="button"
                variant="outline"
                onClick={() => {
                  setShowAddForm(false);
                  setEditingCertification(null);
                  setFormData(createEmptyCertification() as FormDataType);
                  setFormErrors({});
                }}
              >
                Cancel
              </Button>
            </div>
          </form>
        </Card>
      )}

      {/* Content */}
      <div className="min-h-[400px]">
        {view === 'list' && (
          <CertificationListView
            certifications={certifications}
            loading={loading}
            selectedItems={selectedItems}
            onSelectItem={handleSelectItem}
            onSelectAll={handleSelectAll}
            onEdit={handleEdit}
            onDelete={handleDelete}
            onExport={handleExport}
          />
        )}

        {view === 'grid' && (
          <CertificationGridView
            certifications={certifications}
            loading={loading}
            selectedItems={selectedItems}
            onSelectItem={handleSelectItem}
            onEdit={handleEdit}
            onDelete={handleDelete}
            onExport={handleExport}
          />
        )}

        {view === 'table' && (
          <CertificationTableView
            certifications={certifications}
            loading={loading}
            selectedItems={selectedItems}
            onSelectItem={handleSelectItem}
            onSelectAll={handleSelectAll}
            onSort={setSort}
            onEdit={handleEdit}
            onDelete={handleDelete}
            onExport={handleExport}
            currentSort={sort}
          />
        )}

        {view === 'analytics' && (
          <CertificationAnalyticsView
            stats={stats}
            analytics={analytics}
            loading={loading}
            analyticsLoading={analyticsLoading}
          />
        );
    }

    setError(null);
    return true;
  }, [formData, analytics, analyticsLoading, loading, stats]);

  const handleAdd = useCallback(
    async (event: FormEvent<HTMLFormElement>) => {
      event.preventDefault();

      if (!validateForm()) {
        return;
      }

      const trimmedName = formData.name.trim();
      const trimmedIssuer = formData.issuingOrganization.trim();
      const trimmedNumber = formData.certificationNumber.trim();
      const trimmedVerificationUrl = formData.verificationUrl.trim();
      const trimmedAttachmentUrl = formData.attachmentUrl.trim();
      const trimmedNotes = formData.notes.trim();

      try {
        setIsSubmitting(true);

        const { data: userData, error: userError } = await supabase
          .from('users')
          .select('id')
          .eq('auth_id', userId)
          .single();

        if (userError) {
          throw userError;
        }

        if (!userData) {
          throw new Error('User not found');
        }

        const { error: insertError } = await supabase.from('user_certifications').insert({
          user_id: userData.id,
          organization_id: orgId,
          name: trimmedName,
          issuing_organization: trimmedIssuer,
          certification_number: trimmedNumber || undefined,
          issue_date: formData.issueDate || undefined,
          expiry_date: formData.expiryDate || undefined,
          verification_url: trimmedVerificationUrl || undefined,
          attachment_url: trimmedAttachmentUrl || undefined,
          notes: trimmedNotes || undefined,
          status: 'active',
        });

        if (insertError) {
          throw insertError;
        }

        resetFormState();
        setShowAddForm(false);
        await loadCertifications();
      } catch (submitError) {
        const message = submitError instanceof Error ? submitError.message : 'Unknown error';
        console.error('Error adding certification:', submitError);
        setError(message);
      } finally {
        setIsSubmitting(false);
      }
    },
    [formData, loadCertifications, orgId, resetFormState, supabase, userId, validateForm]
  );

  const isSubmitDisabled = useMemo(() => {
    if (isSubmitting) {
      return true;
    }

    if (!formData.name.trim() || !formData.issuingOrganization.trim()) {
      return true;
    }

    const trimmedVerificationUrl = formData.verificationUrl.trim();
    if (trimmedVerificationUrl) {
      try {
        new URL(trimmedVerificationUrl);
      } catch {
        return true;
      }
    }

    const trimmedAttachmentUrl = formData.attachmentUrl.trim();
    if (trimmedAttachmentUrl) {
      try {
        new URL(trimmedAttachmentUrl);
      } catch {
        return true;
      }
    }

    if (formData.issueDate.trim() && formData.expiryDate.trim() && formData.expiryDate < formData.issueDate) {
      return true;
    }

    return false;
  }, [formData.attachmentUrl, formData.expiryDate, formData.issuingOrganization, formData.issueDate, formData.name, formData.verificationUrl, isSubmitting]);

  const getStatusColor = (status: string, expiryDate?: string) => {
    if (status === 'expired') return 'destructive';
    if (expiryDate) {
      const expiry = new Date(expiryDate);
      const now = new Date();
      const thirtyDaysFromNow = new Date();
      thirtyDaysFromNow.setDate(now.getDate() + 30);

      if (expiry < now) return 'destructive';
      if (expiry <= thirtyDaysFromNow) return 'warning';
    }
    return 'default';
  };

  const getStatusText = (status: string, expiryDate?: string) => {
    if (status === 'expired') return 'Expired';
    if (expiryDate) {
      const expiry = new Date(expiryDate);
      const now = new Date();
      const thirtyDaysFromNow = new Date();
      thirtyDaysFromNow.setDate(now.getDate() + 30);

      if (expiry < now) return 'Expired';
      if (expiry <= thirtyDaysFromNow) return 'Expiring Soon';
    }
    return 'Active';
  };

  if (loading) {
    return (
      <div className="stack-md">
        <div className="animate-pulse stack-md">
          <div className="h-4 bg-secondary rounded w-3/4"></div>
          <div className="h-4 bg-secondary rounded w-1/2"></div>
          <div className="h-4 bg-secondary rounded w-2/3"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="stack-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-sm">
          <Award className="h-5 w-5" />
          <h3 className="text-body text-heading-4">Certifications</h3>
        </div>
        <Button onClick={() => setShowAddForm(true)}>
          <Plus className="h-4 w-4 mr-sm" />
          Add Certification
        </Button>
      </div>

      {/* Add Form */}
      {showAddForm && (
        <Card>
          <div className="p-md">
            <h4 className="form-label mb-md">Add New Certification</h4>
            <form className="stack-md" onSubmit={handleAdd}>
              {error ? <div className="text-body-sm color-destructive">{error}</div> : null}

              <div className="grid grid-cols-1 gap-md md:grid-cols-2">
                <UnifiedInput
                  id="name"
                  name="name"
                  label="Certification Name *"
                  placeholder="AWS Solutions Architect"
                  value={formData.name}
                  onChange={handleInputChange}
                  error={fieldErrors.name}
                  required
                />

                <UnifiedInput
                  id="issuingOrganization"
                  name="issuingOrganization"
                  label="Issuing Organization *"
                  placeholder="Amazon Web Services"
                  value={formData.issuingOrganization}
                  onChange={handleInputChange}
                  error={fieldErrors.issuingOrganization}
                  required
                />

                <UnifiedInput
                  id="certificationNumber"
                  name="certificationNumber"
                  label="Certification Number"
                  placeholder="AWS-SAA-123456"
                  value={formData.certificationNumber}
                  onChange={handleInputChange}
                />

                <UnifiedInput
                  id="issueDate"
                  name="issueDate"
                  label="Issue Date"
                  type="date"
                  value={formData.issueDate}
                  onChange={handleInputChange}
                />

                <UnifiedInput
                  id="expiryDate"
                  name="expiryDate"
                  label="Expiry Date"
                  type="date"
                  value={formData.expiryDate}
                  onChange={handleInputChange}
                  error={fieldErrors.expiryDate}
                />

                <UnifiedInput
                  id="verificationUrl"
                  name="verificationUrl"
                  label="Verification URL"
                  placeholder="https://verify.aws.com/..."
                  value={formData.verificationUrl}
                  onChange={handleInputChange}
                  error={fieldErrors.verificationUrl}
                />

                <UnifiedInput
                  id="attachmentUrl"
                  name="attachmentUrl"
                  label="Attachment URL"
                  placeholder="https://example.com/cert.pdf"
                  value={formData.attachmentUrl}
                  onChange={handleInputChange}
                  error={fieldErrors.attachmentUrl}
                />
              </div>

              <Textarea
                id="notes"
                name="notes"
                label="Notes"
                placeholder="Additional notes about this certification..."
                value={formData.notes}
                onChange={handleTextareaChange}
                rows={3}
              />

              <div className="flex gap-sm">
                <Button type="submit" disabled={isSubmitDisabled}>
                  <Save className="h-4 w-4 mr-sm" />
                  {isSubmitting ? 'Adding...' : 'Add Certification'}
                </Button>
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => {
                    resetFormState();
                    setShowAddForm(false);
                  }}
                  disabled={isSubmitting}
                >
                  Cancel
                </Button>
              </div>
            </form>
          </div>
        </Card>
      )}

      {/* Certifications List */}
      <div className="stack-md">
        {certifications.length === 0 ? (
          <Card>
            <div className="p-xl text-center color-muted">
              <Award className="h-12 w-12 mx-auto mb-md opacity-50" />
              <p>No certifications added yet.</p>
              <p className="text-body-sm">Add your professional certifications to showcase your expertise.</p>
            </div>
          </Card>
        ) : (
          certifications.map((cert: unknown) => (
            <Card key={cert.id}>
              <div className="p-md">
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-center gap-sm mb-sm">
                      <h4 className="form-label">{cert.name}</h4>
                      <Badge variant={getStatusColor(cert.status, cert.expiry_date)}>
                        {getStatusText(cert.status, cert.expiry_date)}
                      </Badge>
                    </div>

                    <p className="text-body-sm color-muted mb-sm">
                      Issued by {cert.issuing_organization}
                    </p>

                    {cert.certification_number && (
                      <p className="text-body-sm">
                        <span className="form-label">Number:</span> {cert.certification_number}
                      </p>
                    )}

                    <div className="flex items-center gap-md text-body-sm color-muted mt-sm">
                      {cert.issue_date && (
                        <div className="flex items-center gap-xs">
                          <Calendar className="h-3 w-3" />
                          Issued: {new Date(cert.issue_date).toLocaleDateString()}
                        </div>
                      )}
                      {cert.expiry_date && (
                        <div className="flex items-center gap-xs">
                          <Calendar className="h-3 w-3" />
                          Expires: {new Date(cert.expiry_date).toLocaleDateString()}
                        </div>
                      )}
                    </div>

                    {cert.notes && (
                      <p className="text-body-sm mt-sm color-muted">{cert.notes}</p>
                    )}
                  </div>

                  <div className="flex items-center gap-sm">
                    {cert.verification_url && (
                      <Button

                        variant="outline"
                        onClick={() => window.open(cert.verification_url, '_blank')}
                      >
                        <ExternalLink className="h-3 w-3" />
                      </Button>
                    )}
                    <Button

                      variant="outline"
                      onClick={() => handleDelete(cert.id)}
                    >
                      <Trash2 className="h-3 w-3" />
                    </Button>
                  </div>
                </div>
              </div>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

'use client';


import { Save, Phone, MapPin } from "lucide-react";
import { useCallback, useEffect, useMemo, useState, type ChangeEvent, type FormEvent } from 'react';
import { createBrowserClient } from '@ghxstship/auth';
import { Button, UnifiedInput, Card } from '@ghxstship/ui';

interface UserProfile {
  id: string;
  phone_primary?: string;
  phone_secondary?: string;
  address_line1?: string;
  address_line2?: string;
  city?: string;
  state_province?: string;
  postal_code?: string;
  country?: string;
}

interface ContactFormData {
  phonePrimary: string;
  phoneSecondary: string;
  addressLine1: string;
  addressLine2: string;
  city: string;
  stateProvince: string;
  postalCode: string;
  country: string;
}

const createInitialFormData = (): ContactFormData => ({
  phonePrimary: '',
  phoneSecondary: '',
  addressLine1: '',
  addressLine2: '',
  city: '',
  stateProvince: '',
  postalCode: '',
  country: '',
});

export default function ContactInfoClient({ orgId, userId }: { orgId: string; userId: string }) {
  const supabase = useMemo(() => createBrowserClient(), []);

  const [profile, setProfile] = useState<UserProfile | null>(null);
  const [loading, setLoading] = useState(true);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [formData, setFormData] = useState<ContactFormData>(() => createInitialFormData());
  const [initialFormData, setInitialFormData] = useState<ContactFormData>(() => createInitialFormData());
  const [fieldErrors, setFieldErrors] = useState<Partial<Record<keyof ContactFormData, string>>>({});
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    loadProfile();
  }, [orgId, userId]);

  const loadProfile = async () => {
    try {
      setLoading(true);

      const { data: userData } = await supabase
        .from('users')
        .select('id')
        .eq('auth_id', userId)
        .single();

      if (userData) {
        const { data: profileData } = await supabase
          .from('user_profiles')
          .select('*')
          .eq('user_id', userData.id)
          .eq('organization_id', orgId)
          .single();

        if (profileData) {
          setProfile(profileData);
          const mappedData: ContactFormData = {
            phonePrimary: profileData.phone_primary || '',
            phoneSecondary: profileData.phone_secondary || '',
            addressLine1: profileData.address_line1 || '',
            addressLine2: profileData.address_line2 || '',
            city: profileData.city || '',
            stateProvince: profileData.state_province || '',
            postalCode: profileData.postal_code || '',
            country: profileData.country || '',
          };
          setFormData(mappedData);
          setInitialFormData(mappedData);
        }
      }
    } catch (error) {
      console.error('Error loading profile:', error);
    } finally {
      setLoading(false);
    }
  };

  const updateForm = useCallback(
    <Key extends keyof ContactFormData>(field: Key, value: ContactFormData[Key]) => {
      setFormData(prev => ({ ...prev, [field]: value }));
      setFieldErrors(prev => {
        if (!prev[field]) {
          return prev;
        }
        const next = { ...prev };
        delete next[field];
        return next;
      });
    },
    []
  );

  const handleInputChange = useCallback(
    (event: ChangeEvent<HTMLInputElement>) => {
      const { name, value } = event.target;
      updateForm(name as keyof ContactFormData, value as ContactFormData[keyof ContactFormData]);
    },
    [updateForm]
  );

  const validateForm = useCallback(() => {
    const errors: Partial<Record<keyof ContactFormData, string>> = {};

    const trimmedPrimary = formData.phonePrimary.trim();
    if (!trimmedPrimary) {
      errors.phonePrimary = 'Primary phone is required.';
    }

    if (formData.postalCode.trim() && formData.postalCode.trim().length < 3) {
      errors.postalCode = 'Postal code looks too short.';
    }

    setFieldErrors(errors);

    if (Object.keys(errors).length > 0) {
      setError('Please resolve the highlighted fields.');
      return false;
    }

    setError(null);
    return true;
  }, [formData]);

  const isDirty = useMemo(() => {
    return JSON.stringify(formData) !== JSON.stringify(initialFormData);
  }, [formData, initialFormData]);

  const handleSave = useCallback(
    async (event: FormEvent<HTMLFormElement>) => {
      event.preventDefault();

      if (!profile) {
        return;
      }

      if (!validateForm()) {
        return;
      }

      const trimmedForm: ContactFormData = {
        phonePrimary: formData.phonePrimary.trim(),
        phoneSecondary: formData.phoneSecondary.trim(),
        addressLine1: formData.addressLine1.trim(),
        addressLine2: formData.addressLine2.trim(),
        city: formData.city.trim(),
        stateProvince: formData.stateProvince.trim(),
        postalCode: formData.postalCode.trim(),
        country: formData.country.trim(),
      };

      try {
        setIsSubmitting(true);

        const { error: updateError } = await supabase
          .from('user_profiles')
          .update({
            phone_primary: trimmedForm.phonePrimary || null,
            phone_secondary: trimmedForm.phoneSecondary || null,
            address_line1: trimmedForm.addressLine1 || null,
            address_line2: trimmedForm.addressLine2 || null,
            city: trimmedForm.city || null,
            state_province: trimmedForm.stateProvince || null,
            postal_code: trimmedForm.postalCode || null,
            country: trimmedForm.country || null,
            updated_at: new Date().toISOString(),
          })
          .eq('id', profile.id);

        if (updateError) {
          throw updateError;
        }

        setInitialFormData(trimmedForm);
        setFormData(trimmedForm);
        setError(null);
      } catch (submitError) {
        const message = submitError instanceof Error ? submitError.message : 'Unknown error';
        console.error('Error saving contact info:', submitError);
        setError(message);
      } finally {
        setIsSubmitting(false);
      }
    },
    [formData, profile, supabase, validateForm]
  );

  const isSubmitDisabled = useMemo(() => {
    if (isSubmitting) {
      return true;
    }

    if (!isDirty) {
      return true;
    }

    if (!formData.phonePrimary.trim()) {
      return true;
    }

    if (formData.postalCode.trim() && formData.postalCode.trim().length < 3) {
      return true;
    }

    return false;
  }, [formData.phonePrimary, formData.postalCode, isDirty, isSubmitting]);

  if (loading) {
    return (
      <div className="stack-lg">
        <div className="animate-pulse stack-md">
          <div className="h-4 bg-secondary rounded w-3/4"></div>
          <div className="h-4 bg-secondary rounded w-1/2"></div>
          <div className="h-4 bg-secondary rounded w-2/3"></div>
        </div>
      </div>
    );
  }

  return (
    <form className="stack-lg" onSubmit={handleSave}>
      {error ? <div className="text-body-sm color-destructive">{error}</div> : null}

      {/* Phone Numbers */}
      <Card>
        <div className="p-md">
          <div className="flex items-center gap-sm mb-md">
            <Phone className="h-5 w-5" />
            <h3 className="text-body text-heading-4">Phone Numbers</h3>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
            <div className="stack-sm">
              <label className="text-body-sm form-label">Primary Phone</label>
              <UnifiedInput
                id="phonePrimary"
                name="phonePrimary"
                value={formData.phonePrimary}
                onChange={handleInputChange}
                placeholder="+1 (555) 123-4567"
                error={fieldErrors.phonePrimary}
                required
              />
            </div>

            <div className="stack-sm">
              <label className="text-body-sm form-label">Secondary Phone</label>
              <UnifiedInput
                id="phoneSecondary"
                name="phoneSecondary"
                value={formData.phoneSecondary}
                onChange={handleInputChange}
                placeholder="+1 (555) 987-6543"
              />
            </div>
          </div>
        </div>
      </Card>

      {/* Address */}
      <Card>
        <div className="p-md">
          <div className="flex items-center gap-sm mb-md">
            <MapPin className="h-5 w-5" />
            <h3 className="text-body text-heading-4">Address</h3>
          </div>

          <div className="stack-md">
            <div className="stack-sm">
              <label className="text-body-sm form-label">Address Line 1</label>
              <UnifiedInput
                id="addressLine1"
                name="addressLine1"
                value={formData.addressLine1}
                onChange={handleInputChange}
                placeholder="123 Main Street"
              />
            </div>

            <div className="stack-sm">
              <label className="text-body-sm form-label">Address Line 2</label>
              <UnifiedInput
                id="addressLine2"
                name="addressLine2"
                value={formData.addressLine2}
                onChange={handleInputChange}
                placeholder="Apt 4B, Suite 200"
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-md">
              <div className="stack-sm">
                <label className="text-body-sm form-label">City</label>
                <UnifiedInput
                  id="city"
                  name="city"
                  value={formData.city}
                  onChange={handleInputChange}
                  placeholder="San Francisco"
                />
              </div>

              <div className="stack-sm">
                <label className="text-body-sm form-label">State/Province</label>
                <UnifiedInput
                  id="stateProvince"
                  name="stateProvince"
                  value={formData.stateProvince}
                  onChange={handleInputChange}
                  placeholder="California"
                />
              </div>

              <div className="stack-sm">
                <label className="text-body-sm form-label">Postal Code</label>
                <UnifiedInput
                  id="postalCode"
                  name="postalCode"
                  value={formData.postalCode}
                  onChange={handleInputChange}
                  placeholder="94102"
                  error={fieldErrors.postalCode}
                />
              </div>
            </div>

            <div className="stack-sm">
              <label className="text-body-sm form-label">Country</label>
              <UnifiedInput
                id="country"
                name="country"
                value={formData.country}
                onChange={handleInputChange}
                placeholder="United States"
              />
            </div>
          </div>
        </div>
      </Card>

      {/* Save Button */}
      <div className="flex justify-end">
        <Button type="submit" disabled={isSubmitDisabled}>
          <Save className="h-4 w-4 mr-sm" />
          {isSubmitting ? 'Saving...' : 'Save Changes'}
        </Button>
      </div>
    </form>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useMemo, type ChangeEvent } from 'react';
import { Card, Badge, Button, Checkbox, Input, Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@ghxstship/ui';
import type { ContactInfo, ContactFilters, ContactSort } from '../types';
import { formatPhoneNumber } from '../types';

interface ContactListViewProps {
  contacts: ContactInfo[];
  loading: boolean;
  selectedItems: string[];
  filters: ContactFilters;
  sort: ContactSort;
  onSelectItem: (id: string, selected: boolean) => void;
  onSelectAll: (ids: string[], selected: boolean) => void;
  onChangeFilters: (filters: Partial<ContactFilters>) => void;
  onChangeSort: (sort: ContactSort) => void;
  onExport: (contact?: ContactInfo) => void;
  onViewContact?: (contact: ContactInfo) => void;
}

const verificationBadges: Record<string, { label: string; variant: 'default' | 'outline' | 'destructive' | 'secondary' }> = {
  verified: { label: 'Verified', variant: 'default' },
  pending: { label: 'Pending Verification', variant: 'secondary' },
  unverified: { label: 'Unverified', variant: 'destructive' },
};

export default function ContactListView({
  contacts,
  loading,
  selectedItems,
  filters,
  sort,
  onSelectItem,
  onSelectAll,
  onChangeFilters,
  onChangeSort,
  onExport,
  onViewContact,
}: ContactListViewProps) {
  const allSelected = contacts.length > 0 && contacts.every(contact => selectedItems.includes(contact.id));
  const someSelected = contacts.some(contact => selectedItems.includes(contact.id));

  const sortedContacts = useMemo(() => {
    const sorted = [...contacts];
    sorted.sort((a, b) => {
      const aValue = (a[sort.field] || '').toString().toLowerCase();
      const bValue = (b[sort.field] || '').toString().toLowerCase();
      if (aValue < bValue) return sort.direction === 'asc' ? -1 : 1;
      if (aValue > bValue) return sort.direction === 'asc' ? 1 : -1;
      return 0;
    });
    return sorted;
  }, [contacts, sort]);

  const handleSelectAll = (event: ChangeEvent<HTMLInputElement>) => {
    const checked = event.target.checked;
    onSelectAll(contacts.map(contact => contact.id), checked);
  };

  const handleSort = (field: keyof ContactInfo) => {
    const direction = sort.field === field && sort.direction === 'asc' ? 'desc' : 'asc';
    onChangeSort({ field, direction });
  };

  if (loading) {
    return (
      <Card className="p-6 space-y-4">
        <div className="animate-pulse space-y-2">
          <div className="h-4 bg-muted rounded w-1/3"></div>
          <div className="h-4 bg-muted rounded w-1/2"></div>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {Array.from({ length: 6 }).map((_, i) => (
            <div key={i} className="h-16 bg-muted rounded animate-pulse"></div>
          ))}
        </div>
      </Card>
    );
  }

  return (
    <div className="space-y-4">
      <Card className="p-4 space-y-4">
        <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4">
          <div className="flex flex-1 items-center gap-4">
            <div className="relative flex-1 max-w-lg">
              <Search className="absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 text-muted-foreground" />
              <Input
                placeholder="Search contacts by phone, address, or emergency contact..."
                value={filters.search || ''}
                onChange={(event) => onChangeFilters({ search: event.target.value })}
                className="pl-9"
              />
            </div>
            <Button variant="outline" size="sm" className="flex items-center gap-2">
              <Filter className="h-4 w-4" />
              Filters
            </Button>
          </div>

          <div className="flex items-center gap-2">
            {selectedItems.length > 0 && (
              <Button variant="outline" size="sm" onClick={() => onExport()}>
                <Download className="mr-2 h-4 w-4" />
                Export Selected
              </Button>
            )}
            <Badge variant="secondary">
              {selectedItems.length > 0
                ? `${selectedItems.length} selected`
                : `${contacts.length} contacts`}
            </Badge>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <Select
            value={filters.verification_status || 'all'}
            onValueChange={(value) => onChangeFilters({ verification_status: value as ContactFilters['verification_status'] })}
          >
            <SelectTrigger>
              <SelectValue placeholder="Verification status" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Verification Status</SelectItem>
              <SelectItem value="verified">Verified</SelectItem>
              <SelectItem value="pending">Pending</SelectItem>
              <SelectItem value="unverified">Unverified</SelectItem>
            </SelectContent>
          </Select>

          <Select
            value={filters.preferred_contact_method || 'all'}
            onValueChange={(value) => onChangeFilters({ preferred_contact_method: value as ContactFilters['preferred_contact_method'] })}
          >
            <SelectTrigger>
              <SelectValue placeholder="Preferred contact method" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Contact Methods</SelectItem>
              <SelectItem value="email">Email</SelectItem>
              <SelectItem value="phone">Phone</SelectItem>
              <SelectItem value="sms">SMS</SelectItem>
              <SelectItem value="mail">Mail</SelectItem>
            </SelectContent>
          </Select>

          <Select
            value={filters.has_emergency_contact ? 'true' : 'false'}
            onValueChange={(value) => onChangeFilters({ has_emergency_contact: value === 'true' })}
          >
            <SelectTrigger>
              <SelectValue placeholder="Emergency contact filter" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="false">All Contacts</SelectItem>
              <SelectItem value="true">With Emergency Contact</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </Card>

      <Card className="overflow-hidden">
        <table className="w-full text-sm">
          <thead className="bg-muted/50">
            <tr className="text-left text-xs uppercase tracking-wide text-muted-foreground">
              <th className="p-3 w-10">
                <Checkbox
                  checked={allSelected}
                  indeterminate={someSelected && !allSelected}
                  onChange={handleSelectAll}
                />
              </th>
              <th className="p-3 cursor-pointer" onClick={() => handleSort('phone_primary')}>
                Primary Phone
              </th>
              <th className="p-3 cursor-pointer" onClick={() => handleSort('address_line1')}>
                Address
              </th>
              <th className="p-3 cursor-pointer" onClick={() => handleSort('verification_status')}>
                Verification
              </th>
              <th className="p-3">Emergency Contact</th>
              <th className="p-3 cursor-pointer" onClick={() => handleSort('preferred_contact_method')}>
                Preferred Method
              </th>
              <th className="p-3">Actions</th>
            </tr>
          </thead>
          <tbody>
            {sortedContacts.length === 0 ? (
              <tr>
                <td colSpan={7} className="p-8 text-center text-muted-foreground">
                  No contacts match the current filters.
                </td>
              </tr>
            ) : (
              sortedContacts.map(contact => {
                const isSelected = selectedItems.includes(contact.id);
                const verificationBadge = verificationBadges[contact.verification_status || 'unverified'];
                return (
                  <tr key={contact.id} className="border-t border-border/60 hover:bg-muted/40">
                    <td className="p-3">
                      <Checkbox
                        checked={isSelected}
                        onChange={(event) =>
                          onSelectItem(contact.id, event.target.checked)
                        }
                      />
                    </td>
                    <td className="p-3">
                      <div className="flex items-center gap-2">
                        <Phone className="h-4 w-4 text-muted-foreground" />
                        <span>{contact.phone_primary ? formatPhoneNumber(contact.phone_primary) : 'â€”'}</span>
                      </div>
                    </td>
                    <td className="p-3">
                      <div className="flex items-center gap-2 text-muted-foreground">
                        <MapPin className="h-4 w-4" />
                        <span>
                          {contact.address_line1
                            ? `${contact.address_line1}, ${contact.city || ''} ${contact.state_province || ''}`
                            : 'No address'}
                        </span>
                      </div>
                    </td>
                    <td className="p-3">
                      <Badge variant={verificationBadge.variant}>
                        <div className="flex items-center gap-1">
                          <ShieldCheck className="h-4 w-4" />
                          {verificationBadge.label}
                        </div>
                      </Badge>
                    </td>
                    <td className="p-3 text-muted-foreground">
                      {contact.emergency_contact_name || 'â€”'}
                    </td>
                    <td className="p-3 capitalize text-muted-foreground">
                      {contact.preferred_contact_method || 'email'}
                    </td>
                    <td className="p-3">
                      <div className="flex items-center gap-2">
                        {onViewContact && (
                          <Button variant="ghost" size="sm" onClick={() => onViewContact(contact)}>
                            View
                          </Button>
                        )}
                        <Button variant="outline" size="sm" onClick={() => onExport(contact)}>
                          Export
                        </Button>
                      </div>
                    </td>
                  </tr>
                );
              })
            )}
          </tbody>
        </table>
      </Card>
    </div>
  );
}

'use client';

import { Users, UserCheck, UserX, Trash2, AlertTriangle, CheckCircle, XCircle } from "lucide-react";
import { useState } from 'react';
import {
  UniversalDrawer,
  Button,
  Card,
  Badge,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Label,
  Checkbox,
  useToast,
  Alert,
  AlertDescription,
} from '@ghxstship/ui';

import type { UserProfile, BulkActionConfig } from '../types';

const BULK_ACTIONS: BulkActionConfig[] = [
  {
    action: 'activate',
    label: 'Activate Profiles',
    description: 'Set selected profiles to active status',
    confirmationRequired: false,
    variant: 'default',
  },
  {
    action: 'deactivate',
    label: 'Deactivate Profiles',
    description: 'Set selected profiles to inactive status',
    confirmationRequired: true,
    variant: 'warning',
  },
  {
    action: 'suspend',
    label: 'Suspend Profiles',
    description: 'Suspend selected profiles temporarily',
    confirmationRequired: true,
    variant: 'warning',
  },
  {
    action: 'delete',
    label: 'Delete Profiles',
    description: 'Permanently delete selected profiles and all associated data',
    confirmationRequired: true,
    variant: 'destructive',
  },
  {
    action: 'export',
    label: 'Export Profiles',
    description: 'Export selected profiles to CSV or JSON format',
    confirmationRequired: false,
    variant: 'default',
  },
  {
    action: 'send_notification',
    label: 'Send Notification',
    description: 'Send email notification to selected profiles',
    confirmationRequired: false,
    variant: 'default',
  },
];

interface BulkActionsDrawerProps {
  isOpen: boolean;
  onClose: () => void;
  selectedProfiles: UserProfile[];
  onExecute: (action: string, profileIds: string[]) => Promise<void>;
}

export default function BulkActionsDrawer({
  isOpen,
  onClose,
  selectedProfiles,
  onExecute
}: BulkActionsDrawerProps) {
  const { toast } = useToast();
  const [selectedAction, setSelectedAction] = useState<string>('');
  const [confirmationChecked, setConfirmationChecked] = useState(false);
  const [loading, setLoading] = useState(false);

  const selectedActionConfig = BULK_ACTIONS.find(action => action.action === selectedAction);
  const profileIds = selectedProfiles.map(p => p.id);

  const handleExecute = async () => {
    if (!selectedAction || !selectedActionConfig) {
      toast({
        title: 'Error',
        description: 'Please select an action to perform',
        variant: 'destructive',
      });
      return;
    }

    if (selectedActionConfig.confirmationRequired && !confirmationChecked) {
      toast({
        title: 'Confirmation Required',
        description: 'Please confirm that you want to perform this action',
        variant: 'destructive',
      });
      return;
    }

    try {
      setLoading(true);
      await onExecute(selectedAction, profileIds);
      toast({
        title: 'Success',
        description: `${selectedActionConfig.label} completed successfully`,
      });
      onClose();
    } catch (error) {
      toast({
        title: 'Error',
        description: `Failed to ${selectedActionConfig.label.toLowerCase()}`,
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    setSelectedAction('');
    setConfirmationChecked(false);
    onClose();
  };

  const getActionIcon = (action: string) => {
    switch (action) {
      case 'activate':
        return <UserCheck className="h-4 w-4 text-green-600" />;
      case 'deactivate':
        return <UserX className="h-4 w-4 text-yellow-600" />;
      case 'suspend':
        return <XCircle className="h-4 w-4 text-orange-600" />;
      case 'delete':
        return <Trash2 className="h-4 w-4 text-red-600" />;
      default:
        return <CheckCircle className="h-4 w-4 text-blue-600" />;
    }
  };

  const getStatusCounts = () => {
    const counts = selectedProfiles.reduce((acc, profile) => {
      acc[profile.status] = (acc[profile.status] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    return counts;
  };

  const statusCounts = getStatusCounts();

  return (
    <UniversalDrawer
      isOpen={isOpen}
      onClose={handleClose}
      title="Bulk Actions"
      description={`Perform actions on ${selectedProfiles.length} selected profile${selectedProfiles.length !== 1 ? 's' : ''}`}
      size="md"
    >
      <div className="space-y-lg">
        {/* Selected Profiles Summary */}
        <Card className="p-md">
          <div className="flex items-center gap-sm mb-md">
            <Users className="h-5 w-5 text-primary" />
            <h3 className="font-semibold">Selected Profiles ({selectedProfiles.length})</h3>
          </div>

          <div className="space-y-sm">
            <div className="flex flex-wrap gap-sm">
              {Object.entries(statusCounts).map(([status, count]) => (
                <Badge 
                  key={status}
                  variant={status === 'active' ? 'success' : status === 'inactive' ? 'destructive' : 'warning'}
                >
                  {count} {status}
                </Badge>
              ))}
            </div>

            <div className="text-sm text-muted-foreground">
              <p>Profiles: {selectedProfiles.slice(0, 3).map(p => p.full_name).join(', ')}
                {selectedProfiles.length > 3 && ` and ${selectedProfiles.length - 3} more`}
              </p>
            </div>
          </div>
        </Card>

        {/* Action Selection */}
        <div className="space-y-md">
          <div className="space-y-sm">
            <Label htmlFor="action">Select Action</Label>
            <Select value={selectedAction} onValueChange={setSelectedAction}>
              <SelectTrigger>
                <SelectValue placeholder="Choose an action to perform" />
              </SelectTrigger>
              <SelectContent>
                {BULK_ACTIONS.map((action) => (
                  <SelectItem key={action.action} value={action.action}>
                    <div className="flex items-center gap-sm">
                      {getActionIcon(action.action)}
                      <div>
                        <p className="font-medium">{action.label}</p>
                        <p className="text-xs text-muted-foreground">{action.description}</p>
                      </div>
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Action Details */}
          {selectedActionConfig && (
            <Card className="p-md">
              <div className="flex items-start gap-sm">
                {getActionIcon(selectedAction)}
                <div className="flex-1">
                  <h4 className="font-semibold">{selectedActionConfig.label}</h4>
                  <p className="text-sm text-muted-foreground mb-sm">
                    {selectedActionConfig.description}
                  </p>

                  {/* Action-specific warnings */}
                  {selectedAction === 'delete' && (
                    <Alert className="mt-sm">
                      <AlertTriangle className="h-4 w-4" />
                      <AlertDescription>
                        <strong>Warning:</strong> This action cannot be undone. All profile data, 
                        including certifications, endorsements, and activity history will be permanently deleted.
                      </AlertDescription>
                    </Alert>
                  )}

                  {selectedAction === 'deactivate' && (
                    <Alert className="mt-sm">
                      <AlertTriangle className="h-4 w-4" />
                      <AlertDescription>
                        Deactivated profiles will lose access to the system and won't appear in active user lists.
                      </AlertDescription>
                    </Alert>
                  )}

                  {selectedAction === 'suspend' && (
                    <Alert className="mt-sm">
                      <AlertTriangle className="h-4 w-4" />
                      <AlertDescription>
                        Suspended profiles will be temporarily blocked from accessing the system.
                      </AlertDescription>
                    </Alert>
                  )}
                </div>
              </div>
            </Card>
          )}

          {/* Confirmation Checkbox */}
          {selectedActionConfig?.confirmationRequired && (
            <div className="flex items-start gap-sm p-md bg-muted/50 rounded-lg">
              <Checkbox
                id="confirmation"
                checked={confirmationChecked}
                onCheckedChange={setConfirmationChecked}
              />
              <div className="flex-1">
                <Label htmlFor="confirmation" className="text-sm font-medium cursor-pointer">
                  I understand the consequences of this action
                </Label>
                <p className="text-xs text-muted-foreground mt-xs">
                  {selectedAction === 'delete' 
                    ? 'I confirm that I want to permanently delete the selected profiles and all associated data.'
                    : `I confirm that I want to ${selectedActionConfig.label.toLowerCase()}.`
                  }
                </p>
              </div>
            </div>
          )}
        </div>

        {/* Action Buttons */}
        <div className="flex justify-end gap-sm pt-md border-t border-border">
          <Button
            variant="outline"
            onClick={handleClose}
            disabled={loading}
          >
            Cancel
          </Button>
          <Button
            onClick={handleExecute}
            loading={loading}
            disabled={!selectedAction || (selectedActionConfig?.confirmationRequired && !confirmationChecked)}
            variant={selectedActionConfig?.variant === 'destructive' ? 'destructive' : 'default'}
          >
            {selectedActionConfig ? selectedActionConfig.label : 'Execute Action'}
          </Button>
        </div>
      </div>
    </UniversalDrawer>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useMemo } from 'react';
import {
  Card,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
  Checkbox,
  Button,
  Badge,
  Input,
  Select,
  SelectTrigger,
  SelectValue,
  SelectContent,
  SelectItem,
} from '@ghxstship/ui';
import type { EmergencyContact, EmergencyContactFilters, EmergencyContactSort } from '../types';
import { formatPhone, formatAddress } from '../types';

interface EmergencyTableViewProps {
  contacts: EmergencyContact[];
  loading: boolean;
  selectedIds: string[];
  filters: EmergencyContactFilters;
  sort: EmergencyContactSort;
  onToggleSelect: (id: string, selected: boolean) => void;
  onToggleAll: (selected: boolean) => void;
  onFiltersChange: (filters: Partial<EmergencyContactFilters>) => void;
  onSortChange: (sort: EmergencyContactSort) => void;
  onExport: (contacts: EmergencyContact[]) => void;
  onEdit: (contact: EmergencyContact) => void;
  onVerify: (contact: EmergencyContact) => void;
}

const verificationBadges: Record<string, { label: string; variant: 'default' | 'secondary' | 'destructive' }> = {
  verified: { label: 'Verified', variant: 'default' },
  pending: { label: 'Pending', variant: 'secondary' },
  unverified: { label: 'Unverified', variant: 'destructive' },
};

export default function EmergencyTableView({
  contacts,
  loading,
  selectedIds,
  filters,
  sort,
  onToggleSelect,
  onToggleAll,
  onFiltersChange,
  onSortChange,
  onExport,
  onEdit,
  onVerify,
}: EmergencyTableViewProps) {
  const allSelected = contacts.length > 0 && contacts.every(contact => selectedIds.includes(contact.id));
  const someSelected = contacts.some(contact => selectedIds.includes(contact.id));

  const sortedContacts = useMemo(() => {
    const copy = [...contacts];
    copy.sort((a, b) => {
      const aValue = (a[sort.field] ?? '').toString().toLowerCase();
      const bValue = (b[sort.field] ?? '').toString().toLowerCase();
      if (aValue < bValue) return sort.direction === 'asc' ? -1 : 1;
      if (aValue > bValue) return sort.direction === 'asc' ? 1 : -1;
      return 0;
    });
    return copy;
  }, [contacts, sort]);

  const handleSort = (field: EmergencyContactSort['field']) => {
    onSortChange({
      field,
      direction: sort.field === field && sort.direction === 'asc' ? 'desc' : 'asc',
    });
  };

  if (loading) {
    return (
      <Card className="p-6 space-y-4">
        <div className="h-5 w-1/3 bg-muted rounded animate-pulse" />
        <div className="h-5 w-full bg-muted rounded animate-pulse" />
        <div className="h-5 w-2/3 bg-muted rounded animate-pulse" />
      </Card>
    );
  }

  return (
    <Card className="overflow-hidden">
      <div className="p-4 space-y-3">
        <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-3">
          <div className="flex-1 flex items-center gap-3">
            <Input
              placeholder="Search emergency contacts..."
              value={filters.search ?? ''}
              onChange={(event) => onFiltersChange({ search: event.target.value })}
            />
            <Select
              value={filters.priority ?? 'all'}
              onValueChange={(value) => onFiltersChange({ priority: value as EmergencyContactFilters['priority'] })}
            >
              <SelectTrigger className="w-40">
                <SelectValue placeholder="Priority" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All priorities</SelectItem>
                <SelectItem value="critical">Critical</SelectItem>
                <SelectItem value="high">High</SelectItem>
                <SelectItem value="medium">Medium</SelectItem>
                <SelectItem value="low">Low</SelectItem>
              </SelectContent>
            </Select>
            <Select
              value={filters.verification_status ?? 'all'}
              onValueChange={(value) => onFiltersChange({ verification_status: value as EmergencyContactFilters['verification_status'] })}
            >
              <SelectTrigger className="w-40">
                <SelectValue placeholder="Verification" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All statuses</SelectItem>
                <SelectItem value="verified">Verified</SelectItem>
                <SelectItem value="pending">Pending</SelectItem>
                <SelectItem value="unverified">Unverified</SelectItem>
              </SelectContent>
            </Select>
          </div>
          <div className="flex items-center gap-3">
            {selectedIds.length > 0 ? (
              <Button
                variant="outline"
                size="sm"
                onClick={() => onExport(contacts.filter(contact => selectedIds.includes(contact.id)))}
              >
                <Download className="mr-2 h-4 w-4" />
                Export ({selectedIds.length})
              </Button>
            ) : null}
            <Checkbox
              checked={allSelected}
              indeterminate={someSelected && !allSelected}
              onChange={(event) => onToggleAll(event.target.checked)}
            />
          </div>
        </div>
      </div>

      <Table>
        <TableHeader>
          <TableRow>
            <TableHead className="w-12">
              <Checkbox
                checked={allSelected}
                indeterminate={someSelected && !allSelected}
                onChange={(event) => onToggleAll(event.target.checked)}
              />
            </TableHead>
            <TableHead className="cursor-pointer" onClick={() => handleSort('name')}>
              Name <ArrowUpDown className="inline h-4 w-4 ml-1 text-muted-foreground" />
            </TableHead>
            <TableHead onClick={() => handleSort('relationship')} className="cursor-pointer">
              Relationship <ArrowUpDown className="inline h-4 w-4 ml-1 text-muted-foreground" />
            </TableHead>
            <TableHead>Contact Info</TableHead>
            <TableHead onClick={() => handleSort('priority_level')} className="cursor-pointer">
              Priority <ArrowUpDown className="inline h-4 w-4 ml-1 text-muted-foreground" />
            </TableHead>
            <TableHead>Status</TableHead>
            <TableHead>Actions</TableHead>
          </TableRow>
        </TableHeader>
        <TableBody>
          {sortedContacts.length === 0 ? (
            <TableRow>
              <TableCell colSpan={7} className="py-10 text-center text-muted-foreground">
                No emergency contacts found.
              </TableCell>
            </TableRow>
          ) : (
            sortedContacts.map(contact => {
              const selected = selectedIds.includes(contact.id);
              const verificationBadge = verificationBadges[contact.verification_status] ?? verificationBadges.pending;
              return (
                <TableRow key={contact.id} className={selected ? 'bg-muted/30' : undefined}>
                  <TableCell>
                    <Checkbox
                      checked={selected}
                      onChange={(event) => onToggleSelect(contact.id, event.target.checked)}
                    />
                  </TableCell>
                  <TableCell className="font-medium">{contact.name}</TableCell>
                  <TableCell>{contact.relationship}</TableCell>
                  <TableCell className="space-y-1 text-sm text-muted-foreground">
                    <div>{formatPhone(contact.phone_primary)}</div>
                    {contact.email ? <div>{contact.email}</div> : null}
                    {formatAddress(contact) ? <div>{formatAddress(contact)}</div> : null}
                  </TableCell>
                  <TableCell>
                    <Badge variant={contact.priority_level === 'critical' ? 'destructive' : 'secondary'}>
                      {contact.priority_level.toUpperCase()}
                    </Badge>
                  </TableCell>
                  <TableCell>
                    <Badge variant={verificationBadge.variant} className="flex items-center gap-1">
                      {contact.verification_status === 'verified' ? (
                        <ShieldCheck className="h-4 w-4" />
                      ) : (
                        <ShieldAlert className="h-4 w-4" />
                      )}
                      {verificationBadge.label}
                    </Badge>
                  </TableCell>
                  <TableCell className="space-x-2">
                    <Button variant="outline" size="sm" onClick={() => onEdit(contact)}>
                      Edit
                    </Button>
                    {contact.verification_status !== 'verified' ? (
                      <Button size="sm" onClick={() => onVerify(contact)}>
                        Verify
                      </Button>
                    ) : null}
                  </TableCell>
                </TableRow>
              );
            })
          )}
        </TableBody>
      </Table>
    </Card>
  );
}

'use client';


import { ThumbsUp, Edit, Plus, Star, User, Calendar, Trash2 } from "lucide-react";
import { useCallback, useEffect, useMemo, useState, type ChangeEvent, type FormEvent } from 'react';
import { createBrowserClient } from '@ghxstship/auth';
import {
  Badge,
  Button,
  Card,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Textarea,
  UnifiedInput,
} from '@ghxstship/ui';
import AppDrawer from '@/app/_components/shared/AppDrawer';

interface Endorsement {
  id: string;
  endorser_name: string;
  endorser_title?: string;
  endorser_company?: string;
  relationship: string;
  endorsement_text: string;
  skills_endorsed: string[];
  rating: number;
  date_received: string;
  is_public: boolean;
  created_at: string;
}

type RelationshipOption = 'colleague' | 'supervisor' | 'client' | 'subordinate' | 'other';

interface EndorsementFormData {
  endorserName: string;
  endorserTitle: string;
  endorserCompany: string;
  relationship: RelationshipOption;
  endorsementText: string;
  skills: string[];
  rating: string;
  dateReceived: string;
  isPublic: boolean;
}

const relationshipOptions: ReadonlyArray<{ value: RelationshipOption; label: string }> = [
  { value: 'colleague', label: 'Colleague' },
  { value: 'supervisor', label: 'Supervisor' },
  { value: 'client', label: 'Client' },
  { value: 'subordinate', label: 'Subordinate' },
  { value: 'other', label: 'Other' },
];

const ratingOptions: ReadonlyArray<{ value: string; label: string }> = [
  { value: '5', label: '5 Stars - Excellent' },
  { value: '4', label: '4 Stars - Very Good' },
  { value: '3', label: '3 Stars - Good' },
  { value: '2', label: '2 Stars - Fair' },
  { value: '1', label: '1 Star - Poor' },
];

const createInitialFormData = (): EndorsementFormData => ({
  endorserName: '',
  endorserTitle: '',
  endorserCompany: '',
  relationship: 'colleague',
  endorsementText: '',
  skills: [],
  rating: '5',
  dateReceived: new Date().toISOString().split('T')[0],
  isPublic: true,
});

export default function EndorsementsClient({ orgId, userId }: { orgId: string; userId: string }) {
  const supabase = useMemo(() => createBrowserClient(), []);

  const [endorsements, setEndorsements] = useState<Endorsement[]>([]);
  const [loading, setLoading] = useState(true);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [editingEndorsement, setEditingEndorsement] = useState<Endorsement | null>(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [skillInput, setSkillInput] = useState('');
  const [formData, setFormData] = useState<EndorsementFormData>(() => createInitialFormData());
  const [fieldErrors, setFieldErrors] = useState<Partial<Record<keyof EndorsementFormData, string>>>({});
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    loadEndorsements();
  }, [orgId, userId]);

  const loadEndorsements = async () => {
    try {
      setLoading(true);

      // For now, we'll use mock data since endorsements table doesn't exist
      // In a real implementation, this would query the endorsements table
      const mockEndorsements: Endorsement[] = [
        {
          id: '1',
          endorser_name: 'Captain Jack Sparrow',
          endorser_title: 'Senior Captain',
          endorser_company: 'Black Pearl Shipping',
          relationship: 'supervisor',
          endorsement_text: 'An exceptional crew member with outstanding leadership skills and dedication to maritime excellence.',
          skills_endorsed: ['Leadership', 'Navigation', 'Team Management'],
          rating: 5,
          date_received: '2024-01-15',
          is_public: true,
          created_at: '2024-01-15T10:00:00Z'
        },
        {
          id: '2',
          endorser_name: 'Anne Bonny',
          endorser_title: 'First Mate',
          endorser_company: 'Revenge Maritime',
          relationship: 'colleague',
          endorsement_text: 'Reliable and skilled professional with excellent problem-solving abilities.',
          skills_endorsed: ['Problem Solving', 'Communication', 'Reliability'],
          rating: 4,
          date_received: '2024-02-20',
          is_public: true,
          created_at: '2024-02-20T14:30:00Z'
        }
      ];

      setEndorsements(mockEndorsements);
    } catch (error) {
      console.error('Error loading endorsements:', error);
    } finally {
      setLoading(false);
    }
  };

  const updateForm = useCallback(
    <Key extends keyof EndorsementFormData>(field: Key, value: EndorsementFormData[Key]) => {
      setFormData(prev => ({ ...prev, [field]: value }));
      setFieldErrors(prev => {
        if (!prev[field]) {
          return prev;
        }
        const next = { ...prev };
        delete next[field];
        return next;
      });
    },
    []
  );

  const resetFormState = useCallback(() => {
    setFormData(createInitialFormData());
    setFieldErrors({});
    setSkillInput('');
    setError(null);
  }, []);

  const validateForm = useCallback(() => {
    const errors: Partial<Record<keyof EndorsementFormData, string>> = {};

    if (!formData.endorserName.trim()) {
      errors.endorserName = 'Endorser name is required.';
    }

    if (!formData.endorserTitle.trim()) {
      errors.endorserTitle = 'Endorser title is required.';
    }

    if (!formData.endorserCompany.trim()) {
      errors.endorserCompany = 'Company is required.';
    }

    if (!formData.relationship) {
      errors.relationship = 'Relationship is required.';
    }

    if (formData.skills.length === 0) {
      errors.skills = 'Please add at least one skill.';
    }

    if (!formData.endorsementText.trim() || formData.endorsementText.trim().length < 10) {
      errors.endorsementText = 'Endorsement text must be at least 10 characters.';
    }

    if (!formData.rating.trim()) {
      errors.rating = 'Rating is required.';
    } else {
      const ratingValue = Number(formData.rating);
      if (Number.isNaN(ratingValue) || ratingValue < 1 || ratingValue > 5) {
        errors.rating = 'Rating must be between 1 and 5.';
      }
    }

    setFieldErrors(errors);

    if (Object.keys(errors).length > 0) {
      setError('Please correct the highlighted fields.');
      return false;
    }

    setError(null);
    return true;
  }, [formData]);

  const handleSubmit = async (event: FormEvent<HTMLFormElement>) => {
    event.preventDefault();

    if (!validateForm()) {
      return;
    }

    const trimmedName = formData.endorserName.trim();
    const trimmedTitle = formData.endorserTitle.trim();
    const trimmedCompany = formData.endorserCompany.trim();
    const trimmedText = formData.endorsementText.trim();
    const trimmedDate = formData.dateReceived.trim();
    const ratingValue = Number(formData.rating);
    const skills = formData.skills.map(skill => skill.trim()).filter(Boolean);

    setIsSubmitting(true);
    try {
      if (editingEndorsement) {
        // Update existing endorsement
        const updatedEndorsements = endorsements.map(endorsement =>
          endorsement.id === editingEndorsement.id
            ? {
                ...endorsement,
                endorser_name: trimmedName,
                endorser_title: trimmedTitle || undefined,
                endorser_company: trimmedCompany || undefined,
                relationship: formData.relationship,
                endorsement_text: trimmedText,
                skills_endorsed: skills,
                rating: ratingValue,
                date_received: trimmedDate || new Date().toISOString().split('T')[0],
                is_public: formData.isPublic,
              }
            : endorsement
        );
        setEndorsements(updatedEndorsements);
      } else {
        // Create new endorsement
        const newEndorsement: Endorsement = {
          id: Date.now().toString(),
          endorser_name: trimmedName,
          endorser_title: trimmedTitle || undefined,
          endorser_company: trimmedCompany || undefined,
          relationship: formData.relationship,
          endorsement_text: trimmedText,
          skills_endorsed: skills,
          rating: ratingValue,
          date_received: trimmedDate || new Date().toISOString().split('T')[0],
          is_public: formData.isPublic,
          created_at: new Date().toISOString(),
        };
        setEndorsements([newEndorsement, ...endorsements]);
      }

      // Log activity
      await supabase
        .from('user_profile_activity')
        .insert({
          user_id: userId,
          organization_id: orgId,
          activity_type: editingEndorsement ? 'endorsement_updated' : 'endorsement_added',
          activity_description: editingEndorsement
            ? `Updated endorsement from ${trimmedName}`
            : `Added new endorsement from ${trimmedName}`,
          performed_by: userId,
        });

      setDrawerOpen(false);
      setEditingEndorsement(null);
      resetFormState();
    } catch (error) {
      console.error('Error saving endorsement:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleEdit = (endorsement: Endorsement) => {
    setEditingEndorsement(endorsement);
    setFormData({
      endorserName: endorsement.endorser_name,
      endorserTitle: endorsement.endorser_title || '',
      endorserCompany: endorsement.endorser_company || '',
      relationship: endorsement.relationship as RelationshipOption,
      endorsementText: endorsement.endorsement_text,
      skills: endorsement.skills_endorsed ?? [],
      rating: String(endorsement.rating),
      dateReceived: endorsement.date_received || new Date().toISOString().split('T')[0],
      isPublic: endorsement.is_public,
    });
    setFieldErrors({});
    setError(null);
    setSkillInput('');
    setDrawerOpen(true);
  };

  const handleDelete = async (endorsementId: string) => {
    if (confirm('Are you sure you want to delete this endorsement?')) {
      const endorsement = endorsements.find(e => e.id === endorsementId);
      setEndorsements(endorsements.filter(e => e.id !== endorsementId));

      // Log activity
      if (endorsement) {
        await supabase
          .from('user_profile_activity')
          .insert({
            user_id: userId,
            organization_id: orgId,
            activity_type: 'endorsement_deleted',
            activity_description: `Deleted endorsement from ${endorsement.endorser_name}`,
            performed_by: userId
          });
      }
    }
  };

  const addSkill = () => {
    if (skillInput.trim()) {
      const newSkill = skillInput.trim();
      if (!formData.skills.includes(newSkill)) {
        setFormData(prev => ({ ...prev, skills: [...prev.skills, newSkill] }));
        setFieldErrors(prev => {
          if (!prev.skills) {
            return prev;
          }
          const next = { ...prev };
          delete next.skills;
          return next;
        });
      }
      setSkillInput('');
    }
  };

  const removeSkill = (skillToRemove: string) => {
    setFormData(prev => ({ ...prev, skills: prev.skills.filter(skill => skill !== skillToRemove) }));
  };

  const renderStars = (rating: number) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star
        key={i}
        className={`h-4 w-4 ${i < rating ? 'color-warning fill-current' : 'color-muted'}`}
      />
    ));
  };

  const formatRelationship = (relationship: string) => {
    return relationship.charAt(0).toUpperCase() + relationship.slice(1);
  };

  if (loading) {
    return (
      <div className="stack-lg">
        <div className="animate-pulse">
          <div className="h-8 bg-secondary rounded mb-md"></div>
          <div className="stack-md">
            <div className="h-32 bg-secondary rounded"></div>
            <div className="h-32 bg-secondary rounded"></div>
          </div>
        </div>
      </div>
    );
  }

  const handleCloseDrawer = useCallback(() => {
    if (!isSubmitting) {
      setDrawerOpen(false);
      resetFormState();
      setEditingEndorsement(null);
    }
  }, [isSubmitting, resetFormState]);

  const isSubmitDisabled = useMemo(() => {
    if (isSubmitting) {
      return true;
    }

    if (!formData.endorserName.trim()) {
      return true;
    }

    if (!formData.endorserTitle.trim()) {
      return true;
    }

    if (!formData.endorserCompany.trim()) {
      return true;
    }

    if (!formData.relationship) {
      return true;
    }

    if (formData.skills.length === 0) {
      return true;
    }

    const trimmedText = formData.endorsementText.trim();
    if (!trimmedText || trimmedText.length < 10) {
      return true;
    }

    if (!formData.rating.trim()) {
      return true;
    }

    const ratingValue = Number(formData.rating);
    if (Number.isNaN(ratingValue) || ratingValue < 1 || ratingValue > 5) {
      return true;
    }

    return false;
  }, [formData, isSubmitting]);

  return (
    <div className="stack-lg">
      <div className="flex items-center justify-between">
        <h2 className="text-heading-4 text-heading-4">Endorsements</h2>
        <Button 
          onClick={() => {
            setEditingEndorsement(null);
            resetFormState();
            setDrawerOpen(true);
          }}
          className="flex items-center gap-sm"
        >
          <Plus className="h-4 w-4" />
          Add Endorsement
        </Button>
      </div>

      {endorsements.length === 0 ? (
        <Card>
          <div className="p-xl text-center">
            <ThumbsUp className="h-12 w-12 color-muted mx-auto mb-md" />
            <h3 className="text-body text-heading-4 mb-sm">No Endorsements Yet</h3>
            <p className="color-muted mb-md">
              Start building your professional reputation by adding endorsements from colleagues and clients.
            </p>
            <Button 
              onClick={() => {
                setEditingEndorsement(null);
                resetFormState();
                setDrawerOpen(true);
              }}
              variant="default"
              className="flex items-center gap-sm"
            >
              <Plus className="h-4 w-4" />
              Add Your First Endorsement
            </Button>
          </div>
        </Card>
      ) : (
        <div className="stack-md">
          {endorsements.map((endorsement: Endorsement) => (
            <Card key={endorsement.id}>
              <div className="p-lg">
                <div className="flex items-start justify-between mb-md">
                  <div className="flex items-center gap-sm">
                    <div className="h-10 w-10 bg-accent/10 rounded-full flex items-center justify-center">
                      <User className="h-5 w-5 color-accent" />
                    </div>
                    <div>
                      <h3 className="text-body text-heading-4">{endorsement.endorser_name}</h3>
                      {endorsement.endorser_title && (
                        <p className="text-body-sm color-muted">
                          {endorsement.endorser_title}
                          {endorsement.endorser_company && ` at ${endorsement.endorser_company}`}
                        </p>
                      )}
                      <div className="flex items-center gap-sm mt-xs">
                        <Badge variant="outline">
                          {formatRelationship(endorsement.relationship)}
                        </Badge>
                        <div className="flex items-center gap-xs">
                          {renderStars(endorsement.rating)}
                        </div>
                      </div>
                    </div>
                  </div>
                  <div className="flex items-center gap-sm">
                    <Button
                      variant="outline"

                      onClick={() => handleEdit(endorsement)}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="outline"

                      onClick={() => handleDelete(endorsement.id)}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>

                <blockquote className="color-muted italic mb-md border-l-4 border-primary/20 pl-md">
                  "{endorsement.endorsement_text}"
                </blockquote>

                <div className="flex items-center justify-between">
                  <div className="flex flex-wrap gap-sm">
                    {endorsement.skills_endorsed.map((skill: string, index: number) => (
                      <Badge key={index} variant="secondary">
                        {skill}
                      </Badge>
                    ))}
                  </div>
                  <div className="flex items-center gap-sm text-body-sm color-muted">
                    <Calendar className="h-4 w-4" />
                    {new Date(endorsement.date_received).toLocaleDateString()}
                  </div>
                </div>
              </div>
            </Card>
          ))}
        </div>
      )}

      <AppDrawer
        open={drawerOpen}
        onClose={handleCloseDrawer}
        record={editingEndorsement}
        fields={[]}
        mode={editingEndorsement ? 'edit' : 'create'}
        title={editingEndorsement ? 'Edit Endorsement' : 'Add New Endorsement'}
        loading={isSubmitting}
        tabs={[{
          key: 'content',
          label: 'Details',
          content: (
        <form onSubmit={handleSubmit} className="stack-md">
          {error ? <div className="text-body-sm color-destructive">{error}</div> : null}

          <UnifiedInput
            label="Endorser Name"
            placeholder="Enter endorser's full name"
            value={formData.endorserName}
            onChange={(event: ChangeEvent<HTMLInputElement>) =>
              updateForm('endorserName', event.target.value)
            }
            error={fieldErrors.endorserName}
            required
          />

          <div className="grid grid-cols-2 gap-md">
            <UnifiedInput
              label="Title"
              placeholder="Job title"
              value={formData.endorserTitle}
              onChange={(event: ChangeEvent<HTMLInputElement>) =>
                updateForm('endorserTitle', event.target.value)
              }
              error={fieldErrors.endorserTitle}
            />

            <UnifiedInput
              label="Company"
              placeholder="Company name"
              value={formData.endorserCompany}
              onChange={(event: ChangeEvent<HTMLInputElement>) =>
                updateForm('endorserCompany', event.target.value)
              }
              error={fieldErrors.endorserCompany}
            />
          </div>

          <div className="grid grid-cols-2 gap-md">
            <div className="stack-sm">
              <label className="text-body-sm form-label">Relationship *</label>
              <Select
                value={formData.relationship}
                onValueChange={value => updateForm('relationship', value as RelationshipOption)}
              >
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {relationshipOptions.map(option => (
                    <SelectItem key={option.value} value={option.value}>
                      {option.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              {fieldErrors.relationship ? (
                <p className="text-body-sm color-destructive">{fieldErrors.relationship}</p>
              ) : null}
            </div>

            <UnifiedInput
              label="Date Received"
              type="date"
              value={formData.dateReceived}
              onChange={(event: ChangeEvent<HTMLInputElement>) =>
                updateForm('dateReceived', event.target.value)
              }
            />
          </div>

          <Textarea
            label="Endorsement Text"
            placeholder="Enter the endorsement text"
            value={formData.endorsementText}
            onChange={(event: ChangeEvent<HTMLTextAreaElement>) =>
              updateForm('endorsementText', event.target.value)
            }
            error={fieldErrors.endorsementText}
            rows={4}
          />

          <div>
            <label className="block text-body-sm form-label mb-sm">Skills Endorsed</label>
            <div className="flex gap-sm mb-sm">
              <UnifiedInput
                placeholder="Add a skill"
                value={skillInput}
                onChange={(event: ChangeEvent<HTMLInputElement>) => setSkillInput(event.target.value)}
                onKeyDown={event => {
                  if (event.key === 'Enter') {
                    event.preventDefault();
                    addSkill();
                  }
                }}
              />
              <Button type="button" onClick={addSkill}>
                Add
              </Button>
            </div>
            <div className="flex flex-wrap gap-sm">
              {formData.skills.map(skill => (
                <Badge key={skill} variant="outline" className="flex items-center gap-xs">
                  {skill}
                  <button
                    type="button"
                    onClick={() => removeSkill(skill)}
                    className="ml-xs color-destructive hover:color-destructive/80"
                  >
                    Ã—
                  </button>
                </Badge>
              ))}
            </div>
            {fieldErrors.skills ? (
              <p className="text-body-sm color-destructive mt-xs">{fieldErrors.skills}</p>
            ) : null}
          </div>

          <div className="grid grid-cols-2 gap-md">
            <div className="stack-sm">
              <label className="text-body-sm form-label">Rating *</label>
              <Select
                value={formData.rating}
                onValueChange={value => updateForm('rating', value)}
              >
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {ratingOptions.map(option => (
                    <SelectItem key={option.value} value={option.value}>
                      {option.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              {fieldErrors.rating ? (
                <p className="text-body-sm color-destructive">{fieldErrors.rating}</p>
              ) : null}
            </div>

            <div className="flex items-center gap-sm">
              <input
                type="checkbox"
                id="isPublic"
                checked={formData.isPublic}
                onChange={(event: ChangeEvent<HTMLInputElement>) =>
                  updateForm('isPublic', event.target.checked)
                }
                className="rounded border-border"
              />
              <label htmlFor="isPublic" className="text-body-sm form-label">
                Make this endorsement public
              </label>
            </div>
          </div>

          <div className="flex justify-end gap-sm pt-md">
            <Button type="button" variant="outline" onClick={handleCloseDrawer} disabled={isSubmitting}>
              Cancel
            </Button>
            <Button type="submit" disabled={isSubmitDisabled}>
              {isSubmitting ? 'Savingâ€¦' : editingEndorsement ? 'Update Endorsement' : 'Save Endorsement'}
            </Button>
          </div>
        </form>
          ),
        }]}
      />
    </div>
  );
}

'use client';


import { Activity, AlertTriangle, Calendar, Heart, Plus, Shield, Trash2, Edit } from "lucide-react";
import { useCallback, useEffect, useMemo, useState, type ChangeEvent, type FormEvent } from 'react';
import { createBrowserClient } from '@ghxstship/auth';
import {
  Badge,
  Button,
  Card,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Textarea,
  UnifiedInput,
} from '@ghxstship/ui';
import AppDrawer from '@/app/_components/shared/AppDrawer';

type RecordType = 'medical' | 'vaccination' | 'allergy' | 'medication' | 'condition' | 'emergency';
type SeverityLevel = 'low' | 'medium' | 'high' | 'critical';

interface HealthRecord {
  id: string;
  record_type: RecordType;
  title: string;
  description?: string;
  date_recorded: string;
  expiry_date?: string;
  provider?: string;
  document_url?: string;
  is_active: boolean;
  severity?: SeverityLevel;
  notes?: string;
  created_at: string;
}

interface HealthRecordFormData {
  recordType: RecordType;
  title: string;
  description: string;
  dateRecorded: string;
  expiryDate: string;
  provider: string;
  documentUrl: string;
  isActive: boolean;
  severity: SeverityLevel;
  notes: string;
}

const recordTypeOptions: ReadonlyArray<{ value: RecordType; label: string }> = [
  { value: 'medical', label: 'Medical' },
  { value: 'vaccination', label: 'Vaccination' },
  { value: 'allergy', label: 'Allergy' },
  { value: 'medication', label: 'Medication' },
  { value: 'condition', label: 'Condition' },
  { value: 'emergency', label: 'Emergency' },
];

const severityOptions: ReadonlyArray<{ value: SeverityLevel; label: string }> = [
  { value: 'low', label: 'Low' },
  { value: 'medium', label: 'Medium' },
  { value: 'high', label: 'High' },
  { value: 'critical', label: 'Critical' },
];

const filterOptions: ReadonlyArray<RecordType | 'all'> = [
  'all',
  'medical',
  'vaccination',
  'allergy',
  'medication',
  'condition',
  'emergency',
];

const createInitialFormData = (): HealthRecordFormData => ({
  recordType: 'medical',
  title: '',
  description: '',
  dateRecorded: new Date().toISOString().split('T')[0],
  expiryDate: '',
  provider: '',
  documentUrl: '',
  isActive: true,
  severity: 'low',
  notes: '',
});

export default function HealthClient({ orgId, userId }: { orgId: string; userId: string }) {
  const supabase = useMemo(() => createBrowserClient(), []);

  const [healthRecords, setHealthRecords] = useState<HealthRecord[]>([]);
  const [loading, setLoading] = useState(true);
  const [filterType, setFilterType] = useState<RecordType | 'all'>('all');

  const [isDrawerOpen, setIsDrawerOpen] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [editingRecord, setEditingRecord] = useState<HealthRecord | null>(null);
  const [formData, setFormData] = useState<HealthRecordFormData>(() => createInitialFormData());
  const [fieldErrors, setFieldErrors] = useState<Partial<Record<keyof HealthRecordFormData, string>>>({});
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    void loadHealthRecords();
  }, [orgId, userId]);

  const loadHealthRecords = useCallback(async () => {
    try {
      setLoading(true);

      const mockRecords: HealthRecord[] = [
        {
          id: '1',
          record_type: 'vaccination',
          title: 'COVID-19 Vaccination',
          description: 'Pfizer-BioNTech COVID-19 Vaccine',
          date_recorded: '2024-01-15',
          expiry_date: '2025-01-15',
          provider: 'Maritime Health Services',
          is_active: true,
          severity: 'low',
          notes: 'Booster shot completed',
          created_at: '2024-01-15T10:00:00Z',
        },
        {
          id: '2',
          record_type: 'allergy',
          title: 'Shellfish Allergy',
          description: 'Severe allergic reaction to shellfish',
          date_recorded: '2023-06-10',
          provider: 'Dr. Maritime',
          is_active: true,
          severity: 'high',
          notes: 'Carry EpiPen at all times',
          created_at: '2023-06-10T14:30:00Z',
        },
        {
          id: '3',
          record_type: 'condition',
          title: 'Hypertension',
          description: 'High blood pressure - controlled',
          date_recorded: '2023-03-20',
          provider: 'Blackwater Medical Center',
          is_active: true,
          severity: 'medium',
          notes: 'Regular monitoring required',
          created_at: '2023-03-20T09:15:00Z',
        },
      ];

      setHealthRecords(mockRecords);
    } catch (loadError) {
      console.error('Error loading health records:', loadError);
    } finally {
      setLoading(false);
    }
  }, []);

  const updateForm = useCallback(
    <Key extends keyof HealthRecordFormData>(field: Key, value: HealthRecordFormData[Key]) => {
      setFormData(prev => ({ ...prev, [field]: value }));
      setFieldErrors(prev => {
        if (!prev[field]) {
          return prev;
        }
        const next = { ...prev };
        delete next[field];
        return next;
      });
    },
    []
  );

  const handleInputChange = useCallback(
    (event: ChangeEvent<HTMLInputElement>) => {
      const { name, value, type, checked } = event.target;
      if (type === 'checkbox') {
        updateForm(name as keyof HealthRecordFormData, checked as unknown as HealthRecordFormData[keyof HealthRecordFormData]);
        return;
      }
      updateForm(name as keyof HealthRecordFormData, value as HealthRecordFormData[keyof HealthRecordFormData]);
    },
    [updateForm]
  );

  const handleTextareaChange = useCallback(
    (event: ChangeEvent<HTMLTextAreaElement>) => {
      const { name, value } = event.target;
      updateForm(name as keyof HealthRecordFormData, value as HealthRecordFormData[keyof HealthRecordFormData]);
    },
    [updateForm]
  );

  const handleSelectChange = useCallback(
    (field: keyof HealthRecordFormData) => (value: string) => {
      updateForm(field, value as HealthRecordFormData[typeof field]);
    },
    [updateForm]
  );

  const resetFormState = useCallback(() => {
    setFormData(createInitialFormData());
    setFieldErrors({});
    setError(null);
    setEditingRecord(null);
  }, []);

  const validateForm = useCallback(() => {
    const errors: Partial<Record<keyof HealthRecordFormData, string>> = {};

    if (!formData.title.trim()) {
      errors.title = 'Title is required.';
    }

    const trimmedDocumentUrl = formData.documentUrl.trim();
    if (trimmedDocumentUrl) {
      try {
        new URL(trimmedDocumentUrl);
      } catch {
        errors.documentUrl = 'Document URL must be a valid URL.';
      }
    }

    if (formData.expiryDate.trim() && formData.dateRecorded.trim() && formData.expiryDate < formData.dateRecorded) {
      errors.expiryDate = 'Expiry date cannot be earlier than recorded date.';
    }

    setFieldErrors(errors);

    if (Object.keys(errors).length > 0) {
      setError('Please correct the highlighted fields.');
      return false;
    }

    setError(null);
    return true;
  }, [formData]);

  const handleOpen = useCallback(() => {
    resetFormState();
    setIsDrawerOpen(true);
  }, [resetFormState]);

  const handleClose = useCallback(() => {
    if (!isSubmitting) {
      resetFormState();
      setIsDrawerOpen(false);
    }
  }, [isSubmitting, resetFormState]);

  const handleSubmit = useCallback(
    async (event: FormEvent<HTMLFormElement>) => {
      event.preventDefault();

      if (!validateForm()) {
        return;
      }

      const trimmedTitle = formData.title.trim();
      const trimmedDescription = formData.description.trim();
      const trimmedProvider = formData.provider.trim();
      const trimmedDocumentUrl = formData.documentUrl.trim();
      const trimmedNotes = formData.notes.trim();

      const payload: HealthRecord = {
        id: editingRecord?.id ?? Date.now().toString(),
        record_type: formData.recordType,
        title: trimmedTitle,
        description: trimmedDescription || undefined,
        date_recorded: formData.dateRecorded,
        expiry_date: formData.expiryDate || undefined,
        provider: trimmedProvider || undefined,
        document_url: trimmedDocumentUrl || undefined,
        is_active: formData.isActive,
        severity: formData.severity,
        notes: trimmedNotes || undefined,
        created_at: editingRecord?.created_at ?? new Date().toISOString(),
      };

      try {
        setIsSubmitting(true);

        if (editingRecord) {
          setHealthRecords(prev => prev.map(record => (record.id === editingRecord.id ? payload : record)));
        } else {
          setHealthRecords(prev => [payload, ...prev]);
        }

        await supabase
          .from('user_profile_activity')
          .insert({
            user_id: userId,
            organization_id: orgId,
            activity_type: editingRecord ? 'health_record_updated' : 'health_record_added',
            activity_description: editingRecord
              ? `Updated health record: ${trimmedTitle}`
              : `Added new health record: ${trimmedTitle}`,
            performed_by: userId,
          });

        resetFormState();
        setIsDrawerOpen(false);
      } catch (submitError) {
        const message = submitError instanceof Error ? submitError.message : 'Unknown error';
        console.error('Error saving health record:', submitError);
        setError(message);
      } finally {
        setIsSubmitting(false);
      }
    },
    [editingRecord, formData, orgId, resetFormState, supabase, userId, validateForm]
  );

  const handleEdit = useCallback(
    (record: HealthRecord) => {
      setEditingRecord(record);
      setFormData({
        recordType: record.record_type,
        title: record.title,
        description: record.description || '',
        dateRecorded: record.date_recorded,
        expiryDate: record.expiry_date || '',
        provider: record.provider || '',
        documentUrl: record.document_url || '',
        isActive: record.is_active,
        severity: record.severity ?? 'low',
        notes: record.notes || '',
      });
      setFieldErrors({});
      setError(null);
      setIsDrawerOpen(true);
    },
    []
  );

  const handleDelete = useCallback(
    async (recordId: string) => {
      if (!confirm('Are you sure you want to delete this health record?')) {
        return;
      }

      const record = healthRecords.find(r => r.id === recordId);
      setHealthRecords(prev => prev.filter(r => r.id !== recordId));

      if (record) {
        await supabase
          .from('user_profile_activity')
          .insert({
            user_id: userId,
            organization_id: orgId,
            activity_type: 'health_record_deleted',
            activity_description: `Deleted health record: ${record.title}`,
            performed_by: userId,
          });
      }
    },
    [healthRecords, orgId, supabase, userId]
  );

  const getRecordTypeIcon = useCallback((type: RecordType) => {
    const icons: Record<RecordType, typeof Heart> = {
      medical: Heart,
      vaccination: Shield,
      allergy: AlertTriangle,
      medication: Activity,
      condition: Heart,
      emergency: AlertTriangle,
    };
    return icons[type] || Heart;
  }, []);

  const getRecordTypeColor = useCallback((type: RecordType) => {
    const colors: Record<RecordType, string> = {
      medical: 'blue',
      vaccination: 'green',
      allergy: 'red',
      medication: 'purple',
      condition: 'orange',
      emergency: 'red',
    };
    return colors[type] || 'blue';
  }, []);

  const getSeverityBadgeVariant = useCallback((severity?: SeverityLevel) => {
    const variants: Record<SeverityLevel, 'success' | 'warning' | 'destructive' | 'secondary'> = {
      low: 'success',
      medium: 'warning',
      high: 'destructive',
      critical: 'destructive',
    };
    return severity ? variants[severity] : 'secondary';
  }, []);

  const filteredRecords = useMemo(() => {
    if (filterType === 'all') {
      return healthRecords;
    }
    return healthRecords.filter(record => record.record_type === filterType);
  }, [filterType, healthRecords]);

  const isSubmitDisabled = useMemo(() => {
    if (isSubmitting) {
      return true;
    }

    if (!formData.title.trim()) {
      return true;
    }

    const trimmedDocumentUrl = formData.documentUrl.trim();
    if (trimmedDocumentUrl) {
      try {
        new URL(trimmedDocumentUrl);
      } catch {
        return true;
      }
    }

    if (formData.expiryDate.trim() && formData.dateRecorded.trim() && formData.expiryDate < formData.dateRecorded) {
      return true;
    }

    return false;
  }, [formData.dateRecorded, formData.documentUrl, formData.expiryDate, formData.title, isSubmitting]);

  if (loading) {
    return (
      <div className="stack-lg">
        <div className="animate-pulse">
          <div className="h-8 bg-secondary rounded mb-md" />
          <div className="stack-md">
            <div className="h-32 bg-secondary rounded" />
            <div className="h-32 bg-secondary rounded" />
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="stack-lg">
      <div className="flex items-center justify-between">
        <h2 className="text-heading-4 text-heading-4">Health Records</h2>
        <Button onClick={handleOpen} className="flex items-center gap-sm">
          <Plus className="h-4 w-4" />
          Add Health Record
        </Button>
      </div>

      <div className="flex gap-sm flex-wrap">
        {filterOptions.map(option => (
          <Button
            key={option}
            variant={filterType === option ? 'default' : 'outline'}
            onClick={() => setFilterType(option)}
          >
            {option === 'all' ? 'All Records' : option.charAt(0).toUpperCase() + option.slice(1)}
          </Button>
        ))}
      </div>

      {filteredRecords.length === 0 ? (
        <Card>
          <div className="p-xl text-center">
            <Heart className="h-12 w-12 color-muted mx-auto mb-md" />
            <h3 className="text-body text-heading-4 mb-sm">No Health Records</h3>
            <p className="color-muted mb-md">
              Keep track of your medical history, vaccinations, allergies, and health conditions.
            </p>
            <Button onClick={handleOpen}>Add Your First Health Record</Button>
          </div>
        </Card>
      ) : (
        <div className="stack-md">
          {filteredRecords.map(record => {
            const IconComponent = getRecordTypeIcon(record.record_type);
            const color = getRecordTypeColor(record.record_type);
            const isExpiring = Boolean(
              record.expiry_date &&
                new Date(record.expiry_date) <= new Date(Date.now() + 30 * 24 * 60 * 60 * 1000)
            );

            return (
              <Card key={record.id}>
                <div className="p-lg">
                  <div className="flex items-start justify-between mb-md">
                    <div className="flex items-center gap-sm">
                      <div className={`h-10 w-10 rounded-full flex items-center justify-center bg-${color}-100`}>
                        <IconComponent className={`h-5 w-5 text-${color}-600`} />
                      </div>
                      <div>
                        <h3 className="text-body text-heading-4">{record.title}</h3>
                        {record.description ? (
                          <p className="text-body-sm color-muted">{record.description}</p>
                        ) : null}
                        <div className="flex items-center gap-sm mt-xs">
                          <Badge variant="outline">
                            {record.record_type.charAt(0).toUpperCase() + record.record_type.slice(1)}
                          </Badge>
                          {record.severity ? (
                            <Badge variant={getSeverityBadgeVariant(record.severity)}>
                              {record.severity.charAt(0).toUpperCase() + record.severity.slice(1)}
                            </Badge>
                          ) : null}
                          {!record.is_active ? <Badge variant="secondary">Inactive</Badge> : null}
                          {isExpiring ? <Badge variant="destructive">Expiring Soon</Badge> : null}
                        </div>
                      </div>
                    </div>
                    <div className="flex items-center gap-sm">
                      <Button variant="outline" onClick={() => handleEdit(record)}>
                        <Edit className="h-4 w-4" />
                      </Button>
                      <Button variant="outline" onClick={() => void handleDelete(record.id)}>
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>

                  {record.notes ? (
                    <div className="mb-md p-sm bg-secondary rounded-lg">
                      <p className="text-body-sm">{record.notes}</p>
                    </div>
                  ) : null}

                  <div className="flex items-center justify-between text-body-sm color-muted">
                    <div className="flex items-center gap-md">
                      {record.provider ? <span>Provider: {record.provider}</span> : null}
                      <div className="flex items-center gap-xs">
                        <Calendar className="h-4 w-4" />
                        {new Date(record.date_recorded).toLocaleDateString()}
                      </div>
                    </div>
                    {record.expiry_date ? (
                      <div className="flex items-center gap-xs">
                        <span>Expires: {new Date(record.expiry_date).toLocaleDateString()}</span>
                      </div>
                    ) : null}
                  </div>
                </div>
              </Card>
            );
          })}
        </div>
      )}

      <AppDrawer
        open={isDrawerOpen}
        onClose={handleClose}
        record={editingRecord}
        fields={[]}
        mode={editingRecord ? 'edit' : 'create'}
        title={editingRecord ? 'Edit Health Record' : 'Add New Health Record'}
        loading={isSubmitting}
        tabs={[
          {
            key: 'content',
            label: 'Details',
            content: (
              <form onSubmit={handleSubmit} className="stack-md">
                {error ? <div className="text-body-sm color-destructive">{error}</div> : null}

                <div className="grid grid-cols-1 gap-md md:grid-cols-2">
                  <div className="stack-sm">
                    <label className="text-body-sm form-label">Record Type *</label>
                    <Select value={formData.recordType} onValueChange={handleSelectChange('recordType')}>
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        {recordTypeOptions.map(option => (
                          <SelectItem key={option.value} value={option.value}>
                            {option.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>

                  <div className="stack-sm">
                    <label className="text-body-sm form-label">Severity</label>
                    <Select value={formData.severity} onValueChange={handleSelectChange('severity')}>
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        {severityOptions.map(option => (
                          <SelectItem key={option.value} value={option.value}>
                            {option.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <UnifiedInput
                  id="title"
                  name="title"
                  label="Title *"
                  placeholder="Enter record title"
                  value={formData.title}
                  onChange={handleInputChange}
                  error={fieldErrors.title}
                  required
                />

                <Textarea
                  id="description"
                  name="description"
                  label="Description"
                  placeholder="Enter detailed description"
                  value={formData.description}
                  onChange={handleTextareaChange}
                  rows={3}
                />

                <div className="grid grid-cols-1 gap-md md:grid-cols-2">
                  <UnifiedInput
                    id="dateRecorded"
                    name="dateRecorded"
                    label="Date Recorded"
                    type="date"
                    value={formData.dateRecorded}
                    onChange={handleInputChange}
                  />

                  <UnifiedInput
                    id="expiryDate"
                    name="expiryDate"
                    label="Expiry Date (Optional)"
                    type="date"
                    value={formData.expiryDate}
                    onChange={handleInputChange}
                    error={fieldErrors.expiryDate}
                  />
                </div>

                <UnifiedInput
                  id="provider"
                  name="provider"
                  label="Healthcare Provider"
                  placeholder="Doctor, clinic, or hospital name"
                  value={formData.provider}
                  onChange={handleInputChange}
                />

                <UnifiedInput
                  id="documentUrl"
                  name="documentUrl"
                  label="Document URL (Optional)"
                  placeholder="Link to medical document or report"
                  type="url"
                  value={formData.documentUrl}
                  onChange={handleInputChange}
                  error={fieldErrors.documentUrl}
                />

                <Textarea
                  id="notes"
                  name="notes"
                  label="Notes"
                  placeholder="Additional notes or instructions"
                  value={formData.notes}
                  onChange={handleTextareaChange}
                  rows={3}
                />

                <div className="flex items-center gap-sm">
                  <input
                    type="checkbox"
                    id="isActive"
                    name="isActive"
                    checked={formData.isActive}
                    onChange={handleInputChange}
                    className="rounded border-border"
                  />
                  <label htmlFor="isActive" className="text-body-sm form-label">
                    This record is currently active
                  </label>
                </div>

                <div className="flex justify-end gap-sm pt-md">
                  <Button type="button" variant="outline" onClick={handleClose} disabled={isSubmitting}>
                    Cancel
                  </Button>
                  <Button type="submit" disabled={isSubmitDisabled}>
                    {isSubmitting ? 'Savingâ€¦' : editingRecord ? 'Update Record' : 'Save Record'}
                  </Button>
                </div>
              </form>
            ),
          },
        ]}
      />
    </div>
  );
}

'use client';

import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import React from "react";

import { Badge, Button, Card, UnifiedInput, cn } from '@ghxstship/ui';
import { useState, useEffect } from 'react'
import { createClient } from "../../../../../lib/supabase/client"

interface ClientProps {
  className?: string
}

export default function Client({ className }: ClientProps) {
  const [data, setData] = useState<any[]>([])
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    let isMounted = true
    const supabase = createClient()
    async function fetchRecords() {
      try {
        setLoading(true)
        const { data: auth } = await supabase.auth.getUser()
        let items: unknown[] = []
        if (auth?.user) {
          const { data: profiles, error } = await supabase
            .from('user_profiles')
            .select('id, full_name, status')
            .eq('id', auth.user.id)
            .limit(1)
          if (!error && profiles) {
            const mockData: Array<{ id: string; full_name: string; status: string }> = [
              { id: '1', full_name: 'John Doe', status: 'Active' },
              { id: '2', full_name: 'Jane Smith', status: 'Active' },
            ]
            items = profiles.map((p: unknown) => ({
              id: p.id,
              name: p.full_name || 'Profile',
              status: p.status || 'active'
            }))
          }
        }
        if (isMounted) setData(items)
      } catch (err) {
        // console.error('HealthInfoClient fetch error:', err)
        if (isMounted) setData([])
      } finally {
        if (isMounted) setLoading(false)
      }
    }
    fetchRecords()
    return () => { isMounted = false }
  }, [])

  if (loading) {
    return (
      <div className={className}>
        <Card className="p-md">
          <div className="animate-pulse space-y-md">
            <div className="h-4 bg-secondary rounded w-1/4"></div>
            <div className="h-4 bg-secondary rounded w-1/2"></div>
            <div className="h-4 bg-secondary rounded w-3/4"></div>
          </div>
        </Card>
      </div>
    )
  }

  return (
    <div className={className}>
      <div className="mb-md">
        <h1 className="text-heading-lg text-heading-3 mb-sm">Health Information</h1>
        <p className="color-foreground-subtle">View and manage your health-related profile information</p>
      </div>

      <div className="mb-md">
        <UnifiedInput onChange={(e: React.ChangeEvent<HTMLInputElement>) => {}} placeholder="Search health records..." className="w-full sm:w-auto sm:max-w-sm" aria- />
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-md">
        {data.map((item: unknown) => (
          <Card key={item.id} className="p-md">
            <div className="flex flex-col sm:flex-row items-center justify-between">
              <div>
                <h2 className="text-body text-heading-3">{item.name}</h2>
                <p className="text-body-sm color-foreground-subtle">ID: {item.id}</p>
              </div>
              <div className="flex flex-col sm:flex-row items-center gap-sm">
                <Badge variant={item.status === 'active' ? 'default' : 'secondary'}>
                  {item.status}
                </Badge>
                <Button variant="default" size="sm" onClick={() => console.log("Edit clicked")}>Edit</Button>
              </div>
            </div>
          </Card>
        ))}
      </div>
    </div>
  )
}

'use client';

import { ChevronLeft, ChevronRight, Calendar, Clock, AlertTriangle, Bell, Activity } from "lucide-react";
import { useState, useMemo } from 'react';
import { Card, Button, Badge } from '@ghxstship/ui';
import type { HealthRecord } from '../types';
import {
  RECORD_TYPE_LABELS,
  formatDateShort,
  getDaysUntilExpiry,
  getExpiryUrgency,
  getRecordTypeIcon,
} from '../types';

interface HealthCalendarViewProps {
  records: HealthRecord[];
  loading: boolean;
  onEdit: (record: HealthRecord) => void;
}

export default function HealthCalendarView({
  records,
  loading,
  onEdit,
}: HealthCalendarViewProps) {
  const [currentDate, setCurrentDate] = useState(new Date());

  const currentYear = currentDate.getFullYear();
  const currentMonth = currentDate.getMonth();

  // Get first day of month and number of days
  const firstDayOfMonth = new Date(currentYear, currentMonth, 1);
  const lastDayOfMonth = new Date(currentYear, currentMonth + 1, 0);
  const daysInMonth = lastDayOfMonth.getDate();
  const startingDayOfWeek = firstDayOfMonth.getDay();

  // Generate calendar days
  const calendarDays = useMemo(() => {
    const days = [];

    // Add empty cells for days before the first day of the month
    for (let i = 0; i < startingDayOfWeek; i++) {
      days.push(null);
    }

    // Add days of the month
    for (let day = 1; day <= daysInMonth; day++) {
      days.push(day);
    }

    return days;
  }, [currentYear, currentMonth, daysInMonth, startingDayOfWeek]);

  // Group records by date
  const recordsByDate = useMemo(() => {
    const grouped: Record<string, HealthRecord[]> = {};

    records.forEach(record => {
      // Add to recorded date
      const recordedDate = new Date(record.date_recorded);
      if (recordedDate.getFullYear() === currentYear && recordedDate.getMonth() === currentMonth) {
        const day = recordedDate.getDate();
        const key = `recorded-${day}`;
        if (!grouped[key]) grouped[key] = [];
        grouped[key].push(record);
      }

      // Add to expiry date if exists
      if (record.expiry_date) {
        const expiryDate = new Date(record.expiry_date);
        if (expiryDate.getFullYear() === currentYear && expiryDate.getMonth() === currentMonth) {
          const day = expiryDate.getDate();
          const key = `expiry-${day}`;
          if (!grouped[key]) grouped[key] = [];
          grouped[key].push(record);
        }
      }
    });

    return grouped;
  }, [records, currentYear, currentMonth]);

  const navigateMonth = (direction: 'prev' | 'next') => {
    setCurrentDate(prev => {
      const newDate = new Date(prev);
      if (direction === 'prev') {
        newDate.setMonth(prev.getMonth() - 1);
      } else {
        newDate.setMonth(prev.getMonth() + 1);
      }
      return newDate;
    });
  };

  const goToToday = () => {
    setCurrentDate(new Date());
  };

  const monthName = firstDayOfMonth.toLocaleDateString('en-US', { 
    month: 'long', 
    year: 'numeric' 
  });

  if (loading) {
    return (
      <Card className="p-6">
        <div className="space-y-4">
          <div className="h-8 w-48 bg-muted animate-pulse rounded" />
          <div className="grid grid-cols-7 gap-2">
            {[...Array(35)].map((_, i) => (
              <div key={i} className="h-24 bg-muted animate-pulse rounded" />
            ))}
          </div>
        </div>
      </Card>
    );
  }

  return (
    <div className="space-y-4">
      {/* Header */}
      <Card className="p-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-4">
            <h2 className="text-xl font-semibold flex items-center gap-2">
              <Calendar className="h-5 w-5" />
              {monthName}
            </h2>
            <Button variant="outline" size="sm" onClick={goToToday}>
              Today
            </Button>
          </div>
          <div className="flex items-center gap-2">
            <Button variant="outline" size="sm" onClick={() => navigateMonth('prev')}>
              <ChevronLeft className="h-4 w-4" />
            </Button>
            <Button variant="outline" size="sm" onClick={() => navigateMonth('next')}>
              <ChevronRight className="h-4 w-4" />
            </Button>
          </div>
        </div>
      </Card>

      {/* Legend */}
      <Card className="p-4">
        <div className="flex items-center gap-6 text-sm">
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 bg-blue-500 rounded-full" />
            <span>Recorded</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 bg-orange-500 rounded-full" />
            <span>Expires</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 bg-red-500 rounded-full" />
            <span>Expired</span>
          </div>
          <div className="flex items-center gap-2">
            <Bell className="h-3 w-3" />
            <span>Reminder</span>
          </div>
        </div>
      </Card>

      {/* Calendar */}
      <Card className="p-4">
        <div className="space-y-4">
          {/* Day headers */}
          <div className="grid grid-cols-7 gap-2">
            {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
              <div key={day} className="p-2 text-center font-medium text-muted-foreground">
                {day}
              </div>
            ))}
          </div>

          {/* Calendar grid */}
          <div className="grid grid-cols-7 gap-2">
            {calendarDays.map((day, index) => {
              if (day === null) {
                return <div key={index} className="h-24" />;
              }

              const recordedRecords = recordsByDate[`recorded-${day}`] || [];
              const expiryRecords = recordsByDate[`expiry-${day}`] || [];
              const allDayRecords = [...recordedRecords, ...expiryRecords];

              const isToday = new Date().toDateString() === new Date(currentYear, currentMonth, day).toDateString();

              return (
                <div
                  key={day}
                  className={`h-24 p-1 border rounded-lg ${
                    isToday ? 'bg-primary/10 border-primary' : 'border-border'
                  } ${allDayRecords.length > 0 ? 'hover:bg-muted/50' : ''}`}
                >
                  <div className="h-full flex flex-col">
                    <div className="flex items-center justify-between mb-1">
                      <span className={`text-sm font-medium ${
                        isToday ? 'text-primary' : 'text-foreground'
                      }`}>
                        {day}
                      </span>
                      {allDayRecords.some(r => r.reminder_enabled) && (
                        <Bell className="h-3 w-3 text-muted-foreground" />
                      )}
                    </div>

                    <div className="flex-1 space-y-1 overflow-hidden">
                      {/* Recorded events */}
                      {recordedRecords.slice(0, 2).map((record, i) => (
                        <button
                          key={`recorded-${record.id}-${i}`}
                          onClick={() => onEdit(record)}
                          className="w-full text-left"
                        >
                          <div className="flex items-center gap-1 p-1 bg-blue-100 text-blue-800 rounded text-xs hover:bg-blue-200 transition-colors">
                            <span>{getRecordTypeIcon(record.record_type)}</span>
                            <span className="truncate">{record.title}</span>
                          </div>
                        </button>
                      ))}

                      {/* Expiry events */}
                      {expiryRecords.slice(0, 2 - recordedRecords.length).map((record, i) => {
                        const daysUntilExpiry = getDaysUntilExpiry(record.expiry_date!);
                        const isExpired = daysUntilExpiry < 0;
                        const isExpiringSoon = daysUntilExpiry <= 7 && daysUntilExpiry >= 0;

                        return (
                          <button
                            key={`expiry-${record.id}-${i}`}
                            onClick={() => onEdit(record)}
                            className="w-full text-left"
                          >
                            <div className={`flex items-center gap-1 p-1 rounded text-xs hover:opacity-80 transition-colors ${
                              isExpired 
                                ? 'bg-red-100 text-red-800' 
                                : isExpiringSoon 
                                ? 'bg-orange-100 text-orange-800'
                                : 'bg-yellow-100 text-yellow-800'
                            }`}>
                              {isExpired ? (
                                <AlertTriangle className="h-3 w-3" />
                              ) : (
                                <Clock className="h-3 w-3" />
                              )}
                              <span className="truncate">{record.title}</span>
                            </div>
                          </button>
                        );
                      })}

                      {/* Show more indicator */}
                      {allDayRecords.length > 2 && (
                        <div className="text-xs text-muted-foreground text-center">
                          +{allDayRecords.length - 2} more
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </Card>

      {/* Upcoming Events */}
      {records.length > 0 && (
        <Card className="p-4">
          <h3 className="font-semibold mb-4 flex items-center gap-2">
            <Clock className="h-4 w-4" />
            Upcoming Events
          </h3>
          <div className="space-y-2">
            {records
              .filter(record => {
                if (!record.expiry_date) return false;
                const daysUntil = getDaysUntilExpiry(record.expiry_date);
                return daysUntil >= 0 && daysUntil <= 30;
              })
              .sort((a, b) => getDaysUntilExpiry(a.expiry_date!) - getDaysUntilExpiry(b.expiry_date!))
              .slice(0, 5)
              .map(record => {
                const daysUntil = getDaysUntilExpiry(record.expiry_date!);
                const urgency = getExpiryUrgency(daysUntil);

                return (
                  <button
                    key={record.id}
                    onClick={() => onEdit(record)}
                    className="w-full text-left p-3 border rounded-lg hover:bg-muted/50 transition-colors"
                  >
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-3">
                        <span className="text-lg">{getRecordTypeIcon(record.record_type)}</span>
                        <div>
                          <p className="font-medium">{record.title}</p>
                          <p className="text-sm text-muted-foreground">
                            {RECORD_TYPE_LABELS[record.record_type]}
                            {record.provider && ` â€¢ ${record.provider}`}
                          </p>
                        </div>
                      </div>
                      <div className="text-right">
                        <Badge 
                          variant={urgency === 'critical' ? 'destructive' : 'outline'}
                          className="mb-1"
                        >
                          {daysUntil === 0 ? 'Today' : daysUntil === 1 ? 'Tomorrow' : `${daysUntil} days`}
                        </Badge>
                        <p className="text-xs text-muted-foreground">
                          {formatDateShort(record.expiry_date!)}
                        </p>
                      </div>
                    </div>
                  </button>
                );
              })}

            {records.filter(r => r.expiry_date && getDaysUntilExpiry(r.expiry_date) >= 0 && getDaysUntilExpiry(r.expiry_date) <= 30).length === 0 && (
              <div className="text-center py-8 text-muted-foreground">
                <Activity className="h-8 w-8 mx-auto mb-2 opacity-50" />
                <p>No upcoming events in the next 30 days</p>
              </div>
            )}
          </div>
        </Card>
      )}
    </div>
  );
}

'use client';
