'use client';


import { Plus } from "lucide-react";
import { useMemo, useState } from 'react';
import { createBrowserClient } from '@ghxstship/auth';
import { useRouter } from 'next/navigation';
import { useForm } from 'react-hook-form';
// import { z } from 'zod';
import { zodResolver } from '@hookform/resolvers/zod';
import AppDrawer from '@/app/_components/shared/AppDrawer';
const schema = z.object({
  job_title: z.string().min(2, 'Job title is required'),
  department: z.string().min(2, 'Department is required'),
  employee_id: z.string().optional(),
  hire_date: z.string().optional(),
  skills: z.string().optional(),
  bio: z.string().optional(),
  linkedin_url: z.string().url().optional().or(z.literal('')),
  website_url: z.string().url().optional().or(z.literal('')),
  career_goals: z.string().optional(),
  mentorship_interests: z.string().optional(),
  performance_rating: z.number().min(1).max(5).optional(),
});

type Values = z.infer<typeof schema>;

export default function CreateProfessionalClient({ orgId, userId }: { orgId: string; userId: string }) {
  const router = useRouter();
  const sb = useMemo(() => createBrowserClient(), []);
  const [open, setOpen] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const form = useForm<Values>({
    resolver: zodResolver(schema),
    defaultValues: {
      job_title: '',
      department: '',
      employee_id: '',
      hire_date: '',
      skills: '',
      bio: '',
      linkedin_url: '',
      website_url: '',
      career_goals: '',
      mentorship_interests: '',
      performance_rating: undefined,
    },
    mode: 'onChange',
  });

  async function onSubmit(values: Values) {
    setSubmitting(true);
    setError(null);
    try {
      // Insert professional profile data
      const { error: insErr } = await sb
        .from('user_profiles')
        .upsert({
          user_id: userId,
          organization_id: orgId,
          job_title: values.job_title,
          department: values.department,
          employee_id: values.employee_id || null,
          hire_date: values.hire_date || null,
          skills: values.skills ? values.skills.split(',').map(s => s.trim()) : [],
          bio: values.bio || null,
          linkedin_url: values.linkedin_url || null,
          website_url: values.website_url || null,
          career_goals: values.career_goals || null,
          mentorship_interests: values.mentorship_interests || null,
          performance_rating: values.performance_rating || null,
        });

      if (insErr) throw insErr;

      // Log activity
      await sb.from('user_profile_activity').insert({
        user_id: userId,
        organization_id: orgId,
        activity_type: 'profile_updated',
        description: 'Professional information created',
        metadata: { section: 'professional' }
      });

      // Telemetry
      if (typeof window !== 'undefined' && (window as unknown).posthog) {
        (window as unknown).posthog.capture('profile.professional.created', { 
          organization_id: orgId, 
          user_id: userId 
        });
      }

      setOpen(false);
      form.reset();
      router.refresh();
    } catch (e) {
      if (e instanceof Error) {
        setError(e.message);
      } else {
        setError('Create failed');
      }
    } finally {
      setSubmitting(false);
    }
  }

  return (
    <>
      <Button 
        variant="default" 

        onClick={() => setOpen(true)} 
        aria-label="Add Professional Info" 
        title="Add Professional Info"
      >
        <Plus className="mr-xs h-4 w-4" /> Add Professional Info
      </Button>
      <AppDrawer 
        open={open} 
        onClose={() => setOpen(false)} 
        record={null}
        fields={[]}
        mode="create"
        title="Add Professional Information" 
        tabs={[{
          key: 'content',
          label: 'Details',
          content: (
        <form 
          className="stack-sm" 
          onSubmit={(e: unknown) => { e.preventDefault(); onSubmit(form.getValues()); }} 
          aria-live="polite"
        >
          {error ? <div role="alert" className="mb-sm text-body-sm color-destructive">{error}</div> : null}
          <div className="grid gap-xs">
            <label htmlFor="job_title" className="text-body-sm form-label">Job Title *</label>
            <input 
              id="job_title" 
              className="rounded border  px-md py-xs" 
              value={form.getValues('job_title') || ''} 
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => form.setValue('job_title', e.target.value, { shouldDirty: true })} 
              aria-invalid={!!form.formState.errors.job_title} 
            />
            {form.formState.errors.job_title ? 
              <div className="text-body-sm color-destructive">{String(form.formState.errors.job_title.message)}</div> : null}
          </div>

          <div className="grid gap-xs">
            <label htmlFor="department" className="text-body-sm form-label">Department *</label>
            <input 
              id="department" 
              className="rounded border  px-md py-xs" 
              value={form.getValues('department') || ''} 
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => form.setValue('department', e.target.value, { shouldDirty: true })} 
              aria-invalid={!!form.formState.errors.department} 
            />
            {form.formState.errors.department ? 
              <div className="text-body-sm color-destructive">{String(form.formState.errors.department.message)}</div> : null}
          </div>

          <div className="grid gap-xs">
            <label htmlFor="employee_id" className="text-body-sm form-label">Employee ID</label>
            <input 
              id="employee_id" 
              className="rounded border  px-md py-xs" 
              value={form.getValues('employee_id') || ''} 
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => form.setValue('employee_id', e.target.value, { shouldDirty: true })} 
            />
          </div>

          <div className="grid gap-xs">
            <label htmlFor="hire_date" className="text-body-sm form-label">Hire Date</label>
            <input 
              id="hire_date" 
              type="date" 
              className="rounded border  px-md py-xs" 
              value={form.getValues('hire_date') || ''} 
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => form.setValue('hire_date', e.target.value, { shouldDirty: true })} 
            />
          </div>

          <div className="grid gap-xs">
            <label htmlFor="skills" className="text-body-sm form-label">Skills (comma-separated)</label>
            <textarea 
              id="skills" 
              className="rounded border  px-md py-xs min-h-[60px]" 
              value={form.getValues('skills') || ''} 
              onChange={(e: React.ChangeEvent<HTMLTextAreaElement>) => form.setValue('skills', e.target.value, { shouldDirty: true })} 
              placeholder="e.g. JavaScript, React, Node.js"
            />
          </div>

          <div className="grid gap-xs">
            <label htmlFor="bio" className="text-body-sm form-label">Professional Bio</label>
            <textarea 
              id="bio" 
              className="rounded border  px-md py-xs min-h-[var(--spacing-20)]" 
              value={form.getValues('bio') || ''} 
              onChange={(e: React.ChangeEvent<HTMLTextAreaElement>) => form.setValue('bio', e.target.value, { shouldDirty: true })} 
              placeholder="Brief professional summary..."
            />
          </div>

          <div className="grid gap-xs">
            <label htmlFor="linkedin_url" className="text-body-sm form-label">LinkedIn URL</label>
            <input 
              id="linkedin_url" 
              type="url" 
              className="rounded border  px-md py-xs" 
              value={form.getValues('linkedin_url') || ''} 
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => form.setValue('linkedin_url', e.target.value, { shouldDirty: true })} 
              placeholder="https://linkedin.com/in/username"
            />
          </div>

          <div className="grid gap-xs">
            <label htmlFor="website_url" className="text-body-sm form-label">Website URL</label>
            <input 
              id="website_url" 
              type="url" 
              className="rounded border  px-md py-xs" 
              value={form.getValues('website_url') || ''} 
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => form.setValue('website_url', e.target.value, { shouldDirty: true })} 
              placeholder="https://yourwebsite.com"
            />
          </div>

          <div className="grid gap-xs">
            <label htmlFor="career_goals" className="text-body-sm form-label">Career Goals</label>
            <textarea 
              id="career_goals" 
              className="rounded border  px-md py-xs min-h-[60px]" 
              value={form.getValues('career_goals') || ''} 
              onChange={(e: React.ChangeEvent<HTMLTextAreaElement>) => form.setValue('career_goals', e.target.value, { shouldDirty: true })} 
              placeholder="Your professional aspirations..."
            />
          </div>

          <div className="grid gap-xs">
            <label htmlFor="mentorship_interests" className="text-body-sm form-label">Mentorship Interests</label>
            <textarea 
              id="mentorship_interests" 
              className="rounded border  px-md py-xs min-h-[60px]" 
              value={form.getValues('mentorship_interests') || ''} 
              onChange={(e: React.ChangeEvent<HTMLTextAreaElement>) => form.setValue('mentorship_interests', e.target.value, { shouldDirty: true })} 
              placeholder="Areas where you'd like mentorship or can provide mentorship..."
            />
          </div>

          <div className="grid gap-xs">
            <label htmlFor="performance_rating" className="text-body-sm form-label">Performance Rating (1-5)</label>
            <select 
              id="performance_rating" 
              className="rounded border  px-md py-xs" 
              value={form.getValues('performance_rating') || ''} 
              onChange={(e: React.ChangeEvent<HTMLSelectElement>) => form.setValue('performance_rating', e.target.value ? Number(e.target.value) : undefined, { shouldDirty: true })}
            >
              <option value="">Select rating...</option>
              <option value="1">1 - Needs Improvement</option>
              <option value="2">2 - Below Expectations</option>
              <option value="3">3 - Meets Expectations</option>
              <option value="4">4 - Exceeds Expectations</option>
              <option value="5">5 - Outstanding</option>
            </select>
          </div>

          <div className="flex items-center justify-end gap-sm pt-sm border-t">
            <Button type="button" variant="ghost" onClick={() => setOpen(false)}>Cancel</Button>
            <Button variant="default" disabled={submitting || !form.formState.isDirty}>
              Create
            </Button>
          </div>
        </form>
          )
        }]}
      />
    </>
  );
}

'use client';


import { Plus } from "lucide-react";
import { useMemo, useState } from 'react';
import { createBrowserClient } from '@ghxstship/auth';
import { useRouter } from 'next/navigation';
import { useForm } from 'react-hook-form';
// import { z } from 'zod';
import { zodResolver } from '@hookform/resolvers/zod';
import AppDrawer from '@/app/_components/shared/AppDrawer';
const schema = z.object({
  travel_type: z.enum(['business', 'personal', 'training', 'conference', 'relocation']),
  destination: z.string().min(2, 'Destination is required'),
  purpose: z.string().min(2, 'Purpose is required'),
  start_date: z.string().min(1, 'Start date is required'),
  end_date: z.string().min(1, 'End date is required'),
  accommodation: z.string().optional(),
  transportation: z.string().optional(),
  visa_required: z.boolean().default(false),
  visa_status: z.enum(['not_required', 'pending', 'approved', 'denied']).optional(),
  total_expense: z.number().min(0).optional(),
  currency: z.string().default('USD'),
  notes: z.string().optional(),
});

type Values = z.infer<typeof schema>;

export default function CreateTravelRecordClient({ orgId, userId }: { orgId: string; userId: string }) {
  const router = useRouter();
  const sb = useMemo(() => createBrowserClient(), []);
  const [open, setOpen] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const form = useForm<Values>({
    resolver: zodResolver(schema),
    defaultValues: {
      travel_type: 'business',
      destination: '',
      purpose: '',
      start_date: '',
      end_date: '',
      accommodation: '',
      transportation: '',
      visa_required: false,
      visa_status: 'not_required',
      total_expense: undefined,
      currency: 'USD',
      notes: '',
    },
    mode: 'onChange',
  });

  async function onSubmit(values: Values) {
    setSubmitting(true);
    setError(null);
    try {
      // Create mock travel record data structure
      const travelRecordData = {
        id: crypto.randomUUID(),
        user_id: userId,
        organization_id: orgId,
        travel_type: values.travel_type,
        destination: values.destination,
        purpose: values.purpose,
        start_date: values.start_date,
        end_date: values.end_date,
        accommodation: values.accommodation || null,
        transportation: values.transportation || null,
        visa_required: values.visa_required,
        visa_status: values.visa_status || 'not_required',
        total_expense: values.total_expense || null,
        currency: values.currency,
        notes: values.notes || null,
        created_at: new Date().toISOString(),
      };

      // Log activity
      await sb.from('user_profile_activity').insert({
        user_id: userId,
        organization_id: orgId,
        activity_type: 'travel_record_added',
        description: `Added ${values.travel_type} travel to ${values.destination}`,
        metadata: { 
          travel_type: values.travel_type, 
          destination: values.destination,
          purpose: values.purpose 
        }
      });

      // Telemetry
      if (typeof window !== 'undefined' && (window as unknown).posthog) {
        (window as unknown).posthog.capture('profile.travel.created', { 
          organization_id: orgId, 
          user_id: userId,
          travel_type: values.travel_type
        });
      }

      setOpen(false);
      form.reset();
      router.refresh();
    } catch (e) {
      if (e instanceof Error) {
        setError(e.message);
      } else {
        setError('Create failed');
      }
    } finally {
      setSubmitting(false);
    }
  }

  return (
    <>
      <Button 
        variant="default" 

        onClick={() => setOpen(true)} 
        aria-label="Add Travel Record" 
        title="Add Travel Record"
      >
        <Plus className="mr-xs h-4 w-4" /> Add Travel Record
      </Button>
      <AppDrawer
        open={open}
        onClose={() => setOpen(false)}
        record={null}
        fields={[]}
        mode="create"
        title="Add Travel Record"
        tabs={[{
          key: 'content',
          label: 'Details',
          content: (
        <>
        {error ? <div role="alert" className="mb-sm text-body-sm color-destructive">{error}</div> : null}
        <form 
