              onChange={(e: React.ChangeEvent<HTMLSelectElement>) => form.setValue('item_type', e.target.value as unknown, { shouldDirty: true })}
            >
              <option value="shirt">Shirt</option>
              <option value="pants">Pants</option>
              <option value="jacket">Jacket</option>
              <option value="hat">Hat</option>
              <option value="shoes">Shoes</option>
              <option value="safety_gear">Safety Gear</option>
              <option value="accessories">Accessories</option>
              <option value="equipment">Equipment</option>
            </select>
          </div>

          <div className="grid gap-xs">
            <label htmlFor="item_name" className="text-body-sm form-label">Item Name *</label>
            <input 
