  mode: 'create' | 'edit' | 'view';
  sizing?: UniformSizing | null;
  onSave: (data: UniformSizingFormData) => Promise<void>;
}

export default function CreateEditUniformSizingDrawer({
  isOpen,
  onClose,
  mode,
  sizing,
  onSave,
}: CreateEditUniformSizingDrawerProps) {
  const {
    register,
    handleSubmit,
    watch,
    setValue,
    reset,
    formState: { errors, isSubmitting }
  } = useForm<UniformSizingFormData>({
    resolver: zodResolver(uniformSizingSchema),
    defaultValues: {
      equipment_preferences: {},
    },
  });

  const watchedHeight = watch('height');
  const watchedWeight = watch('weight');

  // Calculate BMI when height and weight change
  const bmi = watchedHeight && watchedWeight ? calculateBMI(watchedHeight, watchedWeight) : null;
  const bmiCategory = bmi ? getBMICategory(watchedHeight!, watchedWeight!) : null;

  // Reset form when sizing changes
  useEffect(() => {
    if (sizing) {
      reset({
        user_id: sizing.user_id,
        first_name: sizing.first_name || '',
        last_name: sizing.last_name || '',
        size_category: sizing.size_category,
        height: sizing.height || undefined,
        weight: sizing.weight || undefined,
        chest: sizing.chest || undefined,
        waist: sizing.waist || undefined,
        neck: sizing.neck || undefined,
        sleeve_length: sizing.sleeve_length || undefined,
        inseam: sizing.inseam || undefined,
        shirt_size: sizing.shirt_size || '',
        pants_size: sizing.pants_size || '',
        shoe_size: sizing.shoe_size || '',
        hat_size: sizing.hat_size || '',
        glove_size: sizing.glove_size || '',
        equipment_preferences: sizing.equipment_preferences || {},
        notes: sizing.notes || '',
      });
    } else {
      reset({
        user_id: '',
        first_name: '',
        last_name: '',
        equipment_preferences: {},
      });
    }
  }, [sizing, reset]);

  const onSubmit = async (data: UniformSizingFormData) => {
    try {
      await onSave(data);
      onClose();
    } catch (error) {
      // Error handling is done in the parent component
      console.error('Error saving uniform sizing:', error);
    }
  };

  const title = mode === 'create' ? 'Add Uniform Sizing' : 
                mode === 'edit' ? 'Edit Uniform Sizing' : 
                'View Uniform Sizing';

  const tabs = [
    {
      key: 'basic',
      label: 'Basic Info',
      icon: User,
      content: (
        <div className="space-y-lg">
          {/* Personal Information */}
          <div>
            <h3 className="text-lg font-semibold mb-md flex items-center">
              <User className="h-5 w-5 mr-sm" />
              Personal Information
            </h3>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
              <div>
                <label className="block text-sm font-medium mb-xs">First Name</label>
                <Input
                  {...register('first_name')}
                  placeholder="Enter first name"
                  disabled={mode === 'view'}
                  error={errors.first_name?.message}
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-xs">Last Name</label>
                <Input
                  {...register('last_name')}
                  placeholder="Enter last name"
                  disabled={mode === 'view'}
                  error={errors.last_name?.message}
                />
              </div>
            </div>

            <div className="mt-md">
              <label className="block text-sm font-medium mb-xs">Size Category</label>
              <Select
                value={watch('size_category') || ''}
                onValueChange={(value) => setValue('size_category', value as unknown)}
                disabled={mode === 'view'}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Select size category" />
                </SelectTrigger>
                <SelectContent>
                  {SIZE_CATEGORIES.map((category) => (
                    <SelectItem key={category} value={category}>
                      {category.toUpperCase()}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
        </div>
      ),
    },
    {
      key: 'measurements',
      label: 'Measurements',
      icon: Ruler,
      content: (
        <div className="space-y-lg">
          {/* Body Measurements */}
          <div>
            <h3 className="text-lg font-semibold mb-md flex items-center">
              <Ruler className="h-5 w-5 mr-sm" />
              Body Measurements
            </h3>

            {/* BMI Display */}
            {bmi && (
              <Card className="p-md mb-md bg-muted/50">
                <div className="flex items-center justify-between">
                  <div>
                    <span className="text-sm font-medium">BMI: {bmi.toFixed(1)}</span>
                    {bmiCategory && (
                      <Badge variant="outline" className="ml-sm">
                        {bmiCategory}
                      </Badge>
                    )}
                  </div>
                  <span className="text-xs text-muted-foreground">
                    Calculated from height and weight
                  </span>
                </div>
              </Card>
            )}

            <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
              <div>
                <label className="block text-sm font-medium mb-xs">Height (inches)</label>
                <Input
                  type="number"
                  {...register('height', { valueAsNumber: true })}
                  placeholder="e.g., 70"
                  disabled={mode === 'view'}
                  error={errors.height?.message}
                />
                {watchedHeight && (
                  <p className="text-xs text-muted-foreground mt-xs">
                    {Math.floor(watchedHeight / 12)}' {watchedHeight % 12}"
                  </p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium mb-xs">Weight (lbs)</label>
                <Input
                  type="number"
                  {...register('weight', { valueAsNumber: true })}
                  placeholder="e.g., 180"
                  disabled={mode === 'view'}
                  error={errors.weight?.message}
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-xs">Chest (inches)</label>
                <Input
                  type="number"
                  {...register('chest', { valueAsNumber: true })}
                  placeholder="e.g., 42"
                  disabled={mode === 'view'}
                  error={errors.chest?.message}
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-xs">Waist (inches)</label>
                <Input
                  type="number"
                  {...register('waist', { valueAsNumber: true })}
                  placeholder="e.g., 34"
                  disabled={mode === 'view'}
                  error={errors.waist?.message}
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-xs">Neck (inches)</label>
                <Input
                  type="number"
                  {...register('neck', { valueAsNumber: true })}
                  placeholder="e.g., 16"
                  disabled={mode === 'view'}
                  error={errors.neck?.message}
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-xs">Sleeve Length (inches)</label>
                <Input
                  type="number"
                  {...register('sleeve_length', { valueAsNumber: true })}
                  placeholder="e.g., 34"
                  disabled={mode === 'view'}
                  error={errors.sleeve_length?.message}
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-xs">Inseam (inches)</label>
                <Input
                  type="number"
                  {...register('inseam', { valueAsNumber: true })}
                  placeholder="e.g., 32"
                  disabled={mode === 'view'}
                  error={errors.inseam?.message}
                />
              </div>
            </div>
          </div>
        </div>
      ),
    },
    {
      key: 'clothing',
      label: 'Clothing Sizes',
      icon: Shirt,
      content: (
        <div className="space-y-lg">
          {/* Clothing Sizes */}
          <div>
            <h3 className="text-lg font-semibold mb-md flex items-center">
              <Shirt className="h-5 w-5 mr-sm" />
              Clothing Sizes
            </h3>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
              <div>
                <label className="block text-sm font-medium mb-xs">Shirt Size</label>
                <Input
                  {...register('shirt_size')}
                  placeholder="e.g., L, XL, 16.5"
                  disabled={mode === 'view'}
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-xs">Pants Size</label>
                <Input
                  {...register('pants_size')}
                  placeholder="e.g., 34x32, L"
                  disabled={mode === 'view'}
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-xs">Shoe Size</label>
                <Input
                  {...register('shoe_size')}
                  placeholder="e.g., 10.5, 11"
                  disabled={mode === 'view'}
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-xs">Hat Size</label>
                <Input
                  {...register('hat_size')}
                  placeholder="e.g., 7 1/4, L"
                  disabled={mode === 'view'}
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-xs">Glove Size</label>
                <Input
                  {...register('glove_size')}
                  placeholder="e.g., M, L, 9"
                  disabled={mode === 'view'}
                />
              </div>
            </div>
          </div>

          {/* Equipment Preferences */}
          <div>
            <h4 className="font-medium mb-sm">Equipment Preferences</h4>
            <div className="space-y-sm">
              {EQUIPMENT_TYPES.map((equipment) => (
                <div key={equipment} className="flex items-center space-x-sm">
                  <label className="text-sm font-medium w-32 capitalize">
                    {equipment.replace('_', ' ')}:
                  </label>
                  <Input
                    placeholder={`Preferred ${equipment.replace('_', ' ')}`}
                    disabled={mode === 'view'}
                    onChange={(e) => {
                      const current = watch('equipment_preferences') || {};
                      setValue('equipment_preferences', {
                        ...current,
                        [equipment]: e.target.value,
                      });
                    }}
                    value={watch('equipment_preferences')?.[equipment] || ''}
                  />
                </div>
              ))}
            </div>
          </div>

          {/* Notes */}
          <div>
            <label className="block text-sm font-medium mb-xs">Notes</label>
            <Textarea
              {...register('notes')}
              placeholder="Additional notes about sizing preferences, fit issues, etc."
              rows={3}
              disabled={mode === 'view'}
            />
          </div>
        </div>
      ),
    },
  ];

  return (
    <UniversalDrawer
      isOpen={isOpen}
      onClose={onClose}
      title={title}
      tabs={tabs}
      actions={
        mode !== 'view' ? (
          <div className="flex items-center space-x-sm">
            <Button variant="outline" onClick={onClose} disabled={isSubmitting}>
              Cancel
            </Button>
            <Button onClick={handleSubmit(onSubmit)} disabled={isSubmitting}>
              <Save className="h-4 w-4 mr-sm" />
              {isSubmitting ? 'Saving...' : mode === 'create' ? 'Create' : 'Update'}
            </Button>
          </div>
        ) : undefined
      }
    />
  );
}

'use client';

import { Calendar, User } from "lucide-react";
import { Card, Badge, Avatar } from '@ghxstship/ui';
import type { UserProfile, FieldConfig } from '../types';

interface ProfileCalendarViewProps {
  profiles: UserProfile[];
  loading: boolean;
  selectedIds: string[];
  onSelectionChange: (ids: string[]) => void;
  onEdit: (profile: UserProfile) => void;
  fieldConfig: FieldConfig[];
}

export default function ProfileCalendarView({
  profiles,
  loading,
  selectedIds,
  onSelectionChange,
  onEdit,
  fieldConfig
}: ProfileCalendarViewProps) {
  if (loading) {
    return (
      <Card className="p-xl text-center">
        <div className="animate-pulse space-y-md">
          <div className="w-12 h-12 bg-muted rounded-full mx-auto" />
          <div className="w-48 h-4 bg-muted rounded mx-auto" />
          <div className="w-32 h-3 bg-muted rounded mx-auto" />
        </div>
      </Card>
    );
  }

  // Group profiles by hire date for calendar view
  const profilesByDate = profiles.reduce((acc, profile) => {
    if (profile.hire_date) {
      const date = new Date(profile.hire_date).toDateString();
      if (!acc[date]) {
        acc[date] = [];
      }
      acc[date].push(profile);
    }
    return acc;
  }, {} as Record<string, UserProfile[]>);

  const sortedDates = Object.keys(profilesByDate).sort((a, b) => 
    new Date(b).getTime() - new Date(a).getTime()
  );

  return (
    <div className="space-y-lg">
      <div className="flex items-center gap-sm mb-lg">
        <Calendar className="h-5 w-5" />
        <h2 className="text-lg font-semibold">Profiles by Hire Date</h2>
      </div>

      {sortedDates.length === 0 ? (
        <Card className="p-xl text-center">
          <Calendar className="h-12 w-12 text-muted-foreground mx-auto mb-md" />
          <h3 className="text-lg font-semibold mb-sm">No hire dates available</h3>
          <p className="text-muted-foreground">
            Profiles with hire dates will appear here in a timeline view.
          </p>
        </Card>
      ) : (
        <div className="space-y-md">
          {sortedDates.map((dateString) => {
            const date = new Date(dateString);
            const dateProfiles = profilesByDate[dateString];

            return (
              <Card key={dateString} className="p-md">
                <div className="flex items-center gap-md mb-md">
                  <div className="flex-shrink-0">
                    <div className="w-12 h-12 bg-primary/10 rounded-full flex items-center justify-center">
                      <Calendar className="h-6 w-6 text-primary" />
                    </div>
                  </div>
                  <div>
                    <h3 className="font-semibold">
                      {date.toLocaleDateString('en-US', { 
                        weekday: 'long',
                        year: 'numeric',
                        month: 'long',
                        day: 'numeric'
                      })}
                    </h3>
                    <p className="text-sm text-muted-foreground">
                      {dateProfiles.length} profile{dateProfiles.length !== 1 ? 's' : ''} hired
                    </p>
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-md">
                  {dateProfiles.map((profile) => (
                    <div
                      key={profile.id}
                      className="flex items-center gap-sm p-sm rounded-lg border cursor-pointer hover:bg-muted/50 transition-colors"
                      onClick={() => onEdit(profile)}
                    >
                      <Avatar className="h-8 w-8">
                        {profile.avatar_url ? (
                          <img src={profile.avatar_url} alt={profile.full_name} />
                        ) : (
                          <div className="flex items-center justify-center h-full w-full bg-primary/10 text-primary font-semibold text-xs">
                            {profile.full_name.split(' ').map(n => n[0]).join('').toUpperCase()}
                          </div>
                        )}
                      </Avatar>
                      <div className="flex-1 min-w-0">
                        <p className="font-medium text-sm truncate">{profile.full_name}</p>
                        <p className="text-xs text-muted-foreground truncate">
                          {profile.position} â€¢ {profile.department}
                        </p>
                      </div>
                      <Badge 
                        variant={profile.status === 'active' ? 'success' : 'secondary'}
                        size="sm"
                      >
                        {profile.status}
                      </Badge>
                    </div>
                  ))}
                </div>
              </Card>
            );
          })}
        </div>
      )}
    </div>
  );
}

'use client';

import { User, Mail, Phone, MapPin, Calendar, MoreHorizontal, Edit, Eye, Trash2, UserCheck, Building } from "lucide-react";
import { useState } from 'react';
import {
  Card,
  Badge,
  Button,
  Avatar,
  Checkbox,
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@ghxstship/ui';

import type { UserProfile, FieldConfig } from '../types';

interface ProfileGridViewProps {
  profiles: UserProfile[];
  loading: boolean;
  selectedIds: string[];
  onSelectionChange: (ids: string[]) => void;
  onEdit: (profile: UserProfile) => void;
  fieldConfig: FieldConfig[];
}

export default function ProfileGridView({
  profiles,
  loading,
  selectedIds,
  onSelectionChange,
  onEdit,
  fieldConfig
}: ProfileGridViewProps) {
  const [hoveredId, setHoveredId] = useState<string | null>(null);

  const handleSelectProfile = (profileId: string, checked: boolean) => {
    if (checked) {
      onSelectionChange([...selectedIds, profileId]);
    } else {
      onSelectionChange(selectedIds.filter(id => id !== profileId));
    }
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      onSelectionChange(profiles.map(p => p.id));
    } else {
      onSelectionChange([]);
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active':
        return 'success';
      case 'inactive':
        return 'destructive';
      case 'pending':
        return 'warning';
      case 'suspended':
        return 'destructive';
      default:
        return 'secondary';
    }
  };

  const getCompletionColor = (percentage: number) => {
    if (percentage >= 90) return 'text-green-600';
    if (percentage >= 70) return 'text-yellow-600';
    return 'text-red-600';
  };

  if (loading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
        {Array.from({ length: 8 }).map((_, i) => (
          <Card key={i} className="p-lg animate-pulse">
            <div className="flex items-start gap-md">
              <div className="w-12 h-12 bg-muted rounded-full" />
              <div className="flex-1 space-y-sm">
                <div className="h-4 bg-muted rounded w-3/4" />
                <div className="h-3 bg-muted rounded w-1/2" />
                <div className="h-3 bg-muted rounded w-2/3" />
              </div>
            </div>
          </Card>
        ))}
      </div>
    );
  }

  if (profiles.length === 0) {
    return (
      <Card className="p-xl text-center">
        <User className="h-12 w-12 text-muted-foreground mx-auto mb-md" />
        <h3 className="text-lg font-semibold mb-sm">No profiles found</h3>
        <p className="text-muted-foreground mb-lg">
          Get started by creating your first profile or adjust your filters.
        </p>
        <Button>Create Profile</Button>
      </Card>
    );
  }

  return (
    <div className="space-y-md">
      {/* Header with bulk selection */}
      <div className="flex items-center gap-sm">
        <Checkbox
          checked={selectedIds.length === profiles.length}
          onCheckedChange={handleSelectAll}
          indeterminate={selectedIds.length > 0 && selectedIds.length < profiles.length}
        />
        <span className="text-sm text-muted-foreground">
          {selectedIds.length > 0 ? `${selectedIds.length} selected` : 'Select all'}
        </span>
      </div>

      {/* Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
        {profiles.map((profile) => (
          <Card
            key={profile.id}
            className={`p-lg transition-all duration-200 hover:shadow-md cursor-pointer ${
              selectedIds.includes(profile.id) ? 'ring-2 ring-primary' : ''
            }`}
            onMouseEnter={() => setHoveredId(profile.id)}
            onMouseLeave={() => setHoveredId(null)}
          >
            {/* Header with selection and actions */}
            <div className="flex items-start justify-between mb-md">
              <Checkbox
                checked={selectedIds.includes(profile.id)}
                onCheckedChange={(checked) => handleSelectProfile(profile.id, checked as boolean)}
                onClick={(e) => e.stopPropagation()}
              />

              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <Button
                    variant="ghost"
                    size="sm"
                    className={`h-8 w-8 p-0 transition-opacity ${
                      hoveredId === profile.id ? 'opacity-100' : 'opacity-0'
                    }`}
                    onClick={(e) => e.stopPropagation()}
                  >
                    <MoreHorizontal className="h-4 w-4" />
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent align="end">
                  <DropdownMenuItem onClick={() => onEdit(profile)}>
                    <Eye className="h-4 w-4 mr-sm" />
                    View Details
                  </DropdownMenuItem>
                  <DropdownMenuItem onClick={() => onEdit(profile)}>
                    <Edit className="h-4 w-4 mr-sm" />
                    Edit Profile
                  </DropdownMenuItem>
                  <DropdownMenuItem className="text-destructive">
                    <Trash2 className="h-4 w-4 mr-sm" />
                    Delete Profile
                  </DropdownMenuItem>
                </DropdownMenuContent>
              </DropdownMenu>
            </div>

            {/* Profile Avatar and Basic Info */}
            <div className="flex items-start gap-md mb-md">
              <Avatar className="h-12 w-12">
                {profile.avatar_url ? (
                  <img src={profile.avatar_url} alt={profile.full_name} />
                ) : (
                  <div className="flex items-center justify-center h-full w-full bg-primary/10 text-primary font-semibold">
                    {profile.full_name.split(' ').map(n => n[0]).join('').toUpperCase()}
                  </div>
                )}
              </Avatar>

              <div className="flex-1 min-w-0">
                <h3 className="font-semibold text-sm truncate" title={profile.full_name}>
                  {profile.full_name}
                </h3>
                <p className="text-xs text-muted-foreground truncate" title={profile.position}>
                  {profile.position || 'No position set'}
                </p>
                <Badge 
                  variant={getStatusColor(profile.status) as unknown} 
                  size="sm" 
                  className="mt-xs"
                >
                  {profile.status}
                </Badge>
              </div>
            </div>

            {/* Contact Information */}
            <div className="space-y-xs mb-md">
              <div className="flex items-center gap-xs text-xs text-muted-foreground">
                <Mail className="h-3 w-3 flex-shrink-0" />
                <span className="truncate" title={profile.email}>
                  {profile.email}
                </span>
              </div>

              {profile.phone && (
                <div className="flex items-center gap-xs text-xs text-muted-foreground">
                  <Phone className="h-3 w-3 flex-shrink-0" />
                  <span>{profile.phone}</span>
                </div>
              )}

              {profile.department && (
                <div className="flex items-center gap-xs text-xs text-muted-foreground">
                  <Building className="h-3 w-3 flex-shrink-0" />
                  <span className="truncate">{profile.department}</span>
                </div>
              )}
            </div>

            {/* Profile Completion */}
            <div className="space-y-xs mb-md">
              <div className="flex items-center justify-between text-xs">
                <span className="text-muted-foreground">Profile Completion</span>
                <span className={`font-medium ${getCompletionColor(profile.completion_percentage)}`}>
                  {profile.completion_percentage}%
                </span>
              </div>
              <div className="w-full bg-muted rounded-full h-1.5">
                <div 
                  className="bg-primary h-1.5 rounded-full transition-all duration-300" 
                  style={{ width: `${profile.completion_percentage}%` }}
                />
              </div>
            </div>

            {/* Additional Stats */}
            <div className="grid grid-cols-2 gap-md text-center">
              <div>
                <p className="text-xs font-medium">{profile.certifications_count || 0}</p>
                <p className="text-xs text-muted-foreground">Certifications</p>
              </div>
              <div>
                <p className="text-xs font-medium">{profile.endorsements_count || 0}</p>
                <p className="text-xs text-muted-foreground">Endorsements</p>
              </div>
            </div>

            {/* Last Activity */}
            {profile.last_login && (
              <div className="mt-md pt-md border-t border-border">
                <div className="flex items-center gap-xs text-xs text-muted-foreground">
                  <Calendar className="h-3 w-3" />
                  <span>
                    Last login: {new Date(profile.last_login).toLocaleDateString()}
                  </span>
                </div>
              </div>
            )}
          </Card>
        ))}
      </div>

      {/* Load More / Pagination */}
      {profiles.length > 0 && (
        <div className="text-center pt-lg">
          <Button variant="outline">
            Load More Profiles
          </Button>
        </div>
      )}
    </div>
  );
}

'use client';

import { User } from "lucide-react";
import { Card, Badge, Avatar } from '@ghxstship/ui';
import type { UserProfile, FieldConfig } from '../types';

interface ProfileKanbanViewProps {
  profiles: UserProfile[];
  loading: boolean;
  selectedIds: string[];
  onSelectionChange: (ids: string[]) => void;
