'use client';


import { Plus, Calendar, Clock, MapPin, Users } from "lucide-react";
import { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
// import { z } from 'zod';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { usePostHog } from 'posthog-js/react';
import AppDrawer from '@/app/_components/shared/AppDrawer';
// Form validation schema
const createEventSchema = z.object({
  name: z.string().min(1, 'Event name is required'),
  description: z.string().optional(),
  kind: z.enum(['performance', 'activation', 'workshop', 'meeting', 'rehearsal', 'setup', 'breakdown']),
  project_id: z.string().uuid('Please select a project'),
  start_at: z.string().min(1, 'Start time is required'),
  end_at: z.string().min(1, 'End time is required'),
  location: z.string().optional(),
  capacity: z.number().min(0).optional(),
  status: z.enum(['draft', 'scheduled', 'in_progress', 'completed', 'cancelled']).default('draft')
});

type CreateEventForm = z.infer<typeof createEventSchema>;

interface Project {
  id: string;
  name: string;
  status: string;
}

export default function CreateCalendarClient({ orgId }: { orgId: string }) {
  const t = useTranslations('programming');
  const sb = createBrowserClient();
  const posthog = usePostHog();

  const [isOpen, setIsOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [projects, setProjects] = useState<Project[]>([]);
  const [loadingProjects, setLoadingProjects] = useState(false);

  const form = useForm<CreateEventForm>({
    resolver: zodResolver(createEventSchema),
    defaultValues: {
      name: '',
      description: '',
      kind: 'performance',
      project_id: '',
      start_at: '',
      end_at: '',
      location: '',
      capacity: undefined,
      status: 'draft'
    }
  });

  // Load projects when drawer opens
  useEffect(() => {
    if (isOpen && projects.length === 0) {
      loadProjects();
    }
  }, [isOpen]);

  const loadProjects = async () => {
    setLoadingProjects(true);
    try {
      const { data, error } = await sb
        .from('projects')
        .select('id, name, status')
        .eq('organization_id', orgId)
        .eq('status', 'active')
        .order('name');

      if (error) throw error;
      setProjects(data || []);
    } catch (error) {
      console.error('Error loading projects:', error);
    } finally {
      setLoadingProjects(false);
    }
  };

  const onSubmit = async (data: CreateEventForm) => {
    setIsLoading(true);
    try {
      const { error } = await sb
        .from('events')
        .insert({
          ...data,
          organization_id: orgId,
          capacity: data.capacity || null
        });

      if (error) throw error;

      // Track event creation
      posthog?.capture('programming_event_created', {
        event_kind: data.kind,
        project_id: data.project_id,
        organization_id: orgId
      });

      // Reset form and close drawer
      form.reset();
      setIsOpen(false);

      // Refresh the page to show new event
      window.location.reload();
    } catch (error) {
      console.error('Error creating event:', error);
      // Error handling implemented
      alert('An error occurred while creating the event. Please try again.');
    } finally {
      setIsLoading(false);
    }
