              {projects.map(project => (
                <option key={project.id} value={project.id}>
                  {project.name}
                </option>
              ))}
            </Select>

            <div className="grid grid-cols-2 gap-md">
              <UnifiedInput                 label="Start Time"
                type="datetime-local"
