  );
}

"use client";

import { CalendarDays, CalendarRange, Calendar as CalendarIcon, Eye, Filter, LayoutGrid, List, MapPin, Pencil, Plus, RefreshCcw, Share2, Trash2, Users } from "lucide-react";
import { useCallback, useEffect, useMemo, useState } from "react";
import { useRouter } from "next/navigation";
import { createBrowserClient } from "@ghxstship/auth";
import {
  Badge,
  Button,
  Card,
  Checkbox,
  Input,
  Select,
  toast,
} from "@ghxstship/ui";
import { addMonths, formatISO } from "date-fns";
import CreateProgrammingEventDrawer from "./drawers/CreateProgrammingEventDrawer";
import EditProgrammingEventDrawer from "./drawers/EditProgrammingEventDrawer";
import ViewProgrammingEventDrawer from "./drawers/ViewProgrammingEventDrawer";
import ProgrammingCalendarBoardView from "./views/ProgrammingCalendarBoardView";
import ProgrammingCalendarListView from "./views/ProgrammingCalendarListView";
import ProgrammingCalendarGridView from "./views/ProgrammingCalendarGridView";
import ProgrammingCalendarTimelineView from "./views/ProgrammingCalendarTimelineView";
import type { ProgrammingEvent, ProgrammingEventProject } from "./types";

const VIEW_TYPES = [
  { id: "board", label: "Board", icon: LayoutGrid },
  { id: "list", label: "List", icon: List },
  { id: "calendar", label: "Calendar", icon: CalendarRange },
  { id: "timeline", label: "Timeline", icon: CalendarDays },
] as const;

type ViewType = (typeof VIEW_TYPES)[number]["id"];

type ProgrammingCalendarClientProps = {
  orgId: string;
  currentUserId: string;
  initialEvents: ProgrammingEvent[];
  projects: ProgrammingEventProject[];
  users: { id: string; email: string; full_name?: string | null; avatar_url?: string | null }[];
};

const STATUS_BADGE: Record<ProgrammingEvent["status"], { label: string; variant: "secondary" | "outline" | "warning" | "info" | "success" | "destructive" }> = {
  draft: { label: "Draft", variant: "secondary" },
  scheduled: { label: "Scheduled", variant: "info" },
  in_progress: { label: "In Progress", variant: "warning" },
  completed: { label: "Completed", variant: "success" },
  cancelled: { label: "Cancelled", variant: "destructive" },
};

const EVENT_TYPE_LABEL: Record<ProgrammingEvent["event_type"], string> = {
  performance: "Performance",
  activation: "Activation",
  workshop: "Workshop",
  meeting: "Meeting",
  rehearsal: "Rehearsal",
  setup: "Setup",
  breakdown: "Breakdown",
  other: "Other",
};

export default function ProgrammingCalendarClient({
  orgId,
  currentUserId,
  initialEvents,
  projects,
  users,
}: ProgrammingCalendarClientProps) {
  const supabase = useMemo(() => createBrowserClient(), []);
  const router = useRouter();

  const [events, setEvents] = useState<ProgrammingEvent[]>(initialEvents);
  const [loading, setLoading] = useState(false);
  const [viewType, setViewType] = useState<ViewType>("calendar");
  const [selectedEvents, setSelectedEvents] = useState<Set<string>>(new Set());
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedProject, setSelectedProject] = useState<string>("all");
  const [selectedStatus, setSelectedStatus] = useState<string>("all");
  const [selectedType, setSelectedType] = useState<string>("all");
  const [dateRangeStart, setDateRangeStart] = useState<string>(() => formatISO(new Date(), { representation: "date" }));
  const [dateRangeEnd, setDateRangeEnd] = useState<string>(() => formatISO(addMonths(new Date(), 1), { representation: "date" }));

  const [createOpen, setCreateOpen] = useState(false);
  const [editOpen, setEditOpen] = useState(false);
  const [viewOpen, setViewOpen] = useState(false);
  const [activeEvent, setActiveEvent] = useState<ProgrammingEvent | null>(null);

  const filteredEvents = useMemo(() => {
    return events.filter((event) => {
      if (searchTerm) {
        const query = searchTerm.toLowerCase();
        const matchesSearch =
          event.title.toLowerCase().includes(query) ||
          (event.description?.toLowerCase().includes(query) ?? false) ||
          event.tags.some((tag) => tag.toLowerCase().includes(query));
        if (!matchesSearch) return false;
      }

      if (selectedProject !== "all" && event.project_id !== selectedProject) return false;
      if (selectedStatus !== "all" && event.status !== selectedStatus) return false;
      if (selectedType !== "all" && event.event_type !== selectedType) return false;

      if (dateRangeStart) {
        if (new Date(event.start_at) < new Date(dateRangeStart)) return false;
      }

      if (dateRangeEnd) {
        if (new Date(event.start_at) > new Date(dateRangeEnd)) return false;
      }

      return true;
    });
  }, [events, searchTerm, selectedProject, selectedStatus, selectedType, dateRangeStart, dateRangeEnd]);

  const loadEvents = useCallback(async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams({
        limit: "200",
        offset: "0",
        start_at: dateRangeStart ? new Date(dateRangeStart).toISOString() : "",
        end_at: dateRangeEnd ? new Date(dateRangeEnd).toISOString() : "",
      });
      if (selectedProject !== "all") params.set("project_id", selectedProject);
      if (selectedStatus !== "all") params.set("status", selectedStatus);
      if (selectedType !== "all") params.set("event_type", selectedType);
      if (searchTerm) params.set("search", searchTerm);

      const response = await fetch(`/api/v1/programming/events?${params.toString()}`);
      if (!response.ok) throw new Error("Failed to load events");
      const json = await response.json();
      setEvents(json.data ?? []);
    } catch (error) {
      console.error("Failed to load programming events", error);
      toast.error("Failed to load programming events");
    } finally {
      setLoading(false);
    }
  }, [dateRangeStart, dateRangeEnd, searchTerm, selectedProject, selectedStatus, selectedType]);

  useEffect(() => {
    loadEvents();
  }, [loadEvents]);

  useEffect(() => {
    const channel = supabase
      .channel(`programming-events-${orgId}`)
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'programming_events',
          filter: `organization_id=eq.${orgId}`,
        },
        () => {
          loadEvents();
        },
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [supabase, orgId, loadEvents]);

  const openCreateDrawer = () => {
    setActiveEvent(null);
    setCreateOpen(true);
  };

  const openEditDrawer = (event: ProgrammingEvent) => {
    setActiveEvent(event);
    setEditOpen(true);
  };

  const openViewDrawer = (event: ProgrammingEvent) => {
    setActiveEvent(event);
    setViewOpen(true);
  };

  const handleDelete = async (event: ProgrammingEvent) => {
    if (!confirm(`Delete event "${event.title}"?`)) return;
    try {
      const res = await fetch(`/api/v1/programming/events/${event.id}`, { method: 'DELETE' });
      if (!res.ok) throw new Error('Delete failed');
      toast.success('Event deleted');
      loadEvents();
    } catch (error) {
      console.error('Failed to delete event', error);
      toast.error('Failed to delete event');
    }
  };

  const selectedEventsList = useMemo(() => events.filter((event) => selectedEvents.has(event.id)), [events, selectedEvents]);

  const clearSelection = () => setSelectedEvents(new Set());

  const toggleSelection = (id: string) => {
    setSelectedEvents((prev) => {
      const next = new Set(prev);
      if (next.has(id)) {
        next.delete(id);
      } else {
        next.add(id);
      }
      return next;
    });
  };

  const bulkDelete = async () => {
    if (selectedEvents.size === 0) return;
    if (!confirm(`Delete ${selectedEvents.size} selected events?`)) return;

    try {
      await Promise.all(
        Array.from(selectedEvents).map((id) => fetch(`/api/v1/programming/events/${id}`, { method: 'DELETE' })),
      );
      toast.success('Selected events deleted');
      clearSelection();
      loadEvents();
    } catch (error) {
      console.error('Bulk delete failed', error);
      toast.error('Failed to delete selected events');
    }
  };

  const renderView = () => {
    switch (viewType) {
      case 'board':
        return (
          <ProgrammingCalendarBoardView
            events={filteredEvents}
            selected={selectedEvents}
            onSelect={toggleSelection}
            onView={openViewDrawer}
            onEdit={openEditDrawer}
            onDelete={handleDelete}
          />
        );
      case 'list':
        return (
          <ProgrammingCalendarListView
            events={filteredEvents}
            selected={selectedEvents}
            onSelect={toggleSelection}
            onView={openViewDrawer}
            onEdit={openEditDrawer}
            onDelete={handleDelete}
          />
        );
      case 'timeline':
        return <ProgrammingCalendarTimelineView events={filteredEvents} onView={openViewDrawer} onEdit={openEditDrawer} />;
      case 'calendar':
      default:
        return <ProgrammingCalendarGridView events={filteredEvents} onView={openViewDrawer} onEdit={openEditDrawer} />;
    }
  };

  return (
    <div className="space-y-lg">
      <div className="flex flex-col gap-md lg:flex-row lg:items-center lg:justify-between">
        <div className="flex flex-1 flex-wrap items-center gap-sm">
          <div className="relative w-full max-w-md">
            <Input
              placeholder="Search events..."
              value={searchTerm}
              onChange={(event) => setSearchTerm(event.target.value)}
              className="pl-9"
            />
            <Filter className="absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 text-muted-foreground" />
          </div>

          <Select value={selectedProject} onValueChange={setSelectedProject}>
            <option value="all">All Projects</option>
            {projects.map((project) => (
              <option key={project.id} value={project.id}>
                {project.name}
              </option>
            ))}
          </Select>

          <Select value={selectedStatus} onValueChange={setSelectedStatus}>
            <option value="all">All Statuses</option>
            {Object.entries(STATUS_BADGE).map(([key, value]) => (
              <option key={key} value={key}>
                {value.label}
              </option>
            ))}
          </Select>

          <Select value={selectedType} onValueChange={setSelectedType}>
            <option value="all">All Types</option>
            {Object.entries(EVENT_TYPE_LABEL).map(([key, label]) => (
              <option key={key} value={key}>
                {label}
              </option>
            ))}
          </Select>

          <Input
            type="date"
            value={dateRangeStart}
            onChange={(event) => setDateRangeStart(event.target.value)}
            className="w-auto"
          />
          <Input
            type="date"
            value={dateRangeEnd}
            onChange={(event) => setDateRangeEnd(event.target.value)}
            className="w-auto"
          />

          <Button variant="outline" size="sm" onClick={loadEvents} disabled={loading}>
            <RefreshCcw className="mr-2 h-4 w-4" />
            Refresh
          </Button>
        </div>

        <div className="flex items-center gap-sm">
          {selectedEvents.size > 0 ? (
            <>
              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <Button variant="outline" size="sm">
                    Bulk actions ({selectedEvents.size})
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent>
                  <DropdownMenuItem onClick={bulkDelete}>
                    <Trash2 className="mr-2 h-4 w-4" />
                    Delete selected
                  </DropdownMenuItem>
                  <DropdownMenuItem onClick={clearSelection}>
                    Clear selection
                  </DropdownMenuItem>
                </DropdownMenuContent>
              </DropdownMenu>
            </>
          ) : null}

          <div className="flex rounded-md shadow-sm">
            {VIEW_TYPES.map((option) => {
              const Icon = option.icon;
              return (
                <Button
                  key={option.id}
                  variant={viewType === option.id ? "default" : "outline"}
                  size="sm"
                  onClick={() => setViewType(option.id)}
                  className="rounded-none first:rounded-l-md last:rounded-r-md"
                >
                  <Icon className="h-4 w-4" />
                  <span className="sr-only">{option.label}</span>
                </Button>
              );
            })}
          </div>

          <Button onClick={openCreateDrawer}>
            <Plus className="mr-2 h-4 w-4" />
            Create Event
          </Button>
        </div>
      </div>

      <Card className="p-lg">
        {loading ? (
          <div className="flex min-h-[320px] items-center justify-center text-muted-foreground">
            Loading programming events...
          </div>
        ) : filteredEvents.length === 0 ? (
          <div className="flex min-h-[320px] flex-col items-center justify-center gap-md text-center">
            <CalendarIcon className="h-12 w-12 text-muted-foreground" />
            <div className="space-y-xs">
              <h3 className="text-heading-5">No events found</h3>
              <p className="text-body-sm text-muted-foreground">
                {searchTerm || selectedStatus !== "all" || selectedType !== "all"
                  ? "Try adjusting your filters or search"
                  : "Create your first programming event to get started"}
              </p>
            </div>
            <Button onClick={openCreateDrawer}>
              <Plus className="mr-2 h-4 w-4" />
              Create Event
            </Button>
          </div>
        ) : (
          renderView()
        )}
      </Card>

      <CreateProgrammingEventDrawer
        open={createOpen}
        onOpenChange={setCreateOpen}
        orgId={orgId}
        currentUserId={currentUserId}
        projects={projects}
        onSuccess={() => {
          loadEvents();
          router.refresh();
        }}
      />

      {activeEvent ? (
        <>
          <EditProgrammingEventDrawer
            open={editOpen}
            onOpenChange={setEditOpen}
            event={activeEvent}
            orgId={orgId}
            currentUserId={currentUserId}
            projects={projects}
            onSuccess={() => {
              loadEvents();
              router.refresh();
            }}
          />
          <ViewProgrammingEventDrawer
            open={viewOpen}
            onOpenChange={setViewOpen}
            event={activeEvent}
            onEdit={() => {
              setViewOpen(false);
              setEditOpen(true);
            }}
            onDelete={() => {
              setViewOpen(false);
              handleDelete(activeEvent);
            }}
            users={users}
          />
        </>
      ) : null}
    </div>
  );
}

export { STATUS_BADGE, EVENT_TYPE_LABEL };

"use client";

import { Calendar, Clock, Edit, MapPin, Users, Activity, Tag, Briefcase, Radio, Settings, FileText } from "lucide-react";
import { AppDrawer, Badge, Button, Card } from "@ghxstship/ui";
import { format, parseISO, differenceInHours } from "date-fns";
import type { ProgrammingEvent } from "../types";
import { EVENT_TYPE_LABEL, STATUS_BADGE } from "../ProgrammingCalendarClient";

type ViewProgrammingEventDrawerProps = {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  event: ProgrammingEvent;
  onEdit?: () => void;
  onDelete?: () => void;
  users: { id: string; email: string; full_name?: string | null; avatar_url?: string | null }[];
};

export default function ViewProgrammingEventDrawer({
  open,
  onOpenChange,
  event,
  onEdit,
  onDelete,
  users,
}: ViewProgrammingEventDrawerProps) {
  const statusConfig = STATUS_BADGE[event.status];

  // Calculate event duration
  const getDuration = () => {
    if (!event.end_at) return null;
    const hours = differenceInHours(parseISO(event.end_at), parseISO(event.start_at));
    if (hours < 1) return "< 1 hour";
    if (hours === 1) return "1 hour";
    return `${hours} hours`;
  };

  // Get user by ID
  const getUserById = (userId?: string) => {
    if (!userId) return null;
    return users.find(user => user.id === userId);
  };

  return (
    <AppDrawer
      open={open}
      onOpenChange={onOpenChange}
      title={event.title}
      description={event.project?.name || "Programming Event"}
      icon={<Calendar className="h-5 w-5" />}
      width="md"
    >
      <div className="space-y-6">
        {/* Header Actions */}
        <div className="flex justify-between items-start">
          <div className="flex items-center gap-2">
            <Badge variant={statusConfig.variant}>{statusConfig.label}</Badge>
            <Badge variant="outline">{EVENT_TYPE_LABEL[event.event_type]}</Badge>
          </div>
          {onEdit && (
            <Button
              variant="outline"
              size="sm"
              onClick={onEdit}
            >
              <Edit className="h-4 w-4 mr-2" />
              Edit
            </Button>
          )}
        </div>

        {/* Description */}
        {event.description && (
          <div>
            <h3 className="font-medium mb-2 flex items-center gap-2">
              <FileText className="h-4 w-4" />
              Description
            </h3>
            <p className="text-sm text-muted-foreground whitespace-pre-wrap">
              {event.description}
            </p>
          </div>
        )}

        {/* Schedule */}
        <div>
          <h3 className="font-medium mb-3 flex items-center gap-2">
            <Clock className="h-4 w-4" />
            Schedule
          </h3>
          <div className="space-y-3">
            <div className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
              <span className="text-sm">Start Time</span>
              <span className="text-sm font-medium">
                {format(parseISO(event.start_at), "MMMM d, yyyy 'at' h:mm a")}
              </span>
            </div>

            {event.end_at && (
              <div className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
                <span className="text-sm">End Time</span>
                <span className="text-sm font-medium">
                  {format(parseISO(event.end_at), "MMMM d, yyyy 'at' h:mm a")}
                </span>
              </div>
            )}

            {getDuration() && (
              <div className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
                <span className="text-sm">Duration</span>
                <span className="text-sm font-medium">{getDuration()}</span>
              </div>
            )}

            {event.setup_start && (
              <div className="flex items-center justify-between p-3 bg-info/10 rounded-lg">
                <span className="text-sm">Setup Start</span>
                <span className="text-sm font-medium">
                  {format(parseISO(event.setup_start), "MMMM d, yyyy 'at' h:mm a")}
                </span>
              </div>
            )}

            {event.teardown_end && (
              <div className="flex items-center justify-between p-3 bg-info/10 rounded-lg">
                <span className="text-sm">Teardown End</span>
                <span className="text-sm font-medium">
                  {format(parseISO(event.teardown_end), "MMMM d, yyyy 'at' h:mm a")}
                </span>
              </div>
            )}
          </div>
        </div>

        {/* Location & Capacity */}
        {(event.location || event.capacity) && (
          <div>
            <h3 className="font-medium mb-3 flex items-center gap-2">
              <MapPin className="h-4 w-4" />
              Venue Details
            </h3>
            <div className="space-y-3">
              {event.location && (
                <div className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
                  <span className="text-sm">Location</span>
                  <span className="text-sm font-medium">{event.location}</span>
                </div>
              )}
              {event.capacity && (
                <div className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
                  <span className="text-sm">Capacity</span>
                  <span className="text-sm font-medium">{event.capacity} people</span>
                </div>
              )}
            </div>
          </div>
        )}

        {/* Project Info */}
        {event.project && (
          <div>
            <h3 className="font-medium mb-2 flex items-center gap-2">
              <Briefcase className="h-4 w-4" />
              Project
            </h3>
            <Card className="p-3">
              <div className="flex items-center justify-between">
                <span className="font-medium">{event.project.name}</span>
                <Badge variant={
                  event.project.status === "active" ? "success" :
                  event.project.status === "planning" ? "warning" :
                  event.project.status === "on_hold" ? "secondary" :
                  "destructive"
                }>
                  {event.project.status}
                </Badge>
              </div>
            </Card>
          </div>
        )}

        {/* Broadcast */}
        {event.broadcast_url && (
          <div>
            <h3 className="font-medium mb-2 flex items-center gap-2">
              <Radio className="h-4 w-4" />
              Broadcast
            </h3>
            <Card className="p-3">
              <a
                href={event.broadcast_url}
                target="_blank"
                rel="noopener noreferrer"
                className="text-sm text-primary hover:underline break-all"
              >
                {event.broadcast_url}
              </a>
            </Card>
          </div>
        )}

        {/* Tags */}
        {event.tags && event.tags.length > 0 && (
          <div>
            <h3 className="font-medium mb-3 flex items-center gap-2">
              <Tag className="h-4 w-4" />
              Tags
            </h3>
            <div className="flex flex-wrap gap-2">
              {event.tags.map((tag) => (
                <Badge key={tag} variant="secondary">
                  {tag}
                </Badge>
              ))}
            </div>
          </div>
        )}

        {/* Resources */}
        {event.resources && event.resources.length > 0 && (
          <div>
            <h3 className="font-medium mb-3 flex items-center gap-2">
              <Settings className="h-4 w-4" />
              Resources
            </h3>
            <div className="space-y-2">
              {event.resources.map((resource, index) => (
                <Card key={index} className="p-3">
                  <div className="flex items-center justify-between">
                    <span className="text-sm font-medium">{resource.name}</span>
                    <Badge variant="outline">Qty: {resource.quantity}</Badge>
                  </div>
                </Card>
              ))}
            </div>
          </div>
        )}

        {/* Staffing */}
        {event.staffing && event.staffing.length > 0 && (
          <div>
            <h3 className="font-medium mb-3 flex items-center gap-2">
              <Users className="h-4 w-4" />
              Staffing
            </h3>
            <div className="space-y-2">
              {event.staffing.map((staff, index) => {
                const user = getUserById(staff.user_id);

                return (
                  <Card key={index} className="p-3">
                    <div className="flex items-center justify-between">
                      <div>
                        <div className="text-sm font-medium">{staff.role}</div>
                        {user && (
                          <div className="text-xs text-muted-foreground">
                            {user.full_name || user.email}
                          </div>
                        )}
                        {staff.notes && (
                          <div className="text-xs text-muted-foreground mt-1">
                            {staff.notes}
                          </div>
                        )}
                      </div>
                      {user && user.avatar_url && (
                        <img
                          src={user.avatar_url}
                          alt={user.full_name || user.email}
                          className="h-8 w-8 rounded-full"
                        />
                      )}
                    </div>
                  </Card>
                );
              })}
            </div>
          </div>
        )}

        {/* Activity */}
        <div>
          <h3 className="font-medium mb-3 flex items-center gap-2">
            <Activity className="h-4 w-4" />
            Activity
          </h3>
          <div className="space-y-2 text-sm text-muted-foreground">
            <div className="flex items-center justify-between">
              <span>Created</span>
              <span>{format(parseISO(event.created_at || event.start_at), "MMM d, yyyy 'at' h:mm a")}</span>
            </div>
            <div className="flex items-center justify-between">
              <span>Last Updated</span>
              <span>{format(parseISO(event.updated_at || event.start_at), "MMM d, yyyy 'at' h:mm a")}</span>
            </div>
            <div className="flex items-center justify-between">
              <span>Timezone</span>
              <span>{event.timezone || "UTC"}</span>
            </div>
            {event.is_all_day && (
              <div className="flex items-center justify-between">
                <span>All Day Event</span>
                <Badge variant="secondary">Yes</Badge>
              </div>
            )}
          </div>
        </div>

        {/* Close Button */}
        <div className="pt-6 border-t">
          <Button
            variant="outline"
            className="w-full"
            onClick={() => onOpenChange(false)}
          >
            Close
          </Button>
        </div>
      </div>
    </AppDrawer>
  );
}

"use client";

import { Badge, Button, Card } from "@ghxstship/ui";
