"use client";

import { Badge, Button, Card } from "@ghxstship/ui";
import type { ProgrammingEvent } from "../types";
import { STATUS_BADGE, EVENT_TYPE_LABEL } from "../ProgrammingEventsClient";

type ProgrammingEventsTimelineViewProps = {
  events: ProgrammingEvent[];
  onView: (event: ProgrammingEvent) => void;
  onEdit: (event: ProgrammingEvent) => void;
};

export default function ProgrammingEventsTimelineView({
  events,
  onView,
  onEdit,
}: ProgrammingEventsTimelineViewProps) {
  // Sort events by start date
  const sortedEvents = [...events].sort((a, b) => 
    new Date(a.start_at).getTime() - new Date(b.start_at).getTime()
  );

  // Group events by date
  const eventsByDate = sortedEvents.reduce((acc, event) => {
    const eventDate = new Date(event.start_at).toDateString();
    if (!acc[eventDate]) acc[eventDate] = [];
    acc[eventDate].push(event);
    return acc;
  }, {} as Record<string, ProgrammingEvent[]>);

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    const today = new Date();
    const tomorrow = new Date(today);
    tomorrow.setDate(today.getDate() + 1);
    const yesterday = new Date(today);
    yesterday.setDate(today.getDate() - 1);

    if (date.toDateString() === today.toDateString()) {
      return "Today";
    } else if (date.toDateString() === tomorrow.toDateString()) {
      return "Tomorrow";
    } else if (date.toDateString() === yesterday.toDateString()) {
      return "Yesterday";
    } else {
      return date.toLocaleDateString('en-US', { 
        weekday: 'long', 
        year: 'numeric', 
        month: 'long', 
        day: 'numeric' 
      });
    }
  };

  return (
    <div className="space-y-lg">
      {Object.entries(eventsByDate).map(([dateString, dayEvents]) => (
        <div key={dateString} className="space-y-md">
          {/* Date Header */}
          <div className="flex items-center gap-md">
            <div className="h-px flex-1 bg-border" />
            <div className="flex items-center gap-sm px-md py-sm bg-muted rounded-full">
              <Calendar className="h-4 w-4" />
              <span className="font-medium">{formatDate(dateString)}</span>
              <Badge variant="outline" className="text-xs">
                {dayEvents.length} event{dayEvents.length !== 1 ? 's' : ''}
              </Badge>
            </div>
            <div className="h-px flex-1 bg-border" />
          </div>

          {/* Events for this date */}
          <div className="relative">
            {/* Timeline line */}
            <div className="absolute left-6 top-0 bottom-0 w-px bg-border" />

            <div className="space-y-md">
              {dayEvents.map((event, index) => {
                const statusConfig = STATUS_BADGE[event.status];
                const isLast = index === dayEvents.length - 1;

                return (
                  <div key={event.id} className="relative flex gap-md">
                    {/* Timeline dot */}
                    <div className="relative z-10 flex h-12 w-12 items-center justify-center">
                      <div className={`h-3 w-3 rounded-full border-2 border-background ${
                        statusConfig.variant === 'success' ? 'bg-success' :
                        statusConfig.variant === 'warning' ? 'bg-warning' :
                        statusConfig.variant === 'destructive' ? 'bg-destructive' :
                        statusConfig.variant === 'info' ? 'bg-info' :
                        'bg-muted'
                      }`} />
                    </div>

                    {/* Event Card */}
                    <Card className="flex-1 p-md">
                      <div className="flex items-start justify-between gap-md">
                        <div className="flex-1 space-y-sm">
                          {/* Event Header */}
                          <div className="flex items-start justify-between gap-md">
                            <div>
                              <h3 className="font-semibold">{event.title}</h3>
                              <div className="flex items-center gap-md text-sm text-muted-foreground">
                                <span className="flex items-center gap-1">
                                  <Clock className="h-3 w-3" />
                                  {new Date(event.start_at).toLocaleTimeString()}
                                  {event.end_at && ` - ${new Date(event.end_at).toLocaleTimeString()}`}
                                </span>
                                <Badge variant="secondary" className="text-xs">
                                  {EVENT_TYPE_LABEL[event.event_type]}
                                </Badge>
                                <Badge variant={statusConfig.variant} className="text-xs">
                                  {statusConfig.label}
                                </Badge>
                              </div>
                            </div>
                            <div className="flex items-center gap-1">
                              <Button size="icon" variant="ghost" onClick={() => onView(event)}>
                                <Eye className="h-4 w-4" />
                              </Button>
                              <Button size="icon" variant="ghost" onClick={() => onEdit(event)}>
                                <Pencil className="h-4 w-4" />
                              </Button>
                            </div>
                          </div>

                          {/* Event Details */}
                          {event.description && (
                            <p className="text-sm text-muted-foreground line-clamp-2">
                              {event.description}
                            </p>
                          )}

                          <div className="flex flex-wrap gap-md text-sm text-muted-foreground">
                            {event.location && (
                              <span className="flex items-center gap-1">
                                <MapPin className="h-3 w-3" />
                                {event.location}
                              </span>
                            )}
                            {event.capacity && (
                              <span className="flex items-center gap-1">
                                <Users className="h-3 w-3" />
                                {event.capacity} capacity
                              </span>
                            )}
                            {event.project && (
                              <span>Project: {event.project.name}</span>
                            )}
                          </div>

                          {/* Tags */}
                          {event.tags.length > 0 && (
                            <div className="flex flex-wrap gap-1">
                              {event.tags.map((tag) => (
                                <Badge key={tag} variant="outline" className="text-xs">
                                  {tag}
                                </Badge>
                              ))}
                            </div>
                          )}

                          {/* Resources & Staffing */}
                          {(event.resources.length > 0 || event.staffing.length > 0) && (
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-md text-xs">
                              {event.resources.length > 0 && (
                                <div>
                                  <div className="font-medium text-muted-foreground mb-1">Resources</div>
                                  <div className="space-y-1">
                                    {event.resources.slice(0, 3).map((resource, idx) => (
                                      <div key={idx} className="text-muted-foreground">
                                        {resource.name} ({resource.quantity})
                                      </div>
                                    ))}
                                    {event.resources.length > 3 && (
                                      <div className="text-muted-foreground">
                                        +{event.resources.length - 3} more
                                      </div>
                                    )}
                                  </div>
                                </div>
                              )}
                              {event.staffing.length > 0 && (
                                <div>
                                  <div className="font-medium text-muted-foreground mb-1">Staffing</div>
                                  <div className="space-y-1">
                                    {event.staffing.slice(0, 3).map((staff, idx) => (
                                      <div key={idx} className="text-muted-foreground">
                                        {staff.role}
                                      </div>
                                    ))}
                                    {event.staffing.length > 3 && (
                                      <div className="text-muted-foreground">
                                        +{event.staffing.length - 3} more
                                      </div>
                                    )}
                                  </div>
                                </div>
                              )}
                            </div>
                          )}
                        </div>
                      </div>
                    </Card>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      ))}

      {sortedEvents.length === 0 && (
        <div className="py-xl text-center text-muted-foreground">
          No events found in the selected time range
        </div>
      )}
    </div>
  );
}

'use client';


import { Plus, Calendar, Clock, Users, Music, Mic, Star, MapPin, FileText } from "lucide-react";
import { useEffect, useState } from 'react';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';

export default function PerformancesClient({ orgId }: { orgId: string }) {
  const t = useTranslations('programming');
  const sb = createBrowserClient();

  const [data, setData] = useState<DataRecord[]>([]);
  const [loading, setLoading] = useState(false);
  const [selectedRecord, setSelectedRecord] = useState<unknown>(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerMode, setDrawerMode] = useState<'view' | 'edit' | 'create'>('view');

  // Define field configuration for performances
  const fields: FieldConfig[] = [
    {
      key: 'name',
      label: 'Performance Name',
      type: 'text',
      required: true,
      sortable: true,
      filterable: true,
      width: 200
    },
    {
      key: 'project_name',
      label: 'Project',
      type: 'text',
      sortable: true,
      filterable: true,
      width: 150
    },
    {
      key: 'starts_at',
      label: 'Performance Date',
      type: 'date',
      sortable: true,
      filterable: true,
      width: 180
    },
    {
      key: 'duration_minutes',
      label: 'Duration (min)',
      type: 'number',
      sortable: true,
      filterable: true,
      width: 120
    },
    {
      key: 'venue',
      label: 'Venue',
      type: 'text',
      sortable: true,
      filterable: true,
      width: 150
    },
    {
      key: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'planning', label: 'Planning' },
        { value: 'rehearsal', label: 'Rehearsal' },
        { value: 'ready', label: 'Ready' },
        { value: 'live', label: 'Live' },
        { value: 'completed', label: 'Completed' },
        { value: 'cancelled', label: 'Cancelled' }
      ],
      sortable: true,
      filterable: true,
      groupable: true,
      width: 120
    },
    {
      key: 'lineup_count',
      label: 'Performers',
      type: 'number',
      sortable: true,
      width: 100
    },
    {
      key: 'technical_requirements',
      label: 'Tech Requirements',
      type: 'text',
      filterable: true,
      width: 180
    }
  ];

  useEffect(() => {
    loadPerformances();
  }, [orgId]);

  const loadPerformances = async () => {
    if (!orgId) return;

    try {
      setLoading(true);

      const { data: performancesData, error } = await sb
        .from('events')
        .select(`
          *,
          projects!inner(
            name,
            organization_id
          ),
          lineups(
            id,
            performer,
            stage,
            start_time,
            end_time
          ),
          riders(
            id,
            kind,
            details
          ),
          call_sheets(
            id,
            call_date,
            details
          )
        `)
        .eq('kind', 'performance')
        .eq('projects.organization_id', orgId)
        .order('starts_at', { ascending: false });

      if (error) throw error;

      // Transform data to include computed fields
      const transformedData = (performancesData || []).map(performance => {
        const duration = performance.ends_at && performance.starts_at 
          ? Math.round((new Date(performance.ends_at).getTime() - new Date(performance.starts_at).getTime()) / (1000 * 60))
          : null;

        return {
          ...performance,
          project_name: performance.projects?.name || 'Unknown Project',
          status: computePerformanceStatus(performance),
          duration_minutes: duration,
          lineup_count: performance.lineups?.length || 0,
          riders_count: performance.riders?.length || 0,
          call_sheets_count: performance.call_sheets?.length || 0,
          venue: performance.location || 'TBD',
          technical_requirements: performance.riders?.find((r: unknown) => r.kind === 'technical')?.details?.summary || 'None specified'
        };
      });

      setData(transformedData);
    } catch (error) {
      console.error('Error loading performances:', error);
    } finally {
      setLoading(false);
    }
  };

  const computePerformanceStatus = (performance: unknown) => {
    const now = new Date();
    const startDate = performance.starts_at ? new Date(performance.starts_at) : null;
    const endDate = performance.ends_at ? new Date(performance.ends_at) : null;

    if (!startDate) return 'planning';

    // Check if performance is happening now
    if (startDate <= now && (!endDate || endDate >= now)) return 'live';

    // Check if performance is completed
    if (endDate && endDate < now) return 'completed';

    // Check if performance is soon (within 2 hours)
    const twoHoursFromNow = new Date(now.getTime() + 2 * 60 * 60 * 1000);
    if (startDate <= twoHoursFromNow && startDate > now) return 'ready';

    // Check if we have lineups (indicates rehearsal stage)
    if (performance.lineups && performance.lineups.length > 0) return 'rehearsal';

    return 'planning';
  };

  const handleCreatePerformance = () => {
    setSelectedRecord(null);
    setDrawerMode('create');
    setDrawerOpen(true);
  };

  const handleEditPerformance = (performance: unknown) => {
    setSelectedRecord(performance);
    setDrawerMode('edit');
    setDrawerOpen(true);
  };

  const handleViewPerformance = (performance: unknown) => {
    setSelectedRecord(performance);
    setDrawerMode('view');
    setDrawerOpen(true);
  };

  const handleSavePerformance = async (performanceData: unknown) => {
    try {
      const eventData = {
        ...performanceData,
        kind: 'performance'
      };

      if (drawerMode === 'create') {
        const { error } = await sb.from('events').insert(eventData);
        if (error) throw error;
      } else if (drawerMode === 'edit') {
        const { error } = await sb
          .from('events')
          .update(eventData)
          .eq('id', selectedRecord.id);
        if (error) throw error;
      }

      await loadPerformances();
      setDrawerOpen(false);
    } catch (error) {
      console.error('Error saving performance:', error);
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'planning':
        return 'color-muted bg-secondary/50';
      case 'rehearsal':
        return 'color-warning bg-warning/10';
      case 'ready':
        return 'color-accent bg-accent/10';
      case 'live':
        return 'color-success bg-success/10';
      case 'completed':
        return 'color-secondary bg-secondary/10';
      case 'cancelled':
        return 'color-destructive bg-destructive/10';
      default:
        return 'color-muted bg-secondary/50';
    }
  };

  // Configure DataView
  const config: DataViewConfig = {
    id: 'performances-management',
    name: 'Performances Management',
    viewType: 'grid',
    defaultView: 'grid',
    fields,
    data,

    onSearch: (query: string) => {
      console.log('Search performances:', query);
    },
    onFilter: (filters: unknown[]) => {
      console.log('Filter performances:', filters);
    },
    onSort: (sorts: unknown[]) => {
      console.log('Sort performances:', sorts);
    },
    onRefresh: loadPerformances,
    onExport: (data: unknown[], format: string) => {
      console.log('Export performances:', format, data);
    },
    onImport: (data: unknown[]) => {
      console.log('Import performances:', data);
    },
  };

  return (
    <div className="stack-md">
      <DataViewProvider config={config}>
        <StateManagerProvider>
          <div className="stack-md">
            {/* Header Actions */}
            <div className="flex items-center justify-between mb-md">
              <div className="flex items-center gap-md">
                <h2 className="text-body text-heading-4">Performance Management</h2>
                <Button>
                  <Plus className="h-4 w-4 mr-sm" />
                  Create Performance
                </Button>
              </div>
              <div className="flex items-center gap-sm">
                <ViewSwitcher />
                <DataActions />
              </div>
            </div>

            {/* Performance Stats */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-md mb-lg">
              <Card className="p-md">
                <div className="flex items-center gap-sm">
                  <div className="p-sm bg-accent/10 rounded-lg">
                    <Calendar className="h-5 w-5 color-accent" />
                  </div>
                  <div>
                    <div className="text-heading-3 text-heading-3">
                      {data.filter(p => p.status === 'ready' || p.status === 'live').length}
                    </div>
                    <div className="text-body-sm color-muted">Upcoming</div>
                  </div>
                </div>
              </Card>

              <Card className="p-md">
                <div className="flex items-center gap-sm">
                  <div className="p-sm bg-success/10 rounded-lg">
                    <Music className="h-5 w-5 color-success" />
                  </div>
                  <div>
                    <div className="text-heading-3 text-heading-3">
                      {data.filter(p => p.status === 'live').length}
                    </div>
                    <div className="text-body-sm color-muted">Live Now</div>
                  </div>
                </div>
              </Card>

              <Card className="p-md">
                <div className="flex items-center gap-sm">
                  <div className="p-sm bg-secondary/10 rounded-lg">
                    <Users className="h-5 w-5 color-secondary" />
                  </div>
                  <div>
                    <div className="text-heading-3 text-heading-3">
                      {data.reduce((sum, p) => sum + (p.lineup_count || 0), 0)}
                    </div>
                    <div className="text-body-sm color-muted">Total Performers</div>
                  </div>
                </div>
              </Card>

              <Card className="p-md">
                <div className="flex items-center gap-sm">
                  <div className="p-sm bg-warning/10 rounded-lg">
                    <Star className="h-5 w-5 color-warning" />
                  </div>
                  <div>
                    <div className="text-heading-3 text-heading-3">
                      {data.filter(p => p.status === 'completed').length}
                    </div>
                    <div className="text-body-sm color-muted">Completed</div>
                  </div>
                </div>
              </Card>
            </div>

            {/* Data Views */}
            <DataGrid />

            <KanbanBoard 
              columns={[
                { id: 'planning', title: 'Planning' },
                { id: 'rehearsal', title: 'Rehearsal' },
                { id: 'ready', title: 'Ready' },
                { id: 'live', title: 'Live' },
                { id: 'completed', title: 'Completed' }
              ]}
              statusField="status"
              titleField="name"
              onCardClick={handleViewPerformance}
            />

            <CalendarView 
              startDateField="starts_at"
              endDateField="ends_at"
              titleField="name"

            />

            <ListView 
              titleField="name"
              subtitleField="project_name"
              onItemClick={handleViewPerformance}
            />

            {/* Performance Details Drawer */}
            <AppDrawer
              open={drawerOpen}
              onClose={() => {
                setDrawerOpen(false);
                setSelectedRecord(null);
              }}
              title={
                drawerMode === 'create' 
                  ? 'Create Performance' 
                  : selectedRecord?.name || 'Performance Details'
              }
              record={selectedRecord || null}
              fields={fields}
              mode={drawerMode}
              tabs={[{
                key: 'content',
                label: 'Details',
                content: (
                  selectedRecord ? (
                    <div className="stack-lg mt-lg">
                      {/* Performance Info */}
                      <div className="grid grid-cols-2 gap-md">
                        <div className="flex items-center gap-sm text-body-sm">
                          <Calendar className="h-4 w-4 color-muted" />
                          <span className="form-label">Date:</span>
                          <span>
                            {selectedRecord.starts_at 
                              ? new Date(selectedRecord.starts_at).toLocaleDateString()
                              : 'Not scheduled'
                            }
                          </span>
                        </div>
                        <div className="flex items-center gap-sm text-body-sm">
                          <Clock className="h-4 w-4 color-muted" />
                          <span className="form-label">Time:</span>
                          <span>
                            {selectedRecord.starts_at 
                              ? new Date(selectedRecord.starts_at).toLocaleTimeString([], { 
                                  hour: '2-digit', 
                                  minute: '2-digit' 
                                })
                              : 'TBD'
                            }
                          </span>
                        </div>
                        <div className="flex items-center gap-sm text-body-sm">
                          <MapPin className="h-4 w-4 color-muted" />
                          <span className="form-label">Venue:</span>
                          <span>{selectedRecord.venue || 'TBD'}</span>
                        </div>
                        <div className="flex items-center gap-sm text-body-sm">
                          <Clock className="h-4 w-4 color-muted" />
                          <span className="form-label">Duration:</span>
                          <span>{selectedRecord.duration_minutes ? `${selectedRecord.duration_minutes} min` : 'TBD'}</span>
                        </div>
                      </div>

                      {/* Status Badge */}
                      <div className="flex items-center gap-sm">
                        <span className="form-label text-body-sm">Status:</span>
                        <Badge className={getStatusColor(selectedRecord.status)}>
                          {selectedRecord.status?.replace('_', ' ') || 'Unknown'}
                        </Badge>
                      </div>

                      {/* Performance Metrics */}
                      <div className="grid grid-cols-3 gap-md pt-md border-t">
                        <div className="text-center">
                          <div className="text-heading-3 text-heading-3 color-accent">{selectedRecord.lineup_count || 0}</div>
                          <div className="text-body-sm color-muted flex items-center justify-center gap-xs">
                            <Users className="h-3 w-3" />
                            Performers
                          </div>
                        </div>
                        <div className="text-center">
                          <div className="text-heading-3 text-heading-3 color-success">{selectedRecord.riders_count || 0}</div>
                          <div className="text-body-sm color-muted flex items-center justify-center gap-xs">
                            <FileText className="h-3 w-3" />
                            Riders
                          </div>
                        </div>
                        <div className="text-center">
                          <div className="text-heading-3 text-heading-3 color-secondary">{selectedRecord.call_sheets_count || 0}</div>
                          <div className="text-body-sm color-muted flex items-center justify-center gap-xs">
                            <Calendar className="h-3 w-3" />
                            Call Sheets
                          </div>
                        </div>
                      </div>

                      {/* Technical Requirements */}
                      {selectedRecord.technical_requirements && selectedRecord.technical_requirements !== 'None specified' && (
                        <div className="pt-md border-t">
                          <h4 className="form-label text-body-sm mb-sm flex items-center gap-sm">
                            <Mic className="h-4 w-4" />
                            Technical Requirements
                          </h4>
                          <p className="text-body-sm color-muted bg-secondary/50 p-sm rounded-lg">
                            {selectedRecord.technical_requirements}
                          </p>
                        </div>
                      )}

                      {/* Quick Actions */}
                      <div className="flex gap-sm pt-md border-t">
                        <Button asChild>
                          <a href={`/programming/lineups?event_id=${selectedRecord.id}`}>
                            <Users className="h-4 w-4 mr-sm" />
                            Manage Lineup
                          </a>
                        </Button>
                        <Button asChild>
                          <a href={`/programming/riders?event_id=${selectedRecord.id}`}>
                            <FileText className="h-4 w-4 mr-sm" />
                            Tech Riders
                          </a>
                        </Button>
                        <Button asChild>
                          <a href={`/programming/call-sheets?event_id=${selectedRecord.id}`}>
                            <Calendar className="h-4 w-4 mr-sm" />
                            Call Sheets
                          </a>
                        </Button>
                      </div>
                    </div>
                  ) : <div />
                )
              }]}
            />
          </div>
        </StateManagerProvider>
      </DataViewProvider>
    </div>
  );
}

'use client';

import { Plus, Search, Filter, Download, Upload, MoreHorizontal, MapPin, Users, Square, Home, Grid3X3, List, BarChart3, Clock } from "lucide-react";
import { useCallback, useEffect, useMemo, useState } from 'react';
import { createBrowserClient } from '@ghxstship/auth';
import {
  Badge,
  Button,
  Card,
  Input,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
} from '@ghxstship/ui';

import type {
  ProgrammingSpace,
  SpaceFilters,
  SpaceSort,
  ViewType,
  SpaceKind,
  SpaceStatus,
  SpaceAccessLevel,
  SpaceProject,
} from './types';

// Import view components
import ProgrammingSpacesListView from './views/ProgrammingSpacesListView';
import ProgrammingSpacesGridView from './views/ProgrammingSpacesGridView';
import ProgrammingSpacesTimelineView from './views/ProgrammingSpacesTimelineView';
import ProgrammingSpacesAnalyticsView from './views/ProgrammingSpacesAnalyticsView';

// Import drawer components
import CreateProgrammingSpaceDrawer from './drawers/CreateProgrammingSpaceDrawer';
import EditProgrammingSpaceDrawer from './drawers/EditProgrammingSpaceDrawer';
import ViewProgrammingSpaceDrawer from './drawers/ViewProgrammingSpaceDrawer';

interface User {
  id: string;
  email: string;
  full_name?: string | null;
  avatar_url?: string | null;
}

interface ProgrammingSpacesClientProps {
  orgId: string;
  currentUserId: string;
  initialSpaces: ProgrammingSpace[];
  projects: SpaceProject[];
  users: User[];
}

export default function ProgrammingSpacesClient({
  orgId,
  currentUserId,
  initialSpaces,
  projects,
  users,
}: ProgrammingSpacesClientProps) {
  const supabase = useMemo(() => createBrowserClient(), []);

  // State management
  const [spaces, setSpaces] = useState<ProgrammingSpace[]>(initialSpaces);
  const [loading, setLoading] = useState(false);
  const [selectedSpaces, setSelectedSpaces] = useState<string[]>([]);
  const [currentView, setCurrentView] = useState<ViewType>('list');
  const [searchQuery, setSearchQuery] = useState('');
  const [filters, setFilters] = useState<SpaceFilters>({});
  const [sort, setSort] = useState<SpaceSort>({ field: 'name', direction: 'asc' });

  // Drawer state
  const [createDrawerOpen, setCreateDrawerOpen] = useState(false);
  const [editDrawerOpen, setEditDrawerOpen] = useState(false);
  const [viewDrawerOpen, setViewDrawerOpen] = useState(false);
  const [selectedSpace, setSelectedSpace] = useState<ProgrammingSpace | null>(null);

  // Real-time subscription
  useEffect(() => {
    const channel = supabase
      .channel(`programming_spaces_${orgId}`)
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'programming_spaces',
          filter: `organization_id=eq.${orgId}`,
        },
        (payload) => {
          if (payload.eventType === 'INSERT') {
            fetchSpaces();
          } else if (payload.eventType === 'UPDATE') {
            setSpaces((prev) =>
              prev.map((space) =>
                space.id === payload.new.id ? { ...space, ...payload.new } : space
              )
            );
          } else if (payload.eventType === 'DELETE') {
            setSpaces((prev) => prev.filter((space) => space.id !== payload.old.id));
          }
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [supabase, orgId]);

  // Fetch spaces with filters
  const fetchSpaces = useCallback(async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams();

      if (filters.project_id) params.append('project_id', filters.project_id);
      if (filters.kind) params.append('kind', filters.kind);
      if (filters.status) params.append('status', filters.status);
      if (filters.access_level) params.append('access_level', filters.access_level);
      if (filters.building) params.append('building', filters.building);
      if (filters.floor) params.append('floor', filters.floor);
      if (searchQuery) params.append('search', searchQuery);
      if (filters.min_capacity) params.append('min_capacity', filters.min_capacity.toString());
      if (filters.max_capacity) params.append('max_capacity', filters.max_capacity.toString());
      if (filters.is_bookable !== undefined) params.append('is_bookable', filters.is_bookable.toString());

      const response = await fetch(`/api/v1/programming/spaces?${params}`);
      if (!response.ok) throw new Error('Failed to fetch spaces');

      const data = await response.json();
      setSpaces(data);
    } catch (error) {
      console.error('Error fetching spaces:', error);
    } finally {
      setLoading(false);
    }
  }, [filters, searchQuery]);

  // Trigger fetch when filters change
  useEffect(() => {
    fetchSpaces();
  }, [fetchSpaces]);

  // Filter and sort spaces
  const filteredAndSortedSpaces = useMemo(() => {
    let filtered = [...spaces];

    // Apply local search if no server search
    if (searchQuery && !filters.search) {
      const query = searchQuery.toLowerCase();
      filtered = filtered.filter(
        (space) =>
          space.name?.toLowerCase().includes(query) ||
          space.description?.toLowerCase().includes(query) ||
          space.location?.toLowerCase().includes(query) ||
          space.building?.toLowerCase().includes(query)
      );
    }

    // Apply sorting
    filtered.sort((a, b) => {
      const aValue = a[sort.field];
      const bValue = b[sort.field];

      if (aValue === null || aValue === undefined) return 1;
      if (bValue === null || bValue === undefined) return -1;

      if (typeof aValue === 'string' && typeof bValue === 'string') {
        return sort.direction === 'asc'
          ? aValue.localeCompare(bValue)
          : bValue.localeCompare(aValue);
      }

      if (aValue < bValue) return sort.direction === 'asc' ? -1 : 1;
      if (aValue > bValue) return sort.direction === 'asc' ? 1 : -1;
      return 0;
    });

    return filtered;
  }, [spaces, searchQuery, filters.search, sort]);

  // Get unique buildings and floors for filters
  const buildings = useMemo(() => {
    const uniqueBuildings = [...new Set(spaces.map(s => s.building).filter(Boolean))];
    return uniqueBuildings.sort();
  }, [spaces]);

  const floors = useMemo(() => {
    const uniqueFloors = [...new Set(spaces.map(s => s.floor).filter(Boolean))];
    return uniqueFloors.sort();
  }, [spaces]);

  // Handlers
  const handleCreateSpace = () => {
    setCreateDrawerOpen(true);
  };

  const handleEditSpace = (space: ProgrammingSpace) => {
    setSelectedSpace(space);
    setEditDrawerOpen(true);
  };

  const handleViewSpace = (space: ProgrammingSpace) => {
    setSelectedSpace(space);
    setViewDrawerOpen(true);
  };

  const handleDeleteSpace = async (spaceId: string) => {
    try {
      const response = await fetch(`/api/v1/programming/spaces/${spaceId}`, {
        method: 'DELETE',
      });

      if (!response.ok) throw new Error('Failed to delete space');

      setSpaces((prev) => prev.filter((space) => space.id !== spaceId));
    } catch (error) {
      console.error('Error deleting space:', error);
    }
  };

  const handleBulkAction = async (action: string, spaceIds: string[]) => {
    try {
      setLoading(true);

      switch (action) {
        case 'delete':
          await Promise.all(
            spaceIds.map((id) =>
              fetch(`/api/v1/programming/spaces/${id}`, { method: 'DELETE' })
            )
          );
          setSpaces((prev) => prev.filter((space) => !spaceIds.includes(space.id)));
          break;
        case 'available':
          await Promise.all(
            spaceIds.map((id) =>
              fetch(`/api/v1/programming/spaces/${id}`, {
                method: 'PATCH',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ status: 'available' }),
              })
            )
          );
          fetchSpaces();
          break;
        case 'maintenance':
          await Promise.all(
            spaceIds.map((id) =>
              fetch(`/api/v1/programming/spaces/${id}`, {
                method: 'PATCH',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ status: 'maintenance' }),
              })
            )
          );
          fetchSpaces();
          break;
      }

      setSelectedSpaces([]);
    } catch (error) {
      console.error('Error performing bulk action:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSuccess = () => {
    fetchSpaces();
    setCreateDrawerOpen(false);
    setEditDrawerOpen(false);
    setSelectedSpace(null);
  };

  // Export functionality
  const handleExport = () => {
    const csvContent = [
      ['Name', 'Kind', 'Status', 'Access Level', 'Capacity', 'Building', 'Floor', 'Location'].join(','),
      ...filteredAndSortedSpaces.map((space) =>
        [
          space.name,
          space.kind,
          space.status,
          space.access_level,
          space.capacity || '',
          space.building || '',
          space.floor || '',
          space.location || '',
        ].join(',')
      ),
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `programming-spaces-${new Date().toISOString().split('T')[0]}.csv`;
    a.click();
    URL.revokeObjectURL(url);
  };

  // View icons
  const viewIcons = {
    list: List,
    grid: Grid3X3,
    timeline: Clock,
    analytics: BarChart3,
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold">Programming Spaces</h1>
          <p className="text-muted-foreground">
            Manage venues, rooms, and facilities for events and productions
          </p>
        </div>
        <Button onClick={handleCreateSpace}>
          <Plus className="mr-2 h-4 w-4" />
          Create Space
        </Button>
      </div>

      {/* Filters and Search */}
      <Card className="p-4">
        <div className="flex flex-col gap-4 md:flex-row md:items-center md:justify-between">
          <div className="flex flex-1 items-center gap-4">
            <div className="relative flex-1 max-w-sm">
              <Search className="absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 text-muted-foreground" />
              <Input
                placeholder="Search spaces..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-9"
              />
            </div>

            <Select
              value={filters.kind || ''}
              onValueChange={(value) =>
                setFilters((prev) => ({ ...prev, kind: value as SpaceKind || undefined }))
              }
            >
              <SelectTrigger className="w-40">
                <SelectValue placeholder="All Types" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All Types</SelectItem>
                <SelectItem value="room">Room</SelectItem>
                <SelectItem value="green_room">Green Room</SelectItem>
                <SelectItem value="dressing_room">Dressing Room</SelectItem>
                <SelectItem value="meeting_room">Meeting Room</SelectItem>
                <SelectItem value="classroom">Classroom</SelectItem>
                <SelectItem value="studio">Studio</SelectItem>
                <SelectItem value="rehearsal_room">Rehearsal Room</SelectItem>
                <SelectItem value="storage">Storage</SelectItem>
                <SelectItem value="office">Office</SelectItem>
                <SelectItem value="lounge">Lounge</SelectItem>
                <SelectItem value="other">Other</SelectItem>
              </SelectContent>
            </Select>

            <Select
              value={filters.status || ''}
              onValueChange={(value) =>
                setFilters((prev) => ({ ...prev, status: value as SpaceStatus || undefined }))
              }
            >
              <SelectTrigger className="w-40">
                <SelectValue placeholder="All Status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All Status</SelectItem>
                <SelectItem value="available">Available</SelectItem>
                <SelectItem value="occupied">Occupied</SelectItem>
                <SelectItem value="reserved">Reserved</SelectItem>
                <SelectItem value="maintenance">Maintenance</SelectItem>
                <SelectItem value="cleaning">Cleaning</SelectItem>
                <SelectItem value="out_of_service">Out of Service</SelectItem>
              </SelectContent>
            </Select>

            {buildings.length > 0 && (
              <Select
                value={filters.building || ''}
                onValueChange={(value) =>
                  setFilters((prev) => ({ ...prev, building: value || undefined }))
                }
              >
                <SelectTrigger className="w-40">
                  <SelectValue placeholder="All Buildings" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="">All Buildings</SelectItem>
                  {buildings.map((building) => (
                    <SelectItem key={building} value={building}>
                      {building}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            )}

            <Select
              value={filters.project_id || ''}
              onValueChange={(value) =>
                setFilters((prev) => ({ ...prev, project_id: value || undefined }))
              }
            >
              <SelectTrigger className="w-48">
                <SelectValue placeholder="All Projects" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All Projects</SelectItem>
                {projects.map((project) => (
                  <SelectItem key={project.id} value={project.id}>
                    {project.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div className="flex items-center gap-2">
            <Button variant="outline" size="sm" onClick={handleExport}>
              <Download className="mr-2 h-4 w-4" />
              Export
            </Button>

            {/* View Switcher */}
            <div className="flex items-center rounded-lg border p-1">
              {(['list', 'grid', 'timeline', 'analytics'] as ViewType[]).map((view) => {
                const Icon = viewIcons[view];
                return (
                  <Button
                    key={view}
                    variant={currentView === view ? 'default' : 'ghost'}
                    size="sm"
                    onClick={() => setCurrentView(view)}
                    className="h-8 w-8 p-0"
                  >
                    <Icon className="h-4 w-4" />
                  </Button>
                );
              })}
            </div>
          </div>
        </div>

        {/* Bulk Actions */}
        {selectedSpaces.length > 0 && (
          <div className="mt-4 flex items-center gap-2 rounded-lg border bg-muted/50 p-3">
            <span className="text-sm text-muted-foreground">
              {selectedSpaces.length} space(s) selected
            </span>
            <div className="flex gap-2">
              <Button
                size="sm"
                variant="outline"
                onClick={() => handleBulkAction('available', selectedSpaces)}
              >
                Mark Available
              </Button>
              <Button
                size="sm"
                variant="outline"
                onClick={() => handleBulkAction('maintenance', selectedSpaces)}
              >
                Mark Maintenance
              </Button>
              <Button
                size="sm"
                variant="destructive"
                onClick={() => handleBulkAction('delete', selectedSpaces)}
              >
                Delete
              </Button>
            </div>
          </div>
        )}
      </Card>

      {/* Content */}
      <div className="min-h-[400px]">
        {currentView === 'list' && (
          <ProgrammingSpacesListView
            spaces={filteredAndSortedSpaces}
            loading={loading}
            selectedSpaces={selectedSpaces}
            onSelectionChange={setSelectedSpaces}
            onEdit={handleEditSpace}
            onView={handleViewSpace}
            onDelete={handleDeleteSpace}
            sort={sort}
            onSortChange={setSort}
          />
        )}

        {currentView === 'grid' && (
          <ProgrammingSpacesGridView
            spaces={filteredAndSortedSpaces}
            loading={loading}
            onEdit={handleEditSpace}
            onView={handleViewSpace}
            onDelete={handleDeleteSpace}
          />
        )}

        {currentView === 'timeline' && (
          <ProgrammingSpacesTimelineView
            spaces={filteredAndSortedSpaces}
            loading={loading}
            onEdit={handleEditSpace}
            onView={handleViewSpace}
          />
        )}

        {currentView === 'analytics' && (
          <ProgrammingSpacesAnalyticsView
            spaces={filteredAndSortedSpaces}
            loading={loading}
          />
        )}
      </div>

      {/* Drawers */}
      <CreateProgrammingSpaceDrawer
        open={createDrawerOpen}
        onOpenChange={setCreateDrawerOpen}
        orgId={orgId}
        currentUserId={currentUserId}
        projects={projects}
        onSuccess={handleSuccess}
      />

      {selectedSpace && (
        <>
          <EditProgrammingSpaceDrawer
            open={editDrawerOpen}
            onOpenChange={setEditDrawerOpen}
            space={selectedSpace}
            orgId={orgId}
            currentUserId={currentUserId}
            projects={projects}
            onSuccess={handleSuccess}
          />

          <ViewProgrammingSpaceDrawer
            open={viewDrawerOpen}
            onOpenChange={setViewDrawerOpen}
            space={selectedSpace}
            users={users}
            onEdit={() => {
              setViewDrawerOpen(false);
              setEditDrawerOpen(true);
            }}
            onDelete={() => {
              handleDeleteSpace(selectedSpace.id);
              setViewDrawerOpen(false);
              setSelectedSpace(null);
            }}
          />
        </>
      )}
    </div>
  );
}

