            {activation.budget && (
              <div className="flex items-center gap-xs text-sm text-muted-foreground">
                <DollarSign className="h-3 w-3" />
                <span>${activation.budget.toLocaleString()}</span>
              </div>
            )}
          </div>
        </Card>
      ))}
    </div>
  );
}

"use client";

import { useMemo } from "react";
import { Card, Badge, Button, Checkbox, DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@ghxstship/ui";
import { format, parseISO } from "date-fns";
import type { Activation } from "../ActivationsClient";

interface ActivationKanbanViewProps {
  activations: Activation[];
  selectedActivations: Set<string>;
  onSelectActivation: (id: string) => void;
  onView: (activation: Activation) => void;
  onEdit: (activation: Activation) => void;
  onDelete: (activation: Activation) => void;
  onStatusChange: (activation: Activation, status: string) => void;
}

export default function ActivationKanbanView({
  activations,
  selectedActivations,
  onSelectActivation,
  onView,
  onEdit,
  onDelete,
  onStatusChange,
}: ActivationKanbanViewProps) {
  // Group activations by status
  const groupedActivations = useMemo(() => {
    const groups: Record<string, Activation[]> = {
      planning: [],
      ready: [],
      active: [],
      completed: [],
      cancelled: [],
    };

    activations.forEach((activation) => {
      if (groups[activation.status]) {
        groups[activation.status].push(activation);
      }
    });

    return groups;
  }, [activations]);

  const getStatusBadgeVariant = (status: string) => {
    switch (status) {
      case "planning":
        return "secondary";
      case "ready":
        return "warning";
      case "active":
        return "info";
      case "completed":
        return "success";
      case "cancelled":
        return "destructive";
      default:
        return "secondary";
    }
  };

  const getTypeBadgeVariant = (type: string) => {
    switch (type) {
      case "full_launch":
        return "default";
      case "soft_launch":
        return "secondary";
      case "beta":
        return "warning";
      case "pilot":
        return "info";
      case "rollout":
        return "success";
      default:
        return "secondary";
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "planning":
        return Clock;
      case "ready":
        return AlertCircle;
      case "active":
        return Play;
      case "completed":
        return CheckCircle;
      case "cancelled":
        return XCircle;
      default:
        return Clock;
    }
  };

  return (
    <div className="flex gap-md overflow-x-auto pb-md">
      {Object.entries(groupedActivations).map(([status, items]) => {
        const StatusIcon = getStatusIcon(status);
        return (
          <div key={status} className="flex-shrink-0 w-80">
            <div className="bg-muted rounded-lg p-sm mb-sm">
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-sm">
                  <StatusIcon className="h-4 w-4" />
                  <h3 className="font-semibold capitalize">{status.replace("_", " ")}</h3>
                </div>
                <Badge variant={getStatusBadgeVariant(status)}>{items.length}</Badge>
              </div>
            </div>

            <div className="space-y-sm max-h-[calc(100vh-300px)] overflow-y-auto">
              {items.map((activation) => (
                <Card
                  key={activation.id}
                  className={`p-sm cursor-pointer hover:shadow-md transition-shadow ${
                    selectedActivations.has(activation.id) ? "ring-2 ring-primary" : ""
                  }`}
                  onClick={() => onView(activation)}
                >
                  <div className="flex items-start justify-between mb-xs">
                    <Checkbox
                      checked={selectedActivations.has(activation.id)}
                      onCheckedChange={() => onSelectActivation(activation.id)}
                      onClick={(e) => e.stopPropagation()}
                    />
                    <DropdownMenu>
                      <DropdownMenuTrigger asChild onClick={(e) => e.stopPropagation()}>
                        <Button variant="ghost" size="sm">
                          <MoreVertical className="h-3 w-3" />
                        </Button>
                      </DropdownMenuTrigger>
                      <DropdownMenuContent align="end">
                        <DropdownMenuItem onClick={() => onView(activation)}>
                          <Eye className="mr-2 h-4 w-4" />
                          View
                        </DropdownMenuItem>
                        <DropdownMenuItem onClick={() => onEdit(activation)}>
                          <Edit className="mr-2 h-4 w-4" />
                          Edit
                        </DropdownMenuItem>
                        {status !== "planning" && (
                          <DropdownMenuItem onClick={() => onStatusChange(activation, "planning")}>
                            <Clock className="mr-2 h-4 w-4" />
                            Move to Planning
                          </DropdownMenuItem>
                        )}
                        {status !== "ready" && (
                          <DropdownMenuItem onClick={() => onStatusChange(activation, "ready")}>
                            <AlertCircle className="mr-2 h-4 w-4" />
                            Move to Ready
                          </DropdownMenuItem>
                        )}
                        {status !== "active" && (
                          <DropdownMenuItem onClick={() => onStatusChange(activation, "active")}>
                            <Play className="mr-2 h-4 w-4" />
                            Move to Active
                          </DropdownMenuItem>
                        )}
                        {status !== "completed" && (
                          <DropdownMenuItem onClick={() => onStatusChange(activation, "completed")}>
                            <CheckCircle className="mr-2 h-4 w-4" />
                            Move to Completed
                          </DropdownMenuItem>
                        )}
                        <DropdownMenuItem 
                          onClick={() => onDelete(activation)}
                          className="text-destructive"
                        >
                          <Trash2 className="mr-2 h-4 w-4" />
                          Delete
                        </DropdownMenuItem>
                      </DropdownMenuContent>
                    </DropdownMenu>
                  </div>

                  <h4 className="font-medium mb-xs truncate">{activation.name}</h4>

                  <Badge 
                    variant={getTypeBadgeVariant(activation.activation_type)}
                    className="mb-xs"
                  >
                    {activation.activation_type.replace("_", " ")}
                  </Badge>

                  {activation.project && (
                    <div className="text-xs text-muted-foreground mb-xs truncate">
                      {activation.project.name}
                    </div>
                  )}

                  {activation.scheduled_date && (
                    <div className="text-xs text-muted-foreground">
                      {format(parseISO(activation.scheduled_date), "MMM d")}
                    </div>
                  )}

                  {activation.budget && (
                    <div className="text-xs text-muted-foreground">
                      ${activation.budget.toLocaleString()}
                    </div>
                  )}
                </Card>
              ))}

              {items.length === 0 && (
                <div className="text-center py-lg text-muted-foreground text-sm">
                  No activations in {status}
                </div>
              )}
            </div>
          </div>
        );
      })}
    </div>
  );
}

"use client";

import { Badge, Button, Checkbox, DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@ghxstship/ui";
import { format, parseISO } from "date-fns";
import type { Activation } from "../ActivationsClient";

interface ActivationListViewProps {
  activations: Activation[];
  selectedActivations: Set<string>;
  fieldVisibility: Array<{ id: string; label: string; visible: boolean; sortable: boolean }>;
  sortField: string;
  sortDirection: "asc" | "desc";
  onSelectAll: () => void;
  onSelectActivation: (id: string) => void;
  onSort: (field: string) => void;
  onView: (activation: Activation) => void;
  onEdit: (activation: Activation) => void;
  onDelete: (activation: Activation) => void;
  onDuplicate: (activation: Activation) => void;
  onStatusChange: (activation: Activation, status: string) => void;
}

export default function ActivationListView({
  activations,
  selectedActivations,
  fieldVisibility,
  sortField,
  sortDirection,
  onSelectAll,
  onSelectActivation,
  onSort,
  onView,
  onEdit,
  onDelete,
  onDuplicate,
  onStatusChange,
}: ActivationListViewProps) {
  const visibleFields = fieldVisibility.filter((f) => f.visible);

  const getStatusBadgeVariant = (status: string) => {
    switch (status) {
      case "planning":
        return "secondary";
      case "ready":
        return "warning";
      case "active":
        return "info";
      case "completed":
        return "success";
      case "cancelled":
        return "destructive";
      default:
        return "secondary";
    }
  };

  const getTypeBadgeVariant = (type: string) => {
    switch (type) {
      case "full_launch":
        return "default";
      case "soft_launch":
        return "secondary";
      case "beta":
        return "warning";
      case "pilot":
        return "info";
      case "rollout":
        return "success";
      default:
        return "secondary";
    }
  };

  const renderFieldValue = (activation: Activation, fieldId: string) => {
    switch (fieldId) {
      case "name":
        return <span className="font-medium">{activation.name}</span>;
      case "status":
        return (
          <Badge variant={getStatusBadgeVariant(activation.status)}>
            {activation.status}
          </Badge>
        );
      case "activation_type":
        return (
          <Badge variant={getTypeBadgeVariant(activation.activation_type)}>
            {activation.activation_type.replace("_", " ")}
          </Badge>
        );
      case "project":
        return activation.project?.name || "-";
      case "scheduled_date":
        return activation.scheduled_date
          ? format(parseISO(activation.scheduled_date), "MMM d, yyyy")
          : "-";
      case "actual_date":
        return activation.actual_date
          ? format(parseISO(activation.actual_date), "MMM d, yyyy")
          : "-";
      case "completion_date":
        return activation.completion_date
          ? format(parseISO(activation.completion_date), "MMM d, yyyy")
          : "-";
      case "location":
        return activation.location || "-";
      case "budget":
        return activation.budget ? `$${activation.budget.toLocaleString()}` : "-";
      case "actual_cost":
        return activation.actual_cost ? `$${activation.actual_cost.toLocaleString()}` : "-";
      case "created_at":
        return format(parseISO(activation.created_at), "MMM d, yyyy");
      case "updated_at":
        return format(parseISO(activation.updated_at), "MMM d, yyyy");
      default:
        return "-";
    }
  };

  return (
    <div className="overflow-x-auto">
      <table className="w-full">
        <thead>
          <tr className="border-b">
            <th className="text-left p-sm">
              <Checkbox
                checked={selectedActivations.size === activations.length && activations.length > 0}
                onCheckedChange={onSelectAll}
              />
            </th>
            {visibleFields.map((field) => (
              <th
                key={field.id}
                className={`text-left p-sm ${
                  field.sortable ? "cursor-pointer hover:bg-muted/50" : ""
                }`}
                onClick={() => field.sortable && onSort(field.id)}
              >
                <div className="flex items-center gap-xs">
                  {field.label}
                  {field.sortable && (
                    <>
                      {sortField === field.id ? (
                        sortDirection === "asc" ? (
                          <ArrowUp className="h-3 w-3" />
                        ) : (
                          <ArrowDown className="h-3 w-3" />
                        )
                      ) : (
                        <ArrowUpDown className="h-3 w-3 opacity-30" />
                      )}
                    </>
                  )}
                </div>
              </th>
            ))}
            <th className="text-left p-sm">Actions</th>
          </tr>
        </thead>
        <tbody>
          {activations.map((activation) => (
            <tr
              key={activation.id}
              className={`border-b hover:bg-muted/50 cursor-pointer ${
                selectedActivations.has(activation.id) ? "bg-primary/5" : ""
              }`}
              onClick={() => onView(activation)}
            >
              <td className="p-sm" onClick={(e) => e.stopPropagation()}>
                <Checkbox
                  checked={selectedActivations.has(activation.id)}
                  onCheckedChange={() => onSelectActivation(activation.id)}
                />
              </td>
              {visibleFields.map((field) => (
                <td key={field.id} className="p-sm">
                  {renderFieldValue(activation, field.id)}
                </td>
              ))}
              <td className="p-sm" onClick={(e) => e.stopPropagation()}>
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="ghost" size="sm">
                      <MoreVertical className="h-4 w-4" />
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end">
                    <DropdownMenuItem onClick={() => onView(activation)}>
                      <Eye className="mr-2 h-4 w-4" />
                      View
                    </DropdownMenuItem>
                    <DropdownMenuItem onClick={() => onEdit(activation)}>
                      <Edit className="mr-2 h-4 w-4" />
                      Edit
                    </DropdownMenuItem>
                    <DropdownMenuItem onClick={() => onDuplicate(activation)}>
                      <Copy className="mr-2 h-4 w-4" />
                      Duplicate
                    </DropdownMenuItem>
                    <DropdownMenuItem 
                      onClick={() => onDelete(activation)}
                      className="text-destructive"
                    >
                      <Trash2 className="mr-2 h-4 w-4" />
                      Delete
                    </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {activations.length === 0 && (
        <div className="text-center py-xl text-muted-foreground">
          No activations to display
        </div>
      )}
    </div>
  );
}

"use client";

import { Card, Badge, Button, Checkbox } from "@ghxstship/ui";
import { format, parseISO } from "date-fns";
import type { Inspection } from "../InspectionsClient";
import type { LucideIcon } from "lucide-react";

interface InspectionGridViewProps {
  inspections: Inspection[];
  selectedInspections: Set<string>;
  onSelectInspection: (id: string) => void;
  onView: (inspection: Inspection) => void;
  onEdit: (inspection: Inspection) => void;
  onDelete: (inspection: Inspection) => void;
  onDuplicate: (inspection: Inspection) => void;
  onStatusChange: (inspection: Inspection, status: string) => void;
  getTypeIcon: (type: string) => LucideIcon;
  getStatusBadgeVariant: (status: string) => any;
  getTypeBadgeVariant: (type: string) => any;
  getScoreColor: (score: number) => string;
}

export default function InspectionGridView({
  inspections,
  selectedInspections,
  onSelectInspection,
  onView,
  onEdit,
  onDelete,
  onDuplicate,
  onStatusChange,
  getTypeIcon,
  getStatusBadgeVariant,
  getTypeBadgeVariant,
  getScoreColor,
}: InspectionGridViewProps) {
  const getStatusIcon = (status: string) => {
    switch (status) {
      case "scheduled":
        return Clock;
      case "in_progress":
        return Play;
      case "completed":
        return CheckCircle;
      case "failed":
        return XCircle;
      case "cancelled":
        return XCircle;
      default:
        return Clock;
    }
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
      {inspections.map((inspection) => {
        const TypeIcon = getTypeIcon(inspection.type);
        const StatusIcon = getStatusIcon(inspection.status);

        return (
          <Card
            key={inspection.id}
            className={`p-md cursor-pointer hover:shadow-lg transition-shadow ${
              selectedInspections.has(inspection.id) ? "ring-2 ring-primary" : ""
            }`}
            onClick={() => onView(inspection)}
          >
            <div className="flex items-start justify-between mb-sm">
              <Checkbox
                checked={selectedInspections.has(inspection.id)}
                onChange={() => onSelectInspection(inspection.id)}
                onClick={(e: React.MouseEvent) => e.stopPropagation()}
              />
              <Button
                variant="ghost"
                size="sm"
                onClick={(e) => {
                  e.stopPropagation();
                  onEdit(inspection);
                }}
              >
                <Edit className="h-4 w-4" />
              </Button>
            </div>

            <div className="space-y-sm">
              {/* Type Icon and Title */}
              <div className="flex items-start gap-sm">
                <TypeIcon className="h-5 w-5 text-muted-foreground flex-shrink-0 mt-1" />
                <div className="flex-1">
                  <h3 className="font-semibold line-clamp-2">{inspection.title}</h3>
                </div>
              </div>

              {/* Badges */}
              <div className="flex items-center gap-xs flex-wrap">
                <Badge variant={getTypeBadgeVariant(inspection.type)}>
                  {inspection.type}
                </Badge>
                <Badge variant={getStatusBadgeVariant(inspection.status)}>
                  <StatusIcon className="mr-1 h-3 w-3" />
                  {inspection.status.replace("_", " ")}
                </Badge>
              </div>

              {/* Score */}
              {inspection.score !== undefined && inspection.score !== null && (
                <div className="flex items-center justify-between">
                  <span className="text-sm text-muted-foreground">Score:</span>
                  <span className={`font-bold text-lg ${getScoreColor(inspection.score)}`}>
                    {inspection.score}%
                  </span>
                </div>
              )}

              {/* Pass/Fail Status */}
              {inspection.status === "completed" && (
                <div className="flex items-center justify-between">
                  <span className="text-sm text-muted-foreground">Result:</span>
                  <Badge variant={inspection.is_passed ? "success" : "destructive"}>
                    {inspection.is_passed ? "PASSED" : "FAILED"}
                  </Badge>
                </div>
              )}

              {/* Project */}
              {inspection.project && (
                <div className="text-sm text-muted-foreground truncate">
                  Project: {inspection.project.name}
                </div>
              )}

              {/* Inspector */}
              {inspection.inspector && (
                <div className="flex items-center gap-xs text-sm text-muted-foreground">
                  <User className="h-3 w-3" />
                  <span className="truncate">
                    {inspection.inspector.full_name || inspection.inspector.email}
                  </span>
                </div>
              )}

              {/* Location */}
              {inspection.location && (
                <div className="flex items-center gap-xs text-sm text-muted-foreground">
                  <MapPin className="h-3 w-3" />
                  <span className="truncate">{inspection.location}</span>
                </div>
              )}

              {/* Date */}
              <div className="flex items-center gap-xs text-sm text-muted-foreground">
                <Calendar className="h-3 w-3" />
                <span>
                  {inspection.status === "completed" && inspection.completed_date
                    ? `Completed ${format(parseISO(inspection.completed_date), "MMM d")}`
                    : `Scheduled ${format(parseISO(inspection.scheduled_date), "MMM d")}`}
                </span>
              </div>

              {/* Follow-up Required */}
              {inspection.follow_up_required && (
                <div className="flex items-center gap-xs text-sm text-warning">
                  <AlertTriangle className="h-3 w-3" />
                  <span>Follow-up required</span>
                </div>
              )}

              {/* Actions */}
              <div className="flex items-center gap-xs pt-sm border-t">
                <Button
                  variant="outline"
                  size="sm"
                  className="flex-1"
                  onClick={(e) => {
                    e.stopPropagation();
                    if (inspection.status === "scheduled") {
                      onStatusChange(inspection, "in_progress");
                    } else if (inspection.status === "in_progress") {
                      onStatusChange(inspection, "completed");
                    }
                  }}
                >
                  {inspection.status === "scheduled" && "Start"}
                  {inspection.status === "in_progress" && "Complete"}
                  {inspection.status === "completed" && "View"}
                  {inspection.status === "failed" && "Retry"}
                  {inspection.status === "cancelled" && "Reschedule"}
                </Button>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={(e) => {
                    e.stopPropagation();
                    onDelete(inspection);
                  }}
                >
                  <Trash2 className="h-4 w-4 text-destructive" />
                </Button>
              </div>
            </div>
          </Card>
        );
      })}
    </div>
  );
}

"use client";

import { useMemo } from "react";
import { Card, Badge, Button, Checkbox } from "@ghxstship/ui";
import { format, parseISO } from "date-fns";
import type { Inspection } from "../InspectionsClient";
import type { LucideIcon } from "lucide-react";

interface InspectionKanbanViewProps {
  inspections: Inspection[];
  selectedInspections: Set<string>;
  onSelectInspection: (id: string) => void;
  onView: (inspection: Inspection) => void;
  onEdit: (inspection: Inspection) => void;
  onDelete: (inspection: Inspection) => void;
  onStatusChange: (inspection: Inspection, status: string) => void;
  getTypeIcon: (type: string) => LucideIcon;
  getTypeBadgeVariant: (type: string) => any;
  getScoreColor: (score: number) => string;
}

export default function InspectionKanbanView({
  inspections,
  selectedInspections,
  onSelectInspection,
  onView,
  onEdit,
  onDelete,
  onStatusChange,
  getTypeIcon,
  getTypeBadgeVariant,
  getScoreColor,
}: InspectionKanbanViewProps) {
  // Group inspections by status
  const groupedInspections = useMemo(() => {
    const groups: Record<string, Inspection[]> = {
      scheduled: [],
      in_progress: [],
      completed: [],
      failed: [],
      cancelled: [],
    };

    inspections.forEach((inspection) => {
      if (groups[inspection.status]) {
        groups[inspection.status].push(inspection);
      }
    });

    return groups;
  }, [inspections]);

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "scheduled":
        return Clock;
      case "in_progress":
        return Play;
      case "completed":
        return CheckCircle;
      case "failed":
        return XCircle;
      case "cancelled":
        return AlertCircle;
      default:
        return Clock;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "scheduled":
        return "bg-gray-100";
      case "in_progress":
        return "bg-yellow-100";
      case "completed":
        return "bg-green-100";
      case "failed":
        return "bg-red-100";
      case "cancelled":
        return "bg-gray-100";
      default:
        return "bg-gray-100";
    }
  };

  return (
    <div className="flex gap-md overflow-x-auto pb-md">
      {Object.entries(groupedInspections).map(([status, items]) => {
        const StatusIcon = getStatusIcon(status);
        return (
          <div key={status} className="flex-shrink-0 w-80">
            <div className={`${getStatusColor(status)} rounded-lg p-sm mb-sm`}>
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-sm">
                  <StatusIcon className="h-4 w-4" />
                  <h3 className="font-semibold capitalize">{status.replace("_", " ")}</h3>
                </div>
                <Badge variant="secondary">{items.length}</Badge>
              </div>
            </div>

            <div className="space-y-sm max-h-[calc(100vh-300px)] overflow-y-auto">
              {items.map((inspection) => {
                const TypeIcon = getTypeIcon(inspection.type);
                return (
                  <Card
                    key={inspection.id}
                    className={`p-sm cursor-pointer hover:shadow-md transition-shadow ${
                      selectedInspections.has(inspection.id) ? "ring-2 ring-primary" : ""
                    }`}
                    onClick={() => onView(inspection)}
                  >
                    <div className="flex items-start justify-between mb-xs">
                      <Checkbox
                        checked={selectedInspections.has(inspection.id)}
                        onChange={() => onSelectInspection(inspection.id)}
                        onClick={(e: React.MouseEvent) => e.stopPropagation()}
                      />
                      <div className="flex items-center gap-xs">
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={(e) => {
                            e.stopPropagation();
                            onEdit(inspection);
                          }}
                        >
                          <Edit className="h-3 w-3" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={(e) => {
                            e.stopPropagation();
                            onDelete(inspection);
                          }}
                        >
                          <Trash2 className="h-3 w-3 text-destructive" />
                        </Button>
                      </div>
                    </div>

                    <div className="flex items-start gap-xs mb-xs">
                      <TypeIcon className="h-4 w-4 text-muted-foreground flex-shrink-0 mt-0.5" />
                      <h4 className="font-medium text-sm line-clamp-2">{inspection.title}</h4>
                    </div>

                    <Badge 
                      variant={getTypeBadgeVariant(inspection.type)}
                      className="mb-xs"
                    >
                      {inspection.type}
                    </Badge>

                    {inspection.score !== undefined && inspection.score !== null && (
                      <div className="flex items-center justify-between mb-xs">
                        <span className="text-xs text-muted-foreground">Score:</span>
                        <span className={`font-bold text-sm ${getScoreColor(inspection.score)}`}>
                          {inspection.score}%
                        </span>
                      </div>
                    )}

                    {inspection.status === "completed" && (
                      <Badge 
                        variant={inspection.is_passed ? "success" : "destructive"}
                        className="mb-xs w-full justify-center"
                      >
                        {inspection.is_passed ? "PASSED" : "FAILED"}
                      </Badge>
                    )}

                    {inspection.project && (
                      <div className="text-xs text-muted-foreground mb-xs truncate">
                        {inspection.project.name}
                      </div>
                    )}

                    {inspection.inspector && (
                      <div className="flex items-center gap-xs text-xs text-muted-foreground mb-xs">
                        <User className="h-3 w-3" />
                        <span className="truncate">
                          {inspection.inspector.full_name || inspection.inspector.email}
                        </span>
                      </div>
                    )}

                    <div className="flex items-center gap-xs text-xs text-muted-foreground">
                      <Calendar className="h-3 w-3" />
                      <span>
                        {inspection.status === "completed" && inspection.completed_date
                          ? format(parseISO(inspection.completed_date), "MMM d")
                          : format(parseISO(inspection.scheduled_date), "MMM d")}
                      </span>
                    </div>

                    {inspection.follow_up_required && (
                      <div className="mt-xs pt-xs border-t">
                        <Badge variant="warning" className="text-xs">
                          Follow-up required
                        </Badge>
                      </div>
                    )}

                    {/* Quick Actions */}
                    <div className="mt-sm pt-sm border-t">
                      {status === "scheduled" && (
                        <Button
                          variant="outline"
                          size="sm"
                          className="w-full"
                          onClick={(e) => {
                            e.stopPropagation();
                            onStatusChange(inspection, "in_progress");
                          }}
                        >
                          Start Inspection
                        </Button>
                      )}
                      {status === "in_progress" && (
                        <Button
                          variant="outline"
                          size="sm"
                          className="w-full"
                          onClick={(e) => {
                            e.stopPropagation();
                            onStatusChange(inspection, "completed");
                          }}
                        >
                          Complete Inspection
                        </Button>
                      )}
                      {status === "failed" && (
                        <Button
                          variant="outline"
                          size="sm"
                          className="w-full"
                          onClick={(e) => {
                            e.stopPropagation();
                            onStatusChange(inspection, "scheduled");
                          }}
                        >
                          Reschedule
                        </Button>
                      )}
                    </div>
                  </Card>
                );
              })}

              {items.length === 0 && (
                <div className="text-center py-lg text-muted-foreground text-sm">
                  No inspections
                </div>
              )}
            </div>
          </div>
        );
      })}
    </div>
  );
}

"use client";

import { Badge, Button, Checkbox } from "@ghxstship/ui";
import { format, parseISO } from "date-fns";
import type { Inspection } from "../InspectionsClient";

interface InspectionListViewProps {
  inspections: Inspection[];
  selectedInspections: Set<string>;
  fieldVisibility: Array<{ id: string; label: string; visible: boolean; sortable: boolean }>;
  sortField: string;
  sortDirection: "asc" | "desc";
  onSelectAll: () => void;
  onSelectInspection: (id: string) => void;
  onSort: (field: string) => void;
  onView: (inspection: Inspection) => void;
  onEdit: (inspection: Inspection) => void;
  onDelete: (inspection: Inspection) => void;
  onDuplicate: (inspection: Inspection) => void;
  onStatusChange: (inspection: Inspection, status: string) => void;
  getTypeBadgeVariant: (type: string) => any;
  getStatusBadgeVariant: (status: string) => any;
  getScoreColor: (score: number) => string;
}

export default function InspectionListView({
  inspections,
  selectedInspections,
  fieldVisibility,
  sortField,
  sortDirection,
  onSelectAll,
  onSelectInspection,
  onSort,
  onView,
  onEdit,
  onDelete,
  onDuplicate,
  onStatusChange,
  getTypeBadgeVariant,
  getStatusBadgeVariant,
  getScoreColor,
}: InspectionListViewProps) {
  const visibleFields = fieldVisibility.filter((f) => f.visible);

  const renderFieldValue = (inspection: Inspection, fieldId: string) => {
    switch (fieldId) {
      case "title":
        return <span className="font-medium">{inspection.title}</span>;
      case "type":
        return (
          <Badge variant={getTypeBadgeVariant(inspection.type)}>
            {inspection.type}
          </Badge>
        );
      case "status":
        return (
          <Badge variant={getStatusBadgeVariant(inspection.status)}>
            {inspection.status.replace("_", " ")}
          </Badge>
        );
      case "project":
        return inspection.project?.name || "-";
      case "scheduled_date":
        return format(parseISO(inspection.scheduled_date), "MMM d, yyyy");
      case "completed_date":
        return inspection.completed_date
          ? format(parseISO(inspection.completed_date), "MMM d, yyyy")
          : "-";
      case "inspector":
        return inspection.inspector?.full_name || inspection.inspector?.email || "-";
      case "location":
        return inspection.location || "-";
      case "score":
        return inspection.score !== undefined && inspection.score !== null ? (
          <span className={`font-bold ${getScoreColor(inspection.score)}`}>
            {inspection.score}%
          </span>
        ) : (
          "-"
        );
      case "is_passed":
        return inspection.status === "completed" ? (
          <Badge variant={inspection.is_passed ? "success" : "destructive"}>
            {inspection.is_passed ? "PASSED" : "FAILED"}
          </Badge>
        ) : (
          "-"
        );
      case "follow_up_required":
        return inspection.follow_up_required ? (
          <Badge variant="warning">Required</Badge>
        ) : (
          <Badge variant="secondary">Not Required</Badge>
        );
      case "created_at":
        return format(parseISO(inspection.created_at), "MMM d, yyyy");
      case "updated_at":
        return format(parseISO(inspection.updated_at), "MMM d, yyyy");
      default:
        return "-";
    }
  };

  return (
    <div className="overflow-x-auto">
      <table className="w-full">
        <thead>
          <tr className="border-b">
            <th className="text-left p-sm">
              <Checkbox
                checked={selectedInspections.size === inspections.length && inspections.length > 0}
                onChange={onSelectAll}
              />
            </th>
            {visibleFields.map((field) => (
              <th
                key={field.id}
                className={`text-left p-sm ${
                  field.sortable ? "cursor-pointer hover:bg-muted/50" : ""
                }`}
                onClick={() => field.sortable && onSort(field.id)}
              >
                <div className="flex items-center gap-xs">
                  {field.label}
                  {field.sortable && (
                    <>
                      {sortField === field.id ? (
                        sortDirection === "asc" ? (
                          <ArrowUp className="h-3 w-3" />
                        ) : (
                          <ArrowDown className="h-3 w-3" />
                        )
                      ) : (
                        <ArrowUpDown className="h-3 w-3 opacity-30" />
                      )}
                    </>
                  )}
                </div>
              </th>
            ))}
            <th className="text-left p-sm">Actions</th>
          </tr>
        </thead>
        <tbody>
          {inspections.map((inspection) => (
            <tr
              key={inspection.id}
              className={`border-b hover:bg-muted/50 cursor-pointer ${
                selectedInspections.has(inspection.id) ? "bg-primary/5" : ""
              }`}
              onClick={() => onView(inspection)}
            >
              <td className="p-sm" onClick={(e) => e.stopPropagation()}>
                <Checkbox
                  checked={selectedInspections.has(inspection.id)}
                  onChange={() => onSelectInspection(inspection.id)}
                />
              </td>
              {visibleFields.map((field) => (
                <td key={field.id} className="p-sm">
                  {renderFieldValue(inspection, field.id)}
                </td>
              ))}
              <td className="p-sm" onClick={(e) => e.stopPropagation()}>
                <div className="flex items-center gap-xs">
                  {inspection.status === "scheduled" && (
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => onStatusChange(inspection, "in_progress")}
                      title="Start Inspection"
                    >
                      <Play className="h-4 w-4" />
                    </Button>
                  )}
                  {inspection.status === "in_progress" && (
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => onStatusChange(inspection, "completed")}
                      title="Complete Inspection"
                    >
                      <CheckCircle className="h-4 w-4" />
                    </Button>
                  )}
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onView(inspection)}
                  >
                    <Eye className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onEdit(inspection)}
                  >
                    <Edit className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onDuplicate(inspection)}
                  >
                    <Copy className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onDelete(inspection)}
                    className="text-destructive"
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {inspections.length === 0 && (
        <div className="text-center py-xl text-muted-foreground">
          No inspections to display
        </div>
      )}
    </div>
  );
}

'use client';

import { MapPin, Users } from "lucide-react";
import { useCallback, useMemo, useState, type ChangeEvent, type FormEvent } from 'react';
import { useRouter } from 'next/navigation';
import { createBrowserClient } from '@ghxstship/auth';
import { Badge, Button } from '@ghxstship/ui';
import AppDrawer from '@/app/_components/shared/AppDrawer';

export type LocationType =
  | 'venue'
  | 'office'
  | 'warehouse'
  | 'studio'
  | 'outdoor'
  | 'virtual'
  | 'other';

export type LocationRow = {
  id: string;
  name: string;
  address: string | null;
  city: string | null;
  state: string | null;
  country: string | null;
  type: string;
  capacity: number | null;
  project_id: string | null;
  project?: { name: string };
};

interface LocationComment {
  id: string;
  body: string;
  created_at: string;
}

interface LocationActivity {
  action: string;
  occurred_at: string;
}

interface LocationFormData {
  name: string;
  type: LocationType;
  address: string;
  city: string;
  state: string;
  country: string;
  capacity: string;
  notes: string;
}

type LocationFormErrors = Partial<Record<keyof LocationFormData, string>>;

const initialFormState: LocationFormData = {
  name: '',
  type: 'venue',
  address: '',
  city: '',
  state: '',
  country: '',
  capacity: '',
  notes: '',
};

interface LocationsTableClientProps {
  rows: LocationRow[];
  orgId: string;
}

const typeColorClasses: Record<LocationType, string> = {
  venue: 'bg-secondary/10 color-secondary-foreground',
  office: 'bg-accent/10 color-accent-foreground',
  warehouse: 'bg-secondary/50 color-muted',
  studio: 'bg-accent/50 color-accent-foreground',
  outdoor: 'bg-success/10 color-success-foreground',
  virtual: 'bg-info/10 text-info-foreground',
  other: 'bg-secondary/50 color-muted',
};

const getTypeColor = (type: string) => typeColorClasses[type as LocationType] ?? 'bg-secondary/50 color-muted';

const formatAddress = (location: LocationRow | null) => {
  if (!location) {
    return 'No address';
  }
  const parts = [location.address, location.city, location.state, location.country].filter(Boolean);
  return parts.length > 0 ? parts.join(', ') : 'No address';
};

export default function LocationsTableClient({ rows, orgId }: LocationsTableClientProps) {
  const router = useRouter();
  const supabase = useMemo(() => createBrowserClient(), []);
  const [openId, setOpenId] = useState<string | null>(null);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [formData, setFormData] = useState<LocationFormData>(initialFormState);
  const [formErrors, setFormErrors] = useState<LocationFormErrors>({});
  const [comments, setComments] = useState<LocationComment[]>([]);
  const [activities, setActivities] = useState<LocationActivity[]>([]);
  const [commentsLoading, setCommentsLoading] = useState(false);
  const [activityLoading, setActivityLoading] = useState(false);
  const [commentInput, setCommentInput] = useState('');
  const [commentSaving, setCommentSaving] = useState(false);

  const current = rows.find((row) => row.id === openId) ?? null;

  const resetDrawerState = useCallback(() => {
    setFormData(initialFormState);
    setFormErrors({});
    setComments([]);
    setActivities([]);
    setCommentsLoading(false);
    setActivityLoading(false);
    setCommentInput('');
    setError(null);
  }, []);

  const loadSupportingData = useCallback(
    async (locationId: string) => {
      setCommentsLoading(true);
      setActivityLoading(true);

      try {
        const { data, error: commentsError } = await supabase
          .from('comments')
          .select('id, body, created_at')
          .eq('organization_id', orgId)
          .eq('entity_type', 'location')
          .eq('entity_id', locationId)
          .order('created_at', { ascending: false })
          .limit(50);

        if (commentsError) {
          throw commentsError;
        }

        setComments(data ?? []);
      } catch (commentsLoadError) {
        setError(
          commentsLoadError instanceof Error ? commentsLoadError.message : 'Failed to load comments',
        );
        setComments([]);
      } finally {
        setCommentsLoading(false);
      }

      try {
        const response = await fetch(`/api/audit/${orgId}/locations/${locationId}`);
        if (!response.ok) {
          throw new Error('Failed to load activity');
        }
        const json = await response.json();
        setActivities((json?.data as LocationActivity[]) ?? []);
      } catch (activityError) {
        setError(activityError instanceof Error ? activityError.message : 'Failed to load activity');
        setActivities([]);
      } finally {
        setActivityLoading(false);
      }
    },
    [orgId, supabase],
  );

  const handleOpen = useCallback(
    (id: string) => {
      const row = rows.find((entry) => entry.id === id);
      if (!row) {
        return;
      }

      setOpenId(id);
      setFormData({
        name: row.name ?? '',
        type: (row.type as LocationType) ?? 'venue',
        address: row.address ?? '',
        city: row.city ?? '',
        state: row.state ?? '',
        country: row.country ?? '',
        capacity: row.capacity != null ? String(row.capacity) : '',
        notes: '',
      });
      setFormErrors({});
      setCommentInput('');
      setError(null);
      void loadSupportingData(id);
    },
    [loadSupportingData, rows],
  );

  const handleClose = useCallback(() => {
    if (saving) {
      return;
    }
    setOpenId(null);
    resetDrawerState();
  }, [resetDrawerState, saving]);

  const validateForm = useCallback((draft: LocationFormData): LocationFormErrors => {
    const nextErrors: LocationFormErrors = {};

    if (!draft.name.trim()) {
      nextErrors.name = 'Name is required';
    }

    return nextErrors;
  }, []);

  const handleInputChange = useCallback(
    (event: ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
      const { id, value } = event.target;
      setFormData((prev) => ({ ...prev, [id]: value }));
    },
    [],
  );

  const handleSelectChange = useCallback((event: ChangeEvent<HTMLSelectElement>) => {
    const { id, value } = event.target;
    setFormData((prev) => ({ ...prev, [id]: value as LocationType }));
  }, []);

  const handleSave = useCallback(
    async (event: FormEvent<HTMLFormElement>) => {
      event.preventDefault();
      if (!openId) {
        return;
      }

      const validation = validateForm(formData);
      setFormErrors(validation);
      if (Object.keys(validation).length > 0) {
        return;
      }

      setSaving(true);
      setError(null);

      try {
        const trimmedCapacity = formData.capacity.trim();
        const capacityNumber = trimmedCapacity ? Number(trimmedCapacity) : null;

        const updateData = {
          name: formData.name.trim(),
          type: formData.type,
          address: formData.address.trim() || null,
          city: formData.city.trim() || null,
          state: formData.state.trim() || null,
          country: formData.country.trim() || null,
          capacity: Number.isFinite(capacityNumber) ? capacityNumber : null,
          notes: formData.notes.trim() || null,
        };

        const { error: updateError } = await supabase
          .from('locations')
          .update(updateData)
          .eq('id', openId)
          .eq('organization_id', orgId);

        if (updateError) {
          throw updateError;
        }

        if (typeof window !== 'undefined' && (window as unknown).posthog) {
          (window as unknown).posthog.capture('location.updated', {
            organization_id: orgId,
            location_id: openId,
          });
        }

        router.refresh();
      } catch (saveError) {
        setError(saveError instanceof Error ? saveError.message : 'Failed to update location');
      } finally {
        setSaving(false);
      }
    },
    [formData, openId, orgId, supabase, validateForm],
  );

  const handleCommentSubmit = useCallback(
    async (event: FormEvent<HTMLFormElement>) => {
      event.preventDefault();
      if (!openId) {
        return;
      }

      const body = commentInput.trim();
      if (!body) {
        return;
      }

      setCommentSaving(true);
      setError(null);

      try {
        const { data, error: insertError } = await supabase
          .from('comments')
          .insert({
            organization_id: orgId,
            entity_type: 'location',
            entity_id: openId,
            body,
          })
          .select('id, body, created_at')
          .single();

        if (insertError) {
          throw insertError;
        }

        if (data) {
          setComments((prev) => [data, ...prev]);
          setCommentInput('');
        }
      } catch (commentError) {
        setError(commentError instanceof Error ? commentError.message : 'Failed to add comment');
      } finally {
        setCommentSaving(false);
      }
    },
    [commentInput, openId, orgId, supabase],
  );

  return (
    <div className="overflow-x-auto">
      <table className="w-full border-collapse text-body-sm">
        <thead>
          <tr className="sticky top-0">
            <th className="border-b p-sm text-left">Location</th>
            <th className="border-b p-sm text-left">Type</th>
            <th className="border-b p-sm text-left">Address</th>
            <th className="border-b p-sm text-left">Capacity</th>
            <th className="border-b p-sm text-left">Project</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((row) => (
            <tr
              key={row.id}
              className="hover:bg-accent/20 cursor-pointer"
              onClick={() => {
                void handleOpen(row.id);
              }}
            >
              <td className="border-b p-sm">
                <div className="flex items-center gap-sm">
                  <MapPin className="w-4 h-4 color-accent" />
                  {row.name}
                </div>
              </td>
              <td className="border-b p-sm">
                <Badge className={getTypeColor(row.type)}>{row.type}</Badge>
              </td>
              <td className="border-b p-sm text-body-sm color-muted">{formatAddress(row)}</td>
              <td className="border-b p-sm">
                {row.capacity ? (
                  <div className="flex items-center gap-xs">
                    <Users className="w-3 h-3" />
                    {row.capacity}
                  </div>
                ) : (
                  '-'
                )}
              </td>
              <td className="border-b p-sm">{row.project?.name || '-'}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <AppDrawer
        open={!!openId}
        onClose={handleClose}
        record={current}
        fields={[]}
        mode="view"
        title={current?.name || 'Location Details'}
        tabs={[
          {
            key: 'details',
            label: 'Details',
            content: current ? (
              <div className="stack-sm text-body-sm">
                {error ? (
                  <div role="alert" className="mb-sm rounded border border-destructive/30 bg-destructive/10 px-sm py-xs color-destructive">
                    {error}
                  </div>
                ) : null}
                <div>
                  <span className="form-label">Name:</span> {current.name}
                </div>
                <div className="flex items-center gap-sm">
                  <span className="form-label">Type:</span>
                  <Badge className={getTypeColor(current.type)}>{current.type}</Badge>
                </div>
                <div>
                  <span className="form-label">Address:</span> {formatAddress(current)}
                </div>
                <div>
                  <span className="form-label">Capacity:</span> {current.capacity ?? 'Not specified'}
                </div>
                <div>
                  <span className="form-label">Project:</span> {current.project?.name || 'No project assigned'}
                </div>
              </div>
            ) : (
              <div />
            ),
          },
          {
            key: 'edit',
            label: 'Edit',
            content: current ? (
              <form onSubmit={handleSave} className="stack-sm" aria-live="polite">
                {error ? (
                  <div role="alert" className="rounded border border-destructive/30 bg-destructive/10 px-sm py-xs text-body-sm color-destructive">
                    {error}
                  </div>
                ) : null}

                <div className="grid gap-xs">
                  <label htmlFor="name" className="text-body-sm form-label">
                    Location Name
                  </label>
                  <input
                    id="name"
                    type="text"
                    className="rounded border px-md py-xs"
                    value={formData.name}
                    onChange={handleInputChange}
                    aria-invalid={formErrors.name ? 'true' : 'false'}
                  />
                  {formErrors.name ? (
                    <div className="text-body-sm color-destructive">{formErrors.name}</div>
                  ) : null}
                </div>

                <div className="grid gap-xs">
                  <label htmlFor="type" className="text-body-sm form-label">
                    Type
                  </label>
                  <select
                    id="type"
                    className="rounded border px-md py-xs"
                    value={formData.type}
                    onChange={handleSelectChange}
                  >
                    <option value="venue">Venue</option>
                    <option value="office">Office</option>
                    <option value="warehouse">Warehouse</option>
                    <option value="studio">Studio</option>
                    <option value="outdoor">Outdoor</option>
                    <option value="virtual">Virtual</option>
                    <option value="other">Other</option>
                  </select>
                </div>

                <div className="grid gap-xs">
                  <label htmlFor="address" className="text-body-sm form-label">
                    Address
                  </label>
                  <input
                    id="address"
                    type="text"
                    className="rounded border px-md py-xs"
                    value={formData.address}
                    onChange={handleInputChange}
                  />
                </div>

                <div className="grid grid-cols-2 gap-sm">
                  <div className="grid gap-xs">
                    <label htmlFor="city" className="text-body-sm form-label">
                      City
                    </label>
                    <input
                      id="city"
                      type="text"
                      className="rounded border px-md py-xs"
                      value={formData.city}
                      onChange={handleInputChange}
                    />
                  </div>

                  <div className="grid gap-xs">
                    <label htmlFor="state" className="text-body-sm form-label">
                      State/Province
                    </label>
                    <input
                      id="state"
                      type="text"
                      className="rounded border px-md py-xs"
                      value={formData.state}
                      onChange={handleInputChange}
                    />
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-sm">
                  <div className="grid gap-xs">
                    <label htmlFor="country" className="text-body-sm form-label">
                      Country
                    </label>
                    <input
                      id="country"
                      type="text"
                      className="rounded border px-md py-xs"
                      value={formData.country}
                      onChange={handleInputChange}
                    />
                  </div>

                  <div className="grid gap-xs">
                    <label htmlFor="capacity" className="text-body-sm form-label">
                      Capacity
                    </label>
                    <input
                      id="capacity"
                      type="number"
                      min="0"
                      className="rounded border px-md py-xs"
                      value={formData.capacity}
                      onChange={handleInputChange}
                    />
                  </div>
                </div>

                <div className="grid gap-xs">
                  <label htmlFor="notes" className="text-body-sm form-label">
                    Notes
                  </label>
                  <textarea
                    id="notes"
                    className="rounded border px-md py-xs"
                    rows={3}
                    value={formData.notes}
                    onChange={handleInputChange}
                  />
                </div>

                <div className="flex items-center justify-between border-t pt-sm">
                  <div className="text-body-sm opacity-70">{saving ? 'Saving changes...' : 'Ready to save'}</div>
                  <Button type="submit" disabled={saving}>
                    {saving ? 'Savingâ€¦' : 'Save Changes'}
                  </Button>
                </div>
              </form>
            ) : (
              <div />
            ),
          },
          {
            key: 'comments',
            label: 'Comments',
            content: current ? (
              <div className="stack-sm">
                <form onSubmit={handleCommentSubmit} className="flex items-start gap-sm">
                  <textarea
                    name="body"
                    className="min-h-16 w-full rounded border p-sm"
                    placeholder="Add a comment..."
                    value={commentInput}
                    onChange={(event) => setCommentInput(event.target.value)}
                    disabled={commentSaving}
                  />
                  <Button type="submit" disabled={commentSaving || !commentInput.trim()}>
                    {commentSaving ? 'Postingâ€¦' : 'Post'}
                  </Button>
                </form>

                {commentsLoading ? (
                  <div className="text-body-sm opacity-70">Loading comments...</div>
                ) : comments.length === 0 ? (
                  <div className="text-body-sm color-muted">No comments yet.</div>
                ) : (
                  <ul className="stack-sm">
                    {comments.map((comment) => (
                      <li key={comment.id} className="rounded border p-sm">
                        <div className="text-body-sm whitespace-pre-wrap">{comment.body}</div>
                        <div className="text-body-xs color-muted">
                          {new Date(comment.created_at).toLocaleString()}
                        </div>
                      </li>
                    ))}
                  </ul>
                )}
              </div>
            ) : (
              <div className="text-body-sm color-muted">Select a location to view comments.</div>
            ),
          },
          {
            key: 'activity',
            label: 'Activity',
            content: current ? (
              <div className="stack-sm text-body-sm">
                {activityLoading ? (
                  'Loading activity...'
                ) : activities.length === 0 ? (
                  <div className="text-body-sm color-muted">No activity yet.</div>
                ) : (
                  <ul className="stack-xs">
                    {activities.map((activity, index) => (
                      <li key={`${activity.occurred_at}-${index}`} className="flex items-center justify-between gap-md">
                        <div className="font-mono text-body-xs opacity-70">
                          {new Date(activity.occurred_at).toLocaleString()}
                        </div>
                        <div className="flex-1">{activity.action}</div>
                      </li>
                    ))}
                  </ul>
                )}
              </div>
            ) : (
              <div className="text-body-sm color-muted">Select a location to view activity.</div>
            ),
          },
        ]}
      />
    </div>
  );
}

"use client";

