'use client';


import { BarChart3, Calendar, Clock, DollarSign, TrendingUp, Users, AlertTriangle, CheckCircle, Plus, ArrowRight } from "lucide-react";
import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { Card, Badge, Button } from '@ghxstship/ui';
import { tryCatch, Result, reportError } from '@ghxstship/ui/utils/error-handling';

interface ProjectStats {
  totalProjects: number;
  activeProjects: number;
  completedProjects: number;
  totalBudget: number;
  totalTasks: number;
  completedTasks: number;
  overdueTasks: number;
  totalRisks: number;
  highRisks: number;
}

interface RecentProject {
  id: string;
  name: string;
  status: string;
  budget: number;
  created_at: string;
  tasks_count?: number;
  completion_percentage?: number;
}

export default function ProjectsOverviewClient({ orgId }: { orgId: string }) {
  const t = useTranslations('projects');
  const router = useRouter();
  const [loading, setLoading] = useState(true);
  const [seedLoading, setSeedLoading] = useState(false);
  const [message, setMessage] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [stats, setStats] = useState<ProjectStats | null>(null);
  const [recentProjects, setRecentProjects] = useState<RecentProject[]>([]);
  const sb = createBrowserClient();

  useEffect(() => {
    loadOverviewData();
  }, [orgId]);

  async function loadOverviewData() {
    setLoading(true);
    try {
      // Load projects
      const { data: projects } = await sb
        .from('projects')
        .select('*')
        .eq('organization_id', orgId);

      // Load tasks
      const { data: tasks } = await sb
        .from('tasks')
        .select('*')
        .eq('organization_id', orgId);

      // Load risks
      const { data: risks } = await sb
        .from('risks')
        .select('*')
        .eq('organization_id', orgId);

      if (projects) {
        const totalBudget = projects.reduce((sum, p) => sum + (p.budget || 0), 0);
        const activeProjects = projects.filter(p => p.status === 'active').length;
        const completedProjects = projects.filter(p => p.status === 'completed').length;

        const completedTasks = tasks?.filter(t => t.status === 'done').length || 0;
        const overdueTasks = tasks?.filter(t => 
          t.due_at && new Date(t.due_at) < new Date() && t.status !== 'done'
        ).length || 0;

        const highRisks = risks?.filter(r => 
          r.impact === 'high' || r.impact === 'very_high'
        ).length || 0;

        setStats({
          totalProjects: projects.length,
          activeProjects,
          completedProjects,
          totalBudget,
          totalTasks: tasks?.length || 0,
          completedTasks,
          overdueTasks,
          totalRisks: risks?.length || 0,
          highRisks
        });

        // Set recent projects (last 5)
        setRecentProjects(
          projects
            .sort((a, b) => new Date(b.created_at).getTime() - new Date(a.created_at).getTime())
            .slice(0, 5)
        );
      }
    } catch (error) {
      console.error('Error loading overview data:', error);
      setError('Failed to load overview data');
    } finally {
      setLoading(false);
    }
  }

  async function onSeedDemo() {
    setSeedLoading(true);
    setMessage(null);
    setError(null);
    try {
      const resResult = await tryCatch(async () => fetch(`/api/organizations/${orgId}/demo`, { method: 'POST' }));
if (!resResult.success) {
  reportError(resResult.error);
  throw new Error(resResult.error.message);
}
const res = resResult.data;
      const json = await res.json();
      if (!res.ok) throw new Error(json?.error || 'Request failed');
      setMessage(t('empty.seeded'));
      if (typeof window !== 'undefined' && (window as unknown).posthog) {
        (window as unknown).posthog.capture('demo.seeded', { organization_id: orgId });
      }
      await loadOverviewData(); // Refresh data after seeding
      router.refresh();
    } catch (e) {
      setError(e?.message || t('empty.error'));
    } finally {
      setSeedLoading(false);
    }
  }

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'bg-success/10 color-success';
      case 'completed': return 'bg-accent/10 color-accent';
      case 'on_hold': return 'bg-warning/10 color-warning';
      case 'cancelled': return 'bg-destructive/10 color-destructive';
      default: return 'bg-secondary/50 color-muted';
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  if (loading) {
    return (
      <div className="stack-lg">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-md">
          {[...Array(4)].map((_, i) => (
            <Card key={i} className="animate-pulse">
              <div className="h-20 bg-secondary rounded"></div>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  if (!stats || stats.totalProjects === 0) {
    return (
      <div className="text-center py-2xl">
        <div className="mx-auto w-24 h-24 bg-secondary/50 rounded-full flex items-center justify-center mb-md">
          <BarChart3 className="w-12 h-12 color-muted" />
        </div>
        <h3 className="text-body text-heading-4 mb-sm">No Projects Yet</h3>
        <p className="color-muted mb-lg">Get started by creating your first project or seeding demo data.</p>
        <div className="flex items-center justify-center gap-md">
          <Button onClick={() => router.push('/projects?create=true')}>
            <Plus className="w-4 h-4 mr-sm" />
            Create Project
          </Button>
          <Button 
            variant="outline" 
            onClick={onSeedDemo}
            disabled={seedLoading}
          >
            {seedLoading ? 'Seeding...' : 'Seed Demo Data'}
          </Button>
        </div>
        {message && (
          <div className="mt-md color-success text-body-sm">{message}</div>
        )}
        {error && (
          <div className="mt-md color-destructive text-body-sm">{error}</div>
        )}
      </div>
    );
  }

  return (
    <div className="stack-lg">
      {/* Key Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-md">
        <Card className="p-lg">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-muted">Total Projects</p>
              <p className="text-heading-3">{stats.totalProjects}</p>
            </div>
            <BarChart3 className="w-8 h-8 color-accent" />
          </div>
          <div className="mt-sm flex items-center text-body-sm">
            <span className="color-success">{stats.activeProjects} active</span>
            <span className="mx-sm">â€¢</span>
            <span className="color-muted">{stats.completedProjects} completed</span>
          </div>
        </Card>

        <Card className="p-lg">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-muted">Total Budget</p>
              <p className="text-heading-3">{formatCurrency(stats.totalBudget)}</p>
            </div>
            <DollarSign className="w-8 h-8 color-success" />
          </div>
          <div className="mt-sm text-body-sm color-muted">
            Across all projects
          </div>
        </Card>

        <Card className="p-lg">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-muted">Tasks Progress</p>
              <p className="text-heading-3">
                {stats.totalTasks > 0 ? Math.round((stats.completedTasks / stats.totalTasks) * 100) : 0}%
              </p>
            </div>
            <CheckCircle className="w-8 h-8 color-accent" />
          </div>
          <div className="mt-sm flex items-center text-body-sm">
            <span className="color-success">{stats.completedTasks} done</span>
            <span className="mx-sm">â€¢</span>
            <span className="color-destructive">{stats.overdueTasks} overdue</span>
          </div>
        </Card>

        <Card className="p-lg">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-muted">Risk Status</p>
              <p className="text-heading-3">{stats.totalRisks}</p>
            </div>
            <AlertTriangle className="w-8 h-8 color-warning" />
          </div>
          <div className="mt-sm text-body-sm">
            <span className="color-destructive">{stats.highRisks} high priority</span>
          </div>
        </Card>
      </div>

      {/* Recent Projects */}
      <Card className="p-lg">
        <div className="flex items-center justify-between mb-md">
          <h3 className="text-body text-heading-4">Recent Projects</h3>
          <Button 
            variant="outline" 

            onClick={() => router.push('/projects')}
