const statusColors = {
  planning: 'blue',
  active: 'green',
  on_hold: 'yellow',
  completed: 'gray',
  cancelled: 'red',
} as const;

const priorityColors = {
  low: 'green',
  medium: 'yellow',
  high: 'orange',
  critical: 'red',
} as const;

export default function ProjectGridView({
