  );
}

"use client";

import React from 'react';
import { Badge, Button, Checkbox } from '@ghxstship/ui';
import { format } from 'date-fns';
import type { Project, FieldConfig } from '../types';

interface ProjectListViewProps {
  projects: Project[];
  selectedProjects: Set<string>;
  fieldVisibility: FieldConfig[];
  sortField: string;
  sortDirection: 'asc' | 'desc';
  onSelectAll: () => void;
  onSelectProject: (id: string) => void;
  onSort: (field: string) => void;
  onView: (project: Project) => void;
  onEdit: (project: Project) => void;
  onDelete: (project: Project) => void;
}

const statusColors = {
  planning: 'blue',
  active: 'green',
  on_hold: 'yellow',
  completed: 'gray',
  cancelled: 'red',
} as const;

const priorityColors = {
  low: 'green',
  medium: 'yellow',
  high: 'orange',
  critical: 'red',
} as const;

export default function ProjectListView({
  projects,
  selectedProjects,
  fieldVisibility,
  sortField,
  sortDirection,
  onSelectAll,
  onSelectProject,
  onSort,
  onView,
  onEdit,
  onDelete,
}: ProjectListViewProps) {
  const visibleFields = fieldVisibility.filter(field => field.visible);

  const renderCellContent = (project: Project, field: FieldConfig) => {
    switch (field.key) {
      case 'name':
        return (
          <div className="min-w-0">
            <div className="font-medium text-sm truncate">{project.name}</div>
            {project.description && (
              <div className="text-xs text-muted-foreground truncate mt-1">
                {project.description}
              </div>
            )}
          </div>
        );

      case 'status':
        return (
          <Badge 
            variant="outline" 
            className={`bg-${statusColors[project.status]}-50 text-${statusColors[project.status]}-700 border-${statusColors[project.status]}-200`}
          >
            {project.status.replace('_', ' ')}
          </Badge>
        );

      case 'priority':
        return (
          <Badge 
            variant="outline" 
            className={`bg-${priorityColors[project.priority]}-50 text-${priorityColors[project.priority]}-700 border-${priorityColors[project.priority]}-200`}
          >
            {project.priority}
          </Badge>
        );

      case 'budget':
        return project.budget ? (
          <div className="flex items-center gap-xs text-sm">
            <DollarSign className="h-3 w-3" />
            {new Intl.NumberFormat('en-US', {
              style: 'currency',
              currency: project.currency || 'USD',
              notation: 'compact',
            }).format(project.budget)}
          </div>
        ) : (
          <span className="text-muted-foreground">-</span>
        );

      case 'startsAt':
        return project.starts_at ? (
          <div className="flex items-center gap-xs text-sm">
            <Calendar className="h-3 w-3" />
            {format(new Date(project.starts_at), 'MMM dd, yyyy')}
          </div>
        ) : (
          <span className="text-muted-foreground">-</span>
        );

      case 'endsAt':
        return project.ends_at ? (
          <div className="flex items-center gap-xs text-sm">
            <Calendar className="h-3 w-3" />
            {format(new Date(project.ends_at), 'MMM dd, yyyy')}
          </div>
        ) : (
          <span className="text-muted-foreground">-</span>
        );

      case 'location':
        return project.location ? (
          <div className="flex items-center gap-xs text-sm">
            <MapPin className="h-3 w-3" />
            <span className="truncate">{project.location}</span>
          </div>
        ) : (
          <span className="text-muted-foreground">-</span>
        );

      case 'created_at':
        return (
          <div className="text-sm text-muted-foreground">
            {format(new Date(project.created_at), 'MMM dd, yyyy')}
          </div>
        );

      case 'tags':
        return project.tags && project.tags.length > 0 ? (
          <div className="flex flex-wrap gap-xs">
            {project.tags.slice(0, 2).map((tag) => (
              <Badge key={tag} variant="secondary" className="text-xs">
                {tag}
              </Badge>
            ))}
            {project.tags.length > 2 && (
              <Badge variant="secondary" className="text-xs">
                +{project.tags.length - 2}
              </Badge>
            )}
          </div>
        ) : (
          <span className="text-muted-foreground">-</span>
        );

      default:
        return <span className="text-muted-foreground">-</span>;
    }
  };

  return (
    <div className="border rounded-lg overflow-hidden">
      {/* Table Header */}
      <div className="bg-muted/50 border-b">
        <div className="flex items-center px-md py-sm">
          <div className="flex items-center gap-sm">
            <Checkbox
              checked={selectedProjects.size === projects.length && projects.length > 0}
              indeterminate={selectedProjects.size > 0 && selectedProjects.size < projects.length}
              onCheckedChange={onSelectAll}
            />
            <span className="text-sm font-medium">
              {selectedProjects.size > 0 ? `${selectedProjects.size} selected` : 'Select all'}
            </span>
          </div>
        </div>
      </div>

      {/* Column Headers */}
      <div className="bg-muted/30 border-b">
        <div className="grid gap-md px-md py-sm" style={{
          gridTemplateColumns: `40px ${visibleFields.map(() => '1fr').join(' ')} 120px`
        }}>
          <div></div> {/* Checkbox column */}

          {visibleFields.map((field) => (
            <div key={field.key} className="flex items-center gap-xs">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => field.sortable && onSort(field.key)}
                className="h-auto p-0 font-medium text-sm hover:bg-transparent"
                disabled={!field.sortable}
              >
                {field.label}
                {field.sortable && sortField === field.key && (
                  <ArrowUpDown className={`ml-xs h-3 w-3 ${
                    sortDirection === 'asc' ? 'rotate-180' : ''
                  }`} />
                )}
              </Button>
            </div>
          ))}

          <div className="text-sm font-medium">Actions</div>
        </div>
      </div>

      {/* Table Body */}
      <div className="divide-y">
        {projects.map((project) => (
          <div
            key={project.id}
            className={`grid gap-md px-md py-sm hover:bg-muted/30 transition-colors ${
              selectedProjects.has(project.id) ? 'bg-primary/5' : ''
            }`}
            style={{
              gridTemplateColumns: `40px ${visibleFields.map(() => '1fr').join(' ')} 120px`
            }}
          >
            {/* Checkbox */}
            <div className="flex items-center">
              <Checkbox
                checked={selectedProjects.has(project.id)}
                onCheckedChange={() => onSelectProject(project.id)}
              />
            </div>

            {/* Field Columns */}
            {visibleFields.map((field) => (
              <div key={field.key} className="flex items-center min-w-0">
                {renderCellContent(project, field)}
              </div>
            ))}

            {/* Actions */}
            <div className="flex items-center gap-xs">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => onView(project)}
                className="h-8 w-8 p-0"
                title="View project"
              >
                <Eye className="h-3 w-3" />
              </Button>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => onEdit(project)}
                className="h-8 w-8 p-0"
                title="Edit project"
              >
                <Edit className="h-3 w-3" />
              </Button>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => onDelete(project)}
                className="h-8 w-8 p-0 text-destructive hover:text-destructive"
                title="Delete project"
              >
                <Trash2 className="h-3 w-3" />
              </Button>
            </div>
          </div>
        ))}
      </div>

      {/* Empty State */}
      {projects.length === 0 && (
        <div className="text-center py-xl">
          <div className="text-muted-foreground">
            <div className="text-lg font-medium mb-sm">No projects found</div>
            <div className="text-sm">Try adjusting your search or filter criteria</div>
          </div>
        </div>
      )}
    </div>
  );
}

"use client";

import { Card, Badge, Button, Checkbox } from "@ghxstship/ui";
import type { Risk } from "../RisksClient";
import type { LucideIcon } from "lucide-react";

interface RiskGridViewProps {
  risks: Risk[];
  selectedRisks: Set<string>;
  onSelectRisk: (id: string) => void;
  onView: (risk: Risk) => void;
  onEdit: (risk: Risk) => void;
  onDelete: (risk: Risk) => void;
  onDuplicate: (risk: Risk) => void;
  getCategoryIcon: (category: string) => LucideIcon;
  getRiskLevelColor: (score: number) => string;
  getRiskLevelBadgeVariant: (score: number) => any;
  getStatusBadgeVariant: (status: string) => any;
  getCategoryBadgeVariant: (category: string) => any;
}

export default function RiskGridView({
  risks,
  selectedRisks,
  onSelectRisk,
  onView,
  onEdit,
  onDelete,
  onDuplicate,
  getCategoryIcon,
  getRiskLevelColor,
  getRiskLevelBadgeVariant,
  getStatusBadgeVariant,
  getCategoryBadgeVariant,
}: RiskGridViewProps) {
  const getRiskLevel = (score: number) => {
    if (score >= 20) return "Critical";
    if (score >= 12) return "High";
    if (score >= 6) return "Medium";
    return "Low";
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
      {risks.map((risk) => {
        const CategoryIcon = getCategoryIcon(risk.category);
        const riskLevel = getRiskLevel(risk.risk_score);

        return (
          <Card
            key={risk.id}
            className={`p-md cursor-pointer hover:shadow-lg transition-shadow ${
              selectedRisks.has(risk.id) ? "ring-2 ring-primary" : ""
            }`}
            onClick={() => onView(risk)}
          >
            <div className="flex items-start justify-between mb-sm">
              <Checkbox
                checked={selectedRisks.has(risk.id)}
                onChange={() => onSelectRisk(risk.id)}
                onClick={(e: React.MouseEvent) => e.stopPropagation()}
              />
              <Button
                variant="ghost"
                size="sm"
                onClick={(e) => {
                  e.stopPropagation();
                  onEdit(risk);
                }}
              >
                <Edit className="h-4 w-4" />
              </Button>
            </div>

            <div className="space-y-sm">
              {/* Risk Score and Level */}
              <div className="flex items-center justify-between">
                <div className={`text-3xl font-bold ${getRiskLevelColor(risk.risk_score)}`}>
                  {risk.risk_score}
                </div>
                <Badge variant={getRiskLevelBadgeVariant(risk.risk_score)}>
                  {riskLevel}
                </Badge>
              </div>

              {/* Title */}
              <h3 className="font-semibold line-clamp-2">{risk.title}</h3>

              {/* Category and Status */}
              <div className="flex items-center gap-xs flex-wrap">
                <Badge variant={getCategoryBadgeVariant(risk.category)}>
                  <CategoryIcon className="h-3 w-3 mr-xs" />
                  {risk.category}
                </Badge>
                <Badge variant={getStatusBadgeVariant(risk.status)}>
                  {risk.status}
                </Badge>
              </div>

              {/* Probability and Impact */}
              <div className="grid grid-cols-2 gap-xs text-sm">
                <div>
                  <span className="text-muted-foreground">Probability:</span>
                  <span className="ml-xs font-medium">{risk.probability.replace("_", " ")}</span>
                </div>
                <div>
                  <span className="text-muted-foreground">Impact:</span>
                  <span className="ml-xs font-medium">{risk.impact.replace("_", " ")}</span>
                </div>
              </div>

              {/* Description */}
              <p className="text-sm text-muted-foreground line-clamp-2">
                {risk.description}
              </p>

              {/* Owner */}
              {risk.owner && (
                <div className="flex items-center gap-xs text-sm text-muted-foreground">
                  <User className="h-3 w-3" />
                  <span className="truncate">
                    {risk.owner.full_name || risk.owner.email}
                  </span>
                </div>
              )}

              {/* Project */}
              {risk.project && (
                <div className="text-sm text-muted-foreground truncate">
                  Project: {risk.project.name}
                </div>
              )}

              {/* Review Date */}
              {risk.review_date && (
                <div className="flex items-center gap-xs text-sm text-muted-foreground">
                  <Clock className="h-3 w-3" />
                  <span>Review: {new Date(risk.review_date).toLocaleDateString()}</span>
                </div>
              )}

              {/* Mitigation Status */}
              {risk.mitigation_plan && (
                <div className="flex items-center gap-xs text-sm text-success">
                  <Shield className="h-3 w-3" />
                  <span>Mitigation plan in place</span>
                </div>
              )}

              {/* Actions */}
              <div className="flex items-center gap-xs pt-sm border-t">
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={(e) => {
                    e.stopPropagation();
                    onView(risk);
                  }}
                >
                  <Eye className="h-4 w-4" />
                </Button>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={(e) => {
                    e.stopPropagation();
                    onDuplicate(risk);
                  }}
                >
                  <Copy className="h-4 w-4" />
                </Button>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={(e) => {
                    e.stopPropagation();
                    onDelete(risk);
                  }}
                >
                  <Trash2 className="h-4 w-4 text-destructive" />
                </Button>
              </div>
            </div>
          </Card>
        );
      })}
    </div>
  );
}

"use client";

import { Badge, Button, Checkbox } from "@ghxstship/ui";
import type { Risk } from "../RisksClient";

interface RiskListViewProps {
  risks: Risk[];
  selectedRisks: Set<string>;
  fieldVisibility: Array<{ id: string; label: string; visible: boolean; sortable: boolean }>;
  sortField: string;
  sortDirection: "asc" | "desc";
  onSelectAll: () => void;
  onSelectRisk: (id: string) => void;
  onSort: (field: string) => void;
  onView: (risk: Risk) => void;
  onEdit: (risk: Risk) => void;
  onDelete: (risk: Risk) => void;
  onDuplicate: (risk: Risk) => void;
  getRiskLevelBadgeVariant: (score: number) => any;
  getStatusBadgeVariant: (status: string) => any;
  getCategoryBadgeVariant: (category: string) => any;
}

export default function RiskListView({
  risks,
  selectedRisks,
  fieldVisibility,
  sortField,
  sortDirection,
  onSelectAll,
  onSelectRisk,
  onSort,
  onView,
  onEdit,
  onDelete,
  onDuplicate,
  getRiskLevelBadgeVariant,
  getStatusBadgeVariant,
  getCategoryBadgeVariant,
}: RiskListViewProps) {
  const visibleFields = fieldVisibility.filter((f) => f.visible);

  const getRiskLevel = (score: number) => {
    if (score >= 20) return "Critical";
    if (score >= 12) return "High";
    if (score >= 6) return "Medium";
    return "Low";
  };

  const renderFieldValue = (risk: Risk, fieldId: string) => {
    switch (fieldId) {
      case "title":
        return <span className="font-medium">{risk.title}</span>;
      case "category":
        return (
          <Badge variant={getCategoryBadgeVariant(risk.category)}>
            {risk.category}
          </Badge>
        );
      case "probability":
        return risk.probability.replace("_", " ");
      case "impact":
        return risk.impact.replace("_", " ");
      case "risk_score":
        return (
          <div className="flex items-center gap-xs">
            <span className="font-bold">{risk.risk_score}</span>
            <Badge variant={getRiskLevelBadgeVariant(risk.risk_score)}>
              {getRiskLevel(risk.risk_score)}
            </Badge>
          </div>
        );
      case "status":
        return (
          <Badge variant={getStatusBadgeVariant(risk.status)}>
            {risk.status}
          </Badge>
        );
      case "owner":
        return risk.owner?.full_name || risk.owner?.email || "-";
      case "project":
        return risk.project?.name || "-";
      case "identified_date":
        return new Date(risk.identified_date).toLocaleDateString();
      case "review_date":
        return risk.review_date ? new Date(risk.review_date).toLocaleDateString() : "-";
      case "created_at":
        return new Date(risk.created_at).toLocaleDateString();
      default:
        return "-";
    }
  };

  return (
    <div className="overflow-x-auto">
      <table className="w-full">
        <thead>
          <tr className="border-b">
            <th className="text-left p-sm">
              <Checkbox
                checked={selectedRisks.size === risks.length && risks.length > 0}
                onChange={onSelectAll}
              />
            </th>
            {visibleFields.map((field) => (
              <th
                key={field.id}
                className={`text-left p-sm ${
                  field.sortable ? "cursor-pointer hover:bg-muted/50" : ""
                }`}
                onClick={() => field.sortable && onSort(field.id)}
              >
                <div className="flex items-center gap-xs">
                  {field.label}
                  {field.sortable && (
                    <>
                      {sortField === field.id ? (
                        sortDirection === "asc" ? (
                          <ArrowUp className="h-3 w-3" />
                        ) : (
                          <ArrowDown className="h-3 w-3" />
                        )
                      ) : (
                        <ArrowUpDown className="h-3 w-3 opacity-30" />
                      )}
                    </>
                  )}
                </div>
              </th>
            ))}
            <th className="text-left p-sm">Actions</th>
          </tr>
        </thead>
        <tbody>
          {risks.map((risk) => (
            <tr
              key={risk.id}
              className={`border-b hover:bg-muted/50 cursor-pointer ${
                selectedRisks.has(risk.id) ? "bg-primary/5" : ""
              }`}
              onClick={() => onView(risk)}
            >
              <td className="p-sm" onClick={(e) => e.stopPropagation()}>
                <Checkbox
                  checked={selectedRisks.has(risk.id)}
                  onChange={() => onSelectRisk(risk.id)}
                />
              </td>
              {visibleFields.map((field) => (
                <td key={field.id} className="p-sm">
                  {renderFieldValue(risk, field.id)}
                </td>
              ))}
              <td className="p-sm" onClick={(e) => e.stopPropagation()}>
                <div className="flex items-center gap-xs">
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onView(risk)}
                  >
                    <Eye className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onEdit(risk)}
                  >
                    <Edit className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onDuplicate(risk)}
                  >
                    <Copy className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onDelete(risk)}
                    className="text-destructive"
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {risks.length === 0 && (
        <div className="text-center py-xl text-muted-foreground">
          No risks to display
        </div>
      )}
    </div>
  );
}

'use client';

import { Calendar, Clock, CheckCircle, AlertCircle, BarChart3, ChevronLeft, ChevronRight, Filter } from "lucide-react";
import { useCallback, useMemo, useState, type ChangeEvent } from 'react';
import { Badge, Button } from '@ghxstship/ui';
import AppDrawer from '@/app/_components/shared/AppDrawer';

export interface ScheduleProject {
  id: string;
  name: string;
  status: string;
  starts_at: string | null;
  ends_at: string | null;
  budget: number;
}

export interface ScheduleMilestone {
  id: string;
  title: string;
  due_at: string;
  status: string;
  project_id: string;
  project?: { name: string };
}

export interface ScheduleTask {
  id: string;
  title: string;
  status: string;
  due_at: string | null;
  project_id: string;
  project?: { name: string };
}

export interface ScheduleData {
  projects: ScheduleProject[];
  milestones: ScheduleMilestone[];
  tasks: ScheduleTask[];
}

type ViewMode = 'timeline' | 'calendar' | 'list';

type DrawerSelection =
  | { type: 'project'; id: string }
  | { type: 'milestone'; id: string }
  | { type: 'task'; id: string }
  | null;

type ScheduleDrawerPayload =
  | {
      type: 'project';
      project: ScheduleProject;
      milestones: ScheduleMilestone[];
      tasks: ScheduleTask[];
    }
  | {
      type: 'milestone';
      milestone: ScheduleMilestone;
      project: ScheduleProject | undefined;
      relatedTasks: ScheduleTask[];
    }
  | {
      type: 'task';
      task: ScheduleTask;
      project: ScheduleProject | undefined;
    }
  | null;

interface UpcomingItem {
  id: string;
  type: 'milestone' | 'task';
  title: string;
  status: string;
  date: string;
  projectName?: string;
}

const getStatusColor = (status: string) => {
  switch (status) {
    case 'active':
    case 'in_progress':
    case 'pending':
      return 'bg-accent/10 color-accent';
    case 'completed':
    case 'done':
      return 'bg-success/10 color-success';
    case 'on_hold':
    case 'blocked':
      return 'bg-warning/10 color-warning';
    case 'cancelled':
      return 'bg-destructive/10 color-destructive';
    default:
      return 'bg-secondary/50 color-muted';
  }
};

const formatCurrency = (amount: number) =>
  new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: 0,
    maximumFractionDigits: 0,
  }).format(amount);

const isOverdue = (date: string | null | undefined) => {
  if (!date) {
    return false;
  }
  const due = new Date(date);
  const now = new Date();
  return due < now;
};

const toDateLabel = (value: string | null | undefined) =>
  value ? new Date(value).toLocaleDateString() : 'Not scheduled';

export default function ScheduleClient({ data }: { data: ScheduleData }) {
  const [view, setView] = useState<ViewMode>('timeline');
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [selectedProject, setSelectedProject] = useState<string>('all');
  const [selection, setSelection] = useState<DrawerSelection>(null);

  const filteredData = useMemo(() => {
    if (selectedProject === 'all') {
      return data;
    }

    return {
      projects: data.projects.filter((project) => project.id === selectedProject),
      milestones: data.milestones.filter((milestone) => milestone.project_id === selectedProject),
      tasks: data.tasks.filter((task) => task.project_id === selectedProject),
    } satisfies ScheduleData;
  }, [data, selectedProject]);

  const upcomingItems = useMemo<UpcomingItem[]>(() => {
    const items: UpcomingItem[] = [];

    filteredData.milestones.forEach((milestone) => {
      items.push({
        id: milestone.id,
        type: 'milestone',
        title: milestone.title,
        status: milestone.status,
        date: milestone.due_at,
        projectName: milestone.project?.name ?? data.projects.find((project) => project.id === milestone.project_id)?.name,
      });
    });

    filteredData.tasks.forEach((task) => {
      if (!task.due_at) {
        return;
      }
      items.push({
        id: task.id,
        type: 'task',
        title: task.title,
        status: task.status,
        date: task.due_at,
        projectName: task.project?.name ?? data.projects.find((project) => project.id === task.project_id)?.name,
      });
    });

    return items
      .filter((item) => Boolean(item.date))
      .sort((a, b) => new Date(a.date).getTime() - new Date(b.date).getTime())
      .slice(0, 10);
  }, [data.projects, filteredData]);

  const calendarCells = useMemo(() => {
    const firstOfMonth = new Date(currentMonth.getFullYear(), currentMonth.getMonth(), 1);
    const start = new Date(firstOfMonth);
    start.setDate(start.getDate() - firstOfMonth.getDay());

    return Array.from({ length: 35 }, (_, index) => {
      const cellDate = new Date(start);
      cellDate.setDate(start.getDate() + index);

      const items = upcomingItems.filter((item) => {
        const itemDate = new Date(item.date);
        return itemDate.toDateString() === cellDate.toDateString();
      });

      return { date: cellDate, items };
    });
  }, [currentMonth, upcomingItems]);

  const drawerPayload = useMemo<ScheduleDrawerPayload>(() => {
    if (!selection) {
      return null;
    }

    if (selection.type === 'project') {
      const project = data.projects.find((candidate) => candidate.id === selection.id);
      if (!project) {
        return null;
      }

      const milestones = data.milestones.filter((milestone) => milestone.project_id === project.id);
      const tasks = data.tasks.filter((task) => task.project_id === project.id);

      return { type: 'project', project, milestones, tasks };
    }

    if (selection.type === 'milestone') {
      const milestone = data.milestones.find((candidate) => candidate.id === selection.id);
      if (!milestone) {
        return null;
      }

      const project = data.projects.find((candidate) => candidate.id === milestone.project_id);
      const relatedTasks = data.tasks.filter((task) => task.project_id === milestone.project_id);

      return { type: 'milestone', milestone, project, relatedTasks };
    }

    const task = data.tasks.find((candidate) => candidate.id === selection.id);
    if (!task) {
      return null;
    }

    const project = data.projects.find((candidate) => candidate.id === task.project_id);

    return { type: 'task', task, project };
  }, [data.milestones, data.projects, data.tasks, selection]);

  const handleSelectProject = useCallback((event: ChangeEvent<HTMLSelectElement>) => {
    setSelectedProject(event.target.value);
  }, []);

  const handleViewChange = useCallback((mode: ViewMode) => {
    setView(mode);
  }, []);

  const handlePrevMonth = useCallback(() => {
    setCurrentMonth((previous) => new Date(previous.getFullYear(), previous.getMonth() - 1, 1));
  }, []);

  const handleNextMonth = useCallback(() => {
    setCurrentMonth((previous) => new Date(previous.getFullYear(), previous.getMonth() + 1, 1));
  }, []);

  const handleToday = useCallback(() => {
    setCurrentMonth(new Date());
  }, []);

  const handleOpenProject = useCallback((projectId: string) => {
    setSelection({ type: 'project', id: projectId });
  }, []);

  const handleOpenMilestone = useCallback((milestoneId: string) => {
    setSelection({ type: 'milestone', id: milestoneId });
  }, []);

  const handleOpenTask = useCallback((taskId: string) => {
    setSelection({ type: 'task', id: taskId });
  }, []);

  const handleDrawerClose = useCallback(() => {
    setSelection(null);
  }, []);

  return (
    <div className="stack-lg">
      <div className="flex items-center justify-between gap-md">
        <div className="flex items-center gap-sm">
          <Filter className="w-4 h-4 color-muted" />
          <select
            value={selectedProject}
            onChange={handleSelectProject}
            className="rounded border px-md py-xs text-body-sm"
          >
            <option value="all">All Projects</option>
            {data.projects.map((project) => (
              <option key={project.id} value={project.id}>
                {project.name}
              </option>
            ))}
          </select>
        </div>

        <div className="flex items-center gap-sm">
          <Button variant={view === 'timeline' ? 'primary' : 'outline'} onClick={() => handleViewChange('timeline')}>
            <BarChart3 className="w-4 h-4 mr-xs" />
            Timeline
          </Button>
          <Button variant={view === 'calendar' ? 'primary' : 'outline'} onClick={() => handleViewChange('calendar')}>
            <Calendar className="w-4 h-4 mr-xs" />
            Calendar
          </Button>
          <Button variant={view === 'list' ? 'primary' : 'outline'} onClick={() => handleViewChange('list')}>
            <Clock className="w-4 h-4 mr-xs" />
            List
          </Button>
        </div>
      </div>

      {view === 'timeline' && (
        <div className="stack-md">
          <h3 className="text-body text-heading-4">Project Timeline</h3>
          <div className="stack-sm">
            {filteredData.projects.map((project) => (
              <div key={project.id} className="border rounded-lg p-md">
                <div className="flex items-center justify-between mb-sm">
                  <div className="flex items-center gap-sm">
                    <h4 className="form-label">{project.name}</h4>
                    <Badge className={getStatusColor(project.status)}>{project.status}</Badge>
                  </div>
                  <div className="text-body-sm color-muted">{formatCurrency(project.budget)}</div>
                </div>

                {project.starts_at && project.ends_at && (
                  <div className="flex flex-wrap items-center gap-md text-body-sm color-muted mb-sm">
                    <span>Start: {toDateLabel(project.starts_at)}</span>
                    <span>End: {toDateLabel(project.ends_at)}</span>
                    <span>
                      Duration:{' '}
                      {Math.ceil(
                        (new Date(project.ends_at).getTime() - new Date(project.starts_at).getTime()) /
                          (1000 * 60 * 60 * 24),
                      )}{' '}
                      days
                    </span>
                  </div>
                )}

                <div className="stack-sm">
                  {filteredData.milestones
                    .filter((milestone) => milestone.project_id === project.id)
                    .map((milestone) => (
                      <button
                        key={milestone.id}
                        type="button"
                        onClick={() => handleOpenMilestone(milestone.id)}
                        className="flex items-center gap-sm p-sm bg-secondary/50 rounded text-left hover:bg-secondary/70"
                      >
                        <CheckCircle className="w-4 h-4 color-accent" />
                        <span className="flex-1">{milestone.title}</span>
                        <Badge className={getStatusColor(milestone.status)}>{milestone.status}</Badge>
                        <span className={`text-body-sm ${isOverdue(milestone.due_at) ? 'color-destructive' : 'color-muted'}`}>
                          {toDateLabel(milestone.due_at)}
                        </span>
                      </button>
                    ))}
                </div>

                <div className="mt-sm flex justify-end">
                  <Button variant="ghost" size="sm" onClick={() => handleOpenProject(project.id)}>
                    View project details
                  </Button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {view === 'calendar' && (
        <div className="stack-md">
          <div className="flex items-center justify-between">
            <h3 className="text-body text-heading-4">
              {currentMonth.toLocaleDateString('en-US', { month: 'long', year: 'numeric' })}
            </h3>
            <div className="flex items-center gap-sm">
              <Button variant="outline" onClick={handlePrevMonth}>
                <ChevronLeft className="w-4 h-4" />
              </Button>
              <Button variant="outline" onClick={handleToday}>
                Today
              </Button>
              <Button variant="outline" onClick={handleNextMonth}>
                <ChevronRight className="w-4 h-4" />
              </Button>
            </div>
          </div>

          <div className="grid grid-cols-7 gap-xs text-center text-body-sm form-label color-muted mb-sm">
            <div>Sun</div>
            <div>Mon</div>
            <div>Tue</div>
            <div>Wed</div>
            <div>Thu</div>
            <div>Fri</div>
            <div>Sat</div>
          </div>

          <div className="grid grid-cols-7 gap-xs">
            {calendarCells.map(({ date, items }, index) => {
              const isCurrentMonth = date.getMonth() === currentMonth.getMonth();
              const isToday = date.toDateString() === new Date().toDateString();

              return (
                <div
                  key={`${date.toISOString()}-${index}`}
                  className={`min-h-20 p-xs border rounded ${isCurrentMonth ? 'bg-background' : 'bg-secondary/50'} ${
                    isToday ? 'ring-2 ring-primary' : ''
                  }`}
                >
                  <div className={`text-body-sm ${isCurrentMonth ? 'color-foreground' : 'color-muted'}`}>{date.getDate()}</div>
                  <div className="stack-md">
                    {items.slice(0, 2).map((item) => (
                      <button
                        key={item.id}
                        type="button"
                        onClick={() => (item.type === 'milestone' ? handleOpenMilestone(item.id) : handleOpenTask(item.id))}
                        className={`text-body-sm p-xs rounded truncate text-left ${
                          item.type === 'milestone' ? 'bg-accent/10 color-accent' : 'bg-success/10 color-success'
                        }`}
                        title={item.title}
                      >
                        {item.title}
                      </button>
                    ))}
                    {items.length > 2 && (
                      <div className="text-body-sm color-muted">+{items.length - 2} more</div>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      )}

      {view === 'list' && (
        <div className="stack-md">
          <h3 className="text-body text-heading-4">Upcoming Items</h3>
          <div className="stack-sm">
            {upcomingItems.map((item) => (
              <button
                key={item.id}
                type="button"
                onClick={() => (item.type === 'milestone' ? handleOpenMilestone(item.id) : handleOpenTask(item.id))}
                className="flex items-center gap-md p-sm border rounded-lg text-left hover:bg-secondary/50"
              >
                <div className="flex items-center gap-sm">
                  {item.type === 'milestone' ? (
                    <CheckCircle className="w-4 h-4 color-accent" />
                  ) : (
                    <Clock className="w-4 h-4 color-success" />
                  )}
                  <span className="text-body-sm form-label capitalize">{item.type}</span>
                </div>

                <div className="flex-1">
                  <div className="form-label">{item.title}</div>
                  <div className="text-body-sm color-muted">{item.projectName || 'Unassigned project'}</div>
                </div>

                <div className="flex items-center gap-sm">
                  <Badge className={getStatusColor(item.status)}>{item.status}</Badge>
                  <div className={`text-body-sm ${isOverdue(item.date) ? 'color-destructive form-label' : 'color-muted'}`}>
                    {isOverdue(item.date) && <AlertCircle className="w-4 h-4 inline mr-xs" />}
                    {toDateLabel(item.date)}
                  </div>
                </div>
              </button>
            ))}
          </div>
        </div>
      )}

      <div className="grid grid-cols-1 gap-md pt-lg border-t md:grid-cols-4">
        <div className="text-center">
          <div className="text-heading-3 color-accent">{filteredData.projects.length}</div>
          <div className="text-body-sm color-muted">Active Projects</div>
        </div>
        <div className="text-center">
          <div className="text-heading-3 color-success">{filteredData.milestones.length}</div>
          <div className="text-body-sm color-muted">Milestones</div>
        </div>
        <div className="text-center">
          <div className="text-heading-3 color-warning">{filteredData.tasks.length}</div>
          <div className="text-body-sm color-muted">Scheduled Tasks</div>
        </div>
        <div className="text-center">
          <div className="text-heading-3 color-destructive">
            {upcomingItems.filter((item) => isOverdue(item.date)).length}
          </div>
          <div className="text-body-sm color-muted">Overdue Items</div>
        </div>
      </div>

      <AppDrawer
        open={drawerPayload !== null}
        onClose={handleDrawerClose}
        record={drawerPayload}
        fields={[]}
        mode="view"
        title={
          drawerPayload?.type === 'project'
            ? drawerPayload.project.name
            : drawerPayload?.type === 'milestone'
            ? drawerPayload.milestone.title
            : drawerPayload?.type === 'task'
            ? drawerPayload.task.title
            : 'Details'
        }
        tabs={drawerPayload ? getDrawerTabs(drawerPayload) : []}
      />
    </div>
  );
}

function getDrawerTabs(payload: Exclude<ScheduleDrawerPayload, null>) {
  if (payload.type === 'project') {
    return [
      {
        key: 'details',
        label: 'Details',
        content: (
          <div className="stack-sm text-body-sm">
            <div>
              <span className="form-label">Status:</span>{' '}
              <Badge className={getStatusColor(payload.project.status)}>{payload.project.status}</Badge>
            </div>
            <div>
              <span className="form-label">Budget:</span> {formatCurrency(payload.project.budget)}
            </div>
            <div>
              <span className="form-label">Start:</span> {toDateLabel(payload.project.starts_at)}
            </div>
            <div>
              <span className="form-label">End:</span> {toDateLabel(payload.project.ends_at)}
            </div>
          </div>
        ),
      },
      {
        key: 'milestones',
        label: 'Milestones',
        content: payload.milestones.length > 0 ? (
          <div className="stack-sm">
            {payload.milestones.map((milestone) => (
              <div key={milestone.id} className="flex items-center gap-sm rounded border px-sm py-xs">
                <span className="flex-1">{milestone.title}</span>
                <Badge className={getStatusColor(milestone.status)}>{milestone.status}</Badge>
                <span className={`text-body-sm ${isOverdue(milestone.due_at) ? 'color-destructive' : 'color-muted'}`}>
                  {toDateLabel(milestone.due_at)}
                </span>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-body-sm color-muted">No milestones for this project.</div>
        ),
      },
      {
        key: 'tasks',
        label: 'Tasks',
        content: payload.tasks.length > 0 ? (
          <div className="stack-sm">
            {payload.tasks.map((task) => (
              <div key={task.id} className="flex items-center gap-sm rounded border px-sm py-xs">
                <span className="flex-1">{task.title}</span>
                <Badge className={getStatusColor(task.status)}>{task.status}</Badge>
                <span className={`text-body-sm ${isOverdue(task.due_at) ? 'color-destructive' : 'color-muted'}`}>
                  {toDateLabel(task.due_at)}
                </span>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-body-sm color-muted">No tasks scheduled for this project.</div>
        ),
      },
    ];
  }

  if (payload.type === 'milestone') {
    return [
      {
        key: 'details',
        label: 'Details',
        content: (
          <div className="stack-sm text-body-sm">
            <div>
              <span className="form-label">Status:</span>{' '}
              <Badge className={getStatusColor(payload.milestone.status)}>{payload.milestone.status}</Badge>
            </div>
            <div>
              <span className="form-label">Due Date:</span> {toDateLabel(payload.milestone.due_at)}
            </div>
            <div>
              <span className="form-label">Project:</span> {payload.project?.name || 'Unknown project'}
            </div>
          </div>
        ),
      },
      {
        key: 'tasks',
        label: 'Project Tasks',
        content: payload.relatedTasks.length > 0 ? (
          <div className="stack-sm">
            {payload.relatedTasks.map((task) => (
              <div key={task.id} className="flex items-center gap-sm rounded border px-sm py-xs">
                <span className="flex-1">{task.title}</span>
                <Badge className={getStatusColor(task.status)}>{task.status}</Badge>
                <span className={`text-body-sm ${isOverdue(task.due_at) ? 'color-destructive' : 'color-muted'}`}>
                  {toDateLabel(task.due_at)}
                </span>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-body-sm color-muted">No tasks associated with this milestone's project.</div>
        ),
      },
    ];
  }

  return [
    {
      key: 'details',
      label: 'Details',
      content: (
        <div className="stack-sm text-body-sm">
          <div>
            <span className="form-label">Status:</span>{' '}
            <Badge className={getStatusColor(payload.task.status)}>{payload.task.status}</Badge>
          </div>
          <div>
            <span className="form-label">Due Date:</span> {toDateLabel(payload.task.due_at)}
          </div>
          <div>
            <span className="form-label">Project:</span> {payload.project?.name || 'Unknown project'}
          </div>
        </div>
      ),
    },
  ];
}

"use client";

import { ClipboardList, Calendar as CalendarIcon, MessageSquare, Activity as ActivityIcon, AlertCircle, CheckCircle, PauseCircle, Clock, XCircle } from "lucide-react";
import { useCallback, useMemo, useState, type ChangeEvent, type FormEvent } from "react";
import { useRouter } from "next/navigation";
import { useTranslations } from "next-intl";
import { createBrowserClient } from "@ghxstship/auth";
import { Badge, Button } from "@ghxstship/ui";
import AppDrawer from "@/app/_components/shared/AppDrawer";

export type TaskStatus = "pending" | "in_progress" | "completed" | "blocked" | "cancelled";

export interface TaskRow {
  id: string;
  title: string;
  status: string;
  due_at: string | null;
  project_id: string | null;
}

interface TaskComment {
  id: string;
  body: string;
  created_at: string;
}

interface TaskActivity {
  action: string;
  occurred_at: string;
}

interface TaskFormData {
  title: string;
  status: TaskStatus;
  dueAt: string;
}

type TaskFormErrors = Partial<Record<keyof TaskFormData, string>>;

const initialFormState: TaskFormData = {
  title: "",
  status: "pending",
  dueAt: "",
};

const normalizeStatus = (status: string | null | undefined): TaskStatus => {
  switch (status) {
    case "in_progress":
    case "completed":
    case "blocked":
    case "cancelled":
    case "pending":
      return status;
    default:
      return "pending";
  }
};

const statusOptions: TaskStatus[] = ["pending", "in_progress", "completed", "blocked", "cancelled"];

export default function TasksTableClient({ rows, orgId }: { rows: TaskRow[]; orgId: string }) {
  const t = useTranslations("tasks");
  const router = useRouter();
  const supabase = useMemo(() => createBrowserClient(), []);

  const [openId, setOpenId] = useState<string | null>(null);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [formData, setFormData] = useState<TaskFormData>(initialFormState);
  const [formErrors, setFormErrors] = useState<TaskFormErrors>({});
  const [comments, setComments] = useState<TaskComment[]>([]);
  const [activities, setActivities] = useState<TaskActivity[]>([]);
  const [commentsLoading, setCommentsLoading] = useState(false);
  const [activityLoading, setActivityLoading] = useState(false);
  const [commentInput, setCommentInput] = useState("");
  const [commentSaving, setCommentSaving] = useState(false);

  const current = rows.find((row) => row.id === openId) ?? null;

  const resetDrawerState = useCallback(() => {
    setFormData(initialFormState);
    setFormErrors({});
    setError(null);
    setComments([]);
    setActivities([]);
    setCommentsLoading(false);
    setActivityLoading(false);
    setCommentInput("");
    setCommentSaving(false);
  }, []);

  const loadSupportingData = useCallback(
    async (taskId: string) => {
      setCommentsLoading(true);
      setActivityLoading(true);

      try {
        const { data: commentsData, error: commentsError } = await supabase
          .from("comments")
          .select("id, body, created_at")
          .eq("organization_id", orgId)
          .eq("entity_type", "task")
          .eq("entity_id", taskId)
          .order("created_at", { ascending: false })
          .limit(50);

        if (commentsError) {
          throw commentsError;
        }

        setComments(commentsData ?? []);
      } catch (commentLoadError) {
        setError(commentLoadError instanceof Error ? commentLoadError.message : "Failed to load comments");
        setComments([]);
      } finally {
        setCommentsLoading(false);
      }

      try {
        const response = await fetch(`/api/audit/${orgId}/tasks/${taskId}`);
        if (!response.ok) {
          throw new Error("Failed to load activity");
        }
        const json = await response.json();
        setActivities((json?.data as TaskActivity[]) ?? []);
      } catch (activityError) {
        setError(activityError instanceof Error ? activityError.message : "Failed to load activity");
        setActivities([]);
      } finally {
        setActivityLoading(false);
      }
    },
    [orgId, supabase]
  );

  const handleOpen = useCallback(
    (id: string) => {
      const row = rows.find((entry) => entry.id === id);
      if (!row) {
        return;
      }

      setOpenId(id);
      setFormData({
        title: row.title ?? "",
        status: normalizeStatus(row.status),
        dueAt: row.due_at ? new Date(row.due_at).toISOString().slice(0, 10) : "",
      });
      setFormErrors({});
      setError(null);
      setCommentInput("");
      void loadSupportingData(id);
    },
    [loadSupportingData, rows]
  );

  const handleClose = useCallback(() => {
    if (saving) {
      return;
    }
    setOpenId(null);
    resetDrawerState();
  }, [resetDrawerState, saving]);

  const handleInputChange = useCallback((event: ChangeEvent<HTMLInputElement>) => {
    const { id, value } = event.target;
    setFormData((prev) => ({ ...prev, [id]: value }));
  }, []);

  const handleStatusChange = useCallback((event: ChangeEvent<HTMLSelectElement>) => {
    const value = event.target.value as TaskStatus;
    setFormData((prev) => ({ ...prev, status: value }));
  }, []);

  const validateForm = useCallback((draft: TaskFormData): TaskFormErrors => {
    const nextErrors: TaskFormErrors = {};
    if (!draft.title.trim()) {
      nextErrors.title = "Title is required";
    }
    return nextErrors;
  }, []);

  const handleSave = useCallback(
    async (event: FormEvent<HTMLFormElement>) => {
      event.preventDefault();
      if (!openId) {
        return;
      }

      const validation = validateForm(formData);
      setFormErrors(validation);
      if (Object.keys(validation).length > 0) {
        return;
      }

      setSaving(true);
      setError(null);

      try {
        const updatePayload = {
          title: formData.title.trim(),
          status: formData.status,
          due_at: formData.dueAt || null,
        };

        const { error: updateError } = await supabase
          .from("tasks")
          .update(updatePayload)
          .eq("id", openId)
          .eq("organization_id", orgId);

        if (updateError) {
          throw updateError;
        }

        if (typeof window !== "undefined" && (window as unknown).posthog) {
          (window as unknown).posthog.capture("task.updated", {
            organization_id: orgId,
            task_id: openId,
            status: formData.status,
          });
        }

        router.refresh();
      } catch (updateError) {
        if (updateError instanceof Error) {
          setError(updateError.message);
        } else {
          setError("Failed to update task");
        }
      } finally {
        setSaving(false);
      }
    },
    [formData, openId, orgId, router, supabase, validateForm]
  );

  const handleCommentSubmit = useCallback(
    async (event: FormEvent<HTMLFormElement>) => {
      event.preventDefault();
      if (!openId) {
        return;
      }

      const body = commentInput.trim();
      if (!body) {
        return;
      }

      setCommentSaving(true);
      setError(null);

      try {
        const { data, error: insertError } = await supabase
          .from("comments")
          .insert({
            organization_id: orgId,
            entity_type: "task",
            entity_id: openId,
            body,
          })
          .select("id, body, created_at")
          .single();

        if (insertError) {
          throw insertError;
        }

        if (data) {
          setComments((prev) => [data, ...prev]);
          setCommentInput("");
        }
      } catch (commentError) {
        setError(commentError instanceof Error ? commentError.message : "Failed to add comment");
      } finally {
        setCommentSaving(false);
      }
    },
    [commentInput, openId, orgId, supabase]
  );

  const getStatusVariant = useCallback((status: TaskStatus) => {
    switch (status) {
      case "completed":
        return "success" as const;
      case "in_progress":
        return "warning" as const;
      case "blocked":
        return "destructive" as const;
      case "cancelled":
        return "secondary" as const;
      default:
        return "outline" as const;
    }
  }, []);

  const getStatusIcon = useCallback((status: TaskStatus) => {
    switch (status) {
      case "completed":
        return CheckCircle;
      case "in_progress":
        return PauseCircle;
      case "blocked":
        return AlertCircle;
      case "cancelled":
        return XCircle;
      default:
        return Clock;
    }
  }, []);

  const renderStatusLabel = useCallback(
    (status: TaskStatus) => t(`statuses.${status}`, { default: status.replace(/_/g, " ") }),
    [t]
  );

  return (
    <div className="overflow-x-auto">
      <table className="w-full border-collapse text-body-sm">
        <thead>
          <tr className="sticky top-0">
            <th className="border-b p-sm text-left">{t("grid.title")}</th>
            <th className="border-b p-sm text-left">{t("grid.status")}</th>
            <th className="border-b p-sm text-left">{t("grid.dueAt")}</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((row) => {
            const status = normalizeStatus(row.status);
            const StatusIcon = getStatusIcon(status);
            return (
              <tr
                key={row.id}
                className="hover:bg-accent/20 cursor-pointer"
                onClick={() => {
                  void handleOpen(row.id);
                }}
              >
                <td className="border-b p-sm">
                  <div className="flex items-center gap-sm">
                    <ClipboardList className="w-4 h-4 color-muted" />
                    {row.title}
                  </div>
                </td>
                <td className="border-b p-sm">
                  <div className="flex items-center gap-sm">
                    <StatusIcon className="w-4 h-4" />
                    <Badge variant={getStatusVariant(status)}>{renderStatusLabel(status)}</Badge>
                  </div>
                </td>
                <td className="border-b p-sm">
                  {row.due_at ? (
                    <div className="flex items-center gap-sm">
                      <CalendarIcon className="w-4 h-4 color-muted" />
                      {new Date(row.due_at).toLocaleDateString()}
                    </div>
                  ) : (
                    "-"
                  )}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>

      <AppDrawer
        open={!!openId}
        onClose={handleClose}
        title={current?.title || t("title")}
        record={current}
        fields={[]}
        mode="view"
        tabs={[
          {
            key: "details",
            label: t("drawer.details"),
            content: current ? (
              <div className="stack-sm text-body-sm">
                {error ? (
                  <div role="alert" className="rounded border border-destructive/30 bg-destructive/10 px-sm py-xs color-destructive">
                    {error}
                  </div>
                ) : null}
                <div>
                  <span className="form-label">{t("grid.title")}:</span> {current.title}
                </div>
                <div className="flex items-center gap-sm">
                  <span className="form-label">{t("grid.status")}:</span>
                  <Badge variant={getStatusVariant(normalizeStatus(current.status))}>
                    {renderStatusLabel(normalizeStatus(current.status))}
                  </Badge>
                </div>
                <div>
                  <span className="form-label">{t("grid.dueAt")}:</span>{" "}
                  {current.due_at ? new Date(current.due_at).toLocaleString() : "-"}
                </div>
              </div>
            ) : (
              <div />
            ),
          },
          {
            key: "edit",
            label: t("drawer.edit"),
            content: current ? (
              <form onSubmit={handleSave} className="stack-sm">
                <div className="grid gap-xs">
                  <label htmlFor="title" className="text-body-sm">
                    {t("grid.title")} *
                  </label>
                  <input
                    id="title"
                    type="text"
                    className="rounded border px-md py-xs"
                    value={formData.title}
                    onChange={handleInputChange}
                    aria-invalid={formErrors.title ? "true" : "false"}
                  />
                  {formErrors.title ? <div className="text-body-sm color-destructive">{formErrors.title}</div> : null}
                </div>

                <div className="grid gap-xs">
                  <label htmlFor="status" className="text-body-sm">
                    {t("grid.status")}
                  </label>
                  <select id="status" className="rounded border px-md py-xs" value={formData.status} onChange={handleStatusChange}>
                    {statusOptions.map((status) => (
                      <option key={status} value={status}>
                        {renderStatusLabel(status)}
                      </option>
                    ))}
                  </select>
                </div>

                <div className="grid gap-xs">
                  <label htmlFor="dueAt" className="text-body-sm">
                    {t("grid.dueAt")}
                  </label>
                  <input
                    id="dueAt"
                    type="date"
                    className="rounded border px-md py-xs"
                    value={formData.dueAt}
                    onChange={handleInputChange}
                  />
                </div>

                <div className="flex items-center justify-between border-t pt-sm">
                  <Button type="submit" disabled={saving}>
                    {saving ? t("drawer.saving") : t("drawer.save", { default: "Save" })}
                  </Button>
                  <Button type="button" variant="outline" onClick={handleClose} disabled={saving}>
                    {t("drawer.cancel", { default: "Cancel" })}
                  </Button>
                </div>
              </form>
            ) : (
              <div />
            ),
          },
          {
            key: "comments",
            label: t("drawer.comments"),
            content: (
              <div className="stack-sm">
                <form onSubmit={handleCommentSubmit} className="flex items-start gap-sm" aria-label={t("drawer.comments")}">
                  <textarea
                    className="min-h-16 w-full rounded border p-sm"
                    placeholder={t("drawer.comments")}
                    value={commentInput}
                    onChange={(event) => setCommentInput(event.target.value)}
                  />
                  <Button type="submit" disabled={commentSaving || !commentInput.trim()}>
                    {commentSaving ? t("drawer.saving") : t("drawer.post")}
                  </Button>
                </form>

                {commentsLoading ? (
                  <div className="text-body-sm opacity-70">{t("drawer.loading")}</div>
                ) : comments.length === 0 ? (
                  <div className="text-body-sm color-muted">
                    {t("drawer.noComments", { default: "No comments yet." })}
                  </div>
                ) : (
                  <div className="stack-sm">
                    {comments.map((comment) => (
                      <div key={comment.id} className="rounded border px-sm py-xs">
                        <div className="flex items-center gap-sm text-body-sm form-label">
                          <MessageSquare className="h-4 w-4" />
                          {new Date(comment.created_at).toLocaleString()}
                        </div>
                        <div className="mt-xs whitespace-pre-wrap text-body-sm">{comment.body}</div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            ),
          },
          {
            key: "activity",
            label: t("drawer.activity"),
            content: (
              <div className="stack-sm text-body-sm">
                {activityLoading ? (
                  <div className="text-body-sm opacity-70">{t("drawer.loading")}</div>
                ) : activities.length === 0 ? (
                  <div className="text-body-sm color-muted">
                    {t("drawer.noActivity", { default: "No activity logged." })}
                  </div>
                ) : (
                  activities.map((activity) => (
                    <div key={`${activity.occurred_at}-${activity.action}`} className="rounded border px-sm py-xs">
                      <div className="flex items-center gap-sm">
                        <ActivityIcon className="h-4 w-4" />
                        {new Date(activity.occurred_at).toLocaleString()}
                      </div>
                      <div className="mt-xs">{activity.action}</div>
                    </div>
                  ))
                )}
              </div>
            ),
          },
        ]}
      />
    </div>
  );
}

"use client";

import { ListTodo, Calendar, Clock, Users, CheckCircle, AlertCircle, Edit, FileText, Activity, Tag, TrendingUp, Briefcase, GitBranch, Paperclip } from "lucide-react";
import { AppDrawer, Button, Badge, Progress, Card } from "@ghxstship/ui";
import { format, parseISO, differenceInDays } from "date-fns";

interface User {
  id: string;
  email: string;
  full_name?: string;
  avatar_url?: string;
}

interface Project {
  id: string;
  name: string;
  status: string;
}

interface Task {
  id: string;
  project_id: string;
  organization_id: string;
  project?: Project;
  title: string;
  description?: string;
  status: "todo" | "in_progress" | "review" | "done" | "blocked";
  priority: "low" | "medium" | "high" | "critical";
  assignee_id?: string;
  assignee?: User;
  reporter_id?: string;
  reporter?: User;
  parent_task_id?: string;
  parent_task?: Task;
  subtasks?: Task[];
  estimated_hours?: number;
  actual_hours?: number;
  start_date?: string;
  due_date?: string;
  completed_at?: string;
  tags?: string[];
  dependencies?: string[];
  attachments?: string[];
  position: number;
  created_at: string;
  updated_at: string;
}

interface ViewTaskDrawerProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  task: Task;
  onEdit?: () => void;
}

export default function ViewTaskDrawer({
  open,
  onOpenChange,
  task,
  onEdit,
}: ViewTaskDrawerProps) {
  // Calculate days until due
  const getDaysUntil = () => {
    if (!task.due_date) return null;

    const days = differenceInDays(parseISO(task.due_date), new Date());

    if (task.status === "done") {
      return (
        <span className="text-success flex items-center gap-1">
          <CheckCircle className="h-4 w-4" />
          Completed
        </span>
      );
    } else if (days < 0) {
      return (
        <span className="text-destructive flex items-center gap-1">
          <AlertCircle className="h-4 w-4" />
          {Math.abs(days)} days overdue
        </span>
      );
    } else if (days === 0) {
      return (
        <span className="text-warning flex items-center gap-1">
          <Clock className="h-4 w-4" />
          Due today
        </span>
      );
    } else if (days <= 7) {
      return (
        <span className="text-warning flex items-center gap-1">
          <Clock className="h-4 w-4" />
          {days} days left
        </span>
      );
    } else {
      return (
        <span className="text-muted-foreground flex items-center gap-1">
          <Clock className="h-4 w-4" />
          {days} days left
        </span>
      );
    }
  };

  // Get status badge
  const getStatusBadge = () => {
    const variant = 
      task.status === "done" ? "success" :
      task.status === "in_progress" ? "warning" :
      task.status === "review" ? "info" :
      task.status === "blocked" ? "destructive" :
      "secondary";

    return (
      <Badge variant={variant}>
        {task.status.replace("_", " ")}
      </Badge>
    );
  };

  // Get priority badge
  const getPriorityBadge = () => {
    const variant = 
      task.priority === "critical" ? "destructive" :
      task.priority === "high" ? "warning" :
      task.priority === "medium" ? "secondary" :
      "outline";

    return (
      <Badge variant={variant}>
        {task.priority}
      </Badge>
    );
  };

  // Calculate progress
  const calculateProgress = () => {
    if (task.status === "done") return 100;
    if (task.status === "review") return 75;
    if (task.status === "in_progress") return 50;
    if (task.status === "blocked") return 25;
    return 0;
  };

  // Calculate efficiency
  const calculateEfficiency = () => {
    if (!task.estimated_hours || !task.actual_hours) return null;
    const efficiency = (task.estimated_hours / task.actual_hours) * 100;
    return efficiency.toFixed(0);
  };

  // Calculate subtask progress
  const getSubtaskProgress = () => {
    if (!task.subtasks || task.subtasks.length === 0) return null;
    const completed = task.subtasks.filter(st => st.status === "done").length;
    const total = task.subtasks.length;
    return { completed, total, percentage: (completed / total) * 100 };
  };

  const subtaskProgress = getSubtaskProgress();

  return (
    <AppDrawer
      open={open}
      onOpenChange={onOpenChange}
      title={task.title}
      description={task.project?.name || "Task Details"}
      icon={<ListTodo className="h-5 w-5" />}
      width="md"
    >
      <div className="space-y-6">
        {/* Header Actions */}
        <div className="flex justify-between items-start">
          <div className="flex items-center gap-2">
            {getStatusBadge()}
            {getPriorityBadge()}
            {getDaysUntil()}
          </div>
          {onEdit && (
            <Button
              variant="outline"
              size="sm"
              onClick={onEdit}
            >
              <Edit className="h-4 w-4 mr-2" />
              Edit
            </Button>
          )}
        </div>

        {/* Progress */}
        <Card className="p-4">
          <div className="space-y-3">
            <div className="flex items-center justify-between">
              <span className="text-sm font-medium">Task Progress</span>
              <span className="text-2xl font-bold">{calculateProgress()}%</span>
            </div>
            <Progress value={calculateProgress()} className="h-2" />
            {subtaskProgress && (
              <div className="text-xs text-muted-foreground">
                {subtaskProgress.completed} of {subtaskProgress.total} subtasks completed
              </div>
            )}
          </div>
        </Card>

        {/* Description */}
        {task.description && (
          <div>
            <h3 className="font-medium mb-2 flex items-center gap-2">
              <FileText className="h-4 w-4" />
              Description
            </h3>
            <p className="text-sm text-muted-foreground whitespace-pre-wrap">
              {task.description}
            </p>
          </div>
        )}

        {/* Timeline */}
        <div>
          <h3 className="font-medium mb-3 flex items-center gap-2">
            <Calendar className="h-4 w-4" />
            Timeline
          </h3>
          <div className="space-y-3">
            {task.start_date && (
              <div className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
                <span className="text-sm">Start Date</span>
                <span className="text-sm font-medium">
                  {format(parseISO(task.start_date), "MMMM d, yyyy")}
                </span>
              </div>
            )}

            {task.due_date && (
              <div className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
                <span className="text-sm">Due Date</span>
                <span className="text-sm font-medium">
                  {format(parseISO(task.due_date), "MMMM d, yyyy")}
                </span>
              </div>
            )}

            {task.completed_at && (
              <div className="flex items-center justify-between p-3 bg-success/10 rounded-lg">
                <span className="text-sm">Completed</span>
                <span className="text-sm font-medium">
                  {format(parseISO(task.completed_at), "MMMM d, yyyy 'at' h:mm a")}
                </span>
              </div>
            )}
          </div>
        </div>

        {/* Time Tracking */}
        {(task.estimated_hours || task.actual_hours) && (
          <div>
            <h3 className="font-medium mb-3 flex items-center gap-2">
              <Clock className="h-4 w-4" />
              Time Tracking
            </h3>
            <div className="grid grid-cols-2 gap-3">
              {task.estimated_hours && (
                <Card className="p-3">
                  <div className="text-sm text-muted-foreground">Estimated</div>
                  <div className="text-lg font-medium">{task.estimated_hours}h</div>
                </Card>
              )}
              {task.actual_hours !== undefined && (
                <Card className="p-3">
                  <div className="text-sm text-muted-foreground">Actual</div>
                  <div className="text-lg font-medium">{task.actual_hours}h</div>
                </Card>
              )}
            </div>
            {calculateEfficiency() && (
              <div className="mt-3 p-3 bg-muted/50 rounded-lg flex items-center justify-between">
                <span className="text-sm flex items-center gap-2">
                  <TrendingUp className="h-4 w-4" />
                  Efficiency
                </span>
                <span className={`text-sm font-medium ${
                  parseFloat(calculateEfficiency()) >= 100 ? "text-success" :
                  parseFloat(calculateEfficiency()) >= 80 ? "text-warning" :
                  "text-destructive"
                }`}>
                  {calculateEfficiency()}%
                </span>
              </div>
            )}
          </div>
        )}

        {/* People */}
        <div>
          <h3 className="font-medium mb-3 flex items-center gap-2">
            <Users className="h-4 w-4" />
            People
          </h3>
          <div className="space-y-3">
            {task.assignee && (
              <div className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
                <span className="text-sm">Assignee</span>
                <div className="flex items-center gap-2">
                  {task.assignee.avatar_url ? (
                    <img
                      src={task.assignee.avatar_url}
                      alt={task.assignee.full_name || task.assignee.email}
                      className="h-6 w-6 rounded-full"
                    />
                  ) : (
                    <div className="h-6 w-6 rounded-full bg-muted flex items-center justify-center">
                      <Users className="h-3 w-3" />
                    </div>
                  )}
                  <span className="text-sm font-medium">
                    {task.assignee.full_name || task.assignee.email}
                  </span>
                </div>
              </div>
            )}
            {task.reporter && (
              <div className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
                <span className="text-sm">Reporter</span>
                <div className="flex items-center gap-2">
                  {task.reporter.avatar_url ? (
                    <img
                      src={task.reporter.avatar_url}
                      alt={task.reporter.full_name || task.reporter.email}
                      className="h-6 w-6 rounded-full"
                    />
                  ) : (
                    <div className="h-6 w-6 rounded-full bg-muted flex items-center justify-center">
                      <Users className="h-3 w-3" />
                    </div>
                  )}
                  <span className="text-sm font-medium">
                    {task.reporter.full_name || task.reporter.email}
                  </span>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Tags */}
        {task.tags && task.tags.length > 0 && (
          <div>
            <h3 className="font-medium mb-3 flex items-center gap-2">
              <Tag className="h-4 w-4" />
              Tags
            </h3>
            <div className="flex flex-wrap gap-2">
              {task.tags.map((tag) => (
                <Badge key={tag} variant="secondary">
                  {tag}
                </Badge>
              ))}
            </div>
          </div>
        )}

        {/* Subtasks */}
        {task.subtasks && task.subtasks.length > 0 && (
          <div>
            <h3 className="font-medium mb-3 flex items-center gap-2">
              <GitBranch className="h-4 w-4" />
              Subtasks ({subtaskProgress?.completed}/{subtaskProgress?.total})
            </h3>
            <div className="space-y-2">
              {task.subtasks.map((subtask) => (
                <Card key={subtask.id} className="p-3">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-2">
                      {subtask.status === "done" ? (
                        <CheckCircle className="h-4 w-4 text-success" />
                      ) : (
                        <div className="h-4 w-4 rounded-full border-2 border-muted-foreground" />
                      )}
                      <span className={`text-sm ${
                        subtask.status === "done" ? "line-through text-muted-foreground" : ""
                      }`}>
                        {subtask.title}
                      </span>
                    </div>
                    <Badge
                      variant={
                        subtask.priority === "critical" ? "destructive" :
                        subtask.priority === "high" ? "warning" :
                        subtask.priority === "medium" ? "secondary" :
                        "outline"
                      }
                      className="text-xs"
                    >
                      {subtask.priority}
                    </Badge>
                  </div>
                </Card>
              ))}
            </div>
          </div>
        )}

        {/* Project Info */}
        {task.project && (
          <div>
            <h3 className="font-medium mb-2 flex items-center gap-2">
              <Briefcase className="h-4 w-4" />
              Project
            </h3>
            <Card className="p-3">
              <div className="flex items-center justify-between">
                <span className="font-medium">{task.project.name}</span>
                <Badge variant={
                  task.project.status === "active" ? "success" :
                  task.project.status === "planning" ? "warning" :
                  task.project.status === "on_hold" ? "secondary" :
                  "destructive"
                }>
                  {task.project.status}
                </Badge>
              </div>
            </Card>
          </div>
        )}

        {/* Attachments */}
        {task.attachments && task.attachments.length > 0 && (
          <div>
            <h3 className="font-medium mb-3 flex items-center gap-2">
              <Paperclip className="h-4 w-4" />
              Attachments ({task.attachments.length})
            </h3>
            <div className="space-y-2">
              {task.attachments.map((attachment, index) => (
                <Card key={index} className="p-2">
                  <div className="flex items-center gap-2">
                    <Paperclip className="h-4 w-4 text-muted-foreground" />
                    <span className="text-sm truncate">{attachment}</span>
                  </div>
                </Card>
              ))}
            </div>
          </div>
        )}

        {/* Activity */}
        <div>
          <h3 className="font-medium mb-3 flex items-center gap-2">
            <Activity className="h-4 w-4" />
            Activity
          </h3>
          <div className="space-y-2 text-sm text-muted-foreground">
            <div className="flex items-center justify-between">
              <span>Created</span>
              <span>{format(parseISO(task.created_at), "MMM d, yyyy 'at' h:mm a")}</span>
            </div>
            <div className="flex items-center justify-between">
              <span>Last Updated</span>
              <span>{format(parseISO(task.updated_at), "MMM d, yyyy 'at' h:mm a")}</span>
            </div>
          </div>
        </div>

        {/* Close Button */}
        <div className="pt-6 border-t">
          <Button
            variant="outline"
            className="w-full"
            onClick={() => onOpenChange(false)}
          >
            Close
          </Button>
        </div>
      </div>
    </AppDrawer>
  );
}

"use client";

import { MoreVertical, Clock, Users, Calendar, Tag, AlertCircle, CheckCircle, Eye, Edit, Copy, Trash2, ChevronUp, ChevronDown, Briefcase } from "lucide-react";
import { useState, useMemo } from "react";
import { Card, Badge, Button, Checkbox, DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@ghxstship/ui";
import { format, parseISO, differenceInDays } from "date-fns";

interface User {
  id: string;
  email: string;
  full_name?: string;
  avatar_url?: string;
}

interface Project {
  id: string;
  name: string;
  status: string;
}

interface Task {
  id: string;
  title: string;
  description?: string;
  status: "todo" | "in_progress" | "review" | "done" | "blocked";
  priority: "low" | "medium" | "high" | "critical";
  assignee?: User;
  project?: Project;
  due_date?: string;
  estimated_hours?: number;
  actual_hours?: number;
  tags?: string[];
  subtasks?: Task[];
  created_at: string;
  updated_at: string;
}

interface TaskBoardViewProps {
  tasks: Task[];
  selectedItems: Set<string>;
  onSelectItem: (id: string) => void;
  onViewTask: (task: Task) => void;
  onEditTask: (task: Task) => void;
  onDuplicateTask: (task: Task) => void;
  onDeleteTask: (task: Task) => void;
}

const BOARD_COLUMNS = [
  { id: "todo", label: "To Do", color: "bg-gray-500" },
  { id: "in_progress", label: "In Progress", color: "bg-blue-500" },
  { id: "review", label: "Review", color: "bg-yellow-500" },
  { id: "done", label: "Done", color: "bg-green-500" },
  { id: "blocked", label: "Blocked", color: "bg-red-500" },
] as const;

export default function TaskBoardView({
  tasks,
  selectedItems,
  onSelectItem,
  onViewTask,
  onEditTask,
  onDuplicateTask,
  onDeleteTask,
}: TaskBoardViewProps) {
  const [collapsedColumns, setCollapsedColumns] = useState<Set<string>>(new Set());

  // Group tasks by status
  const tasksByStatus = useMemo(() => {
    const grouped: Record<string, Task[]> = {
      todo: [],
      in_progress: [],
      review: [],
      done: [],
      blocked: [],
    };

    tasks.forEach(task => {
      if (grouped[task.status]) {
        grouped[task.status].push(task);
      }
    });

    // Sort tasks within each column by priority and due date
    Object.keys(grouped).forEach(status => {
      grouped[status].sort((a, b) => {
        // Priority order: critical > high > medium > low
        const priorityOrder = { critical: 0, high: 1, medium: 2, low: 3 };
        const priorityDiff = priorityOrder[a.priority] - priorityOrder[b.priority];
        if (priorityDiff !== 0) return priorityDiff;

        // Then by due date (earliest first)
        if (a.due_date && b.due_date) {
          return a.due_date.localeCompare(b.due_date);
        }
        if (a.due_date) return -1;
        if (b.due_date) return 1;

        // Finally by creation date
        return b.created_at.localeCompare(a.created_at);
      });
    });

    return grouped;
  }, [tasks]);

  // Toggle column collapse
  const toggleColumn = (columnId: string) => {
    setCollapsedColumns(prev => {
      const next = new Set(prev);
      if (next.has(columnId)) {
        next.delete(columnId);
      } else {
        next.add(columnId);
      }
      return next;
    });
  };

  // Get priority badge variant
  const getPriorityBadge = (priority: string) => {
    const variant = 
      priority === "critical" ? "destructive" :
      priority === "high" ? "warning" :
      priority === "medium" ? "secondary" :
      "outline";

    return <Badge variant={variant} className="text-xs">{priority}</Badge>;
  };

  // Get days until due
  const getDaysUntil = (dueDate?: string) => {
    if (!dueDate) return null;
    const days = differenceInDays(parseISO(dueDate), new Date());

    if (days < 0) {
      return (
        <span className="text-destructive text-xs flex items-center gap-1">
          <AlertCircle className="h-3 w-3" />
          {Math.abs(days)}d overdue
        </span>
      );
    } else if (days === 0) {
      return (
        <span className="text-warning text-xs flex items-center gap-1">
          <Clock className="h-3 w-3" />
          Due today
        </span>
      );
    } else if (days <= 3) {
      return (
        <span className="text-warning text-xs flex items-center gap-1">
          <Clock className="h-3 w-3" />
          {days}d left
        </span>
      );
    } else {
      return (
        <span className="text-muted-foreground text-xs flex items-center gap-1">
          <Calendar className="h-3 w-3" />
          {format(parseISO(dueDate), "MMM d")}
        </span>
      );
    }
  };

  // Render task card
  const renderTaskCard = (task: Task) => {
    const isSelected = selectedItems.has(task.id);

    return (
      <Card
        key={task.id}
        className={`p-3 cursor-pointer transition-all hover:shadow-md ${
          isSelected ? "ring-2 ring-primary" : ""
        }`}
        onClick={() => onViewTask(task)}
      >
        <div className="space-y-2">
          {/* Header */}
          <div className="flex items-start justify-between">
            <div className="flex items-start gap-2 flex-1">
              <Checkbox
                checked={isSelected}
                onChange={() => onSelectItem(task.id)}
                onClick={(e) => e.stopPropagation()}
              />
              <div className="flex-1 min-w-0">
                <h4 className="font-medium text-sm line-clamp-2">{task.title}</h4>
                {task.project && (
                  <div className="flex items-center gap-1 mt-1">
                    <Briefcase className="h-3 w-3 text-muted-foreground" />
                    <span className="text-xs text-muted-foreground truncate">
                      {task.project.name}
                    </span>
                  </div>
                )}
              </div>
            </div>
            <DropdownMenu>
              <DropdownMenuTrigger asChild onClick={(e) => e.stopPropagation()}>
                <Button variant="ghost" size="sm" className="h-6 w-6 p-0">
                  <MoreVertical className="h-4 w-4" />
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                <DropdownMenuItem onClick={(e) => {
                  e.stopPropagation();
                  onViewTask(task);
                }}>
                  <Eye className="mr-2 h-4 w-4" />
                  View
                </DropdownMenuItem>
                <DropdownMenuItem onClick={(e) => {
                  e.stopPropagation();
                  onEditTask(task);
                }}>
                  <Edit className="mr-2 h-4 w-4" />
                  Edit
                </DropdownMenuItem>
                <DropdownMenuItem onClick={(e) => {
                  e.stopPropagation();
                  onDuplicateTask(task);
                }}>
                  <Copy className="mr-2 h-4 w-4" />
                  Duplicate
                </DropdownMenuItem>
                <DropdownMenuItem 
                  onClick={(e) => {
                    e.stopPropagation();
                    onDeleteTask(task);
                  }}
                  className="text-destructive"
                >
                  <Trash2 className="mr-2 h-4 w-4" />
                  Delete
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </div>

          {/* Description */}
          {task.description && (
            <p className="text-xs text-muted-foreground line-clamp-2">
              {task.description}
            </p>
          )}

          {/* Priority and Due Date */}
          <div className="flex items-center justify-between">
            {getPriorityBadge(task.priority)}
            {getDaysUntil(task.due_date)}
          </div>

          {/* Tags */}
          {task.tags && task.tags.length > 0 && (
            <div className="flex items-center gap-1 flex-wrap">
              <Tag className="h-3 w-3 text-muted-foreground" />
              {task.tags.slice(0, 2).map(tag => (
                <Badge key={tag} variant="outline" className="text-xs px-1 py-0">
                  {tag}
                </Badge>
              ))}
              {task.tags.length > 2 && (
                <span className="text-xs text-muted-foreground">
                  +{task.tags.length - 2}
                </span>
              )}
            </div>
          )}

          {/* Footer */}
          <div className="flex items-center justify-between pt-2 border-t">
            {/* Assignee */}
            {task.assignee ? (
              <div className="flex items-center gap-1">
                {task.assignee.avatar_url ? (
                  <img
                    src={task.assignee.avatar_url}
                    alt={task.assignee.full_name || task.assignee.email}
                    className="h-5 w-5 rounded-full"
                  />
                ) : (
                  <div className="h-5 w-5 rounded-full bg-muted flex items-center justify-center">
                    <Users className="h-3 w-3" />
                  </div>
                )}
                <span className="text-xs text-muted-foreground truncate max-w-[100px]">
                  {task.assignee.full_name || task.assignee.email}
                </span>
              </div>
            ) : (
              <span className="text-xs text-muted-foreground">Unassigned</span>
            )}

            {/* Subtasks */}
            {task.subtasks && task.subtasks.length > 0 && (
              <div className="flex items-center gap-1 text-xs text-muted-foreground">
                <CheckCircle className="h-3 w-3" />
                {task.subtasks.filter(st => st.status === "done").length}/{task.subtasks.length}
              </div>
            )}

            {/* Time tracking */}
            {(task.estimated_hours || task.actual_hours) && (
              <div className="flex items-center gap-1 text-xs text-muted-foreground">
                <Clock className="h-3 w-3" />
                {task.actual_hours || 0}/{task.estimated_hours || 0}h
              </div>
            )}
          </div>
        </div>
      </Card>
    );
  };

  return (
    <div className="flex gap-4 overflow-x-auto pb-4">
      {BOARD_COLUMNS.map(column => {
        const columnTasks = tasksByStatus[column.id] || [];
        const isCollapsed = collapsedColumns.has(column.id);

        return (
          <div
            key={column.id}
            className={`flex-shrink-0 ${isCollapsed ? "w-16" : "w-80"} transition-all`}
          >
            {/* Column Header */}
            <div
              className={`flex items-center justify-between p-3 mb-3 rounded-lg ${column.color} bg-opacity-10 cursor-pointer`}
              onClick={() => toggleColumn(column.id)}
            >
              <div className="flex items-center gap-2">
                <div className={`w-2 h-2 rounded-full ${column.color}`} />
                <h3 className={`font-medium ${isCollapsed ? "sr-only" : ""}`}>
                  {column.label}
                </h3>
                <Badge variant="secondary" className="text-xs">
                  {columnTasks.length}
                </Badge>
              </div>
              {isCollapsed ? (
                <ChevronDown className="h-4 w-4" />
              ) : (
                <ChevronUp className="h-4 w-4" />
              )}
            </div>

            {/* Column Tasks */}
            {!isCollapsed && (
              <div className="space-y-3 max-h-[calc(100vh-300px)] overflow-y-auto">
                {columnTasks.length === 0 ? (
                  <div className="text-center py-8 text-muted-foreground">
                    <p className="text-sm">No tasks</p>
                  </div>
                ) : (
                  columnTasks.map(renderTaskCard)
                )}
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
}

"use client";

import { MoreVertical, Clock, Users, Calendar, Tag, AlertCircle, CheckCircle, Eye, Edit, Copy, Trash2, ArrowUpDown, ChevronUp, ChevronDown, Briefcase, ListTodo } from "lucide-react";
import { useState, useMemo } from "react";
import { Badge, Button, Checkbox, DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@ghxstship/ui";
import { format, parseISO, differenceInDays } from "date-fns";

interface User {
  id: string;
  email: string;
  full_name?: string;
  avatar_url?: string;
}

interface Project {
  id: string;
  name: string;
  status: string;
}

interface Task {
  id: string;
  title: string;
  description?: string;
  status: "todo" | "in_progress" | "review" | "done" | "blocked";
  priority: "low" | "medium" | "high" | "critical";
  assignee?: User;
  project?: Project;
  due_date?: string;
  estimated_hours?: number;
  actual_hours?: number;
  tags?: string[];
  subtasks?: Task[];
  created_at: string;
  updated_at: string;
}

interface TaskListViewProps {
  tasks: Task[];
  selectedItems: Set<string>;
  onSelectItem: (id: string) => void;
  onViewTask: (task: Task) => void;
  onEditTask: (task: Task) => void;
  onDuplicateTask: (task: Task) => void;
  onDeleteTask: (task: Task) => void;
}

type SortField = "title" | "status" | "priority" | "due_date" | "assignee" | "project";
type SortDirection = "asc" | "desc";

export default function TaskListView({
  tasks,
  selectedItems,
  onSelectItem,
  onViewTask,
  onEditTask,
  onDuplicateTask,
  onDeleteTask,
}: TaskListViewProps) {
  const [sortField, setSortField] = useState<SortField>("priority");
  const [sortDirection, setSortDirection] = useState<SortDirection>("asc");
  const [expandedTasks, setExpandedTasks] = useState<Set<string>>(new Set());

  // Sort tasks
  const sortedTasks = useMemo(() => {
    return [...tasks].sort((a, b) => {
      let comparison = 0;

      switch (sortField) {
        case "title":
          comparison = a.title.localeCompare(b.title);
          break;
        case "status":
          const statusOrder = { todo: 0, in_progress: 1, review: 2, done: 3, blocked: 4 };
          comparison = statusOrder[a.status] - statusOrder[b.status];
          break;
        case "priority":
          const priorityOrder = { critical: 0, high: 1, medium: 2, low: 3 };
          comparison = priorityOrder[a.priority] - priorityOrder[b.priority];
          break;
        case "due_date":
          comparison = (a.due_date || "").localeCompare(b.due_date || "");
          break;
        case "assignee":
          const aName = a.assignee?.full_name || a.assignee?.email || "";
          const bName = b.assignee?.full_name || b.assignee?.email || "";
          comparison = aName.localeCompare(bName);
          break;
        case "project":
          comparison = (a.project?.name || "").localeCompare(b.project?.name || "");
          break;
        default:
          comparison = 0;
      }

      return sortDirection === "asc" ? comparison : -comparison;
    });
  }, [tasks, sortField, sortDirection]);

  // Toggle sort
  const toggleSort = (field: SortField) => {
    if (sortField === field) {
      setSortDirection(prev => prev === "asc" ? "desc" : "asc");
    } else {
      setSortField(field);
      setSortDirection("asc");
    }
  };

  // Toggle task expansion
  const toggleTaskExpansion = (taskId: string) => {
    setExpandedTasks(prev => {
      const next = new Set(prev);
      if (next.has(taskId)) {
        next.delete(taskId);
      } else {
        next.add(taskId);
      }
      return next;
    });
  };

  // Select all visible tasks
  const selectAll = () => {
    if (selectedItems.size === sortedTasks.length) {
      // Deselect all
      sortedTasks.forEach(task => onSelectItem(task.id));
    } else {
      // Select all
      sortedTasks.forEach(task => {
        if (!selectedItems.has(task.id)) {
          onSelectItem(task.id);
        }
      });
    }
  };

  // Get status badge
  const getStatusBadge = (status: string) => {
    const variant = 
      status === "done" ? "success" :
      status === "in_progress" ? "warning" :
      status === "review" ? "info" :
      status === "blocked" ? "destructive" :
      "secondary";

    return (
      <Badge variant={variant} className="text-xs">
        {status.replace("_", " ")}
      </Badge>
    );
  };

  // Get priority badge
  const getPriorityBadge = (priority: string) => {
    const variant = 
      priority === "critical" ? "destructive" :
      priority === "high" ? "warning" :
      priority === "medium" ? "secondary" :
      "outline";

    return <Badge variant={variant} className="text-xs">{priority}</Badge>;
  };

  // Get days until due
  const getDaysUntil = (dueDate?: string) => {
    if (!dueDate) return null;
    const days = differenceInDays(parseISO(dueDate), new Date());

    if (days < 0) {
      return (
        <span className="text-destructive text-xs flex items-center gap-1">
          <AlertCircle className="h-3 w-3" />
          {Math.abs(days)}d overdue
        </span>
      );
    } else if (days === 0) {
      return (
        <span className="text-warning text-xs flex items-center gap-1">
          <Clock className="h-3 w-3" />
          Due today
        </span>
      );
    } else if (days <= 7) {
      return (
        <span className="text-warning text-xs flex items-center gap-1">
          <Clock className="h-3 w-3" />
          {days}d
        </span>
      );
    } else {
      return (
        <span className="text-muted-foreground text-xs">
          {format(parseISO(dueDate), "MMM d")}
        </span>
      );
    }
  };

  // Render sort button
  const renderSortButton = (field: SortField, label: string) => {
    const isActive = sortField === field;
    return (
      <button
        onClick={() => toggleSort(field)}
        className="flex items-center gap-1 hover:text-primary transition-colors"
      >
        {label}
        {isActive ? (
          sortDirection === "asc" ? (
            <ChevronUp className="h-3 w-3" />
          ) : (
            <ChevronDown className="h-3 w-3" />
          )
        ) : (
          <ArrowUpDown className="h-3 w-3 opacity-50" />
        )}
      </button>
    );
  };

  return (
    <div className="w-full">
      {/* Table Header */}
      <div className="border-b bg-muted/30 px-4 py-3">
        <div className="flex items-center gap-4 text-sm font-medium">
          <div className="w-10">
            <Checkbox
              checked={selectedItems.size === sortedTasks.length && sortedTasks.length > 0}
              onChange={selectAll}
            />
          </div>
          <div className="flex-1 min-w-[200px]">
            {renderSortButton("title", "Title")}
          </div>
          <div className="w-28 hidden lg:block">
            {renderSortButton("status", "Status")}
          </div>
          <div className="w-24 hidden md:block">
            {renderSortButton("priority", "Priority")}
          </div>
          <div className="w-32 hidden xl:block">
            {renderSortButton("assignee", "Assignee")}
          </div>
          <div className="w-32 hidden xl:block">
            {renderSortButton("project", "Project")}
          </div>
          <div className="w-28 hidden lg:block">
            {renderSortButton("due_date", "Due Date")}
          </div>
          <div className="w-20 hidden md:block text-center">
            Progress
          </div>
          <div className="w-20 text-center">
            Actions
          </div>
        </div>
      </div>

      {/* Table Body */}
      <div className="divide-y">
        {sortedTasks.map(task => {
          const isExpanded = expandedTasks.has(task.id);
          const hasSubtasks = task.subtasks && task.subtasks.length > 0;
          const completedSubtasks = task.subtasks?.filter(st => st.status === "done").length || 0;
          const totalSubtasks = task.subtasks?.length || 0;
          const progress = totalSubtasks > 0 ? (completedSubtasks / totalSubtasks) * 100 : 
                          task.status === "done" ? 100 : 
                          task.status === "in_progress" ? 50 : 
                          task.status === "review" ? 75 : 0;

          return (
            <div key={task.id}>
              <div className="px-4 py-3 hover:bg-muted/30 transition-colors">
                <div className="flex items-center gap-4">
                  <div className="w-10">
                    <Checkbox
                      checked={selectedItems.has(task.id)}
                      onChange={() => onSelectItem(task.id)}
                    />
                  </div>

                  <div className="flex-1 min-w-[200px]">
                    <div className="flex items-center gap-2">
                      {hasSubtasks && (
                        <button
                          onClick={() => toggleTaskExpansion(task.id)}
                          className="p-0.5 hover:bg-muted rounded"
                        >
                          {isExpanded ? (
                            <ChevronDown className="h-4 w-4" />
                          ) : (
                            <ChevronUp className="h-4 w-4" />
                          )}
                        </button>
                      )}
                      <div>
                        <p className="font-medium line-clamp-1">{task.title}</p>
                        {task.description && (
                          <p className="text-sm text-muted-foreground line-clamp-1">
                            {task.description}
                          </p>
                        )}
                      </div>
                    </div>
                  </div>

                  <div className="w-28 hidden lg:block">
                    {getStatusBadge(task.status)}
                  </div>

                  <div className="w-24 hidden md:block">
                    {getPriorityBadge(task.priority)}
                  </div>

                  <div className="w-32 hidden xl:block">
                    {task.assignee ? (
                      <div className="flex items-center gap-2">
                        {task.assignee.avatar_url ? (
                          <img
                            src={task.assignee.avatar_url}
                            alt={task.assignee.full_name || task.assignee.email}
                            className="h-6 w-6 rounded-full"
                          />
                        ) : (
                          <div className="h-6 w-6 rounded-full bg-muted flex items-center justify-center">
                            <Users className="h-3 w-3" />
                          </div>
                        )}
                        <span className="text-sm truncate">
                          {task.assignee.full_name || task.assignee.email}
                        </span>
                      </div>
                    ) : (
                      <span className="text-sm text-muted-foreground">Unassigned</span>
                    )}
                  </div>

                  <div className="w-32 hidden xl:block">
                    {task.project && (
                      <div className="flex items-center gap-1">
                        <Briefcase className="h-3 w-3 text-muted-foreground" />
                        <span className="text-sm text-muted-foreground truncate">
                          {task.project.name}
                        </span>
                      </div>
                    )}
                  </div>

                  <div className="w-28 hidden lg:block">
                    {getDaysUntil(task.due_date)}
                  </div>

                  <div className="w-20 hidden md:block">
                    <div className="flex flex-col items-center gap-1">
                      <div className="w-full bg-muted rounded-full h-1.5">
                        <div 
                          className="bg-primary h-1.5 rounded-full transition-all"
                          style={{ width: `${progress}%` }}
                        />
                      </div>
                      <span className="text-xs text-muted-foreground">{Math.round(progress)}%</span>
                    </div>
                  </div>

                  <div className="w-20 flex justify-center">
                    <DropdownMenu>
                      <DropdownMenuTrigger asChild>
                        <Button variant="ghost" size="sm" className="h-8 w-8 p-0">
                          <MoreVertical className="h-4 w-4" />
                        </Button>
                      </DropdownMenuTrigger>
                      <DropdownMenuContent align="end">
                        <DropdownMenuItem onClick={() => onViewTask(task)}>
                          <Eye className="mr-2 h-4 w-4" />
                          View
                        </DropdownMenuItem>
                        <DropdownMenuItem onClick={() => onEditTask(task)}>
                          <Edit className="mr-2 h-4 w-4" />
                          Edit
                        </DropdownMenuItem>
                        <DropdownMenuItem onClick={() => onDuplicateTask(task)}>
                          <Copy className="mr-2 h-4 w-4" />
                          Duplicate
                        </DropdownMenuItem>
                        <DropdownMenuItem 
                          onClick={() => onDeleteTask(task)}
                          className="text-destructive"
                        >
                          <Trash2 className="mr-2 h-4 w-4" />
                          Delete
                        </DropdownMenuItem>
                      </DropdownMenuContent>
                    </DropdownMenu>
                  </div>
                </div>

                {/* Tags */}
                {task.tags && task.tags.length > 0 && (
                  <div className="flex items-center gap-2 mt-2 ml-14">
                    <Tag className="h-3 w-3 text-muted-foreground" />
                    {task.tags.map(tag => (
                      <Badge key={tag} variant="outline" className="text-xs">
                        {tag}
                      </Badge>
                    ))}
                  </div>
                )}
              </div>

              {/* Subtasks */}
              {isExpanded && hasSubtasks && (
                <div className="pl-16 pr-4 pb-2 bg-muted/20">
                  {task.subtasks!.map(subtask => (
                    <div key={subtask.id} className="flex items-center gap-4 py-2 border-t">
                      <ListTodo className="h-4 w-4 text-muted-foreground" />
                      <div className="flex-1">
                        <p className="text-sm">{subtask.title}</p>
                      </div>
                      {getStatusBadge(subtask.status)}
                      {getPriorityBadge(subtask.priority)}
                      {subtask.assignee_id && (
                        <Users className="h-4 w-4 text-muted-foreground" />
                      )}
                      {getDaysUntil(subtask.due_date)}
                    </div>
                  ))}
                </div>
              )}
            </div>
          );
        })}
      </div>

      {/* Empty State */}
      {sortedTasks.length === 0 && (
        <div className="text-center py-12">
          <ListTodo className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
          <h3 className="text-lg font-semibold mb-2">No tasks found</h3>
          <p className="text-muted-foreground">
            Try adjusting your filters or create a new task
          </p>
        </div>
      )}
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useState, useEffect, useCallback, useMemo } from 'react';
import {
  DataViewProvider,
  StateManagerProvider,
  ViewSwitcher,
  DataActions,
  Button,
  useToastContext,
} from '@ghxstship/ui';
import type { 
