interface CreateSettingsClientProps {
  orgId: string;
  userId: string;
}

export default function CreateSettingsClient({ orgId, userId }: CreateSettingsClientProps) {
  const { toast } = useToastContext();

  // State management
  const [settings, setSettings] = useState<SettingRecord[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedSettings, setSelectedSettings] = useState<string[]>([]);
  const [statistics, setStatistics] = useState<SettingsStatistics | null>(null);

  // View state
  const [currentView, setCurrentView] = useState<'grid' | 'list' | 'kanban'>('grid');
  const [searchParams, setSearchParams] = useState<SettingsSearchParams>({});

  // Drawer state
  const [createDrawerOpen, setCreateDrawerOpen] = useState(false);
  const [editDrawerOpen, setEditDrawerOpen] = useState(false);
  const [editingSetting, setEditingSetting] = useState<SettingRecord | null>(null);
  const [drawerMode, setDrawerMode] = useState<'create' | 'edit' | 'view'>('create');

  // Load settings data
  const loadSettings = useCallback(async (params?: SettingsSearchParams) => {
    try {
      setLoading(true);
      setError(null);
      const data = await settingsService.getSettings(params);
      setSettings(data);
    } catch (err) {
      const message = err instanceof Error ? err.message : 'Failed to load settings';
      setError(message);
      toast.error(message);
    } finally {
      setLoading(false);
    }
  }, [toast]);

  // Load statistics
  const loadStatistics = useCallback(async () => {
    try {
      const stats = await settingsService.getStatistics();
      setStatistics(stats);
    } catch (err) {
      console.error('Failed to load statistics:', err);
    }
  }, []);

  // Initial data load
  useEffect(() => {
    loadSettings();
    loadStatistics();
  }, [loadSettings, loadStatistics]);

  // ATLVS DataViews configuration
  const dataViewConfig: SettingsViewConfig = useMemo(() => ({
    id: 'settings',
    name: 'Settings Management',
    viewType: currentView,
    defaultView: 'grid',
    fields: fieldConfig,
    data: settings,
    onSearch: async (query: string) => {
      const params = { ...searchParams, query };
      setSearchParams(params);
      await loadSettings(params);
    },
    onFilter: async (filters: unknown) => {
      const params = { ...searchParams, ...filters };
      setSearchParams(params);
      await loadSettings(params);
    },
    onSort: async (sorts: unknown) => {
      // Sorting is handled client-side in the views
      console.log('Sort settings:', sorts);
    },
    onRefresh: async () => {
      await loadSettings(searchParams);
      await loadStatistics();
      return settings;
    },
    onExport: (data: any, format: unknown) => {
      handleExport(format);
    },
    onImport: (data: unknown) => {
      console.log('Import settings:', data);
      toast.info('Import functionality coming soon');
    }
  }), [currentView, settings, searchParams, loadSettings, loadStatistics]);

  // Event handlers
  const handleCreateSetting = () => {
    setDrawerMode('create');
    setCreateDrawerOpen(true);
  };

  const handleEditSetting = (setting: SettingRecord) => {
    setEditingSetting(setting);
    setDrawerMode('edit');
    setEditDrawerOpen(true);
  };

  const handleViewSetting = (setting: SettingRecord) => {
    setEditingSetting(setting);
    setDrawerMode('view');
    setEditDrawerOpen(true);
  };

  const handleDeleteSetting = async (id: string) => {
    try {
      await settingsService.deleteSetting(id);
      toast.success('Setting deleted successfully');
      await loadSettings(searchParams);
      await loadStatistics();
    } catch (err) {
      const message = err instanceof Error ? err.message : 'Failed to delete setting';
      toast.error(message);
    }
  };

  const handleSaveSetting = async (data: unknown) => {
    if (!editingSetting) return;

    try {
      await settingsService.updateSetting(editingSetting.id, data);
      await loadSettings(searchParams);
      await loadStatistics();
    } catch (err) {
      throw err; // Re-throw to be handled by the drawer
    }
  };

  const handleMoveSetting = async (settingId: string, newCategory: unknown) => {
    try {
      await settingsService.updateSetting(settingId, { category: newCategory });
      toast.success('Setting moved successfully');
      await loadSettings(searchParams);
    } catch (err) {
      const message = err instanceof Error ? err.message : 'Failed to move setting';
      toast.error(message);
    }
  };

  const handleExport = async (format: 'csv' | 'json') => {
    try {
      const blob = await settingsService.exportSettings({
        format,
        includeMetadata: true,
        categories: searchParams.category ? [searchParams.category] : undefined,
      });

      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `settings-export.${format}`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);

      toast.success(`Settings exported as ${format.toUpperCase()}`);
    } catch (err) {
      const message = err instanceof Error ? err.message : 'Failed to export settings';
      toast.error(message);
    }
  };

  const handleRefresh = async () => {
    await loadSettings(searchParams);
    await loadStatistics();
  };

  // Render current view
  const renderCurrentView = () => {
    switch (currentView) {
      case 'grid':
        return (
          <SettingsGridView
            settings={settings}
            loading={loading}
            onEdit={handleEditSetting}
            onDelete={handleDeleteSetting}
            onSelect={setSelectedSettings}
            selectedIds={selectedSettings}
          />
        );
      case 'list':
        return (
          <SettingsListView
            settings={settings}
            loading={loading}
            onEdit={handleEditSetting}
            onView={handleViewSetting}
          />
        );
      case 'kanban':
        return (
          <SettingsKanbanView
            settings={settings}
            loading={loading}
            onEdit={handleEditSetting}
            onMove={handleMoveSetting}
          />
        );
      default:
        return null;
    }
  };

  if (error) {
    return (
      <div className="flex items-center justify-center h-96">
        <div className="text-center">
          <p className="text-destructive mb-4">{error}</p>
          <Button onClick={() => loadSettings()}>
            <RefreshCw className="h-4 w-4 mr-2" />
            Retry
          </Button>
        </div>
      </div>
    );
  }

  return (
    <DataViewProvider config={dataViewConfig}>
      <StateManagerProvider>
        <div className="space-y-6">
          {/* Header */}
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold">Settings Management</h1>
              <p className="text-muted-foreground mt-1">
                Manage your organization's configuration settings and preferences
              </p>
            </div>
            <div className="flex items-center gap-3">
              <Button variant="outline" onClick={handleRefresh} disabled={loading}>
                <RefreshCw className="h-4 w-4 mr-2" />
                Refresh
              </Button>
              <Button variant="outline" onClick={() => handleExport('csv')}>
                <Download className="h-4 w-4 mr-2" />
                Export
              </Button>
              <Button onClick={handleCreateSetting}>
                <Plus className="h-4 w-4 mr-2" />
                Create Setting
              </Button>
            </div>
          </div>

          {/* Statistics */}
          {statistics && (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              <div className="bg-card p-4 rounded-lg border">
                <div className="text-2xl font-bold">{statistics.totalSettings}</div>
                <div className="text-sm text-muted-foreground">Total Settings</div>
              </div>
              <div className="bg-card p-4 rounded-lg border">
                <div className="text-2xl font-bold">{statistics.publicSettings}</div>
                <div className="text-sm text-muted-foreground">Public Settings</div>
              </div>
              <div className="bg-card p-4 rounded-lg border">
                <div className="text-2xl font-bold">{statistics.editableSettings}</div>
                <div className="text-sm text-muted-foreground">Editable Settings</div>
              </div>
              <div className="bg-card p-4 rounded-lg border">
                <div className="text-2xl font-bold">{statistics.recentlyUpdated}</div>
                <div className="text-sm text-muted-foreground">Updated This Week</div>
              </div>
            </div>
          )}

          {/* View Controls */}
          <div className="flex items-center justify-between">
            <ViewSwitcher
              currentView={currentView}
              onViewChange={setCurrentView}
              availableViews={['grid', 'list', 'kanban']}
            />
            <DataActions
              selectedCount={selectedSettings.length}
              onBulkDelete={() => {
                if (window.confirm(`Delete ${selectedSettings.length} settings?`)) {
                  Promise.all(selectedSettings.map(id => settingsService.deleteSetting(id)))
                    .then(() => {
                      toast.success(`Deleted ${selectedSettings.length} settings`);
                      setSelectedSettings([]);
                      loadSettings(searchParams);
                      loadStatistics();
                    })
                    .catch(err => {
                      toast.error('Failed to delete some settings');
                    });
                }
              }}
              onBulkExport={() => handleExport('csv')}
            />
          </div>

          {/* Main Content */}
          {renderCurrentView()}

          {/* Drawers */}
          <CreateSettingsDrawer
            isOpen={createDrawerOpen}
            onClose={() => setCreateDrawerOpen(false)}
            onSuccess={() => {
              loadSettings(searchParams);
              loadStatistics();
            }}
          />

          <EditSettingsDrawer
            mode={drawerMode}
            setting={editingSetting}
            isOpen={editDrawerOpen}
            onClose={() => {
              setEditDrawerOpen(false);
              setEditingSetting(null);
            }}
            onSave={handleSaveSetting}
            onDelete={handleDeleteSetting}
          />
        </div>
      </StateManagerProvider>
    </DataViewProvider>
  );
}

'use client';

import { Settings as SettingsIcon, Building, Palette, Shield, Bell, Link, FileText, Database, Plus, Download, RefreshCw } from "lucide-react";
import { useState, useEffect, useCallback, useMemo } from 'react';
import {
  DataViewProvider,
  StateManagerProvider,
  Button,
  useToastContext,
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
} from '@ghxstship/ui';
import type {
  SettingRecord,
  SettingsViewConfig,
  SettingsStatistics,
  SettingsSearchParams,
} from './types';
import { settingsService } from './lib/settings-service';
import SettingsGridView from './views/SettingsGridView';
import SettingsListView from './views/SettingsListView';
import SettingsKanbanView from './views/SettingsKanbanView';
import CreateSettingsDrawer from './drawers/CreateSettingsDrawer';
import EditSettingsDrawer from './drawers/EditSettingsDrawer';
import { SETTINGS_FIELD_CONFIG } from './lib/field-config';

interface SettingsClientProps {
  orgId: string;
  userId: string;
}

export default function SettingsClient({ orgId, userId }: SettingsClientProps) {
  const { toast } = useToastContext();

  // State management
  const [settings, setSettings] = useState<SettingRecord[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedSettings, setSelectedSettings] = useState<string[]>([]);
  const [statistics, setStatistics] = useState<SettingsStatistics | null>(null);

  // View state
  const [currentView, setCurrentView] = useState<'grid' | 'list' | 'kanban'>('grid');
  const [searchParams, setSearchParams] = useState<SettingsSearchParams>({});
  const [activeTab, setActiveTab] = useState('data');

  // Drawer state
  const [createDrawerOpen, setCreateDrawerOpen] = useState(false);
  const [editDrawerOpen, setEditDrawerOpen] = useState(false);
  const [editingSetting, setEditingSetting] = useState<SettingRecord | null>(null);
  const [drawerMode, setDrawerMode] = useState<'create' | 'edit' | 'view'>('create');

  // Load settings data
  const loadSettings = useCallback(
    async (params?: SettingsSearchParams) => {
      try {
        setLoading(true);
        setError(null);
        const data = await settingsService.getSettings(params);
        setSettings(data);
      } catch (err) {
        const message = err instanceof Error ? err.message : 'Failed to load settings';
        setError(message);
        toast.error(message);
      } finally {
        setLoading(false);
      }
    },
    [toast]
  );

  // Load statistics
  const loadStatistics = useCallback(async () => {
    try {
      const stats = await settingsService.getStatistics();
      setStatistics(stats);
    } catch (err) {
      console.error('Failed to load statistics:', err);
    }
  }, []);

  // Initial data load
  useEffect(() => {
    loadSettings();
    loadStatistics();
  }, [loadSettings, loadStatistics]);

  // ATLVS DataViews configuration
  const dataViewConfig: SettingsViewConfig = useMemo(
    () => ({
      id: 'settings',
      name: 'Settings Management',
      viewType: currentView,
      defaultView: 'grid',
      fields: SETTINGS_FIELD_CONFIG,
      data: settings,
      onSearch: async (query: string) => {
        const params = { ...searchParams, query };
        setSearchParams(params);
        await loadSettings(params);
      },
      onFilter: async (filters: unknown) => {
        const params = { ...searchParams, ...filters };
        setSearchParams(params);
        await loadSettings(params);
      },
      onSort: async (sorts: unknown) => {
        console.log('Sort settings:', sorts);
      },
      onRefresh: async () => {
        await loadSettings(searchParams);
        await loadStatistics();
        return settings;
      },
      onExport: (data: any, format: unknown) => {
        handleExport(format);
      },
      onImport: (data: unknown) => {
        console.log('Import settings:', data);
        toast.info('Import functionality coming soon');
      },
    }),
    [currentView, settings, searchParams, loadSettings, loadStatistics]
  );

  // Event handlers
  const handleCreateSetting = () => {
    setDrawerMode('create');
    setCreateDrawerOpen(true);
  };

  const handleEditSetting = (setting: SettingRecord) => {
    setEditingSetting(setting);
    setDrawerMode('edit');
    setEditDrawerOpen(true);
  };

  const handleViewSetting = (setting: SettingRecord) => {
    setEditingSetting(setting);
    setDrawerMode('view');
    setEditDrawerOpen(true);
  };

  const handleDeleteSetting = async (id: string) => {
    try {
      await settingsService.deleteSetting(id);
      toast.success('Setting deleted successfully');
      await loadSettings(searchParams);
      await loadStatistics();
    } catch (err) {
      const message = err instanceof Error ? err.message : 'Failed to delete setting';
      toast.error(message);
    }
  };

  const handleSaveSetting = async (data: unknown) => {
    if (!editingSetting) return;

    try {
      await settingsService.updateSetting(editingSetting.id, data);
      await loadSettings(searchParams);
      await loadStatistics();
    } catch (err) {
      throw err;
    }
  };

  const handleMoveSetting = async (settingId: string, newCategory: unknown) => {
    try {
      await settingsService.updateSetting(settingId, { category: newCategory });
      toast.success('Setting moved successfully');
      await loadSettings(searchParams);
    } catch (err) {
      const message = err instanceof Error ? err.message : 'Failed to move setting';
      toast.error(message);
    }
  };

  const handleExport = async (format: 'csv' | 'json') => {
    try {
      const blob = await settingsService.exportSettings({
        format,
        includeMetadata: true,
        categories: searchParams.category ? [searchParams.category] : undefined,
      });

      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `settings-export.${format}`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);

      toast.success(`Settings exported as ${format.toUpperCase()}`);
    } catch (err) {
      const message = err instanceof Error ? err.message : 'Failed to export settings';
      toast.error(message);
    }
  };

  const handleRefresh = async () => {
    await loadSettings(searchParams);
    await loadStatistics();
  };

  // View switcher component
  const ViewSwitcher = () => (
    <div className="flex items-center gap-2">
      <Button
        variant={currentView === 'grid' ? 'default' : 'outline'}
        size="sm"
        onClick={() => setCurrentView('grid')}
      >
        Grid
      </Button>
      <Button
        variant={currentView === 'list' ? 'default' : 'outline'}
        size="sm"
        onClick={() => setCurrentView('list')}
      >
        List
      </Button>
      <Button
        variant={currentView === 'kanban' ? 'default' : 'outline'}
        size="sm"
        onClick={() => setCurrentView('kanban')}
      >
        Kanban
      </Button>
    </div>
  );

  // Data actions component
  const DataActions = () => (
    <div className="flex items-center gap-2">
      {selectedSettings.length > 0 && (
        <Button
          variant="destructive"
          size="sm"
          onClick={() => {
            if (window.confirm(`Delete ${selectedSettings.length} settings?`)) {
              Promise.all(selectedSettings.map((id) => settingsService.deleteSetting(id)))
                .then(() => {
                  toast.success(`Deleted ${selectedSettings.length} settings`);
                  setSelectedSettings([]);
                  loadSettings(searchParams);
                  loadStatistics();
                })
                .catch(() => {
                  toast.error('Failed to delete some settings');
                });
            }
          }}
        >
          Delete ({selectedSettings.length})
        </Button>
      )}
      <Button variant="outline" size="sm" onClick={() => handleExport('csv')}>
        Export
      </Button>
    </div>
  );

  // Render current view
  const renderCurrentView = () => {
    switch (currentView) {
      case 'grid':
        return (
          <SettingsGridView
            settings={settings}
            loading={loading}
            onEdit={handleEditSetting}
            onDelete={handleDeleteSetting}
            onSelect={setSelectedSettings}
            selectedIds={selectedSettings}
          />
        );
      case 'list':
        return (
          <SettingsListView
            settings={settings}
            loading={loading}
            onEdit={handleEditSetting}
            onView={handleViewSetting}
          />
        );
      case 'kanban':
        return (
          <SettingsKanbanView
            settings={settings}
            loading={loading}
            onEdit={handleEditSetting}
            onMove={handleMoveSetting}
          />
        );
      default:
        return null;
    }
  };

  if (error) {
    return (
      <div className="flex items-center justify-center h-96">
        <div className="text-center">
          <p className="text-destructive mb-4">{error}</p>
          <Button onClick={() => loadSettings()}>
            <RefreshCw className="h-4 w-4 mr-2" />
            Retry
          </Button>
        </div>
      </div>
    );
  }

  return (
    <DataViewProvider config={dataViewConfig}>
      <StateManagerProvider>
        <div className="space-y-6">
          {/* Header */}
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold">Settings Management</h1>
              <p className="text-muted-foreground mt-1">
                Manage your organization's configuration settings and preferences
              </p>
            </div>
            <div className="flex items-center gap-3">
              <Button variant="outline" onClick={handleRefresh} disabled={loading}>
                <RefreshCw className="h-4 w-4 mr-2" />
                Refresh
              </Button>
              <Button variant="outline" onClick={() => handleExport('csv')}>
                <Download className="h-4 w-4 mr-2" />
                Export
              </Button>
              <Button onClick={handleCreateSetting}>
                <Plus className="h-4 w-4 mr-2" />
                Create Setting
              </Button>
            </div>
          </div>

          {/* Statistics */}
          {statistics && (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              <div className="bg-card p-4 rounded-lg border">
                <div className="text-2xl font-bold">{statistics.totalSettings}</div>
                <div className="text-sm text-muted-foreground">Total Settings</div>
              </div>
              <div className="bg-card p-4 rounded-lg border">
                <div className="text-2xl font-bold">{statistics.publicSettings}</div>
                <div className="text-sm text-muted-foreground">Public Settings</div>
              </div>
              <div className="bg-card p-4 rounded-lg border">
                <div className="text-2xl font-bold">{statistics.editableSettings}</div>
                <div className="text-sm text-muted-foreground">Editable Settings</div>
              </div>
              <div className="bg-card p-4 rounded-lg border">
                <div className="text-2xl font-bold">{statistics.recentlyUpdated}</div>
                <div className="text-sm text-muted-foreground">Updated This Week</div>
              </div>
            </div>
          )}

          {/* Tab Navigation */}
          <Tabs value={activeTab} onValueChange={setActiveTab}>
            <TabsList className="grid w-full grid-cols-8">
              <TabsTrigger value="data">
                <Database className="h-4 w-4 mr-2" />
                Data Management
              </TabsTrigger>
              <TabsTrigger value="organization">
                <Building className="h-4 w-4 mr-2" />
                Organization
              </TabsTrigger>
              <TabsTrigger value="security">
                <Shield className="h-4 w-4 mr-2" />
                Security
              </TabsTrigger>
              <TabsTrigger value="notifications">
                <Bell className="h-4 w-4 mr-2" />
                Notifications
              </TabsTrigger>
              <TabsTrigger value="integrations">
                <Link className="h-4 w-4 mr-2" />
                Integrations
              </TabsTrigger>
              <TabsTrigger value="billing">
                <FileText className="h-4 w-4 mr-2" />
                Billing
              </TabsTrigger>
              <TabsTrigger value="permissions">
                <Shield className="h-4 w-4 mr-2" />
                Permissions
              </TabsTrigger>
              <TabsTrigger value="advanced">
                <SettingsIcon className="h-4 w-4 mr-2" />
                Advanced
              </TabsTrigger>
            </TabsList>

            <TabsContent value="data" className="space-y-4">
              {/* View Controls */}
              <div className="flex items-center justify-between">
                <ViewSwitcher />
                <DataActions />
              </div>

              {/* Main Content */}
              {renderCurrentView()}
            </TabsContent>

            {/* Legacy Settings Tabs - Placeholder for backward compatibility */}
            <TabsContent value="organization">
              <div className="bg-card p-8 rounded-lg border text-center">
                <Building className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                <h3 className="text-lg font-semibold mb-2">Organization Settings</h3>
                <p className="text-muted-foreground mb-4">
                  Organization settings are now managed through the data management interface above.
                </p>
                <Button onClick={() => setActiveTab('data')}>
                  Go to Data Management
                </Button>
              </div>
            </TabsContent>

            <TabsContent value="security">
              <div className="bg-card p-8 rounded-lg border text-center">
                <Shield className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                <h3 className="text-lg font-semibold mb-2">Security Settings</h3>
                <p className="text-muted-foreground mb-4">
                  Security settings are now managed through the data management interface above.
                </p>
                <Button onClick={() => setActiveTab('data')}>
                  Go to Data Management
                </Button>
              </div>
            </TabsContent>

            <TabsContent value="notifications">
              <div className="bg-card p-8 rounded-lg border text-center">
                <Bell className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                <h3 className="text-lg font-semibold mb-2">Notification Settings</h3>
                <p className="text-muted-foreground mb-4">
                  Notification settings are now managed through the data management interface above.
                </p>
                <Button onClick={() => setActiveTab('data')}>
                  Go to Data Management
                </Button>
              </div>
            </TabsContent>

            <TabsContent value="integrations">
              <div className="bg-card p-8 rounded-lg border text-center">
                <Link className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                <h3 className="text-lg font-semibold mb-2">Integration Settings</h3>
                <p className="text-muted-foreground mb-4">
                  Integration settings are now managed through the data management interface above.
                </p>
                <Button onClick={() => setActiveTab('data')}>
                  Go to Data Management
                </Button>
              </div>
            </TabsContent>

            <TabsContent value="billing">
              <div className="bg-card p-8 rounded-lg border text-center">
                <FileText className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                <h3 className="text-lg font-semibold mb-2">Billing Settings</h3>
                <p className="text-muted-foreground mb-4">
                  Billing settings are now managed through the data management interface above.
                </p>
                <Button onClick={() => setActiveTab('data')}>
                  Go to Data Management
                </Button>
              </div>
            </TabsContent>

            <TabsContent value="permissions">
              <div className="bg-card p-8 rounded-lg border text-center">
                <Shield className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                <h3 className="text-lg font-semibold mb-2">Permission Settings</h3>
                <p className="text-muted-foreground mb-4">
                  Permission settings are now managed through the data management interface above.
                </p>
                <Button onClick={() => setActiveTab('data')}>
                  Go to Data Management
                </Button>
              </div>
            </TabsContent>

            <TabsContent value="advanced">
              <div className="bg-card p-8 rounded-lg border text-center">
                <SettingsIcon className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                <h3 className="text-lg font-semibold mb-2">Advanced Settings</h3>
                <p className="text-muted-foreground mb-4">
                  Advanced settings are now managed through the data management interface above.
                </p>
                <Button onClick={() => setActiveTab('data')}>
                  Go to Data Management
                </Button>
              </div>
            </TabsContent>
          </Tabs>

          {/* Drawers */}
          <CreateSettingsDrawer
            isOpen={createDrawerOpen}
            onClose={() => setCreateDrawerOpen(false)}
            onSuccess={() => {
              loadSettings(searchParams);
              loadStatistics();
            }}
          />

          <EditSettingsDrawer
            mode={drawerMode}
            setting={editingSetting ?? undefined}
            isOpen={editDrawerOpen}
            onClose={() => {
              setEditDrawerOpen(false);
              setEditingSetting(null);
            }}
            onSave={handleSaveSetting}
            onDelete={handleDeleteSetting}
          />
        </div>
      </StateManagerProvider>
    </DataViewProvider>
  );
}

