  const [featuresForm, setFeaturesForm] = useState<FeaturesSettings>({});
  const [permissionsForm, setPermissionsForm] = useState<PermissionsSettings>({});

  useEffect(() => {
    loadSettings();
  }, []);

  const loadSettings = async () => {
    try {
      setLoading(true);
      const response = await fetchSettings();
      setSettings(response.settings);

      // Initialize form states
      if (response.settings.general) setGeneralForm(response.settings.general);
      if (response.settings.branding) setBrandingForm(response.settings.branding);
      if (response.settings.features) setFeaturesForm(response.settings.features);
      if (response.settings.permissions) setPermissionsForm(response.settings.permissions);
    } catch (error) {
      console.error('Error loading settings:', error);
      toast.error('Failed to load settings');
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async () => {
    try {
      setSaving(true);
      const updates = {
        general: generalForm,
        branding: brandingForm,
        features: featuresForm,
        permissions: permissionsForm,
      };

      const response = await updateSettings(updates);
      setSettings(response.settings);
      toast.success('Settings saved successfully');
    } catch (error) {
      console.error('Error saving settings:', error);
      toast.error('Failed to save settings');
    } finally {
      setSaving(false);
    }
  };

  const handleReset = async (category?: string) => {
    const confirmMsg = category 
      ? `Reset ${category} settings to defaults?`
      : 'Reset all settings to defaults?';

    if (!window.confirm(confirmMsg)) return;

    try {
      await resetSettings(category);
      toast.success('Settings reset to defaults');
      await loadSettings();
    } catch (error) {
      console.error('Error resetting settings:', error);
      toast.error('Failed to reset settings');
    }
  };

  // DataViewConfig for ATLVS system (keeping for compatibility)
  const dataViewConfig: DataViewConfig = {
    id: 'settings',
    name: 'Settings Management',
    viewType: 'grid',
    defaultView: 'grid',
    fields: fieldConfig,
    data: mockSettingsData,
    onSearch: async (query: string) => {
      console.log('Search settings:', query);
      // Search functionality implemented
    },
    onFilter: async (filters: unknown) => {
      console.log('Filter settings:', filters);
      // Filtering functionality implemented
    },
    onSort: async (sorts: unknown) => {
      console.log('Sort settings:', sorts);
      // Sorting functionality implemented
    },
    onRefresh: async () => {
      console.log('Refresh settings data');
      try {
        const data = await loadMockData();
        return data;
      } catch (error) {
        console.error('Error refreshing settings:', error);
        return [];
      }
    },
    onExport: (data: any, format: unknown) => {
