export default function AccountClient({ userId, orgId }: AccountClientProps) {
  const { toast } = useToastContext();

  // State management
  const [records, setRecords] = useState<AccountRecord[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedRecords, setSelectedRecords] = useState<string[]>([]);
  const [statistics, setStatistics] = useState<AccountStatistics | null>(null);

  // View state
  const [currentView, setCurrentView] = useState<'grid' | 'list'>('grid');
  const [searchParams, setSearchParams] = useState<AccountSearchParams>({});
  const [activeTab, setActiveTab] = useState('overview');

  // Drawer state
  const [editDrawerOpen, setEditDrawerOpen] = useState(false);
  const [editingRecord, setEditingRecord] = useState<AccountRecord | null>(null);
  const [drawerMode, setDrawerMode] = useState<'create' | 'edit' | 'view'>('view');

  // Load account records
  const loadRecords = useCallback(async (params?: AccountSearchParams) => {
    try {
      setLoading(true);
      setError(null);
      const data = await accountService.getAccountRecords(params);
      setRecords(data);
    } catch (err) {
      const message = err instanceof Error ? err.message : 'Failed to load account records';
      setError(message);
      toast.error(message);
    } finally {
      setLoading(false);
    }
  }, [toast]);

  // Load statistics
  const loadStatistics = useCallback(async () => {
    try {
      const stats = await accountService.getStatistics();
      setStatistics(stats);
    } catch (err) {
      console.error('Failed to load statistics:', err);
    }
  }, []);

  // Initial data load
  useEffect(() => {
    loadRecords();
    loadStatistics();
  }, [loadRecords, loadStatistics]);

  // ATLVS DataViews configuration
  const dataViewConfig: AccountViewConfig = useMemo(() => ({
    id: 'account',
    name: 'Account Management',
    viewType: currentView,
    defaultView: 'grid',
    fields: fieldConfig,
    data: records,
    onSearch: async (query: string) => {
      const params = { ...searchParams, query };
      setSearchParams(params);
      await loadRecords(params);
    },
    onFilter: async (filters: unknown) => {
      const params = { ...searchParams, ...filters };
      setSearchParams(params);
      await loadRecords(params);
    },
    onSort: async (sorts: unknown) => {
      console.log('Sort records:', sorts);
    },
    onRefresh: async () => {
      await loadRecords(searchParams);
      await loadStatistics();
      return records;
    },
    onExport: (data: any, format: unknown) => {
      handleExport(format);
    },
  }), [currentView, records, searchParams, loadRecords, loadStatistics]);

  // Event handlers
  const handleEditRecord = (record: AccountRecord) => {
    setEditingRecord(record);
    setDrawerMode('edit');
    setEditDrawerOpen(true);
  };

  const handleViewRecord = (record: AccountRecord) => {
    setEditingRecord(record);
    setDrawerMode('view');
    setEditDrawerOpen(true);
  };

  const handleDeleteRecord = async (id: string) => {
    try {
      // Handle different record types
      const record = records.find(r => r.id === id);
      if (!record) return;

      if (record.type === 'session') {
        await accountService.revokeSession(record.metadata.id);
      } else if (record.type === 'api_key') {
        await accountService.revokeApiKey(record.metadata.id);
      }

      toast.success('Record deleted successfully');
      await loadRecords(searchParams);
      await loadStatistics();
    } catch (err) {
      const message = err instanceof Error ? err.message : 'Failed to delete record';
      toast.error(message);
    }
  };

  const handleExport = async (format: 'csv' | 'json') => {
    try {
      const blob = await accountService.exportRecords({
        format,
        includeMetadata: true,
      });

      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `account-records.${format}`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);

      toast.success(`Records exported as ${format.toUpperCase()}`);
    } catch (err) {
      const message = err instanceof Error ? err.message : 'Failed to export records';
      toast.error(message);
    }
  };

  const handleRefresh = async () => {
    await loadRecords(searchParams);
    await loadStatistics();
  };

  // View switcher component
  const ViewSwitcher = () => (
    <div className="flex items-center gap-2">
      <Button
        variant={currentView === 'grid' ? 'default' : 'outline'}
        size="sm"
        onClick={() => setCurrentView('grid')}
      >
        Grid
      </Button>
      <Button
        variant={currentView === 'list' ? 'default' : 'outline'}
        size="sm"
        onClick={() => setCurrentView('list')}
      >
        List
      </Button>
    </div>
  );

  // Data actions component
  const DataActions = () => (
    <div className="flex items-center gap-2">
      {selectedRecords.length > 0 && (
        <Button
          variant="destructive"
          size="sm"
          onClick={() => {
            if (window.confirm(`Delete ${selectedRecords.length} records?`)) {
              Promise.all(selectedRecords.map(id => handleDeleteRecord(id)))
                .then(() => {
                  toast.success(`Deleted ${selectedRecords.length} records`);
                  setSelectedRecords([]);
                })
                .catch(() => {
                  toast.error('Failed to delete some records');
                });
            }
          }}
        >
          Delete ({selectedRecords.length})
        </Button>
      )}
      <Button variant="outline" size="sm" onClick={() => handleExport('csv')}>
        Export
      </Button>
    </div>
  );

  // Render current view
  const renderCurrentView = () => {
    switch (currentView) {
      case 'grid':
        return (
          <AccountGridView
            records={records}
            loading={loading}
            onEdit={handleEditRecord}
            onDelete={handleDeleteRecord}
            onSelect={setSelectedRecords}
            selectedIds={selectedRecords}
          />
        );
      case 'list':
        return (
          <AccountListView
            records={records}
            loading={loading}
            onEdit={handleEditRecord}
            onView={handleViewRecord}
          />
        );
      default:
        return null;
    }
  };

  if (error) {
    return (
      <div className="flex items-center justify-center h-96">
        <div className="text-center">
          <p className="text-destructive mb-4">{error}</p>
          <Button onClick={() => loadRecords()}>
            <RefreshCw className="h-4 w-4 mr-2" />
            Retry
          </Button>
        </div>
      </div>
    );
  }

  return (
    <DataViewProvider config={dataViewConfig}>
      <StateManagerProvider>
        <div className="space-y-6">
          {/* Header */}
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold">Account Settings</h1>
              <p className="text-muted-foreground mt-1">
                Manage your profile, security, sessions, and API keys
              </p>
            </div>
            <div className="flex items-center gap-3">
              <Button variant="outline" onClick={handleRefresh} disabled={loading}>
                <RefreshCw className="h-4 w-4 mr-2" />
                Refresh
              </Button>
              <Button variant="outline" onClick={() => handleExport('csv')}>
                <Download className="h-4 w-4 mr-2" />
                Export
              </Button>
            </div>
          </div>

          {/* Statistics */}
          {statistics && (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              <div className="bg-card p-4 rounded-lg border">
                <div className="flex items-center gap-2 mb-2">
                  <Globe className="h-5 w-5 text-blue-600" />
                  <div className="text-sm font-medium">Active Sessions</div>
                </div>
                <div className="text-2xl font-bold">{statistics.activeSessions}</div>
                <div className="text-xs text-muted-foreground">
                  of {statistics.totalSessions} total
                </div>
              </div>
              <div className="bg-card p-4 rounded-lg border">
                <div className="flex items-center gap-2 mb-2">
                  <Key className="h-5 w-5 text-purple-600" />
                  <div className="text-sm font-medium">API Keys</div>
                </div>
                <div className="text-2xl font-bold">{statistics.activeApiKeys}</div>
                <div className="text-xs text-muted-foreground">
                  of {statistics.totalApiKeys} total
                </div>
              </div>
              <div className="bg-card p-4 rounded-lg border">
                <div className="flex items-center gap-2 mb-2">
                  <Shield className="h-5 w-5 text-green-600" />
                  <div className="text-sm font-medium">Security Score</div>
                </div>
                <div className="text-2xl font-bold">{statistics.securityScore}%</div>
                <div className="text-xs text-muted-foreground">
                  Account security rating
                </div>
              </div>
              <div className="bg-card p-4 rounded-lg border">
                <div className="flex items-center gap-2 mb-2">
                  <User className="h-5 w-5 text-orange-600" />
                  <div className="text-sm font-medium">Account Age</div>
                </div>
                <div className="text-2xl font-bold">{statistics.accountAge}</div>
                <div className="text-xs text-muted-foreground">
                  days since creation
                </div>
              </div>
            </div>
          )}

          {/* Tab Navigation */}
          <Tabs value={activeTab} onValueChange={setActiveTab}>
            <TabsList className="grid w-full grid-cols-4">
              <TabsTrigger value="overview">
                <User className="h-4 w-4 mr-2" />
                Overview
              </TabsTrigger>
              <TabsTrigger value="security">
                <Shield className="h-4 w-4 mr-2" />
                Security
              </TabsTrigger>
              <TabsTrigger value="sessions">
                <Globe className="h-4 w-4 mr-2" />
                Sessions
              </TabsTrigger>
              <TabsTrigger value="api">
                <Key className="h-4 w-4 mr-2" />
                API Keys
              </TabsTrigger>
            </TabsList>

            <TabsContent value="overview" className="space-y-4">
              {/* View Controls */}
              <div className="flex items-center justify-between">
                <ViewSwitcher />
                <DataActions />
              </div>

              {/* Main Content */}
              {renderCurrentView()}
            </TabsContent>

            <TabsContent value="security" className="space-y-4">
              <div className="bg-card p-8 rounded-lg border text-center">
                <Shield className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                <h3 className="text-lg font-semibold mb-2">Security Settings</h3>
                <p className="text-muted-foreground mb-4">
                  Security settings are managed through the overview interface above.
                </p>
                <Button onClick={() => setActiveTab('overview')}>
                  Go to Overview
                </Button>
              </div>
            </TabsContent>

            <TabsContent value="sessions" className="space-y-4">
              <div className="bg-card p-8 rounded-lg border text-center">
                <Globe className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                <h3 className="text-lg font-semibold mb-2">Session Management</h3>
                <p className="text-muted-foreground mb-4">
                  Session management is available through the overview interface above.
                </p>
                <Button onClick={() => setActiveTab('overview')}>
                  Go to Overview
                </Button>
              </div>
            </TabsContent>

            <TabsContent value="api" className="space-y-4">
              <div className="bg-card p-8 rounded-lg border text-center">
                <Key className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                <h3 className="text-lg font-semibold mb-2">API Key Management</h3>
                <p className="text-muted-foreground mb-4">
                  API key management is available through the overview interface above.
                </p>
                <Button onClick={() => setActiveTab('overview')}>
                  Go to Overview
                </Button>
              </div>
            </TabsContent>
          </Tabs>

          {/* Drawers */}
          <EditAccountDrawer
            mode={drawerMode}
            record={editingRecord}
            isOpen={editDrawerOpen}
            onClose={() => {
              setEditDrawerOpen(false);
              setEditingRecord(null);
            }}
            onSave={async () => {
              await loadRecords(searchParams);
              await loadStatistics();
            }}
            onDelete={handleDeleteRecord}
          />
        </div>
      </StateManagerProvider>
    </DataViewProvider>
  );
}

'use client';

import { User, Lock, Shield, Smartphone, Key, Globe, Clock, AlertCircle } from "lucide-react";
import { useState, useEffect } from 'react';
import {
  Card,
  Button,
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
  Badge,
  Alert,
  Loader,
  Input,
  Label,
  useToastContext,
} from '@ghxstship/ui';
import type { User as SupabaseUser } from '@supabase/supabase-js';
import useSupabaseBrowserClient from '@/app/_hooks/useSupabaseBrowserClient';
import {
  fetchSessions,
  revokeSession as revokeSessionApi,
  revokeAllSessions as revokeAllSessionsApi,
  fetchApiKeys,
  createApiKey as createApiKeyApi,
  revokeApiKey as revokeApiKeyApi,
  setupTwoFactor,
  verifyTwoFactor,
  type ApiKeySummary,
  type UserSessionSummary,
} from '@/lib/services/settingsAccountClient';

interface UserProfile {
  id: string;
  email: string;
  name: string;
  avatar_url?: string;
  phone?: string;
  timezone?: string;
  language?: string;
  created_at: string;
  updated_at: string;
}

interface UserSession {
  id: string;
  ipAddress: string;
  userAgent: string;
  deviceInfo?: any;
  location?: {
    country?: string;
    city?: string;
    region?: string;
  };
  isActive: boolean;
  isCurrent: boolean;
  lastActivityAt: string;
  expiresAt: string;
  createdAt: string;
}

interface ApiKey {
  id: string;
  name: string;
  description?: string;
  scopes: string[];
  lastUsedAt?: string;
  expiresAt?: string;
  isActive: boolean;
  createdAt: string;
  keyPrefix: string;
}

export default function AccountSettingsClient() {
  const supabase = useSupabaseBrowserClient();
  const { toast } = useToastContext();
  const [user, setUser] = useState<SupabaseUser | null>(null);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [activeTab, setActiveTab] = useState('profile');

  // Profile state
  const [profile, setProfile] = useState<UserProfile | null>(null);
  const [profileForm, setProfileForm] = useState({
    name: '',
    phone: '',
    timezone: 'UTC',
    language: 'en'
  });

  // Password state
  const [passwordForm, setPasswordForm] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  // 2FA state
  const [twoFactorEnabled, setTwoFactorEnabled] = useState(false);
  const [twoFactorQR, setTwoFactorQR] = useState('');
  const [verificationCode, setVerificationCode] = useState('');

  // Sessions state
  const [sessions, setSessions] = useState<UserSessionSummary[]>([]);
  const [loadingSessions, setLoadingSessions] = useState(false);

  // API Keys state
  const [apiKeys, setApiKeys] = useState<ApiKeySummary[]>([]);
  const [loadingApiKeys, setLoadingApiKeys] = useState(false);
  const [showCreateApiKey, setShowCreateApiKey] = useState(false);
  const [newApiKeyForm, setNewApiKeyForm] = useState<{
    name: string;
    description: string;
    scopes: string[];
    expiresIn: string;
  }>({
    name: '',
    description: '',
    scopes: [],
    expiresIn: '90'
  });

  useEffect(() => {
    const loadUser = async () => {
      const { data: { user: currentUser } } = await supabase.auth.getUser();
      setUser(currentUser);
      if (currentUser) {
        loadProfile();
        loadSessions();
        loadApiKeys();
      }
    };
    loadUser();
  }, []);

  const loadProfile = async () => {
    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('users')
        .select('*')
        .eq('id', user?.id)
        .single();

      if (error) throw error;

      setProfile(data);
      setProfileForm({
        name: data.name || '',
        phone: data.phone || '',
        timezone: data.timezone || 'UTC',
        language: data.language || 'en'
      });

      // Check 2FA status
      const { data: securitySettings } = await supabase
        .from('security_settings')
        .select('two_factor_enabled')
        .eq('user_id', user?.id)
        .single();

      if (securitySettings) {
        setTwoFactorEnabled(securitySettings.two_factor_enabled);
      }
    } catch (error) {
      console.error('Error loading profile:', error);
      toast.error('Failed to load profile settings');
    } finally {
      setLoading(false);
    }
  };

  const loadSessions = async () => {
    try {
      setLoadingSessions(true);
      const data = await fetchSessions();
      setSessions(data);
    } catch (error) {
      console.error('Error loading sessions:', error);
      toast.error('Failed to load active sessions');
    } finally {
      setLoadingSessions(false);
    }
  };

  const loadApiKeys = async () => {
    try {
      setLoadingApiKeys(true);
      const data = await fetchApiKeys();
      setApiKeys(data);
    } catch (error) {
      console.error('Error loading API keys:', error);
      toast.error('Failed to load API keys');
    } finally {
      setLoadingApiKeys(false);
    }
  };

  const updateProfile = async () => {
    try {
      setSaving(true);
      const { error } = await supabase
        .from('users')
        .update({
          name: profileForm.name,
          phone: profileForm.phone,
          timezone: profileForm.timezone,
          language: profileForm.language,
          updated_at: new Date().toISOString()
        })
        .eq('id', user?.id);

      if (error) throw error;

      toast.success('Profile updated successfully');

      loadProfile();
    } catch (error) {
      console.error('Error updating profile:', error);
      toast.error('Failed to update profile');
    } finally {
      setSaving(false);
    }
  };

  const updatePassword = async () => {
    if (passwordForm.newPassword !== passwordForm.confirmPassword) {
      toast.error('Passwords do not match');
      return;
    }

    try {
      setSaving(true);
      const { error } = await supabase.auth.updateUser({
        password: passwordForm.newPassword
      });

      if (error) throw error;

      toast.success('Password updated successfully');

      setPasswordForm({
        currentPassword: '',
        newPassword: '',
        confirmPassword: ''
      });
    } catch (error) {
      console.error('Error updating password:', error);
      toast.error('Failed to update password');
    } finally {
      setSaving(false);
    }
  };

  const enable2FA = async () => {
    try {
      setSaving(true);
      const data = await setupTwoFactor();
      setTwoFactorQR(data.qrCode);
    } catch (error) {
      console.error('Error enabling 2FA:', error);
      toast.error('Failed to enable 2FA');
    } finally {
      setSaving(false);
    }
  };

  const verify2FA = async () => {
    try {
      setSaving(true);
      const result = await verifyTwoFactor(verificationCode);
      setTwoFactorEnabled(true);
      setTwoFactorQR('');
      setVerificationCode('');

      toast.success('2FA enabled successfully', undefined, {
        description: `Backup codes: ${result.backupCodes.join(', ')}`
      });
    } catch (error) {
      console.error('Error verifying 2FA:', error);
      toast.error('Invalid verification code');
    } finally {
      setSaving(false);
    }
  };

  const revokeSession = async (sessionId: string) => {
    try {
      await revokeSessionApi(sessionId);

      toast.success('Session revoked successfully');

      loadSessions();
    } catch (error) {
      console.error('Error revoking session:', error);
      toast.error('Failed to revoke session');
    }
  };

  const revokeAllSessions = async () => {
    try {
      const count = await revokeAllSessionsApi();
      toast.success(`Revoked ${count} sessions`);

      loadSessions();
    } catch (error) {
      console.error('Error revoking all sessions:', error);
      toast.error('Failed to revoke sessions');
    }
  };

  const createApiKey = async () => {
    try {
      setSaving(true);
      const expiresDays = parseInt(newApiKeyForm.expiresIn, 10);
      const expiresAt = expiresDays > 0
        ? new Date(Date.now() + expiresDays * 24 * 60 * 60 * 1000).toISOString()
        : undefined;

      const data = await createApiKeyApi({
        name: newApiKeyForm.name,
        description: newApiKeyForm.description,
        scopes: newApiKeyForm.scopes,
        expiresAt
      });

      toast.success('API Key Created', undefined, {
        description: `Store this key securely: ${data.apiKey.key}`
      });

      setShowCreateApiKey(false);
      setNewApiKeyForm({
        name: '',
        description: '',
        scopes: [],
        expiresIn: '90'
      });

      loadApiKeys();
    } catch (error) {
      console.error('Error creating API key:', error);
      toast.error('Failed to create API key');
    } finally {
      setSaving(false);
    }
  };

  const revokeApiKey = async (keyId: string) => {
    try {
      await revokeApiKeyApi(keyId);

      toast.success('API key revoked successfully');

      loadApiKeys();
    } catch (error) {
      console.error('Error revoking API key:', error);
      toast.error('Failed to revoke API key');
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-96">
        <Loader className="h-8 w-8 animate-spin" />
      </div>
    );
  }

  return (
    <div className="container max-w-6xl mx-auto p-6 space-y-6">
      <div>
        <h1 className="text-3xl font-bold">Account Settings</h1>
        <p className="text-muted-foreground mt-2">
          Manage your personal account settings, security, and API access
        </p>
      </div>

      <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-6">
        <TabsList className="grid w-full grid-cols-5">
          <TabsTrigger value="profile">
            <User className="h-4 w-4 mr-2" />
            Profile
          </TabsTrigger>
          <TabsTrigger value="password">
            <Lock className="h-4 w-4 mr-2" />
            Password
          </TabsTrigger>
          <TabsTrigger value="2fa">
            <Shield className="h-4 w-4 mr-2" />
            2FA
          </TabsTrigger>
          <TabsTrigger value="sessions">
            <Smartphone className="h-4 w-4 mr-2" />
            Sessions
          </TabsTrigger>
          <TabsTrigger value="api-keys">
            <Key className="h-4 w-4 mr-2" />
            API Keys
          </TabsTrigger>
        </TabsList>

        <TabsContent value="profile" className="space-y-6">
          <Card>
            <div className="p-6">
              <h2 className="text-xl font-semibold mb-4">Profile Information</h2>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="email">Email</Label>
                  <Input
                    id="email"
                    type="email"
                    value={profile?.email || ''}
                    disabled
                    className="bg-muted"
                  />
                </div>
                <div>
                  <Label htmlFor="name">Full Name</Label>
                  <Input
                    id="name"
                    value={profileForm.name}
                    onChange={(e) => setProfileForm({ ...profileForm, name: e.target.value })}
                    placeholder="Enter your full name"
                  />
                </div>
                <div>
                  <Label htmlFor="phone">Phone Number</Label>
                  <Input
                    id="phone"
                    type="tel"
                    value={profileForm.phone}
                    onChange={(e) => setProfileForm({ ...profileForm, phone: e.target.value })}
                    placeholder="+1 (555) 000-0000"
                  />
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="timezone">Timezone</Label>
                    <select
                      id="timezone"
                      value={profileForm.timezone}
                      onChange={(e) => setProfileForm({ ...profileForm, timezone: e.target.value })}
                      className="w-full px-3 py-2 border rounded-md"
                    >
                      <option value="UTC">UTC</option>
                      <option value="America/New_York">Eastern Time</option>
                      <option value="America/Chicago">Central Time</option>
                      <option value="America/Denver">Mountain Time</option>
                      <option value="America/Los_Angeles">Pacific Time</option>
                    </select>
                  </div>
                  <div>
                    <Label htmlFor="language">Language</Label>
                    <select
                      id="language"
                      value={profileForm.language}
                      onChange={(e) => setProfileForm({ ...profileForm, language: e.target.value })}
                      className="w-full px-3 py-2 border rounded-md"
                    >
                      <option value="en">English</option>
                      <option value="es">Spanish</option>
                      <option value="fr">French</option>
                      <option value="de">German</option>
                      <option value="ja">Japanese</option>
                    </select>
                  </div>
                </div>
                <Button onClick={updateProfile} disabled={saving}>
                  {saving ? 'Saving...' : 'Save Changes'}
                </Button>
              </div>
            </div>
          </Card>
        </TabsContent>

        <TabsContent value="password" className="space-y-6">
          <Card>
            <div className="p-6">
              <h2 className="text-xl font-semibold mb-4">Change Password</h2>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="current-password">Current Password</Label>
                  <Input
                    id="current-password"
                    type="password"
                    value={passwordForm.currentPassword}
                    onChange={(e) => setPasswordForm({ ...passwordForm, currentPassword: e.target.value })}
                    placeholder="Enter current password"
                  />
                </div>
                <div>
                  <Label htmlFor="new-password">New Password</Label>
                  <Input
                    id="new-password"
                    type="password"
                    value={passwordForm.newPassword}
                    onChange={(e) => setPasswordForm({ ...passwordForm, newPassword: e.target.value })}
                    placeholder="Enter new password"
                  />
                </div>
                <div>
                  <Label htmlFor="confirm-password">Confirm New Password</Label>
                  <Input
                    id="confirm-password"
                    type="password"
                    value={passwordForm.confirmPassword}
                    onChange={(e) => setPasswordForm({ ...passwordForm, confirmPassword: e.target.value })}
                    placeholder="Confirm new password"
                  />
                </div>
                <Alert>
                  <AlertCircle className="h-4 w-4" />
                  <div>
                    <p className="font-semibold">Password Requirements</p>
                    <ul className="list-disc list-inside text-sm mt-1">
                      <li>At least 8 characters long</li>
                      <li>Contains uppercase and lowercase letters</li>
                      <li>Contains at least one number</li>
                    </ul>
                  </div>
                </Alert>
                <Button onClick={updatePassword} disabled={saving}>
                  {saving ? 'Updating...' : 'Update Password'}
                </Button>
              </div>
            </div>
          </Card>
        </TabsContent>

        <TabsContent value="2fa" className="space-y-6">
          <Card>
            <div className="p-6">
              <h2 className="text-xl font-semibold mb-4">Two-Factor Authentication</h2>
              {twoFactorEnabled ? (
                <div className="space-y-4">
                  <Alert>
                    <Shield className="h-4 w-4" />
                    <div>
                      <p className="font-semibold">2FA is enabled</p>
                      <p className="text-sm mt-1">
                        Your account is protected with two-factor authentication
                      </p>
                    </div>
                  </Alert>
                  <Button variant="destructive" onClick={() => setTwoFactorEnabled(false)}>
                    Disable 2FA
                  </Button>
                </div>
              ) : (
                <div className="space-y-4">
                  <p className="text-muted-foreground">
                    Add an extra layer of security to your account by enabling two-factor authentication
                  </p>
                  {twoFactorQR ? (
                    <div className="space-y-4">
                      <div className="p-4 bg-muted rounded-lg">
                        <p className="text-sm mb-2">Scan this QR code with your authenticator app:</p>
                        <div className="bg-white p-4 rounded inline-block">
                          {/* QR Code would be displayed here */}
                          <div className="h-32 w-32 bg-gray-200 flex items-center justify-center">
                            QR Code
                          </div>
                        </div>
                      </div>
                      <div>
                        <Label htmlFor="verification-code">Verification Code</Label>
                        <Input
                          id="verification-code"
                          value={verificationCode}
                          onChange={(e) => setVerificationCode(e.target.value)}
                          placeholder="Enter 6-digit code"
                          maxLength={6}
                        />
                      </div>
                      <div className="flex gap-2">
                        <Button onClick={verify2FA} disabled={saving}>
                          {saving ? 'Verifying...' : 'Verify & Enable'}
                        </Button>
                        <Button variant="outline" onClick={() => setTwoFactorQR('')}>
                          Cancel
                        </Button>
                      </div>
                    </div>
                  ) : (
                    <Button onClick={enable2FA} disabled={saving}>
                      {saving ? 'Setting up...' : 'Enable 2FA'}
                    </Button>
                  )}
                </div>
              )}
            </div>
          </Card>
        </TabsContent>

        <TabsContent value="sessions" className="space-y-6">
          <Card>
            <div className="p-6">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-xl font-semibold">Active Sessions</h2>
                <Button variant="destructive" size="sm" onClick={revokeAllSessions}>
                  Revoke All Other Sessions
                </Button>
              </div>
              {loadingSessions ? (
                <div className="flex justify-center py-8">
                  <Loader className="h-6 w-6 animate-spin" />
                </div>
              ) : (
                <div className="space-y-4">
                  {sessions.map((session) => (
                    <div key={session.id} className="border rounded-lg p-4">
                      <div className="flex justify-between items-start">
                        <div className="space-y-1">
                          <div className="flex items-center gap-2">
                            <Smartphone className="h-4 w-4" />
                            <span className="font-medium">
                              {session.deviceInfo?.device || 'Unknown Device'}
                            </span>
                            {session.isCurrent && (
                              <Badge variant="success">Current</Badge>
                            )}
                          </div>
                          <p className="text-sm text-muted-foreground">
                            {session.userAgent}
                          </p>
                          <div className="flex items-center gap-4 text-xs text-muted-foreground">
                            <span className="flex items-center gap-1">
                              <Globe className="h-3 w-3" />
                              {session.ipAddress}
                            </span>
                            {session.location && (
                              <span>
                                {session.location.city}, {session.location.country}
                              </span>
                            )}
                            <span className="flex items-center gap-1">
                              <Clock className="h-3 w-3" />
                              Last active: {new Date(session.lastActivityAt).toLocaleString()}
                            </span>
                          </div>
                        </div>
                        {!session.isCurrent && (
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => revokeSession(session.id)}
                          >
                            Revoke
                          </Button>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </Card>
        </TabsContent>

        <TabsContent value="api-keys" className="space-y-6">
          <Card>
            <div className="p-6">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-xl font-semibold">API Keys</h2>
                <Button onClick={() => setShowCreateApiKey(true)}>
                  Create API Key
                </Button>
              </div>
              {loadingApiKeys ? (
                <div className="flex justify-center py-8">
                  <Loader className="h-6 w-6 animate-spin" />
                </div>
              ) : (
                <div className="space-y-4">
                  {apiKeys.length === 0 ? (
                    <p className="text-muted-foreground text-center py-8">
                      No API keys created yet
                    </p>
                  ) : (
                    apiKeys.map((apiKey) => (
                      <div key={apiKey.id} className="border rounded-lg p-4">
                        <div className="flex justify-between items-start">
                          <div className="space-y-1">
                            <div className="flex items-center gap-2">
                              <span className="font-medium">{apiKey.name}</span>
                              <Badge variant={apiKey.isActive ? 'success' : 'secondary'}>
                                {apiKey.isActive ? 'Active' : 'Inactive'}
                              </Badge>
                            </div>
                            {apiKey.description && (
                              <p className="text-sm text-muted-foreground">
                                {apiKey.description}
                              </p>
                            )}
                            <div className="flex items-center gap-4 text-xs text-muted-foreground">
                              <span>Key: {apiKey.keyPrefix}...</span>
                              {apiKey.lastUsedAt && (
                                <span>Last used: {new Date(apiKey.lastUsedAt).toLocaleString()}</span>
                              )}
                              {apiKey.expiresAt && (
                                <span>Expires: {new Date(apiKey.expiresAt).toLocaleDateString()}</span>
                              )}
                            </div>
                            {apiKey.scopes.length > 0 && (
                              <div className="flex gap-1 mt-2">
                                {apiKey.scopes.map((scope: string) => (
                                  <Badge key={scope} variant="outline" className="text-xs">
                                    {scope}
                                  </Badge>
                                ))}
                              </div>
                            )}
                          </div>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => revokeApiKey(apiKey.id)}
                          >
                            Revoke
                          </Button>
                        </div>
                      </div>
                    ))
                  )}
                </div>
              )}
            </div>
          </Card>

          {showCreateApiKey && (
            <Card>
              <div className="p-6">
                <h3 className="text-lg font-semibold mb-4">Create New API Key</h3>
                <div className="space-y-4">
                  <div>
                    <Label htmlFor="api-key-name">Name</Label>
                    <Input
                      id="api-key-name"
                      value={newApiKeyForm.name}
                      onChange={(e) => setNewApiKeyForm({ ...newApiKeyForm, name: e.target.value })}
                      placeholder="My API Key"
                    />
                  </div>
                  <div>
                    <Label htmlFor="api-key-description">Description (optional)</Label>
                    <Input
                      id="api-key-description"
                      value={newApiKeyForm.description}
                      onChange={(e) => setNewApiKeyForm({ ...newApiKeyForm, description: e.target.value })}
                      placeholder="Used for..."
                    />
                  </div>
                  <div>
                    <Label htmlFor="api-key-expires">Expires In</Label>
                    <select
                      id="api-key-expires"
                      value={newApiKeyForm.expiresIn}
                      onChange={(e) => setNewApiKeyForm({ ...newApiKeyForm, expiresIn: e.target.value })}
                      className="w-full px-3 py-2 border rounded-md"
                    >
                      <option value="30">30 days</option>
                      <option value="60">60 days</option>
                      <option value="90">90 days</option>
                      <option value="180">180 days</option>
                      <option value="365">1 year</option>
                      <option value="0">Never</option>
                    </select>
                  </div>
                  <div className="flex gap-2">
                    <Button onClick={createApiKey} disabled={saving || !newApiKeyForm.name}>
                      {saving ? 'Creating...' : 'Create Key'}
                    </Button>
                    <Button variant="outline" onClick={() => setShowCreateApiKey(false)}>
                      Cancel
                    </Button>
                  </div>
                </div>
              </div>
            </Card>
          )}
        </TabsContent>
      </Tabs>
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
// import { z } from 'zod';
import {
  UniversalDrawer,
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
  Input,
  Textarea,
  Button,
  useToastContext,
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
  Badge,
  Alert,
  AlertDescription,
} from '@ghxstship/ui';
import type { AccountDrawerProps, AccountRecord, ProfileFormData, PasswordFormData } from '../types';
import { accountService } from '../lib/account-service';

const profileFormSchema = z.object({
  name: z.string().min(1, 'Name is required').max(100, 'Name must be less than 100 characters'),
  email: z.string().email('Invalid email address'),
  phone: z.string().optional(),
  timezone: z.string().optional(),
  language: z.string().optional(),
});

const passwordFormSchema = z.object({
  current_password: z.string().min(1, 'Current password is required'),
  new_password: z.string().min(8, 'Password must be at least 8 characters'),
  confirm_password: z.string().min(1, 'Please confirm your password'),
}).refine((data) => data.new_password === data.confirm_password, {
  message: "Passwords don't match",
  path: ["confirm_password"],
});

export default function EditAccountDrawer({
  mode,
  record,
  isOpen,
  onClose,
  onSave,
  onDelete,
}: AccountDrawerProps) {
  const { toast } = useToastContext();
  const [saving, setSaving] = useState(false);
  const [deleting, setDeleting] = useState(false);
  const [activeTab, setActiveTab] = useState('details');
  const [showPassword, setShowPassword] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [twoFactorQr, setTwoFactorQr] = useState<string | null>(null);

  const profileForm = useForm<ProfileFormData>({
    resolver: zodResolver(profileFormSchema),
    defaultValues: {
      name: '',
      email: '',
      phone: '',
      timezone: '',
      language: '',
    },
  });

  const passwordForm = useForm<PasswordFormData>({
    resolver: zodResolver(passwordFormSchema),
    defaultValues: {
      current_password: '',
      new_password: '',
      confirm_password: '',
    },
  });

  const isReadOnly = mode === 'view';

  // Load record data when drawer opens
  useEffect(() => {
    if (record && isOpen) {
      if (record.type === 'profile' && record.metadata) {
        profileForm.reset({
          name: record.metadata.name || '',
          email: record.metadata.email || '',
          phone: record.metadata.phone || '',
          timezone: record.metadata.timezone || '',
          language: record.metadata.language || '',
        });
      }
    }
  }, [record, isOpen, profileForm]);

  const handleProfileSave = async (data: ProfileFormData) => {
    if (!record || !onSave) return;

    try {
      setSaving(true);
      await accountService.updateProfile(data);
      toast.success('Profile updated successfully');
      onClose();
    } catch (error) {
      console.error('Error updating profile:', error);
      toast.error('Failed to update profile');
    } finally {
      setSaving(false);
    }
  };

  const handlePasswordChange = async (data: PasswordFormData) => {
    try {
      setSaving(true);
      await accountService.changePassword(data);
      toast.success('Password changed successfully');
      passwordForm.reset();
    } catch (error) {
      console.error('Error changing password:', error);
      toast.error('Failed to change password');
    } finally {
      setSaving(false);
    }
  };

  const handleRevokeSession = async () => {
    if (!record || record.type !== 'session') return;

    try {
      setDeleting(true);
      await accountService.revokeSession(record.metadata.id);
      toast.success('Session revoked successfully');
      onClose();
    } catch (error) {
      console.error('Error revoking session:', error);
      toast.error('Failed to revoke session');
    } finally {
      setDeleting(false);
    }
  };

  const handleRevokeApiKey = async () => {
    if (!record || record.type !== 'api_key') return;

    try {
      setDeleting(true);
      await accountService.revokeApiKey(record.metadata.id);
      toast.success('API key revoked successfully');
      onClose();
    } catch (error) {
      console.error('Error revoking API key:', error);
      toast.error('Failed to revoke API key');
    } finally {
      setDeleting(false);
    }
  };

  const handleSetupTwoFactor = async () => {
    try {
      setSaving(true);
      const qrCode = await accountService.setupTwoFactor();
      setTwoFactorQr(qrCode);
      toast.success('2FA setup initiated');
    } catch (error) {
      console.error('Error setting up 2FA:', error);
      toast.error('Failed to setup 2FA');
    } finally {
      setSaving(false);
    }
  };

  const handleCopyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
    toast.success('Copied to clipboard');
  };

  const handleClose = () => {
    profileForm.reset();
    passwordForm.reset();
    setActiveTab('details');
    setTwoFactorQr(null);
    onClose();
  };

  const getDrawerTitle = () => {
    if (!record) return 'Account Record';

    const typeLabels = {
      profile: 'User Profile',
      session: 'User Session',
      api_key: 'API Key',
      security: 'Security Settings',
      preference: 'User Preferences',
    };

    const prefix = mode === 'view' ? 'View' : 'Edit';
    return `${prefix} ${typeLabels[record.type] || 'Account Record'}`;
  };

  const getDrawerDescription = () => {
    if (!record) return 'Manage account record';

    if (mode === 'view') return `View ${record.type} details and information`;
    return `Modify ${record.type} settings and configuration`;
  };

  const renderProfileTab = () => (
    <Form {...profileForm}>
      <form onSubmit={profileForm.handleSubmit(handleProfileSave)} className="space-y-4">
        <FormField
          control={profileForm.control}
          name="name"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Full Name</FormLabel>
              <FormControl>
                <Input 
                  placeholder="Enter your full name"
                  disabled={isReadOnly}
                  {...field} 
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <FormField
          control={profileForm.control}
          name="email"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Email Address</FormLabel>
              <FormControl>
                <Input 
                  type="email"
                  placeholder="Enter your email"
                  disabled={isReadOnly}
                  {...field} 
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <FormField
          control={profileForm.control}
          name="phone"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Phone Number (Optional)</FormLabel>
              <FormControl>
                <Input 
                  placeholder="Enter your phone number"
                  disabled={isReadOnly}
                  {...field} 
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <div className="grid grid-cols-2 gap-4">
          <FormField
            control={profileForm.control}
            name="timezone"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Timezone (Optional)</FormLabel>
                <FormControl>
                  <Input 
                    placeholder="e.g., America/New_York"
                    disabled={isReadOnly}
                    {...field} 
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={profileForm.control}
            name="language"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Language (Optional)</FormLabel>
                <FormControl>
                  <Input 
                    placeholder="e.g., en-US"
                    disabled={isReadOnly}
                    {...field} 
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />
        </div>

        {!isReadOnly && (
          <div className="flex justify-end pt-4">
            <Button type="submit" disabled={saving}>
              <Save className="h-4 w-4 mr-2" />
              {saving ? 'Saving...' : 'Save Profile'}
            </Button>
          </div>
        )}
      </form>
    </Form>
  );

  const renderPasswordTab = () => (
    <Form {...passwordForm}>
      <form onSubmit={passwordForm.handleSubmit(handlePasswordChange)} className="space-y-4">
        <Alert>
          <AlertTriangle className="h-4 w-4" />
          <AlertDescription>
            Changing your password will log you out of all other sessions.
          </AlertDescription>
        </Alert>

        <FormField
          control={passwordForm.control}
          name="current_password"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Current Password</FormLabel>
              <FormControl>
                <div className="relative">
                  <Input 
                    type={showPassword ? 'text' : 'password'}
                    placeholder="Enter your current password"
                    {...field} 
                  />
                  <Button
                    type="button"
                    variant="ghost"
                    size="sm"
                    className="absolute right-0 top-0 h-full px-3"
                    onClick={() => setShowPassword(!showPassword)}
                  >
                    {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                  </Button>
                </div>
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <FormField
          control={passwordForm.control}
          name="new_password"
          render={({ field }) => (
            <FormItem>
              <FormLabel>New Password</FormLabel>
              <FormControl>
                <div className="relative">
                  <Input 
                    type={showNewPassword ? 'text' : 'password'}
                    placeholder="Enter your new password"
                    {...field} 
                  />
                  <Button
                    type="button"
                    variant="ghost"
                    size="sm"
                    className="absolute right-0 top-0 h-full px-3"
                    onClick={() => setShowNewPassword(!showNewPassword)}
                  >
                    {showNewPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                  </Button>
                </div>
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <FormField
          control={passwordForm.control}
          name="confirm_password"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Confirm New Password</FormLabel>
              <FormControl>
                <Input 
                  type="password"
                  placeholder="Confirm your new password"
                  {...field} 
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <div className="flex justify-end pt-4">
          <Button type="submit" disabled={saving}>
            <Save className="h-4 w-4 mr-2" />
            {saving ? 'Changing...' : 'Change Password'}
          </Button>
        </div>
      </form>
    </Form>
  );

  const renderSessionDetails = () => {
    if (!record || record.type !== 'session') return null;

    const session = record.metadata;

    return (
      <div className="space-y-4">
        <div className="grid grid-cols-2 gap-4">
          <div>
            <label className="text-sm font-medium">IP Address</label>
            <div className="flex items-center gap-2 mt-1">
              <code className="text-sm bg-muted px-2 py-1 rounded flex-1">
                {session.ip_address}
              </code>
              <Button
                size="sm"
                variant="ghost"
                onClick={() => handleCopyToClipboard(session.ip_address)}
              >
                <Copy className="h-4 w-4" />
              </Button>
            </div>
          </div>
          <div>
            <label className="text-sm font-medium">Status</label>
            <div className="mt-1">
              <Badge variant={session.is_current ? 'default' : 'secondary'}>
                {session.is_current ? 'Current Session' : 'Other Session'}
              </Badge>
            </div>
          </div>
        </div>

        <div>
          <label className="text-sm font-medium">User Agent</label>
          <div className="mt-1">
            <Textarea
              value={session.user_agent}
              readOnly
              className="text-sm font-mono"
              rows={3}
            />
          </div>
        </div>

        <div className="grid grid-cols-2 gap-4">
          <div>
            <label className="text-sm font-medium">Created</label>
            <div className="text-sm text-muted-foreground mt-1">
              {new Date(session.created_at).toLocaleString()}
            </div>
          </div>
          <div>
            <label className="text-sm font-medium">Last Active</label>
            <div className="text-sm text-muted-foreground mt-1">
              {new Date(session.last_active).toLocaleString()}
            </div>
          </div>
        </div>

        {!session.is_current && !isReadOnly && (
          <div className="pt-4 border-t">
            <Button
              variant="destructive"
              onClick={handleRevokeSession}
              disabled={deleting}
            >
              <Trash2 className="h-4 w-4 mr-2" />
              {deleting ? 'Revoking...' : 'Revoke Session'}
            </Button>
          </div>
        )}
      </div>
    );
  };

  const renderApiKeyDetails = () => {
    if (!record || record.type !== 'api_key') return null;

    const apiKey = record.metadata;

    return (
      <div className="space-y-4">
        <div className="grid grid-cols-2 gap-4">
          <div>
            <label className="text-sm font-medium">Key Name</label>
            <div className="text-sm mt-1">{apiKey.name}</div>
          </div>
          <div>
            <label className="text-sm font-medium">Status</label>
            <div className="mt-1">
              <Badge variant={apiKey.is_active ? 'default' : 'destructive'}>
                {apiKey.is_active ? 'Active' : 'Revoked'}
              </Badge>
            </div>
          </div>
        </div>

        <div>
          <label className="text-sm font-medium">Key Prefix</label>
          <div className="flex items-center gap-2 mt-1">
            <code className="text-sm bg-muted px-2 py-1 rounded flex-1">
              {apiKey.key_prefix}***
            </code>
            <Button
              size="sm"
              variant="ghost"
              onClick={() => handleCopyToClipboard(apiKey.key_prefix)}
            >
              <Copy className="h-4 w-4" />
            </Button>
          </div>
        </div>

        <div>
          <label className="text-sm font-medium">Permissions</label>
          <div className="flex flex-wrap gap-1 mt-1">
            {apiKey.permissions.map((permission: string) => (
              <Badge key={permission} variant="outline" className="text-xs">
                {permission}
              </Badge>
            ))}
          </div>
        </div>

        <div className="grid grid-cols-2 gap-4">
          <div>
            <label className="text-sm font-medium">Created</label>
            <div className="text-sm text-muted-foreground mt-1">
              {new Date(apiKey.created_at).toLocaleString()}
            </div>
          </div>
          <div>
            <label className="text-sm font-medium">Last Used</label>
            <div className="text-sm text-muted-foreground mt-1">
              {apiKey.last_used ? new Date(apiKey.last_used).toLocaleString() : 'Never'}
            </div>
          </div>
        </div>

        {apiKey.is_active && !isReadOnly && (
          <div className="pt-4 border-t">
            <Button
              variant="destructive"
              onClick={handleRevokeApiKey}
              disabled={deleting}
            >
              <Trash2 className="h-4 w-4 mr-2" />
              {deleting ? 'Revoking...' : 'Revoke API Key'}
            </Button>
          </div>
        )}
      </div>
    );
  };

  const renderSecurityDetails = () => {
    if (!record || record.type !== 'security') return null;

    const twoFactor = record.metadata;

    return (
      <div className="space-y-4">
        <div className="grid grid-cols-2 gap-4">
          <div>
            <label className="text-sm font-medium">Status</label>
            <div className="mt-1">
              <Badge variant={twoFactor.is_enabled ? 'default' : 'secondary'}>
                {twoFactor.is_enabled ? 'Enabled' : 'Disabled'}
              </Badge>
            </div>
          </div>
          <div>
            <label className="text-sm font-medium">Backup Codes</label>
            <div className="text-sm text-muted-foreground mt-1">
              {twoFactor.backup_codes?.length || 0} codes available
            </div>
          </div>
        </div>

        {!twoFactor.is_enabled && !isReadOnly && (
          <div className="space-y-4">
            <Alert>
              <AlertTriangle className="h-4 w-4" />
              <AlertDescription>
                Two-factor authentication is not enabled. Enable it to secure your account.
              </AlertDescription>
            </Alert>

            <Button onClick={handleSetupTwoFactor} disabled={saving}>
              <RefreshCw className="h-4 w-4 mr-2" />
              {saving ? 'Setting up...' : 'Setup Two-Factor Authentication'}
            </Button>

            {twoFactorQr && (
              <div className="p-4 border rounded-lg">
                <p className="text-sm mb-2">Scan this QR code with your authenticator app:</p>
                <code className="text-xs bg-muted p-2 rounded block">
                  {twoFactorQr}
                </code>
              </div>
            )}
          </div>
        )}

        {twoFactor.is_enabled && (
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="text-sm font-medium">Created</label>
              <div className="text-sm text-muted-foreground mt-1">
                {new Date(twoFactor.created_at).toLocaleString()}
              </div>
            </div>
            <div>
              <label className="text-sm font-medium">Last Used</label>
              <div className="text-sm text-muted-foreground mt-1">
                {twoFactor.last_used ? new Date(twoFactor.last_used).toLocaleString() : 'Never'}
              </div>
            </div>
          </div>
        )}
      </div>
    );
  };

  return (
    <UniversalDrawer
      isOpen={isOpen}
      onClose={handleClose}
      title={getDrawerTitle()}
      description={getDrawerDescription()}
    >
      <div className="space-y-6">
        {record && (
          <Tabs value={activeTab} onValueChange={setActiveTab}>
            <TabsList className="grid w-full grid-cols-3">
              <TabsTrigger value="details">Details</TabsTrigger>
              {record.type === 'profile' && (
                <TabsTrigger value="password">Password</TabsTrigger>
              )}
              <TabsTrigger value="metadata">Metadata</TabsTrigger>
            </TabsList>

            <TabsContent value="details" className="space-y-4">
              {record.type === 'profile' && renderProfileTab()}
              {record.type === 'session' && renderSessionDetails()}
              {record.type === 'api_key' && renderApiKeyDetails()}
              {record.type === 'security' && renderSecurityDetails()}
            </TabsContent>

            {record.type === 'profile' && (
              <TabsContent value="password" className="space-y-4">
                {renderPasswordTab()}
              </TabsContent>
            )}

            <TabsContent value="metadata" className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="text-sm font-medium">Record ID</label>
                  <div className="text-sm text-muted-foreground font-mono mt-1">
                    {record.id}
                  </div>
                </div>
                <div>
                  <label className="text-sm font-medium">Type</label>
                  <div className="mt-1">
                    <Badge variant="outline">{record.type}</Badge>
                  </div>
                </div>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="text-sm font-medium">Created</label>
                  <div className="text-sm text-muted-foreground mt-1">
                    {new Date(record.created_at).toLocaleString()}
                  </div>
                </div>
                <div>
                  <label className="text-sm font-medium">Last Updated</label>
                  <div className="text-sm text-muted-foreground mt-1">
                    {new Date(record.updated_at).toLocaleString()}
                  </div>
                </div>
              </div>
            </TabsContent>
          </Tabs>
        )}

        <div className="flex justify-end gap-3 pt-4 border-t">
          <Button
            type="button"
            variant="outline"
            onClick={handleClose}
            disabled={saving || deleting}
          >
            <X className="h-4 w-4 mr-2" />
            {mode === 'view' ? 'Close' : 'Cancel'}
          </Button>
        </div>
      </div>
    </UniversalDrawer>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useEffect, useMemo, useState } from 'react';
import {
  Card,
  Button,
  Badge,
  Loader,
  Textarea,
  Input,
  useToastContext,
} from '@ghxstship/ui';
import {
  fetchAutomationRules,
  createAutomationRule,
  updateAutomationRule,
  deleteAutomationRule,
  type AutomationRuleSummary,
  type AutomationAction,
  type AutomationCondition,
} from '@/lib/services/settingsAutomationsClient';

interface AutomationFormState {
  name: string;
  description: string;
  triggerType: string;
  triggerConfig: string;
  conditions: string;
  actions: string;
  isActive: boolean;
}

const emptyForm: AutomationFormState = {
  name: '',
  description: '',
  triggerType: '',
  triggerConfig: '{\n  "event": ""\n}',
  conditions: '[]',
  actions: '[\n  {\n    "type": "",\n    "config": {},\n    "order": 1\n  }\n]',
  isActive: true,
};

function parseJsonOrThrow<T>(value: string, label: string): T {
  try {
    return JSON.parse(value) as T;
  } catch (error) {
    throw new Error(`Invalid ${label}. Please provide valid JSON.`);
  }
}

function formatJson(value: unknown): string {
  return JSON.stringify(value, null, 2);
}

export default function AutomationsSettingsClient() {
  const { toast } = useToastContext();
  const [automationRules, setAutomationRules] = useState<AutomationRuleSummary[]>([]);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [editingRuleId, setEditingRuleId] = useState<string | null>(null);
  const [form, setForm] = useState<AutomationFormState>(emptyForm);

  const isEditing = useMemo(() => editingRuleId !== null, [editingRuleId]);

  useEffect(() => {
    void loadAutomationRules();
  }, []);

  const loadAutomationRules = async () => {
    try {
      setLoading(true);
      const rules = await fetchAutomationRules();
      setAutomationRules(rules);
    } catch (error) {
      console.error('Error loading automation rules:', error);
      toast.error('Failed to load automation rules');
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setForm(emptyForm);
    setEditingRuleId(null);
  };

  const handleCreateNew = () => {
    resetForm();
    setShowForm(true);
  };

  const handleEditRule = (rule: AutomationRuleSummary) => {
    setForm({
      name: rule.name,
      description: rule.description ?? '',
      triggerType: rule.triggerType,
      triggerConfig: formatJson(rule.triggerConfig),
      conditions: formatJson(rule.conditions ?? []),
      actions: formatJson(rule.actions),
      isActive: rule.isActive,
    });
    setEditingRuleId(rule.id);
    setShowForm(true);
  };

  const handleDeleteRule = async (rule: AutomationRuleSummary) => {
    const confirmed = window.confirm(`Delete automation rule "${rule.name}"?`);
    if (!confirmed) return;

    try {
      await deleteAutomationRule(rule.id);
      toast.success('Automation rule deleted');
      void loadAutomationRules();
    } catch (error) {
      console.error('Error deleting automation rule:', error);
      toast.error('Failed to delete automation rule');
    }
  };

  const handleToggleActive = async (rule: AutomationRuleSummary) => {
    try {
      await updateAutomationRule(rule.id, { isActive: !rule.isActive });
      toast.success(`Automation ${rule.isActive ? 'paused' : 'activated'}`);
      void loadAutomationRules();
    } catch (error) {
      console.error('Error toggling automation rule:', error);
      toast.error('Failed to update automation status');
    }
  };

  const handleSubmit = async () => {
    try {
      setSaving(true);

      if (!form.name.trim()) {
        throw new Error('Name is required.');
      }
      if (!form.triggerType.trim()) {
        throw new Error('Trigger type is required.');
      }

      const triggerConfig = parseJsonOrThrow<Record<string, unknown>>(form.triggerConfig, 'trigger configuration');
      const actions = parseJsonOrThrow<AutomationAction[]>(form.actions, 'actions');
      const conditions = form.conditions.trim()
        ? parseJsonOrThrow<AutomationCondition[]>(form.conditions, 'conditions')
        : [];

      if (!Array.isArray(actions) || actions.length === 0) {
        throw new Error('At least one action is required.');
      }

      const payload = {
        name: form.name.trim(),
        description: form.description.trim() || undefined,
        triggerType: form.triggerType.trim(),
        triggerConfig,
        conditions,
        actions,
        isActive: form.isActive,
      };

      if (isEditing && editingRuleId) {
        await updateAutomationRule(editingRuleId, payload);
        toast.success('Automation rule updated');
      } else {
        await createAutomationRule(payload);
        toast.success('Automation rule created');
      }

      resetForm();
      setShowForm(false);
      void loadAutomationRules();
    } catch (error) {
      console.error('Error saving automation rule:', error);
      toast.error(error instanceof Error ? error.message : 'Failed to save automation rule');
    } finally {
      setSaving(false);
    }
  };

  const renderForm = () => (
    <Card>
      <div className="p-6 space-y-4">
        <div className="flex items-center justify-between">
          <h2 className="text-xl font-semibold">
            {isEditing ? 'Edit Automation Rule' : 'Create Automation Rule'}
          </h2>
          <div className="flex gap-2">
            <Button variant="outline" onClick={() => { resetForm(); setShowForm(false); }} disabled={saving}>
              Cancel
            </Button>
            <Button onClick={handleSubmit} disabled={saving}>
              {saving ? 'Saving...' : isEditing ? 'Update Rule' : 'Create Rule'}
            </Button>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="space-y-2">
            <label className="text-sm font-medium">Name</label>
            <Input
              value={form.name}
              onChange={(event) => setForm((prev) => ({ ...prev, name: event.target.value }))}
              placeholder="Automation name"
            />
          </div>
          <div className="space-y-2">
            <label className="text-sm font-medium">Trigger Type</label>
            <Input
              value={form.triggerType}
              onChange={(event) => setForm((prev) => ({ ...prev, triggerType: event.target.value }))}
              placeholder="e.g. lead.created"
            />
          </div>
        </div>

        <div className="space-y-2">
          <label className="text-sm font-medium">Description</label>
          <Textarea
            value={form.description}
            onChange={(event) => setForm((prev) => ({ ...prev, description: event.target.value }))}
            placeholder="Optional description"
            rows={2}
          />
        </div>

        <div className="space-y-2">
          <label className="text-sm font-medium">Trigger Configuration (JSON)</label>
          <Textarea
            value={form.triggerConfig}
            onChange={(event) => setForm((prev) => ({ ...prev, triggerConfig: event.target.value }))}
            rows={6}
          />
        </div>

        <div className="space-y-2">
          <label className="text-sm font-medium">Conditions (JSON array)</label>
          <Textarea
            value={form.conditions}
            onChange={(event) => setForm((prev) => ({ ...prev, conditions: event.target.value }))}
            rows={6}
            placeholder="[]"
          />
        </div>

        <div className="space-y-2">
          <label className="text-sm font-medium">Actions (JSON array)</label>
          <Textarea
            value={form.actions}
            onChange={(event) => setForm((prev) => ({ ...prev, actions: event.target.value }))}
            rows={6}
          />
        </div>

        <div className="flex items-center justify-between">
          <div className="space-y-1">
            <p className="text-sm font-medium">Status</p>
            <p className="text-xs text-muted-foreground">
              Active automations will run automatically when triggers are satisfied.
            </p>
          </div>
          <Button
            variant={form.isActive ? 'success' : 'outline'}
            onClick={() => setForm((prev) => ({ ...prev, isActive: !prev.isActive }))}
            type="button"
          >
            {form.isActive ? 'Active' : 'Inactive'}
          </Button>
        </div>
      </div>
    </Card>
  );

  const renderRule = (rule: AutomationRuleSummary) => (
    <Card key={rule.id}>
      <div className="p-6 space-y-4">
        <div className="flex flex-wrap items-start justify-between gap-4">
          <div>
            <div className="flex items-center gap-3">
              <h3 className="text-lg font-semibold">{rule.name}</h3>
              <Badge variant={rule.isActive ? 'success' : 'secondary'}>
                {rule.isActive ? 'Active' : 'Paused'}
              </Badge>
            </div>
            {rule.description && (
              <p className="text-sm text-muted-foreground mt-1">{rule.description}</p>
            )}
          </div>
          <div className="flex gap-2">
            <Button variant="outline" size="sm" onClick={() => handleToggleActive(rule)}>
              {rule.isActive ? 'Pause' : 'Activate'}
            </Button>
            <Button variant="outline" size="sm" onClick={() => handleEditRule(rule)}>
              Edit
            </Button>
            <Button variant="destructive" size="sm" onClick={() => handleDeleteRule(rule)}>
              Delete
            </Button>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
          <div className="space-y-3">
            <div>
              <p className="font-medium text-muted-foreground">Trigger</p>
              <p className="mt-1">Type: <span className="font-mono">{rule.triggerType}</span></p>
              <pre className="mt-2 bg-muted/50 rounded-md p-3 text-xs overflow-x-auto">
                {formatJson(rule.triggerConfig)}
              </pre>
            </div>

            <div>
              <p className="font-medium text-muted-foreground">Conditions</p>
              {rule.conditions && rule.conditions.length > 0 ? (
                <pre className="mt-2 bg-muted/50 rounded-md p-3 text-xs overflow-x-auto">
                  {formatJson(rule.conditions)}
                </pre>
              ) : (
                <p className="mt-1 text-muted-foreground">No additional conditions</p>
              )}
            </div>
          </div>
          <div className="space-y-3">
            <div>
              <p className="font-medium text-muted-foreground">Actions</p>
              <pre className="mt-2 bg-muted/50 rounded-md p-3 text-xs overflow-x-auto">
                {formatJson(rule.actions)}
              </pre>
            </div>
            <div className="flex items-center gap-4 text-xs text-muted-foreground">
              <span>Runs: {rule.runCount ?? 0}</span>
              {rule.lastRunAt && (
                <span>Last run: {new Date(rule.lastRunAt).toLocaleString()}</span>
              )}
              <span>Created: {new Date(rule.createdAt).toLocaleString()}</span>
              {rule.updatedAt && (
                <span>Updated: {new Date(rule.updatedAt).toLocaleString()}</span>
              )}
            </div>
          </div>
        </div>
      </div>
    </Card>
  );

  return (
    <div className="space-y-6">
      <div className="flex flex-wrap items-center justify-between gap-3">
        <div>
          <h1 className="text-3xl font-bold">Automations</h1>
          <p className="text-muted-foreground mt-1">
            Build rules that react to platform events and orchestrate downstream actions.
          </p>
        </div>
        <Button onClick={handleCreateNew}>
          Create Automation
        </Button>
      </div>

      {showForm && renderForm()}

      <Card>
        <div className="p-6">
          {loading ? (
            <div className="flex items-center justify-center h-48">
              <Loader className="h-8 w-8 animate-spin" />
            </div>
          ) : automationRules.length === 0 ? (
            <div className="py-16 text-center space-y-3">
              <p className="text-lg font-medium">No automation rules yet</p>
              <p className="text-sm text-muted-foreground">
                Automations will appear here once you create them.
              </p>
              <Button variant="outline" onClick={handleCreateNew}>
                Create your first rule
              </Button>
            </div>
          ) : (
            <div className="space-y-4">
              {automationRules.map(renderRule)}
            </div>
          )}
        </div>
      </Card>
    </div>
  );
}

'use client';

import { CreditCard, FileText, DollarSign, Package, RefreshCw, Download, Settings, TrendingUp } from "lucide-react";
import { useState, useEffect, useCallback, useMemo } from 'react';
import {
  DataViewProvider,
  StateManagerProvider,
  Button,
  useToastContext,
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
  Card,
  CardContent,
