'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useEffect, useMemo, useState } from 'react';
import {
  Card,
  Badge,
  Button,
  Input,
  Textarea,
  Loader,
  useToastContext,
} from '@ghxstship/ui';
import {
  fetchBillingSettings,
  updateBillingSettings,
  type BillingSettingsResponse,
} from '@/lib/services/settingsBillingClient';
import BillingPortalClient from './BillingPortalClient';
import Plans from './Plans';

interface FormState {
  billingEmail: string;
  billingAddress: string;
  paymentMethod: string;
  invoiceSettings: string;
}

const emptyForm: FormState = {
  billingEmail: '',
  billingAddress: '{\n  "line1": "",\n  "city": "",\n  "state": "",\n  "postal_code": "",\n  "country": ""\n}',
  paymentMethod: '{\n  "type": "",\n  "details": {}\n}',
  invoiceSettings: '{\n  "collection_method": "charge_automatically"\n}',
};

function safeStringify(value: unknown) {
  try {
    return JSON.stringify(value ?? {}, null, 2);
  } catch {
    return JSON.stringify({}, null, 2);
  }
}

export default function BillingSettingsClient() {
  const { toast } = useToastContext();
  const [data, setData] = useState<BillingSettingsResponse | null>(null);
  const [form, setForm] = useState<FormState>(emptyForm);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    void load();
  }, []);

  const load = async () => {
    try {
      setLoading(true);
      const response = await fetchBillingSettings();
      setData(response);
      const billing = response.billingSettings;
      setForm({
        billingEmail: billing?.billingEmail ?? '',
        billingAddress: safeStringify(billing?.billingAddress ?? {}),
        paymentMethod: safeStringify(billing?.paymentMethod ?? {}),
        invoiceSettings: safeStringify(billing?.invoiceSettings ?? {}),
      });
    } catch (error) {
      const message = error instanceof Error ? error.message : 'Unable to load billing settings';
      toast.error('Failed to load billing settings', undefined, { description: message });
    } finally {
      setLoading(false);
    }
  };

  const handleChange = <K extends keyof FormState>(key: K, value: FormState[K]) => {
    setForm((prev) => ({ ...prev, [key]: value }));
  };

  const handleSubmit = async () => {
    try {
      setSaving(true);
      const updates = {
        billingEmail: form.billingEmail.trim() || undefined,
        billingAddress: form.billingAddress.trim() ? JSON.parse(form.billingAddress) : undefined,
        paymentMethod: form.paymentMethod.trim() ? JSON.parse(form.paymentMethod) : undefined,
        invoiceSettings: form.invoiceSettings.trim() ? JSON.parse(form.invoiceSettings) : undefined,
      };

      const response = await updateBillingSettings(updates);
      setData(response);
      toast.success('Billing settings updated');
    } catch (error) {
      const message = error instanceof Error ? error.message : 'Unable to update billing settings';
      toast.error('Update failed', undefined, { description: message });
    } finally {
      setSaving(false);
    }
  };

  const subscriptionBadgeVariant = useMemo(() => {
    const status = data?.subscription?.status;
    if (!status) return 'secondary' as const;
    if (status === 'active') return 'success' as const;
    if (status === 'trialing') return 'info' as const;
    if (status === 'past_due' || status === 'unpaid') return 'warning' as const;
    if (status === 'canceled' || status === 'incomplete_expired') return 'destructive' as const;
    return 'secondary' as const;
  }, [data?.subscription?.status]);

  if (loading) {
    return (
      <div className="flex h-64 items-center justify-center">
        <Loader className="h-8 w-8 animate-spin" />
      </div>
    );
  }

  if (!data) {
    return (
      <div className="stack-md">
        <Card>
          <div className="p-md text-body-sm color-foreground/70">
            Unable to load billing settings. Please try again later.
          </div>
        </Card>
      </div>
    );
  }

  const { billingSettings, subscription, seatUsage, stripe, organization } = data;

  return (
    <div className="stack-lg">
      <Card title="Subscription">
        <div className="p-md space-y-lg">
          <div className="flex flex-col gap-sm md:flex-row md:items-center md:justify-between">
            <div className="space-y-xs">
              <div className="flex items-center gap-sm">
                <h2 className="text-heading-4">Current Plan</h2>
                <Badge variant={subscriptionBadgeVariant}>
                  {subscription?.status ? subscription.status.replaceAll('_', ' ') : 'Not Subscribed'}
                </Badge>
              </div>
              <p className="text-body-sm color-foreground/70">
                {billingSettings?.planName ?? 'No plan assigned.'}
              </p>
              {subscription?.currentPeriodEnd && (
                <p className="text-body-xs color-foreground/60">
                  Renews on {new Date(subscription.currentPeriodEnd).toLocaleDateString()}
                </p>
              )}
            </div>
            <div className="flex flex-col gap-xs text-right text-body-sm color-foreground/70">
              <span>Seats configured: {seatUsage.configuredSeats ?? 'â€”'}</span>
              <span>Seats in use: {seatUsage.usedSeats}</span>
            </div>
          </div>

          <BillingPortalClient
            defaultOrganizationId={organization?.id ?? null}
            defaultPriceId={subscription?.priceId ?? billingSettings?.planId ?? null}
          />

          <div className="stack-sm">
            <Plans organizationId={organization?.id ?? null} defaultPriceId={subscription?.priceId ?? null} />
          </div>
        </div>
      </Card>

      <Card title="Billing Profile">
        <div className="p-md space-y-md">
          <div className="grid grid-cols-1 gap-md lg:grid-cols-2">
            <div className="stack-sm">
              <Label htmlFor="billing-email">Billing Email</Label>
              <Input
                id="billing-email"
                value={form.billingEmail}
                onChange={(event) => handleChange('billingEmail', event.target.value)}
                placeholder="billing@company.com"
              />
            </div>
            <div className="stack-sm">
              <Label>Seat Usage</Label>
              <div className="rounded-lg border p-md text-body-sm color-foreground/70">
                <div>Configured seats: {seatUsage.configuredSeats ?? 'Unlimited'}</div>
                <div>Active members: {seatUsage.usedSeats}</div>
                <div className="color-foreground/60">
                  Adjust seats in your Stripe subscription if limits need to change.
                </div>
              </div>
            </div>
          </div>

          <div className="stack-sm">
            <Label htmlFor="billing-address">Billing Address (JSON)</Label>
            <Textarea
              id="billing-address"
              rows={6}
              value={form.billingAddress}
              onChange={(event) => handleChange('billingAddress', event.target.value)}
            />
          </div>

          <div className="stack-sm">
            <Label htmlFor="payment-method">Payment Method Metadata (JSON)</Label>
            <Textarea
              id="payment-method"
              rows={4}
              value={form.paymentMethod}
              onChange={(event) => handleChange('paymentMethod', event.target.value)}
            />
          </div>

          <div className="stack-sm">
            <Label htmlFor="invoice-settings">Invoice Settings (JSON)</Label>
            <Textarea
              id="invoice-settings"
              rows={4}
              value={form.invoiceSettings}
              onChange={(event) => handleChange('invoiceSettings', event.target.value)}
            />
          </div>

          <div className="flex justify-end gap-sm">
            <Button variant="outline" onClick={() => void load()} disabled={saving}>
              Refresh
            </Button>
            <Button onClick={handleSubmit} disabled={saving}>
              {saving ? 'Savingâ€¦' : 'Save Changes'}
            </Button>
          </div>
        </div>
      </Card>

      <Card title="Stripe Status">
        <div className="p-md space-y-sm text-body-sm color-foreground/70">
          <div className="flex items-center gap-sm">
            <Badge variant={stripe.configured ? 'success' : 'secondary'}>
              {stripe.configured ? 'Connected' : 'Not Connected'}
            </Badge>
            <span>
              {stripe.configured
                ? 'Stripe integration is active for this organization.'
                : 'Stripe integration is not configured for this organization.'}
            </span>
          </div>
          {organization && (
            <div className="space-y-xs">
              <div>Organization: {organization.name ?? organization.id}</div>
              <div>Stripe Customer ID: {organization.stripeCustomerId ?? 'â€”'}</div>
            </div>
          )}
        </div>
      </Card>
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useState, useEffect } from 'react';
import {
  Card,
  CardHeader,
  CardTitle,
  CardContent,
  Button,
  Badge,
  Input,
  Label,
  Textarea,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Modal,
  Skeleton,
  useToastContext,
} from '@ghxstship/ui';
import {
  fetchIntegrations,
  createIntegration,
  updateIntegration,
  deleteIntegration,
  testIntegration,
  type IntegrationRecord,
  type AvailableIntegration,
  type CreateIntegrationInput,
} from '@/lib/services/settingsIntegrationsClient';

export default function IntegrationsSettingsClient() {
  const { toast } = useToastContext();
  const [integrations, setIntegrations] = useState<IntegrationRecord[]>([]);
  const [availableIntegrations, setAvailableIntegrations] = useState<AvailableIntegration[]>([]);
  const [loading, setLoading] = useState(true);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [editingIntegration, setEditingIntegration] = useState<IntegrationRecord | null>(null);
  const [testing, setTesting] = useState<string | null>(null);

  // Form state
  const [formData, setFormData] = useState<CreateIntegrationInput>({
    name: '',
    type: 'webhook',
    enabled: true,
    config: {},
  });

  useEffect(() => {
    loadIntegrations();
  }, []);

  const loadIntegrations = async () => {
    try {
      setLoading(true);
      const response = await fetchIntegrations();
      setIntegrations(response.integrations);
      setAvailableIntegrations(response.availableIntegrations);
    } catch (error) {
      console.error('Error loading integrations:', error);
      toast.error('Failed to load integrations');
    } finally {
      setLoading(false);
    }
  };

  const handleCreate = async () => {
    try {
      const response = await createIntegration(formData);
      toast.success('Integration created successfully');
      setShowCreateModal(false);
      setFormData({ name: '', type: 'webhook', enabled: true, config: {} });
      await loadIntegrations();
    } catch (error) {
      console.error('Error creating integration:', error);
      toast.error('Failed to create integration');
    }
  };

  const handleUpdate = async (integration: IntegrationRecord) => {
    try {
      await updateIntegration({
        id: integration.id,
        enabled: !integration.enabled,
      });
      toast.success('Integration updated');
      await loadIntegrations();
    } catch (error) {
      console.error('Error updating integration:', error);
      toast.error('Failed to update integration');
    }
  };

  const handleDelete = async (id: string) => {
    if (!window.confirm('Are you sure you want to delete this integration?')) return;

    try {
      await deleteIntegration(id);
      toast.success('Integration deleted');
      await loadIntegrations();
    } catch (error) {
      console.error('Error deleting integration:', error);
      toast.error('Failed to delete integration');
    }
  };

  const handleTest = async (id: string) => {
    try {
      setTesting(id);
      const response = await testIntegration({ integrationId: id });
      if (response.success) {
        toast.success(response.message);
      } else {
        toast.error(response.message);
      }
    } catch (error) {
      console.error('Error testing integration:', error);
      toast.error('Failed to test integration');
    } finally {
      setTesting(null);
    }
  };

  const getIntegrationIcon = (type: string) => {
    switch (type) {
      case 'webhook':
        return <LinkIcon className="h-4 w-4" />;
      case 'api':
        return <Settings className="h-4 w-4" />;
      default:
        return <LinkIcon className="h-4 w-4" />;
    }
  };

  if (loading) {
    return (
      <div className="space-y-md">
        <Skeleton className="h-12 w-48" />
        <div className="space-y-sm">
          <Skeleton className="h-32 w-full" />
          <Skeleton className="h-32 w-full" />
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-lg">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Integrations</h1>
          <p className="text-muted-foreground mt-1">
            Connect third-party services and configure API access
          </p>
        </div>
        <Button onClick={() => setShowCreateModal(true)}>
          <Plus className="h-4 w-4 mr-2" />
          Add Integration
        </Button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-md">
        {integrations.map((integration) => (
          <Card key={integration.id}>
            <CardHeader className="flex flex-row items-start justify-between space-y-0">
              <div className="flex items-center gap-sm">
                {getIntegrationIcon(integration.type)}
                <div>
                  <CardTitle className="text-lg">{integration.name}</CardTitle>
                  <p className="text-sm text-muted-foreground capitalize">{integration.type}</p>
                </div>
              </div>
              <Badge variant={integration.enabled ? 'success' : 'secondary'}>
                {integration.enabled ? 'Active' : 'Inactive'}
              </Badge>
            </CardHeader>
            <CardContent>
              <div className="space-y-sm">
                {integration.last_sync && (
                  <p className="text-xs text-muted-foreground">
                    Last sync: {new Date(integration.last_sync).toLocaleString()}
                  </p>
                )}
                <div className="flex gap-xs">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => handleTest(integration.id)}
                    disabled={testing === integration.id}
                  >
                    <TestTube className="h-3 w-3 mr-1" />
                    {testing === integration.id ? 'Testing...' : 'Test'}
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => handleUpdate(integration)}
                  >
                    {integration.enabled ? 'Disable' : 'Enable'}
                  </Button>
                  <Button
                    variant="destructive"
                    size="sm"
                    onClick={() => handleDelete(integration.id)}
                  >
                    <Trash2 className="h-3 w-3" />
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {integrations.length === 0 && (
        <Card>
          <CardContent className="py-lg text-center">
            <p className="text-muted-foreground mb-md">No integrations configured yet</p>
            <Button onClick={() => setShowCreateModal(true)}>
              <Plus className="h-4 w-4 mr-2" />
              Add Your First Integration
            </Button>
          </CardContent>
        </Card>
      )}

      <Modal
        open={showCreateModal}
        onClose={() => setShowCreateModal(false)}
        title="Add Integration"
        description="Configure a new integration with external services"
      >
        <div className="space-y-md">
          <div className="space-y-sm">
            <Label htmlFor="name">Name</Label>
            <Input
              id="name"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              placeholder="My Integration"
            />
          </div>

          <div className="space-y-sm">
            <Label htmlFor="type">Type</Label>
            <Select
              value={formData.type}
              onValueChange={(value) => setFormData({ ...formData, type: value as unknown })}
            >
              <SelectTrigger id="type">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                {availableIntegrations.map((integration) => (
                  <SelectItem key={integration.type} value={integration.type}>
                    {integration.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {formData.type === 'webhook' && (
            <div className="space-y-sm">
              <Label htmlFor="webhook-url">Webhook URL</Label>
              <Input
                id="webhook-url"
                placeholder="https://example.com/webhook"
                onChange={(e) => setFormData({
                  ...formData,
                  config: { ...formData.config, url: e.target.value }
                })}
              />
            </div>
          )}

          <div className="flex justify-end gap-sm">
            <Button variant="outline" onClick={() => setShowCreateModal(false)}>
              Cancel
            </Button>
            <Button onClick={handleCreate} disabled={!formData.name}>
              Create Integration
            </Button>
          </div>
        </div>
      </Modal>
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
