"use client";

import { useEffect, useState } from 'react';
import { useRouter, useSearchParams } from 'next/navigation';
import { Button } from '@ghxstship/ui';
import { tryCatch, Result, reportError } from '@ghxstship/ui/utils/error-handling';

type OrgDomain = { id: string; domain: string; status: string; created_at: string };

export default function DomainsClient({ orgId, role, suggest }: { orgId: string; role?: string; suggest?: string }) {
  const [domains, setDomains] = useState<OrgDomain[]>([]);
  const [newDomain, setNewDomain] = useState(suggest ?? '');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const canManage = role === 'owner' || role === 'admin';
  const router = useRouter();
  const searchParams = useSearchParams();

  async function fetchDomains() {
    setError(null);
    const resResult = await tryCatch(async () => fetch(`/api/organizations/${orgId}/domains`, { cache: 'no-store' }));
if (!resResult.success) {
  reportError(resResult.error);
  throw new Error(resResult.error.message);
}
const res = resResult.data;
    const data = await res.json();
    if (!res.ok) return setError(data?.error || 'Failed to load domains');
    setDomains(data.domains || []);
  }

  useEffect(() => { fetchDomains(); }, [orgId]);

  async function addDomain() {
    if (!newDomain) return;
    setLoading(true); setError(null);
    const optimistic: OrgDomain = { id: `tmp-${Date.now()}`, domain: newDomain, status: 'pending', created_at: new Date().toISOString() };
    setDomains((prev: unknown) => [optimistic, ...prev]);
    try {
      const resResult = await tryCatch(async () =>
        fetch(`/api/organizations/${orgId}/domains`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ domain: newDomain })
        })
      );
      if (!resResult.success) {
        reportError(resResult.error);
        throw new Error(resResult.error.message);
      }
      const res = resResult.data;
      const data = await res.json();
      if (!res.ok) throw new Error(data?.error || 'Failed to add');
      setNewDomain('');
      await fetchDomains();
      // If this add came from a suggest param, clear it from the URL to avoid repeated focus/prefill
      if (searchParams.get('suggest')) {
        router.replace('/settings/organization/domains');
      }
    } catch (e) {
      setError(e?.message || 'Unknown error');
      setDomains((prev: OrgDomain[]) => prev.filter((d: OrgDomain) => d.id !== optimistic.id));
    } finally {
      setLoading(false);
    }
  }

  async function removeDomain(id: string) {
    setLoading(true); setError(null);
    const prev = domains;
    setDomains((cur: OrgDomain[]) => cur.filter((d: OrgDomain) => d.id !== id));
    try {
      const resResult = await tryCatch(async () =>
        fetch(`/api/organizations/${orgId}/domains`, {
          method: 'DELETE',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ id })
        })
      );
      if (!resResult.success) {
        reportError(resResult.error);
        throw new Error(resResult.error.message);
      }
      const res = resResult.data;
      const data = await res.json();
      if (!res.ok) throw new Error(data?.error || 'Failed to delete');
      // success: no-op, optimistic state already removed
    } catch (e) {
      setError(e?.message || 'Unknown error');
      setDomains((prev: unknown) => prev);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="stack-md">
      {canManage ? (
        <div className="flex gap-sm">
          <Button onClick={addDomain} disabled={loading || !newDomain}>Add</Button>
        </div>
      ) : (
        <div className="text-body-sm color-foreground/60">Only owners and admins can manage organization domains.</div>
      )}
      {error ? <div className="text-body-sm color-destructive">{error}</div> : null}
      <div className="rounded-md border">
        <table className="w-full text-body-sm">
          <thead>
            <tr className="bg-secondary/40">
              <th className=" px-md py-sm text-left">Domain</th>
              <th className=" px-md py-sm text-left">Status</th>
              <th className=" px-md py-sm text-right">Actions</th>
            </tr>
          </thead>
          <tbody>
            {domains.map((d: unknown) => (
              <tr key={d.id} className="border-t">
                <td className=" px-md py-sm">{d.domain}</td>
                <td className=" px-md py-sm">
                  <span className={
                    `inline-flex items-center rounded-full px-sm py-0.5 text-body-sm ` +
                    (d.status === 'active' ? 'bg-success/10 color-success' :
                     d.status === 'pending' ? 'bg-warning/10 color-warning' :
                     'bg-secondary/10 color-muted')
                  }>
                    {d.status}
                  </span>
                </td>
                <td className=" px-md py-sm text-right">
                  <Button onClick={() => removeDomain(d.id)} disabled={loading || !canManage}>Remove</Button>
                </td>
              </tr>
            ))}
            {domains.length === 0 ? (
              <tr>
                <td className=" px-md py-lg text-center color-foreground/60" colSpan={3}>No domains. Add your company email domain(s) to allow unlimited seats on Team plans.</td>
              </tr>
            ) : null}
          </tbody>
        </table>
      </div>
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useEffect, useMemo, useState } from 'react';
import {
  Badge,
  Button,
  Card,
  CardContent,
  CardHeader,
  CardTitle,
  Checkbox,
  Input,
  Label,
  Modal,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Skeleton,
  Textarea,
  Toggle,
  useToastContext,
} from '@ghxstship/ui';
import {
  createRole,
  deleteRole,
  fetchRoles,
  RoleRecord,
  updateRole,
} from '@/lib/services/settingsRolesClient';
import {
  fetchOrganizationMembers,
  OrganizationMember,
} from '@/lib/services/settingsOrganizationClient';

interface PermissionState {
  [resource: string]: Set<string>;
}

const ROLE_SCOPE_OPTIONS = [
  { value: 'owner', label: 'Owners' },
  { value: 'admin', label: 'Admins' },
  { value: 'manager', label: 'Managers' },
  { value: 'contributor', label: 'Contributors' },
  { value: 'viewer', label: 'Viewers' },
];

function buildPermissionState(permissions: Record<string, string[]>): PermissionState {
  const state: PermissionState = {};
  Object.entries(permissions || {}).forEach(([resource, actions]) => {
    state[resource] = new Set(actions);
  });
  return state;
}

function exportPermissionState(state: PermissionState): Record<string, string[]> {
  return Object.entries(state).reduce<Record<string, string[]>>((acc, [resource, actions]) => {
    if (actions.size > 0) {
      acc[resource] = Array.from(actions).sort();
    }
    return acc;
  }, {});
}

function mergeResourceMatrix(roles: RoleRecord[]): Record<string, Set<string>> {
  const matrix: Record<string, Set<string>> = {};
  roles.forEach((role) => {
    Object.entries(role.permissions || {}).forEach(([resource, actions]) => {
      if (!matrix[resource]) {
        matrix[resource] = new Set();
      }
      actions.forEach((action) => matrix[resource].add(action));
    });
  });
  return matrix;
}

const DEFAULT_ROLE_NAME = 'Untitled Role';

export default function PermissionsSettingsClient() {
  const { toast } = useToastContext();
  const [roles, setRoles] = useState<RoleRecord[]>([]);
  const [members, setMembers] = useState<OrganizationMember[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [saving, setSaving] = useState<boolean>(false);
  const [editingRole, setEditingRole] = useState<RoleRecord | null>(null);
  const [deleteRoleId, setDeleteRoleId] = useState<RoleRecord | null>(null);
  const [createModalOpen, setCreateModalOpen] = useState<boolean>(false);
  const [selectedMember, setSelectedMember] = useState<string>('');

  const [roleName, setRoleName] = useState<string>('');
  const [roleDescription, setRoleDescription] = useState<string>('');
  const [permissionState, setPermissionState] = useState<PermissionState>({});

  const matrix = useMemo(() => mergeResourceMatrix(roles), [roles]);
  const resources = useMemo(() => Object.keys(matrix).sort(), [matrix]);

  const load = async () => {
    try {
      setLoading(true);
      const [rolesResponse, membersResponse] = await Promise.all([
        fetchRoles({ includeSystem: true }),
        fetchOrganizationMembers(),
      ]);
      setRoles(rolesResponse.roles);
      setMembers(membersResponse.members);
    } catch (error) {
      const message = error instanceof Error ? error.message : 'Unable to load permissions data';
      toast.error('Failed to load permissions', undefined, { description: message });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    void load();
  }, []);

  const resetForm = () => {
    setRoleName('');
    setRoleDescription('');
    setPermissionState({});
    setSelectedMember('');
    setEditingRole(null);
  };

  const openCreateModal = () => {
    resetForm();
    setCreateModalOpen(true);
  };

  const openEditModal = (role: RoleRecord) => {
    setEditingRole(role);
    setRoleName(role.name || DEFAULT_ROLE_NAME);
    setRoleDescription(role.description ?? '');
    setPermissionState(buildPermissionState(role.permissions || {}));
    setSelectedMember('');
    setCreateModalOpen(true);
  };

  const togglePermission = (resource: string, action: string, checked: boolean) => {
    setPermissionState((prev) => {
      const next = { ...prev };
      if (!next[resource]) {
        next[resource] = new Set();
      }
      if (checked) {
        next[resource].add(action);
      } else {
        next[resource].delete(action);
      }
      return next;
    });
  };

  const handleSubmitRole = async () => {
    try {
      setSaving(true);
      const payload = {
        name: roleName || DEFAULT_ROLE_NAME,
        description: roleDescription || undefined,
        permissions: exportPermissionState(permissionState),
      };

      if (editingRole) {
        await updateRole({ id: editingRole.id, ...payload });
        toast.success('Role updated');
      } else {
        await createRole(payload);
        toast.success('Role created');
      }

      setCreateModalOpen(false);
      resetForm();
      await load();
    } catch (error) {
      const message = error instanceof Error ? error.message : 'Unable to save role';
      toast.error('Save failed', undefined, { description: message });
    } finally {
      setSaving(false);
    }
  };

  const handleDeleteRole = async () => {
    if (!deleteRoleId) return;
    try {
      setSaving(true);
      await deleteRole(deleteRoleId.id);
      toast.success('Role deleted');
      setDeleteRoleId(null);
      await load();
    } catch (error) {
      const message = error instanceof Error ? error.message : 'Unable to delete role';
      toast.error('Delete failed', undefined, { description: message });
    } finally {
      setSaving(false);
    }
  };

  const selectedMemberName = useMemo(() => {
    if (!selectedMember) return null;
    const member = members.find((m) => m.userId === selectedMember);
    return member?.fullName || member?.email || selectedMember;
  }, [members, selectedMember]);

  const renderRoleCard = (role: RoleRecord) => {
    const isSystem = role.isSystem;
    return (
      <Card key={role.id} className="border">
        <CardHeader className="flex flex-col gap-sm md:flex-row md:items-start md:justify-between">
          <div className="space-y-xs">
            <div className="flex items-center gap-sm">
              <CardTitle>{role.name}</CardTitle>
              {isSystem ? (
                <Badge variant="secondary">System</Badge>
              ) : (
                <Badge variant="outline">Custom</Badge>
              )}
            </div>
            {role.description ? (
              <p className="text-body-sm text-muted-foreground">{role.description}</p>
            ) : null}
            <p className="text-body-xs text-muted-foreground">
              Assigned Users: {role.assignedUsers ?? 0}
            </p>
          </div>
          <div className="flex gap-sm">
            <Button
              variant="outline"
              onClick={() => openEditModal(role)}
              disabled={isSystem}
            >
              Edit Role
            </Button>
            <Button
              variant="outline"
              onClick={() => setDeleteRoleId(role)}
              disabled={isSystem || (role.assignedUsers ?? 0) > 0}
            >
              Delete
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          <div className="overflow-x-auto">
            <table className="min-w-full border-collapse text-left text-body-sm">
              <thead>
                <tr>
                  <th className="border-b border-border px-sm py-xs">Resource</th>
                  <th className="border-b border-border px-sm py-xs">Actions</th>
                </tr>
              </thead>
              <tbody>
                {Object.entries(role.permissions || {}).map(([resource, actions]) => (
                  <tr key={resource}>
                    <td className="border-b border-border px-sm py-xs align-top font-medium">{resource}</td>
                    <td className="border-b border-border px-sm py-xs align-top">
                      <div className="flex flex-wrap gap-xs">
                        {actions.map((action) => (
                          <Badge key={`${resource}-${action}`} variant="outline">
                            {action}
                          </Badge>
                        ))}
                      </div>
                    </td>
                  </tr>
                ))}
                {Object.keys(role.permissions || {}).length === 0 ? (
                  <tr>
                    <td colSpan={2} className="px-sm py-sm text-muted-foreground">
                      No explicit permissions. Role inherits default organization policy.
                    </td>
                  </tr>
                ) : null}
              </tbody>
            </table>
          </div>
        </CardContent>
      </Card>
    );
  };

  if (loading) {
    return (
      <div className="space-y-md">
        <Skeleton className="h-12 w-48" />
        <div className="space-y-sm">
          <Skeleton className="h-24 w-full" />
          <Skeleton className="h-24 w-full" />
          <Skeleton className="h-24 w-full" />
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-lg">
      <Card>
        <CardHeader className="flex flex-col gap-sm md:flex-row md:items-center md:justify-between">
          <div>
            <CardTitle>Role-Based Access Control</CardTitle>
            <p className="text-body-sm text-muted-foreground">
              Manage system and custom roles across GHXSTSHIP. Custom roles let you tailor fine-grained permissions per resource.
            </p>
          </div>
          <Button onClick={openCreateModal}>Create Custom Role</Button>
        </CardHeader>
      </Card>

      <div className="space-y-md">
        {roles.map(renderRoleCard)}
        {roles.length === 0 ? (
          <Card>
            <CardContent className="py-lg text-center text-body-sm text-muted-foreground">
              No roles found. Create a custom role to get started.
            </CardContent>
          </Card>
        ) : null}
      </div>

      <Modal
        open={createModalOpen}
        onClose={() => {
          setCreateModalOpen(false);
          resetForm();
        }}
        title={editingRole ? 'Edit Custom Role' : 'Create Custom Role'}
        description="Select the permissions this role should grant across GHXSTSHIP modules."
      >
        <div className="space-y-lg">
          <div className="space-y-sm">
            <Label htmlFor="role-name">Role Name</Label>
            <Input
              id="role-name"
              value={roleName}
              onChange={(event) => setRoleName(event.target.value)}
              placeholder="e.g. Production Supervisor"
            />
          </div>
          <div className="space-y-sm">
            <Label htmlFor="role-description">Description</Label>
            <Textarea
              id="role-description"
              rows={3}
              value={roleDescription}
              onChange={(event) => setRoleDescription(event.target.value)}
              placeholder="Describe when to assign this role."
            />
          </div>

          <div className="space-y-md">
            <div className="flex items-center justify-between">
              <Label>Permissions</Label>
              <p className="text-body-xs text-muted-foreground">
                Toggle actions per resource. Only checked actions will be granted.
              </p>
            </div>
            <div className="space-y-md">
              {resources.length === 0 ? (
                <div className="rounded-md border border-dashed border-border px-md py-lg text-center text-body-sm text-muted-foreground">
                  No resource matrix detected yet. Create the role with a description first to define permissions.
                </div>
              ) : null}
              {resources.map((resource) => {
                const actions = Array.from(matrix[resource] ?? []).sort();
                return (
                  <div key={resource} className="rounded-lg border p-md space-y-sm">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-sm">
                        <h3 className="font-medium capitalize">{resource.replace(/_/g, ' ')}</h3>
                        <Badge variant="outline">{actions.length} actions</Badge>
                      </div>
                      <Toggle
                        checked={(permissionState[resource]?.size || 0) === actions.length}
                        onChange={(event) => {
                          const checked = event.target.checked;
                          setPermissionState((prev) => {
                            const next = { ...prev };
                            if (!next[resource]) {
                              next[resource] = new Set();
                            }
                            if (checked) {
                              actions.forEach((action) => next[resource].add(action));
                            } else {
                              next[resource].clear();
                            }
                            return next;
                          });
                        }}
                      />
                    </div>
                    <div className="grid grid-cols-2 gap-sm md:grid-cols-3">
                      {actions.map((action) => {
                        const checked = permissionState[resource]?.has(action) ?? false;
                        return (
                          <label key={`${resource}-${action}`} className="flex items-center gap-xs text-body-sm">
                            <Checkbox
                              checked={checked}
                              onChange={(event) => togglePermission(resource, action, event.target.checked)}
                            />
                            <span className="capitalize">{action.replace(/_/g, ' ')}</span>
                          </label>
                        );
                      })}
                    </div>
                  </div>
                );
              })}
            </div>
          </div>

          <div className="space-y-sm">
            <Label htmlFor="member-preview">Preview Assignment</Label>
            <Select value={selectedMember} onValueChange={setSelectedMember}>
              <SelectTrigger id="member-preview">
                <SelectValue placeholder="Select member to preview" />
              </SelectTrigger>
              <SelectContent>
                {members.map((member) => (
                  <SelectItem key={member.userId} value={member.userId}>
                    {member.fullName || member.email || member.userId}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            {selectedMemberName ? (
              <p className="text-body-xs text-muted-foreground">
                Assigning <strong>{selectedMemberName}</strong> will immediately grant the checked actions.
              </p>
            ) : (
              <p className="text-body-xs text-muted-foreground">
                Select a member to preview how this role would apply to them.
              </p>
            )}
          </div>

          <div className="flex justify-end gap-sm">
            <Button
              variant="outline"
              onClick={() => {
                setCreateModalOpen(false);
                resetForm();
              }}
              disabled={saving}
            >
              Cancel
            </Button>
            <Button onClick={handleSubmitRole} disabled={saving}>
              {saving ? 'Savingâ€¦' : editingRole ? 'Update Role' : 'Create Role'}
            </Button>
          </div>
        </div>
      </Modal>

      <Modal
        open={Boolean(deleteRoleId)}
        onClose={() => setDeleteRoleId(null)}
        title="Delete custom role?"
        description="Deleting this role removes its permission grants. This cannot be undone."
      >
        <div className="space-y-md">
          <p className="text-body-sm text-muted-foreground">
            Role: <strong>{deleteRoleId?.name}</strong>
          </p>
          {deleteRoleId?.assignedUsers ? (
            <p className="text-body-xs text-muted-foreground">
              This role is assigned to {deleteRoleId.assignedUsers} users. Reassign them before deletion.
            </p>
          ) : null}
          <div className="flex justify-end gap-sm">
            <Button variant="outline" onClick={() => setDeleteRoleId(null)} disabled={saving}>
              Cancel
            </Button>
            <Button variant="destructive" onClick={handleDeleteRole} disabled={saving}>
              {saving ? 'Deletingâ€¦' : 'Delete Role'}
            </Button>
          </div>
        </div>
      </Modal>
    </div>
  );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import React, { useEffect, useState } from 'react';
import {
  Badge,
  Button,
  Card,
  CardContent,
  CardHeader,
  CardTitle,
  Input,
  Label,
  Modal,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Skeleton,
  Textarea,
  Toggle,
  useToastContext,
} from '@ghxstship/ui';
import {
  fetchSecuritySettings,
  generateSecurityBackupCodes,
  resetSecuritySettings,
  runSecurityTest,
  SecurityDeviceRecord,
  SecurityEventRecord,
  SecuritySettings,
  SecuritySettingsResponse,
  SecuritySessionRecord,
  updateSecuritySettings,
} from '@/lib/services/settingsSecurityClient';

const MFA_METHOD_OPTIONS = [
  { value: 'totp', label: 'Authenticator App' },
  { value: 'sms', label: 'SMS' },
  { value: 'email', label: 'Email' },
];

const PASSWORD_POLICY_PRESETS = [
  {
    name: 'Strict (Recommended)',
    value: 'strict',
    policy: {
      minLength: 12,
      requireUppercase: true,
      requireLowercase: true,
      requireNumbers: true,
      requireSpecialChars: true,
      maxAge: 90,
      preventReuse: 5,
    },
  },
  {
    name: 'Moderate',
    value: 'moderate',
    policy: {
      minLength: 10,
      requireUppercase: true,
      requireLowercase: true,
      requireNumbers: true,
      requireSpecialChars: false,
      maxAge: 180,
      preventReuse: 3,
    },
  },
  {
    name: 'Basic',
    value: 'basic',
    policy: {
      minLength: 8,
      requireUppercase: false,
      requireLowercase: true,
      requireNumbers: true,
      requireSpecialChars: false,
      maxAge: 365,
      preventReuse: 2,
    },
  },
];

const SESSION_TIMEOUT_OPTIONS = [
  { label: '15 minutes', value: 900 },
  { label: '30 minutes', value: 1800 },
  { label: '1 hour', value: 3600 },
  { label: '8 hours (workday)', value: 28800 },
];

function ensureSecuritySettings(data?: SecuritySettings): SecuritySettings {
  return {
    passwordPolicy: {
      minLength: data?.passwordPolicy?.minLength ?? 12,
      requireUppercase: data?.passwordPolicy?.requireUppercase ?? true,
      requireLowercase: data?.passwordPolicy?.requireLowercase ?? true,
      requireNumbers: data?.passwordPolicy?.requireNumbers ?? true,
      requireSpecialChars: data?.passwordPolicy?.requireSpecialChars ?? true,
      maxAge: data?.passwordPolicy?.maxAge ?? 90,
      preventReuse: data?.passwordPolicy?.preventReuse ?? 5,
    },
    sessionSettings: {
      maxSessionDuration: data?.sessionSettings?.maxSessionDuration ?? 28800,
      idleTimeout: data?.sessionSettings?.idleTimeout ?? 1800,
      requireReauth: data?.sessionSettings?.requireReauth ?? true,
      maxConcurrentSessions: data?.sessionSettings?.maxConcurrentSessions ?? 3,
    },
    mfaSettings: {
      required: data?.mfaSettings?.required ?? false,
      allowedMethods: data?.mfaSettings?.allowedMethods ?? ['totp', 'email'],
      gracePeriod: data?.mfaSettings?.gracePeriod ?? 7,
    },
    accessControl: {
      ipWhitelist: data?.accessControl?.ipWhitelist ?? [],
      allowedDomains: data?.accessControl?.allowedDomains ?? [],
      blockSuspiciousActivity: data?.accessControl?.blockSuspiciousActivity ?? true,
      maxFailedAttempts: data?.accessControl?.maxFailedAttempts ?? 5,
      lockoutDuration: data?.accessControl?.lockoutDuration ?? 900,
    },
    auditSettings: {
      logAllActions: data?.auditSettings?.logAllActions ?? true,
      retentionPeriod: data?.auditSettings?.retentionPeriod ?? 365,
      alertOnSensitiveActions: data?.auditSettings?.alertOnSensitiveActions ?? true,
      exportEnabled: data?.auditSettings?.exportEnabled ?? true,
    },
  };
}

const MAX_IPS = 50;

export default function SecuritySettingsClient() {
  const { toast } = useToastContext();
  const [data, setData] = useState<SecuritySettingsResponse | null>(null);
  const [formState, setFormState] = useState<SecuritySettings>(ensureSecuritySettings());
  const [loading, setLoading] = useState<boolean>(true);
  const [saving, setSaving] = useState<boolean>(false);
  const [testing, setTesting] = useState<boolean>(false);
  const [testResults, setTestResults] = useState<string | null>(null);
  const [backupCodes, setBackupCodes] = useState<string[] | null>(null);
  const [backupCodesModalOpen, setBackupCodesModalOpen] = useState<boolean>(false);
  const [resetModalOpen, setResetModalOpen] = useState<'all' | 'sessions' | null>(null);

  const load = async () => {
    try {
      setLoading(true);
      const response = await fetchSecuritySettings();
      setData(response);
      setFormState(ensureSecuritySettings(response.securitySettings));
    } catch (error) {
      const message = error instanceof Error ? error.message : 'Unable to load security settings';
      toast.error('Failed to load security settings', undefined, { description: message });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    void load();
  }, []);

  const handlePresetChange = (value: string) => {
    const preset = PASSWORD_POLICY_PRESETS.find((preset) => preset.value === value);
    if (!preset) return;
    setFormState((prev) => ({
      ...prev,
      passwordPolicy: {
        ...prev.passwordPolicy,
        ...preset.policy,
      },
    }));
  };

  const handleToggle = (path: keyof SecuritySettings, key: string, value: boolean) => {
    setFormState((prev) => ({
      ...prev,
      [path]: {
        ...(prev[path] as Record<string, unknown>),
        [key]: value,
      },
    }));
  };

  const handleNumberChange = (path: keyof SecuritySettings, key: string, value: number) => {
    setFormState((prev) => ({
      ...prev,
      [path]: {
        ...(prev[path] as Record<string, unknown>),
        [key]: value,
      },
    }));
  };

  const handleArrayChange = (path: keyof SecuritySettings, key: string, value: string[]) => {
    setFormState((prev) => ({
      ...prev,
      [path]: {
        ...(prev[path] as Record<string, unknown>),
        [key]: value,
      },
    }));
  };

  const handleWhitelistChange = (value: string) => {
    const entries = value
      .split('\n')
      .map((line) => line.trim())
      .filter(Boolean)
      .slice(0, MAX_IPS);
    handleArrayChange('accessControl', 'ipWhitelist', entries);
  };

  const handleAllowDomainChange = (value: string) => {
    const entries = value
      .split('\n')
      .map((line) => line.trim().toLowerCase())
      .filter(Boolean)
      .slice(0, MAX_IPS);
    handleArrayChange('accessControl', 'allowedDomains', entries);
  };

  const handleSave = async () => {
    try {
      setSaving(true);
      await updateSecuritySettings(formState);
      toast.success('Security settings saved');
      await load();
    } catch (error) {
      const message = error instanceof Error ? error.message : 'Unable to save security settings';
      toast.error('Save failed', undefined, { description: message });
    } finally {
      setSaving(false);
    }
  };

  const handleRunTest = async () => {
    try {
      setTesting(true);
      const result = await runSecurityTest();
      setTestResults(
        `Overall Score: ${result.overallScore}\nPassword Compliance: ${result.passwordPolicyCompliance ? 'Pass' : 'Fail'}\nRecommendations:\n- ${result.recommendations.join('\n- ')}`
      );
      toast.success('Security assessment completed');
    } catch (error) {
      const message = error instanceof Error ? error.message : 'Unable to run security assessment';
      toast.error('Assessment failed', undefined, { description: message });
    } finally {
      setTesting(false);
    }
  };

  const handleBackupCodes = async () => {
    try {
      const codes = await generateSecurityBackupCodes();
      setBackupCodes(codes);
      setBackupCodesModalOpen(true);
      toast.success('Backup codes generated');
    } catch (error) {
      const message = error instanceof Error ? error.message : 'Unable to generate backup codes';
      toast.error('Action failed', undefined, { description: message });
    }
  };

  const confirmReset = async (type: 'all' | 'sessions') => {
    try {
      setSaving(true);
      await resetSecuritySettings(type);
      if (type === 'all') {
        toast.success('Security settings reset to defaults');
        await load();
      } else {
        toast.success('All sessions terminated');
      }
    } catch (error) {
      const message = error instanceof Error ? error.message : 'Unable to reset security settings';
      toast.error('Reset failed', undefined, { description: message });
    } finally {
      setSaving(false);
      setResetModalOpen(null);
    }
  };

  const currentSettings = ensureSecuritySettings(formState);
  const metrics = data?.securityMetrics;

  if (loading) {
    return (
      <div className="space-y-md">
        <Skeleton className="h-12 w-64" />
        <div className="grid gap-md md:grid-cols-2">
          <Skeleton className="h-40 w-full" />
          <Skeleton className="h-40 w-full" />
        </div>
        <Skeleton className="h-40 w-full" />
      </div>
    );
  }

  return (
    <div className="space-y-lg">
      <Card>
        <CardHeader className="flex flex-col gap-sm md:flex-row md:items-center md:justify-between">
          <div>
            <CardTitle>Organization Security</CardTitle>
            <p className="text-body-sm text-muted-foreground">
              Manage password policies, session security, MFA requirements, and access controls for your organization.
            </p>
          </div>
          <div className="flex flex-wrap gap-sm">
            <Button variant="outline" onClick={handleRunTest} disabled={testing}>
              {testing ? 'Assessingâ€¦' : 'Run Security Assessment'}
            </Button>
            <Button variant="outline" onClick={handleBackupCodes}>
              Generate Backup Codes
            </Button>
            <Button variant="outline" onClick={() => void load()} disabled={saving}>
              Refresh
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          <div className="grid gap-md md:grid-cols-3">
            <div className="rounded-lg border p-md">
              <p className="text-body-xs text-muted-foreground">Failed Logins (24h)</p>
              <p className="text-heading-4">{metrics?.failedLoginsLast24h ?? 0}</p>
            </div>
            <div className="rounded-lg border p-md">
              <p className="text-body-xs text-muted-foreground">Active Sessions</p>
              <p className="text-heading-4">{metrics?.activeSessions ?? 0}</p>
            </div>
            <div className="rounded-lg border p-md">
              <p className="text-body-xs text-muted-foreground">Audit Logs (7 days)</p>
              <p className="text-heading-4">{metrics?.auditLogsLast7Days ?? 0}</p>
            </div>
          </div>
        </CardContent>
      </Card>

      {testResults ? (
        <Card>
          <CardHeader>
            <CardTitle>Security Assessment</CardTitle>
          </CardHeader>
          <CardContent>
            <pre className="whitespace-pre-wrap rounded-md bg-muted/40 p-md text-body-sm text-muted-foreground">{testResults}</pre>
          </CardContent>
        </Card>
      ) : null}

      <Card>
        <CardHeader>
          <CardTitle>Password Policy</CardTitle>
        </CardHeader>
        <CardContent className="space-y-md">
          <div className="grid gap-md md:grid-cols-2">
            <div className="space-y-sm">
              <Label>Password Policy Preset</Label>
              <Select onValueChange={handlePresetChange}>
                <SelectTrigger>
                  <SelectValue placeholder="Select preset" />
                </SelectTrigger>
                <SelectContent>
                  {PASSWORD_POLICY_PRESETS.map((preset) => (
                    <SelectItem key={preset.value} value={preset.value}>
                      {preset.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <p className="text-body-xs text-muted-foreground">
                Pick a preset to quickly align with recommended security posture.
              </p>
            </div>
            <div className="space-y-sm">
              <Label htmlFor="min-length">Minimum Length</Label>
              <Input
                id="min-length"
                type="number"
                min={8}
                max={128}
                value={currentSettings.passwordPolicy?.minLength ?? 12}
                onChange={(event) =>
                  handleNumberChange('passwordPolicy', 'minLength', Number(event.target.value))
                }
              />
            </div>
          </div>

          <div className="grid gap-sm md:grid-cols-2">
            <div className="flex items-center justify-between">
              <Label htmlFor="require-uppercase">Require Uppercase</Label>
              <Toggle
                id="require-uppercase"
                checked={currentSettings.passwordPolicy?.requireUppercase ?? true}
                onChange={(event) =>
                  handleToggle('passwordPolicy', 'requireUppercase', event.target.checked)
                }
              />
            </div>
            <div className="flex items-center justify-between">
              <Label htmlFor="require-lowercase">Require Lowercase</Label>
              <Toggle
                id="require-lowercase"
                checked={currentSettings.passwordPolicy?.requireLowercase ?? true}
                onChange={(event) =>
                  handleToggle('passwordPolicy', 'requireLowercase', event.target.checked)
                }
              />
            </div>
            <div className="flex items-center justify-between">
              <Label htmlFor="require-numbers">Require Numbers</Label>
              <Toggle
                id="require-numbers"
                checked={currentSettings.passwordPolicy?.requireNumbers ?? true}
                onChange={(event) =>
                  handleToggle('passwordPolicy', 'requireNumbers', event.target.checked)
                }
              />
            </div>
            <div className="flex items-center justify-between">
              <Label htmlFor="require-special">Require Special Characters</Label>
              <Toggle
                id="require-special"
                checked={currentSettings.passwordPolicy?.requireSpecialChars ?? true}
                onChange={(event) =>
                  handleToggle('passwordPolicy', 'requireSpecialChars', event.target.checked)
                }
              />
            </div>
          </div>

          <div className="grid gap-md md:grid-cols-3">
            <div className="space-y-sm">
              <Label htmlFor="max-age">Maximum Password Age (days)</Label>
              <Input
                id="max-age"
                type="number"
                min={0}
                max={365}
                value={currentSettings.passwordPolicy?.maxAge ?? 90}
                onChange={(event) =>
                  handleNumberChange('passwordPolicy', 'maxAge', Number(event.target.value))
                }
              />
            </div>
            <div className="space-y-sm">
              <Label htmlFor="prevent-reuse">Prevent Reuse (Previous passwords)</Label>
              <Input
                id="prevent-reuse"
                type="number"
                min={0}
                max={24}
                value={currentSettings.passwordPolicy?.preventReuse ?? 5}
                onChange={(event) =>
                  handleNumberChange('passwordPolicy', 'preventReuse', Number(event.target.value))
                }
              />
            </div>
            <div className="space-y-sm">
              <Label htmlFor="session-timeout">Idle Timeout</Label>
              <Select
                value={String(currentSettings.sessionSettings?.idleTimeout ?? 1800)}
                onValueChange={(value) =>
                  handleNumberChange('sessionSettings', 'idleTimeout', Number(value))
                }
              >
                <SelectTrigger id="session-timeout">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {SESSION_TIMEOUT_OPTIONS.map((option) => (
                    <SelectItem key={option.value} value={String(option.value)}>
                      {option.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle>Multi-Factor Authentication</CardTitle>
        </CardHeader>
        <CardContent className="space-y-md">
          <div className="flex items-center justify-between">
            <div className="space-y-xs">
              <Label>Require MFA</Label>
              <p className="text-body-xs text-muted-foreground">
                Enforce MFA for all members to access the platform.
              </p>
            </div>
            <Toggle
              checked={currentSettings.mfaSettings?.required ?? false}
              onChange={(event) => handleToggle('mfaSettings', 'required', event.target.checked)}
            />
          </div>

          <div className="space-y-sm">
            <Label htmlFor="allowed-methods">Allowed Methods</Label>
            <div className="grid gap-sm md:grid-cols-3">
              {MFA_METHOD_OPTIONS.map((option) => {
                const checked = currentSettings.mfaSettings?.allowedMethods?.includes(
                  option.value
                );
                return (
                  <label key={option.value} className="flex items-center gap-xs text-body-sm">
                    <input
                      type="checkbox"
                      checked={checked}
                      onChange={() => {
                        const methods = currentSettings.mfaSettings?.allowedMethods || [];
                        const newMethods = checked
                          ? methods.filter(m => m !== option.value)
                          : [...methods, option.value];
                        setCurrentSettings({
                          ...currentSettings,
                          mfaSettings: {
                            ...currentSettings.mfaSettings,
                            allowedMethods: newMethods
                          }
                        });
                      }}
                    />
                    {option.label}
                  </label>
                );
              })}
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import { useEffect, useMemo, useState } from 'react';
import { Button, UnifiedInput } from '@ghxstship/ui';
import Link from 'next/link';
import { tryCatch, Result, reportError } from '@ghxstship/ui/utils/error-handling';

const ROLES = ['viewer', 'contributor', 'manager', 'admin'] as const;

type Invite = { id: string; email: string; role: string; status: string; created_at: string };

type Props = { orgId: string; role?: string };

export default function InviteMemberClient({ orgId, role }: Props) {
  const canManage = role === 'owner' || role === 'admin';
  const [email, setEmail] = useState('');
  const [inviteRole, setInviteRole] = useState<typeof ROLES[number]>('contributor');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [result, setResult] = useState<string | null>(null);
  const [invites, setInvites] = useState<Invite[]>([]);
  const [usage, setUsage] = useState<{ seat_policy: string; seats_limit: number | null; active_count: number } | null>(null);
  const [domains, setDomains] = useState<Array<{ id: string; domain: string; status: string }>>([]);
  const [bulkText, setBulkText] = useState('');
  const [bulkRole, setBulkRole] = useState<typeof ROLES[number]>('contributor');

  const validEmail = useMemo(() => /.+@.+\..+/.test(email), [email]);
  const emailDomain = useMemo(() => {
    const m = email.toLowerCase().match(/@([a-z0-9.-]+\.[a-z]{2,})$/i);
    return m ? m[1] : '';
  }, [email]);
  const domainRecord = useMemo(() => domains.find((d: unknown) => d.domain === emailDomain), [domains, emailDomain]);

  async function fetchInvites() {
    setError(null);
    const resResult = await tryCatch(async () => fetch(`/api/organizations/${orgId}/invites`, { cache: 'no-store' }));
if (!resResult.success) {
  reportError(resResult.error);
  throw new Error(resResult.error.message);
}
const res = resResult.data;
    const data = await res.json();
    if (!res.ok) return setError(data?.error || 'Failed to load invites');
    setInvites(data.invites || []);
  }

  async function fetchUsage() {
    const resResult = await tryCatch(async () => fetch(`/api/organizations/${orgId}/memberships/usage`, { cache: 'no-store' }));
if (!resResult.success) {
  reportError(resResult.error);
  throw new Error(resResult.error.message);
}
const res = resResult.data;
    const data = await res.json();
    if (res.ok) setUsage(data);
  }

  async function fetchDomains() {
    const resResult = await tryCatch(async () => fetch(`/api/organizations/${orgId}/domains`, { cache: 'no-store' }));
if (!resResult.success) {
  reportError(resResult.error);
  throw new Error(resResult.error.message);
}
const res = resResult.data;
    const data = await res.json();
    if (res.ok) setDomains(data.domains || []);
  }

  useEffect(() => {
    fetchInvites();
    fetchUsage();
    fetchDomains();
  }, [orgId]);

  async function submit(mode: 'invite' | 'addExisting') {
    setLoading(true); setError(null); setResult(null);
    try {
      const resResult = await tryCatch(async () =>
        fetch(`/api/organizations/${orgId}/memberships`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ email, role: inviteRole, mode })
        })
      );
      if (!resResult.success) {
        reportError(resResult.error);
        throw new Error(resResult.error.message);
      }
      const res = resResult.data;
      const data = await res.json();
      if (!res.ok) throw new Error(data?.error || 'Request failed');
      setResult(mode === 'invite' ? `Invite sent to ${data?.email || email}` : 'Member added');
      setEmail('');
      await Promise.all([fetchInvites(), fetchUsage()]);
    } catch (e) {
      setError(e?.message || 'Unknown error');
    } finally {
      setLoading(false);
    }
  }

  async function resendInvite(id: string) {
    setLoading(true); setError(null); setResult(null);
    try {
      const resResult = await tryCatch(async () =>
        fetch(`/api/organizations/${orgId}/invites`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ id, action: 'resend' })
        })
      );
      if (!resResult.success) {
        reportError(resResult.error);
        throw new Error(resResult.error.message);
      }
      const res = resResult.data;
      const data = await res.json();
      if (!res.ok) throw new Error(data?.error || 'Failed to resend');
      setResult('Invite resent');
    } catch (e) {
      setError(e?.message || 'Unknown error');
    } finally {
      setLoading(false);
    }
  }

  async function revokeInvite(id: string) {
    setLoading(true); setError(null); setResult(null);
    const prev = invites;
    setInvites(prev.map((i: unknown) => (i.id === id ? { ...i, status: 'revoked' } : i)));
    try {
      const resResult = await tryCatch(async () =>
        fetch(`/api/organizations/${orgId}/invites`, {
          method: 'DELETE',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ id })
        })
      );
      if (!resResult.success) {
        reportError(resResult.error);
        throw new Error(resResult.error.message);
      }
      const res = resResult.data;
      const data = await res.json();
      if (!res.ok) throw new Error(data?.error || 'Failed to revoke');
    } catch (e) {
      setError(e?.message || 'Unknown error');
      setInvites((prev: unknown) => prev);
    } finally {
      setLoading(false);
    }
  }

  function parseBulk(text: string): string[] {
    return Array.from(new Set(
      text
        .split(/[\s,;]+/)
        .map((s: unknown) => s.trim())
        .filter((s: unknown) => /.+@.+\..+/.test(s))
    ));
  }

  async function submitBulk() {
    const emails = parseBulk(bulkText);
    if (emails.length === 0) return;
    setLoading(true); setError(null); setResult(null);
    let success = 0; let failed = 0;
    for (const e of emails) {
      try {
        const resResult = await tryCatch(async () =>
          fetch(`/api/organizations/${orgId}/memberships`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ email: e, role: bulkRole, mode: 'invite' })
          })
        );
        if (!resResult.success) {
          reportError(resResult.error);
          throw new Error(resResult.error.message);
        }
        const res = resResult.data;
        if (!res.ok) failed++; else success++;
      } catch {
        failed++;
      }
    }
    await Promise.all([fetchInvites(), fetchUsage()]);
    setLoading(false);
    setResult(`Bulk invites complete: ${success} sent, ${failed} failed`);
  }

  const renderUsageInfo = () => {
    if (!usage) {
      return <div className="color-foreground/60">Loading seat usageâ€¦</div>;
    }

    if (usage.seat_policy === 'domain-unlimited') {
      return (
        <div className="flex items-center justify-between">
          <div>
            <span className="form-label">Seat policy:</span> Domain-unlimited
          </div>
          <div className="color-foreground/70">Active members: {usage.active_count}</div>
        </div>
      );
    }

    return (
      <div className="flex items-center justify-between">
        <div>
          <span className="form-label">Seat policy:</span> Per-user
        </div>
        <div className="color-foreground/70">
          Active members: {usage.active_count}
          {usage.seats_limit != null && ` / ${usage.seats_limit}`}
        </div>
      </div>
    );
  };

  return (
    <div className="stack-lg">
      <div className="rounded-md border bg-secondary/30 p-sm text-body-sm">
        {renderUsageInfo()}
      </div>

      <div className="stack-sm">
        <div className="text-body-sm color-foreground/70">
          Invite teammates by email. Team plan supports unlimited seats for emails matching your active organization domains.
        </div>
        <div className="flex flex-col gap-sm sm:flex-row sm:items-center">
          <UnifiedInput
            type="email"
            value={email}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setEmail(e.target.value)}
            placeholder="teammate@company.com"
            variant={error ? 'error' : 'default'}
            error={error || undefined}
          />
          <select
            value={inviteRole}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setInviteRole(e.target.value)}
            className="h-10 rounded-md border bg-background  px-md text-body-sm"
          >
            {ROLES.map((r: unknown) => (
              <option key={r} value={r}>{r}</option>
            ))}
          </select>
          {canManage ? (
            <div className="flex gap-sm">
              <Button onClick={() => submit('invite')} disabled={loading || !validEmail}>Invite</Button>
              <Button variant="outline" onClick={() => submit('addExisting')} disabled={loading || !validEmail}>Add Existing</Button>
            </div>
          ) : (
            <div className="text-body-sm color-foreground/60">Only owners and admins can send invites.</div>
          )}
        </div>
        {emailDomain ? (
          <div className="text-body-sm flex items-center gap-sm">
            {domainRecord && domainRecord.status === 'active' ? (
              <span className="color-success">Domain matches active organization domain â€” seat not counted.</span>
            ) : domainRecord && domainRecord.status === 'pending' ? (
              <span className="color-warning">Domain is pending verification â€” may not count toward domain-unlimited yet.</span>
            ) : (
              <>
                <span className="color-foreground/60">External domain â€” will count against seat limit if enforced.</span>
                <Link
                  href="#"
                  title="Add your organization email domain(s). When seat policy is domain-unlimited, invites matching active domains do not consume seats."
                  className="inline-flex items-center rounded-md border  px-md py-xs text-[11px] hover:bg-secondary/50"
                >
                  Manage domains
                </Link>
              </>
            )}
          </div>
        ) : null}
        {error ? <div className="text-body-sm color-destructive">{error}</div> : null}
        {result ? <div className="text-body-sm color-success">{result}</div> : null}
      </div>

      <div className="stack-sm">
        <div className="text-body-sm form-label">Bulk invites</div>
        <div className="text-body-sm color-foreground/70">Paste a list of emails (comma, space, or newline separated).</div>
        <textarea
          value={bulkText}
          onChange={(e: React.ChangeEvent<HTMLInputElement>) => setBulkText(e.target.value)}
          rows={4}
          className="w-full rounded-md border bg-background  px-md py-sm text-body-sm focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring"
          placeholder="user1@company.com, user2@company.com\nuser3@other.com"
        />
        <div className="flex items-center gap-sm">
          <span className="text-body-sm color-foreground/60">Role for all:</span>
          <select
            value={bulkRole}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setBulkRole(e.target.value)}
            className="h-9 rounded-md border bg-background  px-md text-body-sm"
          >
            {ROLES.map((r: unknown) => (
              <option key={r} value={r}>{r}</option>
            ))}
          </select>
          <Button onClick={submitBulk} disabled={loading || parseBulk(bulkText).length === 0 || !canManage}>Invite All</Button>
          <div className="text-body-sm color-foreground/60">Detected: {parseBulk(bulkText).length} email(s)</div>
        </div>
      </div>

      <div className="rounded-md border">
        <table className="w-full text-body-sm">
          <thead>
            <tr className="bg-secondary/40">
              <th className=" px-md py-sm text-left">Email</th>
              <th className=" px-md py-sm text-left">Role</th>
              <th className=" px-md py-sm text-left">Status</th>
              <th className=" px-md py-sm text-right">Actions</th>
            </tr>
          </thead>
          <tbody>
            {invites.map((inv: unknown) => (
              <tr key={inv.id} className="border-t">
                <td className=" px-md py-sm">{inv.email}</td>
                <td className=" px-md py-sm">{inv.role}</td>
                <td className=" px-md py-sm">
                  <span className={
                    `inline-flex items-center rounded-full px-sm py-0.5 text-body-sm ` +
                    (inv.status === 'pending' ? 'bg-warning/10 color-warning' :
                     inv.status === 'accepted' ? 'bg-success/10 color-success' :
                     inv.status === 'revoked' ? 'bg-secondary/10 color-muted' :
                     'bg-secondary/10 color-muted')
                  }>
                    {inv.status}
                  </span>
                </td>
                <td className=" px-md py-sm text-right cluster-sm">
                  <Button onClick={() => resendInvite(inv.id)} disabled={loading || !canManage || inv.status !== 'pending'}>
                    Resend
                  </Button>
                  <Button onClick={() => revokeInvite(inv.id)} disabled={loading || !canManage || inv.status !== 'pending'}>
                    Revoke
                  </Button>
                </td>
              </tr>
            ))}
            {invites.length === 0 ? (
              <tr>
                <td className=" px-md py-lg text-center color-foreground/60" colSpan={4}>No invites yet.</td>
              </tr>
            ) : null}
          </tbody>
        </table>
      </div>
    </div>
  );
}

