'use client';

/**
 * Assets Kanban View
 *
 * Enterprise-grade kanban board view for asset management.
 * Features drag-and-drop functionality, swimlanes, WIP limits,
 * card customization, and workflow management.
 *
 * @module assets/views/KanbanView
 */

import React, { useState, useCallback, useMemo } from 'react';
import { Asset, AssetViewState } from '../types';
import { apiClient } from '../lib/api';
import { subscribeToAssets, unsubscribe, type AssetChangePayload } from '../lib/realtime';
import {
  Badge,
  Button,
  Card,
  CardContent,
  CardHeader,
  CardTitle,
  Checkbox
} from "@ghxstship/ui";
import { Button } from '@ghxstship/ui';
import { Badge } from '@ghxstship/ui';
import { Checkbox } from '@ghxstship/ui';
import { Dropdown, type DropdownItem } from '@ghxstship/ui';
import {
  MoreHorizontal,
  Edit,
  Eye,
  Trash2,
  Copy,
  Download,
  Plus,
  User,
  MapPin,
  Clock,
  AlertTriangle,
  CheckCircle,
  Package
} from 'lucide-react';

interface KanbanViewProps {
  viewState: AssetViewState;
  onViewStateChange: (newState: Partial<AssetViewState>) => void;
  onAssetSelect?: (asset: Asset, selected: boolean) => void;
  onAssetAction?: (action: string, asset: Asset) => void;
  className?: string;
}

type AssetRecord = Asset & {
  assignedToUser?: { name: string; avatar?: string; email?: string };
  locationInfo?: { name: string };
  supplierInfo?: { name: string };
  warrantyExpiry?: Date | null;
};

interface KanbanColumn {
  id: string;
  title: string;
  status: string;
  assets: AssetRecord[];
  wipLimit?: number;
  color: string;
}

interface KanbanCard {
  asset: AssetRecord;
  isSelected: boolean;
  isDragging: boolean;
}

const KANBAN_COLUMNS: KanbanColumn[] = [
  {
    id: 'available',
    title: 'Available',
    status: 'available',
    assets: [],
    color: 'bg-green-50 border-green-200'
  },
  {
    id: 'in_use',
    title: 'In Use',
    status: 'in_use',
    assets: [],
    wipLimit: 20,
    color: 'bg-blue-50 border-blue-200'
  },
  {
    id: 'maintenance',
    title: 'Maintenance',
    status: 'maintenance',
    assets: [],
    wipLimit: 5,
    color: 'bg-yellow-50 border-yellow-200'
  },
  {
    id: 'retired',
    title: 'Retired',
    status: 'retired',
    assets: [],
    color: 'bg-gray-50 border-gray-200'
  }
];

export default function KanbanView({
  viewState,
  onViewStateChange,
  onAssetSelect,
  onAssetAction,
  className = ''
}: KanbanViewProps) {
  const [assets, setAssets] = useState<AssetRecord[]>([]);
  const [loading, setLoading] = useState(false);
  const [selectedCards, setSelectedCards] = useState<Set<string>>(new Set());
  const [draggedAsset, setDraggedAsset] = useState<AssetRecord | null>(null);
  const [dragOverColumn, setDragOverColumn] = useState<string | null>(null);

  const normalizeAsset = useCallback((record: any): AssetRecord => {
    const assignedRelation = record?.assigned_to;
    const locationRelation = record?.location;
    const supplierRelation = record?.supplier;

    return {
      ...record,
      assigned_to:
        typeof assignedRelation === 'string'
          ? assignedRelation
          : assignedRelation?.id ?? record?.assigned_to ?? undefined,
      assignedToUser:
        assignedRelation && typeof assignedRelation === 'object'
          ? {
              name: assignedRelation.name as string,
              avatar: assignedRelation.avatar as string | undefined,
              email: assignedRelation.email as string | undefined
            }
          : undefined,
      locationInfo:
        locationRelation && typeof locationRelation === 'object'
          ? { name: locationRelation.name as string }
          : undefined,
      supplierInfo:
        supplierRelation && typeof supplierRelation === 'object'
          ? { name: supplierRelation.name as string }
          : undefined,
      warrantyExpiry: record?.warranty_expiry
        ? new Date(record.warranty_expiry as string | number | Date)
        : undefined
    } as AssetRecord;
  }, []);

  // Fetch assets
  const fetchAssets = useCallback(async () => {
    setLoading(true);
    try {
      const response = await apiClient.getAssets(
        viewState.filters,
        viewState.sort,
        { ...viewState.pagination, pageSize: 200 } // Load more for kanban
      );
      const enriched = (response.data ?? []).map((item: any) => normalizeAsset(item));
      setAssets(enriched);
      onViewStateChange({
        pagination: {
          ...viewState.pagination,
          total: response.pagination.total
        }
      });
    } catch (error) {
      console.error('Failed to fetch assets:', error);
    } finally {
      setLoading(false);
    }
  }, [viewState.filters, viewState.sort, viewState.pagination, onViewStateChange, normalizeAsset]);

  // Set up realtime subscription
  React.useEffect(() => {
    const channel = subscribeToAssets(
      'org-id', // Would come from context
      (payload: AssetChangePayload) => {
        if (payload.eventType === 'INSERT' && payload.new) {
          const next = normalizeAsset(payload.new);
          setAssets(prev => {
            if (prev.some(asset => asset.id === next.id)) {
              return prev.map(asset => (asset.id === next.id ? { ...asset, ...next } : asset));
            }
            return [next, ...prev];
          });
        } else if (payload.eventType === 'UPDATE' && payload.new) {
          const updated = normalizeAsset(payload.new);
          setAssets(prev =>
            prev.map(asset => (asset.id === updated.id ? { ...asset, ...updated } : asset))
          );
        } else if (payload.eventType === 'DELETE' && payload.old) {
          const removed = payload.old as AssetRecord;
          setAssets(prev => prev.filter(asset => asset.id !== removed.id));
        }
      }
    );

    return () => {
      void unsubscribe(channel);
    };
  }, [normalizeAsset]);

  // Load data on mount and when filters change
  React.useEffect(() => {
    fetchAssets();
  }, [fetchAssets]);

  // Group assets by status for kanban columns
  const kanbanData = useMemo(() => {
    const grouped = assets.reduce((acc, asset) => {
      const status = asset.status;
      if (!acc[status]) {
        acc[status] = [];
      }
      acc[status].push(asset);
      return acc;
    }, {} as Record<string, AssetRecord[]>);

    return KANBAN_COLUMNS.map(column => ({
      ...column,
      assets: grouped[column.status] || []
    }));
  }, [assets]);

  const buildDropdownItems = useCallback((asset: AssetRecord): DropdownItem[] => [
    {
      id: 'view',
      label: 'View Details',
      icon: Eye,
      onClick: () => onAssetAction?.('view', asset)
    },
    {
      id: 'edit',
      label: 'Edit Asset',
      icon: Edit,
      onClick: () => onAssetAction?.('edit', asset)
    },
    {
      id: 'separator-1',
      label: 'separator',
      separator: true
    },
    {
      id: 'duplicate',
      label: 'Duplicate',
      icon: Copy,
      onClick: () => onAssetAction?.('duplicate', asset)
    },
    {
      id: 'export',
      label: 'Export',
      icon: Download,
      onClick: () => onAssetAction?.('export', asset)
    },
    {
      id: 'separator-2',
      label: 'separator',
      separator: true
    },
    {
      id: 'delete',
      label: 'Delete',
      icon: Trash2,
      onClick: () => onAssetAction?.('delete', asset)
    }
  ], [onAssetAction]);

  // Handle card selection
  const handleCardSelect = useCallback((assetId: string, selected: boolean) => {
    setSelectedCards(prev => {
      const newSet = new Set(prev);
      if (selected) {
        newSet.add(assetId);
      } else {
        newSet.delete(assetId);
      }
      return newSet;
    });
    const asset = assets.find(a => a.id === assetId);
    if (asset) {
      onAssetSelect?.(asset, selected);
    }
  }, [assets, onAssetSelect, setSelectedCards]);

  // Handle drag start
  const handleDragStart = useCallback((asset: AssetRecord) => {
    setDraggedAsset(asset);
  }, []);

  // Handle drag end
  const handleDragEnd = useCallback(() => {
    setDraggedAsset(null);
    setDragOverColumn(null);
  }, []);

  // Handle drop
  const handleDrop = useCallback(async (columnId: string) => {
    if (!draggedAsset || draggedAsset.status === columnId) return;

    try {
      // Update asset status via API
      await apiClient.updateAsset(draggedAsset.id, { status: columnId as any });

      // Optimistically update local state
      setAssets(prev =>
        prev.map(asset =>
          asset.id === draggedAsset.id
            ? { ...asset, status: columnId as any }
            : asset
        )
      );

      onAssetAction?.('status_changed', { ...draggedAsset, status: columnId as any });
    } catch (error) {
      console.error('Failed to update asset status:', error);
      // Revert optimistic update on error
      setAssets(prev =>
        prev.map(asset =>
          asset.id === draggedAsset.id ? draggedAsset : asset
        )
      );
    }

    setDraggedAsset(null);
    setDragOverColumn(null);
  }, [draggedAsset, onAssetAction]);

  // Handle drag over
  const handleDragOver = useCallback((columnId: string) => {
    setDragOverColumn(columnId);
  }, []);

  // Render kanban card
  const renderKanbanCard = (asset: AssetRecord) => {
    const isSelected = selectedCards.has(asset.id);
    const isDragging = draggedAsset?.id === asset.id;
    const assignee = asset.assignedToUser;
    const location = asset.locationInfo;
    const warrantyExpiry = asset.warrantyExpiry ?? null;

    return (
      <Card
        key={asset.id}
        className={`mb-3 cursor-pointer transition-all ${
          isSelected ? 'ring-2 ring-blue-500' : ''
        } ${isDragging ? 'opacity-50 rotate-2' : ''} hover:shadow-md`}
        draggable
        onDragStart={() => handleDragStart(asset)}
        onDragEnd={handleDragEnd}
        onClick={() => onAssetAction?.('view', asset)}
      >
        <CardHeader className="pb-2">
          <div className="flex items-start justify-between">
            <div className="flex items-center gap-xs">
              <Checkbox
                checked={isSelected}
                onChange={(event) => handleCardSelect(asset.id, event.target.checked)}
                onClick={(event) => event.stopPropagation()}
              />
              {asset.image_urls?.[0] ? (
                <img
                  src={asset.image_urls[0]}
                  alt={asset.name}
                  className="w-icon-lg h-icon-lg rounded object-cover bg-gray-100"
                />
              ) : (
                <div className="w-icon-lg h-icon-lg rounded bg-gray-100 flex items-center justify-center">
                  <Package className="w-icon-xs h-icon-xs text-gray-400" />
                </div>
              )}
            </div>
            <div onClick={(event) => event.stopPropagation()}>
              <Dropdown
                align="end"
                trigger={
                  <Button
                    variant="ghost"
                    size="sm"
                    className="h-icon-md w-icon-md p-0"
                  >
                    <MoreHorizontal className="h-3 w-3" />
                  </Button>
                }
                items={buildDropdownItems(asset)}
              />
            </div>
          </div>
        </CardHeader>

        <CardContent className="pt-0">
          <div className="space-y-xs">
            <div>
              <CardTitle className="text-sm font-medium line-clamp-xs">
                {asset.name}
              </CardTitle>
              <div className="flex items-center gap-xs mt-1">
                <Badge variant="secondary" className="font-mono text-xs">
                  {asset.asset_tag}
                </Badge>
              </div>
            </div>

            <div className="flex items-center justify-between text-xs text-gray-600">
              <Badge variant="secondary" className="text-xs capitalize">
                {asset.category?.replace('_', ' ')}
              </Badge>
              <span className={`capitalize ${
                asset.condition === 'excellent' ? 'text-green-600' :
                asset.condition === 'good' ? 'text-blue-600' :
                asset.condition === 'fair' ? 'text-yellow-600' :
                asset.condition === 'poor' ? 'text-orange-600' :
                'text-red-600'
              }`}>
                {asset.condition?.replace('_', ' ')}
              </span>
            </div>

            {assignee?.name && (
              <div className="flex items-center gap-xs text-xs text-gray-600">
                <User className="w-3 h-3" />
                <span className="truncate">
                  {assignee.name}
                </span>
              </div>
            )}

            {location?.name && (
              <div className="flex items-center gap-xs text-xs text-gray-600">
                <MapPin className="w-3 h-3" />
                <span className="truncate">
                  {location?.name}
                </span>
              </div>
            )}

            {warrantyExpiry && (
              <div className="flex items-center gap-xs text-xs text-gray-600">
                <Clock className="w-3 h-3" />
                <span>
                  {warrantyExpiry > new Date() ? 'Warranty Active' : 'Warranty Expired'}
                </span>
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    );
  };

  // Render kanban column
  const renderKanbanColumn = (column: KanbanColumn & { assets: AssetRecord[] }) => {
    const isDragOver = dragOverColumn === column.id;
    const wipLimitExceeded = column.wipLimit && column.assets.length > column.wipLimit;

    return (
      <div
        key={column.id}
        className={`flex flex-col bg-gray-50 rounded-lg p-md min-h-container-lg ${
          isDragOver ? 'ring-2 ring-blue-300 bg-blue-50' : ''
        } ${column.color}`}
        onDragOver={(e) => {
          e.preventDefault();
          handleDragOver(column.id);
        }}
        onDrop={(event) => {
          event.preventDefault();
          handleDrop(column.id);
        }}
      >
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center gap-xs">
            <h3 className="font-semibold text-gray-900">{column.title}</h3>
            <Badge variant="secondary" className="text-xs">
              {column.assets.length}
              {column.wipLimit && ` / ${column.wipLimit}`}
            </Badge>
            {wipLimitExceeded && (
              <AlertTriangle className="w-icon-xs h-icon-xs text-red-500" />
            )}
          </div>
          <Button
            variant="ghost"
            size="sm"
            className="h-icon-md w-icon-md p-0"
            onClick={() => onAssetAction?.('create', { status: column.status } as any)}
          >
            <Plus className="h-3 w-3" />
          </Button>
        </div>

        <div className="flex-1 space-y-sm overflow-y-auto">
          {column.assets.map(renderKanbanCard)}

          {column.assets.length === 0 && (
            <div className="flex flex-col items-center justify-center py-xl text-gray-400">
              <Package className="w-icon-lg h-icon-lg mb-2" />
              <span className="text-sm">No assets</span>
            </div>
          )}
        </div>

        {wipLimitExceeded && (
          <div className="mt-2 text-xs text-red-600 font-medium">
            WIP limit exceeded ({column.assets.length - (column.wipLimit || 0)} over)
          </div>
        )}
      </div>
    );
  };

  return (
    <div className={`flex flex-col h-full ${className}`}>
      {/* Kanban Controls */}
      <div className="flex items-center justify-between p-md border-b">
        <div className="flex items-center gap-md">
          <span className="text-sm text-gray-600">
            {assets.length} total assets
          </span>
          {selectedCards.size > 0 && (
            <span className="text-sm text-blue-600 font-medium">
              {selectedCards.size} selected
            </span>
          )}
        </div>
        <div className="flex items-center gap-xs">
          {selectedCards.size > 0 && (
            <>
              <Button
                variant="outline"
                size="sm"
                onClick={() => setSelectedCards(new Set())}
              >
                Clear Selection
              </Button>
              <Button variant="outline" size="sm">
                Bulk Actions
              </Button>
            </>
          )}
        </div>
      </div>

      {/* Kanban Board */}
      <div className="flex-1 overflow-x-auto p-md">
        {loading ? (
          <div className="flex items-center justify-center h-container-sm">
            <div className="animate-spin rounded-full h-icon-lg w-icon-lg border-b-2 border-gray-900"></div>
            <span className="ml-2">Loading kanban board...</span>
          </div>
        ) : (
          <div className="flex gap-lg min-w-max">
            {kanbanData.map(renderKanbanColumn)}
          </div>
        )}
      </div>

      {/* WIP Limits Indicator */}
      <div className="px-md pb-4">
        <div className="flex gap-xs text-xs text-gray-600">
          <span>WIP Limits:</span>
          {KANBAN_COLUMNS.filter(col => col.wipLimit).map(col => (
            <Badge
              key={col.id}
              variant={(kanbanData.find(k => k.id === col.id)?.assets.length ?? 0) > (col.wipLimit ?? 0) ? 'error' : 'secondary'}
              className="text-xs"
            >
              {col.title}: {kanbanData.find(k => k.id === col.id)?.assets.length || 0}/{col.wipLimit}
            </Badge>
          ))}
        </div>
      </div>
    </div>
  );
}

'use client';

/**
 * Assets List View
 *
 * Enterprise-grade list view component for asset management.
 * Features hierarchical organization, inline actions, density options,
 * and nested item support.
 *
 * @module assets/views/ListView
 */

import React, { useState, useCallback, useMemo } from 'react';
import { Asset, AssetViewState } from '../types';
import { apiClient } from '../lib/api';
import { realtimeService } from '../lib/realtime';
import {
  Badge,
  Button,
  Checkbox,
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
  ListView
} from "@ghxstship/ui";
import { Badge ,
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator
} from '@ghxstship/ui';
import { Checkbox ,
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator
} from '@ghxstship/ui';
import {
  Dropdown,

  DropdownItem,

  DropdownMenuSeparator
} from '@ghxstship/ui';
import {
  ChevronRight,
  ChevronDown,
  MoreHorizontal,
  Edit,
  Eye,
  Trash2,
  Copy,
  Download,
  MapPin,
  User,
  Package,
  AlertTriangle,
  CheckCircle,
  Clock,
  DollarSign
} from 'lucide-react';

interface ListViewProps {
  viewState: AssetViewState;
  onViewStateChange: (newState: Partial<AssetViewState>) => void;
  onAssetSelect?: (asset: Asset, selected: boolean) => void;
  onAssetAction?: (action: string, asset: Asset) => void;
  className?: string;
}

interface ListItem {
  asset: Asset;
  level: number;
  expanded: boolean;
  hasChildren: boolean;
  parentId?: string;
}

type Density = 'compact' | 'comfortable' | 'spacious';
type Grouping = 'none' | 'category' | 'status' | 'location';

const DENSITY_CONFIG = {
  compact: {
    itemHeight: 'h-icon-2xl',
    fontSize: 'text-sm',
    padding: 'py-xs px-sm',
    iconSize: 'w-icon-xs h-icon-xs'
  },
  comfortable: {
    itemHeight: 'h-component-md',
    fontSize: 'text-base',
    padding: 'py-sm px-md',
    iconSize: 'w-icon-sm h-icon-sm'
  },
  spacious: {
    itemHeight: 'h-component-lg',
    fontSize: 'text-lg',
    padding: 'py-md px-lg',
    iconSize: 'w-icon-md h-icon-md'
  }
};

export default function ListView({
  viewState,
  onViewStateChange,
  onAssetSelect,
  onAssetAction,
  className = ''
}: ListViewProps) {
  const [assets, setAssets] = useState<Asset[]>([]);
  const [loading, setLoading] = useState(false);
  const [selectedItems, setSelectedItems] = useState<Set<string>(new Set());
  const [expandedGroups, setExpandedGroups] = useState<Set<string>(new Set());
  const [density, setDensity] = useState<Density>('comfortable');
  const [grouping, setGrouping] = useState<Grouping>('none');

  // Fetch assets
  const fetchAssets = useCallback(async () => {
    setLoading(true);
    try {
      const response = await apiClient.getAssets(
        viewState.filters,
        viewState.sort,
        viewState.pagination
      );
      setAssets(response.data);
      onViewStateChange({
        pagination: {
          ...viewState.pagination,
          total: response.pagination.total
        }
      });
    } catch (error) {
      console.error('Failed to fetch assets:', error);
    } finally {
      setLoading(false);
    }
  }, [viewState.filters, viewState.sort, viewState.pagination, onViewStateChange]);

  // Set up realtime subscription
  React.useEffect(() => {
    const unsubscribe = realtimeService.subscribeToAssets(
      'org-id', // Would come from context
      (event, record, oldRecord) => {
        if (event === 'INSERT') {
          setAssets(prev => [record, ...prev]);
        } else if (event === 'UPDATE') {
          setAssets(prev =>
            prev.map(asset =>
              asset.id === record.id ? { ...asset, ...record } : asset
            )
          );
        } else if (event === 'DELETE') {
          setAssets(prev => prev.filter(asset => asset.id !== record.id));
        }
      }
    );

    return unsubscribe;
  }, []);

  // Load data on mount and when filters/sort change
  React.useEffect(() => {
    fetchAssets();
  }, [fetchAssets]);

  // Group assets
  const groupedAssets = useMemo(() => {
    if (grouping === 'none') {
      return [{
        key: 'all',
        label: 'All Assets',
        assets,
        expanded: true,
        count: assets.length
      }];
    }

    const groups: Record<string, Asset[]> = {};

    assets.forEach(asset => {
      let groupKey: string;
      let groupLabel: string;

      switch (grouping) {
        case 'category':
          groupKey = asset.category || 'uncategorized';
          groupLabel = asset.category?.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase()) || 'Uncategorized';
          break;
        case 'status':
          groupKey = asset.status;
          groupLabel = asset.status.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase());
          break;
        case 'location':
          groupKey = asset.location?.id || 'unassigned';
          groupLabel = asset.location?.name || 'Unassigned';
          break;
        default:
          groupKey = 'all';
          groupLabel = 'All Assets';
      }

      if (!groups[groupKey]) {
        groups[groupKey] = [];
      }
      groups[groupKey].push(asset);
    });

    return Object.entries(groups).map(([key, groupAssets]) => ({
      key,
      label: key === 'all' ? 'All Assets' : Object.keys(groups).indexOf(key) >= 0 ?
        Object.values(groups)[Object.keys(groups).indexOf(key)]?.[0]?.category?.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase()) || key : key,
      assets: groupAssets,
      expanded: expandedGroups.has(key),
      count: groupAssets.length
    })).sort((a, b) => a.label.localeCompare(b.label));
  }, [assets, grouping, expandedGroups]);

  // Handle item selection
  const handleItemSelect = useCallback((assetId: string, selected: boolean) => {
    setSelectedItems(prev => {
      const newSet = new Set(prev);
      if (selected) {
        newSet.add(assetId);
      } else {
        newSet.delete(assetId);
      }
      return newSet;
    });
    const asset = assets.find(a => a.id === assetId);
    if (asset) {
      onAssetSelect?.(asset, selected);
    }
  }, [assets, onAssetSelect, setSelectedItems]);

  // Handle bulk selection
  const handleSelectAll = useCallback((selected: boolean) => {
    if (selected) {
      const allIds = new Set(assets.map(a => a.id));
      setSelectedItems(allIds);
      assets.forEach(asset => onAssetSelect?.(asset, true));
    } else {
      setSelectedItems(new Set());
      assets.forEach(asset => onAssetSelect?.(asset, false));
    }
  }, [assets, onAssetSelect, setSelectedItems]);

  // Handle group expansion
  const handleGroupToggle = useCallback((groupKey: string) => {
    setExpandedGroups(prev => {
      const newSet = new Set(prev);
      if (newSet.has(groupKey)) {
        newSet.delete(groupKey);
      } else {
        newSet.add(groupKey);
      }
      return newSet;
    });
  }, [setExpandedGroups]);

  // Get status display
  const getStatusDisplay = (status: string) => {
    const statusConfig = {
      available: { color: 'text-green-600', icon: CheckCircle },
      in_use: { color: 'text-blue-600', icon: User },
      maintenance: { color: 'text-yellow-600', icon: AlertTriangle },
      retired: { color: 'text-gray-600', icon: Package },
      lost: { color: 'text-red-600', icon: AlertTriangle },
      damaged: { color: 'text-orange-600', icon: AlertTriangle }
    };
    return statusConfig[status as keyof typeof statusConfig] || statusConfig.retired;
  };

  // Render list item
  const renderListItem = (asset: Asset, level: number = 0) => {
    const isSelected = selectedItems.has(asset.id);
    const statusDisplay = getStatusDisplay(asset.status);
    const StatusIcon = statusDisplay.icon;
    const config = DENSITY_CONFIG[density];

    return (
      <div
        key={asset.id}
        className={`flex items-center border-b border-gray-100 hover:bg-gray-50 transition-colors cursor-pointer ${
          isSelected ? 'bg-blue-50' : ''
        }`}
        style={{ paddingLeft: `${level * 20 + 16}px` }}
        onClick={() => onAssetAction?.('view', asset)}
      >
        {/* Selection checkbox */}
        <div className={`${config.padding} flex items-center`}>
          <Checkbox
            checked={isSelected}
            onChange={(checked) => handleItemSelect(asset.id, checked)}
            onClick={(e) => e.stopPropagation()}
          />
        </div>

        {/* Asset icon */}
        <div className={`${config.padding} flex items-center`}>
          {asset.image_urls?.[0] ? (
            <img
              src={asset.image_urls[0]}
              alt={asset.name}
              className={`rounded ${config.iconSize} object-cover bg-gray-100`}
            />
          ) : (
            <div className={`rounded bg-gray-100 flex items-center justify-center ${config.iconSize}`}>
              <Package className={`${config.iconSize} text-gray-400`} />
            </div>
          )}
        </div>

        {/* Asset info */}
        <div className="flex-1 min-w-0">
          <div className={`flex items-center gap-sm ${config.padding}`}>
            <div className="flex-1 min-w-0">
              <div className={`font-medium truncate ${config.fontSize}`}>
                {asset.name}
              </div>
              <div className="flex items-center gap-xs mt-1">
                <Badge variant="outline" className="font-mono text-xs">
                  {asset.asset_tag}
                </Badge>
                <Badge variant="secondary" className="text-xs capitalize">
                  {asset.category?.replace('_', ' ')}
                </Badge>
                <div className={`flex items-center gap-xs ${statusDisplay.color}`}>
                  <StatusIcon className="w-3 h-3" />
                  <span className="text-xs capitalize">
                    {asset.status.replace('_', ' ')}
                  </span>
                </div>
              </div>
            </div>

            {/* Quick info */}
            <div className="flex items-center gap-md text-sm text-gray-600">
              {asset.assigned_to && (
                <div className="flex items-center gap-xs">
                  <User className="w-icon-xs h-icon-xs" />
                  <span className="truncate max-w-component-lg">
                    {asset.assigned_to.name}
                  </span>
                </div>
              )}
              {asset.location && (
                <div className="flex items-center gap-xs">
                  <MapPin className="w-icon-xs h-icon-xs" />
                  <span className="truncate max-w-component-lg">
                    {asset.location.name}
                  </span>
                </div>
              )}
              {asset.purchase_price && (
                <div className="flex items-center gap-xs">
                  <DollarSign className="w-icon-xs h-icon-xs" />
                  <span>${asset.purchase_price.toLocaleString()}</span>
                </div>
              )}
              {asset.warranty_expiry && (
                <div className="flex items-center gap-xs">
                  <Clock className="w-icon-xs h-icon-xs" />
                  <span>
                    {new Date(asset.warranty_expiry) > new Date() ? 'Active' : 'Expired'}
                  </span>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Actions */}
        <div className={`${config.padding} flex items-center`}>
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button
                variant="ghost"
                size="sm"
                className="h-icon-lg w-icon-lg p-0"
                onClick={(e) => e.stopPropagation()}
              >
                <MoreHorizontal className="h-icon-xs w-icon-xs" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
              <DropdownMenuItem onClick={(e) => { e.stopPropagation(); onAssetAction?.('view', asset); }}>
                <Eye className="mr-2 h-icon-xs w-icon-xs" />
                View Details
              </DropdownMenuItem>
              <DropdownMenuItem onClick={(e) => { e.stopPropagation(); onAssetAction?.('edit', asset); }}>
                <Edit className="mr-2 h-icon-xs w-icon-xs" />
                Edit Asset
              </DropdownMenuItem>
              <DropdownMenuSeparator />
              <DropdownMenuItem onClick={(e) => { e.stopPropagation(); onAssetAction?.('duplicate', asset); }}>
                <Copy className="mr-2 h-icon-xs w-icon-xs" />
                Duplicate
              </DropdownMenuItem>
              <DropdownMenuItem onClick={(e) => { e.stopPropagation(); onAssetAction?.('export', asset); }}>
                <Download className="mr-2 h-icon-xs w-icon-xs" />
                Export
              </DropdownMenuItem>
              <DropdownMenuSeparator />
              <DropdownMenuItem
                onClick={(e) => { e.stopPropagation(); onAssetAction?.('delete', asset); }}
                className="text-red-600"
              >
                <Trash2 className="mr-2 h-icon-xs w-icon-xs" />
                Delete
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>
    );
  };

  // Render group header
  const renderGroupHeader = (group: { key: string; label: string; assets: Asset[]; expanded: boolean; count: number }) => {
    const config = DENSITY_CONFIG[density];

    return (
      <div
        key={group.key}
        className="bg-gray-50 border-b-2 border-gray-200 cursor-pointer hover:bg-gray-100 transition-colors"
        onClick={() => handleGroupToggle(group.key)}
      >
        <div className={`flex items-center ${config.padding}`}>
          <div className="flex items-center gap-xs">
            {group.expanded ? (
              <ChevronDown className="w-icon-xs h-icon-xs text-gray-500" />
            ) : (
              <ChevronRight className="w-icon-xs h-icon-xs text-gray-500" />
            )}
            <span className={`font-medium text-gray-900 ${config.fontSize}`}>
              {group.label}
            </span>
            <Badge variant="secondary" className="text-xs">
              {group.count}
            </Badge>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className={`flex flex-col h-full bg-white ${className}`}>
      {/* List Controls */}
      <div className="flex items-center justify-between p-md border-b">
        <div className="flex items-center gap-md">
          <div className="flex items-center gap-xs">
            <span className="text-sm font-medium">Density:</span>
            <select
              value={density}
              onChange={(e) => setDensity(e.target.e.target.value as Density)}
              className="text-sm border rounded px-xs py-xs"
            >
              <option value="compact">Compact</option>
              <option value="comfortable">Comfortable</option>
              <option value="spacious">Spacious</option>
            </select>
          </div>
          {grouping !== 'none' && (
            <div className="flex items-center gap-xs">
              <span className="text-sm font-medium">Grouping:</span>
              <select
                value={grouping}
                onChange={(e) => setGrouping(e.target.e.target.value as Grouping)}
                className="text-sm border rounded px-xs py-xs"
              >
                <option value="none">None</option>
                <option value="category">Category</option>
                <option value="status">Status</option>
                <option value="location">Location</option>
              </select>
            </div>
          )}
          <span className="text-sm text-gray-600">
            {assets.length} assets
          </span>
          {selectedItems.size > 0 && (
            <span className="text-sm text-blue-600 font-medium">
              {selectedItems.size} selected
            </span>
          )}
        </div>
        <div className="flex items-center gap-xs">
          {selectedItems.size > 0 && (
            <>
              <Button
                variant="outline"
                size="sm"
                onClick={() => handleSelectAll(false)}
              >
                Clear
              </Button>
              <Button variant="outline" size="sm">
                Bulk Actions
              </Button>
            </>
          )}
          <Button
            variant="outline"
            size="sm"
            onClick={() => handleSelectAll(selectedItems.size !== assets.length)}
          >
            {selectedItems.size === assets.length ? 'Deselect All' : 'Select All'}
          </Button>
        </div>
      </div>

      {/* List Content */}
      <div className="flex-1 overflow-auto">
        {loading ? (
          <div className="flex items-center justify-center h-container-sm">
            <div className="animate-spin rounded-full h-icon-lg w-icon-lg border-b-2 border-gray-900"></div>
            <span className="ml-2">Loading assets...</span>
          </div>
        ) : assets.length === 0 ? (
          <div className="flex flex-col items-center justify-center h-container-sm text-gray-500">
            <Package className="w-icon-2xl h-icon-2xl mb-4 text-gray-300" />
            <div className="text-lg font-medium">No assets found</div>
            <div className="text-sm">Try adjusting your filters</div>
          </div>
        ) : (
          <div className="divide-y divide-gray-100">
            {groupedAssets.map(group => (
              <React.Fragment key={group.key}>
                {grouping !== 'none' && renderGroupHeader(group)}
                {group.expanded && group.assets.map(asset => renderListItem(asset))}
              </React.Fragment>
            ))}
          </div>
        )}
      </div>

      {/* Load More */}
      {viewState.pagination.total &&
       viewState.pagination.page * viewState.pagination.pageSize < viewState.pagination.total && (
        <div className="flex justify-center p-md border-t">
          <Button
            variant="outline"
            onClick={() => onViewStateChange({
              pagination: {
                ...viewState.pagination,
                page: viewState.pagination.page + 1
              }
            })}
          >
            Load More Assets
          </Button>
        </div>
      )}
    </div>
  );
}

