  onAssetSelect?: (asset: Asset, selected: boolean) => void;
  onAssetAction?: (action: string, asset: Asset) => void;
  className?: string;
}

// Column configuration
interface ColumnConfig {
  key: keyof Asset | string;
  label: string;
  width?: number;
  sortable?: boolean;
  filterable?: boolean;
  editable?: boolean;
  frozen?: boolean;
