    )
  },
  {
    key: 'name',
    label: 'Asset Name',
    width: 200,
    sortable: true,
    filterable: true,
    editable: true,
    frozen: true,
    render: (value) => (
      <div className="font-medium truncate">{value}</div>
    )
  },
  {
    key: 'asset_tag',
    label: 'Asset Tag',
    width: 120,
    sortable: true,
    filterable: true,
    editable: true,
    render: (value) => (
      <Badge variant="outline" className="font-mono text-xs">
        {value}
      </Badge>
    )
  },
  {
    key: 'category',
    label: 'Category',
    width: 120,
    sortable: true,
    filterable: true,
    editable: true,
    render: (value) => (
      <Badge variant="secondary" className="text-xs capitalize">
