    width: 100,
    sortable: true,
    filterable: true,
    editable: true
  },
  {
    key: 'model',
    label: 'Model',
    width: 100,
    sortable: true,
    filterable: true,
    editable: true
  },
  {
    key: 'actions',
    label: '',
    width: 48,
    frozen: true,
    render: (value, asset) => (
      <DropdownMenu>
