  DrawerTitle,
  DrawerDescription,
  DrawerFooter
} from '@ghxstship/ui';
import { FilesService } from '../lib/files-service';
import type { DigitalAsset, AssetFilters } from '../types';
import { Download, Eye, Filter, Upload, FileCode, Image, Video, Music } from 'lucide-react';

const mediaTypeIcons = {
 image: Image,
 video: Video,
 audio: Music,
 other: FileCode
};

export default function MediaClient() {
 const [assets, setAssets] = useState<DigitalAsset[]>([]);
 const [loading, setLoading] = useState(true);
 const [error, setError] = useState<string | null>(null);
 const [selectedAssets, setSelectedAssets] = useState<string[]>([]);
 const [showUploadDrawer, setShowUploadDrawer] = useState(false);

 const filesService = useMemo(() => new FilesService(), []);

 const fetchMediaAssets = useCallback(async () => {
 try {
 setLoading(true);
 setError(null);

 // Filter for media types only
 const mediaFilters: AssetFilters = {
 category: 'image' // We'll need to expand this to support multiple media types
 };

 const result = await filesService.getAssets(undefined, {
 filters: mediaFilters,
 sortField: 'created_at',
 sortDirection: 'desc'
 });

 if (result.error) {
 setError(result.error);
 } else {
 setAssets(result.data?.data || []);
 }
 } catch (err) {
 console.error('Error fetching media assets:', err);
 setError(err instanceof Error ? err.message : 'Failed to load media assets');
 } finally {
 setLoading(false);
 }
 }, [filesService]);

 useEffect(() => {
 fetchMediaAssets();
 }, [fetchMediaAssets]);

 const handleUpload = useCallback(() => {
 setShowUploadDrawer(true);
 }, []);

 const handleView = useCallback(async (asset: DigitalAsset) => {
 try {
 await filesService.incrementViewCount(asset.id);
 await fetchMediaAssets();
 } catch (err) {
 console.error('Error updating view count:', err);
 }
 }, [filesService, fetchMediaAssets]);

 const handleDownload = useCallback(async (asset: DigitalAsset) => {
 if (!asset.file_url) return;

 try {
 await filesService.incrementDownloadCount(asset.id);
 window.open(asset.file_url, '_blank');
 await fetchMediaAssets();
 } catch (err) {
 console.error('Error downloading asset:', err);
 }
 }, [filesService, fetchMediaAssets]);

 // Calculate stats
 const stats = useMemo(() => {
 const totalSize = assets.reduce((sum, asset) => sum + (asset.file_size || 0), 0);
 const imageCount = assets.filter(asset => asset.category === 'image').length;
 const videoCount = assets.filter(asset => asset.category === 'video').length;
 const audioCount = assets.filter(asset => asset.category === 'audio').length;

 return {
 total: assets.length,
 totalSize: Math.round(totalSize / (1024 * 1024)), // Convert to MB
 imageCount,
 videoCount,
 audioCount
 };
 }, [assets]);

 return (
 <div className="stack-lg">
 {/* Header */}
 <div className="flex items-center justify-between">
 <div>
 <h1 className="text-heading-3">Media Assets</h1>
 <p className="color-muted">Manage multimedia content and digital assets</p>
 </div>
 <Button onClick={handleUpload}>
 <Upload className="w-icon-xs h-icon-xs mr-sm" />
 Upload Media
 </Button>
 </div>

 {/* Stats Cards */}
 <div className="grid grid-cols-1 md:grid-cols-4 gap-md">
 <Card>
 <div className="flex items-center gap-sm">
 <FileCode className="w-icon-sm h-icon-sm color-accent" />
 <div>
 <div className="text-heading-3">{stats.total}</div>
 <div className="text-body-sm color-muted">Total Assets</div>
 </div>
 </div>
 </Card>
 <Card>
 <div className="flex items-center gap-sm">
 <Image className="w-icon-sm h-icon-sm color-success" />
 <div>
 <div className="text-heading-3 color-success">{stats.imageCount}</div>
 <div className="text-body-sm color-muted">Images</div>
 </div>
 </div>
 </Card>
 <Card>
 <div className="flex items-center gap-sm">
 <Video className="w-icon-sm h-icon-sm color-warning" />
 <div>
 <div className="text-heading-3 color-warning">{stats.videoCount}</div>
 <div className="text-body-sm color-muted">Videos</div>
 </div>
 </div>
 </Card>
 <Card>
 <div className="flex items-center gap-sm">
 <Music className="w-icon-sm h-icon-sm color-secondary" />
 <div>
 <div className="text-heading-3 color-secondary">{stats.audioCount}</div>
 <div className="text-body-sm color-muted">Audio</div>
 </div>
 </div>
 </Card>
 </div>

 {/* Media Grid */}
 {loading && (
 <div className="text-center py-xl">
 <div className="animate-spin rounded-full h-icon-lg w-icon-lg border-b-2 border-primary mx-auto mb-md"></div>
 <p className="color-muted">Loading media assets...</p>
 </div>
 )}

 {error && (
 <Card>
 <div className="text-center py-xl">
 <FileCode className="w-icon-2xl h-icon-2xl color-destructive mx-auto mb-md" />
 <h3 className="text-body form-label color-foreground mb-sm">Error loading media</h3>
 <p className="color-muted mb-md">{error}</p>
 <Button onClick={fetchMediaAssets}>
 Try Again
 </Button>
 </div>
 </Card>
 )}

 {!loading && !error && assets.length === 0 && (
 <Card>
 <div className="text-center py-xl">
 <Image className="w-icon-2xl h-icon-2xl color-muted mx-auto mb-md" />
 <h3 className="text-body form-label color-foreground mb-sm">No media assets</h3>
 <p className="color-muted mb-md">
 Start building your media library by uploading images, videos, and audio files.
 </p>
 <Button onClick={handleUpload}>
 <Upload className="w-icon-xs h-icon-xs mr-sm" />
 Upload First Asset
 </Button>
 </div>
 </Card>
 )}

 {!loading && !error && assets.length > 0 && (
 <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
 {assets.map(asset => {
 const IconComponent = mediaTypeIcons[asset.category as keyof typeof mediaTypeIcons] || FileCode;
 const isSelected = selectedAssets.includes(asset.id);

 return (
 <Card 
 key={asset.id}
 className={`cursor-pointer hover:shadow-elevated transition-shadow ${
 isSelected ? 'ring-2 ring-primary' : ''
 }`}
 onClick={() => handleView(asset)}
 >
 <div className="aspect-square bg-muted/50 rounded-md mb-sm flex items-center justify-center">
 {asset.file_url && (asset.category === 'image') ? (
 <img 
 src={asset.file_url} 
 alt={asset.title}
 className="w-full h-full object-cover rounded-md"
 />
 ) : (
 <IconComponent className="w-icon-2xl h-icon-2xl color-muted" />
 )}
 </div>

 <div className="space-y-sm">
 <h3 className="text-body form-label line-clamp-xs">{asset.title}</h3>

 <div className="flex items-center justify-between">
 <Badge variant="secondary">{asset.category}</Badge>
 <div className="flex items-center gap-xs text-body-sm color-muted">
 <Eye className="w-3 h-3" />
 <span>{asset.view_count}</span>
 </div>
 </div>

 <div className="flex items-center gap-sm">
 <Button
 size="sm"
 variant="outline"
 onClick={(e) => {
 e.stopPropagation();
 handleDownload(asset);
 }}
 className="flex-1"
 >
 <Download className="w-3 h-3 mr-xs" />
 Download
 </Button>
 </div>
 </div>
 </Card>
 );
 })}
 </div>
 )}
 </div>
 );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2, Download, Eye, Edit } from 'lucide-react';
import { Card, Badge, Button } from '@ghxstship/ui';
import type { MediaAsset } from '../types';

const mediaTypeIcons = {
 image: Image,
 video: Video,
 audio: Music,
 other: FileCode
};

interface MediaGridViewProps {
 assets: MediaAsset[];
 onAssetClick: (asset: MediaAsset) => void;
 onAssetEdit: (asset: MediaAsset) => void;
 onAssetDownload: (asset: MediaAsset) => void;
 selectedAssets: string[];
 onSelectionChange: (assetIds: string[]) => void;
}

export default function MediaGridView({
 assets,
 onAssetClick,
 onAssetEdit,
 onAssetDownload,
 selectedAssets,
 onSelectionChange
}: MediaGridViewProps) {
 const handleAssetSelect = (assetId: string, selected: boolean) => {
 if (selected) {
 onSelectionChange([...selectedAssets, assetId]);
 } else {
 onSelectionChange(selectedAssets.filter(id => id !== assetId));
 }
 };

 const formatFileSize = (bytes: number) => {
 const sizes = ['Bytes', 'KB', 'MB', 'GB'];
 if (bytes === 0) return '0 Bytes';
 const i = Math.floor(Math.log(bytes) / Math.log(1024));
 return Math.round(bytes / Math.pow(1024, i) * 100) / 100 + ' ' + sizes[i];
 };

 const formatDuration = (seconds: number) => {
 const mins = Math.floor(seconds / 60);
 const secs = seconds % 60;
 return `${mins}:${secs.toString().padStart(2, '0')}`;
 };

 return (
 <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
 {assets.map(asset => {
 const IconComponent = mediaTypeIcons[asset.category] || FileCode;
 const isSelected = selectedAssets.includes(asset.id);

 return (
 <Card 
 key={asset.id}
 className={`cursor-pointer hover:shadow-elevated transition-shadow ${
 isSelected ? 'ring-2 ring-primary' : ''
 }`}
 onClick={() => onAssetClick(asset)}
 >
 <div className="relative">
 <input
 type="checkbox"
 checked={isSelected}
 onChange={(e) => {
 e.stopPropagation();
 handleAssetSelect(asset.id, e.target.checked);
 }}
 className="absolute top-sm left-sm z-10 rounded border-border"
 />

 <div className="aspect-square bg-muted/50 rounded-md mb-sm flex items-center justify-center relative overflow-hidden">
 {asset.thumbnail_url || (asset.category === 'image' && asset.file_url) ? (
 <img 
 src={asset.thumbnail_url || asset.file_url} 
 alt={asset.title}
 className="w-full h-full object-cover"
 />
 ) : (
 <IconComponent className="w-icon-2xl h-icon-2xl color-muted" />
 )}

 {asset.category === 'video' && (
 <div className="absolute inset-0 flex items-center justify-center bg-black/20">
 <Play className="w-icon-lg h-icon-lg text-white" />
 </div>
 )}

 {asset.duration && (
 <div className="absolute bottom-xs right-xs bg-black/70 text-white text-xs px-xs py-xxs rounded">
 {formatDuration(asset.duration)}
 </div>
 )}
 </div>
 </div>

 <div className="space-y-sm">
 <h3 className="text-body form-label line-clamp-xs">{asset.title}</h3>

 {asset.description && (
 <p className="text-body-sm color-muted line-clamp-xs">{asset.description}</p>
 )}

 <div className="flex items-center justify-between">
 <Badge variant="outline">{asset.category}</Badge>
 <span className="text-xs color-muted">{formatFileSize(asset.file_size)}</span>
 </div>

 {asset.dimensions && (
 <div className="text-xs color-muted">
 {asset.dimensions.width}  {asset.dimensions.height}
 </div>
 )}

 <div className="flex items-center justify-between text-body-sm color-muted">
 <div className="flex items-center gap-xs">
 <Eye className="w-3 h-3" />
 <span>{asset.view_count}</span>
 </div>
 <div className="flex items-center gap-xs">
 <Download className="w-3 h-3" />
 <span>{asset.download_count}</span>
 </div>
 </div>

 <div className="flex items-center gap-sm pt-sm border-t">
 <Button
 size="sm"
 variant="outline"
 onClick={(e) => {
 e.stopPropagation();
 onAssetEdit(asset);
 }}
 className="flex-1"
 >
 <Edit className="w-3 h-3 mr-xs" />
 Edit
 </Button>
 <Button
 size="sm"
 variant="outline"
 onClick={(e) => {
 e.stopPropagation();
 onAssetDownload(asset);
 }}
 className="flex-1"
 >
 <Download className="w-3 h-3 mr-xs" />
 Download
 </Button>
 </div>
 </div>
 </Card>
 );
 })}
 </div>
 );
}

'use client';

import { Plus, Search, Filter, Download, Upload, MoreHorizontal, FileText, Mic, Utensils, Settings, Calendar as CalendarIcon, Shield, Car, Hotel, Film, Users, Grid3X3, List, BarChart3, Clock } from "lucide-react";
import { useCallback, useEffect, useMemo, useState } from 'react';
import { createBrowserClient } from '@ghxstship/auth';
import {
 Badge,
 Button,
 Card,
 DataActions,
 DataGrid,
 DataViewProvider,
 KanbanBoard,
 ListView,
 StateManagerProvider,
 ViewSwitcher,
 Input,
 Select,
 SelectContent,
 SelectItem,
 SelectTrigger,
 SelectValue,
 Tabs,
 type DataRecord,
  Drawer,
  DrawerContent,
  DrawerHeader,
  DrawerTitle,
  DrawerDescription,
  DrawerFooter
} from "@ghxstship/ui";
import type { DataViewConfig, FieldConfig, FilterConfig, SortConfig } from '@ghxstship/ui/src/components/DataViews/types';

import type {
 ProgrammingRider,
 RiderFilters,
 RiderSort,
 ViewType,
 RiderKind,
 RiderStatus,
 RiderPriority,
 RiderEvent,
 RiderProject,
 STATUS_BADGE,
 PRIORITY_BADGE,
 RIDER_KIND_BADGE
} from './types';

// Import view components
import ProgrammingRidersListView from './views/ProgrammingRidersListView';
import ProgrammingRidersGridView from './views/ProgrammingRidersGridView';
import ProgrammingRidersTimelineView from './views/ProgrammingRidersTimelineView';
import ProgrammingRidersAnalyticsView from './views/ProgrammingRidersAnalyticsView';

// Import drawer components
import CreateProgrammingRiderDrawer from './drawers/CreateProgrammingRiderDrawer';
import EditProgrammingRiderDrawer from './drawers/EditProgrammingRiderDrawer';
import ViewProgrammingRiderDrawer from './drawers/ViewProgrammingRiderDrawer';

interface User {
 id: string;
 email: string;
 full_name?: string | null;
 avatar_url?: string | null;
}

interface ProgrammingRidersClientProps {
 orgId: string;
 currentUserId: string;
 initialRiders: ProgrammingRider[];
 projects: RiderProject[];
 events: RiderEvent[];
 users: User[];
}

export default function ProgrammingRidersClient({
 orgId,
 currentUserId,
 initialRiders,
 projects,
 events,
 users
}: ProgrammingRidersClientProps) {
 const supabase = useMemo(() => createBrowserClient(), []);

 // State management
 const [riders, setRiders] = useState<ProgrammingRider[]>(initialRiders);
 const [loading, setLoading] = useState(false);
 const [selectedRiders, setSelectedRiders] = useState<string[]>([]);
 const [currentView, setCurrentView] = useState<ViewType>('list');
 const [searchQuery, setSearchQuery] = useState('');
 const [filters, setFilters] = useState<RiderFilters>({});
 const [sort, setSort] = useState<RiderSort>({ field: 'created_at', direction: 'desc' });

 // Drawer state
 const [createDrawerOpen, setCreateDrawerOpen] = useState(false);
 const [editDrawerOpen, setEditDrawerOpen] = useState(false);
 const [viewDrawerOpen, setViewDrawerOpen] = useState(false);
 const [selectedRider, setSelectedRider] = useState<ProgrammingRider | null>(null);

 // Real-time subscription
 useEffect(() => {
 const channel = supabase
 .channel(`programming_riders_${orgId}`)
 .on(
 'postgres_changes',
 {
 event: '*',
 schema: 'public',
 table: 'programming_riders',
 filter: `organization_id=eq.${orgId}`
 },
 (payload) => {
 if (payload.eventType === 'INSERT') {
 fetchRiders();
 } else if (payload.eventType === 'UPDATE') {
 setRiders((prev: unknown) =>
 prev.map((rider) =>
 rider.id === payload.new.id ? { ...rider, ...payload.new } : rider
 )
 );
 } else if (payload.eventType === 'DELETE') {
 setRiders((prev: unknown) => prev.filter((rider) => rider.id !== payload.old.id));
 }
 }
 )
 .subscribe();

 return () => {
 supabase.removeChannel(channel);
 };
 }, [supabase, orgId]);

 // Fetch riders with filters
 const fetchRiders = useCallback(async () => {
 try {
 setLoading(true);
 const params = new URLSearchParams();

 if (filters.event_id) params.append('event_id', filters.event_id);
 if (filters.project_id) params.append('project_id', filters.project_id);
 if (filters.kind) params.append('kind', filters.kind);
 if (filters.status) params.append('status', filters.status);
 if (filters.priority) params.append('priority', filters.priority);
 if (searchQuery) params.append('search', searchQuery);
 if (filters.start_date) params.append('start_date', filters.start_date);
 if (filters.end_date) params.append('end_date', filters.end_date);
 if (filters.fulfilled !== undefined) params.append('fulfilled', filters.fulfilled.toString());
 if (filters.approved !== undefined) params.append('approved', filters.approved.toString());

 const response = await fetch(`/api/v1/programming/riders?${params}`);
 if (!response.ok) throw new Error('Failed to fetch riders');

 const data = await response.json();
 setRiders(data);
 } catch (error) {
 console.error('Error fetching riders:', error);
 } finally {
 setLoading(false);
 }
 }, [filters, searchQuery]);

 // Trigger fetch when filters change
 useEffect(() => {
 fetchRiders();
 }, [fetchRiders]);

 // Filter and sort riders
 const filteredAndSortedRiders = useMemo(() => {
 let filtered = [...riders];

 // Apply local search if no server search
 if (searchQuery && !filters.search) {
 const query = searchQuery.toLowerCase();
 filtered = filtered.filter(
 (rider) =>
 rider.title?.toLowerCase().includes(query) ||
 rider.description?.toLowerCase().includes(query) ||
 rider.requirements?.toLowerCase().includes(query) ||
 rider.event?.title?.toLowerCase().includes(query)
 );
 }

 // Apply sorting
 filtered.sort((a, b) => {
 const aValue = a[sort.field];
 const bValue = b[sort.field];

 if (aValue === null || aValue === undefined) return 1;
 if (bValue === null || bValue === undefined) return -1;

 if (typeof aValue === 'string' && typeof bValue === 'string') {
 return sort.direction === 'asc'
 ? aValue.localeCompare(bValue)
 : bValue.localeCompare(aValue);
 }

 if (aValue < bValue) return sort.direction === 'asc' ? -1 : 1;
 if (aValue > bValue) return sort.direction === 'asc' ? 1 : -1;
 return 0;
 });

 return filtered;
 }, [riders, searchQuery, filters.search, sort]);

 // Handlers
 const handleCreateRider = () => {
 setCreateDrawerOpen(true);
 };

 const handleEditRider = (rider: ProgrammingRider) => {
 setSelectedRider(rider);
 setEditDrawerOpen(true);
 };

 const handleViewRider = (rider: ProgrammingRider) => {
 setSelectedRider(rider);
 setViewDrawerOpen(true);
 };

 const handleDeleteRider = async (riderId: string) => {
 try {
 const response = await fetch(`/api/v1/programming/riders/${riderId}`, {
 method: 'DELETE'
 });

 if (!response.ok) throw new Error('Failed to delete rider');

 setRiders((prev: unknown) => prev.filter((rider) => rider.id !== riderId));
 } catch (error) {
 console.error('Error deleting rider:', error);
 }
 };

 const handleBulkAction = async (action: string, riderIds: string[]) => {
 try {
 setLoading(true);

 switch (action) {
 case 'delete':
 await Promise.all(
 riderIds.map((id) =>
 fetch(`/api/v1/programming/riders/${id}`, { method: 'DELETE' })
 )
 );
 setRiders((prev: unknown) => prev.filter((rider) => !riderIds.includes(rider.id)));
 break;
 case 'approve':
 await Promise.all(
 riderIds.map((id) =>
 fetch(`/api/v1/programming/riders/${id}`, {
 method: 'PATCH',
 headers: { 'Content-Type': 'application/json' },
 body: JSON.stringify({
 status: 'approved',
 approved_at: new Date().toISOString(),
 approved_by: currentUserId
 })
 })
 )
 );
 fetchRiders();
 break;
 case 'fulfill':
 await Promise.all(
 riderIds.map((id) =>
 fetch(`/api/v1/programming/riders/${id}`, {
 method: 'PATCH',
 headers: { 'Content-Type': 'application/json' },
 body: JSON.stringify({
 status: 'fulfilled',
 fulfilled_at: new Date().toISOString(),
 fulfilled_by: currentUserId
 })
 })
 )
 );
 fetchRiders();
 break;
 }

 setSelectedRiders([]);
 } catch (error) {
 console.error('Error performing bulk action:', error);
 } finally {
 setLoading(false);
 }
 };

 const handleSuccess = () => {
 fetchRiders();
 setCreateDrawerOpen(false);
 setEditDrawerOpen(false);
 setSelectedRider(null);
 };

 // Export functionality
 const handleExport = () => {
 const csvContent = [
 ['Title', 'Kind', 'Status', 'Priority', 'Event', 'Created At'].join(','),
 ...filteredAndSortedRiders.map((rider) =>
 [
 rider.title,
 rider.kind,
 rider.status,
 rider.priority,
 rider.event?.title || '',
 new Date(rider.created_at).toLocaleDateString(),
 ].join(',')
 ),
 ].join('\n');

 const blob = new Blob([csvContent], { type: 'text/csv' });
 const url = URL.createObjectURL(blob);
 const a = document.createElement('a');
 a.href = url;
 a.download = `programming-riders-${new Date().toISOString().split('T')[0]}.csv`;
 a.click();
 URL.revokeObjectURL(url);
 };

 // View icons
 const viewIcons = {
 list: List,
 grid: Grid3X3,
 timeline: Clock,
 analytics: BarChart3
 };

 return (
 <div className="space-y-lg">
 {/* Header */}
 <div className="flex items-center justify-between">
 <div>
 <h1 className="text-2xl font-bold">Programming Riders</h1>
 <p className="text-muted-foreground">
 Manage technical, hospitality, and production requirements
 </p>
 </div>
 <Button onClick={handleCreateRider}>
 <Plus className="mr-2 h-icon-xs w-icon-xs" />
 Create Rider
 </Button>
 </div>

 {/* Filters and Search */}
 <Card className="p-md">
 <div className="flex flex-col gap-md md:flex-row md:items-center md:justify-between">
 <div className="flex flex-1 items-center gap-md">
 <div className="relative flex-1 max-w-sm">
 <Search className="absolute left-3 top-xs/2 h-icon-xs w-icon-xs -translate-y-1/2 text-muted-foreground" />
 <Input
 placeholder="Search riders..."
 value={searchQuery}
 onChange={(e) => setSearchQuery(e.target.value)}
 className="pl-9"
 />
 </div>

 <Select
 value={filters.kind || ''}
 onChange={(e) =>
 setFilters((prev: unknown) => ({ ...prev, kind: e.target.value as RiderKind || undefined }))
 }
 >
 <SelectTrigger className="w-40">
 <SelectValue placeholder="All Types" />
 </SelectTrigger>
 <SelectContent>
 <SelectItem value="">All Types</SelectItem>
 <SelectItem value="technical">Technical</SelectItem>
 <SelectItem value="hospitality">Hospitality</SelectItem>
 <SelectItem value="stage_plot">Stage Plot</SelectItem>
 <SelectItem value="security">Security</SelectItem>
 <SelectItem value="catering">Catering</SelectItem>
 <SelectItem value="transportation">Transportation</SelectItem>
 <SelectItem value="accommodation">Accommodation</SelectItem>
 <SelectItem value="production">Production</SelectItem>
 <SelectItem value="artist">Artist</SelectItem>
 <SelectItem value="crew">Crew</SelectItem>
 </SelectContent>
 </Select>

 <Select
 value={filters.status || ''}
 onChange={(e) =>
 setFilters((prev: unknown) => ({ ...prev, status: e.target.value as RiderStatus || undefined }))
 }
 >
 <SelectTrigger className="w-40">
 <SelectValue placeholder="All Status" />
 </SelectTrigger>
 <SelectContent>
 <SelectItem value="">All Status</SelectItem>
 <SelectItem value="draft">Draft</SelectItem>
 <SelectItem value="pending_review">Pending Review</SelectItem>
 <SelectItem value="under_review">Under Review</SelectItem>
 <SelectItem value="approved">Approved</SelectItem>
 <SelectItem value="rejected">Rejected</SelectItem>
 <SelectItem value="fulfilled">Fulfilled</SelectItem>
 <SelectItem value="cancelled">Cancelled</SelectItem>
 </SelectContent>
 </Select>

 <Select
 value={filters.event_id || ''}
 onChange={(e) =>
 setFilters((prev: unknown) => ({ ...prev, event_id: e.target.value || undefined }))
 }
 >
 <SelectTrigger className="w-container-xs">
 <SelectValue placeholder="All Events" />
 </SelectTrigger>
 <SelectContent>
 <SelectItem value="">All Events</SelectItem>
 {events.map((event) => (
 <SelectItem key={event.id} value={event.id}>
 {event.title}
 </SelectItem>
 ))}
 </SelectContent>
 </Select>
 </div>

 <div className="flex items-center gap-xs">
 <Button variant="outline" size="sm" onClick={handleExport}>
 <Download className="mr-2 h-icon-xs w-icon-xs" />
 Export
 </Button>

 {/* View Switcher */}
 <div className="flex items-center rounded-lg border p-xs">
 {(['list', 'grid', 'timeline', 'analytics'] as ViewType[]).map((view) => {
 const Icon = viewIcons[view];
 return (
 <Button
 key={view}
 variant={currentView === view ? 'default' : 'ghost'}
 size="sm"
 onClick={() => setCurrentView(view)}
 className="h-icon-lg w-icon-lg p-0"
 >
 <Icon className="h-icon-xs w-icon-xs" />
 </Button>
 );
 })}
 </div>
 </div>
 </div>

 {/* Bulk Actions */}
 {selectedRiders.length > 0 && (
 <div className="mt-4 flex items-center gap-xs rounded-lg border bg-muted/50 p-sm">
 <span className="text-sm text-muted-foreground">
 {selectedRiders.length} rider(s) selected
 </span>
 <div className="flex gap-xs">
 <Button
 size="sm"
 variant="outline"
 onClick={() => handleBulkAction('approve', selectedRiders)}
 >
 Approve
 </Button>
 <Button
 size="sm"
 variant="outline"
 onClick={() => handleBulkAction('fulfill', selectedRiders)}
 >
 Fulfill
 </Button>
 <Button
 size="sm"
 variant="destructive"
 onClick={() => handleBulkAction('delete', selectedRiders)}
 >
 Delete
 </Button>
 </div>
 </div>
 )}
 </Card>

 {/* Content */}
 <div className="min-h-content-lg">
 {currentView === 'list' && (
 <ProgrammingRidersListView
 riders={filteredAndSortedRiders}
 loading={loading}
 selectedRiders={selectedRiders}
 onSelectionChange={setSelectedRiders}
 onEdit={handleEditRider}
 onView={handleViewRider}
 onDelete={handleDeleteRider}
 sort={sort}
 onSortChange={setSort}
 />
 )}

 {currentView === 'grid' && (
 <ProgrammingRidersGridView
 riders={filteredAndSortedRiders}
 loading={loading}
 onEdit={handleEditRider}
 onView={handleViewRider}
 onDelete={handleDeleteRider}
 />
 )}

 {currentView === 'timeline' && (
 <ProgrammingRidersTimelineView
 riders={filteredAndSortedRiders}
 loading={loading}
 onEdit={handleEditRider}
 onView={handleViewRider}
 />
 )}

 {currentView === 'analytics' && (
 <ProgrammingRidersAnalyticsView
 riders={filteredAndSortedRiders}
 loading={loading}
 />
 )}
 </div>

 {/* Drawers */}
 <CreateProgrammingRiderDrawer
 open={createDrawerOpen}
 onClose={() => setCreateDrawerOpen(false)}
 orgId={orgId}
 currentUserId={currentUserId}
 projects={projects}
 events={events}
 onSuccess={handleSuccess}
 />

 {selectedRider && (
 <>
 <EditProgrammingRiderDrawer
 open={editDrawerOpen}
 onClose={() => setEditDrawerOpen(false)}
 rider={selectedRider}
 orgId={orgId}
 currentUserId={currentUserId}
 projects={projects}
 events={events}
 onSuccess={handleSuccess}
 />

 <ViewProgrammingRiderDrawer
 open={viewDrawerOpen}
 onClose={() => setViewDrawerOpen(false)}
 rider={selectedRider}
 users={users}
 onEdit={() => {
 setViewDrawerOpen(false);
 setEditDrawerOpen(true);
 }}
 onDelete={() => {
 handleDeleteRider(selectedRider.id);
 setViewDrawerOpen(false);
 setSelectedRider(null);
 }}
 />
 </>
 )}
 </div>
 );
}

'use client';

import { MoreHorizontal, Edit, Eye, Trash2, ChevronDown, ChevronRight, Calendar, MapPin, User, Clock, CheckCircle, XCircle, AlertCircle } from "lucide-react";
import { useState } from 'react';
import {
 Badge,
 Button,
 Card,
 Checkbox,
 Table,
 TableBody,
 TableCell,
 TableHead,
 TableHeader,
 TableRow,
 Dropdown,

 DropdownItem,

 Collapsible,
 CollapsibleContent,
 CollapsibleTrigger,
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator
} from "@ghxstship/ui";

import type { ProgrammingRider, RiderSort, STATUS_BADGE, PRIORITY_BADGE, RIDER_KIND_BADGE } from '../types';

interface ProgrammingRidersListViewProps {
 riders: ProgrammingRider[];
 loading: boolean;
 selectedRiders: string[];
 onSelectionChange: (selected: string[]) => void;
 onEdit: (rider: ProgrammingRider) => void;
 onView: (rider: ProgrammingRider) => void;
 onDelete: (riderId: string) => void;
 sort: RiderSort;
 onSortChange: (sort: RiderSort) => void;
}

const STATUS_BADGE_CONFIG = {
 draft: { label: 'Draft', variant: 'secondary' as const },
 pending_review: { label: 'Pending Review', variant: 'warning' as const },
 under_review: { label: 'Under Review', variant: 'info' as const },
 approved: { label: 'Approved', variant: 'success' as const },
 rejected: { label: 'Rejected', variant: 'error' as const },
 fulfilled: { label: 'Fulfilled', variant: 'success' as const },
 cancelled: { label: 'Cancelled', variant: 'secondary' as const }
};

const PRIORITY_BADGE_CONFIG = {
 low: { label: 'Low', variant: 'secondary' as const },
 medium: { label: 'Medium', variant: 'secondary' as const },
 high: { label: 'High', variant: 'warning' as const },
 critical: { label: 'Critical', variant: 'error' as const },
 urgent: { label: 'Urgent', variant: 'error' as const }
};

const RIDER_KIND_CONFIG = {
 technical: { label: 'Technical', icon: '' },
 hospitality: { label: 'Hospitality', icon: '' },
 stage_plot: { label: 'Stage Plot', icon: '' },
 security: { label: 'Security', icon: '' },
 catering: { label: 'Catering', icon: '' },
 transportation: { label: 'Transportation', icon: '' },
 accommodation: { label: 'Accommodation', icon: '' },
 production: { label: 'Production', icon: '' },
 artist: { label: 'Artist', icon: '' },
 crew: { label: 'Crew', icon: '' }
};

export default function ProgrammingRidersListView({
 riders,
 loading,
 selectedRiders,
 onSelectionChange,
 onEdit,
 onView,
 onDelete,
 sort,
 onSortChange
}: ProgrammingRidersListViewProps) {
 const [expandedRows, setExpandedRows] = useState<string[]>([]);

 const handleSelectAll = (checked: boolean) => {
 if (checked) {
 onSelectionChange(riders.map((rider) => rider.id));
 } else {
 onSelectionChange([]);
 }
 };

 const handleSelectRider = (riderId: string, checked: boolean) => {
 if (checked) {
 onSelectionChange([...selectedRiders, riderId]);
 } else {
 onSelectionChange(selectedRiders.filter((id) => id !== riderId));
 }
 };

 const toggleRowExpansion = (riderId: string) => {
 setExpandedRows((prev: unknown) =>
 prev.includes(riderId)
 ? prev.filter((id) => id !== riderId)
 : [...prev, riderId]
 );
 };

 const handleSort = (field: keyof ProgrammingRider) => {
 const direction = sort.field === field && sort.direction === 'asc' ? 'desc' : 'asc';
 onSortChange({ field, direction });
 };

 const getSortIcon = (field: keyof ProgrammingRider) => {
 if (sort.field !== field) return null;
 return sort.direction === 'asc' ? '' : '';
 };

 if (loading) {
 return (
 <Card className="p-xl">
 <div className="flex items-center justify-center">
 <div className="animate-spin rounded-full h-icon-lg w-icon-lg border-b-2 border-primary"></div>
 <span className="ml-2">Loading riders...</span>
 </div>
 </Card>
 );
 }

 if (riders.length === 0) {
 return (
 <Card className="p-xl">
 <div className="text-center">
 <h3 className="text-lg font-semibold">No riders found</h3>
 <p className="text-muted-foreground">
 No riders match your current filters. Try adjusting your search criteria.
 </p>
 </div>
 </Card>
 );
 }

 return (
 <Card>
 <Table>
 <TableHeader>
 <TableRow>
 <TableHead className="w-icon-2xl">
 <Checkbox
 checked={selectedRiders.length === riders.length}
 onCheckedChange={handleSelectAll}
 aria-
 />
 </TableHead>
 <TableHead className="w-icon-2xl"></TableHead>
 <TableHead 
 className="cursor-pointer hover:bg-muted/50"
 onClick={() => handleSort('title')}
 >
 Title {getSortIcon('title')}
 </TableHead>
 <TableHead>Kind</TableHead>
 <TableHead 
 className="cursor-pointer hover:bg-muted/50"
 onClick={() => handleSort('status')}
 >
 Status {getSortIcon('status')}
 </TableHead>
 <TableHead 
 className="cursor-pointer hover:bg-muted/50"
 onClick={() => handleSort('priority')}
 >
 Priority {getSortIcon('priority')}
 </TableHead>
 <TableHead>Event</TableHead>
 <TableHead 
 className="cursor-pointer hover:bg-muted/50"
 onClick={() => handleSort('created_at')}
 >
 Created {getSortIcon('created_at')}
 </TableHead>
 <TableHead className="w-icon-2xl"></TableHead>
 </TableRow>
 </TableHeader>
 <TableBody>
 {riders.map((rider) => {
 const isExpanded = expandedRows.includes(rider.id);
 const isSelected = selectedRiders.includes(rider.id);

 return (
 <>
 <TableRow key={rider.id} className={isSelected ? 'bg-muted/50' : ''}>
 <TableCell>
 <Checkbox
 checked={isSelected}
 onCheckedChange={(checked) => handleSelectRider(rider.id, checked as boolean)}
 aria-label={`Select rider ${rider.title}`}
 />
 </TableCell>
 <TableCell>
 <Button
 variant="ghost"
 size="sm"
 onClick={() => toggleRowExpansion(rider.id)}
 className="h-icon-md w-icon-md p-0"
 >
 {isExpanded ? (
 <ChevronDown className="h-icon-xs w-icon-xs" />
 ) : (
 <ChevronRight className="h-icon-xs w-icon-xs" />
 )}
 </Button>
 </TableCell>
 <TableCell>
 <div className="font-medium">{rider.title}</div>
 {rider.description && (
 <div className="text-sm text-muted-foreground line-clamp-xs">
 {rider.description}
 </div>
 )}
 </TableCell>
 <TableCell>
 <div className="flex items-center gap-xs">
 <span className="text-lg">{RIDER_KIND_CONFIG[rider.kind]?.icon}</span>
 <span className="text-sm">{RIDER_KIND_CONFIG[rider.kind]?.label}</span>
 </div>
 </TableCell>
 <TableCell>
 <Badge variant={STATUS_BADGE_CONFIG[rider.status]?.variant}>
 {STATUS_BADGE_CONFIG[rider.status]?.label}
 </Badge>
 </TableCell>
 <TableCell>
 <Badge variant={PRIORITY_BADGE_CONFIG[rider.priority]?.variant}>
 {PRIORITY_BADGE_CONFIG[rider.priority]?.label}
 </Badge>
 </TableCell>
 <TableCell>
 {rider.event && (
 <div>
 <div className="font-medium">{rider.event.title}</div>
 <div className="text-sm text-muted-foreground flex items-center gap-xs">
 <Calendar className="h-3 w-3" />
 {new Date(rider.event.start_at).toLocaleDateString()}
 </div>
 </div>
 )}
 </TableCell>
 <TableCell>
 <div className="text-sm">
 {new Date(rider.created_at).toLocaleDateString()}
 </div>
 </TableCell>
 <TableCell>
 <DropdownMenu>
 <DropdownMenuTrigger asChild>
 <Button variant="ghost" size="sm" className="h-icon-lg w-icon-lg p-0">
 <MoreHorizontal className="h-icon-xs w-icon-xs" />
 </Button>
 </DropdownMenuTrigger>
 <DropdownMenuContent align="end">
 <DropdownMenuItem onClick={() => onView(rider)}>
 <Eye className="mr-2 h-icon-xs w-icon-xs" />
 View
 </DropdownMenuItem>
 <DropdownMenuItem onClick={() => onEdit(rider)}>
 <Edit className="mr-2 h-icon-xs w-icon-xs" />
 Edit
 </DropdownMenuItem>
 <DropdownMenuItem
 onClick={() => onDelete(rider.id)}
 className="text-destructive"
 >
 <Trash2 className="mr-2 h-icon-xs w-icon-xs" />
 Delete
 </DropdownMenuItem>
 </DropdownMenuContent>
 </DropdownMenu>
 </TableCell>
 </TableRow>

 {/* Expanded Row Content */}
 {isExpanded && (
 <TableRow>
 <TableCell colSpan={9} className="bg-muted/25">
 <div className="p-md space-y-md">
 <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-md">
 {/* Requirements */}
 <div>
 <h4 className="font-semibold mb-2">Requirements</h4>
 <p className="text-sm text-muted-foreground">
 {rider.requirements}
 </p>
 </div>

 {/* Project */}
 {rider.project && (
 <div>
 <h4 className="font-semibold mb-2">Project</h4>
 <div className="flex items-center gap-xs">
 <Badge variant="secondary">{rider.project.name}</Badge>
 <Badge variant="secondary">{rider.project.status}</Badge>
 </div>
 </div>
 )}

 {/* Fulfillment Status */}
 <div>
 <h4 className="font-semibold mb-2">Fulfillment</h4>
 <div className="flex items-center gap-xs">
 {rider.fulfilled_at ? (
 <>
 <CheckCircle className="h-icon-xs w-icon-xs text-green-600" />
 <span className="text-sm">
 Fulfilled {new Date(rider.fulfilled_at).toLocaleDateString()}
 </span>
 </>
 ) : rider.approved_at ? (
 <>
 <AlertCircle className="h-icon-xs w-icon-xs text-yellow-600" />
 <span className="text-sm">Approved, pending fulfillment</span>
 </>
 ) : (
 <>
 <XCircle className="h-icon-xs w-icon-xs text-gray-400" />
 <span className="text-sm">Not fulfilled</span>
 </>
 )}
 </div>
 </div>
 </div>

 {/* Notes */}
 {rider.notes && (
 <div>
 <h4 className="font-semibold mb-2">Notes</h4>
 <p className="text-sm text-muted-foreground">{rider.notes}</p>
 </div>
 )}

 {/* Tags */}
 {rider.tags && rider.tags.length > 0 && (
 <div>
 <h4 className="font-semibold mb-2">Tags</h4>
 <div className="flex flex-wrap gap-xs">
 {rider.tags.map((tag, index) => (
 <Badge key={index} variant="secondary" className="text-xs">
 {tag}
 </Badge>
 ))}
 </div>
 </div>
 )}

 {/* Specific Requirements */}
 {rider.kind === 'technical' && rider.technical_requirements && (
 <div>
 <h4 className="font-semibold mb-2">Technical Requirements</h4>
 <div className="grid grid-cols-2 gap-xs text-sm">
 {rider.technical_requirements.sound_system && (
 <div>Sound System: {rider.technical_requirements.sound_system}</div>
 )}
 {rider.technical_requirements.lighting && (
 <div>Lighting: {rider.technical_requirements.lighting}</div>
 )}
 {rider.technical_requirements.power_requirements && (
 <div>Power: {rider.technical_requirements.power_requirements}</div>
 )}
 {rider.technical_requirements.crew_requirements && (
 <div>Crew: {rider.technical_requirements.crew_requirements}</div>
 )}
 </div>
 </div>
 )}

 {rider.kind === 'hospitality' && rider.hospitality_requirements && (
 <div>
 <h4 className="font-semibold mb-2">Hospitality Requirements</h4>
 <div className="grid grid-cols-2 gap-xs text-sm">
 {rider.hospitality_requirements.catering && (
 <div>Catering: {rider.hospitality_requirements.catering}</div>
 )}
 {rider.hospitality_requirements.beverages && (
 <div>Beverages: {rider.hospitality_requirements.beverages}</div>
 )}
 {rider.hospitality_requirements.green_room_setup && (
 <div>Green Room: {rider.hospitality_requirements.green_room_setup}</div>
 )}
 </div>
 </div>
 )}
 </div>
 </TableCell>
 </TableRow>
 )}
 </>
 );
 })}
 </TableBody>
 </Table>
 </Card>
 );
}

