  { value: 'pending', label: 'Pending' },
  { value: 'in_progress', label: 'In Progress' },
  { value: 'compliant', label: 'Compliant' },
  { value: 'non_compliant', label: 'Non-Compliant' },
  { value: 'expired', label: 'Expired' },
  { value: 'waived', label: 'Waived' },
];

const KIND_OPTIONS = [
  { value: 'all', label: 'All Types' },
  { value: 'regulatory', label: 'Regulatory' },
  { value: 'safety', label: 'Safety' },
  { value: 'quality', label: 'Quality' },
  { value: 'security', label: 'Security' },
  { value: 'environmental', label: 'Environmental' },
  { value: 'legal', label: 'Legal' },
  { value: 'financial', label: 'Financial' },
];

const PRIORITY_OPTIONS = [
  { value: 'all', label: 'All Priorities' },
  { value: 'low', label: 'Low' },
  { value: 'medium', label: 'Medium' },
  { value: 'high', label: 'High' },
  { value: 'critical', label: 'Critical' },
];

export function ComplianceClient({ user, orgId, translations }: ComplianceClientProps) {
  const [compliance, setCompliance] = useState<JobCompliance[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [kindFilter, setKindFilter] = useState('all');
  const [priorityFilter, setPriorityFilter] = useState('all');
  const [showCreateDialog, setShowCreateDialog] = useState(false);

  const supabase = createBrowserClient();

  useEffect(() => {
    loadCompliance();
  }, [orgId]);

  const loadCompliance = async () => {
    try {
      setLoading(true);

      // Load compliance items with related job data
      const { data, error } = await supabase
        .from('job_compliance')
        .select(`
          *,
          jobs (
            title
          )
        `)
        .eq('organization_id', orgId)
        .order('created_at', { ascending: false });

      if (error) throw error;

      // Transform the data to flatten relationships
      const transformedCompliance = (data || []).map((item: any) => ({
        ...item,
        job_title: item.jobs?.title || 'Unknown Job'
      }));

      setCompliance(transformedCompliance);
    } catch (error) {
      console.error('Error loading compliance:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredCompliance = compliance.filter((item: any) => {
    const matchesSearch = item.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         item.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (item.job_title && item.job_title.toLowerCase().includes(searchTerm.toLowerCase())) ||
                         (item.notes && item.notes.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesStatus = statusFilter === 'all' || item.status === statusFilter;
    const matchesKind = kindFilter === 'all' || item.kind === kindFilter;
    const matchesPriority = priorityFilter === 'all' || item.priority === priorityFilter;

    return matchesSearch && matchesStatus && matchesKind && matchesPriority;
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending':
        return 'bg-warning/10 color-warning';
      case 'in_progress':
        return 'bg-accent/10 color-accent';
      case 'compliant':
        return 'bg-success/10 color-success';
      case 'non_compliant':
        return 'bg-destructive/10 color-destructive';
      case 'expired':
        return 'bg-warning/10 color-warning';
      case 'waived':
        return 'bg-secondary color-muted';
      default:
        return 'bg-secondary color-muted';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'pending':
        return ClockIcon;
      case 'in_progress':
        return DocumentTextIcon;
      case 'compliant':
        return CheckCircleIcon;
      case 'non_compliant':
        return XCircleIcon;
      case 'expired':
        return ExclamationTriangleIcon;
      case 'waived':
        return DocumentCheckIcon;
      default:
        return ClockIcon;
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'low':
        return 'bg-secondary color-muted';
      case 'medium':
        return 'bg-accent/10 color-accent';
      case 'high':
        return 'bg-warning/10 color-warning';
      case 'critical':
        return 'bg-destructive/10 color-destructive';
      default:
        return 'bg-secondary color-muted';
    }
  };

  const getKindColor = (kind: string) => {
    switch (kind) {
      case 'regulatory':
        return 'bg-secondary/10 color-secondary';
      case 'safety':
        return 'bg-destructive/10 color-destructive';
      case 'quality':
        return 'bg-accent/10 color-accent';
      case 'security':
        return 'bg-accent/10 color-accent';
      case 'environmental':
        return 'bg-success/10 color-success';
      case 'legal':
        return 'bg-warning/10 color-warning';
      case 'financial':
        return 'bg-warning/10 color-warning';
      default:
        return 'bg-secondary color-muted';
    }
  };

  const isOverdue = (dueDate?: string) => {
    if (!dueDate) return false;
    return new Date(dueDate) < new Date();
  };

  const isDueSoon = (dueDate?: string) => {
    if (!dueDate) return false;
    const due = new Date(dueDate);
    const now = new Date();
    const diffDays = Math.ceil((due.getTime() - now.getTime()) / (1000 * 60 * 60 * 24));
    return diffDays <= 7 && diffDays >= 0;
  };

  return (
    <div className="stack-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-3 text-heading-3 color-foreground">{translations.title}</h1>
          <p className="text-body-sm color-foreground/70 mt-xs">{translations.subtitle}</p>
        </div>
        <Button onClick={() => setShowCreateDialog(true)}>
          <PlusIcon className="h-icon-xs w-icon-xs mr-sm" />
          Add Compliance Item
        </Button>
      </div>

      {/* Filters */}
      <Card className="p-md">
        <div className="flex flex-col sm:flex-row gap-md">
          <div className="flex-1">
            <Input               placeholder="Search compliance items..."
              value={searchTerm}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => setSearchTerm(e.target.value)}
              className="w-full"
            />
          </div>
          <div className="flex gap-sm">
            <select
              value={statusFilter}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => setStatusFilter(e.target.value)}
              className=" px-md py-sm border border-border rounded-md bg-background"
            >
              {STATUS_OPTIONS.map((option: any) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            <select
              value={kindFilter}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => setKindFilter(e.target.value)}
              className=" px-md py-sm border border-border rounded-md bg-background"
            >
              {KIND_OPTIONS.map((option: any) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            <select
              value={priorityFilter}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => setPriorityFilter(e.target.value)}
              className=" px-md py-sm border border-border rounded-md bg-background"
            >
              {PRIORITY_OPTIONS.map((option: any) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          </div>
        </div>
      </Card>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-5 gap-md">
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Total Items</p>
              <p className="text-heading-3 text-heading-3 color-foreground">{compliance.length}</p>
            </div>
            <ShieldCheckIcon className="h-icon-lg w-icon-lg color-accent" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Compliant</p>
              <p className="text-heading-3 text-heading-3 color-success">
                {compliance.filter(c => c.status === 'compliant').length}
              </p>
            </div>
            <CheckCircleIcon className="h-icon-lg w-icon-lg color-success" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Non-Compliant</p>
              <p className="text-heading-3 text-heading-3 color-destructive">
                {compliance.filter(c => c.status === 'non_compliant').length}
              </p>
            </div>
            <XCircleIcon className="h-icon-lg w-icon-lg color-destructive" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Overdue</p>
              <p className="text-heading-3 text-heading-3 color-warning">
                {compliance.filter(c => isOverdue(c.due_at)).length}
              </p>
            </div>
            <ExclamationTriangleIcon className="h-icon-lg w-icon-lg color-warning" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Critical</p>
              <p className="text-heading-3 text-heading-3 color-secondary">
                {compliance.filter(c => c.priority === 'critical').length}
              </p>
            </div>
            <ExclamationTriangleIcon className="h-icon-lg w-icon-lg color-secondary" />
          </div>
        </Card>
      </div>

      {/* Compliance List */}
      <div className="stack-md">
        {loading ? (
          Array.from({ length: 5 }).map((_, i) => (
            <Card key={i} className="p-lg">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <Skeleton className="h-icon-sm w-container-sm mb-sm" />
                  <Skeleton className="h-icon-xs w-component-xl mb-sm" />
                  <Skeleton className="h-icon-xs w-full mb-sm" />
                  <Skeleton className="h-icon-xs w-3/4" />
                </div>
                <div className="flex gap-sm">
                  <Skeleton className="h-icon-md w-component-md" />
                  <Skeleton className="h-icon-md w-component-lg" />
                </div>
              </div>
            </Card>
          ))
        ) : filteredCompliance.length > 0 ? (
          filteredCompliance.map((item: any) => {
            const StatusIcon = getStatusIcon(item.status);
            const isLate = isOverdue(item.due_at);
            const isDue = isDueSoon(item.due_at);

            return (
              <Card key={item.id} className={`p-lg hover:shadow-elevated transition-shadow ${isLate ? 'border-destructive/20 bg-destructive/5' : isDue ? 'border-warning/20 bg-warning/5' : ''}`}>
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-start justify-between mb-sm">
                      <div>
                        <h3 className="text-body text-heading-4 color-foreground mb-xs flex items-center gap-sm">
                          <StatusIcon className="h-icon-sm w-icon-sm color-foreground/60" />
                          {item.title}
                          {(isDue || isLate) && (
                            <ExclamationTriangleIcon className={`h-icon-xs w-icon-xs ${isLate ? 'color-destructive' : 'color-warning'}`} />
                          )}
                        </h3>
                        <div className="flex items-center gap-sm text-body-sm color-foreground/70">
                          <BuildingOfficeIcon className="h-icon-xs w-icon-xs" />
                          <span>{item.job_title}</span>
                          {item.responsible_party && (
                            <>
                              <span></span>
                              <span>Responsible: {item.responsible_party}</span>
                            </>
                          )}
                        </div>
                      </div>
                      <div className="flex gap-sm">
                        <Badge className={getStatusColor(item.status)}>
                          {item.status.replace('_', ' ')}
                        </Badge>
                        <Badge className={getKindColor(item.kind)}>
                          {item.kind}
                        </Badge>
                        <Badge className={getPriorityColor(item.priority)}>
                          {item.priority}
                        </Badge>
                      </div>
                    </div>

                    <p className="text-body-sm color-foreground/80 mb-sm line-clamp-xs">
                      {item.description}
                    </p>

                    <div className="flex items-center gap-lg text-body-sm color-foreground/70 mb-sm">
                      {item.due_at && (
                        <div className={`flex items-center gap-xs ${isLate ? 'color-destructive' : isDue ? 'color-warning' : ''}`}>
                          <CalendarIcon className="h-icon-xs w-icon-xs" />
                          <span>
                            {isLate ? 'Overdue: ' : 'Due: '}
                            {new Date(item.due_at).toLocaleDateString()}
                          </span>
                        </div>
                      )}
                      {item.completed_at && (
                        <div className="flex items-center gap-xs color-success">
                          <CheckCircleIcon className="h-icon-xs w-icon-xs" />
                          <span>Completed: {new Date(item.completed_at).toLocaleDateString()}</span>
                        </div>
                      )}
                      {item.reviewer && (
                        <div className="flex items-center gap-xs">
                          <span>Reviewer: {item.reviewer}</span>
                        </div>
                      )}
                    </div>

                    {item.requirements && item.requirements.length > 0 && (
                      <div className="mb-sm">
                        <span className="text-body-sm form-label color-foreground/70 mb-xs block">Requirements:</span>
                        <div className="stack-md">
                          {item.requirements.slice(0, 3).map((req, index) => (
                            <div key={index} className="text-body-sm color-foreground/70 flex items-start gap-sm">
                              <span className="color-foreground/40"></span>
                              <span>{req}</span>
                            </div>
                          ))}
                          {item.requirements.length > 3 && (
                            <div className="text-body-sm color-foreground/50">
                              +{item.requirements.length - 3} more requirements
                            </div>
                          )}
                        </div>
                      </div>
                    )}

                    {item.notes && (
                      <div className="mt-sm p-sm bg-accent rounded-md">
                        <p className="text-body-sm color-foreground/70">
                          <strong>Notes:</strong> {item.notes}
                        </p>
                      </div>
                    )}
                  </div>
                </div>

                <div className="flex items-center justify-between mt-md pt-md border-t border-border">
                  <div className="text-body-sm color-foreground/50">
                    Created: {new Date(item.created_at).toLocaleDateString()}
                    {item.updated_at !== item.created_at && (
                      <span>  Updated: {new Date(item.updated_at).toLocaleDateString()}</span>
                    )}
                  </div>
                  <div className="flex gap-sm">
                    <Button>
                      <EyeIcon className="h-icon-xs w-icon-xs mr-xs" />
                      View
                    </Button>
                    <Button>
                      <PencilIcon className="h-icon-xs w-icon-xs mr-xs" />
                      Edit
                    </Button>
                    {item.evidence_url && (
                      <Button>
                        <ArrowDownTrayIcon className="h-icon-xs w-icon-xs mr-xs" />
                        Evidence
                      </Button>
                    )}
                    <Button>
                      <DocumentCheckIcon className="h-icon-xs w-icon-xs mr-xs" />
                      Audit
                    </Button>
                  </div>
                </div>
              </Card>
            );
          })
        ) : (
          <Card className="p-xsxl text-center">
            <ShieldCheckIcon className="h-icon-2xl w-icon-2xl color-foreground/30 mx-auto mb-md" />
            <h3 className="text-body form-label color-foreground mb-sm">No compliance items found</h3>
            <p className="text-body-sm color-foreground/70 mb-md">
              {searchTerm || statusFilter !== 'all' || kindFilter !== 'all' || priorityFilter !== 'all'
                ? 'Try adjusting your filters or search terms.'
                : 'Get started by adding your first compliance requirement.'}
            </p>
            <Button onClick={() => setShowCreateDialog(true)}>
              <PlusIcon className="h-icon-xs w-icon-xs mr-sm" />
              Add Compliance Item
            </Button>
          </Card>
        )}
      </div>
    </div>
  );
}

'use client';


import { useState, useEffect } from 'react';
import { User } from '@supabase/supabase-js';
import {
  Badge,
  Button,
  Card,
  Input,
  Skeleton,
  UnifiedInput
} from "@ghxstship/ui";
import {
  Plus,
  Search,
  Filter,
  FileText,
  Clock,
  CheckCircle,
  XCircle,
  Calendar,
  DollarSign,
  Building,
  AlertTriangle,
  Eye,
  Download,
  Edit,
  BarChart3
} from 'lucide-react';

// Domain interface for JobContract
interface JobContract {
  id: string;
  organizationId: string;
  jobId: string;
  companyId: string;
  contractType: 'msa' | 'sow' | 'amendment' | 'termination';
  title: string;
  description?: string;
  status: 'draft' | 'pending_review' | 'approved' | 'active' | 'completed' | 'terminated' | 'cancelled';
  startDate: string;
  endDate?: string;
  value: number;
  currency: string;
  paymentTerms?: string;
  documentUrl?: string;
  signedAt?: string;
  notes?: string;
  createdAt?: string;
  updatedAt?: string;
  milestones?: JobContractMilestone[];
  jobs?: { title: string };
  companies?: { name: string };
  end_date?: string;
  contract_type?: string;
  job_title?: string;
  company_name?: string;
  start_date?: string;
  signed_at?: string;
  created_at?: string;
  document_url?: string;
}

interface JobContractMilestone {
  id: string;
  contractId: string;
  title: string;
  description?: string;
  status: 'pending' | 'in_progress' | 'completed' | 'cancelled';
  dueDate: string;
  completedAt?: string;
  value?: number;
  createdAt: string;
  updatedAt: string;
}

interface ContractsClientProps {
  user: User;
}

const STATUS_OPTIONS = [
  { value: 'all', label: 'All Statuses' },
  { value: 'draft', label: 'Draft' },
  { value: 'pending_review', label: 'Pending Review' },
  { value: 'approved', label: 'Approved' },
  { value: 'active', label: 'Active' },
  { value: 'completed', label: 'Completed' },
  { value: 'terminated', label: 'Terminated' },
  { value: 'cancelled', label: 'Cancelled' },
];

const TYPE_OPTIONS = [
  { value: 'all', label: 'All Types' },
  { value: 'msa', label: 'MSA' },
  { value: 'sow', label: 'SOW' },
  { value: 'amendment', label: 'Amendment' },
  { value: 'termination', label: 'Termination' },
];

const translations = {
  title: 'Contracts',
  subtitle: 'Manage job contracts and agreements'
};

function ContractsClient({ user }: ContractsClientProps) {
  const [contracts, setContracts] = useState<JobContract[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [typeFilter, setTypeFilter] = useState('all');
  const [showCreateDialog, setShowCreateDialog] = useState(false);

  // Fetch contracts from API
  useEffect(() => {
    fetchContracts();
  }, []);

  const fetchContracts = async () => {
    try {
      setLoading(true);
      const orgId = user.user_metadata?.organizationId;
      if (!orgId) return;

      const response = await fetch('/api/v1/jobs/contracts', {
        headers: {
          'x-org-id': orgId
        }
      });

      if (response.ok) {
        const data = await response.json();
        setContracts(data);
      }
    } catch (error) {
      console.error('Error fetching contracts:', error);
    } finally {
      setLoading(false);
    }
  };

  // Filter contracts based on search term, status, and type
  const filteredContracts = contracts.filter(contract => {
    const matchesSearch = contract.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         contract.description?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === 'all' || contract.status === statusFilter;
    const matchesType = typeFilter === 'all' || contract.contractType === typeFilter;

    return matchesSearch && matchesStatus && matchesType;
  });

  // Helper functions for formatting and status
  const formatAmount = (amount: number, currency: string = 'USD') => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency
    }).format(amount);
  };

  const formatDate = (dateValue: string | Date | null | undefined): string => {
    if (!dateValue) return 'N/A';
    try {
      const date = typeof dateValue === 'string' ? new Date(dateValue) : dateValue;
      return date.toLocaleDateString();
    } catch {
      return 'Invalid Date';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'draft': return FileText;
      case 'pending': return Clock;
      case 'active': return CheckCircle;
      case 'completed': return CheckCircle;
      case 'terminated': return CheckCircle;
      case 'cancelled': return XCircle;
      case 'expired': return XCircle;
      default: return FileText;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'draft': return 'bg-secondary color-muted border-border';
      case 'pending_review': return 'bg-warning/10 color-warning border-warning/20';
      case 'approved': return 'bg-accent/10 color-accent border-primary/20';
      case 'active': return 'bg-success/10 color-success border-success/20';
      case 'completed': return 'bg-success/10 color-success border-success/20';
      case 'terminated': return 'bg-destructive/10 color-destructive border-destructive/20';
      case 'cancelled': return 'bg-destructive/10 color-destructive border-destructive/20';
      default: return 'bg-secondary color-muted border-border';
    }
  };

  const getTypeColor = (type: string) => {
    switch (type) {
      case 'msa': return 'bg-secondary/10 color-secondary border-secondary/20';
      case 'sow': return 'bg-accent/10 color-accent border-primary/20';
      case 'amendment': return 'bg-warning/10 color-warning border-warning/20';
      case 'termination': return 'bg-destructive/10 color-destructive border-destructive/20';
      default: return 'bg-secondary color-muted border-border';
    }
  };

  const calculateProgress = (milestones?: JobContractMilestone[]) => {
    if (!milestones || milestones.length === 0) return 0;
    const completed = milestones.filter(m => m.status === 'completed').length;
    return Math.round((completed / milestones.length) * 100);
  };

  const isExpiringSoon = (endDate?: string) => {
    if (!endDate) return false;
    const end = new Date(endDate);
    const now = new Date();
    const diffDays = Math.ceil((end.getTime() - now.getTime()) / (1000 * 60 * 60 * 24));
    return diffDays <= 30 && diffDays >= 0;
  };

  const isExpired = (endDate?: string) => {
    if (!endDate) return false;
    return new Date(endDate) < new Date();
  };

  return (
    <div className="stack-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-3 text-heading-3 color-foreground">{translations.title}</h1>
          <p className="text-body-sm color-foreground/70 mt-xs">{translations.subtitle}</p>
        </div>
        <Button onClick={() => setShowCreateDialog(true)}>
          <Plus className="h-icon-xs w-icon-xs mr-sm" />
          Create Contract
        </Button>
      </div>

      {/* Filters */}
      <Card className="p-md">
        <div className="flex flex-col sm:flex-row gap-md">
          <div className="flex-1">
            <Input               placeholder="Search contracts..."
              value={searchTerm}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => setSearchTerm(e.target.value)}
              className="w-full"
            />
          </div>
          <div className="flex gap-sm">
            <select
              value={statusFilter}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => setStatusFilter(e.target.value)}
              className=" px-md py-sm border border-border rounded-md bg-background"
            >
              {STATUS_OPTIONS.map((option: any) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            <select
              value={typeFilter}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => setTypeFilter(e.target.value)}
              className=" px-md py-sm border border-border rounded-md bg-background"
            >
              {TYPE_OPTIONS.map((option: any) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          </div>
        </div>
      </Card>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-5 gap-md">
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Total Contracts</p>
              <p className="text-heading-3 text-heading-3 color-foreground">{contracts.length}</p>
            </div>
            <FileText className="h-icon-lg w-icon-lg color-accent" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Active</p>
              <p className="text-heading-3 text-heading-3 color-success">
                {contracts.filter(c => c.status === 'active').length}
              </p>
            </div>
            <CheckCircle className="h-icon-lg w-icon-lg color-success" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Completed</p>
              <p className="text-heading-3 text-heading-3 color-accent">
                {contracts.filter(c => c.status === 'completed').length}
              </p>
            </div>
            <CheckCircle className="h-icon-lg w-icon-lg color-accent" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Expiring Soon</p>
              <p className="text-heading-3 text-heading-3 color-warning">
                {contracts.filter(c => isExpiringSoon(c.end_date)).length}
              </p>
            </div>
            <AlertTriangle className="h-icon-sm w-icon-sm color-warning" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Total Value</p>
              <p className="text-heading-3 text-heading-3 color-secondary">
                {formatAmount(contracts.reduce((sum, c) => sum + c.value, 0))}
              </p>
            </div>
            <DollarSign className="h-icon-sm w-icon-sm color-success" />
          </div>
        </Card>
      </div>

      {/* Contracts List */}
      <div className="stack-md">
        {loading ? (
          Array.from({ length: 5 }).map((_, i) => (
            <Card key={i} className="p-lg">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <Skeleton className="h-icon-sm w-container-sm mb-sm" />
                  <Skeleton className="h-icon-xs w-component-xl mb-sm" />
                  <Skeleton className="h-icon-xs w-full mb-sm" />
                  <Skeleton className="h-icon-xs w-3/4" />
                </div>
                <div className="flex gap-sm">
                  <Skeleton className="h-icon-md w-component-md" />
                  <Skeleton className="h-icon-md w-component-lg" />
                </div>
              </div>
            </Card>
          ))
        ) : filteredContracts.length > 0 ? (
          filteredContracts.map((contract: any) => {
            const StatusIcon = getStatusIcon(contract.status);
            const isExpiring = isExpiringSoon(contract.end_date);
            const hasExpired = isExpired(contract.end_date);
            const progress = calculateProgress(contract.milestones);

            return (
              <Card key={contract.id} className={`p-lg hover:shadow-elevated transition-shadow ${hasExpired ? 'border-destructive/20 bg-destructive/5' : isExpiring ? 'border-warning/20 bg-warning/5' : ''}`}>
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-start justify-between mb-sm">
                      <div>
                        <h3 className="text-body text-heading-4 color-foreground mb-xs flex items-center gap-sm">
                          <StatusIcon className="h-icon-sm w-icon-sm color-foreground/60" />
                          {contract.title}
                          {(isExpiring || hasExpired) && (
                            <AlertTriangle className={`h-icon-xs w-icon-xs ${hasExpired ? 'color-destructive' : 'color-warning'}`} />
                          )}
                        </h3>
                        <div className="flex items-center gap-sm text-body-sm color-foreground/70">
                          <Building className="h-icon-xs w-icon-xs" />
                          <span>{contract.job_title || 'N/A'}</span>
                          <span></span>
                          <span>{contract.company_name || 'N/A'}</span>
                        </div>
                      </div>
                      <div className="flex gap-sm">
                        <Badge className={getStatusColor(contract.status)}>
                          {contract.status.replace('_', ' ')}
                        </Badge>
                        <Badge className={getTypeColor(contract.contract_type || contract.contractType)}>
                          {(contract.contract_type || contract.contractType).toUpperCase()}
                        </Badge>
                      </div>
                    </div>

                    <p className="text-body-sm color-foreground/80 mb-sm line-clamp-xs">
                      {contract.description}
                    </p>

                    <div className="flex items-center gap-lg text-body-sm color-foreground/70 mb-sm">
                      <div className="flex items-center gap-xs">
                        <DollarSign className="h-icon-xs w-icon-xs" />
                        <span className="form-label">{formatAmount(contract.value, contract.currency)}</span>
                      </div>
                      <div className="flex items-center gap-xs">
                        <Calendar className="h-icon-xs w-icon-xs" />
                        <span>
                          {formatDate(contract.start_date || contract.startDate)}
                          {(contract.end_date || contract.endDate) && ` - ${formatDate(contract.end_date || contract.endDate)}`}
                        </span>
                      </div>
                      {contract.paymentTerms && (
                        <div className="flex items-center gap-xs">
                          <Clock className="h-icon-xs w-icon-xs" />
                          <span>{contract.paymentTerms}</span>
                        </div>
                      )}
                    </div>

                    {/* Milestones Progress */}
                    {contract.milestones && contract.milestones.length > 0 && (
                      <div className="mb-sm">
                        <div className="flex items-center justify-between mb-xs">
                          <span className="text-body-sm form-label color-foreground/70">
                            Milestones Progress
                          </span>
                          <span className="text-body-sm color-foreground/70">
                            {contract.milestones.filter(m => m.status === 'completed').length} of {contract.milestones.length} completed
                          </span>
                        </div>
                        <div className="w-full bg-secondary rounded-full h-2">
                          <div 
                            className="bg-success h-2 rounded-full transition-all duration-300"
                            style={{ width: `${progress}%` }}
                          />
                        </div>
                        <div className="text-body-sm color-foreground/70 mt-xs">
                          {progress}% complete
                        </div>
                      </div>
                    )}

                    {contract.notes && (
                      <div className="mt-sm p-sm bg-accent rounded-md">
                        <p className="text-body-sm color-foreground/70">
                          <strong>Notes:</strong> {contract.notes}
                        </p>
                      </div>
                    )}
                  </div>
                </div>

                <div className="flex items-center justify-between mt-md pt-md border-t border-border">
                  <div className="text-body-sm color-foreground/50">
                    Created: {formatDate(contract.created_at || contract.createdAt)}
                    {(contract.signed_at || contract.signedAt) && (
                      <span>  Signed: {formatDate(contract.signed_at || contract.signedAt)}</span>
                    )}
                  </div>
                  <div className="flex gap-sm">
                    <Button>
                      <Eye className="h-icon-xs w-icon-xs" /> 
                      View
                    </Button>
                    <Button>
                      <Edit className="h-icon-xs w-icon-xs" /> 
                      Edit
                    </Button>
                    {(contract.document_url || contract.documentUrl) && (
                      <Button>
                        <Download className="h-icon-xs w-icon-xs" /> 
                        Download
                      </Button>
                    )}
                    {contract.milestones && contract.milestones.length > 0 && (
                      <Button>
                        <BarChart3 className="h-icon-xs w-icon-xs" /> 
                        Milestones
                      </Button>
                    )}
                  </div>
                </div>
              </Card>
            );
          })
        ) : (
          <Card className="p-xsxl text-center">
            <FileText className="h-icon-2xl w-icon-2xl color-foreground/30 mx-auto mb-md" />
            <h3 className="text-body form-label color-foreground mb-sm">No contracts found</h3>
            <p className="text-body-sm color-foreground/70 mb-md">
              {searchTerm || statusFilter !== 'all' || typeFilter !== 'all'
                ? 'Try adjusting your filters or search terms.'
                : 'Get started by creating your first contract.'}
            </p>
            <Button onClick={() => setShowCreateDialog(true)}>
              <Plus className="h-icon-xs w-icon-xs mr-sm" />
              Create Contract
            </Button>
          </Card>
        )}
      </div>
    </div>
  );
}

