              </Link>
            </div>
          )}
        </Card>
      </div>
    </div>
  );
}

'use client';


import { CalendarIcon, Download, Edit } from "lucide-react";
import { useState, useEffect } from 'react';
import { User } from '@supabase/supabase-js';
import { createBrowserClient } from '@ghxstship/auth';
import {
  Badge,
  Button,
  Card,
  Input,
  Skeleton,
  UnifiedInput
} from "@ghxstship/ui";
import { 
  MagnifyingGlassIcon,
  PlusIcon,
  EyeIcon,
  PencilIcon,
  DocumentTextIcon,
  ClockIcon,
  CheckCircleIcon,
  XCircleIcon,
  ExclamationTriangleIcon,
  CalendarIcon,
  BuildingOfficeIcon,
  CurrencyDollarIcon,
  ArrowDownTrayIcon,
  PaperAirplaneIcon,
  UsersIcon
} from '@heroicons/react/24/outline';

interface RFPsClientProps {
  user: User;
  orgId: string;
  translations: {
    title: string;
    subtitle: string;
  };
}

interface RFP {
  id: string;
  title: string;
  description: string;
  issuer_organization: string;
  status: 'draft' | 'published' | 'open' | 'closed' | 'awarded' | 'cancelled';
  type: 'services' | 'products' | 'consulting' | 'construction' | 'technology' | 'other';
  budget_min?: number;
  budget_max?: number;
  currency: string;
  submission_deadline: string;
  project_start_date?: string;
  project_duration?: string;
  requirements?: string[];
  evaluation_criteria?: string[];
  contact_email?: string;
  document_url?: string;
  responses_count?: number;
  published_at?: string;
  created_at: string;
  updated_at: string;
  organization_id: string;
}

const STATUS_OPTIONS = [
  { value: 'all', label: 'All Statuses' },
  { value: 'draft', label: 'Draft' },
  { value: 'published', label: 'Published' },
  { value: 'open', label: 'Open' },
  { value: 'closed', label: 'Closed' },
  { value: 'awarded', label: 'Awarded' },
  { value: 'cancelled', label: 'Cancelled' },
];

const TYPE_OPTIONS = [
  { value: 'all', label: 'All Types' },
  { value: 'services', label: 'Services' },
  { value: 'products', label: 'Products' },
  { value: 'consulting', label: 'Consulting' },
  { value: 'construction', label: 'Construction' },
  { value: 'technology', label: 'Technology' },
  { value: 'other', label: 'Other' },
];

export function RFPsClient({ user, orgId, translations }: RFPsClientProps) {
  const [rfps, setRfps] = useState<RFP[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [typeFilter, setTypeFilter] = useState('all');
  const [showCreateDialog, setShowCreateDialog] = useState(false);

  const supabase = createBrowserClient();

  useEffect(() => {
    loadRFPs();
  }, [orgId]);

  const loadRFPs = async () => {
    try {
      setLoading(true);

      const { data, error } = await supabase
        .from('rfps')
        .select('*')
        .eq('organization_id', orgId)
        .order('created_at', { ascending: false });

      if (error) throw error;
      setRfps(data || []);
    } catch (error) {
      console.error('Error loading RFPs:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredRFPs = rfps.filter((rfp: any) => {
    const matchesSearch = rfp.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         rfp.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         rfp.issuer_organization.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === 'all' || rfp.status === statusFilter;
    const matchesType = typeFilter === 'all' || rfp.type === typeFilter;

    return matchesSearch && matchesStatus && matchesType;
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'draft':
        return 'bg-secondary color-muted';
      case 'published':
        return 'bg-accent/10 color-accent';
      case 'open':
        return 'bg-success/10 color-success';
      case 'closed':
        return 'bg-warning/10 color-warning';
      case 'awarded':
        return 'bg-secondary/10 color-secondary';
      case 'cancelled':
        return 'bg-destructive/10 color-destructive';
      default:
        return 'bg-secondary color-muted';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'draft':
        return DocumentTextIcon;
      case 'published':
        return PaperAirplaneIcon;
      case 'open':
        return CheckCircleIcon;
      case 'closed':
        return ClockIcon;
      case 'awarded':
        return CheckCircleIcon;
      case 'cancelled':
        return XCircleIcon;
      default:
        return DocumentTextIcon;
    }
  };

  const getTypeColor = (type: string) => {
    switch (type) {
      case 'services':
        return 'bg-accent/10 color-accent';
      case 'products':
        return 'bg-success/10 color-success';
      case 'consulting':
        return 'bg-secondary/10 color-secondary';
      case 'construction':
        return 'bg-warning/10 color-warning';
      case 'technology':
        return 'bg-accent/10 color-accent';
      case 'other':
        return 'bg-secondary color-muted';
      default:
        return 'bg-secondary color-muted';
    }
  };

  const formatBudget = (min?: number, max?: number, currency: string = 'USD') => {
    const formatter = new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency
    });

    if (!min && !max) return 'Budget not specified';
    if (min && max) return `${formatter.format(min)} - ${formatter.format(max)}`;
    if (min) return `From ${formatter.format(min)}`;
    if (max) return `Up to ${formatter.format(max)}`;
    return 'Budget not specified';
  };

  const isDeadlineApproaching = (deadline: string) => {
    const deadlineDate = new Date(deadline);
    const now = new Date();
    const diffDays = Math.ceil((deadlineDate.getTime() - now.getTime()) / (1000 * 60 * 60 * 24));
    return diffDays <= 7 && diffDays >= 0;
  };

  const isOverdue = (deadline: string) => {
    return new Date(deadline) < new Date();
  };

  return (
    <div className="stack-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-heading-3 text-heading-3 color-foreground">{translations.title}</h1>
          <p className="text-body-sm color-foreground/70 mt-xs">{translations.subtitle}</p>
        </div>
        <Button onClick={() => setShowCreateDialog(true)}>
          <PlusIcon className="h-icon-xs w-icon-xs mr-sm" />
          Create RFP
        </Button>
      </div>

      {/* Filters */}
      <Card className="p-md">
        <div className="flex flex-col sm:flex-row gap-md">
          <div className="flex-1">
            <Input               placeholder="Search RFPs..."
              value={searchTerm}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => setSearchTerm(e.target.value)}
              className="w-full"
            />
          </div>
          <div className="flex gap-sm">
            <select
              value={statusFilter}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => setStatusFilter(e.target.value)}
              className=" px-md py-sm border border-border rounded-md bg-background"
            >
              {STATUS_OPTIONS.map((option: any) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            <select
              value={typeFilter}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => setTypeFilter(e.target.value)}
              className=" px-md py-sm border border-border rounded-md bg-background"
            >
              {TYPE_OPTIONS.map((option: any) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          </div>
        </div>
      </Card>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-5 gap-md">
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Total RFPs</p>
              <p className="text-heading-3 text-heading-3 color-foreground">{rfps.length}</p>
            </div>
            <DocumentTextIcon className="h-icon-lg w-icon-lg color-accent" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Open</p>
              <p className="text-heading-3 text-heading-3 color-success">
                {rfps.filter(r => r.status === 'open').length}
              </p>
            </div>
            <CheckCircleIcon className="h-icon-lg w-icon-lg color-success" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Published</p>
              <p className="text-heading-3 text-heading-3 color-accent">
                {rfps.filter(r => r.status === 'published').length}
              </p>
            </div>
            <PaperAirplaneIcon className="h-icon-lg w-icon-lg color-accent" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Awarded</p>
              <p className="text-heading-3 text-heading-3 color-secondary">
                {rfps.filter(r => r.status === 'awarded').length}
              </p>
            </div>
            <CheckCircleIcon className="h-icon-lg w-icon-lg color-secondary" />
          </div>
        </Card>
        <Card className="p-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-foreground/70">Total Responses</p>
              <p className="text-heading-3 text-heading-3 color-warning">
                {rfps.reduce((sum, r) => sum + (r.responses_count || 0), 0)}
              </p>
            </div>
            <UsersIcon className="h-icon-lg w-icon-lg color-warning" />
          </div>
        </Card>
      </div>

      {/* RFPs List */}
      <div className="stack-md">
        {loading ? (
          Array.from({ length: 5 }).map((_, i) => (
            <Card key={i} className="p-lg">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <Skeleton className="h-icon-sm w-container-sm mb-sm" />
                  <Skeleton className="h-icon-xs w-component-xl mb-sm" />
                  <Skeleton className="h-icon-xs w-full mb-sm" />
                  <Skeleton className="h-icon-xs w-3/4" />
                </div>
                <div className="flex gap-sm">
                  <Skeleton className="h-icon-md w-component-md" />
                  <Skeleton className="h-icon-md w-component-lg" />
                </div>
              </div>
            </Card>
          ))
        ) : filteredRFPs.length > 0 ? (
          filteredRFPs.map((rfp: any) => {
            const StatusIcon = getStatusIcon(rfp.status);
            const isUrgent = isDeadlineApproaching(rfp.submission_deadline);
            const isLate = isOverdue(rfp.submission_deadline);

            return (
              <Card key={rfp.id} className={`p-lg hover:shadow-elevated transition-shadow ${isLate ? 'border-destructive/20 bg-destructive/5' : isUrgent ? 'border-warning/20 bg-warning/5' : ''}`}>
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-start justify-between mb-sm">
                      <div>
                        <h3 className="text-body text-heading-4 color-foreground mb-xs flex items-center gap-sm">
                          <StatusIcon className="h-icon-sm w-icon-sm color-foreground/60" />
                          {rfp.title}
                          {(isUrgent || isLate) && (
                            <ExclamationTriangleIcon className={`h-icon-xs w-icon-xs ${isLate ? 'color-destructive' : 'color-warning'}`} />
                          )}
                        </h3>
                        <div className="flex items-center gap-sm text-body-sm color-foreground/70">
                          <BuildingOfficeIcon className="h-icon-xs w-icon-xs" />
                          <span>{rfp.issuer_organization}</span>
                          {rfp.responses_count !== undefined && (
                            <>
                              <span></span>
                              <span>{rfp.responses_count} responses</span>
                            </>
                          )}
                        </div>
                      </div>
                      <div className="flex gap-sm">
                        <Badge className={getStatusColor(rfp.status)}>
                          {rfp.status.replace('_', ' ')}
                        </Badge>
                        <Badge className={getTypeColor(rfp.type)}>
                          {rfp.type}
                        </Badge>
                      </div>
                    </div>

                    <p className="text-body-sm color-foreground/80 mb-sm line-clamp-xs">
                      {rfp.description}
                    </p>

                    <div className="flex items-center gap-lg text-body-sm color-foreground/70 mb-sm">
                      <div className="flex items-center gap-xs">
                        <CurrencyDollarIcon className="h-icon-xs w-icon-xs" />
                        <span>{formatBudget(rfp.budget_min, rfp.budget_max, rfp.currency)}</span>
                      </div>
                      <div className={`flex items-center gap-xs ${isLate ? 'color-destructive' : isUrgent ? 'color-warning' : ''}`}>
                        <CalendarIcon className="h-icon-xs w-icon-xs" />
                        <span>
                          {isLate ? 'Overdue: ' : 'Deadline: '}
                          {new Date(rfp.submission_deadline).toLocaleDateString()}
                        </span>
                      </div>
                      {rfp.project_start_date && (
                        <div className="flex items-center gap-xs">
                          <span>Start: {new Date(rfp.project_start_date).toLocaleDateString()}</span>
                        </div>
                      )}
                      {rfp.project_duration && (
                        <div className="flex items-center gap-xs">
                          <ClockIcon className="h-icon-xs w-icon-xs" />
                          <span>{rfp.project_duration}</span>
                        </div>
                      )}
                    </div>

                    {rfp.requirements && rfp.requirements.length > 0 && (
                      <div className="mb-sm">
                        <span className="text-body-sm form-label color-foreground/70 mb-xs block">Key Requirements:</span>
                        <div className="stack-md">
                          {rfp.requirements.slice(0, 3).map((req, index) => (
                            <div key={index} className="text-body-sm color-foreground/70 flex items-start gap-sm">
                              <span className="color-foreground/40"></span>
                              <span>{req}</span>
                            </div>
                          ))}
                          {rfp.requirements.length > 3 && (
                            <div className="text-body-sm color-foreground/50">
                              +{rfp.requirements.length - 3} more requirements
                            </div>
                          )}
                        </div>
                      </div>
                    )}

                    {rfp.evaluation_criteria && rfp.evaluation_criteria.length > 0 && (
                      <div className="mb-sm">
                        <span className="text-body-sm form-label color-foreground/70 mb-xs block">Evaluation Criteria:</span>
                        <div className="flex flex-wrap gap-xs">
                          {rfp.evaluation_criteria.slice(0, 4).map((criteria, index) => (
                            <Badge key={index} variant="secondary">
                              {criteria}
                            </Badge>
                          ))}
                          {rfp.evaluation_criteria.length > 4 && (
                            <Badge variant="secondary">
                              +{rfp.evaluation_criteria.length - 4} more
                            </Badge>
                          )}
                        </div>
                      </div>
                    )}
                  </div>
                </div>

                <div className="flex items-center justify-between mt-md pt-md border-t border-border">
                  <div className="text-body-sm color-foreground/50">
                    Created: {new Date(rfp.created_at).toLocaleDateString()}
                    {rfp.published_at && (
                      <span>  Published: {new Date(rfp.published_at).toLocaleDateString()}</span>
                    )}
                    {rfp.contact_email && (
                      <span>  Contact: {rfp.contact_email}</span>
                    )}
                  </div>
                  <div className="flex gap-sm">
                    <Button>
                      <EyeIcon className="h-icon-xs w-icon-xs mr-xs" />
                      View
                    </Button>
                    <Button>
                      <PencilIcon className="h-icon-xs w-icon-xs mr-xs" />
                      Edit
                    </Button>
                    {rfp.document_url && (
                      <Button>
                        <ArrowDownTrayIcon className="h-icon-xs w-icon-xs mr-xs" />
                        Download
                      </Button>
                    )}
                    {rfp.status === 'open' && (
                      <Button>
                        <PaperAirplaneIcon className="h-icon-xs w-icon-xs mr-xs" />
                        Respond
                      </Button>
                    )}
                  </div>
                </div>
              </Card>
            );
          })
        ) : (
          <Card className="p-xsxl text-center">
            <DocumentTextIcon className="h-icon-2xl w-icon-2xl color-foreground/30 mx-auto mb-md" />
            <h3 className="text-body form-label color-foreground mb-sm">No RFPs found</h3>
            <p className="text-body-sm color-foreground/70 mb-md">
              {searchTerm || statusFilter !== 'all' || typeFilter !== 'all'
                ? 'Try adjusting your filters or search terms.'
                : 'Get started by creating your first RFP.'}
            </p>
            <Button onClick={() => setShowCreateDialog(true)}>
              <PlusIcon className="h-icon-xs w-icon-xs mr-sm" />
              Create RFP
            </Button>
          </Card>
        )}
      </div>
    </div>
  );
}

'use client';

import { Edit3, FileText, Activity as ActivityIcon, MessageSquare } from "lucide-react";
import { useEffect, useState } from 'react';
import {
  AppDrawer,
  Button
} from "@ghxstship/ui";
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { tryCatch, Result, reportError } from '@ghxstship/ui/utils/error-handling';
import { AppDrawer } from '@ghxstship/ui';

export default function OpenDeckVendorClient({ orgId, vendorId, open, onClose }: { orgId: string; vendorId: string | null; open: boolean; onClose: () => void }): JSX.Element | null {
 const t = useTranslations('opendeck');
 const sb = createBrowserClient();
 const [vendor, setVendor] = useState<any | null>(null);
 const [tab, setTab] = useState<'details' | 'edit' | 'comments' | 'activity'>('details');
 const [saving, setSaving] = useState(false);
 const [msg, setMsg] = useState<string | null>(null);
 const [form, setForm] = useState<{ name: string; website?: string; contactEmail?: string; status: string }>({ name: '', website: '', contactEmail: '', status: 'active' });

 useEffect(() => {
 if (!open || !vendorId) return;
 (async () => {
 const resResult = await tryCatch(async () => fetch(`/api/v1/marketplace/vendors?limit=1&offset=0`, { headers: { 'x-org-id': orgId } }));
if (!resResult.success) {
 reportError(resResult.error);
 throw new Error(resResult.error.message);
}
const res = resResult.data;
 const json = await res.json();
 const found = (json.items || []).find((v: unknown) => v.id === vendorId) || null;
 if (found) {
 setVendor(found);
 setForm({ name: found.name, website: found.website ?? '', contactEmail: found.contactEmail ?? '', status: found.status ?? 'active' });
 } else {
 setVendor(null);
 }
 })();
 }, [open, vendorId, orgId]);

 async function saveEdit() {
 if (!vendorId) return;
 setSaving(true);
 setMsg(null);
 try {
 const resResult = await tryCatch(async () => fetch('/api/v1/marketplace/vendors', {
 method: 'PATCH',
 headers: { 'content-type': 'application/json', 'x-org-id': orgId },
 body: JSON.stringify({ id: vendorId, ...form })
 }));

 if (!resResult.success) {
 reportError(resResult.error);
 throw new Error(resResult.error.message);
 }

 const res = resResult.data;
 if (!res.ok) throw new Error((await res.json())?.error || 'Update failed');
 setMsg(t('updated'));
 if (typeof window !== 'undefined' && (window as unknown).posthog) {
 (window as unknown).posthog.capture('vendor.updated', { organization_id: orgId, vendor_id: vendorId });
 }
 } catch (e) {
 setMsg((e as Error)?.message || 'Update failed');
 } finally {
 setSaving(false);
 }
 }

 // Comments & Activity
 const [comments, setComments] = useState<Array<{ id: string; body: string; created_at: string }>([]);
 const [activity, setActivity] = useState<Array<{ action: string; occurred_at: string }>([]);
 const [loadingComments, setLoadingComments] = useState(false);
 const [loadingActivity, setLoadingActivity] = useState(false);

 useEffect(() => {
 if (!open || !vendorId) return;
 (async () => {
 setLoadingComments(true);
 try {
 const { data } = await sb
 .from('comments')
 .select('id,body,created_at')
 .eq('organization_id', orgId)
 .eq('entity_type', 'vendor')
 .eq('entity_id', vendorId)
 .order('created_at', { ascending: false })
 .limit(50);
 setComments(data ?? []);
 } catch { setComments([]); }
 setLoadingComments(false);
 })();
 }, [open, vendorId, orgId, sb]);

 useEffect(() => {
 if (!open || !vendorId) return;
 (async () => {
 setLoadingActivity(true);
 try {
 const resResult = await tryCatch(async () => fetch(`/api/audit/${orgId}/vendors/${vendorId}`));

 if (!resResult.success) {
 reportError(resResult.error);
 throw new Error(resResult.error.message);
 }

 const res = resResult.data;
 const json = await res.json();
 setActivity(json?.data ?? []);
 } catch { 
 setActivity([]); 
 }
 setLoadingActivity(false);
 })();
 }, [open, vendorId, orgId]);

 if (!open) return null;

 const tabs = [
 {
 key: 'details',
 label: (
 <span className="flex items-center gap-xs">
 <FileText className="h-icon-xs w-icon-xs" />
 {t('drawer.details')}
 </span>
 ),
 content: vendor ? (
 <div className="brand-marketplace stack-sm text-body-sm">
 {msg ? <div role="alert" className="mb-sm text-body-sm">{msg}</div> : null}
 <div><span className="form-label">Name:</span> {vendor.name}</div>
 <div><span className="form-label">Website:</span> {vendor.website || '-'}</div>
 <div><span className="form-label">Email:</span> {vendor.contactEmail || '-'}</div>
 <div><span className="form-label">Status:</span> {vendor.status}</div>
 </div>
 ) : null
 },
 {
 key: 'edit',
 label: (
 <span className="flex items-center gap-xs">
 <Edit3 className="h-icon-xs w-icon-xs" />
 {t('drawer.edit')}
 </span>
 ),
 content: vendor ? (
 <form className="stack-sm" onSubmit={(e: unknown) => { e.preventDefault(); saveEdit(); }} aria-live="polite">
 <div className="brand-marketplace grid gap-xs">
 <label htmlFor="name" className="text-body-sm">Name</label>
 <input className="rounded border px-md py-xs" value={form.name} onChange={(e: React.ChangeEvent<HTMLInputElement>) => setForm({ ...form, name: e.target.value })} />
 </div>
 <div className="brand-marketplace grid gap-xs">
 <label htmlFor="website" className="text-body-sm">Website</label>
 <input className="rounded border px-md py-xs" value={form.website} onChange={(e: React.ChangeEvent<HTMLInputElement>) => setForm({ ...form, website: e.target.value })} />
 </div>
 <div className="brand-marketplace grid gap-xs">
 <label htmlFor="email" className="text-body-sm">Email</label>
 <input className="rounded border px-md py-xs" value={form.contactEmail} onChange={(e: React.ChangeEvent<HTMLInputElement>) => setForm({ ...form, contactEmail: e.target.value })} />
 </div>
 <div className="brand-marketplace grid gap-xs">
 <label htmlFor="status" className="text-body-sm">Status</label>
 <input className="rounded border px-md py-xs" value={form.status} onChange={(e: React.ChangeEvent<HTMLInputElement>) => setForm({ ...form, status: e.target.value })} />
 </div>
 <div className="brand-marketplace flex items-center justify-end gap-sm pt-sm border-t">
 <Button variant="primary" disabled={saving}>{t('save')}</Button>
 </div>
 </form>
 ) : null
 },
 {
 key: 'comments',
 label: (
 <span className="flex items-center gap-xs">
 <MessageSquare className="h-icon-xs w-icon-xs" />
 {t('drawer.comments')}
 </span>
 ),
 content: (
 <div className="brand-marketplace stack-sm text-body-sm">
 {loadingComments ? t('drawer.loading') : comments.length === 0 ? t('emptyComments') : (
 <ul className="stack-sm">
 {comments.map(c => (
 <li key={c.id} className="rounded border p-sm">
 <div className="brand-marketplace whitespace-pre-wrap">{c.body}</div>
 <div className="brand-marketplace text-body-sm opacity-60">{new Date(c.created_at).toLocaleString()}</div>
 </li>
 ))}
 </ul>
 )}
 </div>
 )
 },
 {
 key: 'activity',
 label: (
 <span className="flex items-center gap-xs">
 <ActivityIcon className="h-icon-xs w-icon-xs" />
 {t('drawer.activity')}
 </span>
 ),
 content: (
 <div className="brand-marketplace stack-sm text-body-sm">
 {loadingActivity ? t('drawer.loading') : (
 <ul className="stack-xs">
 {activity.map((a, i) => (
 <li key={i} className="flex items-center justify-between gap-md">
 <div className="brand-marketplace font-mono text-body-sm opacity-70">{new Date(a.occurred_at).toLocaleString()}</div>
 <div className="brand-marketplace flex-1">{a.action}</div>
 </li>
 ))}
 </ul>
 )}
 </div>
 )
 }
 ];

 return (
 <AppDrawer
 open={open}
 onClose={onClose}
 record={vendor}
 mode="view"
 title={vendor?.name || 'Vendor'}
 fields={[]}
 tabs={tabs}
 />
 );
}

'use client';

import { User, Briefcase, Award, Globe, Mail, Phone, MapPin, Calendar, DollarSign, Clock, Star, Upload, Plus, Edit, Trash2, Eye, Camera, FileText, Link2, Shield, CheckCircle, AlertCircle } from "lucide-react";
import { useState, useEffect } from 'react';
import { createBrowserClient } from '@ghxstship/auth';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
// import { z } from 'zod';
import {
  AppDrawer,
  Badge,
  Button,
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
  Input,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
  Textarea
} from "@ghxstship/ui";

const vendorProfileSchema = z.object({
 business_name: z.string().min(2, 'Business name is required'),
 business_type: z.enum(['individual', 'company', 'agency']),
 display_name: z.string().min(2, 'Display name is required'),
 tagline: z.string().max(100).optional(),
 bio: z.string().max(1000).optional(),
 email: z.string().email('Valid email required'),
 phone: z.string().optional(),
 website: z.string().url().optional().or(z.literal('')),
 primary_category: z.string().min(1, 'Category is required'),
 skills: z.array(z.string()).optional(),
 years_experience: z.number().min(0).max(50).optional(),
 team_size: z.number().min(1).max(1000).optional(),
 hourly_rate: z.number().min(0).optional(),
 currency: z.string().default('USD'),
 availability_status: z.enum(['available', 'busy', 'unavailable']),
 response_time: z.string().optional()
});

type VendorProfileForm = z.infer<typeof vendorProfileSchema>;

interface VendorProfileClientProps {
 userId: string;
 orgId: string;
}

export default function VendorProfileClient({ userId, orgId }: VendorProfileClientProps) {
 const supabase = createBrowserClient();
 const [loading, setLoading] = useState(true);
 const [profile, setProfile] = useState<unknown>(null);
 const [portfolio, setPortfolio] = useState<any[]>([]);
 const [services, setServices] = useState<any[]>([]);
 const [certifications, setCertifications] = useState<any[]>([]);
 const [activeTab, setActiveTab] = useState('profile');
 const [editMode, setEditMode] = useState(false);
 const [drawerOpen, setDrawerOpen] = useState(false);
 type DrawerMode = 'portfolio' | 'service' | 'certification';

 interface PortfolioItem {
 id: string;
 title: string;
 category?: string;
 description?: string;
 views?: number;
 featured?: boolean;
 }

 interface ServiceItem {
 id: string;
 title: string;
 status?: string;
 category?: string;
 description?: string;
 base_price?: number;
 delivery_time?: string;
 orders_completed?: number;
 }

 interface CertificationItem {
 id?: string;
 name?: string;
 issuer?: string;
 issued_at?: string;
 description?: string;
 }

 type DrawerItem = PortfolioItem | ServiceItem | CertificationItem | null;

 const [drawerMode, setDrawerMode] = useState<DrawerMode>('portfolio');
 const [selectedItem, setSelectedItem] = useState<DrawerItem>(null);

 const {
 register,
 handleSubmit,
 formState: { errors, isSubmitting },
 reset,
 setValue,
 watch
 } = useForm<VendorProfileForm>({
 resolver: zodResolver(vendorProfileSchema)
 });

 const categories = [
 'Audio & Sound',
 'Lighting Design',
 'Stage Production',
 'Video & Projection',
 'Event Management',
 'Artist Services',
 'Logistics & Transport',
 'Catering & Hospitality',
 'Security & Safety',
 'Marketing & Promotion'
 ];

 const skillsList = [
 'Live Sound Engineering',
 'Stage Design',
 'Lighting Programming',
 'Video Production',
 'Event Planning',
 'Artist Management',
 'Technical Direction',
 'Project Management',
 'Budget Management',
 'Team Leadership'
 ];

 useEffect(() => {
 loadVendorProfile();
 }, [userId]);

 async function loadVendorProfile() {
 setLoading(true);
 try {
 // Load vendor profile
 const { data: vendorData } = await supabase
 .from('opendeck_vendor_profiles')
 .select('*')
 .eq('user_id', userId)
 .single();

 if (vendorData) {
 setProfile(vendorData);
 reset(vendorData);

 // Load portfolio items
 const { data: portfolioData } = await supabase
 .from('opendeck_portfolio_items')
 .select('*')
 .eq('vendor_id', vendorData.id)
 .order('created_at', { ascending: false });

 setPortfolio(portfolioData || []);

 // Load services
 const { data: servicesData } = await supabase
 .from('opendeck_services')
 .select('*')
 .eq('vendor_id', vendorData.id)
 .order('created_at', { ascending: false });

 setServices(servicesData || []);

 // Load certifications
 if (vendorData.certifications) {
 setCertifications(vendorData.certifications);
 }
 }
 } catch (error) {
 console.error('Error loading vendor profile:', error);
 } finally {
 setLoading(false);
 }
 }

 async function onSubmit(data: VendorProfileForm) {
 try {
 if (profile) {
 // Update existing profile
 const { error } = await supabase
 .from('opendeck_vendor_profiles')
 .update(data)
 .eq('id', profile.id);

 if (error) throw error;
 } else {
 // Create new profile
 const { error } = await supabase
 .from('opendeck_vendor_profiles')
 .insert({
 ...data,
 user_id: userId,
 organization_id: orgId
 });

 if (error) throw error;
 }

 setEditMode(false);
 loadVendorProfile();
 } catch (error) {
 console.error('Error saving profile:', error);
 }
 }

 const ProfileTab = () => (
 <div className="brand-marketplace stack-lg">
 {/* Profile Header */}
 <Card className="p-lg">
 <div className="brand-marketplace flex items-start justify-between">
 <div className="brand-marketplace flex items-start cluster">
 <div className="brand-marketplace relative">
 <div className="brand-marketplace w-component-lg h-component-lg rounded-full bg-gradient-to-br from-primary to-secondary flex items-center justify-center color-accent-foreground text-heading-3">
 {profile?.display_name?.charAt(0) || 'V'}
 </div>
 {profile?.verified && (
 <div className="brand-marketplace absolute -bottom-1 -right-1 bg-success rounded-full p-xs">
 <CheckCircle className="h-icon-sm w-icon-sm text-background" />
 </div>
 )}
 </div>
 <div>
 <h2 className="text-heading-3">{profile?.display_name || 'Vendor Profile'}</h2>
 <p className="color-muted">{profile?.tagline || 'No tagline set'}</p>
 <div className="brand-marketplace flex items-center cluster mt-sm">
 <Badge variant={profile?.availability_status === 'available' ? 'success' : 'secondary'}>
 {profile?.availability_status || 'Available'}
 </Badge>
 <div className="brand-marketplace flex items-center">
 <Star className="h-icon-xs w-icon-xs color-warning fill-warning" />
 <span className="ml-xs text-body-sm">{profile?.rating || 0} ({profile?.total_reviews || 0} reviews)</span>
 </div>
 </div>
 </div>
 </div>
 <Button onClick={() => setEditMode(!editMode)} variant="outline">
 <Edit className="h-icon-xs w-icon-xs mr-sm" />
 {editMode ? 'Cancel' : 'Edit Profile'}
 </Button>
 </div>
 </Card>

 {/* Profile Form */}
 {editMode ? (
 <Card className="p-lg">
 <form onSubmit={handleSubmit(onSubmit)} className="stack-md">
 <div className="brand-marketplace grid grid-cols-1 md:grid-cols-2 gap-md">
 <div>
 <label className="text-body-sm form-label">Business Name</label>
 <Input {...register('business_name')} placeholder="Your business name" />
 {errors.business_name && (
 <p className="text-body-sm color-destructive mt-xs">{errors.business_name.message}</p>
 )}
 </div>

 <div>
 <label className="text-body-sm form-label">Business Type</label>
 <Select 
 value={watch('business_type')} 
 onChange={(value: unknown) => setValue('business_type', e.target.value as unknown)}
 >
 <SelectTrigger>
 <SelectValue placeholder="Select type" />
 </SelectTrigger>
 <SelectContent>
 <SelectItem value="individual">Individual</SelectItem>
 <SelectItem value="company">Company</SelectItem>
 <SelectItem value="agency">Agency</SelectItem>
 </SelectContent>
 </Select>
 </div>

 <div>
 <label className="text-body-sm form-label">Display Name</label>
 <Input {...register('display_name')} placeholder="Public display name" />
 {errors.display_name && (
 <p className="text-body-sm color-destructive mt-xs">{errors.display_name.message}</p>
 )}
 </div>

 <div>
 <label className="text-body-sm form-label">Email</label>
 <Input {...register('email')} type="email" placeholder="contact@example.com" />
 {errors.email && (
 <p className="text-body-sm color-destructive mt-xs">{errors.email.message}</p>
 )}
 </div>

 <div>
 <label className="text-body-sm form-label">Phone</label>
 <Input {...register('phone')} placeholder="+1 234 567 8900" />
 </div>

 <div>
 <label className="text-body-sm form-label">Website</label>
 <Input {...register('website')} placeholder="https://example.com" />
 </div>

 <div>
 <label className="text-body-sm form-label">Primary Category</label>
 <Select 
 value={watch('primary_category')} 
 onChange={(value: unknown) => setValue('primary_category', value)}
 >
 <SelectTrigger>
 <SelectValue placeholder="Select category" />
 </SelectTrigger>
 <SelectContent>
 {categories.map(cat => (
 <SelectItem key={cat} value={cat}>{cat}</SelectItem>
 ))}
 </SelectContent>
 </Select>
 </div>

 <div>
 <label className="text-body-sm form-label">Availability</label>
 <Select 
 value={watch('availability_status')} 
 onChange={(value: unknown) => setValue('availability_status', e.target.value as unknown)}
 >
 <SelectTrigger>
 <SelectValue placeholder="Select status" />
 </SelectTrigger>
 <SelectContent>
 <SelectItem value="available">Available</SelectItem>
 <SelectItem value="busy">Busy</SelectItem>
 <SelectItem value="unavailable">Unavailable</SelectItem>
 </SelectContent>
 </Select>
 </div>

 <div>
 <label className="text-body-sm form-label">Years of Experience</label>
 <Input 
 {...register('years_experience', { valueAsNumber: true })} 
 type="number" 
 placeholder="5" 
 />
 </div>

 <div>
 <label className="text-body-sm form-label">Team Size</label>
 <Input 
 {...register('team_size', { valueAsNumber: true })} 
 type="number" 
 placeholder="10" 
 />
 </div>

 <div>
 <label className="text-body-sm form-label">Hourly Rate</label>
 <Input 
 {...register('hourly_rate', { valueAsNumber: true })} 
 type="number" 
 placeholder="150" 
 />
 </div>

 <div>
 <label className="text-body-sm form-label">Response Time</label>
 <Input {...register('response_time')} placeholder="Within 1 hour" />
 </div>
 </div>

 <div>
 <label className="text-body-sm form-label">Tagline</label>
 <Input {...register('tagline')} placeholder="Your professional tagline" />
 </div>

 <div>
 <label className="text-body-sm form-label">Bio</label>
 <Textarea 
 {...register('bio')} 
 placeholder="Tell clients about your experience and expertise..."
 rows={4}
 />
 </div>

 <div className="brand-marketplace flex justify-end cluster-sm">
 <Button type="button" variant="outline" onClick={() => setEditMode(false)}>
 Cancel
 </Button>
 <Button type="submit" disabled={isSubmitting}>
 {isSubmitting ? 'Saving...' : 'Save Profile'}
 </Button>
 </div>
 </form>
 </Card>
 ) : (
 <div className="brand-marketplace grid grid-cols-1 md:grid-cols-3 gap-lg">
 <Card className="md:col-span-2 p-lg">
 <h3 className="text-body text-heading-4 mb-md">About</h3>
 <p className="color-muted mb-lg">
 {profile?.bio || 'No bio provided yet.'}
 </p>

 <div className="brand-marketplace stack-md">
 <div className="brand-marketplace flex items-center">
 <Briefcase className="h-icon-sm w-icon-sm mr-sm color-muted" />
 <span>{profile?.years_experience || 0} years experience</span>
 </div>
 <div className="brand-marketplace flex items-center">
 <User className="h-icon-sm w-icon-sm mr-sm color-muted" />
 <span>Team of {profile?.team_size || 1}</span>
 </div>
 <div className="brand-marketplace flex items-center">
 <DollarSign className="h-icon-sm w-icon-sm mr-sm color-muted" />
 <span>${profile?.hourly_rate || 0}/hour</span>
 </div>
 <div className="brand-marketplace flex items-center">
 <Clock className="h-icon-sm w-icon-sm mr-sm color-muted" />
 <span>Responds {profile?.response_time || 'within 24 hours'}</span>
 </div>
 </div>

 <div className="brand-marketplace mt-lg">
 <h4 className="form-label mb-sm">Skills</h4>
 <div className="brand-marketplace flex flex-wrap gap-sm">
 {(profile?.skills || []).map((skill: string) => (
 <Badge key={skill} variant="secondary">{skill}</Badge>
 ))}
 </div>
 </div>
 </Card>

 <Card className="p-lg">
 <h3 className="text-body text-heading-4 mb-md">Contact</h3>
 <div className="brand-marketplace stack-sm">
 <div className="brand-marketplace flex items-center">
 <Mail className="h-icon-xs w-icon-xs mr-sm color-muted" />
 <span className="text-body-sm">{profile?.email}</span>
 </div>
 {profile?.phone && (
 <div className="brand-marketplace flex items-center">
 <Phone className="h-icon-xs w-icon-xs mr-sm color-muted" />
 <span className="text-body-sm">{profile?.phone}</span>
 </div>
 )}
 {profile?.website && (
 <div className="brand-marketplace flex items-center">
 <Globe className="h-icon-xs w-icon-xs mr-sm color-muted" />
 <a href="#" target="_blank" rel="noopener noreferrer" 
 className="text-body-sm color-accent hover:underline">
 Website
 </a>
 </div>
 )}
 {profile?.address && (
 <div className="brand-marketplace flex items-start">
 <MapPin className="h-icon-xs w-icon-xs mr-sm mt-0.5 color-muted" />
 <span className="text-body-sm">
 {profile.address.city}, {profile.address.state}
 </span>
 </div>
 )}
 </div>

 <div className="brand-marketplace mt-lg">
 <h4 className="form-label mb-sm">Verification</h4>
 <div className="brand-marketplace stack-sm">
 <div className="brand-marketplace flex items-center">
 {profile?.verified ? (
 <CheckCircle className="h-icon-xs w-icon-xs mr-sm color-success" />
 ) : (
 <AlertCircle className="h-icon-xs w-icon-xs mr-sm color-warning" />
 )}
 <span className="text-body-sm">
 {profile?.verified ? 'Verified Vendor' : 'Pending Verification'}
 </span>
 </div>
 </div>
 </div>
 </Card>
 </div>
 )}
 </div>
 );

 const PortfolioTab = () => (
 <div className="brand-marketplace stack-lg">
 <div className="brand-marketplace flex justify-between items-center">
 <h3 className="text-body text-heading-4">Portfolio Items</h3>
 <Button onClick={() => handleOpenDrawer('portfolio')}>
 <Plus className="h-icon-xs w-icon-xs mr-sm" />
 Add Portfolio Item
 </Button>
 </div>

 <div className="brand-marketplace grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-md">
 {portfolio.map((item: unknown) => (
 <Card key={item.id} className="overflow-hidden">
 <div className="brand-marketplace aspect-video bg-gradient-to-br from-primary to-secondary relative">
 {item.featured && (
 <Badge className="absolute top-xs right-2" variant="secondary">
 Featured
 </Badge>
 )}
 </div>
 <div className="brand-marketplace p-md">
 <h4 className="text-body text-heading-4">{item.title}</h4>
 <p className="text-body-sm color-muted mt-xs">{item.category}</p>
 <p className="text-body-sm mt-sm line-clamp-xs">{item.description}</p>
 <div className="brand-marketplace flex items-center justify-between mt-md">
 <div className="brand-marketplace flex items-center cluster-sm text-body-sm color-muted">
 <Eye className="h-icon-xs w-icon-xs" />
 <span>{item.views || 0}</span>
 </div>
 <div className="brand-marketplace flex cluster-sm">
 <Button
 variant="ghost"
 onClick={() => handleOpenDrawer('portfolio', item)}
 >
 <Edit className="h-icon-xs w-icon-xs" />
 </Button>
 <Button variant="ghost">
 <Trash2 className="h-icon-xs w-icon-xs" />
 </Button>
 </div>
 </div>
 </div>
 </Card>
 ))}
 </div>
 </div>
 );

 const ServicesTab = () => (
 <div className="brand-marketplace stack-lg">
 <div className="brand-marketplace flex justify-between items-center">
 <h3 className="text-body text-heading-4">Services</h3>
 <Button onClick={() => handleOpenDrawer('service')}>
 <Plus className="h-icon-xs w-icon-xs mr-sm" />
 Add Service
 </Button>
 </div>

 <div className="brand-marketplace grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-md">
 {services.map((service: unknown) => (
 <Card key={service.id} className="p-md">
 <div className="brand-marketplace flex justify-between items-start mb-sm">
 <h4 className="text-body text-heading-4">{service.title}</h4>
 <Badge variant={service.status === 'active' ? 'success' : 'secondary'}>
 {service.status}
 </Badge>
 </div>
 <p className="text-body-sm color-muted mb-sm">{service.category}</p>
 <p className="text-body-sm line-clamp-xs mb-md">{service.description}</p>
 <div className="brand-marketplace stack-sm">
 <div className="brand-marketplace flex justify-between text-body-sm">
 <span>Starting at</span>
 <span className="text-body-sm">${service.base_price}</span>
 </div>
 <div className="brand-marketplace flex justify-between text-body-sm">
 <span>Delivery</span>
 <span>{service.delivery_time}</span>
 </div>
 <div className="brand-marketplace flex justify-between text-body-sm">
 <span>Orders</span>
 <span>{service.orders_completed || 0}</span>
 </div>
 </div>
 <div className="brand-marketplace flex cluster-sm mt-md">
 <Button
 variant="outline"
 className="flex-1"
 onClick={() => handleOpenDrawer('service', service)}
 >
 Edit
 </Button>
 <Button variant="outline" className="flex-1">
 View
 </Button>
 </div>
 </Card>
 ))}
 </div>
 </div>
 );

 const handleOpenDrawer = (mode: DrawerMode, item: DrawerItem = null) => {
 setDrawerMode(mode);
 setSelectedItem(item);
 setDrawerOpen(true);
 };

 const handleCloseDrawer = () => {
 setDrawerOpen(false);
 setSelectedItem(null);
 };

 if (loading) {
 return (
 <div className="brand-marketplace flex items-center justify-center h-container-sm">
 <div className="brand-marketplace animate-spin rounded-full h-icon-lg w-icon-lg border-b-2 border-primary"></div>
 </div>
 );
 }

 return (
 <div className="brand-marketplace stack-lg">
 <Tabs value={activeTab} onValueChange={setActiveTab}>
 <TabsList>
 <TabsTrigger value="profile">Profile</TabsTrigger>
 <TabsTrigger value="portfolio">Portfolio</TabsTrigger>
 <TabsTrigger value="services">Services</TabsTrigger>
 <TabsTrigger value="earnings">Earnings</TabsTrigger>
 </TabsList>

 <TabsContent value="profile">
 <ProfileTab />
 </TabsContent>

 <TabsContent value="portfolio">
 <PortfolioTab />
 </TabsContent>

 <TabsContent value="services">
 <ServicesTab />
 </TabsContent>

 <TabsContent value="earnings">
 <Card className="p-lg">
 <h3 className="text-body text-heading-4 mb-md">Earnings Overview</h3>
 <p className="color-muted">Earnings tracking coming soon...</p>
 </Card>
 </TabsContent>
 </Tabs>

 {/* Drawer for creating/editing items */}
 <AppDrawer
 open={drawerOpen}
 onClose={handleCloseDrawer}
 title={
 drawerMode === 'portfolio' ? 'Portfolio Item' :
 drawerMode === 'service' ? 'Service Package' :
 'Certification'
 }
 record={selectedItem}
 mode="view"
 fields={[]}
 tabs={[{
 key: 'details',
 label: 'Details',
 content: (
 <div className="brand-marketplace p-md">
 <p className="color-muted">
 Form for {drawerMode} will be implemented here...
 </p>
 </div>
 )
 }]}
 />
 </div>
 );
}

'use client';

import { Plus, Edit, Trash2, Eye, Calendar, DollarSign, MapPin, Users, Paperclip, Search, AlertCircle, Key } from 'lucide-react';
import { useCallback, useEffect, useMemo, useState, type ChangeEvent, type FormEvent } from 'react';
import {
  AppDrawer,
  Badge,
  Button,
  Card,
  Input,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Textarea,
  UnifiedInput
} from "@ghxstship/ui";
import { createBrowserClient } from '@ghxstship/auth';
import { AppDrawer } from '@ghxstship/ui';

type ProjectStatus = 'draft' | 'open' | 'in_progress' | 'completed' | string;
type ProjectExperienceLevel = 'entry' | 'intermediate' | 'expert';
type ProjectBudgetType = 'fixed' | 'hourly' | 'not_specified';
type ProjectLocationType = 'remote' | 'onsite' | 'hybrid';
type ProjectVisibility = 'public' | 'private' | 'invite_only';

interface ProjectFormData {
 title: string;
 description: string;
 category: string;
 subcategory: string;
 scope: string;
 skillsRequired: string;
 experienceLevel: ProjectExperienceLevel;
 budgetType: ProjectBudgetType;
 budgetMin: string;
 budgetMax: string;
 currency: string;
 startDate: string;
 endDate: string;
 duration: string;
 isUrgent: boolean;
 locationType: ProjectLocationType;
 visibility: ProjectVisibility;
}

interface ProjectProposalCount {
 count?: number | null;
}

interface OpenDeckProject {
 id: string;
 title?: string | null;
 description?: string | null;
 category?: string | null;
 subcategory?: string | null;
 scope?: string | null;
 skills_required?: string[] | null;
 experience_level?: string | null;
 budget_type?: string | null;
 budget_min?: number | null;
 budget_max?: number | null;
 currency?: string | null;
 start_date?: string | null;
 end_date?: string | null;
 duration?: string | null;
 is_urgent?: boolean | null;
 location_type?: string | null;
 visibility?: string | null;
 status: ProjectStatus;
 proposals?: ProjectProposalCount[];
 created_at: string;
 client_id: string;
 organization_id: string;
 is_demo?: boolean | null;
}

interface ProjectPostingClientProps {
 userId: string;
 orgId: string;
}

const categories = [
 'Audio & Sound',
 'Lighting Design',
 'Stage Production',
 'Video & Projection',
 'Event Management',
 'Artist Services',
 'Logistics & Transport',
 'Catering & Hospitality',
 'Security & Safety',
 'Marketing & Promotion',
];

const experienceLevelOptions: ReadonlyArray<{ value: ProjectExperienceLevel; label: string }> = [
 { value: 'entry', label: 'Entry Level' },
 { value: 'intermediate', label: 'Intermediate' },
 { value: 'expert', label: 'Expert' },
];

const budgetTypeOptions: ReadonlyArray<{ value: ProjectBudgetType; label: string }> = [
 { value: 'fixed', label: 'Fixed Price' },
 { value: 'hourly', label: 'Hourly Rate' },
 { value: 'not_specified', label: 'Not Specified' },
];

const locationTypeOptions: ReadonlyArray<{ value: ProjectLocationType; label: string }> = [
 { value: 'remote', label: 'Remote' },
 { value: 'onsite', label: 'Onsite' },
 { value: 'hybrid', label: 'Hybrid' },
];

const visibilityOptions: ReadonlyArray<{ value: ProjectVisibility; label: string }> = [
 { value: 'public', label: 'Public' },
 { value: 'private', label: 'Private' },
 { value: 'invite_only', label: 'Invite Only' },
];

const createInitialFormData = (): ProjectFormData => ({
 title: '',
 description: '',
 category: '',
 subcategory: '',
 scope: '',
 skillsRequired: '',
 experienceLevel: 'entry',
 budgetType: 'not_specified',
 budgetMin: '',
 budgetMax: '',
 currency: 'USD',
 startDate: '',
 endDate: '',
 duration: '',
 isUrgent: false,
 locationType: 'remote',
 visibility: 'public'
});

export default function ProjectPostingClient({ userId, orgId }: ProjectPostingClientProps) {
 const supabase = createBrowserClient();
 const [projects, setProjects] = useState<OpenDeckProject[]>([]);
 const [loading, setLoading] = useState(true);
 const [drawerOpen, setDrawerOpen] = useState(false);
 const [isSubmitting, setIsSubmitting] = useState(false);
 const [error, setError] = useState<string | null>(null);
 const [selectedProject, setSelectedProject] = useState<OpenDeckProject | null>(null);
 const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
 const [filterStatus, setFilterStatus] = useState('all');
 const [searchQuery, setSearchQuery] = useState('');
 const [formData, setFormData] = useState<ProjectFormData>(() => createInitialFormData());

 useEffect(() => {
 loadProjects();
 }, [userId, orgId]);

 const loadProjects = useCallback(async () => {
 setLoading(true);
 try {
 const { data, error: fetchError } = await supabase
 .from('opendeck_projects')
 .select(
 `*, proposals:opendeck_proposals(count)`
 )
 .eq('client_id', userId)
 .order('created_at', { ascending: false });

 if (fetchError) {
 throw fetchError;
 }

 setProjects((data || []) as OpenDeckProject[]);
 } catch (fetchErr) {
 console.error('Error loading projects:', fetchErr);
 } finally {
 setLoading(false);
 }
 }, [supabase, userId]);

 const updateForm = useCallback(<Key extends keyof ProjectFormData>(field: Key, value: ProjectFormData[Key]) => {
 setFormData(prev => ({ ...prev, [field]: value }));
 }, []);

 const handleInputChange = useCallback((event: ChangeEvent<HTMLInputElement>) => {
 const { name, value, type, checked } = event.target;
 if (type === 'checkbox') {
 updateForm(name as keyof ProjectFormData, checked as ProjectFormData[keyof ProjectFormData]);
 return;
 }
 updateForm(name as keyof ProjectFormData, e.target.value as ProjectFormData[keyof ProjectFormData]);
 }, [updateForm]);

 const handleTextareaChange = useCallback((event: ChangeEvent<HTMLTextAreaElement>) => {
 const { name, value } = event.target;
 updateForm(name as keyof ProjectFormData, e.target.value as ProjectFormData[keyof ProjectFormData]);
 }, [updateForm]);

 const handleSelectChange = useCallback(<Key extends keyof ProjectFormData>(field: Key) => (value: string) => {
 updateForm(field, e.target.value as ProjectFormData[Key]);
 }, [updateForm]);

 const handleOpenDrawer = useCallback((project?: OpenDeckProject) => {
 if (project) {
 setSelectedProject(project);
 setFormData({
 title: project.title ?? '',
 description: project.description ?? '',
 category: project.category ?? '',
 subcategory: project.subcategory ?? '',
 scope: project.scope ?? '',
 skillsRequired: (project.skills_required ?? []).join(', '),
 experienceLevel: (project.experience_level as ProjectExperienceLevel) || 'entry',
 budgetType: (project.budget_type as ProjectBudgetType) || 'not_specified',
 budgetMin: project.budget_min != null ? String(project.budget_min) : '',
 budgetMax: project.budget_max != null ? String(project.budget_max) : '',
 currency: project.currency ?? 'USD',
 startDate: project.start_date ?? '',
 endDate: project.end_date ?? '',
 duration: project.duration ?? '',
 isUrgent: project.is_urgent ?? false,
 locationType: (project.location_type as ProjectLocationType) || 'remote',
 visibility: (project.visibility as ProjectVisibility) || 'public'
 });
 } else {
 setSelectedProject(null);
 setFormData(createInitialFormData());
 }

 setError(null);
 setDrawerOpen(true);
 }, []);

 const handleCloseDrawer = useCallback(() => {
 if (isSubmitting) {
 return;
 }

 setDrawerOpen(false);
 setSelectedProject(null);
 setFormData(createInitialFormData());
 setError(null);
 }, [isSubmitting]);

 const deleteProject = useCallback(async (id: string) => {
 if (!confirm('Are you sure you want to delete this project?')) {
 return;
 }

 try {
 const { error: deleteError } = await supabase
 .from('opendeck_projects')
 .delete()
 .eq('id', id);

 if (deleteError) {
 throw deleteError;
 }

 loadProjects();
 } catch (deleteErr) {
 console.error('Error deleting project:', deleteErr);
 }
 }, [loadProjects, supabase]);

 const filteredProjects = useMemo(() => {
 return projects.filter(project => {
 const title = project.title || '';
 const description = project.description || '';
 const matchesStatus = filterStatus === 'all' || project.status === filterStatus;
 const matchesSearch =
 title.toLowerCase().includes(searchQuery.toLowerCase()) ||
 description.toLowerCase().includes(searchQuery.toLowerCase());

 return matchesStatus && matchesSearch;
 });
 }, [filterStatus, projects, searchQuery]);

 const statusItems = useMemo(() => (
 <>
 <SelectItem value="all">All Projects</SelectItem>
 <SelectItem value="draft">Draft</SelectItem>
 <SelectItem value="open">Open</SelectItem>
 <SelectItem value="in_progress">In Progress</SelectItem>
 <SelectItem value="completed">Completed</SelectItem>
 </>
 ), []);

 const experienceLevelItems = useMemo(() => experienceLevelOptions.map(option => (
 <SelectItem key={option.value} value={option.value}>
 {option.label}
 </SelectItem>
 )), []);

 const budgetTypeItems = useMemo(() => budgetTypeOptions.map(option => (
 <SelectItem key={option.value} value={option.value}>
 {option.label}
 </SelectItem>
 )), []);

 const locationTypeItems = useMemo(() => locationTypeOptions.map(option => (
 <SelectItem key={option.value} value={option.value}>
 {option.label}
 </SelectItem>
 )), []);

 const visibilityItems = useMemo(() => visibilityOptions.map(option => (
 <SelectItem key={option.value} value={option.value}>
 {option.label}
 </SelectItem>
 )), []);

 const handleSubmit = useCallback(async (event: FormEvent<HTMLFormElement>) => {
 event.preventDefault();

 const trimmedTitle = formData.title.trim();
 const trimmedDescription = formData.description.trim();
 const trimmedScope = formData.scope.trim();
 const trimmedCategory = formData.category.trim();
 const trimmedSkills = formData.skillsRequired.split(',').map(skill => skill.trim()).filter(Boolean);

 if (trimmedTitle.length < 5) {
 setError('Title must be at least 5 characters.');
 return;
 }

 if (trimmedDescription.length < 50) {
 setError('Description must be at least 50 characters.');
 return;
 }

 if (!trimmedCategory) {
 setError('Category is required.');
 return;
 }

 if (trimmedScope.length < 20) {
 setError('Scope must be at least 20 characters.');
 return;
 }

 if (trimmedSkills.length === 0) {
 setError('Please specify at least one required skill.');
 return;
 }

 const budgetMinValue = parseFloat(formData.budgetMin.trim());
 const budgetMaxValue = parseFloat(formData.budgetMax.trim());

 if (formData.budgetType !== 'not_specified') {
 if (Number.isNaN(budgetMinValue) || budgetMinValue < 0) {
 setError('Minimum budget must be a non-negative number.');
 return;
 }

 if (Number.isNaN(budgetMaxValue) || budgetMaxValue < 0) {
 setError('Maximum budget must be a non-negative number.');
 return;
 }

 if (budgetMaxValue < budgetMinValue) {
 setError('Maximum budget must be greater than or equal to minimum budget.');
 return;
 }
 }

 setError(null);
 setIsSubmitting(true);

 const payload = {
 title: trimmedTitle,
 description: trimmedDescription,
 category: trimmedCategory,
 subcategory: formData.subcategory.trim() || undefined,
 scope: trimmedScope,
 skills_required: trimmedSkills,
 experience_level: formData.experienceLevel,
 budget_type: formData.budgetType,
 budget_min: formData.budgetType === 'not_specified' ? null : budgetMinValue,
 budget_max: formData.budgetType === 'not_specified' ? null : budgetMaxValue,
 currency: formData.currency,
 start_date: formData.startDate || undefined,
 end_date: formData.endDate || undefined,
 duration: formData.duration.trim() || undefined,
 is_urgent: formData.isUrgent,
 location_type: formData.locationType,
 visibility: formData.visibility,
 client_id: userId,
 organization_id: orgId,
 status: selectedProject?.status || 'open'
 };

 try {
 if (selectedProject) {
 const { error: updateError } = await supabase
 .from('opendeck_projects')
 .update(payload)
 .eq('id', selectedProject.id);

 if (updateError) {
 throw updateError;
 }
 } else {
 const { error: insertError } = await supabase
 .from('opendeck_projects')
 .insert(payload);

 if (insertError) {
 throw insertError;
 }
 }

 setDrawerOpen(false);
 setSelectedProject(null);
 setFormData(createInitialFormData());
 loadProjects();
 } catch (submitError) {
 console.error('Error saving project:', submitError);
 setError(submitError instanceof Error ? submitError.message : 'An unexpected error occurred while saving the project.');
 } finally {
 setIsSubmitting(false);
 }
 }, [formData, loadProjects, orgId, selectedProject, supabase, userId]);

 const isSubmitDisabled = useMemo(() => {
 return (
 isSubmitting ||
 !formData.title.trim() ||
 formData.title.trim().length < 5 ||
 !formData.description.trim() ||
 formData.description.trim().length < 50 ||
 !formData.category.trim() ||
 !formData.scope.trim() ||
 formData.scope.trim().length < 20 ||
 formData.skillsRequired.split(',').map(skill => skill.trim()).filter(Boolean).length === 0
 );
 }, [formData, isSubmitting]);

 if (loading) {
 return (
 <div className="brand-marketplace flex items-center justify-center h-container-sm">
 <div className="brand-marketplace animate-spin rounded-full h-icon-lg w-icon-lg border-b-2 border-primary" />
 </div>
 );
 }

 return (
 <div className="brand-marketplace stack-lg">
 <div className="brand-marketplace flex justify-between items-center">
 <div>
 <h2 className="text-heading-3">Project Postings</h2>
 <p className="color-muted">Manage your project briefs and proposals</p>
 </div>
 <Button onClick={() => handleOpenDrawer()}>
 <Plus className="h-icon-xs w-icon-xs mr-sm" />
 Post New Project
 </Button>
 </div>

 <Card className="p-md">
 <div className="brand-marketplace flex flex-col md:flex-row gap-md">
 <div className="brand-marketplace flex-1">
 <div className="brand-marketplace relative">
 <Search className="absolute left-3 top-xs/2 -translate-y-1/2 h-icon-xs w-icon-xs color-muted" />
 <Input
 placeholder="Search projects..."
 value={searchQuery}
 onChange={(event: ChangeEvent<HTMLInputElement>) => setSearchQuery(event.target.value)}
 className="pl-2xl"
 />
 </div>
 </div>
 <Select value={filterStatus} onValueChange={setFilterStatus}>
 <SelectTrigger className="w-dropdown">
 <SelectValue placeholder="Filter by status" />
 </SelectTrigger>
 <SelectContent>{statusItems}</SelectContent>
 </Select>
 <div className="brand-marketplace flex gap-sm">
 <Button
 variant={viewMode === 'grid' ? 'default' : 'outline'}
 onClick={() => setViewMode('grid')}
 >
 Grid
 </Button>
 <Button
 variant={viewMode === 'list' ? 'default' : 'outline'}
 onClick={() => setViewMode('list')}
 >
 List
 </Button>
 </div>
 </div>
 </Card>

 {filteredProjects.length === 0 ? (
 <Card className="p-xsxl text-center">
 <div className="brand-marketplace max-w-md mx-auto">
 <Paperclip className="h-icon-2xl w-icon-2xl mx-auto mb-md color-muted" />
 <h3 className="text-body text-heading-4 mb-sm">No projects found</h3>
 <p className="color-muted mb-md">
 Post your first project to start receiving proposals from vendors
 </p>
 <Button onClick={() => handleOpenDrawer()}>
 <Plus className="h-icon-xs w-icon-xs mr-sm" />
 Post New Project
 </Button>
 </div>
 </Card>
 ) : viewMode === 'grid' ? (
 <div className="brand-marketplace grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-md">
 {filteredProjects.map(project => (
 <ProjectCard
 key={project.id}
 project={project}
 onEdit={handleOpenDrawer}
 onDelete={deleteProject}
 />
 ))}
 </div>
 ) : (
 <Card>
 <div className="brand-marketplace divide-y">
 {filteredProjects.map(project => (
 <ProjectListRow
 key={project.id}
 project={project}
 onEdit={handleOpenDrawer}
 onDelete={deleteProject}
 />
 ))}
 </div>
 </Card>
 )}

 <AppDrawer
 open={drawerOpen}
 onClose={handleCloseDrawer}
 record={selectedProject}
 mode={selectedProject ? 'edit' : 'create'}
 title={selectedProject ? 'Edit Project' : 'Post New Project'}
 fields={[]}
 loading={isSubmitting}
 tabs={[{
 key: 'details',
 label: 'Details',
 content: (
 <form onSubmit={handleSubmit} className="p-lg stack-md">
 <div>
 <label htmlFor="title" className="text-body-sm form-label">
 Project Title
 </label>
 <Input


 value={formData.title}
 onChange={handleInputChange}
 placeholder="e.g., Stage Design for Music Festival"
 required
 />
 </div>

 <div>
 <label htmlFor="category" className="text-body-sm form-label">
 Category
 </label>
 <Select
 value={formData.category}
 onValueChange={handleSelectChange('category')}
 >
 <SelectTrigger>
 <SelectValue placeholder="Select category" />
 </SelectTrigger>
 <SelectContent>
 {categories.map(cat => (
 <SelectItem key={cat} value={cat}>
 {cat}
 </SelectItem>
 ))}
 </SelectContent>
 </Select>
 </div>

 <div>
 <label htmlFor="description" className="text-body-sm form-label">
 Description
 </label>
 <Textarea


 value={formData.description}
 onChange={handleTextareaChange}
 placeholder="Describe your project requirements in detail..."
 rows={4}
 required
 />
 </div>

 <div>
 <label htmlFor="scope" className="text-body-sm form-label">
 Scope of Work
 </label>
 <Textarea


 value={formData.scope}
 onChange={handleTextareaChange}
 placeholder="Define the scope and deliverables..."
 rows={3}
 required
 />
 </div>

 <div>
 <label htmlFor="skillsRequired" className="text-body-sm form-label">
 Required Skills
 </label>
 <Input


 value={formData.skillsRequired}
 onChange={handleInputChange}
 placeholder="Comma-separated list of skills"
 required
 />
 </div>

 <div className="brand-marketplace grid grid-cols-1 gap-md md:grid-cols-2">
 <div>
 <label className="text-body-sm form-label">Budget Type</label>
 <Select
 value={formData.budgetType}
 onValueChange={handleSelectChange('budgetType')}
 >
 <SelectTrigger>
 <SelectValue />
 </SelectTrigger>
 <SelectContent>{budgetTypeItems}</SelectContent>
 </Select>
 </div>

 <div>
 <label className="text-body-sm form-label">Experience Level</label>
 <Select
 value={formData.experienceLevel}
 onValueChange={handleSelectChange('experienceLevel')}
 >
 <SelectTrigger>
 <SelectValue />
 </SelectTrigger>
 <SelectContent>{experienceLevelItems}</SelectContent>
 </Select>
 </div>
 </div>

 {formData.budgetType !== 'not_specified' && (
 <div className="brand-marketplace grid grid-cols-1 gap-md md:grid-cols-2">
 <div>
 <label htmlFor="budgetMin" className="text-body-sm form-label">
 {formData.budgetType === 'hourly' ? 'Min Rate' : 'Min Budget'}
 </label>
 <Input


 type="number"
 value={formData.budgetMin}
 onChange={handleInputChange}
 placeholder="0"
 min="0"
 step="0.01"
 />
 </div>
 <div>
 <label htmlFor="budgetMax" className="text-body-sm form-label">
 {formData.budgetType === 'hourly' ? 'Max Rate' : 'Max Budget'}
 </label>
 <Input


 type="number"
 value={formData.budgetMax}
 onChange={handleInputChange}
 placeholder="0"
 min="0"
 step="0.01"
 />
 </div>
 </div>
 )}

 <div className="brand-marketplace grid grid-cols-1 gap-md md:grid-cols-2">
 <div>
 <label htmlFor="startDate" className="text-body-sm form-label">
 Start Date
 </label>
 <Input


 type="date"
 value={formData.startDate}
 onChange={handleInputChange}
 />
 </div>
 <div>
 <label htmlFor="endDate" className="text-body-sm form-label">
 End Date
 </label>
 <Input


 type="date"
 value={formData.endDate}
 onChange={handleInputChange}
 />
 </div>
 </div>

 <div>
 <label htmlFor="duration" className="text-body-sm form-label">
 Duration
 </label>
 <Input


 value={formData.duration}
 onChange={handleInputChange}
 placeholder="e.g., 2 weeks, 1 month"
 />
 </div>

 <div className="brand-marketplace grid grid-cols-1 gap-md md:grid-cols-2">
 <div>
 <label className="text-body-sm form-label">Location Type</label>
 <Select
 value={formData.locationType}
 onValueChange={handleSelectChange('locationType')}
 >
 <SelectTrigger>
 <SelectValue />
 </SelectTrigger>
 <SelectContent>{locationTypeItems}</SelectContent>
 </Select>
 </div>
 <div>
 <label className="text-body-sm form-label">Visibility</label>
 <Select
 value={formData.visibility}
 onValueChange={handleSelectChange('visibility')}
 >
 <SelectTrigger>
 <SelectValue />
 </SelectTrigger>
 <SelectContent>{visibilityItems}</SelectContent>
 </Select>
 </div>
 </div>

 <div className="brand-marketplace flex items-center gap-sm pt-sm">
 <input


 type="checkbox"
 checked={formData.isUrgent}
 onChange={handleInputChange}
 className="rounded border-border"
 />
 <label htmlFor="isUrgent" className="text-body-sm">
 Mark as urgent project
 </label>
 </div>

 <div className="brand-marketplace flex items-center justify-between gap-sm pt-md">
 <div className="flex items-center gap-sm text-body-sm color-muted">
 {error && (
 <>
 <AlertCircle className="h-icon-xs w-icon-xs color-destructive" />
 <span className="color-destructive">{error}</span>
 </>
 )}
 </div>
 <div className="brand-marketplace flex items-center gap-sm">
 <Button type="button" variant="outline" onClick={handleCloseDrawer}>
 Cancel
 </Button>
 <Button type="submit" disabled={isSubmitDisabled}>
 {selectedProject ? 'Update Project' : 'Post Project'}
 </Button>
 </div>
 </div>
 </form>
 )
 }]}
 />
 </div>
 );
}

