  Drawer,
  DrawerContent,
  DrawerHeader,
  DrawerTitle,
  DrawerDescription,
  DrawerFooter
} from '@ghxstship/ui';
import {
  Download,
  FileText,
  CheckCircle,
  AlertCircle,
  Loader2,
  Info
} from 'lucide-react';
import { marketplaceService } from '../lib/marketplace-service';
import type { ListingFilters } from '../types';

interface ExportDrawerProps {
  orgId: string;
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onExportComplete?: (results: unknown) => void;
}

export default function ExportDrawer({
  orgId,
  open,
  onOpenChange,
  onExportComplete
}: ExportDrawerProps) {
  const [format, setFormat] = useState<'csv' | 'json' | 'excel' | 'pdf'>('csv');
  const [selectedFields, setSelectedFields] = useState<string[]>([]);
  const [includeMetadata, setIncludeMetadata] = useState(true);
  const [filters, setFilters] = useState<Partial<ListingFilters>({});
  const [isExporting, setIsExporting] = useState(false);
  const [results, setResults] = useState<(null);
  const [metadata, setMetadata] = useState<(null);

  // Available fields for export
  const availableFields = [
    { key: 'title', label: 'Title', required: true },
    { key: 'description', label: 'Description' },
    { key: 'type', label: 'Type' },
    { key: 'category', label: 'Category' },
    { key: 'subcategory', label: 'Subcategory' },
    { key: 'status', label: 'Status' },
    { key: 'pricing.amount', label: 'Price Amount' },
    { key: 'pricing.currency', label: 'Currency' },
    { key: 'pricing.negotiable', label: 'Negotiable' },
    { key: 'location.city', label: 'City' },
    { key: 'location.country', label: 'Country' },
    { key: 'location.isRemote', label: 'Remote' },
    { key: 'featured', label: 'Featured' },
    { key: 'response_count', label: 'Responses' },
    { key: 'view_count', label: 'Views' },
    { key: 'created_at', label: 'Created Date' },
    { key: 'updated_at', label: 'Updated Date' },
  ];

  const handleFieldToggle = (fieldKey: string) => {
    setSelectedFields(prev =>
      prev.includes(fieldKey)
        ? prev.filter(f => f !== fieldKey)
        : [...prev, fieldKey]
    );
  };

  const handleSelectAllFields = () => {
    setSelectedFields(availableFields.map(f => f.key));
  };

  const handleSelectTemplate = (templateKey: string) => {
    const templates = marketplaceService.getExportTemplates();
    const template = templates[templateKey as keyof typeof templates];
    if (template) {
      setSelectedFields(template.fields);
    }
  };

  const loadMetadata = async () => {
    try {
      const meta = await marketplaceService.getExportMetadata(orgId, filters);
      setMetadata(meta);
    } catch (error) {
      console.error('Failed to load export metadata:', error);
    }
  };

  React.useEffect(() => {
    if (open) {
      loadMetadata();
      // Set default fields
      setSelectedFields(availableFields.filter(f => f.required).map(f => f.key));
    }
  }, [open, filters]);

  const handleExport = async () => {
    setIsExporting(true);
    setResults(null);

    try {
      const exportResults = await marketplaceService.exportListingsBulk(
        orgId,
        '',
        format,
        filters,
        selectedFields
      );

      setResults({
        success: true,
        format,
        recordCount: metadata?.totalRecords || 0,
        fileSize: exportResults.length,
        downloadUrl: `data:text/plain;charset=utf-8,${encodeURIComponent(exportResults)}`
      });

      onExportComplete?.(exportResults);
    } catch (error) {
      setResults({
        success: false,
        error: error instanceof Error ? error.message : 'Export failed'
      });
    } finally {
      setIsExporting(false);
    }
  };

  const handleDownload = () => {
    if (results?.downloadUrl) {
      const link = document.createElement('a');
      link.href = results.downloadUrl;
      link.download = `marketplace-listings-${new Date().toISOString().split('T')[0]}.${format}`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };

  const resetForm = () => {
    setFormat('csv');
    setSelectedFields([]);
    setIncludeMetadata(true);
    setFilters({});
    setResults(null);
    setMetadata(null);
  };

  const handleClose = () => {
    resetForm();
    onOpenChange(false);
  };

  return (
    <Drawer open={open} onOpenChange={handleClose}>
      <DrawerContent className="max-h-[90vh]">
        <DrawerHeader>
          <DrawerTitle className="flex items-center gap-xs">
            <Download className="h-icon-sm w-icon-sm" />
            Export Listings
          </DrawerTitle>
          <DrawerDescription>
            Export marketplace listings to various formats
          </DrawerDescription>
        </DrawerHeader>

        <div className="flex-1 overflow-y-auto p-lg">
          <div className="space-y-lg">
            {/* Export Summary */}
            {metadata && (
              <div className="bg-muted/50 rounded-lg p-md">
                <div className="flex items-center gap-xs mb-2">
                  <Info className="h-icon-xs w-icon-xs" />
                  <span className="font-medium">Export Summary</span>
                </div>
                <div className="grid grid-cols-3 gap-md text-sm">
                  <div>
                    <div className="text-2xl font-bold">{metadata.totalRecords}</div>
                    <div className="text-muted-foreground">Total Records</div>
                  </div>
                  <div>
                    <div className="text-lg">{metadata.estimatedFileSize}</div>
                    <div className="text-muted-foreground">Estimated Size</div>
                  </div>
                  <div>
                    <div className="text-lg">{metadata.fieldCount}</div>
                    <div className="text-muted-foreground">Available Fields</div>
                  </div>
                </div>
              </div>
            )}

            {/* Format Selection */}
            <div className="space-y-md">
              <div>
                <Label htmlFor="format">Export Format</Label>
                <Select value={format} onChange={(value: 'csv' | 'json' | 'excel' | 'pdf') => setFormat(value)}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="csv">CSV (Comma Separated Values)</SelectItem>
                    <SelectItem value="json">JSON (JavaScript Object Notation)</SelectItem>
                    <SelectItem value="excel">Excel (XLSX)</SelectItem>
                    <SelectItem value="pdf">PDF (Portable Document Format)</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            <Separator />

            {/* Export Templates */}
            <div className="space-y-md">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold">Quick Templates</h3>
                <Button variant="outline" size="sm" onClick={handleSelectAllFields}>
                  Select All Fields
                </Button>
              </div>

              <div className="grid grid-cols-1 gap-sm">
                {Object.entries(marketplaceService.getExportTemplates()).map(([key, template]) => (
                  <button
                    key={key}
                    onClick={() => handleSelectTemplate(key)}
                    className="text-left border rounded-lg p-md hover:bg-muted/50 transition-colors"
                  >
                    <div className="flex items-center justify-between mb-2">
                      <h4 className="font-medium">{template.name}</h4>
                      <Badge variant="secondary" className="text-xs">
                        {template.fields.length} fields
                      </Badge>
                    </div>
                    <p className="text-sm text-muted-foreground">{template.description}</p>
                  </button>
                ))}
              </div>
            </div>

            <Separator />

            {/* Field Selection */}
            <div className="space-y-md">
              <h3 className="text-lg font-semibold">Select Fields</h3>

              <div className="grid grid-cols-1 gap-xs max-h-60 overflow-y-auto">
                {availableFields.map((field) => (
                  <div key={field.key} className="flex items-center space-x-xs">
                    <Checkbox
                      id={field.key}
                      checked={selectedFields.includes(field.key)}
                      onCheckedChange={() => handleFieldToggle(field.key)}
                      disabled={field.required}
                    />
                    <Label htmlFor={field.key} className="text-sm flex-1">
                      {field.label}
                      {field.required && <span className="text-red-500 ml-1">*</span>}
                    </Label>
                  </div>
                ))}
              </div>

              <div className="flex items-center space-x-xs pt-2">
                <Checkbox
                  id="include-metadata"
                  checked={includeMetadata}
                  onCheckedChange={(checked) => setIncludeMetadata(!!checked)}
                />
                <Label htmlFor="include-metadata" className="text-sm">
                  Include export metadata and timestamp
                </Label>
              </div>
            </div>

            {/* Results */}
            {results && (
              <>
                <Separator />
                <div className="space-y-md">
                  <h3 className="text-lg font-semibold">Export Results</h3>

                  {results.success ? (
                    <div className="space-y-md">
                      <div className="flex items-center gap-sm p-md bg-green-50 border border-green-200 rounded-lg">
                        <CheckCircle className="h-icon-sm w-icon-sm text-green-500 flex-shrink-0" />
                        <div>
                          <div className="font-medium text-green-900">Export Successful</div>
                          <div className="text-sm text-green-700">
                            {results.recordCount} records exported in {results.format.toUpperCase()} format
                          </div>
                        </div>
                      </div>

                      <div className="flex gap-sm">
                        <Button onClick={handleDownload} className="flex-1">
                          <Download className="h-icon-xs w-icon-xs mr-2" />
                          Download File
                        </Button>
                        <Button variant="outline" onClick={() => setResults(null)}>
                          Export Again
                        </Button>
                      </div>
                    </div>
                  ) : (
                    <div className="flex items-center gap-sm p-md bg-red-50 border border-red-200 rounded-lg">
                      <AlertCircle className="h-icon-sm w-icon-sm text-red-500 flex-shrink-0" />
                      <div>
                        <div className="font-medium text-red-900">Export Failed</div>
                        <div className="text-sm text-red-700">{results.error}</div>
                      </div>
                    </div>
                  )}
                </div>
              </>
            )}
          </div>
        </div>

        {/* Footer Actions */}
        <div className="border-t p-lg">
          <div className="flex gap-sm">
            {!results?.success && (
              <Button
                onClick={handleExport}
                disabled={isExporting || selectedFields.length === 0}
                className="flex-1"
              >
                {isExporting ? (
                  <>
                    <Loader2 className="h-icon-xs w-icon-xs mr-2 animate-spin" />
                    Exporting...
                  </>
                ) : (
                  <>
                    <Download className="h-icon-xs w-icon-xs mr-2" />
                    Export {metadata?.totalRecords || 0} Listings
                  </>
                )}
              </Button>
            )}
            <Button variant="outline" onClick={handleClose}>
              Close
            </Button>
          </div>
        </div>
      </DrawerContent>
    </Drawer>
  );
}

'use client';

import { Edit3, FileText, Activity as ActivityIcon, MessageSquare } from "lucide-react";
import { useEffect, useState } from 'react';
import {
  AppDrawer,
  Button
} from "@ghxstship/ui";
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { tryCatch, reportError } from '@ghxstship/ui/utils/error-handling';
import { AppDrawer } from '@ghxstship/ui';

interface VendorProfileDrawerProps {
 orgId: string;
 vendorId: string | null;
 open: boolean;
 onClose: () => void;
}

export function VendorProfileDrawer({ orgId, vendorId, open, onClose }: VendorProfileDrawerProps) {
 const t = useTranslations('marketplace');
 const supabase = createBrowserClient();

 const [vendor, setVendor] = useState<any | null>(null);
 const [saving, setSaving] = useState(false);
 const [message, setMessage] = useState<string | null>(null);
 const [form, setForm] = useState({
 name: '',
 website: '',
 contactEmail: '',
 status: 'active'
 });

 const [tabState, setTabState] = useState<'details' | 'edit' | 'comments' | 'activity'>('details');

 useEffect(() => {
 if (!open || !vendorId) return;

 (async () => {
 const responseResult = await tryCatch(async () =>
 fetch('/api/v1/vendors?limit=100', {
 headers: { 'x-org-id': orgId }
 })
 );

 if (!responseResult.success) {
 reportError(responseResult.error);
 setVendor(null);
 return;
 }

 const response = responseResult.data;
 const payload = await response.json();
 const found = (payload.vendors || []).find((entry: unknown) => entry.id === vendorId) ?? null;

 if (found) {
 setVendor(found);
 setForm({
 name: found.display_name ?? found.business_name ?? '',
 website: found.website ?? '',
 contactEmail: found.email ?? '',
 status: found.status ?? 'active'
 });
 } else {
 setVendor(null);
 }
 })();
 }, [open, vendorId, orgId]);

 async function handleSave() {
 if (!vendorId) return;

 setSaving(true);
 setMessage(null);

 try {
 const responseResult = await tryCatch(async () =>
 fetch('/api/v1/vendors', {
 method: 'PUT',
 headers: {
 'content-type': 'application/json',
 'x-org-id': orgId
 },
 body: JSON.stringify({
 id: vendorId,
 display_name: form.name,
 website: form.website || null,
 email: form.contactEmail || null,
 status: form.status
 })
 })
 );

 if (!responseResult.success) {
 reportError(responseResult.error);
 throw responseResult.error;
 }

 const response = responseResult.data;
 if (!response.ok) {
 const payload = await response.json();
 throw new Error(payload?.error ?? 'Update failed');
 }

 setMessage(t('vendor.updated'));
 } catch (error) {
 setMessage(error instanceof Error ? error.message : 'Update failed');
 } finally {
 setSaving(false);
 }
 }

 const [comments, setComments] = useState<Array<{ id: string; body: string; created_at: string }>([]);
 const [activity, setActivity] = useState<Array<{ action: string; occurred_at: string }>([]);
 const [loadingComments, setLoadingComments] = useState(false);
 const [loadingActivity, setLoadingActivity] = useState(false);

 useEffect(() => {
 if (!open || !vendorId) return;

 (async () => {
 setLoadingComments(true);
 try {
 const { data } = await supabase
 .from('comments')
 .select('id, body, created_at')
 .eq('organization_id', orgId)
 .eq('entity_type', 'vendor')
 .eq('entity_id', vendorId)
 .order('created_at', { ascending: false })
 .limit(50);

 setComments(data ?? []);
 } catch {
 setComments([]);
 }
 setLoadingComments(false);
 })();
 }, [open, vendorId, orgId, supabase]);

 useEffect(() => {
 if (!open || !vendorId) return;

 (async () => {
 setLoadingActivity(true);
 try {
 const responseResult = await tryCatch(async () => fetch(`/api/audit/${orgId}/vendors/${vendorId}`));

 if (!responseResult.success) {
 reportError(responseResult.error);
 throw responseResult.error;
 }

 const response = responseResult.data;
 const payload = await response.json();
 setActivity(payload?.data ?? []);
 } catch {
 setActivity([]);
 }
 setLoadingActivity(false);
 })();
 }, [open, vendorId, orgId]);

 if (!open) return null;

 const tabs = [
 {
 key: 'details',
 label: (
 <span className="flex items-center gap-xs">
 <FileText className="h-icon-xs w-icon-xs" />
 {t('vendor.drawer.details')}
 </span>
 ),
 content: vendor ? (
 <div className="stack-sm text-body-sm">
 {message ? (
 <div role="alert" className="mb-sm text-body-sm">
 {message}
 </div>
 ) : null}
 <div>
 <span className="form-label">{t('vendor.name')}:</span> {vendor.display_name || vendor.business_name}
 </div>
 <div>
 <span className="form-label">{t('vendor.website')}:</span> {vendor.website || ''}
 </div>
 <div>
 <span className="form-label">{t('vendor.email')}:</span> {vendor.email || ''}
 </div>
 <div>
 <span className="form-label">{t('vendor.status')}:</span> {vendor.status}
 </div>
 <div>
 <span className="form-label">{t('vendor.primaryCategory')}:</span> {vendor.primary_category || ''}
 </div>
 </div>
 ) : null
 },
 {
 key: 'edit',
 label: (
 <span className="flex items-center gap-xs">
 <Edit3 className="h-icon-xs w-icon-xs" />
 {t('vendor.drawer.edit')}
 </span>
 ),
 content: vendor ? (
 <form
 className="stack-sm"
 onSubmit={(event) => {
 event.preventDefault();
 void handleSave();
 }}
 >
 <div className="grid gap-xs">
 <label htmlFor="vendor-name" className="text-body-sm">
 {t('vendor.name')}
 </label>
 <input

 className="rounded border px-md py-xs"
 value={form.name}
 onChange={(event) => setForm((prev: unknown) => ({ ...prev, name: event.target.value }))}
 />
 </div>
 <div className="grid gap-xs">
 <label htmlFor="vendor-website" className="text-body-sm">
 {t('vendor.website')}
 </label>
 <input

 className="rounded border px-md py-xs"
 value={form.website}
 onChange={(event) => setForm((prev: unknown) => ({ ...prev, website: event.target.value }))}
 />
 </div>
 <div className="grid gap-xs">
 <label htmlFor="vendor-email" className="text-body-sm">
 {t('vendor.email')}
 </label>
 <input

 className="rounded border px-md py-xs"
 value={form.contactEmail}
 onChange={(event) => setForm((prev: unknown) => ({ ...prev, contactEmail: event.target.value }))}
 />
 </div>
 <div className="grid gap-xs">
 <label htmlFor="vendor-status" className="text-body-sm">
 {t('vendor.status')}
 </label>
 <select

 className="rounded border px-md py-xs"
 value={form.status}
 onChange={(event) => setForm((prev: unknown) => ({ ...prev, status: event.target.value }))}
 >
 <option value="active">{t('vendor.statusActive')}</option>
 <option value="pending">{t('vendor.statusPending')}</option>
 <option value="inactive">{t('vendor.statusInactive')}</option>
 </select>
 </div>
 <div className="flex items-center justify-end gap-sm pt-sm border-t">
 <Button variant="primary" disabled={saving} type="submit">
 {t('actions.save')}
 </Button>
 </div>
 </form>
 ) : null
 },
 {
 key: 'comments',
 label: (
 <span className="flex items-center gap-xs">
 <MessageSquare className="h-icon-xs w-icon-xs" />
 {t('vendor.drawer.comments')}
 </span>
 ),
 content: (
 <div className="stack-sm text-body-sm">
 {loadingComments
 ? t('vendor.drawer.loading')
 : comments.length === 0
 ? t('vendor.drawer.emptyComments')
 : (
 <ul className="stack-sm">
 {comments.map((comment) => (
 <li key={comment.id} className="rounded border p-sm">
 <div className="whitespace-pre-wrap">{comment.body}</div>
 <div className="text-body-sm opacity-60">
 {new Date(comment.created_at).toLocaleString()}
 </div>
 </li>
 ))}
 </ul>
 )}
 </div>
 )
 },
 {
 key: 'activity',
 label: (
 <span className="flex items-center gap-xs">
 <ActivityIcon className="h-icon-xs w-icon-xs" />
 {t('vendor.drawer.activity')}
 </span>
 ),
 content: (
 <div className="stack-sm text-body-sm">
 {loadingActivity ? (
 t('vendor.drawer.loading')
 ) : (
 <ul className="stack-xs">
 {activity.map((entry, index) => (
 <li key={`${entry.action}-${index}`} className="flex items-center justify-between gap-md">
 <div className="font-mono text-body-sm opacity-70">
 {new Date(entry.occurred_at).toLocaleString()}
 </div>
 <div className="flex-1">{entry.action}</div>
 </li>
 ))}
 </ul>
 )}
 </div>
 )
 },
 ];

 return (
 <AppDrawer
 open={open}
 onClose={onClose}
 record={vendor}
 mode="view"
 title={vendor?.display_name || vendor?.business_name || t('vendor.drawer.title')}
 fields={[]}
 tabs={tabs}
 activeTab={tabState}
 onTabChange={(key) => setTabState(key as typeof tabState)}
 />
 );
}

