'use client';


import { useState, useEffect } from 'react';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { Card, Badge, Button } from '@ghxstship/ui';
import { Activity, Calendar, Clock, Download, Edit, Eye, Filter, History, Trash2, User } from "lucide-react";

interface ActivityRecord {
  id: string;
  activity_type: string;
  activity_description: string;
  performed_by: string;
  performed_by_name?: string;
  created_at: string;
  metadata?;
}

export default function ActivityClient({ orgId, userId }: { orgId: string; userId: string }) {
  const t = useTranslations('profile');
  const sb = createBrowserClient();

  const [activities, setActivities] = useState<ActivityRecord[]>([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState<string>('all');

  useEffect(() => {
    loadActivities();
  }, [orgId, userId, filter]);

  const loadActivities = async () => {
    try {
      setLoading(true);

      let query = sb
        .from('user_profile_activity')
        .select(`
          *,
          performed_by_user:users!performed_by(full_name)
        `)
        .eq('organization_id', orgId)
        .eq('user_id', userId)
        .order('created_at', { ascending: false });

      if (filter !== 'all') {
        query = query.eq('activity_type', filter);
      }

      const { data, error } = await query.limit(50);

      if (error) throw error;

      const formattedActivities = (data || []).map(activity => ({
        ...activity,
        performed_by_name: activity.performed_by_user?.full_name || 'System'
      }));

      setActivities(formattedActivities);
    } catch (error) {
      console.error('Error loading activities:', error);
    } finally {
      setLoading(false);
    }
  };

  const getActivityIcon = (type: string) => {
    const icons = {
      profile_updated: Edit,
      profile_created: User,
      profile_viewed: Eye,
      certification_added: Badge,
      job_history_added: Activity,
      default: Activity
    };

    const IconComponent = icons[type as keyof typeof icons] || icons.default;
    return <IconComponent className="h-icon-xs w-icon-xs" />;
  };

  const getActivityColor = (type: string) => {
    const colors = {
      profile_updated: 'color-accent',
      profile_created: 'color-success',
      profile_viewed: 'color-muted',
      certification_added: 'color-secondary',
      job_history_added: 'color-warning',
      default: 'color-muted'
    };

    return colors[type as keyof typeof colors] || colors.default;
  };

  const formatActivityType = (type: string) => {
    return type.split('_').map(word => 
      word.charAt(0).toUpperCase() + word.slice(1)
    ).join(' ');
  };

  const exportActivities = () => {
    const csvContent = [
      ['Date', 'Activity Type', 'Description', 'Performed By'],
      ...activities.map(activity => [
        new Date(activity.created_at).toLocaleString(),
        formatActivityType(activity.activity_type),
        activity.activity_description,
        activity.performed_by_name || 'System'
      ])
    ].map(row => row.join(',')).join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `profile-activity-${new Date().toISOString().split('T')[0]}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  if (loading) {
    return (
      <div className="stack-md">
        {[...Array(5)].map((_, i) => (
          <div key={i} className="animate-pulse">
            <div className="h-component-md bg-secondary rounded-lg"></div>
          </div>
        ))}
      </div>
    );
  }

  return (
    <div className="stack-lg">
      {/* Header with filters */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-md">
          <h2 className="text-heading-4 text-heading-4">Activity Log</h2>
          <select
            value={filter}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFilter(e.target.value)}
            className=" px-md py-xs border rounded-md text-body-sm"
          >
            <option value="all">All Activities</option>
            <option value="profile_updated">Profile Updates</option>
            <option value="profile_created">Profile Created</option>
            <option value="certification_added">Certifications</option>
            <option value="job_history_added">Job History</option>
          </select>
        </div>

        <Button
          variant="outline"

          onClick={exportActivities}
          className="flex items-center gap-sm"
        >
          <Download className="h-icon-xs w-icon-xs" />
          Export
        </Button>
      </div>

      {/* Activity Timeline */}
      <div className="stack-md">
        {activities.length > 0 ? (
          activities.map((activity, index) => (
            <Card key={activity.id}>
              <div className="p-md">
                <div className="flex items-start gap-md">
                  <div className={`p-sm rounded-full bg-secondary ${getActivityColor(activity.activity_type)}`}>
                    {getActivityIcon(activity.activity_type)}
                  </div>

                  <div className="flex-1">
                    <div className="flex items-center justify-between mb-sm">
                      <div className="flex items-center gap-sm">
                        <h4 className="form-label">{activity.activity_description}</h4>
                        <Badge variant="secondary">
                          {formatActivityType(activity.activity_type)}
                        </Badge>
                      </div>

                      <div className="flex items-center gap-sm text-body-sm color-muted">
                        <Clock className="h-icon-xs w-icon-xs" />
                        <span>{new Date(activity.created_at).toLocaleString()}</span>
                      </div>
                    </div>

                    <div className="flex items-center gap-sm text-body-sm color-muted">
                      <User className="h-icon-xs w-icon-xs" />
                      <span>by {activity.performed_by_name}</span>
                    </div>

                    {activity.metadata && (
                      <div className="mt-sm p-sm bg-secondary/50 rounded text-body-sm">
                        <pre className="whitespace-pre-wrap">
                          {JSON.stringify(activity.metadata, null, 2)}
                        </pre>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </Card>
          ))
        ) : (
          <Card>
            <div className="p-xl text-center">
              <Activity className="h-icon-2xl w-icon-2xl mx-auto mb-md color-muted opacity-50" />
              <h3 className="text-body form-label mb-sm">No Activity Found</h3>
              <p className="color-muted">
                {filter === 'all' 
                  ? 'No profile activity has been recorded yet.'
                  : `No ${formatActivityType(filter)} activities found.`
                }
              </p>
            </div>
          </Card>
        )}
      </div>

      {/* Load More */}
      {activities.length >= 50 && (
        <div className="text-center">
          <Button variant="outline" onClick={loadActivities}>
            Load More Activities
          </Button>
        </div>
      )}
    </div>
  );
}

'use client';


import { useState, useEffect } from 'react';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import {
  Avatar,
  Badge,
  Button,
  Card,
  Input,
  UnifiedInput
} from "@ghxstship/ui";
import { Camera, Save, User } from 'lucide-react';

interface UserProfile {
  id: string;
  user_id: string;
  avatar_url?: string;
  date_of_birth?: string;
  gender?: 'male' | 'female' | 'non-binary' | 'prefer-not-to-say';
  nationality?: string;
  languages: string[];
  profile_completion_percentage: number;
  status: string;
  created_at: string;
  updated_at: string;
}

export default function BasicInfoClient({ orgId, userId }: { orgId: string; userId: string }) {
  const t = useTranslations('profile');
  const sb = createBrowserClient();
  const [profile, setProfile] = useState<UserProfile | null>(null);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [formData, setFormData] = useState({
    avatar_url: '',
    date_of_birth: '',
    gender: '',
    nationality: '',
    languages: [] as string[]
  });

  useEffect(() => {
    loadProfile();
  }, [orgId, userId]);

  const loadProfile = async () => {
    try {
      setLoading(true);

      // Get current user's profile
      const { data: userData } = await sb
        .from('users')
        .select('id')
        .eq('auth_id', userId)
        .single();

      if (userData) {
        const { data: profileData } = await sb
          .from('user_profiles')
          .select('*')
          .eq('user_id', userData.id)
          .eq('organization_id', orgId)
          .single();

        if (profileData) {
          setProfile(profileData);
          setFormData({
            avatar_url: profileData.avatar_url || '',
            date_of_birth: profileData.date_of_birth || '',
            gender: profileData.gender || '',
            nationality: profileData.nationality || '',
            languages: profileData.languages || []
          });
        }
      }
    } catch (error) {
      console.error('Error loading profile:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async () => {
    if (!profile) return;

    try {
      setSaving(true);

      const { error } = await sb
        .from('user_profiles')
        .update({
          ...formData,
          updated_at: new Date().toISOString()
        })
        .eq('id', profile.id);

      if (error) throw error;

      await loadProfile(); // Reload to get updated data
    } catch (error) {
      console.error('Error saving profile:', error);
    } finally {
      setSaving(false);
    }
  };

  const handleLanguageAdd = (language: string) => {
    if (language && !formData.languages.includes(language)) {
      setFormData(prev => ({
        ...prev,
        languages: [...prev.languages, language]
      }));
    }
  };

  const handleLanguageRemove = (language: string) => {
    setFormData(prev => ({
      ...prev,
      languages: prev.languages.filter(l => l !== language)
    }));
  };

  if (loading) {
    return (
      <div className="stack-lg">
        <div className="animate-pulse">
          <div className="h-component-lg w-component-lg bg-secondary rounded-full mx-auto mb-md"></div>
          <div className="stack-sm">
            <div className="h-icon-xs bg-secondary rounded w-3/4"></div>
            <div className="h-icon-xs bg-secondary rounded w-1/2"></div>
            <div className="h-icon-xs bg-secondary rounded w-2/3"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="stack-lg">
      {/* Avatar Section */}
      <div className="flex flex-col items-center stack-md">
        <div className="relative">
          <Avatar className="h-component-lg w-component-lg">
            {formData.avatar_url ? (
              <img src={formData.avatar_url} alt="Profile" className="h-full w-full object-cover" />
            ) : (
              <User className="h-icon-2xl w-icon-2xl" />
            )}
          </Avatar>
          <Button

            variant="outline"
            className="absolute -bottom-2 -right-2 rounded-full p-sm"
          >
            <Camera className="h-3 w-3" />
          </Button>
        </div>

        {profile && (
          <div className="text-center">
            <Badge variant={profile.status === 'active' ? 'default' : 'secondary'}>
              {profile.status}
            </Badge>
            <div className="mt-sm text-body-sm color-muted">
              Profile {profile.profile_completion_percentage}% complete
            </div>
          </div>
        )}
      </div>

      {/* Basic Information Form */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
        <div className="stack-sm">
          <label className="text-body-sm form-label">Avatar URL</label>
          <Input             value={formData.avatar_url}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData(prev => ({ ...prev, avatar_url: e.target.value }))}
            placeholder="https://example.com/avatar.jpg"
          />
        </div>

        <div className="stack-sm">
          <label className="text-body-sm form-label">Date of Birth</label>
          <Input             type="date"
            value={formData.date_of_birth}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData(prev => ({ ...prev, date_of_birth: e.target.value }))}
          />
        </div>

        <div className="stack-sm">
          <label className="text-body-sm form-label">Gender</label>
          <select
            value={formData.gender}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData(prev => ({ ...prev, gender: e.target.value }))}
            className="w-full  px-md py-sm border border-input bg-background rounded-md"
          >
            <option value="">Select gender</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
            <option value="non-binary">Non-binary</option>
            <option value="prefer-not-to-say">Prefer not to say</option>
          </select>
        </div>

        <div className="stack-sm">
          <label className="text-body-sm form-label">Nationality</label>
          <Input             value={formData.nationality}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData(prev => ({ ...prev, nationality: e.target.value }))}
            placeholder="e.g., American, British, Canadian"
          />
        </div>
      </div>

      {/* Languages Section */}
      <div className="stack-md">
        <div>
          <label className="text-body-sm form-label">Languages</label>
          <div className="flex flex-wrap gap-sm mt-sm">
            {formData.languages.map((language: any) => (
              <Badge
                key={language}
                variant="secondary"
                className="cursor-pointer"
                onClick={() => handleLanguageRemove(language)}
              >
                {language} 
              </Badge>
            ))}
          </div>
        </div>

        <div className="flex gap-sm">
          <Input             placeholder="Add a language"
            onKeyPress={(e: any) => {
              if (e.key === 'Enter') {
                handleLanguageAdd(e.currentTarget.value);
                e.currentTarget.value = '';
              }
            }}
          />
          <Button
            variant="outline"
            onClick={(e: React.MouseEvent) => {
              const input = e.currentTarget.previousElementSibling as HTMLInputElement;
              handleLanguageAdd(input.value);
              input.value = '';
            }}
          >
            Add
          </Button>
        </div>
      </div>

      {/* Save Button */}
      <div className="flex justify-end">
        <Button onClick={handleSave} disabled={saving}>
          <Save className="h-icon-xs w-icon-xs mr-sm" />
          {saving ? 'Saving...' : 'Save Changes'}
        </Button>
      </div>
    </div>
  );
}

