'use client';

import { Camera, Save, User, Plus, X, Eye, EyeOff } from "lucide-react";
import { useState } from 'react';
import { 
 Button, 
 Input, 
 Textarea, 
 Select,
 SelectContent,
 SelectItem,
 SelectTrigger,
 SelectValue,
 Card, 
 Avatar, 
 Badge,
 Skeleton
} from '@ghxstship/ui';
import type { UserProfile, FieldConfig } from '../types';
import { PROFILE_FIELD_CONFIG } from '../types';

interface ProfileFormViewProps {
 profile: UserProfile | null;
 loading: boolean;
 saving: boolean;
 onSave: (data: Partial<UserProfile>) => Promise<void>;
 onFieldVisibilityChange?: (fieldKey: string, visible: boolean) => void;
 visibleFields?: Set<string>;
 editableFields?: Set<string>;
}

export default function ProfileFormView({
 profile,
 loading,
 saving,
 onSave,
 onFieldVisibilityChange,
 visibleFields = new Set(PROFILE_FIELD_CONFIG.map(f => f.key)),
 editableFields = new Set(PROFILE_FIELD_CONFIG.map(f => f.key))
}: ProfileFormViewProps) {
 const [formData, setFormData] = useState<Partial<UserProfile>(profile || {});
 const [newTag, setNewTag] = useState('');
 const [activeTagField, setActiveTagField] = useState<string | null>(null);

 const handleInputChange = (field: keyof UserProfile, value: unknown) => {
 setFormData(prev => ({ ...prev, [field]: value }));
 };

 const handleTagAdd = (field: 'languages' | 'skills', tag: string) => {
 if (!tag.trim()) return;

 const currentTags = (formData[field] as string[]) || [];
 if (!currentTags.includes(tag.trim())) {
 handleInputChange(field, [...currentTags, tag.trim()]);
 }
 setNewTag('');
 setActiveTagField(null);
 };

 const handleTagRemove = (field: 'languages' | 'skills', tag: string) => {
 const currentTags = (formData[field] as string[]) || [];
 handleInputChange(field, currentTags.filter(t => t !== tag));
 };

 const handleSubmit = async (e: React.FormEvent) => {
 e.preventDefault();
 await onSave(formData);
 };

 const renderField = (fieldConfig: FieldConfig) => {
 if (!visibleFields.has(fieldConfig.key)) return null;

 const isEditable = editableFields.has(fieldConfig.key);
 const value = formData[fieldConfig.key];

 const fieldWrapper = (content: React.ReactNode) => (
 <div key={fieldConfig.key} className="space-y-xs">
 <div className="flex items-center justify-between">
 <label className="text-sm font-medium">
 {fieldConfig.label}
 {fieldConfig.required && <span className="text-red-500 ml-1">*</span>}
 </label>
 {onFieldVisibilityChange && (
 <Button
 variant="ghost"
 size="sm"
 onClick={() => onFieldVisibilityChange(fieldConfig.key, false)}
 className="h-icon-md w-icon-md p-0"
 >
 <EyeOff className="h-3 w-3" />
 </Button>
 )}
 </div>
 {content}
 </div>
 );

 if (!isEditable) {
 return fieldWrapper(
 <div className="px-sm py-xs bg-muted/50 rounded-md text-sm">
 {Array.isArray(value) ? value.join(', ') : value || 'Not specified'}
 </div>
 );
 }

 switch (fieldConfig.type) {
 case 'select':
 return fieldWrapper(
 <Select
 value={e.target.value as string || ''}
 onValueChange={(newValue) => handleInputChange(fieldConfig.key, newValue)}
 >
 <SelectTrigger>
 <SelectValue placeholder={`Select ${fieldConfig.label.toLowerCase()}`} />
 </SelectTrigger>
 <SelectContent>
 {fieldConfig.options?.map(option => (
 <SelectItem key={option.value} value={option.value}>
 {option.label}
 </SelectItem>
 ))}
 </SelectContent>
 </Select>
 );

 case 'textarea':
 return fieldWrapper(
 <Textarea
 value={e.target.value as string || ''}
 onChange={(e) => handleInputChange(fieldConfig.key, e.target.value)}
 placeholder={fieldConfig.placeholder}
 maxLength={fieldConfig.validation?.maxLength}
 rows={3}
 />
 );

 case 'tags':
 const tags = (e.target.value as string[]) || [];
 return fieldWrapper(
 <div className="space-y-xs">
 <div className="flex flex-wrap gap-xs">
 {tags.map(tag => (
 <Badge
 key={tag}
 variant="secondary"
 className="cursor-pointer hover:bg-destructive hover:text-destructive-foreground"
 onClick={() => handleTagRemove(fieldConfig.key as 'languages' | 'skills', tag)}
 >
 {tag}
 <X className="ml-1 h-3 w-3" />
 </Badge>
 ))}
 </div>
 {activeTagField === fieldConfig.key ? (
 <div className="flex gap-xs">
 <Input
 value={newTag}
 onChange={(e) => setNewTag(e.target.value)}
 placeholder={`Add ${fieldConfig.label.toLowerCase()}`}
 onKeyPress={(e) => {
 if (e.key === 'Enter') {
 e.preventDefault();
 handleTagAdd(fieldConfig.key as 'languages' | 'skills', newTag);
 }
 }}
 autoFocus
 />
 <Button
 type="button"
 variant="outline"
 onClick={() => handleTagAdd(fieldConfig.key as 'languages' | 'skills', newTag)}
 >
 Add
 </Button>
 <Button
 type="button"
 variant="ghost"
 onClick={() => {
 setActiveTagField(null);
 setNewTag('');
 }}
 >
 Cancel
 </Button>
 </div>
 ) : (
 <Button
 type="button"
 variant="outline"
 size="sm"
 onClick={() => setActiveTagField(fieldConfig.key)}
 className="w-fit"
 >
 <Plus className="mr-1 h-3 w-3" />
 Add {fieldConfig.label.slice(0, -1)}
 </Button>
 )}
 </div>
 );

 default:
 return fieldWrapper(
 <Input
 type={fieldConfig.type}
 value={e.target.value as string || ''}
 onChange={(e) => handleInputChange(fieldConfig.key, e.target.value)}
 placeholder={fieldConfig.placeholder}
 minLength={fieldConfig.validation?.minLength}
 maxLength={fieldConfig.validation?.maxLength}
 pattern={fieldConfig.validation?.pattern}
 />
 );
 }
 };

 if (loading) {
 return (
 <div className="space-y-lg">
 <div className="flex flex-col items-center space-y-md">
 <Skeleton className="h-component-lg w-component-lg rounded-full" />
 <Skeleton className="h-icon-xs w-component-xl" />
 </div>
 <div className="grid grid-cols-1 md:grid-cols-2 gap-lg">
 {Array.from({ length: 8 }).map((_, i) => (
 <div key={i} className="space-y-xs">
 <Skeleton className="h-icon-xs w-component-lg" />
 <Skeleton className="h-icon-xl w-full" />
 </div>
 ))}
 </div>
 </div>
 );
 }

 const groupedFields = PROFILE_FIELD_CONFIG.reduce((acc, field) => {
 if (!acc[field.section]) acc[field.section] = [];
 acc[field.section].push(field);
 return acc;
 }, {} as Record<string, FieldConfig[]>);

 return (
 <form onSubmit={handleSubmit} className="space-y-xl">
 {/* Avatar Section */}
 <div className="flex flex-col items-center space-y-md">
 <div className="relative">
 <Avatar className="h-component-lg w-component-lg">
 {formData.avatar_url ? (
 <img 
 src={formData.avatar_url} 
 alt="Profile" 
 className="h-full w-full object-cover" 
 />
 ) : (
 <User className="h-icon-2xl w-icon-2xl" />
 )}
 </Avatar>
 <Button
 type="button"
 variant="outline"
 size="sm"
 className="absolute -bottom-2 -right-2 rounded-full h-icon-lg w-icon-lg p-0"
 >
 <Camera className="h-icon-xs w-icon-xs" />
 </Button>
 </div>

 {profile && (
 <div className="text-center space-y-xs">
 <Badge variant={profile.status === 'active' ? 'default' : 'secondary'}>
 {profile.status}
 </Badge>
 <div className="text-sm text-muted-foreground">
 Profile {profile.profile_completion_percentage}% complete
 </div>
 </div>
 )}
 </div>

 {/* Form Sections */}
 {Object.entries(groupedFields).map(([section, fields]) => (
 <Card key={section} className="p-lg">
 <h3 className="text-lg font-semibold mb-4 capitalize">
 {section === 'basic' ? 'Basic Information' : 
 section === 'contact' ? 'Contact Information' : 
 section === 'professional' ? 'Professional Information' : 
 'Metadata'}
 </h3>
 <div className="grid grid-cols-1 md:grid-cols-2 gap-lg">
 {fields.map(renderField)}
 </div>
 </Card>
 ))}

 {/* Hidden Fields */}
 {onFieldVisibilityChange && (
 <Card className="p-lg">
 <h3 className="text-lg font-semibold mb-4">Hidden Fields</h3>
 <div className="flex flex-wrap gap-xs">
 {PROFILE_FIELD_CONFIG
 .filter(field => !visibleFields.has(field.key))
 .map(field => (
 <Button
 key={field.key}
 type="button"
 variant="outline"
 size="sm"
 onClick={() => onFieldVisibilityChange(field.key, true)}
 className="h-icon-lg"
 >
 <Eye className="mr-1 h-3 w-3" />
 {field.label}
 </Button>
 ))}
 </div>
 </Card>
 )}

 {/* Save Button */}
 <div className="flex justify-end">
 <Button type="submit" disabled={saving}>
 <Save className="mr-2 h-icon-xs w-icon-xs" />
 {saving ? 'Saving...' : 'Save Changes'}
 </Button>
 </div>
 </form>
 );
}

'use client';

import { User, Edit, Eye, Download, ArrowUpDown, MoreHorizontal } from "lucide-react";
import { useMemo } from 'react';
import { 
 Card, 
 Avatar, 
 Badge, 
 Button,
 Skeleton
} from '@ghxstship/ui';
import type { UserProfile, ProfileSort } from '../types';

interface ProfileTableViewProps {
 profiles: UserProfile[];
 loading: boolean;
 selectedItems: string[];
 onSelectItem: (id: string, selected: boolean) => void;
 onSelectAll: (ids: string[], selected: boolean) => void;
 onSort?: (sort: ProfileSort) => void;
 onEdit?: (profile: UserProfile) => void;
 onView?: (profile: UserProfile) => void;
 onExport?: (profile: UserProfile) => void;
 currentSort?: ProfileSort;
}

export default function ProfileTableView({
 profiles,
 loading,
 selectedItems,
 onSelectItem,
 onSelectAll,
 onSort,
 onEdit,
 onView,
 onExport,
 currentSort
}: ProfileTableViewProps) {
 const allSelected = profiles.length > 0 && profiles.every(profile => selectedItems.includes(profile.id));
 const someSelected = profiles.some(profile => selectedItems.includes(profile.id));

 const handleSelectAll = () => {
 const profileIds = profiles.map(profile => profile.id);
 onSelectAll(profileIds, !allSelected);
 };

 const handleSort = (field: keyof UserProfile) => {
 if (!onSort) return;

 const direction = currentSort?.field === field && currentSort?.direction === 'asc' ? 'desc' : 'asc';
 onSort({ field, direction });
 };

 const getSortIcon = (field: keyof UserProfile) => {
 if (currentSort?.field !== field) return <ArrowUpDown className="h-3 w-3 opacity-50" />;
 return <ArrowUpDown className={`h-3 w-3 ${currentSort.direction === 'desc' ? 'rotate-180' : ''}`} />;
 };

 const formatDate = (dateString?: string) => {
 if (!dateString) return 'Not specified';
 return new Date(dateString).toLocaleDateString();
 };

 const getCompletionColor = (percentage: number) => {
 if (percentage >= 80) return 'text-green-600';
 if (percentage >= 60) return 'text-yellow-600';
 return 'text-red-600';
 };

 if (loading) {
 return (
 <Card className="p-lg">
 <div className="space-y-md">
 <Skeleton className="h-icon-lg w-full" />
 {Array.from({ length: 5 }).map((_, i) => (
 <div key={i} className="flex items-center gap-md">
 <Skeleton className="h-icon-xs w-icon-xs" />
 <Skeleton className="h-icon-lg w-icon-lg rounded-full" />
 <Skeleton className="h-icon-xs flex-1" />
 <Skeleton className="h-icon-xs w-component-lg" />
 <Skeleton className="h-icon-xs w-component-md" />
 </div>
 ))}
 </div>
 </Card>
 );
 }

 if (profiles.length === 0) {
 return (
 <Card className="p-xsxl text-center">
 <User className="h-icon-2xl w-icon-2xl text-muted-foreground mx-auto mb-4" />
 <h3 className="text-lg font-semibold mb-2">No Profiles Found</h3>
 <p className="text-muted-foreground">
 No profiles match the current filters.
 </p>
 </Card>
 );
 }

 return (
 <Card className="p-lg">
 <div className="overflow-x-auto">
 <table className="w-full">
 <thead>
 <tr className="border-b">
 <th className="text-left p-xs">
 <input
 type="checkbox"
 checked={allSelected}
 ref={(input) => {
 if (input) input.indeterminate = someSelected && !allSelected;
 }}
 onChange={handleSelectAll}
 className="h-icon-xs w-icon-xs rounded border-border"
 />
 </th>
 <th className="text-left p-xs">Profile</th>
 <th className="text-left p-xs">
 <Button
 variant="ghost"
 size="sm"
 onClick={() => handleSort('job_title')}
 className="h-auto p-0 font-semibold"
 >
 Position
 {getSortIcon('job_title')}
 </Button>
 </th>
 <th className="text-left p-xs">
 <Button
 variant="ghost"
 size="sm"
 onClick={() => handleSort('department')}
 className="h-auto p-0 font-semibold"
 >
 Department
 {getSortIcon('department')}
 </Button>
 </th>
 <th className="text-left p-xs">
 <Button
 variant="ghost"
 size="sm"
 onClick={() => handleSort('employment_type')}
 className="h-auto p-0 font-semibold"
 >
 Type
 {getSortIcon('employment_type')}
 </Button>
 </th>
 <th className="text-left p-xs">
 <Button
 variant="ghost"
 size="sm"
 onClick={() => handleSort('profile_completion_percentage')}
 className="h-auto p-0 font-semibold"
 >
 Completion
 {getSortIcon('profile_completion_percentage')}
 </Button>
 </th>
 <th className="text-left p-xs">
 <Button
 variant="ghost"
 size="sm"
 onClick={() => handleSort('status')}
 className="h-auto p-0 font-semibold"
 >
 Status
 {getSortIcon('status')}
 </Button>
 </th>
 <th className="text-left p-xs">
 <Button
 variant="ghost"
 size="sm"
 onClick={() => handleSort('updated_at')}
 className="h-auto p-0 font-semibold"
 >
 Updated
 {getSortIcon('updated_at')}
 </Button>
 </th>
 <th className="text-left p-xs">Actions</th>
 </tr>
 </thead>
 <tbody>
 {profiles.map((profile) => {
 const isSelected = selectedItems.includes(profile.id);

 return (
 <tr 
 key={profile.id} 
 className={`border-b hover:bg-muted/50 ${
 isSelected ? 'bg-muted/30' : ''
 }`}
 >
 <td className="p-xs">
 <input
 type="checkbox"
 checked={isSelected}
 onChange={(e) => onSelectItem(profile.id, e.target.checked)}
 className="h-icon-xs w-icon-xs rounded border-border"
 />
 </td>

 <td className="p-xs">
 <div className="flex items-center gap-sm">
 <Avatar className="h-icon-lg w-icon-lg">
 {profile.avatar_url ? (
 <img 
 src={profile.avatar_url} 
 alt="Profile" 
 className="h-full w-full object-cover" 
 />
 ) : (
 <User className="h-icon-xs w-icon-xs" />
 )}
 </Avatar>
 <div>
 <div className="font-medium text-sm">
 {profile.employee_id || 'No ID'}
 </div>
 <div className="text-xs text-muted-foreground">
 {profile.nationality || 'Not specified'}
 </div>
 </div>
 </div>
 </td>

 <td className="p-xs">
 <div className="text-sm">
 {profile.job_title || 'No Title'}
 </div>
 </td>

 <td className="p-xs">
 <div className="text-sm">
 {profile.department || 'Unassigned'}
 </div>
 </td>

 <td className="p-xs">
 {profile.employment_type && (
 <Badge variant="outline" className="text-xs">
 {profile.employment_type}
 </Badge>
 )}
 </td>

 <td className="p-xs">
 <div className={`text-sm font-medium ${getCompletionColor(profile.profile_completion_percentage)}`}>
 {profile.profile_completion_percentage}%
 </div>
 <div className="w-component-md bg-muted rounded-full h-1 mt-1">
 <div
 className="bg-primary h-1 rounded-full transition-all"
 style={{ width: `${profile.profile_completion_percentage}%` }}
 />
 </div>
 </td>

 <td className="p-xs">
 <Badge variant={profile.status === 'active' ? 'default' : 'secondary'}>
 {profile.status}
 </Badge>
 </td>

 <td className="p-xs">
 <div className="text-sm text-muted-foreground">
 {formatDate(profile.updated_at)}
 </div>
 </td>

 <td className="p-xs">
 <div className="flex items-center gap-xs">
 {onView && (
 <Button
 variant="ghost"
 size="sm"
 onClick={() => onView(profile)}
 className="h-icon-md w-icon-md p-0"
 >
 <Eye className="h-3 w-3" />
 </Button>
 )}
 {onEdit && (
 <Button
 variant="ghost"
 size="sm"
 onClick={() => onEdit(profile)}
 className="h-icon-md w-icon-md p-0"
 >
 <Edit className="h-3 w-3" />
 </Button>
 )}
 {onExport && (
 <Button
 variant="ghost"
 size="sm"
 onClick={() => onExport(profile)}
 className="h-icon-md w-icon-md p-0"
 >
 <Download className="h-3 w-3" />
 </Button>
 )}
 </div>
 </td>
 </tr>
 );
 })}
 </tbody>
 </table>
 </div>
 </Card>
 );
}

'use client';


import { useState, useEffect } from 'react';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import {
  Badge,
  Button,
  Card,
  Input,
  UnifiedInput
} from "@ghxstship/ui";
import { Award, Calendar, ExternalLink, ExternalTrash2, Plus, Save, Trash2 } from 'lucide-react';

interface Certification {
  id: string;
  name: string;
  issuing_organization: string;
  certification_number?: string;
  issue_date?: string;
  expiry_date?: string;
  status: 'active' | 'expired' | 'suspended' | 'revoked';
  verification_url?: string;
  attachment_url?: string;
  notes?: string;
  created_at: string;
  updated_at: string;
}

export default function CertificationsClient({ orgId, userId }: { orgId: string; userId: string }) {
  const t = useTranslations('profile');
  const sb = createBrowserClient();
  const [certifications, setCertifications] = useState<Certification[]>([]);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [showAddForm, setShowAddForm] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    issuing_organization: '',
    certification_number: '',
    issue_date: '',
    expiry_date: '',
    verification_url: '',
    attachment_url: '',
    notes: ''
  });

  useEffect(() => {
    loadCertifications();
  }, [orgId, userId]);

  const loadCertifications = async () => {
    try {
      setLoading(true);

      const { data: userData } = await sb
        .from('users')
        .select('id')
        .eq('auth_id', userId)
        .single();

      if (userData) {
        const { data: certificationsData } = await sb
          .from('user_certifications')
          .select('*')
          .eq('user_id', userData.id)
          .eq('organization_id', orgId)
          .order('created_at', { ascending: false });

        setCertifications(certificationsData || []);
      }
    } catch (error) {
      console.error('Error loading certifications:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAdd = async () => {
    try {
      setSaving(true);

      const { data: userData } = await sb
        .from('users')
        .select('id')
        .eq('auth_id', userId)
        .single();

      if (userData) {
        const { error } = await sb
          .from('user_certifications')
          .insert({
            ...formData,
            user_id: userData.id,
            organization_id: orgId,
            status: 'active'
          });

        if (error) throw error;

        setFormData({
          name: '',
          issuing_organization: '',
          certification_number: '',
          issue_date: '',
          expiry_date: '',
          verification_url: '',
          attachment_url: '',
          notes: ''
        });
        setShowAddForm(false);
        await loadCertifications();
      }
    } catch (error) {
      console.error('Error adding certification:', error);
    } finally {
      setSaving(false);
    }
  };

  const handleDelete = async (certificationId: string) => {
    try {
      const { error } = await sb
        .from('user_certifications')
        .delete()
        .eq('id', certificationId);

      if (error) throw error;
      await loadCertifications();
    } catch (error) {
      console.error('Error deleting certification:', error);
    }
  };

  const getStatusColor = (status: string, expiryDate?: string) => {
    if (status === 'expired') return 'destructive';
    if (expiryDate) {
      const expiry = new Date(expiryDate);
      const now = new Date();
      const thirtyDaysFromNow = new Date();
      thirtyDaysFromNow.setDate(now.getDate() + 30);

      if (expiry < now) return 'destructive';
      if (expiry <= thirtyDaysFromNow) return 'warning';
    }
    return 'default';
  };

  const getStatusText = (status: string, expiryDate?: string) => {
    if (status === 'expired') return 'Expired';
    if (expiryDate) {
      const expiry = new Date(expiryDate);
      const now = new Date();
      const thirtyDaysFromNow = new Date();
      thirtyDaysFromNow.setDate(now.getDate() + 30);

      if (expiry < now) return 'Expired';
      if (expiry <= thirtyDaysFromNow) return 'Expiring Soon';
    }
    return 'Active';
  };

  if (loading) {
    return (
      <div className="stack-md">
        <div className="animate-pulse stack-md">
          <div className="h-icon-xs bg-secondary rounded w-3/4"></div>
          <div className="h-icon-xs bg-secondary rounded w-1/2"></div>
          <div className="h-icon-xs bg-secondary rounded w-2/3"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="stack-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-sm">
          <Award className="h-icon-sm w-icon-sm" />
          <h3 className="text-body text-heading-4">Certifications</h3>
        </div>
        <Button onClick={() => setShowAddForm(true)}>
          <Plus className="h-icon-xs w-icon-xs mr-sm" />
          Add Certification
        </Button>
      </div>

      {/* Add Form */}
      {showAddForm && (
        <Card>
          <div className="p-md">
            <h4 className="form-label mb-md">Add New Certification</h4>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-md mb-md">
              <div className="stack-sm">
                <label className="text-body-sm form-label">Certification Name *</label>
                <Input                   value={formData.name}
                  onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                  placeholder="AWS Solutions Architect"
                />
              </div>

              <div className="stack-sm">
                <label className="text-body-sm form-label">Issuing Organization *</label>
                <Input                   value={formData.issuing_organization}
                  onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData(prev => ({ ...prev, issuing_organization: e.target.value }))}
                  placeholder="Amazon Web Services"
                />
              </div>

              <div className="stack-sm">
                <label className="text-body-sm form-label">Certification Number</label>
                <Input                   value={formData.certification_number}
                  onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData(prev => ({ ...prev, certification_number: e.target.value }))}
                  placeholder="AWS-SAA-123456"
                />
              </div>

              <div className="stack-sm">
                <label className="text-body-sm form-label">Issue Date</label>
                <Input                   type="date"
                  value={formData.issue_date}
                  onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData(prev => ({ ...prev, issue_date: e.target.value }))}
                />
              </div>

              <div className="stack-sm">
                <label className="text-body-sm form-label">Expiry Date</label>
                <Input                   type="date"
                  value={formData.expiry_date}
                  onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData(prev => ({ ...prev, expiry_date: e.target.value }))}
                />
              </div>

              <div className="stack-sm">
                <label className="text-body-sm form-label">Verification URL</label>
                <Input                   value={formData.verification_url}
                  onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData(prev => ({ ...prev, verification_url: e.target.value }))}
                  placeholder="https://verify.aws.com/..."
                />
              </div>
            </div>

            <div className="stack-sm mb-md">
              <label className="text-body-sm form-label">Notes</label>
              <textarea
                value={formData.notes}
                onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData(prev => ({ ...prev, notes: e.target.value }))}
                className="w-full  px-md py-sm border border-input bg-background rounded-md"
                rows={3}
                placeholder="Additional notes about this certification..."
              />
            </div>

            <div className="flex gap-sm">
              <Button onClick={handleAdd} disabled={saving || !formData.name || !formData.issuing_organization}>
                <Save className="h-icon-xs w-icon-xs mr-sm" />
                {saving ? 'Adding...' : 'Add Certification'}
              </Button>
              <Button variant="outline" onClick={() => setShowAddForm(false)}>
                Cancel
              </Button>
            </div>
          </div>
        </Card>
      )}

      {/* Certifications List */}
      <div className="stack-md">
        {certifications.length === 0 ? (
          <Card>
            <div className="p-xl text-center color-muted">
              <Award className="h-icon-2xl w-icon-2xl mx-auto mb-md opacity-50" />
              <p>No certifications added yet.</p>
              <p className="text-body-sm">Add your professional certifications to showcase your expertise.</p>
            </div>
          </Card>
        ) : (
          certifications.map((cert: any) => (
            <Card key={cert.id}>
              <div className="p-md">
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-center gap-sm mb-sm">
                      <h4 className="form-label">{cert.name}</h4>
                      <Badge variant={getStatusColor(cert.status, cert.expiry_date)}>
                        {getStatusText(cert.status, cert.expiry_date)}
                      </Badge>
                    </div>

                    <p className="text-body-sm color-muted mb-sm">
                      Issued by {cert.issuing_organization}
                    </p>

                    {cert.certification_number && (
                      <p className="text-body-sm">
                        <span className="form-label">Number:</span> {cert.certification_number}
                      </p>
                    )}

                    <div className="flex items-center gap-md text-body-sm color-muted mt-sm">
                      {cert.issue_date && (
                        <div className="flex items-center gap-xs">
                          <Calendar className="h-3 w-3" />
                          Issued: {new Date(cert.issue_date).toLocaleDateString()}
                        </div>
                      )}
                      {cert.expiry_date && (
                        <div className="flex items-center gap-xs">
                          <Calendar className="h-3 w-3" />
                          Expires: {new Date(cert.expiry_date).toLocaleDateString()}
                        </div>
                      )}
                    </div>

                    {cert.notes && (
                      <p className="text-body-sm mt-sm color-muted">{cert.notes}</p>
                    )}
                  </div>

                  <div className="flex items-center gap-sm">
                    {cert.verification_url && (
                      <Button

                        variant="outline"
                        onClick={() => window.open(cert.verification_url, '_blank')}
                      >
                        <ExternalLink className="h-3 w-3" />
                      </Button>
                    )}
                    <Button

                      variant="outline"
                      onClick={() => handleDelete(cert.id)}
                    >
                      <Trash2 className="h-3 w-3" />
                    </Button>
                  </div>
                </div>
              </div>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

'use client';

import { FileText, CreditCard, List, Map, BarChart3, RefreshCw, ShieldCheck, AlertTriangle, type LucideIcon } from "lucide-react";
import { useState, useEffect, useCallback, useMemo } from 'react';
import {
  Badge,
  Button,
  Card,
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger
} from "@ghxstship/ui";
import type { 
 ContactInfo,
 ContactFilters,
 ContactSort,
 ContactStats,
 ContactAnalytics,
 ViewType,
 ContactFormData
} from './types';
import {
 VIEW_CONFIG,
 QUICK_FILTERS,
 createEmptyContact,
 createEmptyContactStats,
 createEmptyContactAnalytics,
 validateContactForm
} from './types';
import ContactFormView from './views/ContactFormView';
import ContactCardView from './views/ContactCardView';
import ContactListView from './views/ContactListView';
import ContactMapView from './views/ContactMapView';
import ContactAnalyticsView from './views/ContactAnalyticsView';

const DEFAULT_FILTERS: ContactFilters = {
 search: '',
 verification_status: 'all',
 preferred_contact_method: 'all',
 has_emergency_contact: false
};

const DEFAULT_SORT: ContactSort = {
 field: 'phone_primary',
 direction: 'asc'
};

interface ContactClientProps {
 orgId: string;
 userId: string;
}

export default function ContactClient({ orgId, userId }: ContactClientProps) {
 const [contact, setContact] = useState<ContactInfo | null>(null);
 const [contactsList, setContactsList] = useState<ContactInfo[]>([]);
 const [stats, setStats] = useState<ContactStats>(createEmptyContactStats());
 const [analytics, setAnalytics] = useState<ContactAnalytics>(createEmptyContactAnalytics());
 const [formData, setFormData] = useState<ContactFormData>(createEmptyContact());
 const [formErrors, setFormErrors] = useState<Record<string, string>({});
 const [filters, setFilters] = useState<ContactFilters>(DEFAULT_FILTERS);
 const [sort, setSort] = useState<ContactSort>(DEFAULT_SORT);
 const [view, setView] = useState<ViewType>('form');
 const [selectedItems, setSelectedItems] = useState<string[]>([]);
 const [loading, setLoading] = useState(true);
 const [saving, setSaving] = useState(false);
 const [analyticsLoading, setAnalyticsLoading] = useState(false);
 const [selectedContactId, setSelectedContactId] = useState<string | null>(null);

 const fetchContact = useCallback(async () => {
 try {
 setLoading(true);
 const response = await fetch(`/api/v1/profile/contact?user_id=${userId}`);
 if (!response.ok) throw new Error('Failed to fetch contact');
 const data = await response.json();
 setContact(data);
 setFormData({
 phone_primary: data.phone_primary || '',
 phone_secondary: data.phone_secondary || '',
 phone_mobile: data.phone_mobile || '',
 phone_work: data.phone_work || '',
 phone_extension: data.phone_extension || '',
 address_line1: data.address_line1 || '',
 address_line2: data.address_line2 || '',
 city: data.city || '',
 state_province: data.state_province || '',
 postal_code: data.postal_code || '',
 country: data.country || '',
 billing_address_line1: data.billing_address_line1 || '',
 billing_address_line2: data.billing_address_line2 || '',
 billing_city: data.billing_city || '',
 billing_state_province: data.billing_state_province || '',
 billing_postal_code: data.billing_postal_code || '',
 billing_country: data.billing_country || '',
 billing_same_as_primary: data.billing_same_as_primary || false,
 emergency_contact_name: data.emergency_contact_name || '',
 emergency_contact_relationship: data.emergency_contact_relationship || '',
 emergency_contact_phone: data.emergency_contact_phone || '',
 emergency_contact_email: data.emergency_contact_email || '',
 timezone: data.timezone || '',
 preferred_contact_method: data.preferred_contact_method || 'email',
 do_not_contact: data.do_not_contact || false,
 contact_notes: data.contact_notes || ''
 });
 } catch (error) {
 console.error('Error fetching contact:', error);
 setContact(null);
 } finally {
 setLoading(false);
 }
 }, [userId]);

 const fetchContactsList = useCallback(async () => {
 try {
 const params = new URLSearchParams();
 params.append('view_all', 'true');
 Object.entries(filters).forEach(([key, value]) => {
 if (value !== undefined && value !== null) {
 params.append(key, String(value));
 }
 });

 const response = await fetch(`/api/v1/profile/contact?${params.toString()}`);
 if (!response.ok) {
 if (response.status === 403) {
 setContactsList(prev => (contact ? [contact] : prev));
 setStats(createEmptyContactStats());
 return;
 }
 throw new Error('Failed to fetch contacts list');
 }
 const data = await response.json();
 setContactsList(data.contacts || []);
 setStats(data.stats || createEmptyContactStats());
 } catch (error) {
 console.error('Error fetching contacts list:', error);
 setContactsList(contact ? [contact] : []);
 setStats(createEmptyContactStats());
 }
 }, [filters, contact]);

 const fetchAnalytics = useCallback(async () => {
 try {
 setAnalyticsLoading(true);
 const response = await fetch(`/api/v1/profile/contact/analytics`);
 if (!response.ok) throw new Error('Failed to fetch analytics');
 const data = await response.json();
 setAnalytics(data);
 } catch (error) {
 console.error('Error fetching contact analytics:', error);
 setAnalytics(createEmptyContactAnalytics());
 } finally {
 setAnalyticsLoading(false);
 }
 }, []);

 useEffect(() => {
 fetchContact();
 }, [fetchContact]);

 useEffect(() => {
 fetchContactsList();
 }, [fetchContactsList]);

 useEffect(() => {
 if (view === 'analytics') {
 fetchAnalytics();
 }
 }, [view, fetchAnalytics]);

 const handleFieldChange = useCallback((field: keyof ContactFormData, value: unknown) => {
 setFormData(prev => ({
 ...prev,
 [field]: value
 }));
 setFormErrors(prev => {
 if (!prev[field]) return prev;
 const next = { ...prev };
 delete next[field];
 return next;
 });
 }, []);

 const handleSave = useCallback(async () => {
 const errors = validateContactForm(formData);
 if (Object.keys(errors).length > 0) {
 setFormErrors(errors);
 return;
 }

 try {
 setSaving(true);
 const response = await fetch(`/api/v1/profile/contact?user_id=${userId}`, {
 method: 'PUT',
 headers: { 'Content-Type': 'application/json' },
 body: JSON.stringify(formData)
 });

 if (!response.ok) throw new Error('Failed to save contact');

 await Promise.all([
 fetchContact(),
 fetchContactsList(),
 view === 'analytics' ? fetchAnalytics() : Promise.resolve(),
 ]);
 } catch (error) {
 console.error('Error saving contact:', error);
 } finally {
 setSaving(false);
 }
 }, [formData, fetchContact, fetchContactsList, fetchAnalytics, userId, view]);

 const handleVerify = useCallback(async () => {
 try {
 const response = await fetch(`/api/v1/profile/contact?user_id=${userId}&action=verify`, {
 method: 'POST'
 });
 if (!response.ok) throw new Error('Failed to verify contact');
 await Promise.all([
 fetchContact(),
 fetchContactsList(),
 ]);
 } catch (error) {
 console.error('Error verifying contact:', error);
 }
 }, [fetchContact, fetchContactsList, userId]);

 const handleSelectContact = useCallback((contact: ContactInfo) => {
 setSelectedContactId(contact.id);
 setContact(contact);
 setFormData({
 phone_primary: contact.phone_primary || '',
 phone_secondary: contact.phone_secondary || '',
 phone_mobile: contact.phone_mobile || '',
 phone_work: contact.phone_work || '',
 phone_extension: contact.phone_extension || '',
 address_line1: contact.address_line1 || '',
 address_line2: contact.address_line2 || '',
 city: contact.city || '',
 state_province: contact.state_province || '',
 postal_code: contact.postal_code || '',
 country: contact.country || '',
 billing_address_line1: contact.billing_address_line1 || '',
 billing_address_line2: contact.billing_address_line2 || '',
 billing_city: contact.billing_city || '',
 billing_state_province: contact.billing_state_province || '',
 billing_postal_code: contact.billing_postal_code || '',
 billing_country: contact.billing_country || '',
 billing_same_as_primary: contact.billing_same_as_primary || false,
 emergency_contact_name: contact.emergency_contact_name || '',
 emergency_contact_relationship: contact.emergency_contact_relationship || '',
 emergency_contact_phone: contact.emergency_contact_phone || '',
 emergency_contact_email: contact.emergency_contact_email || '',
 timezone: contact.timezone || '',
 preferred_contact_method: contact.preferred_contact_method || 'email',
 do_not_contact: contact.do_not_contact || false,
 contact_notes: contact.contact_notes || ''
 });
 }, []);

 const handleSelectItem = useCallback((id: string, selected: boolean) => {
 setSelectedItems(prev => {
 if (selected) {
 return prev.includes(id) ? prev : [...prev, id];
 }
 return prev.filter(itemId => itemId !== id);
 });
 }, []);

 const handleSelectAll = useCallback((ids: string[], selected: boolean) => {
 setSelectedItems(selected ? Array.from(new Set([...selectedItems, ...ids])) : []);
 }, [selectedItems]);

 const iconMap: Record<ViewType, LucideIcon> = useMemo(() => ({
 form: FileText,
 card: CreditCard,
 list: List,
 map: Map,
 analytics: BarChart3
 }), []);

 const selectedContact = selectedContactId
 ? contactsList.find(c => c.id === selectedContactId) || contact
 : contact;

 return (
 <div className="space-y-lg">
 <Card className="p-md flex items-center justify-between">
 <div className="space-y-xs">
 <div className="flex items-center gap-xs">
 <Badge variant="outline">Contact</Badge>
 <span className="text-sm text-muted-foreground">
 {contact?.verification_status === 'verified' ? (
 <span className="text-green-600 flex items-center gap-xs">
 <ShieldCheck className="h-icon-xs w-icon-xs" /> Contact verified
 </span>
 ) : (
 <span className="text-yellow-600 flex items-center gap-xs">
 <AlertTriangle className="h-icon-xs w-icon-xs" /> Verification pending
 </span>
 )}
 </span>
 </div>
 <h1 className="text-2xl font-semibold">
 Contact Information
 </h1>
 </div>
 <Button variant="outline" size="sm" onClick={() => {
 fetchContact();
 fetchContactsList();
 if (view === 'analytics') fetchAnalytics();
 }}>
 <RefreshCw className="mr-2 h-icon-xs w-icon-xs" />
 Refresh
 </Button>
 </Card>

 <Tabs value={view} onChange={(e) => setView(e.target.value as ViewType)}>
 <TabsList className="grid grid-cols-5 w-full">
 {Object.entries(VIEW_CONFIG).map(([key, { label, icon }]) => {
 const Icon = iconMap[key as ViewType] ?? FileText;
 return (
 <TabsTrigger key={key} value={key} className="flex items-center gap-xs">
 <Icon className="h-icon-xs w-icon-xs" />
 {label}
 </TabsTrigger>
 );
 })}
 </TabsList>

 <TabsContent value="form">
 <ContactFormView
 contact={selectedContact}
 formData={formData}
 formErrors={formErrors}
 loading={loading}
 saving={saving}
 onFieldChange={handleFieldChange}
 onSave={handleSave}
 onVerify={handleVerify}
 />
 </TabsContent>

 <TabsContent value="card">
 <ContactCardView
 contact={selectedContact}
 loading={loading}
 onEdit={() => setView('form')}
 onVerify={contact?.verification_status !== 'verified' ? handleVerify : undefined}
 />
 </TabsContent>

 <TabsContent value="list">
 <ContactListView
 contacts={contactsList}
 loading={loading}
 selectedItems={selectedItems}
 filters={filters}
 sort={sort}
 onSelectItem={handleSelectItem}
 onSelectAll={handleSelectAll}
 onChangeFilters={(newFilters) => {
 setFilters(prev => ({ ...prev, ...newFilters }));
 }}
 onChangeSort={setSort}
 onExport={(contact) => {
 const contactsToExport = contact ? [contact] : contactsList.filter(c => selectedItems.includes(c.id));
 const rows = [
 ['Name', 'Primary Phone', 'Preferred Method', 'City', 'State', 'Country'],
 ...contactsToExport.map(item => [
 item.emergency_contact_name || '',
 item.phone_primary || '',
 item.preferred_contact_method || '',
 item.city || '',
 item.state_province || '',
 item.country || '',
 ]),
 ];
 const csvContent = rows.map(row => row.map(cell => `"${cell}"`).join(',')).join('\n');
 const blob = new Blob([csvContent], { type: 'text/csv' });
 const url = URL.createObjectURL(blob);
 const link = document.createElement('a');
 link.href = url;
 link.download = contact ? `contact-${contact.id}.csv` : `contacts-${new Date().toISOString().split('T')[0]}.csv`;
 link.click();
 URL.revokeObjectURL(url);
 }}
 onViewContact={handleSelectContact}
 />
 </TabsContent>

 <TabsContent value="map">
 <ContactMapView
 contacts={contactsList}
 loading={loading}
 selectedContactId={selectedContactId}
 onSelectContact={handleSelectContact}
 />
 </TabsContent>

 <TabsContent value="analytics">
 <ContactAnalyticsView
 stats={stats}
 analytics={analytics}
 loading={loading}
 analyticsLoading={analyticsLoading}
 />
 </TabsContent>
 </Tabs>
 </div>
 );
}

'use client';


import { useState, useEffect } from 'react';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import {
  Button,
  Card,
  Input,
  UnifiedInput
} from "@ghxstship/ui";
import { Save, Phone, Mail, MapPin } from 'lucide-react';

interface UserProfile {
  id: string;
  phone_primary?: string;
  phone_secondary?: string;
  address_line1?: string;
  address_line2?: string;
  city?: string;
  state_province?: string;
  postal_code?: string;
  country?: string;
}

export default function ContactInfoClient({ orgId, userId }: { orgId: string; userId: string }) {
  const t = useTranslations('profile');
  const sb = createBrowserClient();
  const [profile, setProfile] = useState<UserProfile | null>(null);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [formData, setFormData] = useState({
    phone_primary: '',
    phone_secondary: '',
    address_line1: '',
    address_line2: '',
    city: '',
    state_province: '',
    postal_code: '',
    country: ''
  });

  useEffect(() => {
    loadProfile();
  }, [orgId, userId]);

  const loadProfile = async () => {
    try {
      setLoading(true);

      const { data: userData } = await sb
        .from('users')
        .select('id')
        .eq('auth_id', userId)
        .single();

      if (userData) {
        const { data: profileData } = await sb
          .from('user_profiles')
          .select('*')
          .eq('user_id', userData.id)
          .eq('organization_id', orgId)
          .single();

        if (profileData) {
          setProfile(profileData);
          setFormData({
            phone_primary: profileData.phone_primary || '',
            phone_secondary: profileData.phone_secondary || '',
            address_line1: profileData.address_line1 || '',
            address_line2: profileData.address_line2 || '',
            city: profileData.city || '',
            state_province: profileData.state_province || '',
            postal_code: profileData.postal_code || '',
            country: profileData.country || ''
          });
        }
      }
    } catch (error) {
      console.error('Error loading profile:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async () => {
    if (!profile) return;

    try {
      setSaving(true);

      const { error } = await sb
        .from('user_profiles')
        .update({
          ...formData,
          updated_at: new Date().toISOString()
        })
        .eq('id', profile.id);

      if (error) throw error;

      await loadProfile();
    } catch (error) {
      console.error('Error saving contact info:', error);
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <div className="stack-lg">
        <div className="animate-pulse stack-md">
          <div className="h-icon-xs bg-secondary rounded w-3/4"></div>
          <div className="h-icon-xs bg-secondary rounded w-1/2"></div>
          <div className="h-icon-xs bg-secondary rounded w-2/3"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="stack-lg">
      {/* Phone Numbers */}
      <Card>
        <div className="p-md">
          <div className="flex items-center gap-sm mb-md">
            <Phone className="h-icon-sm w-icon-sm" />
            <h3 className="text-body text-heading-4">Phone Numbers</h3>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
            <div className="stack-sm">
              <label className="text-body-sm form-label">Primary Phone</label>
              <Input                 value={formData.phone_primary}
                onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData(prev => ({ ...prev, phone_primary: e.target.value }))}
                placeholder="+1 (555) 123-4567"
              />
            </div>

            <div className="stack-sm">
              <label className="text-body-sm form-label">Secondary Phone</label>
              <Input                 value={formData.phone_secondary}
                onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData(prev => ({ ...prev, phone_secondary: e.target.value }))}
                placeholder="+1 (555) 987-6543"
              />
            </div>
          </div>
        </div>
      </Card>

      {/* Address */}
      <Card>
        <div className="p-md">
          <div className="flex items-center gap-sm mb-md">
            <MapPin className="h-icon-sm w-icon-sm" />
            <h3 className="text-body text-heading-4">Address</h3>
          </div>

          <div className="stack-md">
            <div className="stack-sm">
              <label className="text-body-sm form-label">Address Line 1</label>
              <Input                 value={formData.address_line1}
                onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData(prev => ({ ...prev, address_line1: e.target.value }))}
                placeholder="123 Main Street"
              />
            </div>

            <div className="stack-sm">
              <label className="text-body-sm form-label">Address Line 2</label>
              <Input                 value={formData.address_line2}
                onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData(prev => ({ ...prev, address_line2: e.target.value }))}
                placeholder="Apt 4B, Suite 200"
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-md">
              <div className="stack-sm">
                <label className="text-body-sm form-label">City</label>
                <Input                   value={formData.city}
                  onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData(prev => ({ ...prev, city: e.target.value }))}
                  placeholder="San Francisco"
                />
              </div>

              <div className="stack-sm">
                <label className="text-body-sm form-label">State/Province</label>
                <Input                   value={formData.state_province}
                  onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData(prev => ({ ...prev, state_province: e.target.value }))}
                  placeholder="California"
                />
              </div>

              <div className="stack-sm">
                <label className="text-body-sm form-label">Postal Code</label>
                <Input                   value={formData.postal_code}
                  onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData(prev => ({ ...prev, postal_code: e.target.value }))}
                  placeholder="94102"
                />
              </div>
            </div>

            <div className="stack-sm">
              <label className="text-body-sm form-label">Country</label>
              <Input                 value={formData.country}
                onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData(prev => ({ ...prev, country: e.target.value }))}
                placeholder="United States"
              />
            </div>
          </div>
        </div>
      </Card>

      {/* Save Button */}
      <div className="flex justify-end">
        <Button onClick={handleSave} disabled={saving}>
          <Save className="h-icon-xs w-icon-xs mr-sm" />
          {saving ? 'Saving...' : 'Save Changes'}
        </Button>
      </div>
    </div>
  );
}

'use client';

import { FileText, Contact2, Users, Table, BarChart3, RefreshCw, ShieldAlert, ShieldCheck, type LucideIcon } from "lucide-react";
import { useCallback, useEffect, useMemo, useState } from 'react';
import {
  Badge,
  Button,
  Card,
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger
} from "@ghxstship/ui";
import type {
 EmergencyContact,
 EmergencyContactFilters,
 EmergencyContactSort,
 EmergencyContactStats,
 EmergencyContactAnalytics,
 EmergencyViewType,
 EmergencyContactFormData
} from './types';
import {
 VIEW_CONFIG,
 QUICK_FILTERS,
 createEmptyFormData,
 createEmptyStats,
 createEmptyAnalytics,
 validateEmergencyForm,
 formatPhone
} from './types';
import EmergencyFormView from './views/EmergencyFormView';
import EmergencyCardView from './views/EmergencyCardView';
import EmergencyRosterView from './views/EmergencyRosterView';
import EmergencyTableView from './views/EmergencyTableView';
import EmergencyAnalyticsView from './views/EmergencyAnalyticsView';

const DEFAULT_FILTERS: EmergencyContactFilters = {
 search: '',
 priority: 'all',
 verification_status: 'all',
 is_primary: 'all',
 availability: 'all'
};

const DEFAULT_SORT: EmergencyContactSort = {
 field: 'priority_level',
 direction: 'asc'
};

interface EmergencyClientProps {
 orgId: string;
 userId: string;
}

interface FetchContactsResponse {
 contacts: EmergencyContact[];
 total: number;
 stats: EmergencyContactStats;
}

function EmergencyClient({ orgId, userId }: EmergencyClientProps) {
 const [contacts, setContacts] = useState<EmergencyContact[]>([]);
 const [stats, setStats] = useState<EmergencyContactStats>(createEmptyStats());
 const [analytics, setAnalytics] = useState<EmergencyContactAnalytics>(createEmptyAnalytics());
 const [selectedContactId, setSelectedContactId] = useState<string | null>(null);
 const [formData, setFormData] = useState<EmergencyContactFormData>(createEmptyFormData());
 const [formErrors, setFormErrors] = useState<Record<string, string>({});
 const [filters, setFilters] = useState<EmergencyContactFilters>(DEFAULT_FILTERS);
 const [sort, setSort] = useState<EmergencyContactSort>(DEFAULT_SORT);
 const [view, setView] = useState<EmergencyViewType>('form');
 const [selectedIds, setSelectedIds] = useState<string[]>([]);
 const [loading, setLoading] = useState(true);
 const [saving, setSaving] = useState(false);
 const [analyticsLoading, setAnalyticsLoading] = useState(false);

 const selectedContact = useMemo(() => {
 if (!selectedContactId) return null;
 return contacts.find(contact => contact.id === selectedContactId) ?? null;
 }, [contacts, selectedContactId]);

 const fetchContacts = useCallback(async () => {
 try {
 setLoading(true);
 const params = new URLSearchParams();
 params.append('view_all', 'true');
 Object.entries(filters).forEach(([key, value]) => {
 if (value !== undefined && value !== null) {
 params.append(key, String(value));
 }
 });

 const response = await fetch(`/api/v1/profile/emergency?${params.toString()}`);
 if (!response.ok) {
 throw new Error('Failed to load emergency contacts');
 }

 const data = await response.json() as FetchContactsResponse;
 const nextContacts = data.contacts ?? [];
 setContacts(nextContacts);
 setStats(data.stats ?? createEmptyStats());

 setSelectedIds(prev => prev.filter(id => nextContacts.some(contact => contact.id === id)));

 if (nextContacts.length === 0) {
 setSelectedContactId(null);
 setFormData(createEmptyFormData());
 return;
 }

 const existingSelection = selectedContactId
 ? nextContacts.find(contact => contact.id === selectedContactId)
 : undefined;

 const nextSelected = existingSelection
 || nextContacts.find(contact => contact.is_primary)
 || nextContacts[0];

 setSelectedContactId(nextSelected.id);
 setFormData(mapContactToForm(nextSelected));
 } catch (error) {
 console.error('Error loading emergency contacts:', error);
 setContacts([]);
 setStats(createEmptyStats());
 setSelectedContactId(null);
 setFormData(createEmptyFormData());
 } finally {
 setLoading(false);
 }
 }, [filters, selectedContactId]);

 const fetchAnalytics = useCallback(async () => {
 try {
 setAnalyticsLoading(true);
 const response = await fetch('/api/v1/profile/emergency/analytics');
 if (!response.ok) {
 throw new Error('Failed to load emergency analytics');
 }
 const data = await response.json() as EmergencyContactAnalytics;
 setAnalytics(data);
 } catch (error) {
 console.error('Error fetching emergency analytics:', error);
 setAnalytics(createEmptyAnalytics());
 } finally {
 setAnalyticsLoading(false);
 }
 }, []);

 useEffect(() => {
 void fetchContacts();
 }, [fetchContacts]);

 useEffect(() => {
 if (view === 'analytics') {
 void fetchAnalytics();
 }
 }, [view, fetchAnalytics]);

 const handleFieldChange = useCallback((field: keyof EmergencyContactFormData, value: EmergencyContactFormData[keyof EmergencyContactFormData]) => {
 setFormData(prev => ({
 ...prev,
 [field]: value
 }));
 setFormErrors(prev => {
 if (!prev[field]) return prev;
 delete next[field];
 return next;
 });
}, [setFormErrors]);

  const handleNewContact = useCallback(() => {
    setSelectedContactId(null);
    setFormData(createEmptyFormData());
    setFormErrors({});
  }, [setSelectedContactId, setFormData, setFormErrors]);

 const handleSave = useCallback(async () => {
 const errors = validateEmergencyForm(formData);
 if (Object.keys(errors).length > 0) {
 setFormErrors(errors);
 return;
 }

 const payload = mapFormToPayload(formData);

 try {
 setSaving(true);
 const endpoint = selectedContact
 ? `/api/v1/profile/emergency?contact_id=${selectedContact.id}`
 : '/api/v1/profile/emergency';

 const response = await fetch(endpoint, {
 method: selectedContact ? 'PUT' : 'POST',
 headers: { 'Content-Type': 'application/json' },
 body: JSON.stringify(payload)
 });

 if (!response.ok) {
 throw new Error('Failed to save emergency contact');
 }

 await Promise.all([
 fetchContacts(),
 view === 'analytics' ? fetchAnalytics() : Promise.resolve(),
 ]);
 } catch (error) {
 console.error('Error saving emergency contact:', error);
 } finally {
 setSaving(false);
 }
}, [fetchAnalytics, fetchContacts, formData, selectedContact, view]);

  const handleVerify = useCallback(async (contactId: string) => {
    try {
      const response = await fetch(`/api/v1/profile/emergency?contact_id=${contactId}&action=verify`, {
        method: 'POST'
      });
      if (!response.ok) {
        throw new Error('Failed to verify emergency contact');
      }
      await fetchContacts();
    } catch (error) {
      console.error('Error verifying emergency contact:', error);
    }
  }, [fetchContacts]);

  const handleVerifyContact = useCallback((contact: EmergencyContact) => {
    void handleVerify(contact.id);
  }, [handleVerify]);

  const handleDelete = useCallback(async (contactId: string) => {
    if (!confirm('Delete this emergency contact?')) {
      return;
    }
    try {
      const response = await fetch(`/api/v1/profile/emergency?contact_id=${contactId}`, {
        method: 'DELETE'
      });
      if (!response.ok) {
        throw new Error('Failed to delete emergency contact');
      }
      await fetchContacts();
    } catch (error) {
      console.error('Error deleting emergency contact:', error);
    }
  }, [fetchContacts]);

 const handleSelectContact = useCallback((contact: EmergencyContact) => {
 setSelectedContactId(contact.id);
 setFormData(mapContactToForm(contact));
 setView('form');
 }, []);

 const handleSelectItem = useCallback((id: string, selected: boolean) => {
 setSelectedIds(prev => {
 if (selected) {
 return prev.includes(id) ? prev : [...prev, id];
 }
 return prev.filter(existing => existing !== id);
 });
 }, []);

 const handleSelectAll = useCallback((ids: string[], selected: boolean) => {
 setSelectedIds(selected ? Array.from(new Set([...selectedIds, ...ids])) : []);
 }, [selectedIds]);

 const handleFiltersChange = useCallback((nextFilters: Partial<EmergencyContactFilters>) => {
 setFilters(prev => ({ ...prev, ...nextFilters }));
 }, []);

 const handleSortChange = useCallback((nextSort: EmergencyContactSort) => {
 setSort(nextSort);
 }, []);

 const handleExport = useCallback((list: EmergencyContact[]) => {
 const exportList = list.length > 0 ? list : contacts.filter(contact => selectedIds.includes(contact.id));
 if (exportList.length === 0) {
 console.warn('No contacts selected for export');
 return;
 }

 const rows = [
 ['Name', 'Relationship', 'Priority', 'Primary Phone', 'Email', 'Address', 'Verification', 'Primary', 'Backup'],
 ...exportList.map(contact => [
 contact.name,
 contact.relationship,
 contact.priority_level,
 formatPhone(contact.phone_primary),
 contact.email ?? '',
 formatAddressString(contact),
 contact.verification_status,
 contact.is_primary ? 'Yes' : 'No',
 contact.is_backup ? 'Yes' : 'No',
 ]),
 ];

 const csv = rows.map(row => row.map(cell => `"${cell.replace(/"/g, '""')}"`).join(',')).join('\n');
 const blob = new Blob([csv], { type: 'text/csv' });
 const url = URL.createObjectURL(blob);
 const link = document.createElement('a');
 link.href = url;
 link.download = `emergency-contacts-${new Date().toISOString().split('T')[0]}.csv`;
 link.click();
 URL.revokeObjectURL(url);
 }, [contacts, selectedIds]);

 const iconMap: Record<EmergencyViewType, LucideIcon> = useMemo(() => ({
 form: FileText,
 card: Contact2,
 roster: Users,
 table: Table,
 analytics: BarChart3
 }), []);

 const quickFilterValue = useMemo(() => {
