'use client';


import { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import {
  Badge,
  Button,
  Card,
  Drawer,
  Input,
  Select,
  UnifiedInput
} from "@ghxstship/ui";
import { Phone, Plus, Edit, Trash2, User, AlertTriangle } from 'lucide-react';

const emergencyContactSchema = z.object({
  name: z.string().min(1, 'Name is required'),
  relationship: z.string().min(1, 'Relationship is required'),
  phone_primary: z.string().min(1, 'Primary phone is required'),
  phone_secondary: z.string().optional(),
  email: z.string().email().optional(),
  address: z.string().optional(),
  is_primary: z.boolean().default(false),
  notes: z.string().optional()
});

type EmergencyContactForm = z.infer<typeof emergencyContactSchema>;

interface EmergencyContact {
  id: string;
  name: string;
  relationship: string;
  phone_primary: string;
  phone_secondary?: string;
  email?: string;
  address?: string;
  is_primary: boolean;
  notes?: string;
  created_at: string;
}

export default function EmergencyContactClient({ orgId, userId }: { orgId: string; userId: string }) {
  const t = useTranslations('profile');
  const sb = createBrowserClient();

  const [contacts, setContacts] = useState<EmergencyContact[]>([]);
  const [loading, setLoading] = useState(true);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [editingContact, setEditingContact] = useState<EmergencyContact | null>(null);
  const [saving, setSaving] = useState(false);

  const form = useForm<EmergencyContactForm>({
    resolver: zodResolver(emergencyContactSchema),
    defaultValues: {
      name: '',
      relationship: '',
      phone_primary: '',
      phone_secondary: '',
      email: '',
      address: '',
      is_primary: false,
      notes: ''
    }
  });

  useEffect(() => {
    loadEmergencyContacts();
  }, [orgId, userId]);

  const loadEmergencyContacts = async () => {
    try {
      setLoading(true);

      const { data, error } = await sb
        .from('emergency_contacts')
        .select('*')
        .eq('organization_id', orgId)
        .eq('user_id', userId)
        .order('is_primary', { ascending: false })
        .order('created_at', { ascending: true });

      if (error) throw error;
      setContacts(data || []);
    } catch (error) {
      console.error('Error loading emergency contacts:', error);
    } finally {
      setLoading(false);
    }
  };

  const openDrawer = (contact?: EmergencyContact) => {
    if (contact) {
      setEditingContact(contact);
      form.reset({
        name: contact.name,
        relationship: contact.relationship,
        phone_primary: contact.phone_primary,
        phone_secondary: contact.phone_secondary || '',
        email: contact.email || '',
        address: contact.address || '',
        is_primary: contact.is_primary,
        notes: contact.notes || ''
      });
    } else {
      setEditingContact(null);
      form.reset();
    }
    setDrawerOpen(true);
  };

  const onSubmit = async (data: EmergencyContactForm) => {
    setSaving(true);
    try {
      if (editingContact) {
        const { error } = await sb
          .from('emergency_contacts')
          .update(data)
          .eq('id', editingContact.id);

        if (error) throw error;
      } else {
        const { error } = await sb
          .from('emergency_contacts')
          .insert({
            ...data,
            user_id: userId,
            organization_id: orgId
          });

        if (error) throw error;
      }

      setDrawerOpen(false);
      loadEmergencyContacts();
    } catch (error) {
      console.error('Error saving emergency contact:', error);
    } finally {
      setSaving(false);
    }
  };

  const deleteContact = async (contactId: string) => {
    if (!confirm('Are you sure you want to delete this emergency contact?')) return;

    try {
      const { error } = await sb
        .from('emergency_contacts')
        .delete()
        .eq('id', contactId);

      if (error) throw error;
      loadEmergencyContacts();
    } catch (error) {
      console.error('Error deleting emergency contact:', error);
    }
  };

  if (loading) {
    return (
      <div className="stack-md">
        {[...Array(2)].map((_, i) => (
          <div key={i} className="animate-pulse">
            <div className="h-component-lg bg-secondary rounded-lg"></div>
          </div>
        ))}
      </div>
    );
  }

  return (
    <div className="stack-lg">
      <div className="flex items-center justify-between">
        <h2 className="text-heading-4 text-heading-4">Emergency Contacts</h2>
        <Button onClick={() => openDrawer()} className="flex items-center gap-sm">
          <Plus className="h-icon-xs w-icon-xs" />
          Add Contact
        </Button>
      </div>

      <div className="stack-md">
        {contacts.length > 0 ? (
          contacts.map((contact: any) => (
            <Card key={contact.id}>
              <div className="p-lg">
                <div className="flex items-start justify-between">
                  <div className="flex items-start gap-md">
                    <div className="p-sm bg-destructive/10 rounded-lg">
                      <AlertTriangle className="h-icon-md w-icon-md color-destructive" />
                    </div>

                    <div>
                      <div className="flex items-center gap-sm mb-sm">
                        <h3 className="text-body text-heading-4">{contact.name}</h3>
                        {contact.is_primary && (
                          <Badge variant="destructive">Primary</Badge>
                        )}
                      </div>

                      <div className="stack-xs text-body-sm color-muted">
                        <div className="flex items-center gap-sm">
                          <User className="h-icon-xs w-icon-xs" />
                          <span>{contact.relationship}</span>
                        </div>

                        <div className="flex items-center gap-sm">
                          <Phone className="h-icon-xs w-icon-xs" />
                          <span>{contact.phone_primary}</span>
                          {contact.phone_secondary && (
                            <span> {contact.phone_secondary}</span>
                          )}
                        </div>

                        {contact.email && (
                          <div className="flex items-center gap-sm">
                            <span></span>
                            <span>{contact.email}</span>
                          </div>
                        )}

                        {contact.address && (
                          <div className="text-body-sm mt-sm">
                            <strong>Address:</strong> {contact.address}
                          </div>
                        )}

                        {contact.notes && (
                          <div className="text-body-sm mt-sm">
                            <strong>Notes:</strong> {contact.notes}
                          </div>
                        )}
                      </div>
                    </div>
                  </div>

                  <div className="flex items-center gap-sm">
                    <Button
                      variant="outline"

                      onClick={() => openDrawer(contact)}
                    >
                      <Edit className="h-icon-xs w-icon-xs" />
                    </Button>
                    <Button
                      variant="outline"

                      onClick={() => deleteContact(contact.id)}
                    >
                      <Trash2 className="h-icon-xs w-icon-xs" />
                    </Button>
                  </div>
                </div>
              </div>
            </Card>
          ))
        ) : (
          <Card>
            <div className="p-xl text-center">
              <AlertTriangle className="h-icon-2xl w-icon-2xl mx-auto mb-md color-muted opacity-50" />
              <h3 className="text-body form-label mb-sm">No Emergency Contacts</h3>
              <p className="color-muted mb-md">
                Add emergency contacts for safety and security purposes.
              </p>
              <Button onClick={() => openDrawer()}>
                <Plus className="h-icon-xs w-icon-xs mr-sm" />
                Add Emergency Contact
              </Button>
            </div>
          </Card>
        )}
      </div>

      <Drawer
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        title={editingContact ? 'Edit Emergency Contact' : 'Add Emergency Contact'}
      >
        <form onSubmit={form.handleSubmit(onSubmit)} className="stack-lg">
          <div className="grid grid-cols-1 gap-md">
            <Input               label="Full Name"
              placeholder="Enter contact's full name"
              {...form.register('name')}
              error={form.formState.errors.name?.message}
              required
            />

            <Input               label="Relationship"
              placeholder="e.g., Spouse, Parent, Sibling, Friend"
              {...form.register('relationship')}
              error={form.formState.errors.relationship?.message}
              required
            />

            <div className="grid grid-cols-2 gap-md">
              <Input                 label="Primary Phone"
                placeholder="Enter primary phone number"
                {...form.register('phone_primary')}
                error={form.formState.errors.phone_primary?.message}
                required
              />

              <Input                 label="Secondary Phone"
                placeholder="Enter secondary phone (optional)"
                {...form.register('phone_secondary')}
                error={form.formState.errors.phone_secondary?.message}
              />
            </div>

            <Input               label="Email"
              type="email"
              placeholder="Enter email address (optional)"
              {...form.register('email')}
              error={form.formState.errors.email?.message}
            />

            <Input               label="Address"
              placeholder="Enter full address (optional)"
              {...form.register('address')}
              error={form.formState.errors.address?.message}
            />

            <Input               label="Notes"
              placeholder="Any additional notes (optional)"
              {...form.register('notes')}
              error={form.formState.errors.notes?.message}
            />

            <div className="flex items-center gap-sm">
              <input
                type="checkbox"
                id="is_primary"
                {...form.register('is_primary')}
                className="rounded border-border"
              />
              <label htmlFor="is_primary" className="text-body-sm form-label">
                Set as primary emergency contact
              </label>
            </div>
          </div>

          <div className="flex justify-end gap-sm pt-md border-t">
            <Button
              type="button"
              variant="outline"
              onClick={() => setDrawerOpen(false)}
              disabled={saving}
            >
              Cancel
            </Button>
            <Button type="submit" loading={saving}>
              {editingContact ? 'Update Contact' : 'Add Contact'}
            </Button>
          </div>
        </form>
      </Drawer>
    </div>
  );
}

'use client';


import React from "react";

import {
  Badge,
  Button,
  Card,
  Input,
  UnifiedInput,
  cn
} from "@ghxstship/ui";
import { Edit } from "lucide-react";
import { useState, useEffect } from 'react'
import { createClient } from "../../../../../lib/supabase/client"

interface ClientProps {
  className?: string
}

export default function Client({ className }: ClientProps) {
  const [data, setData] = useState<any[]>([])
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    let isMounted = true
    const supabase = createClient()
    async function fetchRecords() {
      try {
        setLoading(true)
        const { data: auth } = await supabase.auth.getUser()
        let items: unknown[] = []
        if (auth?.user) {
          const { data: profiles, error } = await supabase
            .from('user_profiles')
            .select('id, full_name, status')
            .eq('id', auth.user.id)
            .limit(1)
          if (!error && profiles) {
            const mockData: Array<{ id: string; full_name: string; status: string }> = [
              { id: '1', full_name: 'John Doe', status: 'Active' },
              { id: '2', full_name: 'Jane Smith', status: 'Active' },
            ]
            items = profiles.map((p: any) => ({
              id: p.id,
              name: p.full_name || 'Profile',
              status: p.status || 'active'
            }))
          }
        }
        if (isMounted) setData(items)
      } catch (err) {
        // console.error('HealthInfoClient fetch error:', err)
        if (isMounted) setData([])
      } finally {
        if (isMounted) setLoading(false)
      }
    }
    fetchRecords()
    return () => { isMounted = false }
  }, [])

  if (loading) {
    return (
      <div className={className}>
        <Card className="p-md">
          <div className="animate-pulse space-y-md">
            <div className="h-icon-xs bg-secondary rounded w-1/4"></div>
            <div className="h-icon-xs bg-secondary rounded w-1/2"></div>
            <div className="h-icon-xs bg-secondary rounded w-3/4"></div>
          </div>
        </Card>
      </div>
    )
  }

  return (
    <div className={className}>
      <div className="mb-md">
        <h1 className="text-heading-lg text-heading-3 mb-sm">Health Information</h1>
        <p className="color-foreground-subtle">View and manage your health-related profile information</p>
      </div>

      <div className="mb-md">
        <Input onChange={(e: React.ChangeEvent<HTMLInputElement>) => {}} placeholder="Search health records..." className="w-full sm:w-auto sm:max-w-sm" aria- />
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-md">
        {data.map((item: any) => (
          <Card key={item.id} className="p-md">
            <div className="flex flex-col sm:flex-row items-center justify-between">
              <div>
                <h2 className="text-body text-heading-3">{item.name}</h2>
                <p className="text-body-sm color-foreground-subtle">ID: {item.id}</p>
              </div>
              <div className="flex flex-col sm:flex-row items-center gap-sm">
                <Badge variant={item.status === 'active' ? 'default' : 'secondary'}>
                  {item.status}
                </Badge>
                <Button variant="default" size="sm" onClick={() => console.log("Edit clicked")}>Edit</Button>
              </div>
            </div>
          </Card>
        ))}
      </div>
    </div>
  )
}

'use client';

import { ChevronLeft, ChevronRight, Calendar, Clock, AlertTriangle, Bell, Activity } from "lucide-react";
import { useState, useMemo } from 'react';
import { Card, Button, Badge } from '@ghxstship/ui';
import type { HealthRecord } from '../types';
import {
 RECORD_TYPE_LABELS,
 formatDateShort,
 getDaysUntilExpiry,
 getExpiryUrgency,
 getRecordTypeIcon
} from '../types';

interface HealthCalendarViewProps {
 records: HealthRecord[];
 loading: boolean;
 onEdit: (record: HealthRecord) => void;
}

export default function HealthCalendarView({
 records,
 loading,
 onEdit
}: HealthCalendarViewProps) {
 const [currentDate, setCurrentDate] = useState(new Date());

 const currentYear = currentDate.getFullYear();
 const currentMonth = currentDate.getMonth();

 // Get first day of month and number of days
 const firstDayOfMonth = new Date(currentYear, currentMonth, 1);
 const lastDayOfMonth = new Date(currentYear, currentMonth + 1, 0);
 const daysInMonth = lastDayOfMonth.getDate();
 const startingDayOfWeek = firstDayOfMonth.getDay();

 // Generate calendar days
 const calendarDays = useMemo(() => {
 const days = [];

 // Add empty cells for days before the first day of the month
 for (let i = 0; i < startingDayOfWeek; i++) {
 days.push(null);
 }

 // Add days of the month
 for (let day = 1; day <= daysInMonth; day++) {
 days.push(day);
 }

 return days;
 }, [currentYear, currentMonth, daysInMonth, startingDayOfWeek]);

 // Group records by date
 const recordsByDate = useMemo(() => {
 const grouped: Record<string, HealthRecord[]> = {};

 records.forEach(record => {
 // Add to recorded date
 const recordedDate = new Date(record.date_recorded);
 if (recordedDate.getFullYear() === currentYear && recordedDate.getMonth() === currentMonth) {
 const day = recordedDate.getDate();
 const key = `recorded-${day}`;
 if (!grouped[key]) grouped[key] = [];
 grouped[key].push(record);
 }

 // Add to expiry date if exists
 if (record.expiry_date) {
 const expiryDate = new Date(record.expiry_date);
 if (expiryDate.getFullYear() === currentYear && expiryDate.getMonth() === currentMonth) {
 const day = expiryDate.getDate();
 const key = `expiry-${day}`;
 if (!grouped[key]) grouped[key] = [];
 grouped[key].push(record);
 }
 }
 });

 return grouped;
 }, [records, currentYear, currentMonth]);

 const navigateMonth = (direction: 'prev' | 'next') => {
 setCurrentDate(prev => {
 const newDate = new Date(prev);
 if (direction === 'prev') {
 newDate.setMonth(prev.getMonth() - 1);
 } else {
 newDate.setMonth(prev.getMonth() + 1);
 }
 return newDate;
 });
 };

 const goToToday = () => {
 setCurrentDate(new Date());
 };

 const monthName = firstDayOfMonth.toLocaleDateString('en-US', { 
 month: 'long', 
 year: 'numeric' 
 });

 if (loading) {
 return (
 <Card className="p-lg">
 <div className="space-y-md">
 <div className="h-icon-lg w-container-xs bg-muted animate-pulse rounded" />
 <div className="grid grid-cols-7 gap-xs">
 {[...Array(35)].map((_, i) => (
 <div key={i} className="h-component-lg bg-muted animate-pulse rounded" />
 ))}
 </div>
 </div>
 </Card>
 );
 }

 return (
 <div className="space-y-md">
 {/* Header */}
 <Card className="p-md">
 <div className="flex items-center justify-between">
 <div className="flex items-center gap-md">
 <h2 className="text-xl font-semibold flex items-center gap-xs">
 <Calendar className="h-icon-sm w-icon-sm" />
 {monthName}
 </h2>
 <Button variant="outline" size="sm" onClick={goToToday}>
 Today
 </Button>
 </div>
 <div className="flex items-center gap-xs">
 <Button variant="outline" size="sm" onClick={() => navigateMonth('prev')}>
 <ChevronLeft className="h-icon-xs w-icon-xs" />
 </Button>
 <Button variant="outline" size="sm" onClick={() => navigateMonth('next')}>
 <ChevronRight className="h-icon-xs w-icon-xs" />
 </Button>
 </div>
 </div>
 </Card>

 {/* Legend */}
 <Card className="p-md">
 <div className="flex items-center gap-lg text-sm">
 <div className="flex items-center gap-xs">
 <div className="w-3 h-3 bg-blue-500 rounded-full" />
 <span>Recorded</span>
 </div>
 <div className="flex items-center gap-xs">
 <div className="w-3 h-3 bg-orange-500 rounded-full" />
 <span>Expires</span>
 </div>
 <div className="flex items-center gap-xs">
 <div className="w-3 h-3 bg-red-500 rounded-full" />
 <span>Expired</span>
 </div>
 <div className="flex items-center gap-xs">
 <Bell className="h-3 w-3" />
 <span>Reminder</span>
 </div>
 </div>
 </Card>

 {/* Calendar */}
 <Card className="p-md">
 <div className="space-y-md">
 {/* Day headers */}
 <div className="grid grid-cols-7 gap-xs">
 {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
 <div key={day} className="p-xs text-center font-medium text-muted-foreground">
 {day}
 </div>
 ))}
 </div>

 {/* Calendar grid */}
 <div className="grid grid-cols-7 gap-xs">
 {calendarDays.map((day, index) => {
 if (day === null) {
 return <div key={index} className="h-component-lg" />;
 }

 const recordedRecords = recordsByDate[`recorded-${day}`] || [];
 const expiryRecords = recordsByDate[`expiry-${day}`] || [];
 const allDayRecords = [...recordedRecords, ...expiryRecords];

 const isToday = new Date().toDateString() === new Date(currentYear, currentMonth, day).toDateString();

 return (
 <div
 key={day}
 className={`h-component-lg p-xs border rounded-lg ${
 isToday ? 'bg-primary/10 border-primary' : 'border-border'
 } ${allDayRecords.length > 0 ? 'hover:bg-muted/50' : ''}`}
 >
 <div className="h-full flex flex-col">
 <div className="flex items-center justify-between mb-1">
 <span className={`text-sm font-medium ${
 isToday ? 'text-primary' : 'text-foreground'
 }`}>
 {day}
 </span>
 {allDayRecords.some(r => r.reminder_enabled) && (
 <Bell className="h-3 w-3 text-muted-foreground" />
 )}
 </div>

 <div className="flex-1 space-y-xs overflow-hidden">
 {/* Recorded events */}
 {recordedRecords.slice(0, 2).map((record, i) => (
 <button
 key={`recorded-${record.id}-${i}`}
 onClick={() => onEdit(record)}
 className="w-full text-left"
 >
 <div className="flex items-center gap-xs p-xs bg-blue-100 text-blue-800 rounded text-xs hover:bg-blue-200 transition-colors">
 <span>{getRecordTypeIcon(record.record_type)}</span>
 <span className="truncate">{record.title}</span>
 </div>
 </button>
 ))}

 {/* Expiry events */}
 {expiryRecords.slice(0, 2 - recordedRecords.length).map((record, i) => {
 const daysUntilExpiry = getDaysUntilExpiry(record.expiry_date!);
 const isExpired = daysUntilExpiry < 0;
 const isExpiringSoon = daysUntilExpiry <= 7 && daysUntilExpiry >= 0;

 return (
 <button
 key={`expiry-${record.id}-${i}`}
 onClick={() => onEdit(record)}
 className="w-full text-left"
 >
 <div className={`flex items-center gap-xs p-xs rounded text-xs hover:opacity-80 transition-colors ${
 isExpired 
 ? 'bg-red-100 text-red-800' 
 : isExpiringSoon 
 ? 'bg-orange-100 text-orange-800'
 : 'bg-yellow-100 text-yellow-800'
 }`}>
 {isExpired ? (
 <AlertTriangle className="h-3 w-3" />
 ) : (
 <Clock className="h-3 w-3" />
 )}
 <span className="truncate">{record.title}</span>
 </div>
 </button>
 );
 })}

 {/* Show more indicator */}
 {allDayRecords.length > 2 && (
 <div className="text-xs text-muted-foreground text-center">
 +{allDayRecords.length - 2} more
 </div>
 )}
 </div>
 </div>
 </div>
 );
 })}
 </div>
 </div>
 </Card>

 {/* Upcoming Events */}
 {records.length > 0 && (
 <Card className="p-md">
 <h3 className="font-semibold mb-4 flex items-center gap-xs">
 <Clock className="h-icon-xs w-icon-xs" />
 Upcoming Events
 </h3>
 <div className="space-y-xs">
 {records
 .filter(record => {
 if (!record.expiry_date) return false;
 const daysUntil = getDaysUntilExpiry(record.expiry_date);
 return daysUntil >= 0 && daysUntil <= 30;
 })
 .sort((a, b) => getDaysUntilExpiry(a.expiry_date!) - getDaysUntilExpiry(b.expiry_date!))
 .slice(0, 5)
 .map(record => {
 const daysUntil = getDaysUntilExpiry(record.expiry_date!);
 const urgency = getExpiryUrgency(daysUntil);

 return (
 <button
 key={record.id}
 onClick={() => onEdit(record)}
 className="w-full text-left p-sm border rounded-lg hover:bg-muted/50 transition-colors"
 >
 <div className="flex items-center justify-between">
 <div className="flex items-center gap-sm">
 <span className="text-lg">{getRecordTypeIcon(record.record_type)}</span>
 <div>
 <p className="font-medium">{record.title}</p>
 <p className="text-sm text-muted-foreground">
 {RECORD_TYPE_LABELS[record.record_type]}
 {record.provider && `  ${record.provider}`}
 </p>
 </div>
 </div>
 <div className="text-right">
 <Badge 
 variant={urgency === 'critical' ? 'destructive' : 'outline'}
 className="mb-1"
 >
 {daysUntil === 0 ? 'Today' : daysUntil === 1 ? 'Tomorrow' : `${daysUntil} days`}
 </Badge>
 <p className="text-xs text-muted-foreground">
 {formatDateShort(record.expiry_date!)}
 </p>
 </div>
 </div>
 </button>
 );
 })}

 {records.filter(r => r.expiry_date && getDaysUntilExpiry(r.expiry_date) >= 0 && getDaysUntilExpiry(r.expiry_date) <= 30).length === 0 && (
 <div className="text-center py-xl text-muted-foreground">
 <Activity className="h-icon-lg w-icon-lg mx-auto mb-2 opacity-50" />
 <p>No upcoming events in the next 30 days</p>
 </div>
 )}
 </div>
 </Card>
 )}
 </div>
 );
}

'use client';


import { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import {
  Badge,
  Button,
  Card,
  Drawer,
  Input,
  Select,
  Textarea,
  UnifiedInput
} from "@ghxstship/ui";
import { Briefcase, Building, Calendar, Clock, Edit, History, MapPin, Plus, Trash2 } from "lucide-react";

const jobHistorySchema = z.object({
  company_name: z.string().min(1, 'Company name is required'),
  job_title: z.string().min(1, 'Job title is required'),
  department: z.string().optional(),
  employment_type: z.enum(['full-time', 'part-time', 'contract', 'freelance', 'intern']),
  start_date: z.string().min(1, 'Start date is required'),
  end_date: z.string().optional(),
  is_current: z.boolean().default(false),
  location: z.string().optional(),
  description: z.string().optional(),
  achievements: z.array(z.string()).default([]),
  skills_used: z.array(z.string()).default([])
});

type JobHistoryForm = z.infer<typeof jobHistorySchema>;

interface JobHistory {
  id: string;
  company_name: string;
  job_title: string;
  department?: string;
  employment_type: string;
  start_date: string;
  end_date?: string;
  is_current: boolean;
  location?: string;
  description?: string;
  achievements: string[];
  skills_used: string[];
  created_at: string;
}

export default function JobHistoryClient({ orgId, userId }: { orgId: string; userId: string }) {
  const t = useTranslations('profile');
  const sb = createBrowserClient();

  const [jobHistory, setJobHistory] = useState<JobHistory[]>([]);
  const [loading, setLoading] = useState(true);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [editingJob, setEditingJob] = useState<JobHistory | null>(null);
  const [saving, setSaving] = useState(false);

  const form = useForm<JobHistoryForm>({
    resolver: zodResolver(jobHistorySchema),
    defaultValues: {
      company_name: '',
      job_title: '',
      department: '',
      employment_type: 'full-time',
      start_date: '',
      end_date: '',
      is_current: false,
      location: '',
      description: '',
      achievements: [],
      skills_used: []
    }
  });

  useEffect(() => {
    loadJobHistory();
  }, [orgId, userId]);

  const loadJobHistory = async () => {
    try {
      setLoading(true);

      const { data, error } = await sb
        .from('job_history')
        .select('*')
        .eq('organization_id', orgId)
        .eq('user_id', userId)
        .order('start_date', { ascending: false });

      if (error) throw error;
      setJobHistory(data || []);
    } catch (error) {
      console.error('Error loading job history:', error);
    } finally {
      setLoading(false);
    }
  };

  const openDrawer = (job?: JobHistory) => {
    if (job) {
      setEditingJob(job);
      form.reset({
        company_name: job.company_name,
        job_title: job.job_title,
        department: job.department || '',
        employment_type: job.employment_type as any,
        start_date: job.start_date,
        end_date: job.end_date || '',
        is_current: job.is_current,
        location: job.location || '',
        description: job.description || '',
        achievements: job.achievements || [],
        skills_used: job.skills_used || []
      });
    } else {
      setEditingJob(null);
      form.reset();
    }
    setDrawerOpen(true);
  };

  const onSubmit = async (data: JobHistoryForm) => {
    setSaving(true);
    try {
      if (editingJob) {
        // Update existing job
        const { error } = await sb
          .from('job_history')
          .update({
            ...data,
            end_date: data.is_current ? null : data.end_date
          })
          .eq('id', editingJob.id);

        if (error) throw error;
      } else {
        // Create new job
        const { error } = await sb
          .from('job_history')
          .insert({
            ...data,
            user_id: userId,
            organization_id: orgId,
            end_date: data.is_current ? null : data.end_date
          });

        if (error) throw error;
      }

      // Log activity
      await sb
        .from('user_profile_activity')
        .insert({
          user_id: userId,
          organization_id: orgId,
          activity_type: 'job_history_added',
          activity_description: `${editingJob ? 'Updated' : 'Added'} job history: ${data.job_title} at ${data.company_name}`,
          performed_by: userId
        });

      setDrawerOpen(false);
      loadJobHistory();
    } catch (error) {
      console.error('Error saving job history:', error);
    } finally {
      setSaving(false);
    }
  };

  const deleteJob = async (jobId: string) => {
    if (!confirm('Are you sure you want to delete this job history entry?')) return;

    try {
      const { error } = await sb
        .from('job_history')
        .delete()
        .eq('id', jobId);

      if (error) throw error;
      loadJobHistory();
    } catch (error) {
      console.error('Error deleting job history:', error);
    }
  };

  const formatEmploymentType = (type: string) => {
    return type.split('-').map(word => 
      word.charAt(0).toUpperCase() + word.slice(1)
    ).join(' ');
  };

  const calculateDuration = (startDate: string, endDate?: string) => {
    const start = new Date(startDate);
    const end = endDate ? new Date(endDate) : new Date();

    const months = (end.getFullYear() - start.getFullYear()) * 12 + 
                   (end.getMonth() - start.getMonth());

    if (months < 12) {
      return `${months} month${months !== 1 ? 's' : ''}`;
    }

    const years = Math.floor(months / 12);
    const remainingMonths = months % 12;

    if (remainingMonths === 0) {
      return `${years} year${years !== 1 ? 's' : ''}`;
    }

    return `${years} year${years !== 1 ? 's' : ''}, ${remainingMonths} month${remainingMonths !== 1 ? 's' : ''}`;
  };

  if (loading) {
    return (
      <div className="stack-md">
        {[...Array(3)].map((_, i) => (
          <div key={i} className="animate-pulse">
            <div className="h-component-xl bg-secondary rounded-lg"></div>
          </div>
        ))}
      </div>
    );
  }

  return (
    <div className="stack-lg">
      {/* Header */}
      <div className="flex items-center justify-between">
        <h2 className="text-heading-4 text-heading-4">Job History</h2>
        <Button onClick={() => openDrawer()} className="flex items-center gap-sm">
          <Plus className="h-icon-xs w-icon-xs" />
          Add Job
        </Button>
      </div>

      {/* Job History List */}
      <div className="stack-md">
        {jobHistory.length > 0 ? (
          jobHistory.map((job: any) => (
            <Card key={job.id}>
              <div className="p-lg">
                <div className="flex items-start justify-between mb-md">
                  <div className="flex items-start gap-md">
                    <div className="p-sm bg-accent/10 rounded-lg">
                      <Briefcase className="h-icon-md w-icon-md color-accent" />
                    </div>

                    <div>
                      <h3 className="text-body text-heading-4">{job.job_title}</h3>
                      <div className="flex items-center gap-sm color-muted mb-sm">
                        <Building className="h-icon-xs w-icon-xs" />
                        <span>{job.company_name}</span>
                        {job.department && <span> {job.department}</span>}
                      </div>

                      <div className="flex items-center gap-md text-body-sm color-muted mb-sm">
                        <div className="flex items-center gap-xs">
                          <Calendar className="h-icon-xs w-icon-xs" />
                          <span>
                            {new Date(job.start_date).toLocaleDateString()} - {' '}
                            {job.is_current ? 'Present' : 
                             job.end_date ? new Date(job.end_date).toLocaleDateString() : 'Present'
                            }
                          </span>
                        </div>

                        <div className="flex items-center gap-xs">
                          <Clock className="h-icon-xs w-icon-xs" />
                          <span>{calculateDuration(job.start_date, job.end_date)}</span>
                        </div>
                      </div>

                      {job.location && (
                        <div className="flex items-center gap-xs text-body-sm color-muted mb-sm">
                          <MapPin className="h-icon-xs w-icon-xs" />
                          <span>{job.location}</span>
                        </div>
                      )}

                      <div className="flex items-center gap-sm mb-sm">
                        <Badge variant="secondary">
                          {formatEmploymentType(job.employment_type)}
                        </Badge>
                        {job.is_current && (
                          <Badge variant="success">Current</Badge>
                        )}
                      </div>
                    </div>
                  </div>

                  <div className="flex items-center gap-sm">
                    <Button
                      variant="outline"

                      onClick={() => openDrawer(job)}
                    >
                      <Edit className="h-icon-xs w-icon-xs" />
                    </Button>
                    <Button
                      variant="outline"

                      onClick={() => deleteJob(job.id)}
                    >
                      <Trash2 className="h-icon-xs w-icon-xs" />
                    </Button>
                  </div>
                </div>

                {job.description && (
                  <div className="mb-md">
                    <h4 className="form-label mb-sm">Description</h4>
                    <p className="text-body-sm color-muted">{job.description}</p>
                  </div>
                )}

                {job.achievements && job.achievements.length > 0 && (
                  <div className="mb-md">
                    <h4 className="form-label mb-sm">Key Achievements</h4>
                    <ul className="list-disc list-inside text-body-sm color-muted stack-xs">
                      {job.achievements.map((achievement, index) => (
                        <li key={index}>{achievement}</li>
                      ))}
                    </ul>
                  </div>
                )}

                {job.skills_used && job.skills_used.length > 0 && (
                  <div>
                    <h4 className="form-label mb-sm">Skills Used</h4>
                    <div className="flex flex-wrap gap-sm">
                      {job.skills_used.map((skill, index) => (
                        <Badge key={index} variant="outline">
                          {skill}
                        </Badge>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            </Card>
          ))
        ) : (
          <Card>
            <div className="p-xl text-center">
              <Briefcase className="h-icon-2xl w-icon-2xl mx-auto mb-md color-muted opacity-50" />
              <h3 className="text-body form-label mb-sm">No Job History</h3>
              <p className="color-muted mb-md">
                Add your work experience to showcase your professional background.
              </p>
              <Button onClick={() => openDrawer()}>
                <Plus className="h-icon-xs w-icon-xs mr-sm" />
                Add Your First Job
              </Button>
            </div>
          </Card>
        )}
      </div>

      {/* Add/Edit Job Drawer */}
      <Drawer
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        title={editingJob ? 'Edit Job History' : 'Add Job History'}
        description="Add details about your work experience"
      >
        <form onSubmit={form.handleSubmit(onSubmit)} className="stack-lg">
          <div className="grid grid-cols-1 gap-md">
            <Input               label="Company Name"
              placeholder="Enter company name"
              {...form.register('company_name')}

              required
            />

            <Input               label="Job Title"
              placeholder="Enter job title"
              {...form.register('job_title')}

              required
            />

            <div className="grid grid-cols-2 gap-md">
              <Input                 label="Department"
                placeholder="Enter department (optional)"
                {...form.register('department')}

              />

              <Select
                {...form.register('employment_type')}
              >
                <option value="full-time">Full Time</option>
                <option value="part-time">Part Time</option>
                <option value="contract">Contract</option>
                <option value="freelance">Freelance</option>
                <option value="intern">Intern</option>
              </Select>
            </div>

            <div className="grid grid-cols-2 gap-md">
              <Input                 label="Start Date"
                type="date"
                {...form.register('start_date')}

                required
              />

              <Input                 label="End Date"
                type="date"
                {...form.register('end_date')}

                disabled={form.watch('is_current')}
              />
            </div>

            <div className="flex items-center gap-sm">
              <input
                type="checkbox"
                id="is_current"
                {...form.register('is_current')}
                className="rounded border-border"
              />
              <label htmlFor="is_current" className="text-body-sm form-label">
                This is my current job
              </label>
            </div>

            <Input               label="Location"
              placeholder="Enter location (optional)"
              {...form.register('location')}

            />

            <Textarea
              label="Description"
              placeholder="Describe your role and responsibilities (optional)"
              {...form.register('description')}

              rows={4}
            />
          </div>

          <div className="flex justify-end gap-sm pt-md border-t">
            <Button
              type="button"
              variant="outline"
              onClick={() => setDrawerOpen(false)}
              disabled={saving}
            >
              Cancel
            </Button>
            <Button type="submit" loading={saving}>
              {editingJob ? 'Update Job' : 'Add Job'}
            </Button>
          </div>
        </form>
      </Drawer>
    </div>
  );
}

'use client';


import { useState, useEffect } from 'react';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { Card, Badge, Button } from '@ghxstship/ui';
import { AlertCircle, Award, Briefcase, Calendar, Camera, CheckCircle, Edit, History, Mail, MapPin, Phone, User } from "lucide-react";

interface ProfileOverview {
  id: string;
  user_id: string;
  avatar_url?: string;
  full_name: string;
  email: string;
  job_title?: string;
  department?: string;
  phone_primary?: string;
  location?: string;
  profile_completion_percentage: number;
  status: string;
  last_login?: string;
  created_at: string;
  certifications_count: number;
  job_history_count: number;
}

interface RecentActivity {
  id: string;
  activity_type: string;
  activity_description: string;
  created_at: string;
}

export default function ProfileOverviewClient({ orgId, userId }: { orgId: string; userId: string }) {
  const t = useTranslations('profile');
  const sb = createBrowserClient();

  const [profile, setProfile] = useState<ProfileOverview | null>(null);
  const [activities, setActivities] = useState<RecentActivity[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadProfileOverview();
    loadRecentActivity();
  }, [orgId, userId]);

  const loadProfileOverview = async () => {
    try {
      // Get user profile with aggregated data
      const { data: profileData, error: profileError } = await sb
        .from('user_profiles')
        .select(`
          *,
          user:users(full_name, email),
          certifications:certifications(count),
          job_history:job_history(count)
        `)
        .eq('organization_id', orgId)
        .eq('user_id', userId)
        .single();

      if (profileError && profileError.code !== 'PGRST116') {
        throw profileError;
      }

      if (profileData) {
        setProfile({
          ...profileData,
          full_name: profileData.user?.full_name || 'Unknown User',
          email: profileData.user?.email || '',
          certifications_count: profileData.certifications?.length || 0,
          job_history_count: profileData.job_history?.length || 0
        });
      }
    } catch (error) {
      console.error('Error loading profile overview:', error);
    }
  };

  const loadRecentActivity = async () => {
    try {
      const { data, error } = await sb
        .from('user_profile_activity')
        .select('*')
        .eq('organization_id', orgId)
        .eq('user_id', userId)
        .order('created_at', { ascending: false })
        .limit(5);

      if (error) throw error;
      setActivities(data || []);
    } catch (error) {
      console.error('Error loading recent activity:', error);
    } finally {
      setLoading(false);
    }
  };

  const getCompletionColor = (percentage: number) => {
    if (percentage >= 80) return 'success';
    if (percentage >= 50) return 'warning';
    return 'destructive';
  };

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      active: { variant: 'success' as const, label: 'Active' },
      inactive: { variant: 'secondary' as const, label: 'Inactive' },
      pending: { variant: 'warning' as const, label: 'Pending' }
    };

    return statusConfig[status as keyof typeof statusConfig] || statusConfig.pending;
  };

  if (loading) {
    return (
      <div className="stack-lg">
        <div className="animate-pulse">
          <div className="h-component-xl bg-secondary rounded-lg mb-md"></div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
            <div className="h-container-xs bg-secondary rounded-lg"></div>
            <div className="h-container-xs bg-secondary rounded-lg"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="stack-lg">
      {/* Profile Header */}
      <Card>
        <div className="flex items-start gap-lg p-lg">
          <div className="relative">
            <div className="h-component-lg w-component-lg rounded-full bg-secondary flex items-center justify-center">
              {profile?.avatar_url ? (
                <img 
                  src={profile.avatar_url} 
                  alt={profile.full_name || 'User'}
                  className="h-component-lg w-component-lg rounded-full object-cover"
                />
              ) : (
                <User className="h-icon-lg w-icon-lg color-muted" />
              )}
            </div>
            <Button

              variant="outline"
              className="absolute -bottom-2 -right-2 h-icon-lg w-icon-lg rounded-full p-0"
            >
              <Camera className="h-icon-xs w-icon-xs" />
            </Button>
          </div>

          <div className="flex-1">
            <div className="flex items-center justify-between mb-sm">
              <h1 className="text-heading-3 text-heading-3">{profile?.full_name || 'Unknown User'}</h1>
              <Badge {...getStatusBadge(profile?.status || 'pending')}>
                {getStatusBadge(profile?.status || 'pending').label}
              </Badge>
            </div>

            <div className="stack-sm text-body-sm color-muted">
              {profile?.job_title && (
                <div className="flex items-center gap-sm">
                  <Briefcase className="h-icon-xs w-icon-xs" />
                  <span>{profile.job_title}</span>
                  {profile.department && <span> {profile.department}</span>}
                </div>
              )}

              <div className="flex items-center gap-sm">
                <Mail className="h-icon-xs w-icon-xs" />
                <span>{profile?.email}</span>
              </div>

              {profile?.phone_primary && (
                <div className="flex items-center gap-sm">
                  <Phone className="h-icon-xs w-icon-xs" />
                  <span>{profile.phone_primary}</span>
                </div>
              )}

              {profile?.location && (
                <div className="flex items-center gap-sm">
                  <MapPin className="h-icon-xs w-icon-xs" />
                  <span>{profile.location}</span>
                </div>
              )}
            </div>
          </div>

          <Button variant="outline" className="flex items-center gap-sm">
            <Edit className="h-icon-xs w-icon-xs" />
            Edit Profile
          </Button>
        </div>
      </Card>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-lg">
        {/* Profile Completion */}
        <Card>
          <div className="p-lg">
            <div className="flex items-center justify-between mb-md">
              <h3 className="text-body text-heading-4">Profile Completion</h3>
              <Badge variant={getCompletionColor(profile?.profile_completion_percentage || 0)}>
                {profile?.profile_completion_percentage || 0}%
              </Badge>
            </div>

            <div className="w-full bg-secondary rounded-full h-2 mb-md">
              <div 
                className="bg-accent h-2 rounded-full transition-all duration-300" 
                style={{ width: `${profile?.profile_completion_percentage || 0}%` }}
              ></div>
            </div>

            <div className="stack-sm text-body-sm">
              <div className="flex items-center gap-sm color-muted">
                <CheckCircle className="h-icon-xs w-icon-xs color-success" />
                <span>Basic information completed</span>
              </div>
              <div className="flex items-center gap-sm color-muted">
                <AlertCircle className="h-icon-xs w-icon-xs color-warning" />
                <span>Add professional certifications</span>
              </div>
              <div className="flex items-center gap-sm color-muted">
                <AlertCircle className="h-icon-xs w-icon-xs color-warning" />
                <span>Complete job history</span>
              </div>
            </div>
          </div>
        </Card>

        {/* Quick Stats */}
        <Card>
          <div className="p-lg">
            <h3 className="text-body text-heading-4 mb-md">Quick Stats</h3>

            <div className="grid grid-cols-2 gap-md">
              <div className="text-center p-md bg-secondary/50 rounded-lg">
                <div className="text-heading-3 text-heading-3 color-accent">
                  {profile?.certifications_count || 0}
                </div>
                <div className="text-body-sm color-muted">Certifications</div>
              </div>

              <div className="text-center p-md bg-secondary/50 rounded-lg">
                <div className="text-heading-3 text-heading-3 color-success">
                  {profile?.job_history_count || 0}
                </div>
                <div className="text-body-sm color-muted">Job History</div>
              </div>
            </div>

            <div className="mt-md pt-md border-t">
              <div className="flex items-center gap-sm text-body-sm color-muted">
                <Calendar className="h-icon-xs w-icon-xs" />
                <span>
                  Member since {profile?.created_at ? 
                    new Date(profile.created_at).toLocaleDateString() : 'Unknown'
                  }
                </span>
              </div>
            </div>
          </div>
        </Card>

        {/* Recent Activity */}
        <Card className="md:col-span-2">
          <div className="p-lg">
            <h3 className="text-body text-heading-4 mb-md">Recent Activity</h3>

            {activities.length > 0 ? (
              <div className="stack-sm">
                {activities.map((activity: any) => (
                  <div key={activity.id} className="flex items-center gap-sm p-sm bg-secondary/50 rounded-lg">
                    <div className="h-2 w-2 bg-accent rounded-full"></div>
                    <div className="flex-1">
                      <div className="text-body-sm form-label">{activity.activity_description}</div>
                      <div className="text-body-sm color-muted">
                        {new Date(activity.created_at).toLocaleString()}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center py-xl color-muted">
                <User className="h-icon-2xl w-icon-2xl mx-auto mb-sm opacity-50" />
                <p>No recent activity</p>
              </div>
            )}
          </div>
        </Card>
      </div>
    </div>
  );
}

'use client';


import { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { 
  Briefcase, 
  Edit, 
  Save, 
  Award, 
  Target,
  Users,
  TrendingUp,
  Calendar
} from 'lucide-react';import {
  Badge,
  Button,
  Card,
  Input,
  Select,
  Textarea,
  UnifiedInput
} from "@ghxstship/ui";
import { Card, Button, Select, Textarea, Badge } from '@ghxstship/ui';


const professionalSchema = z.object({
  job_title: z.string().optional(),
  department: z.string().optional(),
  employee_id: z.string().optional(),
  hire_date: z.string().optional(),
  employment_type: z.enum(['full-time', 'part-time', 'contract', 'freelance', 'intern']).optional(),
  manager_id: z.string().optional(),
  skills: z.array(z.string()).default([]),
  bio: z.string().optional(),
  linkedin_url: z.string().url().optional().or(z.literal('')),
  website_url: z.string().url().optional().or(z.literal('')),
  salary_range: z.string().optional(),
  performance_rating: z.enum(['exceeds', 'meets', 'below', 'not-rated']).optional(),
  career_goals: z.string().optional(),
  mentorship_interests: z.array(z.string()).default([])
});

type ProfessionalForm = z.infer<typeof professionalSchema>;

interface ProfessionalProfile {
  id: string;
  job_title?: string;
  department?: string;
  employee_id?: string;
  hire_date?: string;
  employment_type?: string;
  manager_id?: string;
  skills: string[];
  bio?: string;
  linkedin_url?: string;
  website_url?: string;
  salary_range?: string;
  performance_rating?: string;
  career_goals?: string;
  mentorship_interests: string[];
  updated_at: string;
}

export default function ProfessionalClient({ orgId, userId }: { orgId: string; userId: string }) {
  const t = useTranslations('profile');
  const sb = createBrowserClient();

  const [profile, setProfile] = useState<ProfessionalProfile | null>(null);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [saving, setSaving] = useState(false);
  const [skillInput, setSkillInput] = useState('');
  const [mentorshipInput, setMentorshipInput] = useState('');

  const form = useForm<ProfessionalForm>({
    resolver: zodResolver(professionalSchema),
    defaultValues: {
      job_title: '',
      department: '',
      employee_id: '',
      hire_date: '',
      employment_type: 'full-time',
      manager_id: '',
      skills: [],
      bio: '',
      linkedin_url: '',
      website_url: '',
      salary_range: '',
      performance_rating: 'not-rated',
      career_goals: '',
      mentorship_interests: []
    }
  });

  useEffect(() => {
    loadProfessionalProfile();
  }, [orgId, userId]);

  const loadProfessionalProfile = async () => {
    try {
      setLoading(true);

      const { data, error } = await sb
        .from('user_profiles')
        .select('*')
        .eq('organization_id', orgId)
        .eq('user_id', userId)
        .single();

      if (error && error.code !== 'PGRST116') {
        throw error;
      }

      if (data) {
        setProfile(data);
        form.reset({
          job_title: data.job_title || '',
          department: data.department || '',
          employee_id: data.employee_id || '',
          hire_date: data.hire_date || '',
          employment_type: data.employment_type || 'full-time',
          manager_id: data.manager_id || '',
          skills: data.skills || [],
          bio: data.bio || '',
          linkedin_url: data.linkedin_url || '',
          website_url: data.website_url || '',
          salary_range: data.salary_range || '',
          performance_rating: data.performance_rating || 'not-rated',
          career_goals: data.career_goals || '',
          mentorship_interests: data.mentorship_interests || []
        });
      }
    } catch (error) {
      console.error('Error loading professional profile:', error);
    } finally {
      setLoading(false);
    }
  };

  const onSubmit = async (data: ProfessionalForm) => {
    setSaving(true);
    try {
      const { error } = await sb
        .from('user_profiles')
        .upsert({
          user_id: userId,
          organization_id: orgId,
          ...data,
          linkedin_url: data.linkedin_url || null,
          website_url: data.website_url || null
        });

      if (error) throw error;

      // Log activity
      await sb
        .from('user_profile_activity')
        .insert({
          user_id: userId,
          organization_id: orgId,
          activity_type: 'profile_updated',
          activity_description: 'Updated professional information',
          performed_by: userId
        });

      setEditing(false);
      loadProfessionalProfile();
    } catch (error) {
      console.error('Error saving professional profile:', error);
    } finally {
      setSaving(false);
    }
  };

  const addSkill = () => {
    if (skillInput.trim()) {
      const currentSkills = form.getValues('skills');
      if (!currentSkills.includes(skillInput.trim())) {
        form.setValue('skills', [...currentSkills, skillInput.trim()]);
        setSkillInput('');
      }
    }
  };

  const removeSkill = (skillToRemove: string) => {
    const currentSkills = form.getValues('skills');
    form.setValue('skills', currentSkills.filter(skill => skill !== skillToRemove));
  };

  const addMentorshipInterest = () => {
    if (mentorshipInput.trim()) {
      const currentInterests = form.getValues('mentorship_interests');
      if (!currentInterests.includes(mentorshipInput.trim())) {
        form.setValue('mentorship_interests', [...currentInterests, mentorshipInput.trim()]);
        setMentorshipInput('');
      }
    }
  };

  const removeMentorshipInterest = (interestToRemove: string) => {
    const currentInterests = form.getValues('mentorship_interests');
    form.setValue('mentorship_interests', currentInterests.filter(interest => interest !== interestToRemove));
  };

  const formatEmploymentType = (type: string) => {
    return type.split('-').map(word => 
      word.charAt(0).toUpperCase() + word.slice(1)
    ).join(' ');
  };

  const getPerformanceRatingColor = (rating: string) => {
    const colors = {
      exceeds: 'success',
      meets: 'secondary',
      below: 'warning',
      'not-rated': 'outline'
    };
    return colors[rating as keyof typeof colors] || 'outline';
  };

  if (loading) {
    return (
      <div className="stack-lg">
        <div className="animate-pulse">
          <div className="h-icon-lg bg-secondary rounded mb-md"></div>
          <div className="stack-md">
            <div className="h-component-xl bg-secondary rounded"></div>
            <div className="h-component-lg bg-secondary rounded"></div>
            <div className="h-component-lg bg-secondary rounded"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="stack-lg">
      <div className="flex items-center justify-between">
        <h2 className="text-heading-4 text-heading-4">Professional Information</h2>
        {!editing ? (
          <Button onClick={() => setEditing(true)} className="flex items-center gap-sm">
            <Edit className="h-icon-xs w-icon-xs" />
            Edit Profile
          </Button>
        ) : (
          <div className="flex items-center gap-sm">
            <Button
              variant="outline"
              onClick={() => {
                setEditing(false);
                form.reset();
              }}
            >
              Cancel
            </Button>
            <Button
              onClick={form.handleSubmit(onSubmit)}
              loading={saving}
              className="flex items-center gap-sm"
            >
              <Save className="h-icon-xs w-icon-xs" />
              Save Changes
            </Button>
          </div>
        )}
      </div>

      <form onSubmit={form.handleSubmit(onSubmit)} className="stack-lg">
        {/* Employment Information */}
        <Card>
          <div className="p-lg">
            <div className="flex items-center gap-sm mb-md">
              <Briefcase className="h-icon-sm w-icon-sm color-accent" />
              <h3 className="text-body text-heading-4">Employment Details</h3>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
              <Input                 label="Job Title"
                placeholder="Enter your job title"
                {...form.register('job_title')}

                disabled={!editing}
              />

              <Input                 label="Department"
                placeholder="Enter your department"
                {...form.register('department')}

                disabled={!editing}
              />

              <Input                 label="Employee ID"
                placeholder="Enter employee ID"
                {...form.register('employee_id')}

                disabled={!editing}
              />

              <Input                 label="Hire Date"
                type="date"
                {...form.register('hire_date')}

                disabled={!editing}
              />

              <Select
                {...form.register('employment_type')}
                disabled={!editing}
              >
                <option value="full-time">Full Time</option>
                <option value="part-time">Part Time</option>
                <option value="contract">Contract</option>
                <option value="freelance">Freelance</option>
                <option value="intern">Intern</option>
              </Select>

              <Select
                {...form.register('performance_rating')}
                disabled={!editing}
              >
                <option value="not-rated">Not Rated</option>
                <option value="exceeds">Exceeds Expectations</option>
                <option value="meets">Meets Expectations</option>
                <option value="below">Below Expectations</option>
              </Select>
            </div>

            {!editing && profile && (
              <div className="mt-md flex items-center gap-md">
                {profile.employment_type && (
                  <Badge variant="secondary">
                    {formatEmploymentType(profile.employment_type)}
                  </Badge>
                )}
                {profile.performance_rating && profile.performance_rating !== 'not-rated' && (
                  <Badge variant={getPerformanceRatingColor(profile.performance_rating) as any}>
                    {profile.performance_rating === 'exceeds' ? 'Exceeds Expectations' :
                     profile.performance_rating === 'meets' ? 'Meets Expectations' :
                     'Below Expectations'}
                  </Badge>
                )}
              </div>
            )}
          </div>
        </Card>

        {/* Skills */}
        <Card>
          <div className="p-lg">
            <div className="flex items-center gap-sm mb-md">
              <Award className="h-icon-sm w-icon-sm color-secondary" />
              <h3 className="text-body text-heading-4">Skills & Expertise</h3>
            </div>

            {editing && (
              <div className="flex gap-sm mb-md">
                <Input                   placeholder="Add a skill"
                  value={skillInput}
                  onChange={(e: React.ChangeEvent<HTMLInputElement>) => setSkillInput(e.target.value)}
                  onKeyPress={(e: any) => e.key === 'Enter' && (e.preventDefault(), addSkill())}
                />
                <Button type="button" onClick={addSkill}>Add</Button>
              </div>
            )}

            <div className="flex flex-wrap gap-sm">
              {form.watch('skills').map((skill, index) => (
                <Badge
                  key={index}
                  variant="outline"
                  className="flex items-center gap-xs"
                >
                  {skill}
                  {editing && (
                    <button
                      type="button"
                      onClick={() => removeSkill(skill)}
                      className="ml-xs color-destructive hover:color-destructive/80"
                    >

                    </button>
                  )}
                </Badge>
              ))}
            </div>
          </div>
        </Card>

        {/* Professional Bio */}
        <Card>
          <div className="p-lg">
            <div className="flex items-center gap-sm mb-md">
              <Users className="h-icon-sm w-icon-sm color-success" />
              <h3 className="text-body text-heading-4">Professional Bio</h3>
            </div>

            <Textarea
              label="Bio"
              placeholder="Write a brief professional bio"
              {...form.register('bio')}

              disabled={!editing}
              rows={4}
            />
          </div>
        </Card>

        {/* Career Development */}
        <Card>
          <div className="p-lg">
            <div className="flex items-center gap-sm mb-md">
              <TrendingUp className="h-icon-sm w-icon-sm color-warning" />
              <h3 className="text-body text-heading-4">Career Development</h3>
            </div>

            <div className="stack-md">
              <Textarea
                label="Career Goals"
                placeholder="Describe your career goals and aspirations"
                {...form.register('career_goals')}

                disabled={!editing}
                rows={3}
              />

              <div>
                <label className="block text-body-sm form-label mb-sm">Mentorship Interests</label>
                {editing && (
                  <div className="flex gap-sm mb-md">
                    <Input                       placeholder="Add mentorship interest"
                      value={mentorshipInput}
                      onChange={(e: React.ChangeEvent<HTMLInputElement>) => setMentorshipInput(e.target.value)}
                      onKeyPress={(e: any) => e.key === 'Enter' && (e.preventDefault(), addMentorshipInterest())}
                    />
                    <Button type="button" onClick={addMentorshipInterest}>Add</Button>
                  </div>
                )}

                <div className="flex flex-wrap gap-sm">
                  {form.watch('mentorship_interests').map((interest, index) => (
                    <Badge
                      key={index}
                      variant="outline"
                      className="flex items-center gap-xs"
                    >
                      {interest}
                      {editing && (
                        <button
                          type="button"
                          onClick={() => removeMentorshipInterest(interest)}
                          className="ml-xs color-destructive hover:color-destructive/80"
                        >

                        </button>
                      )}
                    </Badge>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </Card>

        {/* Online Presence */}
        <Card>
          <div className="p-lg">
            <div className="flex items-center gap-sm mb-md">
              <Target className="h-icon-sm w-icon-sm color-accent" />
              <h3 className="text-body text-heading-4">Online Presence</h3>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
              <Input                 label="LinkedIn URL"
                placeholder="https://linkedin.com/in/username"
                {...form.register('linkedin_url')}

                disabled={!editing}
              />

              <Input                 label="Website URL"
                placeholder="https://yourwebsite.com"
                {...form.register('website_url')}

                disabled={!editing}
              />
            </div>
          </div>
        </Card>
      </form>
    </div>
  );
}

'use client';


import { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { Calendar, Clock, CreditCard, Edit, Globe, History, MapPin, Plane, Plus, Save, Trash2 } from "lucide-react";import {
  Badge,
  Button,
  Card,
  Drawer,
  Input,
  Select,
  Textarea,
  UnifiedInput
} from "@ghxstship/ui";
import {
  Card,
  Button,
  Select,
  Textarea,
  Badge,
  Drawer
} from '@ghxstship/ui';


const travelRecordSchema = z.object({
  travel_type: z.enum(['business', 'personal', 'relocation', 'training', 'conference', 'other']),
  destination: z.string().min(1, 'Destination is required'),
  country: z.string().min(1, 'Country is required'),
  purpose: z.string().min(1, 'Purpose is required'),
  start_date: z.string(),
  end_date: z.string(),
  duration_days: z.number().min(1),
  accommodation: z.string().optional(),
  transportation: z.string().optional(),
  visa_required: z.boolean().default(false),
  visa_status: z.enum(['not-required', 'pending', 'approved', 'denied']).optional(),
  passport_used: z.string().optional(),
  notes: z.string().optional(),
  expenses: z.number().optional()
});

type TravelRecordForm = z.infer<typeof travelRecordSchema>;

interface TravelRecord {
  id: string;
  travel_type: string;
  destination: string;
  country: string;
  purpose: string;
  start_date: string;
  end_date: string;
  duration_days: number;
  accommodation?: string;
  transportation?: string;
  visa_required: boolean;
  visa_status?: string;
  passport_used?: string;
  notes?: string;
  expenses?: number;
  created_at: string;
}

export default function TravelClient({ orgId, userId }: { orgId: string; userId: string }) {
  const t = useTranslations('profile');
  const sb = createBrowserClient();

  const [travelRecords, setTravelRecords] = useState<TravelRecord[]>([]);
  const [loading, setLoading] = useState(true);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [editingRecord, setEditingRecord] = useState<TravelRecord | null>(null);
  const [saving, setSaving] = useState(false);
  const [filterType, setFilterType] = useState<string>('all');

  const form = useForm<TravelRecordForm>({
    resolver: zodResolver(travelRecordSchema),
    defaultValues: {
      travel_type: 'business',
      destination: '',
      country: '',
      purpose: '',
      start_date: '',
      end_date: '',
      duration_days: 1,
      accommodation: '',
      transportation: '',
      visa_required: false,
      visa_status: 'not-required',
      passport_used: '',
      notes: '',
      expenses: 0
    }
  });

  useEffect(() => {
    loadTravelRecords();
  }, [orgId, userId]);

  useEffect(() => {
    // Calculate duration when dates change
    const startDate = form.watch('start_date');
    const endDate = form.watch('end_date');

    if (startDate && endDate) {
      const start = new Date(startDate);
      const end = new Date(endDate);
      const diffTime = Math.abs(end.getTime() - start.getTime());
      const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)) + 1;
      form.setValue('duration_days', diffDays);
    }
  }, [form.watch('start_date'), form.watch('end_date')]);

  const loadTravelRecords = async () => {
    try {
      setLoading(true);

      // Mock travel records data
      const mockRecords: TravelRecord[] = [
        {
          id: '1',
          travel_type: 'business',
          destination: 'Port Royal, Jamaica',
          country: 'Jamaica',
          purpose: 'Maritime Operations Conference',
          start_date: '2024-01-15',
          end_date: '2024-01-20',
          duration_days: 6,
          accommodation: 'Royal Caribbean Resort',
          transportation: 'Company Vessel',
          visa_required: false,
          visa_status: 'not-required',
          passport_used: 'Maritime Passport #12345',
          notes: 'Attended advanced navigation workshops and fleet coordination meetings.',
          expenses: 2500,
          created_at: '2024-01-15T10:00:00Z'
        },
        {
          id: '2',
          travel_type: 'training',
          destination: 'Portsmouth, England',
          country: 'United Kingdom',
          purpose: 'Advanced Maritime Certification',
          start_date: '2023-09-01',
          end_date: '2023-09-30',
          duration_days: 30,
          accommodation: 'Naval Academy Quarters',
          transportation: 'Commercial Flight',
          visa_required: true,
          visa_status: 'approved',
          passport_used: 'Standard Passport #67890',
          notes: 'Completed advanced navigation and maritime law certification program.',
          expenses: 5000,
          created_at: '2023-09-01T08:00:00Z'
        },
        {
          id: '3',
          travel_type: 'personal',
          destination: 'Tortuga Island',
          country: 'Caribbean Territory',
          purpose: 'Family Vacation',
          start_date: '2023-12-20',
          end_date: '2023-12-27',
          duration_days: 8,
          accommodation: 'Beachfront Villa',
          transportation: 'Private Yacht',
          visa_required: false,
          visa_status: 'not-required',
          notes: 'Holiday break with family. Explored local maritime history.',
          expenses: 3000,
          created_at: '2023-12-20T12:00:00Z'
        }
      ];

      setTravelRecords(mockRecords);
    } catch (error) {
      console.error('Error loading travel records:', error);
    } finally {
      setLoading(false);
    }
  };

  const onSubmit = async (data: TravelRecordForm) => {
    setSaving(true);
    try {
      if (editingRecord) {
        // Update existing record
        const updatedRecords = travelRecords.map(record =>
          record.id === editingRecord.id
            ? { ...record, ...data }
            : record
        );
        setTravelRecords(updatedRecords);
      } else {
        // Create new record
        const newRecord: TravelRecord = {
          id: Date.now().toString(),
          ...data,
          created_at: new Date().toISOString()
        };
        setTravelRecords([newRecord, ...travelRecords]);
      }

      // Log activity
      await sb
        .from('user_profile_activity')
        .insert({
          user_id: userId,
          organization_id: orgId,
          activity_type: editingRecord ? 'travel_record_updated' : 'travel_record_added',
          activity_description: editingRecord 
            ? `Updated travel record: ${data.destination}`
            : `Added new travel record: ${data.destination}`,
          performed_by: userId
        });

      setDrawerOpen(false);
      setEditingRecord(null);
      form.reset();
    } catch (error) {
      console.error('Error saving travel record:', error);
    } finally {
      setSaving(false);
    }
  };

  const handleEdit = (record: TravelRecord) => {
    setEditingRecord(record);
    form.reset({
      travel_type: record.travel_type as any,
      destination: record.destination,
      country: record.country,
      purpose: record.purpose,
      start_date: record.start_date,
      end_date: record.end_date,
      duration_days: record.duration_days,
      accommodation: record.accommodation || '',
      transportation: record.transportation || '',
      visa_required: record.visa_required,
      visa_status: record.visa_status as any || 'not-required',
      passport_used: record.passport_used || '',
      notes: record.notes || '',
      expenses: record.expenses || 0
    });
    setDrawerOpen(true);
  };

  const handleDelete = async (recordId: string) => {
    if (confirm('Are you sure you want to delete this travel record?')) {
      const record = travelRecords.find(r => r.id === recordId);
      setTravelRecords(travelRecords.filter(r => r.id !== recordId));

      // Log activity
      if (record) {
        await sb
          .from('user_profile_activity')
          .insert({
            user_id: userId,
            organization_id: orgId,
            activity_type: 'travel_record_deleted',
            activity_description: `Deleted travel record: ${record.destination}`,
            performed_by: userId
          });
      }
    }
  };

  const getTravelTypeIcon = (type: string) => {
    const icons = {
      business: Plane,
      personal: Globe,
      relocation: MapPin,
      training: Calendar,
      conference: Clock,
      other: Plane
    };
    return icons[type as keyof typeof icons] || Plane;
  };

  const getTravelTypeColor = (type: string) => {
    const colors = {
      business: 'blue',
      personal: 'green',
      relocation: 'purple',
      training: 'orange',
      conference: 'red',
      other: 'gray'
    };
    return colors[type as keyof typeof colors] || 'gray';
  };

  const getVisaStatusColor = (status?: string) => {
    const colors = {
      'not-required': 'secondary',
      'pending': 'warning',
      'approved': 'success',
      'denied': 'destructive'
    };
    return colors[status as keyof typeof colors] || 'secondary';
  };

  const formatTravelType = (type: string) => {
    return type.charAt(0).toUpperCase() + type.slice(1);
  };

  const formatVisaStatus = (status?: string) => {
    if (!status) return 'Not Required';
    return status.split('-').map(word => 
      word.charAt(0).toUpperCase() + word.slice(1)
    ).join(' ');
  };

  const calculateTotalExpenses = () => {
    return travelRecords.reduce((total, record) => total + (record.expenses || 0), 0);
  };

  const calculateTotalDays = () => {
    return travelRecords.reduce((total, record) => total + record.duration_days, 0);
  };

  const filteredRecords = filterType === 'all' 
    ? travelRecords 
    : travelRecords.filter(record => record.travel_type === filterType);

  // Sort records by start date (most recent first)
  const sortedRecords = [...filteredRecords].sort((a, b) => 
    new Date(b.start_date).getTime() - new Date(a.start_date).getTime()
  );

  if (loading) {
    return (
      <div className="stack-lg">
        <div className="animate-pulse">
          <div className="h-icon-lg bg-secondary rounded mb-md"></div>
          <div className="stack-md">
            <div className="h-component-xl bg-secondary rounded"></div>
            <div className="h-component-xl bg-secondary rounded"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="stack-lg">
      <div className="flex items-center justify-between">
        <h2 className="text-heading-4 text-heading-4">Travel History</h2>
        <Button 
          onClick={() => {
            setEditingRecord(null);
            form.reset();
            setDrawerOpen(true);
          }}
          className="flex items-center gap-sm"
        >
          <Plus className="h-icon-xs w-icon-xs" />
          Add Travel Record
        </Button>
      </div>

      {/* Travel Summary */}
      {travelRecords.length > 0 && (
        <Card>
          <div className="p-lg">
            <div className="flex items-center gap-sm mb-md">
              <Globe className="h-icon-sm w-icon-sm color-accent" />
              <h3 className="text-body text-heading-4">Travel Summary</h3>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-md">
              <div className="text-center">
                <div className="text-heading-3 text-heading-3 color-accent">{travelRecords.length}</div>
                <div className="text-body-sm color-muted">Total Trips</div>
              </div>
              <div className="text-center">
                <div className="text-heading-3 text-heading-3 color-success">{calculateTotalDays()}</div>
                <div className="text-body-sm color-muted">Days Traveled</div>
              </div>
              <div className="text-center">
                <div className="text-heading-3 text-heading-3 color-secondary">
                  ${calculateTotalExpenses().toLocaleString()}
                </div>
                <div className="text-body-sm color-muted">Total Expenses</div>
              </div>
            </div>
          </div>
        </Card>
      )}

      {/* Filter Tabs */}
      <div className="flex gap-sm flex-wrap">
        {['all', 'business', 'personal', 'training', 'conference', 'relocation', 'other'].map((type: any) => (
          <Button
            key={type}
            variant={filterType === type ? 'primary' : 'outline'}

            onClick={() => setFilterType(type)}
          >
            {type === 'all' ? 'All Trips' : formatTravelType(type)}
          </Button>
        ))}
      </div>

      {sortedRecords.length === 0 ? (
        <Card>
          <div className="p-xl text-center">
            <Plane className="h-icon-2xl w-icon-2xl color-muted mx-auto mb-md" />
            <h3 className="text-body text-heading-4 mb-sm">No Travel Records</h3>
            <p className="color-muted mb-md">
              Keep track of your business trips, training, and personal travel history.
            </p>
            <Button 
              onClick={() => {
                setEditingRecord(null);
                form.reset();
                setDrawerOpen(true);
              }}
            >
              Add Your First Trip
            </Button>
          </div>
        </Card>
      ) : (
        <div className="stack-md">
          {sortedRecords.map((record: any) => {
            const IconComponent = getTravelTypeIcon(record.travel_type);

            return (
              <Card key={record.id}>
                <div className="p-lg">
                  <div className="flex items-start justify-between mb-md">
                    <div className="flex items-center gap-sm">
                      <div className={`h-icon-xl w-icon-xl bg-${getTravelTypeColor(record.travel_type)}-100 rounded-full flex items-center justify-center`}>
                        <IconComponent className={`h-icon-sm w-icon-sm text-${getTravelTypeColor(record.travel_type)}-600`} />
                      </div>
                      <div>
                        <h3 className="text-body text-heading-4">{record.destination}</h3>
                        <p className="text-body-sm color-muted">{record.country}</p>
                        <div className="flex items-center gap-sm mt-xs">
                          <Badge variant="outline">
                            {formatTravelType(record.travel_type)}
                          </Badge>
                          {record.visa_required && (
                            <Badge variant={getVisaStatusColor(record.visa_status) as any}>
                              Visa: {formatVisaStatus(record.visa_status)}
                            </Badge>
                          )}
                        </div>
                      </div>
                    </div>
                    <div className="flex items-center gap-sm">
                      <Button
                        variant="outline"

                        onClick={() => handleEdit(record)}
                      >
                        <Edit className="h-icon-xs w-icon-xs" />
                      </Button>
                      <Button
                        variant="outline"

                        onClick={() => handleDelete(record.id)}
                      >
                        <Trash2 className="h-icon-xs w-icon-xs" />
                      </Button>
                    </div>
                  </div>

                  <div className="mb-md">
                    <h4 className="text-body-sm form-label mb-xs">Purpose</h4>
                    <p className="text-body-sm color-muted">{record.purpose}</p>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-md mb-md">
                    <div>
                      <h4 className="text-body-sm form-label mb-xs">Travel Dates</h4>
                      <div className="flex items-center gap-xs text-body-sm color-muted">
                        <Calendar className="h-icon-xs w-icon-xs" />
                        {new Date(record.start_date).toLocaleDateString()} - {new Date(record.end_date).toLocaleDateString()}
                      </div>
                      <div className="flex items-center gap-xs text-body-sm color-muted mt-xs">
                        <Clock className="h-icon-xs w-icon-xs" />
                        {record.duration_days} day{record.duration_days !== 1 ? 's' : ''}
                      </div>
                    </div>

                    {record.expenses && (
                      <div>
                        <h4 className="text-body-sm form-label mb-xs">Expenses</h4>
                        <p className="text-body-sm color-muted">
                          ${record.expenses.toLocaleString()}
                        </p>
                      </div>
                    )}
                  </div>

                  {(record.accommodation || record.transportation) && (
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-md mb-md">
                      {record.accommodation && (
                        <div>
                          <h4 className="text-body-sm form-label mb-xs">Accommodation</h4>
                          <p className="text-body-sm color-muted">{record.accommodation}</p>
                        </div>
                      )}

                      {record.transportation && (
                        <div>
                          <h4 className="text-body-sm form-label mb-xs">Transportation</h4>
                          <p className="text-body-sm color-muted">{record.transportation}</p>
                        </div>
                      )}
                    </div>
                  )}

                  {record.passport_used && (
                    <div className="mb-md">
                      <h4 className="text-body-sm form-label mb-xs">Passport Used</h4>
                      <div className="flex items-center gap-xs text-body-sm color-muted">
                        <CreditCard className="h-icon-xs w-icon-xs" />
                        {record.passport_used}
                      </div>
                    </div>
                  )}

                  {record.notes && (
                    <div className="mb-md p-sm bg-secondary rounded-lg">
                      <h4 className="text-body-sm form-label mb-xs">Notes</h4>
                      <p className="text-body-sm color-muted">{record.notes}</p>
                    </div>
                  )}
                </div>
              </Card>
            );
          })}
        </div>
      )}

      <Drawer
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        title={editingRecord ? 'Edit Travel Record' : 'Add New Travel Record'}
        description={editingRecord ? 'Update travel record details' : 'Add a new travel record'}
      >
        <form onSubmit={form.handleSubmit(onSubmit)} className="stack-md">
          <Select
            {...form.register('travel_type')}
          >
            <option value="business">Business</option>
            <option value="personal">Personal</option>
            <option value="training">Training</option>
            <option value="conference">Conference</option>
            <option value="relocation">Relocation</option>
            <option value="other">Other</option>
          </Select>

          <div className="grid grid-cols-2 gap-md">
            <Input               label="Destination"
              placeholder="City, location"
              {...form.register('destination')}

            />

            <Input               label="Country"
              placeholder="Country name"
              {...form.register('country')}

            />
          </div>

          <Input             label="Purpose"
            placeholder="Purpose of travel"
            {...form.register('purpose')}

          />

          <div className="grid grid-cols-3 gap-md">
            <Input               label="Start Date"
              type="date"
              {...form.register('start_date')}

            />

            <Input               label="End Date"
              type="date"
              {...form.register('end_date')}

            />

            <Input               label="Duration (Days)"
              type="number"
              {...form.register('duration_days', { valueAsNumber: true })}

              disabled
            />
          </div>

          <div className="grid grid-cols-2 gap-md">
            <Input               label="Accommodation"
              placeholder="Hotel, residence, etc."
              {...form.register('accommodation')}

            />

            <Input               label="Transportation"
              placeholder="Flight, car, ship, etc."
              {...form.register('transportation')}

            />
          </div>

          <div className="grid grid-cols-2 gap-md">
            <div className="flex items-center cluster-sm">
              <input
                type="checkbox"
                id="visa_required"
                {...form.register('visa_required')}
                className="rounded border-border"
              />
              <label htmlFor="visa_required" className="text-body-sm form-label">
                Visa required for this trip
              </label>
            </div>

            {form.watch('visa_required') && (
              <Select
                {...form.register('visa_status')}
              >
                <option value="pending">Pending</option>
                <option value="approved">Approved</option>
                <option value="denied">Denied</option>
              </Select>
            )}
          </div>

          <div className="grid grid-cols-2 gap-md">
            <Input               label="Passport Used"
              placeholder="Passport number or type"
              {...form.register('passport_used')}

            />

            <Input               label="Expenses ($)"
              type="number"
              placeholder="0"
              {...form.register('expenses', { valueAsNumber: true })}

            />
          </div>

          <Textarea
            label="Notes"
            placeholder="Additional notes about the trip"
            {...form.register('notes')}

            rows={3}
          />

          <div className="flex justify-end gap-sm pt-md">
            <Button
              type="button"
              variant="outline"
              onClick={() => setDrawerOpen(false)}
            >
              Cancel
            </Button>
            <Button type="submit" loading={saving}>
              <Save className="h-icon-xs w-icon-xs mr-sm" />
              {editingRecord ? 'Update' : 'Save'} Record
            </Button>
          </div>
        </form>
      </Drawer>
    </div>
  );
}

'use client';


import React from "react";

import {
  Badge,
  Button,
  Card,
  Input,
  UnifiedInput,
  cn
} from "@ghxstship/ui";
import { Edit } from "lucide-react";
import { useState, useEffect } from 'react'
import { createClient } from "../../../../../lib/supabase/client"

interface ClientProps {
  className?: string
}

export default function Client({ className }: ClientProps) {
  const [data, setData] = useState<any[]>([])
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    let isMounted = true
    const supabase = createClient()
    async function fetchPreferences() {
      try {
        setLoading(true)
        const { data: auth } = await supabase.auth.getUser()
        let items: unknown[] = []
        if (auth?.user) {
          const { data: prefs, error } = await supabase
            .from('user_travel_preferences')
            .select('id, preference_name, status')
            .eq('user_id', auth.user.id)
            .limit(25)
          if (!error && prefs) {
            const mockData: Array<{ id: string; preference_name: string; status: string }> = [
              { id: '1', preference_name: 'Window Seat', status: 'Active' },
              { id: '2', preference_name: 'Aisle Seat', status: 'Active' },
            ]
            items = prefs.map((p: any) => ({
              id: p.id,
              name: p.preference_name || 'Preference',
              status: p.status || 'active'
            }))
          }
        }
        if (isMounted) setData(items)
      } catch (err) {
        // console.error('TravelPreferencesClient fetch error:', err)
        if (isMounted) setData([])
      } finally {
        if (isMounted) setLoading(false)
      }
    }
    fetchPreferences()
    return () => { isMounted = false }
  }, [])

  if (loading) {
    return (
      <div className={className}>
        <Card className="p-md">
          <div className="animate-pulse space-y-md">
            <div className="h-icon-xs bg-secondary rounded w-1/4"></div>
            <div className="h-icon-xs bg-secondary rounded w-1/2"></div>
            <div className="h-icon-xs bg-secondary rounded w-3/4"></div>
          </div>
        </Card>
      </div>
    )
  }

  return (
    <div className={className}>
      <div className="mb-md">
        <h1 className="text-heading-lg text-heading-3 mb-sm">Travel Preferences</h1>
        <p className="color-foreground-subtle">Manage your travel and accommodation preferences</p>
      </div>

      <div className="mb-md">
        <Input onChange={(e: React.ChangeEvent<HTMLInputElement>) => {}} placeholder="Search travel preferences..." className="w-full sm:w-auto sm:max-w-sm" aria- />
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-md">
        {data.map((item: any) => (
          <Card key={item.id} className="p-md">
            <div className="flex flex-col sm:flex-row items-center justify-between">
              <div>
                <h2 className="text-body text-heading-3">{item.name}</h2>
                <p className="text-body-sm color-foreground-subtle">ID: {item.id}</p>
              </div>
              <div className="flex flex-col sm:flex-row items-center gap-sm">
                <Badge variant={item.status === 'active' ? 'default' : 'secondary'}>
                  {item.status}
                </Badge>
                <Button variant="default" size="sm" onClick={() => console.log("Edit clicked")}>Edit</Button>
              </div>
            </div>
          </Card>
        ))}
      </div>
    </div>
  )
}

'use client';

import { X, Save, Plane, Calendar, CreditCard, Globe, ShieldCheck } from "lucide-react";
import { useEffect, useMemo, useState } from 'react';
import {
 Button,
 Input,
 Textarea,
 Select,
 SelectContent,
 SelectItem,
 SelectTrigger,
 SelectValue,
 Label,
 Checkbox,
 Badge
} from '@ghxstship/ui';
import type { TravelRecord, TravelFormData, TravelType, VisaStatus, TravelStatus } from '../types';
import {
 TRAVEL_TYPE_LABELS,
 VISA_STATUS_LABELS,
 TRAVEL_STATUS_LABELS,
 COMMON_COUNTRIES,
 CURRENCIES,
 createEmptyFormData,
 validateTravelForm
} from '../types';

interface CreateTravelRecordDrawerProps {
 isOpen: boolean;
 onClose: () => void;
 onSave: (data: TravelFormData) => Promise<void>;
 record?: TravelRecord | null;
 loading?: boolean;
 recentCountries?: string[];
 recentDestinations?: string[];
}

export default function CreateTravelRecordDrawer({
 isOpen,
 onClose,
 onSave,
 record,
 loading = false,
 recentCountries = [],
 recentDestinations = []
}: CreateTravelRecordDrawerProps) {
 const [formData, setFormData] = useState<TravelFormData>(createEmptyFormData());
 const [formErrors, setFormErrors] = useState<Record<string, string>({});

 useEffect(() => {
 if (!isOpen) {
 return;
 }

 if (record) {
 setFormData({
 travel_type: record.travel_type,
 destination: record.destination,
 country: record.country,
 purpose: record.purpose,
 start_date: record.start_date,
 end_date: record.end_date,
 accommodation: record.accommodation || '',
 transportation: record.transportation || '',
 visa_required: record.visa_required,
 visa_status: record.visa_status,
 passport_used: record.passport_used || '',
 notes: record.notes || '',
 expenses: record.expenses !== null && record.expenses !== undefined ? record.expenses.toString() : '',
 currency: record.currency || 'USD',
 status: record.status,
 booking_reference: record.booking_reference || '',
 emergency_contact: record.emergency_contact || ''
 });
 } else {
 setFormData(createEmptyFormData());
 }

 setFormErrors({});
 }, [isOpen, record]);

 const countryOptions = useMemo(() => {
 const combined = new Set<string>([
 ...recentCountries,
 ...COMMON_COUNTRIES,
 ]);
 return Array.from(combined).sort();
 }, [recentCountries]);

 const destinationSuggestions = useMemo(() => {
 return Array.from(new Set(recentDestinations)).slice(0, 10);
 }, [recentDestinations]);

 if (!isOpen) return null;

 const handleInputChange = (field: keyof TravelFormData, value: string | boolean) => {
 setFormData(prev => ({
 ...prev,
 [field]: value
 }));

 if (formErrors[field as string]) {
 setFormErrors(prev => {
 const next = { ...prev };
 delete next[field as string];
 return next;
 });
 }
 };

 const handleSubmit = async () => {
 const errors = validateTravelForm(formData);
 if (Object.keys(errors).length > 0) {
 setFormErrors(errors);
 return;
 }

 await onSave(formData);
 };

 return (
 <div className="fixed inset-0 z-50 overflow-hidden">
 <div className="absolute inset-0 bg-black/50" onClick={onClose} />
 <div className="absolute right-0 top-0 h-full w-full max-w-2xl bg-background shadow-xl">
 <div className="flex h-full flex-col">
 {/* Header */}
 <div className="flex items-center justify-between border-b p-lg">
 <div>
 <h2 className="text-xl font-semibold">
 {record ? 'Edit Travel Record' : 'Create Travel Record'}
 </h2>
 <p className="text-sm text-muted-foreground">
 {record ? 'Update the details of this travel record' : 'Log a new trip with full travel details'}
 </p>
 </div>
 <Button variant="ghost" size="sm" onClick={onClose}>
 <X className="h-icon-xs w-icon-xs" />
 </Button>
 </div>

 {/* Content */}
 <div className="flex-1 overflow-y-auto p-lg">
 <div className="grid grid-cols-1 gap-lg">
 {/* Trip Overview */}
 <div className="space-y-md">
 <div className="flex items-center gap-xs">
 <Plane className="h-icon-sm w-icon-sm text-primary" />
 <h3 className="font-medium">Trip Overview</h3>
 </div>
 <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
 <div>
 <Label htmlFor="travel_type">Travel Type</Label>
 <Select
 value={formData.travel_type}
 onChange={(e) => handleInputChange('travel_type', e.target.value as TravelType)}
 disabled={loading}
 >
 <SelectTrigger>
 <SelectValue />
 </SelectTrigger>
 <SelectContent>
 {Object.entries(TRAVEL_TYPE_LABELS).map(([value, label]) => (
 <SelectItem key={value} value={value}>
 {label}
 </SelectItem>
 ))}
 </SelectContent>
 </Select>
 </div>
 <div>
 <Label htmlFor="status">Travel Status</Label>
 <Select
 value={formData.status}
 onChange={(e) => handleInputChange('status', e.target.value as TravelStatus)}
 disabled={loading}
 >
 <SelectTrigger>
 <SelectValue />
 </SelectTrigger>
 <SelectContent>
 {Object.entries(TRAVEL_STATUS_LABELS).map(([value, label]) => (
 <SelectItem key={value} value={value}>
 {label}
 </SelectItem>
 ))}
 </SelectContent>
 </Select>
 </div>
 </div>

 <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
 <div>
 <Label htmlFor="destination">Destination</Label>
 <Input

 placeholder="City or location"
 value={formData.destination}
 onChange={(event) => handleInputChange('destination', event.target.value)}
 disabled={loading}
 list="travel-destination-suggestions"
 />
 {formErrors.destination && (
 <p className="text-sm text-destructive">{formErrors.destination}</p>
 )}
 {destinationSuggestions.length > 0 && (
 <datalist >
 {destinationSuggestions.map(destination => (
 <option key={destination} value={destination} />
 ))}
 </datalist>
 )}
 </div>
 <div>
 <Label htmlFor="country">Country</Label>
 <Select
 value={formData.country || ''}
 onChange={(e) => handleInputChange('country', value)}
 disabled={loading}
 >
 <SelectTrigger>
 <SelectValue placeholder="Select country" />
 </SelectTrigger>
 <SelectContent>
 {countryOptions.map(country => (
 <SelectItem key={country} value={country}>
 {country}
 </SelectItem>
 ))}
 </SelectContent>
 </Select>
 {formErrors.country && (
 <p className="text-sm text-destructive">{formErrors.country}</p>
 )}
 </div>
 </div>

 <div>
 <Label htmlFor="purpose">Purpose</Label>
 <Input

 placeholder="Trip purpose"
 value={formData.purpose}
 onChange={(event) => handleInputChange('purpose', event.target.value)}
 disabled={loading}
 />
 {formErrors.purpose && (
 <p className="text-sm text-destructive">{formErrors.purpose}</p>
 )}
 </div>
 </div>

 {/* Travel Dates */}
 <div className="space-y-md">
 <div className="flex items-center gap-xs">
 <Calendar className="h-icon-sm w-icon-sm text-primary" />
 <h3 className="font-medium">Travel Dates</h3>
 </div>
 <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
 <div>
 <Label htmlFor="start_date">Start Date</Label>
 <Input

 type="date"
 value={formData.start_date}
 onChange={(event) => handleInputChange('start_date', event.target.value)}
 disabled={loading}
 />
 {formErrors.start_date && (
 <p className="text-sm text-destructive">{formErrors.start_date}</p>
 )}
 </div>
 <div>
 <Label htmlFor="end_date">End Date</Label>
 <Input

 type="date"
 value={formData.end_date}
 onChange={(event) => handleInputChange('end_date', event.target.value)}
 disabled={loading}
 />
 {formErrors.end_date && (
 <p className="text-sm text-destructive">{formErrors.end_date}</p>
 )}
 </div>
 </div>
 </div>

 {/* Travel Logistics */}
 <div className="space-y-md">
 <div className="flex items-center gap-xs">
 <Globe className="h-icon-sm w-icon-sm text-primary" />
 <h3 className="font-medium">Travel Logistics</h3>
 </div>
 <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
 <div>
 <Label htmlFor="accommodation">Accommodation</Label>
 <Input

 placeholder="Hotel, Airbnb, etc."
 value={formData.accommodation}
 onChange={(event) => handleInputChange('accommodation', event.target.value)}
 disabled={loading}
 />
 </div>
 <div>
 <Label htmlFor="transportation">Transportation</Label>
 <Input

 placeholder="Flight, car, train, etc."
 value={formData.transportation}
 onChange={(event) => handleInputChange('transportation', event.target.value)}
 disabled={loading}
 />
 </div>
 </div>
 <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
 <div>
 <Label htmlFor="booking_reference">Booking Reference</Label>
 <Input

 placeholder="Booking confirmation number"
 value={formData.booking_reference}
 onChange={(event) => handleInputChange('booking_reference', event.target.value)}
 disabled={loading}
 />
 </div>
 <div>
 <Label htmlFor="emergency_contact">Emergency Contact</Label>
 <Input

 placeholder="Name, phone, or email"
 value={formData.emergency_contact}
 onChange={(event) => handleInputChange('emergency_contact', event.target.value)}
 disabled={loading}
 />
 </div>
 </div>
 </div>

 {/* Visa & Passport */}
 <div className="space-y-md">
 <div className="flex items-center gap-xs">
 <ShieldCheck className="h-icon-sm w-icon-sm text-primary" />
 <h3 className="font-medium">Visa & Passport</h3>
 </div>
 <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
 <div className="flex items-center gap-xs">
 <Checkbox

 checked={formData.visa_required}
 onCheckedChange={(checked) => handleInputChange('visa_required', !!checked)}
 disabled={loading}
 />
 <Label htmlFor="visa_required">Visa required for this trip</Label>
 </div>
 {formData.visa_required && (
 <div>
 <Label htmlFor="visa_status">Visa Status</Label>
 <Select
 value={formData.visa_status}
 onChange={(e) => handleInputChange('visa_status', e.target.value as VisaStatus)}
 disabled={loading}
 >
 <SelectTrigger>
 <SelectValue />
 </SelectTrigger>
 <SelectContent>
 {Object.entries(VISA_STATUS_LABELS).map(([value, label]) => (
 <SelectItem key={value} value={value}>
 {label}
 </SelectItem>
 ))}
 </SelectContent>
 </Select>
 </div>
 )}
 </div>
 <div>
 <Label htmlFor="passport_used">Passport Used</Label>
 <Input

 placeholder="Passport number or descriptor"
 value={formData.passport_used}
 onChange={(event) => handleInputChange('passport_used', event.target.value)}
 disabled={loading}
 />
 </div>
 </div>

 {/* Expenses */}
 <div className="space-y-md">
 <div className="flex items-center gap-xs">
 <CreditCard className="h-icon-sm w-icon-sm text-primary" />
 <h3 className="font-medium">Travel Expenses</h3>
 </div>
 <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
 <div>
 <Label htmlFor="expenses">Total Expenses</Label>
 <Input

 type="number"
 min="0"
 placeholder="0"
 value={formData.expenses}
 onChange={(event) => handleInputChange('expenses', event.target.value)}
 disabled={loading}
 />
 {formErrors.expenses && (
 <p className="text-sm text-destructive">{formErrors.expenses}</p>
 )}
 </div>
 <div>
 <Label htmlFor="currency">Currency</Label>
 <Select
 value={formData.currency}
 onChange={(e) => handleInputChange('currency', value)}
 disabled={loading}
 >
 <SelectTrigger>
 <SelectValue />
 </SelectTrigger>
 <SelectContent>
 {CURRENCIES.map(currency => (
 <SelectItem key={currency.code} value={currency.code}>
 {currency.code}  {currency.name}
 </SelectItem>
 ))}
 </SelectContent>
 </Select>
 </div>
 </div>
 </div>

 {/* Notes */}
 <div className="space-y-xs">
 <Label htmlFor="notes">Notes</Label>
 <Textarea

 placeholder="Additional notes, travel instructions, or important details"
 value={formData.notes}
 onChange={(event) => handleInputChange('notes', event.target.value)}
 disabled={loading}
 rows={4}
 />
 </div>
 </div>
 </div>

 {/* Footer */}
 <div className="border-t p-lg">
 <div className="flex items-center justify-end gap-sm">
 <Button variant="outline" onClick={onClose} disabled={loading}>
 Cancel
 </Button>
 <Button onClick={handleSubmit} disabled={loading}>
 <Save className="h-icon-xs w-icon-xs mr-2" />
 {loading ? 'Saving...' : record ? 'Update Trip' : 'Create Trip'}
 </Button>
 </div>
 </div>
 </div>
 </div>
 </div>
 );
}

'use client';

import { Calendar, User } from "lucide-react";
import { Card, Badge, Avatar } from '@ghxstship/ui';
import type { UserProfile, FieldConfig } from '../types';

interface ProfileCalendarViewProps {
 profiles: UserProfile[];
 loading: boolean;
 selectedIds: string[];
 onSelectionChange: (ids: string[]) => void;
 onEdit: (profile: UserProfile) => void;
 fieldConfig: FieldConfig[];
}

export default function ProfileCalendarView({
 profiles,
 loading,
 selectedIds,
 onSelectionChange,
 onEdit,
 fieldConfig
}: ProfileCalendarViewProps) {
 if (loading) {
 return (
 <Card className="p-xl text-center">
 <div className="animate-pulse space-y-md">
 <div className="w-icon-2xl h-icon-2xl bg-muted rounded-full mx-auto" />
 <div className="w-container-xs h-icon-xs bg-muted rounded mx-auto" />
 <div className="w-component-xl h-3 bg-muted rounded mx-auto" />
 </div>
 </Card>
 );
 }

 // Group profiles by hire date for calendar view
 const profilesByDate = profiles.reduce((acc, profile) => {
 if (profile.hire_date) {
 const date = new Date(profile.hire_date).toDateString();
 if (!acc[date]) {
 acc[date] = [];
 }
 acc[date].push(profile);
 }
 return acc;
 }, {} as Record<string, UserProfile[]>);

 const sortedDates = Object.keys(profilesByDate).sort((a, b) => 
 new Date(b).getTime() - new Date(a).getTime()
 );

 return (
 <div className="space-y-lg">
 <div className="flex items-center gap-sm mb-lg">
 <Calendar className="h-icon-sm w-icon-sm" />
 <h2 className="text-lg font-semibold">Profiles by Hire Date</h2>
 </div>

 {sortedDates.length === 0 ? (
 <Card className="p-xl text-center">
 <Calendar className="h-icon-2xl w-icon-2xl text-muted-foreground mx-auto mb-md" />
 <h3 className="text-lg font-semibold mb-sm">No hire dates available</h3>
 <p className="text-muted-foreground">
 Profiles with hire dates will appear here in a timeline view.
 </p>
 </Card>
 ) : (
 <div className="space-y-md">
 {sortedDates.map((dateString) => {
 const date = new Date(dateString);
 const dateProfiles = profilesByDate[dateString];

 return (
 <Card key={dateString} className="p-md">
 <div className="flex items-center gap-md mb-md">
 <div className="flex-shrink-0">
 <div className="w-icon-2xl h-icon-2xl bg-primary/10 rounded-full flex items-center justify-center">
 <Calendar className="h-icon-md w-icon-md text-primary" />
 </div>
 </div>
 <div>
 <h3 className="font-semibold">
 {date.toLocaleDateString('en-US', { 
 weekday: 'long',
 year: 'numeric',
 month: 'long',
 day: 'numeric'
 })}
 </h3>
 <p className="text-sm text-muted-foreground">
 {dateProfiles.length} profile{dateProfiles.length !== 1 ? 's' : ''} hired
 </p>
 </div>
 </div>

 <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-md">
 {dateProfiles.map((profile) => (
 <div
 key={profile.id}
 className="flex items-center gap-sm p-sm rounded-lg border cursor-pointer hover:bg-muted/50 transition-colors"
 onClick={() => onEdit(profile)}
 >
 <Avatar className="h-icon-lg w-icon-lg">
 {profile.avatar_url ? (
 <img src={profile.avatar_url} alt={profile.full_name} />
 ) : (
 <div className="flex items-center justify-center h-full w-full bg-primary/10 text-primary font-semibold text-xs">
 {profile.full_name.split(' ').map(n => n[0]).join('').toUpperCase()}
 </div>
 )}
 </Avatar>
 <div className="flex-1 min-w-0">
 <p className="font-medium text-sm truncate">{profile.full_name}</p>
 <p className="text-xs text-muted-foreground truncate">
 {profile.position}  {profile.department}
 </p>
 </div>
 <Badge 
 variant={profile.status === 'active' ? 'success' : 'secondary'}
 size="sm"
 >
 {profile.status}
 </Badge>
 </div>
 ))}
 </div>
 </Card>
 );
 })}
 </div>
 )}
 </div>
 );
}

'use client';

import { User, Mail, Phone, MapPin, Calendar, MoreHorizontal, Edit, Eye, Trash2, UserCheck, Building } from "lucide-react";
import { useState } from 'react';
import {
  Avatar,
  Badge,
  Button,
  Card,
  Checkbox,
  Dropdown,
  DropdownItem,
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger
} from "@ghxstship/ui";

import type { UserProfile, FieldConfig } from '../types';

interface ProfileGridViewProps {
 profiles: UserProfile[];
 loading: boolean;
 selectedIds: string[];
 onSelectionChange: (ids: string[]) => void;
 onEdit: (profile: UserProfile) => void;
 fieldConfig: FieldConfig[];
}

export default function ProfileGridView({
 profiles,
 loading,
 selectedIds,
 onSelectionChange,
 onEdit,
 fieldConfig
}: ProfileGridViewProps) {
 const [hoveredId, setHoveredId] = useState<string | null>(null);

 const handleSelectProfile = (profileId: string, checked: boolean) => {
 if (checked) {
 onSelectionChange([...selectedIds, profileId]);
 } else {
 onSelectionChange(selectedIds.filter(id => id !== profileId));
 }
 };

 const handleSelectAll = (checked: boolean) => {
 if (checked) {
 onSelectionChange(profiles.map(p => p.id));
 } else {
 onSelectionChange([]);
 }
 };

 const getStatusColor = (status: string) => {
 switch (status) {
 case 'active':
 return 'success';
 case 'inactive':
 return 'destructive';
 case 'pending':
 return 'warning';
 case 'suspended':
 return 'destructive';
 default:
 return 'secondary';
 }
 };

 const getCompletionColor = (percentage: number) => {
 if (percentage >= 90) return 'text-green-600';
 if (percentage >= 70) return 'text-yellow-600';
 return 'text-red-600';
 };

 if (loading) {
 return (
 <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
 {Array.from({ length: 8 }).map((_, i) => (
 <Card key={i} className="p-lg animate-pulse">
 <div className="flex items-start gap-md">
 <div className="w-icon-2xl h-icon-2xl bg-muted rounded-full" />
 <div className="flex-1 space-y-sm">
 <div className="h-icon-xs bg-muted rounded w-3/4" />
 <div className="h-3 bg-muted rounded w-1/2" />
 <div className="h-3 bg-muted rounded w-2/3" />
 </div>
 </div>
 </Card>
 ))}
 </div>
 );
 }

 if (profiles.length === 0) {
 return (
 <Card className="p-xl text-center">
 <User className="h-icon-2xl w-icon-2xl text-muted-foreground mx-auto mb-md" />
 <h3 className="text-lg font-semibold mb-sm">No profiles found</h3>
 <p className="text-muted-foreground mb-lg">
 Get started by creating your first profile or adjust your filters.
 </p>
 <Button>Create Profile</Button>
 </Card>
 );
 }

 return (
 <div className="space-y-md">
 {/* Header with bulk selection */}
 <div className="flex items-center gap-sm">
 <Checkbox
 checked={selectedIds.length === profiles.length}
 onCheckedChange={handleSelectAll}
 indeterminate={selectedIds.length > 0 && selectedIds.length < profiles.length}
 />
 <span className="text-sm text-muted-foreground">
 {selectedIds.length > 0 ? `${selectedIds.length} selected` : 'Select all'}
 </span>
 </div>

 {/* Grid */}
 <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-md">
 {profiles.map((profile) => (
 <Card
 key={profile.id}
 className={`p-lg transition-all duration-200 hover:shadow-md cursor-pointer ${
 selectedIds.includes(profile.id) ? 'ring-2 ring-primary' : ''
 }`}
 onMouseEnter={() => setHoveredId(profile.id)}
 onMouseLeave={() => setHoveredId(null)}
 >
 {/* Header with selection and actions */}
 <div className="flex items-start justify-between mb-md">
 <Checkbox
 checked={selectedIds.includes(profile.id)}
 onCheckedChange={(checked) => handleSelectProfile(profile.id, checked as boolean)}
 onClick={(e) => e.stopPropagation()}
 />

 <DropdownMenu>
 <DropdownMenuTrigger asChild>
 <Button
 variant="ghost"
 size="sm"
 className={`h-icon-lg w-icon-lg p-0 transition-opacity ${
 hoveredId === profile.id ? 'opacity-100' : 'opacity-0'
 }`}
 onClick={(e) => e.stopPropagation()}
 >
 <MoreHorizontal className="h-icon-xs w-icon-xs" />
 </Button>
 </DropdownMenuTrigger>
 <DropdownMenuContent align="end">
 <DropdownMenuItem onClick={() => onEdit(profile)}>
 <Eye className="h-icon-xs w-icon-xs mr-sm" />
 View Details
 </DropdownMenuItem>
 <DropdownMenuItem onClick={() => onEdit(profile)}>
 <Edit className="h-icon-xs w-icon-xs mr-sm" />
 Edit Profile
 </DropdownMenuItem>
 <DropdownMenuItem className="text-destructive">
 <Trash2 className="h-icon-xs w-icon-xs mr-sm" />
 Delete Profile
 </DropdownMenuItem>
 </DropdownMenuContent>
 </DropdownMenu>
 </div>

 {/* Profile Avatar and Basic Info */}
 <div className="flex items-start gap-md mb-md">
 <Avatar className="h-icon-2xl w-icon-2xl">
 {profile.avatar_url ? (
 <img src={profile.avatar_url} alt={profile.full_name} />
 ) : (
 <div className="flex items-center justify-center h-full w-full bg-primary/10 text-primary font-semibold">
 {profile.full_name.split(' ').map(n => n[0]).join('').toUpperCase()}
 </div>
 )}
 </Avatar>

 <div className="flex-1 min-w-0">
 <h3 className="font-semibold text-sm truncate" title={profile.full_name}>
 {profile.full_name}
 </h3>
 <p className="text-xs text-muted-foreground truncate" title={profile.position}>
 {profile.position || 'No position set'}
 </p>
 <Badge 
 variant={getStatusColor(profile.status) as unknown} 
 size="sm" 
 className="mt-xs"
 >
 {profile.status}
 </Badge>
 </div>
 </div>

 {/* Contact Information */}
 <div className="space-y-xs mb-md">
 <div className="flex items-center gap-xs text-xs text-muted-foreground">
 <Mail className="h-3 w-3 flex-shrink-0" />
 <span className="truncate" title={profile.email}>
 {profile.email}
 </span>
 </div>

 {profile.phone && (
 <div className="flex items-center gap-xs text-xs text-muted-foreground">
 <Phone className="h-3 w-3 flex-shrink-0" />
 <span>{profile.phone}</span>
 </div>
 )}

 {profile.department && (
 <div className="flex items-center gap-xs text-xs text-muted-foreground">
 <Building className="h-3 w-3 flex-shrink-0" />
 <span className="truncate">{profile.department}</span>
 </div>
 )}
 </div>

 {/* Profile Completion */}
 <div className="space-y-xs mb-md">
 <div className="flex items-center justify-between text-xs">
 <span className="text-muted-foreground">Profile Completion</span>
 <span className={`font-medium ${getCompletionColor(profile.completion_percentage)}`}>
 {profile.completion_percentage}%
 </span>
 </div>
 <div className="w-full bg-muted rounded-full h-1.5">
 <div 
 className="bg-primary h-1.5 rounded-full transition-all duration-300" 
 style={{ width: `${profile.completion_percentage}%` }}
 />
 </div>
 </div>

 {/* Additional Stats */}
 <div className="grid grid-cols-2 gap-md text-center">
 <div>
 <p className="text-xs font-medium">{profile.certifications_count || 0}</p>
 <p className="text-xs text-muted-foreground">Certifications</p>
 </div>
 <div>
