'use client';


import { useEffect, useState } from 'react';
import { Button, Card, Badge } from '@ghxstship/ui';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { 
  Calendar, 
  ChevronLeft, 
  ChevronRight, 
  Plus, 
  Clock, 
  MapPin,
  Users,
  Filter
} from 'lucide-react';

interface Event {
  id: string;
  name: string;
  kind: 'performance' | 'activation' | 'workshop';
  starts_at: string;
  ends_at: string;
  project_name: string;
  status: string;
}

export default function CalendarClient({ orgId }: { orgId: string }) {
  const t = useTranslations('programming');
  const sb = createBrowserClient();

  const [events, setEvents] = useState<Event[]>([]);
  const [loading, setLoading] = useState(false);
  const [currentDate, setCurrentDate] = useState(new Date());
  const [viewMode, setViewMode] = useState<'month' | 'week' | 'day'>('month');
  const [selectedFilters, setSelectedFilters] = useState<string[]>([]);

  useEffect(() => {
    loadEvents();
  }, [orgId, currentDate]);

  const loadEvents = async () => {
    if (!orgId) return;

    try {
      setLoading(true);

      // Get start and end of current month for filtering
      const startOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1);
      const endOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0);

      const { data: eventsData, error } = await sb
        .from('events')
        .select(`
          *,
          projects!inner(
            name,
            organization_id
          )
        `)
        .eq('projects.organization_id', orgId)
        .gte('starts_at', startOfMonth.toISOString())
        .lte('starts_at', endOfMonth.toISOString())
        .order('starts_at', { ascending: true });

      if (error) throw error;

      const transformedEvents = (eventsData || []).map(event => ({
        ...event,
        project_name: event.projects?.name || 'Unknown Project',
        status: computeEventStatus(event)
      }));

      setEvents(transformedEvents);
    } catch (error) {
      console.error('Error loading events:', error);
    } finally {
      setLoading(false);
    }
  };

  const computeEventStatus = (event: any) => {
    const now = new Date();
    const startDate = event.starts_at ? new Date(event.starts_at) : null;
    const endDate = event.ends_at ? new Date(event.ends_at) : null;

    if (!startDate) return 'draft';
    if (startDate > now) return 'scheduled';
    if (endDate && endDate < now) return 'completed';
    if (startDate <= now && (!endDate || endDate >= now)) return 'in_progress';

    return 'draft';
  };

  const navigateMonth = (direction: 'prev' | 'next') => {
    const newDate = new Date(currentDate);
    if (direction === 'prev') {
      newDate.setMonth(newDate.getMonth() - 1);
    } else {
      newDate.setMonth(newDate.getMonth() + 1);
    }
    setCurrentDate(newDate);
  };

  const getDaysInMonth = () => {
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const daysInMonth = lastDay.getDate();
    const startingDayOfWeek = firstDay.getDay();

    const days = [];

    // Add empty cells for days before the first day of the month
    for (let i = 0; i < startingDayOfWeek; i++) {
      days.push(null);
    }

    // Add days of the month
    for (let day = 1; day <= daysInMonth; day++) {
      days.push(new Date(year, month, day));
    }

    return days;
  };

  const getEventsForDate = (date: Date | null) => {
    if (!date) return [];

    return events.filter(event => {
      const eventDate = new Date(event.starts_at);
      return eventDate.toDateString() === date.toDateString();
    });
  };

  const getEventTypeColor = (kind: string) => {
    switch (kind) {
      case 'performance':
        return 'bg-accent';
      case 'activation':
        return 'bg-success';
      case 'workshop':
        return 'bg-secondary';
      default:
        return 'bg-secondary-foreground';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'scheduled':
        return 'color-accent bg-accent/10';
      case 'in_progress':
        return 'color-success bg-success/10';
      case 'completed':
        return 'color-muted bg-secondary/50';
      case 'cancelled':
        return 'color-destructive bg-destructive/10';
      default:
        return 'color-warning bg-warning/10';
    }
  };

  const monthNames = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December'
  ];

  const weekDays = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

  return (
    <div className="stack-lg">
      {/* Calendar Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-md">
          <div className="flex items-center gap-sm">
            <Button
              variant="outline"

              onClick={() => navigateMonth('prev')}
            >
              <ChevronLeft className="h-icon-xs w-icon-xs" />
            </Button>
            <h2 className="text-heading-4 text-heading-4 min-w-content-narrow text-center">
              {monthNames[currentDate.getMonth()]} {currentDate.getFullYear()}
            </h2>
            <Button
              variant="outline"

              onClick={() => navigateMonth('next')}
            >
              <ChevronRight className="h-icon-xs w-icon-xs" />
            </Button>
          </div>

          <Button
            variant="outline"

            onClick={() => setCurrentDate(new Date())}
          >
            Today
          </Button>
        </div>

        <div className="flex items-center gap-sm">
          <Button>
            <Filter className="h-icon-xs w-icon-xs mr-sm" />
            Filter
          </Button>
          <Button>
            <Plus className="h-icon-xs w-icon-xs mr-sm" />
            Create Event
          </Button>
        </div>
      </div>

      {/* Event Type Legend */}
      <div className="flex items-center gap-md text-body-sm">
        <div className="flex items-center gap-sm">
          <div className="w-3 h-3 rounded-full bg-accent"></div>
          <span>Performance</span>
        </div>
        <div className="flex items-center gap-sm">
          <div className="w-3 h-3 rounded-full bg-success"></div>
          <span>Activation</span>
        </div>
        <div className="flex items-center gap-sm">
          <div className="w-3 h-3 rounded-full bg-secondary"></div>
          <span>Workshop</span>
        </div>
      </div>

      {/* Calendar Grid */}
      <Card className="p-0">
        <div className="grid grid-cols-7 gap-0">
          {/* Week day headers */}
          {weekDays.map(day => (
            <div
              key={day}
              className="p-sm text-center text-body-sm form-label color-muted border-b border-r last:border-r-0"
            >
              {day}
            </div>
          ))}

          {/* Calendar days */}
          {getDaysInMonth().map((date, index) => {
            const dayEvents = getEventsForDate(date);
            const isToday = date && date.toDateString() === new Date().toDateString();

            return (
              <div
                key={index}
                className={`min-h-header-lg p-sm border-b border-r last:border-r-0 ${
                  date ? 'bg-background hover:bg-secondary/50' : 'bg-secondary/20'
                } ${isToday ? 'bg-accent/10' : ''}`}
              >
                {date && (
                  <>
                    <div className={`text-body-sm form-label mb-sm ${
                      isToday ? 'color-accent text-heading-3' : 'color-foreground'
                    }`}>
                      {date.getDate()}
                    </div>

                    <div className="stack-md">
                      {dayEvents.slice(0, 3).map(event => (
                        <div
                          key={event.id}
                          className="text-body-sm p-xs rounded cursor-pointer hover:opacity-80 transition-opacity"
                          style={{ backgroundColor: `${getEventTypeColor(event.kind)}20` }}
                        >
                          <div className="flex items-center gap-xs">
                            <div className={`w-2 h-2 rounded-full ${getEventTypeColor(event.kind)}`}></div>
                            <span className="truncate form-label">{event.name}</span>
                          </div>
                          <div className="color-muted mt-xs flex items-center gap-xs">
                            <Clock className="h-3 w-3" />
                            <span>
                              {new Date(event.starts_at).toLocaleTimeString([], { 
                                hour: '2-digit', 
                                minute: '2-digit' 
                              })}
                            </span>
                          </div>
                        </div>
                      ))}

                      {dayEvents.length > 3 && (
                        <div className="text-body-sm color-muted p-xs">
                          +{dayEvents.length - 3} more
                        </div>
                      )}
                    </div>
                  </>
                )}
              </div>
            );
          })}
        </div>
      </Card>

      {/* Upcoming Events List */}
      <Card>
        <div className="p-lg">
          <h3 className="text-body text-heading-4 mb-md">Upcoming Events This Month</h3>

          {loading ? (
            <div className="text-center py-xl color-muted">
              Loading events...
            </div>
          ) : events.length === 0 ? (
            <div className="text-center py-xl color-muted">
              <Calendar className="h-icon-2xl w-icon-2xl mx-auto mb-md opacity-50" />
              <p>No events scheduled for this month</p>
              <Button>
                <Plus className="h-icon-xs w-icon-xs mr-sm" />
                Create Your First Event
              </Button>
            </div>
          ) : (
            <div className="stack-sm">
              {events.slice(0, 5).map(event => (
                <div
                  key={event.id}
                  className="flex items-center justify-between p-sm rounded-lg border hover:bg-secondary/50 cursor-pointer transition-colors"
                >
                  <div className="flex items-center gap-sm">
                    <div className={`w-3 h-3 rounded-full ${getEventTypeColor(event.kind)}`}></div>
                    <div>
                      <div className="form-label">{event.name}</div>
                      <div className="text-body-sm color-muted flex items-center gap-md">
                        <span className="flex items-center gap-xs">
                          <Calendar className="h-3 w-3" />
                          {new Date(event.starts_at).toLocaleDateString()}
                        </span>
                        <span className="flex items-center gap-xs">
                          <Clock className="h-3 w-3" />
                          {new Date(event.starts_at).toLocaleTimeString([], { 
                            hour: '2-digit', 
                            minute: '2-digit' 
                          })}
                        </span>
                        <span className="flex items-center gap-xs">
                          <MapPin className="h-3 w-3" />
                          {event.project_name}
                        </span>
                      </div>
                    </div>
                  </div>

                  <div className="flex items-center gap-sm">
                    <Badge variant="outline">
                      {event.status.replace('_', ' ')}
                    </Badge>
                    <Badge variant="outline">
                      {event.kind}
                    </Badge>
                  </div>
                </div>
              ))}

              {events.length > 5 && (
                <div className="text-center pt-md">
                  <Button>
                    View All {events.length} Events
                  </Button>
                </div>
              )}
            </div>
          )}
        </div>
      </Card>
    </div>
  );
}

'use client';


import { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import {
  Badge,
  Button,
  Card,
  Drawer,
  Input,
  Select,
  Textarea,
  UnifiedInput
} from "@ghxstship/ui";
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { Plus, Calendar, Clock, MapPin, Users } from 'lucide-react';
import { usePostHog } from 'posthog-js/react';

// Form validation schema
const createEventSchema = z.object({
  name: z.string().min(1, 'Event name is required'),
  description: z.string().optional(),
  kind: z.enum(['performance', 'activation', 'workshop', 'meeting', 'rehearsal', 'setup', 'breakdown']),
  project_id: z.string().uuid('Please select a project'),
  start_at: z.string().min(1, 'Start time is required'),
  end_at: z.string().min(1, 'End time is required'),
  location: z.string().optional(),
  capacity: z.number().min(0).optional(),
  status: z.enum(['draft', 'scheduled', 'in_progress', 'completed', 'cancelled']).default('draft')
});

type CreateEventForm = z.infer<typeof createEventSchema>;

interface Project {
  id: string;
  name: string;
  status: string;
}

export default function CreateCalendarClient({ orgId }: { orgId: string }) {
  const t = useTranslations('programming');
  const sb = createBrowserClient();
  const posthog = usePostHog();

  const [isOpen, setIsOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [projects, setProjects] = useState<Project[]>([]);
  const [loadingProjects, setLoadingProjects] = useState(false);

  const form = useForm<CreateEventForm>({
    resolver: zodResolver(createEventSchema),
    defaultValues: {
      name: '',
      description: '',
      kind: 'performance',
      project_id: '',
      start_at: '',
      end_at: '',
      location: '',
      capacity: undefined,
      status: 'draft'
    }
  });

  // Load projects when drawer opens
  useEffect(() => {
    if (isOpen && projects.length === 0) {
      loadProjects();
    }
  }, [isOpen]);

  const loadProjects = async () => {
    setLoadingProjects(true);
    try {
      const { data, error } = await sb
        .from('projects')
        .select('id, name, status')
        .eq('organization_id', orgId)
        .eq('status', 'active')
        .order('name');

      if (error) throw error;
      setProjects(data || []);
    } catch (error) {
      console.error('Error loading projects:', error);
    } finally {
      setLoadingProjects(false);
    }
  };

  const onSubmit = async (data: CreateEventForm) => {
    setIsLoading(true);
    try {
      const { error } = await sb
        .from('events')
        .insert({
          ...data,
          organization_id: orgId,
          capacity: data.capacity || null
        });

      if (error) throw error;

      // Track event creation
      posthog?.capture('programming_event_created', {
        event_kind: data.kind,
        project_id: data.project_id,
        organization_id: orgId
      });

      // Reset form and close drawer
      form.reset();
      setIsOpen(false);

      // Refresh the page to show new event
      window.location.reload();
    } catch (error) {
      console.error('Error creating event:', error);
      // Error handling implemented
      alert('An error occurred while creating the event. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const eventKindOptions = [
    { value: 'performance', label: 'Performance' },
    { value: 'activation', label: 'Activation' },
    { value: 'workshop', label: 'Workshop' },
    { value: 'meeting', label: 'Meeting' },
    { value: 'rehearsal', label: 'Rehearsal' },
    { value: 'setup', label: 'Setup' },
    { value: 'breakdown', label: 'Breakdown' }
  ];

  const statusOptions = [
    { value: 'draft', label: 'Draft' },
    { value: 'scheduled', label: 'Scheduled' },
    { value: 'in_progress', label: 'In Progress' },
    { value: 'completed', label: 'Completed' },
    { value: 'cancelled', label: 'Cancelled' }
  ];

  return (
    <>
      <Button
        onClick={() => setIsOpen(true)}
        className="flex items-center gap-sm"

      >
        <Plus className="h-icon-xs w-icon-xs" />
        Create Event
      </Button>

      <Drawer
        open={isOpen}
        onClose={() => setIsOpen(false)}
        title="Create New Event"
        description="Add a new event to the programming calendar"
      >
        <form onSubmit={form.handleSubmit(onSubmit)} className="stack-lg">
          <div className="grid grid-cols-1 gap-md">
            <Input               label="Event Name"
              placeholder="Enter event name"
              {...form.register('name')}

              required
            />

            <Textarea
              label="Description"
              placeholder="Enter event description (optional)"
              {...form.register('description')}

              rows={3}
            />

            <div className="grid grid-cols-2 gap-md">
              <Select
                {...form.register('kind')}
              >
                <option value="">Select type</option>
                {eventKindOptions.map(option => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </Select>

              <Select
                {...form.register('status')}
              >
                {statusOptions.map(option => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </Select>
            </div>

            <Select
              {...form.register('project_id')}
              disabled={loadingProjects}
            >
              <option value="">
                {loadingProjects ? 'Loading projects...' : 'Select project'}
              </option>
              {projects.map(project => (
                <option key={project.id} value={project.id}>
                  {project.name}
                </option>
              ))}
            </Select>

            <div className="grid grid-cols-2 gap-md">
              <Input                 label="Start Time"
                type="datetime-local"
                {...form.register('start_at')}

                required
              />

              <Input                 label="End Time"
                type="datetime-local"
                {...form.register('end_at')}

                required
              />
            </div>

            <div className="grid grid-cols-2 gap-md">
              <Input                 label="Location"
                placeholder="Enter location (optional)"
                {...form.register('location')}

              />

              <Input                 label="Capacity"
                type="number"
                placeholder="Enter capacity (optional)"
                {...form.register('capacity', { valueAsNumber: true })}

                min={0}
              />
            </div>
          </div>

          <div className="flex justify-end gap-sm pt-md border-t">
            <Button
              type="button"
              variant="outline"
              onClick={() => setIsOpen(false)}
              disabled={isLoading}
            >
              Cancel
            </Button>
            <Button
              type="submit"
              loading={isLoading}
              className="flex items-center gap-sm"
            >
              <Calendar className="h-icon-xs w-icon-xs" />
              Create Event
            </Button>
          </div>
        </form>
      </Drawer>
    </>
  );
}

"use client";

import { Calendar, Clock, Edit, MapPin, Users, Activity, Tag, Briefcase, Radio, Settings, FileText } from "lucide-react";
import { AppDrawer, Badge, Button, Card } from "@ghxstship/ui";
import { format, parseISO, differenceInHours } from "date-fns";
import type { ProgrammingEvent } from "../types";
import { EVENT_TYPE_LABEL, STATUS_BADGE } from "../ProgrammingCalendarClient";

type ViewProgrammingEventDrawerProps = {
 open: boolean;
 onOpenChange: (open: boolean) => void;
 event: ProgrammingEvent;
 onEdit?: () => void;
 onDelete?: () => void;
 users: { id: string; email: string; full_name?: string | null; avatar_url?: string | null }[];
};

export default function ViewProgrammingEventDrawer({
 open,
 onOpenChange,
 event,
 onEdit,
 onDelete,
 users
}: ViewProgrammingEventDrawerProps) {
 const statusConfig = STATUS_BADGE[event.status];

 // Calculate event duration
 const getDuration = () => {
 if (!event.end_at) return null;
 const hours = differenceInHours(parseISO(event.end_at), parseISO(event.start_at));
 if (hours < 1) return "< 1 hour";
 if (hours === 1) return "1 hour";
 return `${hours} hours`;
 };

 // Get user by ID
 const getUserById = (userId?: string) => {
 if (!userId) return null;
 return users.find(user => user.id === userId);
 };

 return (
 <AppDrawer
 open={open}
 onClose={() => onOpenChange(false)}
 title={event.title}
 description={event.project?.name || "Programming Event"}
 icon={<Calendar className="h-icon-sm w-icon-sm" />}

 >
 <div className="space-y-lg">
 {/* Header Actions */}
 <div className="flex justify-between items-start">
 <div className="flex items-center gap-xs">
 <Badge variant={statusConfig.variant}>{statusConfig.label}</Badge>
 <Badge variant="outline">{EVENT_TYPE_LABEL[event.event_type]}</Badge>
 </div>
 {onEdit && (
 <Button
 variant="outline"
 size="sm"
 onClick={onEdit}
 >
 <Edit className="h-icon-xs w-icon-xs mr-2" />
 Edit
 </Button>
 )}
 </div>

 {/* Description */}
 {event.description && (
 <div>
 <h3 className="font-medium mb-2 flex items-center gap-xs">
 <FileText className="h-icon-xs w-icon-xs" />
 Description
 </h3>
 <p className="text-sm text-muted-foreground whitespace-pre-wrap">
 {event.description}
 </p>
 </div>
 )}

 {/* Schedule */}
 <div>
 <h3 className="font-medium mb-3 flex items-center gap-xs">
 <Clock className="h-icon-xs w-icon-xs" />
 Schedule
 </h3>
 <div className="space-y-sm">
 <div className="flex items-center justify-between p-sm bg-muted/50 rounded-lg">
 <span className="text-sm">Start Time</span>
 <span className="text-sm font-medium">
 {format(parseISO(event.start_at), "MMMM d, yyyy 'at' h:mm a")}
 </span>
 </div>

 {event.end_at && (
 <div className="flex items-center justify-between p-sm bg-muted/50 rounded-lg">
 <span className="text-sm">End Time</span>
 <span className="text-sm font-medium">
 {format(parseISO(event.end_at), "MMMM d, yyyy 'at' h:mm a")}
 </span>
 </div>
 )}

 {getDuration() && (
 <div className="flex items-center justify-between p-sm bg-muted/50 rounded-lg">
 <span className="text-sm">Duration</span>
 <span className="text-sm font-medium">{getDuration()}</span>
 </div>
 )}

 {event.setup_start && (
 <div className="flex items-center justify-between p-sm bg-info/10 rounded-lg">
 <span className="text-sm">Setup Start</span>
 <span className="text-sm font-medium">
 {format(parseISO(event.setup_start), "MMMM d, yyyy 'at' h:mm a")}
 </span>
 </div>
 )}

 {event.teardown_end && (
 <div className="flex items-center justify-between p-sm bg-info/10 rounded-lg">
 <span className="text-sm">Teardown End</span>
 <span className="text-sm font-medium">
 {format(parseISO(event.teardown_end), "MMMM d, yyyy 'at' h:mm a")}
 </span>
 </div>
 )}
 </div>
 </div>

 {/* Location & Capacity */}
 {(event.location || event.capacity) && (
 <div>
 <h3 className="font-medium mb-3 flex items-center gap-xs">
 <MapPin className="h-icon-xs w-icon-xs" />
 Venue Details
 </h3>
 <div className="space-y-sm">
 {event.location && (
