 )}

 {/* Resources */}
 {event.resources && event.resources.length > 0 && (
 <div>
 <h3 className="font-medium mb-3 flex items-center gap-xs">
 <Settings className="h-icon-xs w-icon-xs" />
 Resources
 </h3>
