'use client';


import { useEffect, useState } from 'react';
import {
  Drawer,
  type Button,
  Card,
  Badge,
  StateManagerProvider,
  DataViewProvider,
  ViewSwitcher,
  DataActions,
  DataGrid,
  KanbanBoard,
  ListView,
  Button
} from '@ghxstship/ui';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { Plus, FileText, Calendar, Clock, Users, CheckCircle } from 'lucide-react';

export default function CallSheetsClient({ orgId }: { orgId: string }) {
  const t = useTranslations('programming');
  const sb = createBrowserClient();

  const [data, setData] = useState<DataRecord[]>([]);
  const [loading, setLoading] = useState(false);
  const [selectedRecord, setSelectedRecord] = useState<any>(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerMode, setDrawerMode] = useState<'view' | 'edit' | 'create'>('view');

  // Define field configuration for call sheets
  const fields: FieldConfig[] = [
    {
      key: 'event_name',
      label: 'Event',
      type: 'text',
      sortable: true,
      filterable: true,
      width: 200
    },
    {
      key: 'call_date',
      label: 'Call Date',
      type: 'date',
      required: true,
      sortable: true,
      filterable: true,
      width: 130
    },
    {
      key: 'call_time',
      label: 'Call Time',
      type: 'text',
      sortable: true,
      filterable: true,
      width: 120
    },
    {
      key: 'location',
      label: 'Location',
      type: 'text',
      sortable: true,
      filterable: true,
      width: 150
    },
    {
      key: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'draft', label: 'Draft' },
        { value: 'published', label: 'Published' },
        { value: 'distributed', label: 'Distributed' },
        { value: 'completed', label: 'Completed' }
      ],
      sortable: true,
      filterable: true,
      groupable: true,
      width: 120
    },
    {
      key: 'created_at',
      label: 'Created',
      type: 'date',
      sortable: true,
      filterable: true,
      width: 120
    }
  ];

  useEffect(() => {
    loadCallSheets();
  }, [orgId]);

  const loadCallSheets = async () => {
    if (!orgId) return;

    try {
      setLoading(true);

      const { data: callSheetsData, error } = await sb
        .from('call_sheets')
        .select(`
          *,
          events!inner(
            name,
            starts_at,
            projects!inner(
              organization_id
            )
          )
        `)
        .eq('events.projects.organization_id', orgId)
        .order('call_date', { ascending: false });

      if (error) throw error;

      // Transform data to include event name and computed fields
      const transformedData = (callSheetsData || []).map(callSheet => ({
        ...callSheet,
        event_name: callSheet.events?.name || 'Unknown Event',
        event_date: callSheet.events?.starts_at,
        status: callSheet.status || 'draft',
        call_time: callSheet.details?.call_time || 'TBD',
        location: callSheet.details?.location || 'TBD'
      }));

      setData(transformedData);
    } catch (error) {
      console.error('Error loading call sheets:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateCallSheet = () => {
    setSelectedRecord(null);
    setDrawerMode('create');
    setDrawerOpen(true);
  };

  const handleEditCallSheet = (callSheet: any) => {
    setSelectedRecord(callSheet);
    setDrawerMode('edit');
    setDrawerOpen(true);
  };

  const handleViewCallSheet = (callSheet: any) => {
    setSelectedRecord(callSheet);
    setDrawerMode('view');
    setDrawerOpen(true);
  };

  const handleSaveCallSheet = async (callSheetData: any) => {
    try {
      if (drawerMode === 'create') {
        const { error } = await sb.from('call_sheets').insert(callSheetData);
        if (error) throw error;
      } else if (drawerMode === 'edit') {
        const { error } = await sb
          .from('call_sheets')
          .update(callSheetData)
          .eq('id', selectedRecord.id);
        if (error) throw error;
      }

      await loadCallSheets();
      setDrawerOpen(false);
    } catch (error) {
      console.error('Error saving call sheet:', error);
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'draft':
        return 'color-muted bg-secondary';
      case 'published':
        return 'color-accent bg-accent/10';
      case 'distributed':
        return 'color-success bg-success/10';
      case 'completed':
        return 'color-secondary bg-secondary/10';
      default:
        return 'color-muted bg-secondary';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'completed':
        return CheckCircle;
      case 'distributed':
        return Users;
      case 'published':
        return FileText;
      default:
        return Clock;
    }
  };

  // Configure DataView
  const config: DataViewConfig = {
    id: 'call-sheets-management',
    name: 'Call Sheets Management',
    viewType: 'grid',
    defaultView: 'grid',
    fields,
    data,

    onSearch: (query: string) => {
      console.log('Search call sheets:', query);
    },
    onFilter: (filters: any[]) => {
      console.log('Filter call sheets:', filters);
    },
    onSort: (sorts: any[]) => {
      console.log('Sort call sheets:', sorts);
    },
    onRefresh: loadCallSheets,
    onExport: (data: any[], format: string) => {
      console.log('Export call sheets:', format, data);
    },
    onImport: (data: any[]) => {
      console.log('Import call sheets:', data);
    }
  };

  // Get upcoming call sheets for quick overview
  const upcomingCallSheets = data
    .filter(cs => new Date(cs.call_date) >= new Date())
    .sort((a, b) => new Date(a.call_date).getTime() - new Date(b.call_date).getTime())
    .slice(0, 3);

  const statusCounts = data.reduce((acc, cs) => {
    acc[cs.status] = (acc[cs.status] || 0) + 1;
    return acc;
  }, {});

  return (
    <div className="stack-md">
      <DataViewProvider config={config}>
        <StateManagerProvider>
          <div className="stack-md">
            {/* Header Actions */}
            <div className="flex items-center justify-between mb-md">
              <div className="flex items-center gap-md">
                <h2 className="text-body text-heading-4">Call Sheets Management</h2>
                <Button>
                  <Plus className="h-icon-xs w-icon-xs mr-sm" />
                  Create Call Sheet
                </Button>
              </div>
              <div className="flex items-center gap-sm">
                <ViewSwitcher />
                <DataActions />
              </div>
            </div>

            {/* Status Overview Cards */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-md mb-lg">
              {[
                { status: 'draft', label: 'Draft', icon: Clock },
                { status: 'published', label: 'Published', icon: FileText },
                { status: 'distributed', label: 'Distributed', icon: Users },
                { status: 'completed', label: 'Completed', icon: CheckCircle }
              ].map(({ status, label, icon: IconComponent }) => (
                <Card key={status} className="p-md">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-sm">
                      <IconComponent className="h-icon-xs w-icon-xs" />
                      <span className="form-label">{label}</span>
                    </div>
                    <Badge variant="outline">
                      {statusCounts[status] || 0}
                    </Badge>
                  </div>
                </Card>
              ))}
            </div>

            {/* Upcoming Call Sheets */}
            {upcomingCallSheets.length > 0 && (
              <Card className="p-md mb-lg">
                <h3 className="text-heading-4 mb-sm flex items-center gap-sm">
                  <Calendar className="h-icon-xs w-icon-xs" />
                  Upcoming Call Sheets
                </h3>
                <div className="stack-sm">
                  {upcomingCallSheets.map((callSheet: any) => (
                    <div
                      key={callSheet.id}
                      className="flex items-center justify-between p-sm rounded border cursor-pointer hover:bg-secondary/50"
                      onClick={() => handleViewCallSheet(callSheet)}
                    >
                      <div>
                        <div className="form-label">{callSheet.event_name}</div>
                        <div className="text-body-sm color-muted flex items-center gap-md">
                          <span className="flex items-center gap-xs">
                            <Calendar className="h-3 w-3" />
                            {new Date(callSheet.call_date).toLocaleDateString()}
                          </span>
                          <span className="flex items-center gap-xs">
                            <Clock className="h-3 w-3" />
                            {callSheet.call_time}
                          </span>
                          {callSheet.location !== 'TBD' && (
                            <span>{callSheet.location}</span>
                          )}
                        </div>
                      </div>
                      <Badge className={getStatusColor(callSheet.status)}>
                        {callSheet.status}
                      </Badge>
                    </div>
                  ))}
                </div>
              </Card>
            )}

            {/* Data Views */}
            <DataGrid />

            <KanbanBoard 
              columns={[
                { id: 'draft', title: 'Draft' },
                { id: 'published', title: 'Published' },
                { id: 'distributed', title: 'Distributed' },
                { id: 'completed', title: 'Completed' }
              ]}
              statusField="status"
              titleField="event_name"
              onCardClick={handleViewCallSheet}
            />

            <ListView 
              titleField="event_name"
              subtitleField="call_date"
              onItemClick={handleViewCallSheet}
            />

            {/* Call Sheet Details Drawer */}
            <Drawer
              open={drawerOpen}
              onClose={() => {
                setDrawerOpen(false);
                setSelectedRecord(null);
              }}
              title={
                drawerMode === 'create' 
                  ? 'Create Call Sheet' 
                  : `${selectedRecord?.event_name} Call Sheet` || 'Call Sheet Details'
              }
            >
              {/* Custom Call Sheet Details */}
              {selectedRecord && (
                <div className="stack-md mt-lg">
                  <div className="grid grid-cols-2 gap-md">
                    <div className="flex items-center gap-sm text-body-sm color-muted">
                      <Calendar className="h-icon-xs w-icon-xs" />
                      <span>
                        {new Date(selectedRecord.call_date).toLocaleDateString()}
                      </span>
                    </div>
                    <div className="flex items-center gap-sm text-body-sm color-muted">
                      <Clock className="h-icon-xs w-icon-xs" />
                      <span>
                        {selectedRecord.call_time || 'Call time TBD'}
                      </span>
                    </div>
                  </div>

                  {selectedRecord.location && selectedRecord.location !== 'TBD' && (
                    <div className="flex items-center gap-sm text-body-sm color-muted">
                      <Users className="h-icon-xs w-icon-xs" />
                      <span>{selectedRecord.location}</span>
                    </div>
                  )}

                  <div className="pt-md border-t">
                    <Badge className={getStatusColor(selectedRecord.status)}>
                      {selectedRecord.status || 'Draft'}
                    </Badge>
                  </div>

                  {selectedRecord.details && typeof selectedRecord.details === 'object' && (
                    <div className="pt-md border-t">
                      <h4 className="form-label mb-sm">Call Sheet Details</h4>
                      <div className="text-body-sm color-muted bg-secondary/50 p-sm rounded">
                        <pre className="whitespace-pre-wrap">
                          {JSON.stringify(selectedRecord.details, null, 2)}
                        </pre>
                      </div>
                    </div>
                  )}
                </div>
              )}
            </Drawer>

            {/* Empty State */}
            {!loading && data.length === 0 && (
              <Card className="p-xl text-center">
                <FileText className="h-icon-2xl w-icon-2xl mx-auto mb-md color-muted" />
                <h3 className="text-body text-heading-4 mb-sm">No Call Sheets Yet</h3>
                <p className="color-muted mb-md">
                  Create call sheets to organize production schedules and ensure everyone knows when and where to be.
                </p>
                <Button onClick={handleCreateCallSheet}>
                  <Plus className="h-icon-xs w-icon-xs mr-sm" />
                  Create First Call Sheet
                </Button>
              </Card>
            )}
          </div>
        </StateManagerProvider>
      </DataViewProvider>
    </div>
  );
}

'use client';


import { useEffect, useState } from 'react';
import {
  Drawer,
  type Button,
  StateManagerProvider,
  DataViewProvider,
  ViewSwitcher,
  DataActions,
  DataGrid,
  KanbanBoard,
  CalendarView,
  ListView,
  Button
} from '@ghxstship/ui';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { Plus, Calendar, MapPin, Clock } from 'lucide-react';

export default function EventsClient({ orgId }: { orgId: string }) {
  const t = useTranslations('programming');
  const sb = createBrowserClient();

  const [data, setData] = useState<DataRecord[]>([]);
  const [loading, setLoading] = useState(false);
  const [selectedRecord, setSelectedRecord] = useState<any>(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerMode, setDrawerMode] = useState<'view' | 'edit' | 'create'>('view');

  // Define field configuration for events
  const fields: FieldConfig[] = [
    {
      key: 'name',
      label: 'Event Name',
      type: 'text',
      required: true,
      sortable: true,
      filterable: true,
      width: 200
    },
    {
      key: 'kind',
      label: 'Type',
      type: 'select',
      options: [
        { value: 'performance', label: 'Performance' },
        { value: 'activation', label: 'Activation' },
        { value: 'workshop', label: 'Workshop' }
      ],
      sortable: true,
      filterable: true,
      groupable: true,
      width: 120
    },
    {
      key: 'project_name',
      label: 'Project',
      type: 'text',
      sortable: true,
      filterable: true,
      width: 150
    },
    {
      key: 'starts_at',
      label: 'Start Date',
      type: 'date',
      sortable: true,
      filterable: true,
      width: 180
    },
    {
      key: 'ends_at',
      label: 'End Date',
      type: 'date',
      sortable: true,
      filterable: true,
      width: 180
    },
    {
      key: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'draft', label: 'Draft' },
        { value: 'scheduled', label: 'Scheduled' },
        { value: 'in_progress', label: 'In Progress' },
        { value: 'completed', label: 'Completed' },
        { value: 'cancelled', label: 'Cancelled' }
      ],
      sortable: true,
      filterable: true,
      groupable: true,
      width: 120
    }
  ];

  useEffect(() => {
    loadEvents();
  }, [orgId]);

  const loadEvents = async () => {
    if (!orgId) return;

    try {
      setLoading(true);

      const { data: eventsData, error } = await sb
        .from('events')
        .select(`
          *,
          projects!inner(
            name,
            organization_id
          ),
          lineups(count),
          riders(count),
          call_sheets(count)
        `)
        .eq('projects.organization_id', orgId)
        .order('starts_at', { ascending: false });

      if (error) throw error;

      // Transform data to include project name and computed status
      const transformedData = (eventsData || []).map(event => ({
        ...event,
        project_name: event.projects?.name || 'Unknown Project',
        status: computeEventStatus(event),
        lineups_count: event.lineups?.[0]?.count || 0,
        riders_count: event.riders?.[0]?.count || 0,
        call_sheets_count: event.call_sheets?.[0]?.count || 0
      }));

      setData(transformedData);
    } catch (error) {
      console.error('Error loading events:', error);
    } finally {
      setLoading(false);
    }
  };

  const computeEventStatus = (event: any) => {
    const now = new Date();
    const startDate = event.starts_at ? new Date(event.starts_at) : null;
    const endDate = event.ends_at ? new Date(event.ends_at) : null;

    if (!startDate) return 'draft';
    if (startDate > now) return 'scheduled';
    if (endDate && endDate < now) return 'completed';
    if (startDate <= now && (!endDate || endDate >= now)) return 'in_progress';

    return 'draft';
  };

  const handleCreateEvent = () => {
    setSelectedRecord(null);
    setDrawerMode('create');
    setDrawerOpen(true);
  };

  const handleEditEvent = (event: any) => {
    setSelectedRecord(event);
    setDrawerMode('edit');
    setDrawerOpen(true);
  };

  const handleViewEvent = (event: any) => {
    setSelectedRecord(event);
    setDrawerMode('view');
    setDrawerOpen(true);
  };

  const handleSaveEvent = async (eventData: any) => {
    try {
      if (drawerMode === 'create') {
        const { error } = await sb.from('events').insert(eventData);
        if (error) throw error;
      } else if (drawerMode === 'edit') {
        const { error } = await sb
          .from('events')
          .update(eventData)
          .eq('id', selectedRecord.id);
        if (error) throw error;
      }

      await loadEvents();
      setDrawerOpen(false);
    } catch (error) {
      console.error('Error saving event:', error);
    }
  };

  // Configure DataView
  const config: DataViewConfig = {
    id: 'events-management',
    name: 'Events Management',
    viewType: 'grid',
    defaultView: 'grid',
    fields,
    data,

    onSearch: (query: string) => {
      console.log('Search events:', query);
    },
    onFilter: (filters: any[]) => {
      console.log('Filter events:', filters);
    },
    onSort: (sorts: any[]) => {
      console.log('Sort events:', sorts);
    },
    onRefresh: loadEvents,
    onExport: (data: any[], format: string) => {
      console.log('Export events:', format, data);
    },
    onImport: (data: any[]) => {
      console.log('Import events:', data);
    }
  };

  return (
    <div className="stack-md">
      <DataViewProvider config={config}>
        <StateManagerProvider>
          <div className="stack-md">
            {/* Header Actions */}
            <div className="flex items-center justify-between mb-md">
              <div className="flex items-center gap-md">
                <h2 className="text-body text-heading-4">Events Management</h2>
                <Button>
                  <Plus className="h-icon-xs w-icon-xs mr-sm" />
                  Create Event
                </Button>
              </div>
              <div className="flex items-center gap-sm">
                <ViewSwitcher />
                <DataActions />
              </div>
            </div>

            {/* Data Views */}
            <DataGrid />

            <KanbanBoard 
              columns={[
                { id: 'draft', title: 'Draft' },
                { id: 'scheduled', title: 'Scheduled' },
                { id: 'in_progress', title: 'In Progress' },
                { id: 'completed', title: 'Completed' },
                { id: 'cancelled', title: 'Cancelled' }
              ]}
              statusField="status"
              titleField="name"
              onCardClick={handleViewEvent}
            />

            <CalendarView 
              startDateField="starts_at"
              endDateField="ends_at"
              titleField="name"

            />

            <ListView 
              titleField="name"
              subtitleField="project_name"
              onItemClick={handleViewEvent}
            />

            {/* Event Details Drawer */}
            <Drawer
              open={drawerOpen}
              onClose={() => {
                setDrawerOpen(false);
                setSelectedRecord(null);
              }}
              title={
