 const supabase = useMemo(() => createBrowserClient(), []);

 // State management
 const [spaces, setSpaces] = useState<ProgrammingSpace[]>(initialSpaces);
 const [loading, setLoading] = useState(false);
 const [selectedSpaces, setSelectedSpaces] = useState<string[]>([]);
 const [currentView, setCurrentView] = useState<ViewType>('list');
 const [searchQuery, setSearchQuery] = useState('');
 const [filters, setFilters] = useState<SpaceFilters>({});
 const [sort, setSort] = useState<SpaceSort>({ field: 'name', direction: 'asc' });

 // Drawer state
 const [createDrawerOpen, setCreateDrawerOpen] = useState(false);
 const [editDrawerOpen, setEditDrawerOpen] = useState(false);
 const [viewDrawerOpen, setViewDrawerOpen] = useState(false);
 const [selectedSpace, setSelectedSpace] = useState<ProgrammingSpace | null>(null);

 // Real-time subscription
 useEffect(() => {
 const channel = supabase
 .channel(`programming_spaces_${orgId}`)
 .on(
 'postgres_changes',
 {
 event: '*',
 schema: 'public',
 table: 'programming_spaces',
 filter: `organization_id=eq.${orgId}`
 },
 (payload) => {
 if (payload.eventType === 'INSERT') {
 fetchSpaces();
 } else if (payload.eventType === 'UPDATE') {
 setSpaces((prev: unknown) =>
 prev.map((space) =>
 space.id === payload.new.id ? { ...space, ...payload.new } : space
 )
 );
 } else if (payload.eventType === 'DELETE') {
 setSpaces((prev: unknown) => prev.filter((space) => space.id !== payload.old.id));
 }
 }
 )
 .subscribe();

 return () => {
 supabase.removeChannel(channel);
 };
 }, [supabase, orgId]);

 // Fetch spaces with filters
 const fetchSpaces = useCallback(async () => {
 try {
 setLoading(true);
 const params = new URLSearchParams();

 if (filters.project_id) params.append('project_id', filters.project_id);
 if (filters.kind) params.append('kind', filters.kind);
 if (filters.status) params.append('status', filters.status);
 if (filters.access_level) params.append('access_level', filters.access_level);
 if (filters.building) params.append('building', filters.building);
 if (filters.floor) params.append('floor', filters.floor);
 if (searchQuery) params.append('search', searchQuery);
 if (filters.min_capacity) params.append('min_capacity', filters.min_capacity.toString());
 if (filters.max_capacity) params.append('max_capacity', filters.max_capacity.toString());
 if (filters.is_bookable !== undefined) params.append('is_bookable', filters.is_bookable.toString());

 const response = await fetch(`/api/v1/programming/spaces?${params}`);
 if (!response.ok) throw new Error('Failed to fetch spaces');

 const data = await response.json();
 setSpaces(data);
 } catch (error) {
 console.error('Error fetching spaces:', error);
 } finally {
 setLoading(false);
 }
 }, [filters, searchQuery]);

 // Trigger fetch when filters change
 useEffect(() => {
 fetchSpaces();
 }, [fetchSpaces]);

 // Filter and sort spaces
 const filteredAndSortedSpaces = useMemo(() => {
 let filtered = [...spaces];

 // Apply local search if no server search
 if (searchQuery && !filters.search) {
 const query = searchQuery.toLowerCase();
 filtered = filtered.filter(
 (space) =>
 space.name?.toLowerCase().includes(query) ||
 space.description?.toLowerCase().includes(query) ||
 space.location?.toLowerCase().includes(query) ||
 space.building?.toLowerCase().includes(query)
 );
 }

 // Apply sorting
 filtered.sort((a, b) => {
 const aValue = a[sort.field];
 const bValue = b[sort.field];

 if (aValue === null || aValue === undefined) return 1;
 if (bValue === null || bValue === undefined) return -1;

 if (typeof aValue === 'string' && typeof bValue === 'string') {
 return sort.direction === 'asc'
 ? aValue.localeCompare(bValue)
 : bValue.localeCompare(aValue);
 }

 if (aValue < bValue) return sort.direction === 'asc' ? -1 : 1;
 if (aValue > bValue) return sort.direction === 'asc' ? 1 : -1;
 return 0;
 });

 return filtered;
 }, [spaces, searchQuery, filters.search, sort]);

 // Get unique buildings and floors for filters
 const buildings = useMemo(() => {
 const uniqueBuildings = [...new Set(spaces.map(s => s.building).filter(Boolean))];
 return uniqueBuildings.sort();
 }, [spaces]);

 const floors = useMemo(() => {
 const uniqueFloors = [...new Set(spaces.map(s => s.floor).filter(Boolean))];
 return uniqueFloors.sort();
 }, [spaces]);

 // Handlers
 const handleCreateSpace = () => {
 setCreateDrawerOpen(true);
 };

 const handleEditSpace = (space: ProgrammingSpace) => {
 setSelectedSpace(space);
 setEditDrawerOpen(true);
 };

 const handleViewSpace = (space: ProgrammingSpace) => {
 setSelectedSpace(space);
 setViewDrawerOpen(true);
 };

 const handleDeleteSpace = async (spaceId: string) => {
 try {
 const response = await fetch(`/api/v1/programming/spaces/${spaceId}`, {
 method: 'DELETE'
 });

 if (!response.ok) throw new Error('Failed to delete space');

 setSpaces((prev: unknown) => prev.filter((space) => space.id !== spaceId));
 } catch (error) {
 console.error('Error deleting space:', error);
 }
 };

 const handleBulkAction = async (action: string, spaceIds: string[]) => {
 try {
 setLoading(true);

 switch (action) {
 case 'delete':
 await Promise.all(
 spaceIds.map((id) =>
 fetch(`/api/v1/programming/spaces/${id}`, { method: 'DELETE' })
 )
 );
 setSpaces((prev: unknown) => prev.filter((space) => !spaceIds.includes(space.id)));
 break;
 case 'available':
 await Promise.all(
 spaceIds.map((id) =>
 fetch(`/api/v1/programming/spaces/${id}`, {
 method: 'PATCH',
 headers: { 'Content-Type': 'application/json' },
 body: JSON.stringify({ status: 'available' })
 })
 )
 );
 fetchSpaces();
 break;
 case 'maintenance':
 await Promise.all(
 spaceIds.map((id) =>
 fetch(`/api/v1/programming/spaces/${id}`, {
 method: 'PATCH',
 headers: { 'Content-Type': 'application/json' },
 body: JSON.stringify({ status: 'maintenance' })
 })
 )
 );
 fetchSpaces();
 break;
 }

 setSelectedSpaces([]);
 } catch (error) {
 console.error('Error performing bulk action:', error);
 } finally {
 setLoading(false);
 }
 };

 const handleSuccess = () => {
 fetchSpaces();
 setCreateDrawerOpen(false);
 setEditDrawerOpen(false);
 setSelectedSpace(null);
 };

 // Export functionality
 const handleExport = () => {
 const csvContent = [
 ['Name', 'Kind', 'Status', 'Access Level', 'Capacity', 'Building', 'Floor', 'Location'].join(','),
 ...filteredAndSortedSpaces.map((space) =>
 [
 space.name,
 space.kind,
 space.status,
 space.access_level,
 space.capacity || '',
 space.building || '',
 space.floor || '',
 space.location || '',
 ].join(',')
 ),
 ].join('\n');

 const blob = new Blob([csvContent], { type: 'text/csv' });
 const url = URL.createObjectURL(blob);
 const a = document.createElement('a');
 a.href = url;
 a.download = `programming-spaces-${new Date().toISOString().split('T')[0]}.csv`;
 a.click();
 URL.revokeObjectURL(url);
 };

 // View icons
 const viewIcons = {
 list: List,
 grid: Grid3X3,
 timeline: Clock,
 analytics: BarChart3
 };

 return (
 <div className="space-y-lg">
 {/* Header */}
 <div className="flex items-center justify-between">
 <div>
 <h1 className="text-2xl font-bold">Programming Spaces</h1>
 <p className="text-muted-foreground">
 Manage venues, rooms, and facilities for events and productions
 </p>
 </div>
 <Button onClick={handleCreateSpace}>
 <Plus className="mr-2 h-icon-xs w-icon-xs" />
 Create Space
 </Button>
 </div>

 {/* Filters and Search */}
 <Card className="p-md">
 <div className="flex flex-col gap-md md:flex-row md:items-center md:justify-between">
 <div className="flex flex-1 items-center gap-md">
 <div className="relative flex-1 max-w-sm">
 <Search className="absolute left-3 top-xs/2 h-icon-xs w-icon-xs -translate-y-1/2 text-muted-foreground" />
 <Input
 placeholder="Search spaces..."
 value={searchQuery}
 onChange={(e) => setSearchQuery(e.target.value)}
 className="pl-9"
 />
 </div>

 <Select
 value={filters.kind || ''}
 onChange={(e) =>
 setFilters((prev: unknown) => ({ ...prev, kind: e.target.value as SpaceKind || undefined }))
 }
 >
 <SelectTrigger className="w-40">
 <SelectValue placeholder="All Types" />
 </SelectTrigger>
 <SelectContent>
 <SelectItem value="">All Types</SelectItem>
 <SelectItem value="room">Room</SelectItem>
 <SelectItem value="green_room">Green Room</SelectItem>
 <SelectItem value="dressing_room">Dressing Room</SelectItem>
 <SelectItem value="meeting_room">Meeting Room</SelectItem>
 <SelectItem value="classroom">Classroom</SelectItem>
 <SelectItem value="studio">Studio</SelectItem>
 <SelectItem value="rehearsal_room">Rehearsal Room</SelectItem>
 <SelectItem value="storage">Storage</SelectItem>
 <SelectItem value="office">Office</SelectItem>
 <SelectItem value="lounge">Lounge</SelectItem>
 <SelectItem value="other">Other</SelectItem>
 </SelectContent>
 </Select>

 <Select
 value={filters.status || ''}
 onChange={(e) =>
 setFilters((prev: unknown) => ({ ...prev, status: e.target.value as SpaceStatus || undefined }))
 }
 >
 <SelectTrigger className="w-40">
 <SelectValue placeholder="All Status" />
 </SelectTrigger>
 <SelectContent>
 <SelectItem value="">All Status</SelectItem>
 <SelectItem value="available">Available</SelectItem>
 <SelectItem value="occupied">Occupied</SelectItem>
 <SelectItem value="reserved">Reserved</SelectItem>
 <SelectItem value="maintenance">Maintenance</SelectItem>
 <SelectItem value="cleaning">Cleaning</SelectItem>
 <SelectItem value="out_of_service">Out of Service</SelectItem>
 </SelectContent>
 </Select>

 {buildings.length > 0 && (
 <Select
 value={filters.building || ''}
 onChange={(e) =>
 setFilters((prev: unknown) => ({ ...prev, building: e.target.value || undefined }))
 }
 >
 <SelectTrigger className="w-40">
 <SelectValue placeholder="All Buildings" />
 </SelectTrigger>
 <SelectContent>
 <SelectItem value="">All Buildings</SelectItem>
 {buildings.map((building) => (
 <SelectItem key={building} value={building}>
 {building}
 </SelectItem>
 ))}
 </SelectContent>
 </Select>
 )}

 <Select
 value={filters.project_id || ''}
 onChange={(e) =>
 setFilters((prev: unknown) => ({ ...prev, project_id: e.target.value || undefined }))
 }
 >
 <SelectTrigger className="w-container-xs">
 <SelectValue placeholder="All Projects" />
 </SelectTrigger>
 <SelectContent>
 <SelectItem value="">All Projects</SelectItem>
 {projects.map((project) => (
 <SelectItem key={project.id} value={project.id}>
 {project.name}
 </SelectItem>
 ))}
 </SelectContent>
 </Select>
 </div>

 <div className="flex items-center gap-xs">
 <Button variant="outline" size="sm" onClick={handleExport}>
 <Download className="mr-2 h-icon-xs w-icon-xs" />
 Export
 </Button>

 {/* View Switcher */}
 <div className="flex items-center rounded-lg border p-xs">
 {(['list', 'grid', 'timeline', 'analytics'] as ViewType[]).map((view) => {
 const Icon = viewIcons[view];
 return (
 <Button
 key={view}
 variant={currentView === view ? 'default' : 'ghost'}
 size="sm"
 onClick={() => setCurrentView(view)}
 className="h-icon-lg w-icon-lg p-0"
 >
 <Icon className="h-icon-xs w-icon-xs" />
 </Button>
 );
 })}
 </div>
 </div>
 </div>

 {/* Bulk Actions */}
 {selectedSpaces.length > 0 && (
 <div className="mt-4 flex items-center gap-xs rounded-lg border bg-muted/50 p-sm">
 <span className="text-sm text-muted-foreground">
 {selectedSpaces.length} space(s) selected
 </span>
 <div className="flex gap-xs">
 <Button
 size="sm"
 variant="outline"
 onClick={() => handleBulkAction('available', selectedSpaces)}
 >
 Mark Available
 </Button>
 <Button
 size="sm"
 variant="outline"
 onClick={() => handleBulkAction('maintenance', selectedSpaces)}
 >
 Mark Maintenance
 </Button>
 <Button
 size="sm"
 variant="destructive"
 onClick={() => handleBulkAction('delete', selectedSpaces)}
 >
 Delete
 </Button>
 </div>
 </div>
 )}
 </Card>

 {/* Content */}
 <div className="min-h-content-lg">
 {currentView === 'list' && (
 <ProgrammingSpacesListView
 spaces={filteredAndSortedSpaces}
 loading={loading}
 selectedSpaces={selectedSpaces}
 onSelectionChange={setSelectedSpaces}
 onEdit={handleEditSpace}
 onView={handleViewSpace}
 onDelete={handleDeleteSpace}
 sort={sort}
 onSortChange={setSort}
 />
 )}

 {currentView === 'grid' && (
 <ProgrammingSpacesGridView
 spaces={filteredAndSortedSpaces}
 loading={loading}
 onEdit={handleEditSpace}
 onView={handleViewSpace}
 onDelete={handleDeleteSpace}
 />
 )}

 {currentView === 'timeline' && (
 <ProgrammingSpacesTimelineView
 spaces={filteredAndSortedSpaces}
 loading={loading}
 onEdit={handleEditSpace}
 onView={handleViewSpace}
 />
 )}

 {currentView === 'analytics' && (
 <ProgrammingSpacesAnalyticsView
 spaces={filteredAndSortedSpaces}
 loading={loading}
 />
 )}
 </div>

 {/* Drawers */}
 <CreateProgrammingSpaceDrawer
 open={createDrawerOpen}
 onClose={() => setCreateDrawerOpen(false)}
 orgId={orgId}
 currentUserId={currentUserId}
 projects={projects}
 onSuccess={handleSuccess}
 />

 {selectedSpace && (
 <>
 <EditProgrammingSpaceDrawer
 open={editDrawerOpen}
 onClose={() => setEditDrawerOpen(false)}
 space={selectedSpace}
 orgId={orgId}
 currentUserId={currentUserId}
 projects={projects}
 onSuccess={handleSuccess}
 />

 <ViewProgrammingSpaceDrawer
 open={viewDrawerOpen}
 onClose={() => setViewDrawerOpen(false)}
 space={selectedSpace}
 users={users}
 onEdit={() => {
 setViewDrawerOpen(false);
 setEditDrawerOpen(true);
 }}
 onDelete={() => {
 handleDeleteSpace(selectedSpace.id);
 setViewDrawerOpen(false);
 setSelectedSpace(null);
 }}
 />
 </>
 )}
 </div>
 );
}

'use client';


import { useEffect, useState } from 'react';
import {
  Drawer,
  type Button,
  Card,
  Badge,
  StateManagerProvider,
  DataViewProvider,
  ViewSwitcher,
  DataActions,
  DataGrid,
  KanbanBoard,
  ListView,
  Button
} from '@ghxstship/ui';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { Plus, MapPin, Users, Square, Home, Coffee, Presentation } from 'lucide-react';

export default function SpacesClient({ orgId }: { orgId: string }) {
  const t = useTranslations('programming');
  const sb = createBrowserClient();

  const [data, setData] = useState<DataRecord[]>([]);
  const [loading, setLoading] = useState(false);
  const [selectedRecord, setSelectedRecord] = useState<any>(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerMode, setDrawerMode] = useState<'view' | 'edit' | 'create'>('view');

  // Define field configuration for spaces
  const fields: FieldConfig[] = [
    {
      key: 'name',
      label: 'Space Name',
      type: 'text',
      required: true,
      sortable: true,
      filterable: true,
      width: 200
    },
    {
      key: 'kind',
      label: 'Space Type',
      type: 'select',
      options: [
        { value: 'room', label: 'Room' },
        { value: 'green_room', label: 'Green Room' },
        { value: 'dressing_room', label: 'Dressing Room' },
        { value: 'meeting_room', label: 'Meeting Room' },
        { value: 'classroom', label: 'Classroom' }
      ],
      required: true,
      sortable: true,
      filterable: true,
      groupable: true,
      width: 150
    },
    {
      key: 'capacity',
      label: 'Capacity',
      type: 'number',
      sortable: true,
      filterable: true,
      width: 100
    },
    {
      key: 'location',
      label: 'Location',
      type: 'text',
      sortable: true,
      filterable: true,
      width: 150
    },
    {
      key: 'availability',
      label: 'Availability',
      type: 'select',
      options: [
        { value: 'available', label: 'Available' },
        { value: 'occupied', label: 'Occupied' },
        { value: 'maintenance', label: 'Maintenance' },
        { value: 'reserved', label: 'Reserved' }
      ],
      sortable: true,
      filterable: true,
      groupable: true,
      width: 120
    }
  ];

  useEffect(() => {
    loadSpaces();
  }, [orgId]);

  const loadSpaces = async () => {
    if (!orgId) return;

    try {
      setLoading(true);

      const { data: spacesData, error } = await sb
        .from('spaces')
        .select('*')
        .eq('organization_id', orgId)
        .order('name', { ascending: true });

      if (error) throw error;

      // Transform data to include computed availability status
      const transformedData = (spacesData || []).map(space => ({
        ...space,
        availability: space.availability || 'available'
      }));

      setData(transformedData);
    } catch (error) {
      console.error('Error loading spaces:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateSpace = () => {
    setSelectedRecord(null);
    setDrawerMode('create');
    setDrawerOpen(true);
  };

  const handleEditSpace = (space: any) => {
    setSelectedRecord(space);
    setDrawerMode('edit');
    setDrawerOpen(true);
  };

  const handleViewSpace = (space: any) => {
    setSelectedRecord(space);
    setDrawerMode('view');
    setDrawerOpen(true);
  };

  const handleSaveSpace = async (spaceData: any) => {
    try {
      if (drawerMode === 'create') {
        const { error } = await sb.from('spaces').insert({
          ...spaceData,
          organization_id: orgId
        });
        if (error) throw error;
      } else if (drawerMode === 'edit') {
        const { error } = await sb
          .from('spaces')
          .update(spaceData)
          .eq('id', selectedRecord.id);
        if (error) throw error;
      }

      await loadSpaces();
      setDrawerOpen(false);
    } catch (error) {
      console.error('Error saving space:', error);
    }
  };

  const getSpaceIcon = (kind: string) => {
    switch (kind) {
      case 'green_room':
        return Coffee;
      case 'dressing_room':
        return Home;
      case 'meeting_room':
        return Presentation;
      case 'classroom':
        return Users;
      default:
        return Square;
    }
  };

  const getAvailabilityColor = (availability: string) => {
    switch (availability) {
      case 'available':
        return 'color-success bg-success/10';
      case 'occupied':
        return 'color-destructive bg-destructive/10';
      case 'maintenance':
        return 'color-warning bg-warning/10';
      case 'reserved':
        return 'color-accent bg-accent/10';
      default:
        return 'color-muted bg-secondary';
    }
  };

  // Configure DataView
  const config: DataViewConfig = {
    id: 'spaces-management',
    name: 'Spaces Management',
    viewType: 'grid',
    defaultView: 'grid',
    fields,
    data,

    onSearch: (query: string) => {
      console.log('Search spaces:', query);
    },
    onFilter: (filters: any[]) => {
      console.log('Filter spaces:', filters);
    },
    onSort: (sorts: any[]) => {
      console.log('Sort spaces:', sorts);
    },
    onRefresh: loadSpaces,
    onExport: (data: any[], format: string) => {
      console.log('Export spaces:', format, data);
    },
    onImport: (data: any[]) => {
      console.log('Import spaces:', data);
    }
  };

  // Group spaces by type for better visualization
  const spacesByType = data.reduce((acc, space) => {
    const type = space.kind || 'room';
    if (!acc[type]) {
      acc[type] = [];
    }
    acc[type].push(space);
    return acc;
  }, {});

  const spaceTypeLabels: { [key: string]: string } = {
    room: 'General Rooms',
    green_room: 'Green Rooms',
    dressing_room: 'Dressing Rooms',
    meeting_room: 'Meeting Rooms',
    classroom: 'Classrooms'
  };

  return (
    <div className="stack-md">
      <DataViewProvider config={config}>
        <StateManagerProvider>
          <div className="stack-md">
            {/* Header Actions */}
            <div className="flex items-center justify-between mb-md">
              <div className="flex items-center gap-md">
                <h2 className="text-body text-heading-4">Spaces Management</h2>
                <Button>
                  <Plus className="h-icon-xs w-icon-xs mr-sm" />
                  Add Space
                </Button>
              </div>
              <div className="flex items-center gap-sm">
                <ViewSwitcher />
                <DataActions />
              </div>
            </div>

            {/* Space Type Overview Cards */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-md mb-lg">
              {Object.entries(spacesByType).map(([type, typeSpaces]: [string, any]) => {
                const IconComponent = getSpaceIcon(type);
                const availableSpaces = typeSpaces.filter((s: any) => s.availability === 'available').length;
                const totalCapacity = typeSpaces.reduce((sum: number, s) => sum + (s.capacity || 0), 0);

                return (
                  <Card key={type} className="p-md">
                    <div className="flex items-center justify-between mb-sm">
                      <h3 className="text-heading-4 flex items-center gap-sm">
                        <IconComponent className="h-icon-xs w-icon-xs" />
                        {spaceTypeLabels[type] || type}
                      </h3>
                      <Badge variant="secondary">
                        {typeSpaces.length} spaces
                      </Badge>
                    </div>

                    <div className="stack-sm">
                      <div className="flex justify-between text-body-sm">
                        <span className="color-muted">Available:</span>
                        <span className="form-label color-success">{availableSpaces}</span>
                      </div>
                      <div className="flex justify-between text-body-sm">
                        <span className="color-muted">Total Capacity:</span>
                        <span className="form-label">{totalCapacity}</span>
                      </div>
                    </div>

                    <div className="mt-sm stack-xs">
                      {typeSpaces.slice(0, 2).map((space: any) => (
                        <div
                          key={space.id}
                          className="flex items-center justify-between text-body-sm p-sm rounded border cursor-pointer hover:bg-secondary/50"
                          onClick={() => handleViewSpace(space)}
                        >
                          <div>
                            <div className="form-label">{space.name}</div>
                            <div className="color-muted flex items-center gap-xs">
                              <MapPin className="h-3 w-3" />
                              {space.location || 'Location TBD'}
                            </div>
                          </div>
                          <Badge variant="outline">
                            {space.availability}
                          </Badge>
                        </div>
                      ))}

                      {typeSpaces.length > 2 && (
                        <div className="text-body-sm color-muted text-center pt-xs">
                          +{typeSpaces.length - 2} more spaces
                        </div>
                      )}
                    </div>
                  </Card>
                );
              })}
            </div>

            {/* Data Views */}
            <DataGrid />

            <KanbanBoard 
              columns={[
                { id: 'available', title: 'Available' },
                { id: 'occupied', title: 'Occupied' },
                { id: 'reserved', title: 'Reserved' },
                { id: 'maintenance', title: 'Maintenance' }
              ]}
              statusField="availability"
              titleField="name"
              onCardClick={handleViewSpace}
            />

            <ListView 
              titleField="name"
              subtitleField="kind"
              onItemClick={handleViewSpace}
            />

            {/* Space Details Drawer */}
            <Drawer
              open={drawerOpen}
              onClose={() => {
                setDrawerOpen(false);
                setSelectedRecord(null);
              }}
              title={
                drawerMode === 'create' 
                  ? 'Add New Space' 
                  : selectedRecord?.name || 'Space Details'
              }
            >
              {/* Custom Space Details */}
              {selectedRecord && (
                <div className="stack-md mt-lg">
                  <div className="grid grid-cols-2 gap-md">
                    <div className="flex items-center gap-sm text-body-sm color-muted">
                      <Square className="h-icon-xs w-icon-xs" />
                      <span>
                        {selectedRecord.kind?.replace('_', ' ') || 'General Room'}
                      </span>
                    </div>
                    <div className="flex items-center gap-sm text-body-sm color-muted">
                      <Users className="h-icon-xs w-icon-xs" />
                      <span>
                        {selectedRecord.capacity ? `${selectedRecord.capacity} people` : 'Capacity TBD'}
                      </span>
                    </div>
                  </div>

                  {selectedRecord.location && (
                    <div className="flex items-center gap-sm text-body-sm color-muted">
                      <MapPin className="h-icon-xs w-icon-xs" />
                      <span>{selectedRecord.location}</span>
                    </div>
                  )}

                  <div className="pt-md border-t">
                    <Badge className={getAvailabilityColor(selectedRecord.availability)}>
                      {selectedRecord.availability?.replace('_', ' ') || 'Available'}
                    </Badge>
                  </div>
                </div>
              )}
            </Drawer>

            {/* Empty State */}
            {!loading && data.length === 0 && (
              <Card className="p-xl text-center">
                <Square className="h-icon-2xl w-icon-2xl mx-auto mb-md color-muted" />
                <h3 className="text-body text-heading-4 mb-sm">No Spaces Yet</h3>
                <p className="color-muted mb-md">
                  Start managing your venue spaces by adding rooms, green rooms, dressing rooms, and other facilities.
                </p>
                <Button onClick={handleCreateSpace}>
                  <Plus className="h-icon-xs w-icon-xs mr-sm" />
                  Add First Space
                </Button>
              </Card>
            )}
          </div>
        </StateManagerProvider>
      </DataViewProvider>
    </div>
  );
}

'use client';

import React, { useState } from 'react';
import { MoreHorizontal, Edit, Trash2, Eye, ZoomIn, Download } from 'lucide-react';
import {
  Badge,
  Button,
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
  Checkbox,
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger
} from "@ghxstship/ui";
import { Card, CardContent ,
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator
} from '@ghxstship/ui';
import { Checkbox ,
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator
} from '@ghxstship/ui';
import { Badge ,
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator
} from '@ghxstship/ui';
import {
  Dropdown,

  DropdownItem,
  DropdownMenuTrigger
} from '@ghxstship/ui';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle
} from '@ghxstship/ui';
import type { ProgrammingEntity } from '../types';

interface ImageViewProps<T extends ProgrammingEntity> {
  data: T[];
  loading?: boolean;
  selectable?: boolean;
  selectedIds?: string[];
  onSelectionChange?: (ids: string[]) => void;
  onEdit?: (item: T) => void;
  onDelete?: (item: T) => void;
  onView?: (item: T) => void;
  getImageUrl?: (item: T) => string | null;
  getThumbnailUrl?: (item: T) => string | null;
  renderImageItem?: (item: T) => React.ReactNode;
  emptyMessage?: string;
  className?: string;
  columns?: number; // 2-6 columns
}

export function ImageView<T extends ProgrammingEntity>({
  data,
  loading = false,
  selectable = false,
  selectedIds = [],
  onSelectionChange,
  onEdit,
  onDelete,
  onView,
  getImageUrl,
  getThumbnailUrl,
  renderImageItem,
  emptyMessage = 'No items to display',
  className,
  columns = 4
}: ImageViewProps<T>) {
  const [internalSelectedIds, setInternalSelectedIds] = useState<string[]>(selectedIds);
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [lightboxItem, setLightboxItem] = useState<T | null>(null);

  const selectedIdsToUse = onSelectionChange ? selectedIds : internalSelectedIds;
  const setSelectedIds = onSelectionChange || setInternalSelectedIds;

  const handleSelectItem = (itemId: string, checked: boolean) => {
    if (checked) {
      setSelectedIds([...selectedIdsToUse, itemId]);
    } else {
      setSelectedIds(selectedIdsToUse.filter(id => id !== itemId));
    }
  };

  const openLightbox = (item: T) => {
    setLightboxItem(item);
    setLightboxOpen(true);
  };

  const closeLightbox = () => {
    setLightboxOpen(false);
    setLightboxItem(null);
  };

  const getGridClass = () => {
    switch (columns) {
      case 2: return 'grid-cols-2';
      case 3: return 'grid-cols-3';
      case 4: return 'grid-cols-4';
      case 5: return 'grid-cols-5';
      case 6: return 'grid-cols-6';
      default: return 'grid-cols-4';
    }
  };

  // Default gallery item renderer
  const defaultRenderImageItem = (item: T) => {
    const imageUrl = getImageUrl?.(item) || getThumbnailUrl?.(item);
    const isEvent = 'start_date' in item && 'end_date' in item;
    const isPerformance = 'date' in item && 'venue' in item;
    const isWorkshop = 'instructor' in item;

    return (
      <Card className={`group relative overflow-hidden hover:shadow-lg transition-shadow ${
        selectedIdsToUse.includes(item.id) ? 'ring-2 ring-blue-500' : ''
      }`}>
        {/* Image/Thumbnail */}
        <div className="aspect-square bg-gray-100 relative overflow-hidden">
          {imageUrl ? (
            <img
              src={imageUrl}
              alt={item.title || 'Item'}
              className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-200 cursor-pointer"
              onClick={() => openLightbox(item)}
            />
          ) : (
            <div className="w-full h-full flex items-center justify-center bg-gradient-to-br from-gray-100 to-gray-200">
              <div className="text-center text-gray-400">
                <div className="text-4xl mb-2"></div>
                <div className="text-sm">No image</div>
              </div>
            </div>
          )}

          {/* Overlay with actions */}
          <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-40 transition-all duration-200">
            <div className="absolute top-xs right-2 opacity-0 group-hover:opacity-100 transition-opacity">
              <div className="flex gap-xs">
                {imageUrl && (
                  <Button
                    variant="secondary"
                    size="sm"
                    onClick={() => openLightbox(item)}
                    className="h-icon-lg w-icon-lg p-0"
                  >
                    <ZoomIn className="h-icon-xs w-icon-xs" />
                  </Button>
                )}
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="secondary" size="sm" className="h-icon-lg w-icon-lg p-0">
                      <MoreHorizontal className="h-icon-xs w-icon-xs" />
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end">
                    {onView && (
                      <DropdownMenuItem onClick={() => onView(item)}>
                        <Eye className="h-icon-xs w-icon-xs mr-2" />
                        View
                      </DropdownMenuItem>
                    )}
                    {onEdit && (
                      <DropdownMenuItem onClick={() => onEdit(item)}>
                        <Edit className="h-icon-xs w-icon-xs mr-2" />
                        Edit
                      </DropdownMenuItem>
                    )}
                    {onDelete && (
                      <DropdownMenuItem
                        onClick={() => onDelete(item)}
                        className="text-red-600"
                      >
                        <Trash2 className="h-icon-xs w-icon-xs mr-2" />
                        Delete
                      </DropdownMenuItem>
                    )}
                  </DropdownMenuContent>
                </DropdownMenu>
              </div>
            </div>
          </div>

          {/* Selection checkbox */}
          {selectable && (
            <div className="absolute top-xs left-2">
              <Checkbox
                checked={selectedIdsToUse.includes(item.id)}
                onCheckedChange={(checked) => handleSelectItem(item.id, checked as boolean)}
                className="bg-white/80 backdrop-blur-sm"
              />
            </div>
          )}
        </div>

        {/* Content */}
        <CardContent className="p-sm">
          <div className="space-y-xs">
            <h3 className="font-medium text-sm line-clamp-xs">
              {item.title || 'Untitled'}
            </h3>

            {/* Status and type badges */}
            <div className="flex items-center gap-xs">
              {'status' in item && (
                <Badge variant="secondary" className="text-xs">
                  {item.status}
                </Badge>
              )}
              {'type' in item && (
                <Badge variant="outline" className="text-xs">
                  {item.type}
                </Badge>
              )}
            </div>

            {/* Date information */}
            <div className="text-xs text-gray-500">
              {isEvent && (
                <div>
                  {new Date((item as any).start_date).toLocaleDateString()} - {' '}
                  {new Date((item as any).end_date).toLocaleDateString()}
                </div>
              )}
              {isPerformance && (
                <div>
                  {new Date((item as any).date).toLocaleDateString()}
                </div>
              )}
              {isWorkshop && (
                <div>
                  {new Date((item as any).start_date).toLocaleDateString()}
                </div>
              )}
            </div>

            {/* Additional metadata */}
            <div className="text-xs text-gray-500">
              {('location' in item || 'venue' in item) && (
                <div>{(item as any).location || (item as any).venue}</div>
              )}
              {('instructor' in item) && (
                <div>Instructor: {(item as any).instructor}</div>
              )}
              {('capacity' in item) && (
                <div>Capacity: {(item as any).capacity}</div>
              )}
            </div>
          </div>
        </CardContent>
      </Card>
    );
  };

  if (loading) {
    return (
      <div className={`grid ${getGridClass()} gap-md ${className}`}>
        {Array.from({ length: 12 }).map((_, i) => (
          <Card key={i} className="overflow-hidden">
            <div className="aspect-square bg-gray-200 animate-pulse" />
            <CardContent className="p-sm">
              <div className="space-y-xs">
                <div className="h-icon-xs bg-gray-200 animate-pulse rounded" />
                <div className="h-3 bg-gray-200 animate-pulse rounded w-2/3" />
                <div className="h-3 bg-gray-200 animate-pulse rounded w-1/2" />
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    );
  }

  if (data.length === 0) {
    return (
      <div className={`text-center py-xsxl ${className}`}>
        <p className="text-gray-500">{emptyMessage}</p>
      </div>
    );
  }

  return (
    <>
      <div className={`grid ${getGridClass()} gap-md ${className}`}>
        {data.map((item) => (
          <div key={item.id}>
            {renderImageItem ? renderImageItem(item) : defaultRenderImageItem(item)}
          </div>
        ))}
      </div>

      {/* Lightbox Dialog */}
      <Dialog open={lightboxOpen} onOpenChange={setLightboxOpen}>
        <DialogContent className="max-w-4xl max-h-[90vh] p-0">
          <DialogHeader className="p-md pb-0">
            <DialogTitle>{lightboxItem?.title || 'Item Details'}</DialogTitle>
          </DialogHeader>
          <div className="p-md pt-0">
            {lightboxItem && (
              <div className="space-y-md">
                {/* Large image */}
                {(getImageUrl?.(lightboxItem) || getThumbnailUrl?.(lightboxItem)) && (
                  <div className="relative">
                    <img
                      src={getImageUrl?.(lightboxItem) || getThumbnailUrl?.(lightboxItem) || ''}
                      alt={lightboxItem.title || 'Item'}
                      className="w-full max-h-container-lg object-contain rounded-lg"
                    />
                  </div>
                )}

                {/* Item details */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
                  <div>
                    <h3 className="font-medium mb-2">Details</h3>
                    <dl className="space-y-xs text-sm">
                      <div className="flex justify-between">
                        <dt className="text-gray-600">Title:</dt>
                        <dd className="font-medium">{lightboxItem.title || 'Untitled'}</dd>
                      </div>
                      {lightboxItem.description && (
                        <div className="flex justify-between">
                          <dt className="text-gray-600">Description:</dt>
                          <dd className="font-medium">{lightboxItem.description}</dd>
                        </div>
                      )}
                      {'status' in lightboxItem && (
                        <div className="flex justify-between">
                          <dt className="text-gray-600">Status:</dt>
                          <dd className="font-medium">{lightboxItem.status}</dd>
                        </div>
                      )}
                      {'type' in lightboxItem && (
                        <div className="flex justify-between">
                          <dt className="text-gray-600">Type:</dt>
                          <dd className="font-medium">{lightboxItem.type}</dd>
                        </div>
                      )}
                    </dl>
                  </div>

                  <div>
                    <h3 className="font-medium mb-2">Actions</h3>
                    <div className="flex gap-xs">
                      {onView && (
                        <Button variant="outline" onClick={() => { onView(lightboxItem); closeLightbox(); }}>
                          <Eye className="h-icon-xs w-icon-xs mr-2" />
                          View Details
                        </Button>
                      )}
                      {onEdit && (
                        <Button variant="outline" onClick={() => { onEdit(lightboxItem); closeLightbox(); }}>
                          <Edit className="h-icon-xs w-icon-xs mr-2" />
                          Edit
                        </Button>
                      )}
                      {onDelete && (
                        <Button variant="outline" onClick={() => { onDelete(lightboxItem); closeLightbox(); }} className="text-red-600">
                          <Trash2 className="h-icon-xs w-icon-xs mr-2" />
                          Delete
                        </Button>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        </DialogContent>
      </Dialog>
    </>
  );
}

export default ImageView;

'use client';

import { Plus, Search, Download, Grid3X3, List, BarChart3, Calendar } from "lucide-react";
import { useCallback, useEffect, useMemo, useState } from 'react';
import { createBrowserClient } from '@ghxstship/auth';
import {
 Badge,
 Button,
 Card,
 Input,
 Select,
 SelectContent,
 SelectItem,
 SelectTrigger,
 SelectValue,
  Drawer,
  DrawerContent,
  DrawerHeader,
  DrawerTitle,
  DrawerDescription,
  DrawerFooter
} from "@ghxstship/ui";

import type {
 ProgrammingWorkshop,
 WorkshopFilters,
 WorkshopSort,
 ViewType,
 WorkshopCategory,
 WorkshopStatus,
 WorkshopSkillLevel,
 WorkshopFormat,
 WorkshopProject,
 WorkshopEvent
} from './types';

// Import view components
import ProgrammingWorkshopsListView from './views/ProgrammingWorkshopsListView';
import ProgrammingWorkshopsGridView from './views/ProgrammingWorkshopsGridView';
import ProgrammingWorkshopsTimelineView from './views/ProgrammingWorkshopsTimelineView';
import ProgrammingWorkshopsAnalyticsView from './views/ProgrammingWorkshopsAnalyticsView';

// Import drawer components
import CreateProgrammingWorkshopDrawer from './drawers/CreateProgrammingWorkshopDrawer';
import EditProgrammingWorkshopDrawer from './drawers/EditProgrammingWorkshopDrawer';
import ViewProgrammingWorkshopDrawer from './drawers/ViewProgrammingWorkshopDrawer';

interface User {
 id: string;
 email: string;
 full_name?: string | null;
 avatar_url?: string | null;
}

interface ProgrammingWorkshopsClientProps {
 orgId: string;
 currentUserId: string;
 initialWorkshops: ProgrammingWorkshop[];
 projects: WorkshopProject[];
 events: WorkshopEvent[];
 users: User[];
}

export default function ProgrammingWorkshopsClient({
 orgId,
 currentUserId,
 initialWorkshops,
 projects,
 events,
 users
}: ProgrammingWorkshopsClientProps) {
 const supabase = useMemo(() => createBrowserClient(), []);

 // State management
 const [workshops, setWorkshops] = useState<ProgrammingWorkshop[]>(initialWorkshops);
 const [loading, setLoading] = useState(false);
 const [selectedWorkshops, setSelectedWorkshops] = useState<string[]>([]);
 const [currentView, setCurrentView] = useState<ViewType>('list');
 const [searchQuery, setSearchQuery] = useState('');
 const [filters, setFilters] = useState<WorkshopFilters>({});
 const [sort, setSort] = useState<WorkshopSort>({ field: 'start_date', direction: 'asc' });

 // Drawer state
 const [createDrawerOpen, setCreateDrawerOpen] = useState(false);
 const [editDrawerOpen, setEditDrawerOpen] = useState(false);
 const [viewDrawerOpen, setViewDrawerOpen] = useState(false);
 const [selectedWorkshop, setSelectedWorkshop] = useState<ProgrammingWorkshop | null>(null);

 // Real-time subscription
 useEffect(() => {
 const channel = supabase
 .channel(`programming_workshops_${orgId}`)
 .on(
 'postgres_changes',
 {
 event: '*',
 schema: 'public',
 table: 'programming_workshops',
 filter: `organization_id=eq.${orgId}`
 },
 (payload) => {
 if (payload.eventType === 'INSERT') {
 fetchWorkshops();
 } else if (payload.eventType === 'UPDATE') {
 setWorkshops((prev: unknown) =>
 prev.map((workshop) =>
 workshop.id === payload.new.id ? { ...workshop, ...payload.new } : workshop
 )
 );
 } else if (payload.eventType === 'DELETE') {
 setWorkshops((prev: unknown) => prev.filter((workshop) => workshop.id !== payload.old.id));
 }
 }
 )
 .subscribe();

 return () => {
 supabase.removeChannel(channel);
 };
 }, [supabase, orgId]);

 // Fetch workshops with filters
 const fetchWorkshops = useCallback(async () => {
 try {
 setLoading(true);
 const params = new URLSearchParams();

 if (filters.project_id) params.append('project_id', filters.project_id);
 if (filters.event_id) params.append('event_id', filters.event_id);
 if (filters.category) params.append('category', filters.category);
 if (filters.status) params.append('status', filters.status);
 if (filters.skill_level) params.append('skill_level', filters.skill_level);
 if (filters.format) params.append('format', filters.format);
 if (searchQuery) params.append('search', searchQuery);
 if (filters.start_date_from) params.append('start_date_from', filters.start_date_from);
 if (filters.start_date_to) params.append('start_date_to', filters.start_date_to);
 if (filters.price_min !== undefined) params.append('price_min', filters.price_min.toString());
 if (filters.price_max !== undefined) params.append('price_max', filters.price_max.toString());
 if (filters.has_availability !== undefined) params.append('has_availability', filters.has_availability.toString());
 if (filters.certification_available !== undefined) params.append('certification_available', filters.certification_available.toString());

 const response = await fetch(`/api/v1/programming/workshops?${params}`);
 if (!response.ok) throw new Error('Failed to fetch workshops');

 const data = await response.json();
 setWorkshops(data);
 } catch (error) {
 console.error('Error fetching workshops:', error);
 } finally {
 setLoading(false);
 }
 }, [filters, searchQuery]);

 // Trigger fetch when filters change
 useEffect(() => {
 fetchWorkshops();
 }, [fetchWorkshops]);

 // Filter and sort workshops
 const filteredAndSortedWorkshops = useMemo(() => {
 let filtered = [...workshops];

 // Apply local search if no server search
 if (searchQuery && !filters.search) {
 const query = searchQuery.toLowerCase();
 filtered = filtered.filter(
 (workshop) =>
 workshop.title?.toLowerCase().includes(query) ||
 workshop.description?.toLowerCase().includes(query) ||
 workshop.agenda?.toLowerCase().includes(query)
 );
 }

 // Apply sorting
 filtered.sort((a, b) => {
 const aValue = a[sort.field];
 const bValue = b[sort.field];

 if (aValue === null || aValue === undefined) return 1;
 if (bValue === null || bValue === undefined) return -1;

 if (typeof aValue === 'string' && typeof bValue === 'string') {
 return sort.direction === 'asc'
 ? aValue.localeCompare(bValue)
 : bValue.localeCompare(aValue);
 }

 if (aValue < bValue) return sort.direction === 'asc' ? -1 : 1;
 if (aValue > bValue) return sort.direction === 'asc' ? 1 : -1;
 return 0;
 });

 return filtered;
 }, [workshops, searchQuery, filters.search, sort]);

 // Handlers
 const handleCreateWorkshop = () => {
 setCreateDrawerOpen(true);
 };

 const handleEditWorkshop = (workshop: ProgrammingWorkshop) => {
 setSelectedWorkshop(workshop);
 setEditDrawerOpen(true);
 };

 const handleViewWorkshop = (workshop: ProgrammingWorkshop) => {
 setSelectedWorkshop(workshop);
 setViewDrawerOpen(true);
 };

 const handleDeleteWorkshop = async (workshopId: string) => {
 try {
 const response = await fetch(`/api/v1/programming/workshops/${workshopId}`, {
 method: 'DELETE'
 });

 if (!response.ok) throw new Error('Failed to delete workshop');

 setWorkshops((prev: unknown) => prev.filter((workshop) => workshop.id !== workshopId));
 } catch (error) {
 console.error('Error deleting workshop:', error);
 }
 };

 const handleBulkAction = async (action: string, workshopIds: string[]) => {
 try {
 setLoading(true);

 switch (action) {
 case 'delete':
 await Promise.all(
 workshopIds.map((id) =>
 fetch(`/api/v1/programming/workshops/${id}`, { method: 'DELETE' })
 )
 );
 setWorkshops((prev: unknown) => prev.filter((workshop) => !workshopIds.includes(workshop.id)));
 break;
 case 'cancel':
 await Promise.all(
 workshopIds.map((id) =>
 fetch(`/api/v1/programming/workshops/${id}`, {
 method: 'PATCH',
 headers: { 'Content-Type': 'application/json' },
 body: JSON.stringify({ status: 'cancelled' })
 })
 )
 );
 fetchWorkshops();
 break;
 case 'open_registration':
 await Promise.all(
 workshopIds.map((id) =>
 fetch(`/api/v1/programming/workshops/${id}`, {
 method: 'PATCH',
 headers: { 'Content-Type': 'application/json' },
 body: JSON.stringify({ status: 'open_registration' })
 })
 )
 );
 fetchWorkshops();
 break;
 }

 setSelectedWorkshops([]);
 } catch (error) {
 console.error('Error performing bulk action:', error);
 } finally {
 setLoading(false);
 }
 };

 const handleSuccess = () => {
 fetchWorkshops();
 setCreateDrawerOpen(false);
 setEditDrawerOpen(false);
 setSelectedWorkshop(null);
 };

 // Export functionality
 const handleExport = () => {
 const csvContent = [
 ['Title', 'Category', 'Status', 'Skill Level', 'Format', 'Start Date', 'Participants', 'Price'].join(','),
 ...filteredAndSortedWorkshops.map((workshop) =>
 [
 workshop.title,
 workshop.category,
 workshop.status,
 workshop.skill_level,
 workshop.format,
 new Date(workshop.start_date).toLocaleDateString(),
 `${workshop.current_participants}${workshop.max_participants ? `/${workshop.max_participants}` : ''}`,
 workshop.price ? `${workshop.currency || '$'}${workshop.price}` : 'Free',
 ].join(',')
 ),
 ].join('\n');

 const blob = new Blob([csvContent], { type: 'text/csv' });
 const url = URL.createObjectURL(blob);
 const a = document.createElement('a');
 a.href = url;
 a.download = `programming-workshops-${new Date().toISOString().split('T')[0]}.csv`;
 a.click();
 URL.revokeObjectURL(url);
 };

 // View icons
 const viewIcons = {
 list: List,
 grid: Grid3X3,
 timeline: Calendar,
 analytics: BarChart3
 };

 return (
 <div className="space-y-lg">
 {/* Header */}
 <div className="flex items-center justify-between">
 <div>
 <h1 className="text-2xl font-bold">Programming Workshops</h1>
 <p className="text-muted-foreground">
 Manage educational workshops, training sessions, and learning experiences
 </p>
 </div>
 <Button onClick={handleCreateWorkshop}>
 <Plus className="mr-2 h-icon-xs w-icon-xs" />
 Create Workshop
 </Button>
 </div>

 {/* Filters and Search */}
 <Card className="p-md">
 <div className="flex flex-col gap-md md:flex-row md:items-center md:justify-between">
 <div className="flex flex-1 items-center gap-md">
 <div className="relative flex-1 max-w-sm">
 <Search className="absolute left-3 top-xs/2 h-icon-xs w-icon-xs -translate-y-1/2 text-muted-foreground" />
 <Input
 placeholder="Search workshops..."
 value={searchQuery}
 onChange={(e) => setSearchQuery(e.target.value)}
 className="pl-9"
 />
 </div>

 <Select
 value={filters.category || ''}
 onChange={(e) =>
 setFilters((prev: unknown) => ({ ...prev, category: e.target.value as WorkshopCategory || undefined }))
 }
 >
 <SelectTrigger className="w-40">
 <SelectValue placeholder="All Categories" />
 </SelectTrigger>
 <SelectContent>
 <SelectItem value="">All Categories</SelectItem>
 <SelectItem value="technical">Technical</SelectItem>
 <SelectItem value="creative">Creative</SelectItem>
 <SelectItem value="business">Business</SelectItem>
 <SelectItem value="leadership">Leadership</SelectItem>
 <SelectItem value="production">Production</SelectItem>
 <SelectItem value="design">Design</SelectItem>
 <SelectItem value="marketing">Marketing</SelectItem>
 <SelectItem value="finance">Finance</SelectItem>
 <SelectItem value="legal">Legal</SelectItem>
 <SelectItem value="other">Other</SelectItem>
 </SelectContent>
 </Select>

 <Select
 value={filters.status || ''}
 onChange={(e) =>
 setFilters((prev: unknown) => ({ ...prev, status: e.target.value as WorkshopStatus || undefined }))
 }
 >
 <SelectTrigger className="w-40">
 <SelectValue placeholder="All Status" />
 </SelectTrigger>
 <SelectContent>
 <SelectItem value="">All Status</SelectItem>
 <SelectItem value="planning">Planning</SelectItem>
 <SelectItem value="open_registration">Open Registration</SelectItem>
 <SelectItem value="registration_closed">Registration Closed</SelectItem>
 <SelectItem value="full">Full</SelectItem>
 <SelectItem value="in_progress">In Progress</SelectItem>
 <SelectItem value="completed">Completed</SelectItem>
 <SelectItem value="cancelled">Cancelled</SelectItem>
 <SelectItem value="postponed">Postponed</SelectItem>
 </SelectContent>
 </Select>

 <Select
 value={filters.skill_level || ''}
 onChange={(e) =>
 setFilters((prev: unknown) => ({ ...prev, skill_level: e.target.value as WorkshopSkillLevel || undefined }))
 }
 >
 <SelectTrigger className="w-40">
 <SelectValue placeholder="All Levels" />
 </SelectTrigger>
 <SelectContent>
 <SelectItem value="">All Levels</SelectItem>
 <SelectItem value="beginner">Beginner</SelectItem>
 <SelectItem value="intermediate">Intermediate</SelectItem>
 <SelectItem value="advanced">Advanced</SelectItem>
 <SelectItem value="expert">Expert</SelectItem>
 <SelectItem value="all_levels">All Levels</SelectItem>
 </SelectContent>
 </Select>

 <Select
 value={filters.format || ''}
 onChange={(e) =>
 setFilters((prev: unknown) => ({ ...prev, format: e.target.value as WorkshopFormat || undefined }))
 }
 >
 <SelectTrigger className="w-40">
 <SelectValue placeholder="All Formats" />
 </SelectTrigger>
 <SelectContent>
 <SelectItem value="">All Formats</SelectItem>
 <SelectItem value="in_person">In Person</SelectItem>
 <SelectItem value="virtual">Virtual</SelectItem>
 <SelectItem value="hybrid">Hybrid</SelectItem>
 </SelectContent>
 </Select>

 <Select
 value={filters.project_id || ''}
 onChange={(e) =>
 setFilters((prev: unknown) => ({ ...prev, project_id: e.target.value || undefined }))
 }
 >
 <SelectTrigger className="w-container-xs">
 <SelectValue placeholder="All Projects" />
 </SelectTrigger>
 <SelectContent>
 <SelectItem value="">All Projects</SelectItem>
 {projects.map((project) => (
 <SelectItem key={project.id} value={project.id}>
 {project.name}
 </SelectItem>
 ))}
 </SelectContent>
 </Select>
 </div>

 <div className="flex items-center gap-xs">
 <Button variant="outline" size="sm" onClick={handleExport}>
 <Download className="mr-2 h-icon-xs w-icon-xs" />
 Export
 </Button>

 {/* View Switcher */}
 <div className="flex items-center rounded-lg border p-xs">
 {(['list', 'grid', 'timeline', 'analytics'] as ViewType[]).map((view) => {
 const Icon = viewIcons[view];
 return (
 <Button
 key={view}
 variant={currentView === view ? 'default' : 'ghost'}
 size="sm"
 onClick={() => setCurrentView(view)}
 className="h-icon-lg w-icon-lg p-0"
 >
 <Icon className="h-icon-xs w-icon-xs" />
 </Button>
 );
 })}
 </div>
 </div>
 </div>

 {/* Bulk Actions */}
 {selectedWorkshops.length > 0 && (
 <div className="mt-4 flex items-center gap-xs rounded-lg border bg-muted/50 p-sm">
 <span className="text-sm text-muted-foreground">
 {selectedWorkshops.length} workshop(s) selected
 </span>
 <div className="flex gap-xs">
 <Button
 size="sm"
 variant="outline"
 onClick={() => handleBulkAction('open_registration', selectedWorkshops)}
 >
 Open Registration
 </Button>
 <Button
 size="sm"
 variant="outline"
 onClick={() => handleBulkAction('cancel', selectedWorkshops)}
 >
 Cancel
 </Button>
 <Button
 size="sm"
 variant="destructive"
 onClick={() => handleBulkAction('delete', selectedWorkshops)}
 >
 Delete
 </Button>
 </div>
 </div>
 )}
 </Card>

 {/* Content */}
 <div className="min-h-content-lg">
 {currentView === 'list' && (
 <ProgrammingWorkshopsListView
 workshops={filteredAndSortedWorkshops}
 loading={loading}
 selectedWorkshops={selectedWorkshops}
 onSelectionChange={setSelectedWorkshops}
 onEdit={handleEditWorkshop}
 onView={handleViewWorkshop}
 onDelete={handleDeleteWorkshop}
 sort={sort}
 onSortChange={setSort}
 />
 )}

 {currentView === 'grid' && (
 <ProgrammingWorkshopsGridView
 workshops={filteredAndSortedWorkshops}
 loading={loading}
 onEdit={handleEditWorkshop}
 onView={handleViewWorkshop}
 onDelete={handleDeleteWorkshop}
 />
 )}

 {currentView === 'timeline' && (
 <ProgrammingWorkshopsTimelineView
 workshops={filteredAndSortedWorkshops}
 loading={loading}
 onEdit={handleEditWorkshop}
 onView={handleViewWorkshop}
 />
 )}

 {currentView === 'analytics' && (
 <ProgrammingWorkshopsAnalyticsView
 workshops={filteredAndSortedWorkshops}
 loading={loading}
 />
 )}
 </div>

 {/* Drawers */}
 <CreateProgrammingWorkshopDrawer
 open={createDrawerOpen}
 onClose={() => setCreateDrawerOpen(false)}
 orgId={orgId}
 currentUserId={currentUserId}
 projects={projects}
 events={events}
 users={users}
 onSuccess={handleSuccess}
 />

 {selectedWorkshop && (
 <>
 <EditProgrammingWorkshopDrawer
 open={editDrawerOpen}
 onClose={() => setEditDrawerOpen(false)}
 workshop={selectedWorkshop}
 orgId={orgId}
 currentUserId={currentUserId}
 projects={projects}
 events={events}
 users={users}
 onSuccess={handleSuccess}
 />

 <ViewProgrammingWorkshopDrawer
 open={viewDrawerOpen}
 onClose={() => setViewDrawerOpen(false)}
 workshop={selectedWorkshop}
 users={users}
 onEdit={() => {
 setViewDrawerOpen(false);
 setEditDrawerOpen(true);
 }}
 onDelete={() => {
 handleDeleteWorkshop(selectedWorkshop.id);
 setViewDrawerOpen(false);
 setSelectedWorkshop(null);
 }}
 />
 </>
 )}
 </div>
 );
}

'use client';


import { useEffect, useState } from 'react';
import {
  Drawer,
  type Button,
  Card,
  Badge,
  StateManagerProvider,
  DataViewProvider,
  ViewSwitcher,
  DataActions,
  DataGrid,
  KanbanBoard,
  CalendarView,
  ListView,
  Button
} from '@ghxstship/ui';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { 
  Plus, 
  Calendar, 
  Clock, 
  Users, 
  BookOpen,
  GraduationCap,
  MapPin,
  User,
  Target
} from 'lucide-react';

export default function WorkshopsClient({ orgId }: { orgId: string }) {
  const t = useTranslations('programming');
  const sb = createBrowserClient();

  const [data, setData] = useState<DataRecord[]>([]);
  const [loading, setLoading] = useState(false);
  const [selectedRecord, setSelectedRecord] = useState<any>(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerMode, setDrawerMode] = useState<'view' | 'edit' | 'create'>('view');

  // Define field configuration for workshops
  const fields: FieldConfig[] = [
    {
      key: 'name',
      label: 'Workshop Title',
      type: 'text',
      required: true,
      sortable: true,
      filterable: true,
      width: 200
    },
    {
      key: 'project_name',
      label: 'Project',
      type: 'text',
      sortable: true,
      filterable: true,
      width: 150
    },
    {
      key: 'starts_at',
      label: 'Start Date',
      type: 'date',
      sortable: true,
      filterable: true,
      width: 130
    },
    {
      key: 'duration_minutes',
      label: 'Duration (min)',
      type: 'number',
      sortable: true,
      filterable: true,
      width: 120
    },
    {
      key: 'instructor',
      label: 'Instructor',
      type: 'text',
      sortable: true,
      filterable: true,
      width: 150
    },
    {
      key: 'max_participants',
      label: 'Max Participants',
      type: 'number',
      sortable: true,
      filterable: true,
      width: 130
    },
    {
      key: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'planning', label: 'Planning' },
        { value: 'open_registration', label: 'Open Registration' },
        { value: 'full', label: 'Full' },
        { value: 'in_progress', label: 'In Progress' },
        { value: 'completed', label: 'Completed' },
        { value: 'cancelled', label: 'Cancelled' }
      ],
      sortable: true,
      filterable: true,
      groupable: true,
      width: 140
    },
    {
      key: 'skill_level',
      label: 'Skill Level',
      type: 'select',
      options: [
        { value: 'beginner', label: 'Beginner' },
        { value: 'intermediate', label: 'Intermediate' },
        { value: 'advanced', label: 'Advanced' },
        { value: 'all_levels', label: 'All Levels' }
      ],
      sortable: true,
      filterable: true,
      groupable: true,
      width: 120
    }
  ];

  useEffect(() => {
    loadWorkshops();
  }, [orgId]);

  const loadWorkshops = async () => {
    if (!orgId) return;

    try {
      setLoading(true);

      const { data: workshopsData, error } = await sb
        .from('events')
        .select(`
          *,
          projects!inner(
            name,
            organization_id
          )
        `)
        .eq('kind', 'workshop')
        .eq('projects.organization_id', orgId)
        .order('starts_at', { ascending: false });

      if (error) throw error;

      // Transform data to include computed fields
      const transformedData = (workshopsData || []).map(workshop => {
        const duration = workshop.ends_at && workshop.starts_at 
          ? Math.round((new Date(workshop.ends_at).getTime() - new Date(workshop.starts_at).getTime()) / (1000 * 60))
          : null;

        return {
          ...workshop,
          project_name: workshop.projects?.name || 'Unknown Project',
          status: computeWorkshopStatus(workshop),
          duration_minutes: duration,
          instructor: workshop.instructor || 'TBD',
          max_participants: workshop.max_participants || 20,
          skill_level: workshop.skill_level || 'all_levels',
          current_participants: workshop.current_participants || 0
        };
      });

      setData(transformedData);
    } catch (error) {
      console.error('Error loading workshops:', error);
    } finally {
      setLoading(false);
    }
  };

  const computeWorkshopStatus = (workshop: any) => {
    const now = new Date();
    const startDate = workshop.starts_at ? new Date(workshop.starts_at) : null;
    const endDate = workshop.ends_at ? new Date(workshop.ends_at) : null;

    if (!startDate) return 'planning';

    // Check if workshop is happening now
    if (startDate <= now && (!endDate || endDate >= now)) return 'in_progress';

    // Check if workshop is completed
    if (endDate && endDate < now) return 'completed';

    // Check registration status for future workshops
    if (startDate > now) {
      const maxParticipants = workshop.max_participants || 20;
      const currentParticipants = workshop.current_participants || 0;

      if (currentParticipants >= maxParticipants) return 'full';
      return 'open_registration';
    }

    return 'planning';
  };

  const handleCreateWorkshop = () => {
    setSelectedRecord(null);
    setDrawerMode('create');
    setDrawerOpen(true);
  };

  const handleEditWorkshop = (workshop: any) => {
    setSelectedRecord(workshop);
    setDrawerMode('edit');
    setDrawerOpen(true);
  };

  const handleViewWorkshop = (workshop: any) => {
    setSelectedRecord(workshop);
    setDrawerMode('view');
    setDrawerOpen(true);
  };

  const handleSaveWorkshop = async (workshopData: any) => {
    try {
      const eventData = {
        ...workshopData,
        kind: 'workshop'
      };

      if (drawerMode === 'create') {
        const { error } = await sb.from('events').insert(eventData);
        if (error) throw error;
      } else if (drawerMode === 'edit') {
        const { error } = await sb
          .from('events')
          .update(eventData)
          .eq('id', selectedRecord.id);
        if (error) throw error;
      }

      await loadWorkshops();
      setDrawerOpen(false);
    } catch (error) {
      console.error('Error saving workshop:', error);
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'planning':
        return 'color-muted bg-secondary/50';
      case 'open_registration':
        return 'color-success bg-success/10';
      case 'full':
        return 'color-warning bg-warning/10';
      case 'in_progress':
        return 'color-accent bg-accent/10';
      case 'completed':
        return 'color-secondary bg-secondary/10';
      case 'cancelled':
        return 'color-destructive bg-destructive/10';
      default:
        return 'color-muted bg-secondary/50';
    }
  };

  const getSkillLevelColor = (level: string) => {
    switch (level) {
      case 'beginner':
        return 'color-success bg-success/10';
      case 'intermediate':
        return 'color-warning bg-warning/10';
      case 'advanced':
        return 'color-destructive bg-destructive/10';
      case 'all_levels':
        return 'color-accent bg-accent/10';
      default:
        return 'color-muted bg-secondary/50';
    }
  };

  // Configure DataView
  const config: DataViewConfig = {
    id: 'workshops-management',
    name: 'Workshops Management',
    viewType: 'grid',
    defaultView: 'grid',
    fields,
    data,

    onSearch: (query: string) => {
      console.log('Search workshops:', query);
    },
    onFilter: (filters: any[]) => {
      console.log('Filter workshops:', filters);
    },
    onSort: (sorts: any[]) => {
      console.log('Sort workshops:', sorts);
    },
    onRefresh: loadWorkshops,
    onExport: (data: any[], format: string) => {
      console.log('Export workshops:', format, data);
    },
    onImport: (data: any[]) => {
      console.log('Import workshops:', data);
    }
  };

  return (
    <div className="stack-md">
      <DataViewProvider config={config}>
        <StateManagerProvider>
          <div className="stack-md">
            {/* Header Actions */}
            <div className="flex items-center justify-between mb-md">
              <div className="flex items-center gap-md">
                <h2 className="text-body text-heading-4">Workshop Management</h2>
                <Button>
                  <Plus className="h-icon-xs w-icon-xs mr-sm" />
                  Create Workshop
                </Button>
              </div>
              <div className="flex items-center gap-sm">
                <ViewSwitcher />
                <DataActions />
              </div>
            </div>

            {/* Workshop Stats */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-md mb-lg">
              <Card className="p-md">
                <div className="flex items-center gap-sm">
                  <div className="p-sm bg-success/10 rounded-lg">
                    <BookOpen className="h-icon-sm w-icon-sm color-success" />
                  </div>
                  <div>
                    <div className="text-heading-3 text-heading-3">
                      {data.filter(w => w.status === 'open_registration').length}
                    </div>
                    <div className="text-body-sm color-muted">Open Registration</div>
                  </div>
                </div>
              </Card>

              <Card className="p-md">
                <div className="flex items-center gap-sm">
                  <div className="p-sm bg-accent/10 rounded-lg">
                    <GraduationCap className="h-icon-sm w-icon-sm color-accent" />
                  </div>
                  <div>
                    <div className="text-heading-3 text-heading-3">
                      {data.filter(w => w.status === 'in_progress').length}
                    </div>
                    <div className="text-body-sm color-muted">In Progress</div>
                  </div>
                </div>
              </Card>

              <Card className="p-md">
                <div className="flex items-center gap-sm">
                  <div className="p-sm bg-secondary/10 rounded-lg">
                    <Users className="h-icon-sm w-icon-sm color-secondary" />
                  </div>
                  <div>
                    <div className="text-heading-3 text-heading-3">
                      {data.reduce((sum, w) => sum + (w.current_participants || 0), 0)}
                    </div>
                    <div className="text-body-sm color-muted">Total Participants</div>
                  </div>
                </div>
              </Card>

              <Card className="p-md">
                <div className="flex items-center gap-sm">
                  <div className="p-sm bg-warning/10 rounded-lg">
                    <Target className="h-icon-sm w-icon-sm color-warning" />
                  </div>
                  <div>
                    <div className="text-heading-3 text-heading-3">
                      {data.filter(w => w.status === 'completed').length}
                    </div>
                    <div className="text-body-sm color-muted">Completed</div>
                  </div>
                </div>
              </Card>
            </div>

            {/* Data Views */}
            <DataGrid />

            <KanbanBoard 
              columns={[
                { id: 'planning', title: 'Planning' },
                { id: 'open_registration', title: 'Open Registration' },
                { id: 'full', title: 'Full' },
                { id: 'in_progress', title: 'In Progress' },
                { id: 'completed', title: 'Completed' }
              ]}
              statusField="status"
              titleField="name"
              onCardClick={handleViewWorkshop}
            />

            <CalendarView 
              startDateField="starts_at"
              endDateField="ends_at"
              titleField="name"

            />

            <ListView 
              titleField="name"
              subtitleField="project_name"
              onItemClick={handleViewWorkshop}
            />

            {/* Workshop Details Drawer */}
            <Drawer
              open={drawerOpen}
              onClose={() => {
                setDrawerOpen(false);
                setSelectedRecord(null);
              }}
              title={
                drawerMode === 'create' 
                  ? 'Create Workshop' 
                  : selectedRecord?.name || 'Workshop Details'
              }
            >
              {/* Custom Workshop Details */}
              {selectedRecord && (
                <div className="stack-lg mt-lg">
                  {/* Workshop Info */}
                  <div className="grid grid-cols-2 gap-md">
                    <div className="flex items-center gap-sm text-body-sm">
                      <Calendar className="h-icon-xs w-icon-xs color-muted" />
                      <span className="form-label">Date:</span>
                      <span>
                        {selectedRecord.starts_at 
                          ? new Date(selectedRecord.starts_at).toLocaleDateString()
                          : 'Not scheduled'
                        }
                      </span>
                    </div>
                    <div className="flex items-center gap-sm text-body-sm">
                      <Clock className="h-icon-xs w-icon-xs color-muted" />
                      <span className="form-label">Time:</span>
                      <span>
                        {selectedRecord.starts_at 
                          ? new Date(selectedRecord.starts_at).toLocaleTimeString([], { 
                              hour: '2-digit', 
                              minute: '2-digit' 
                            })
                          : 'TBD'
                        }
                      </span>
                    </div>
                    <div className="flex items-center gap-sm text-body-sm">
                      <User className="h-icon-xs w-icon-xs color-muted" />
                      <span className="form-label">Instructor:</span>
                      <span>{selectedRecord.instructor || 'TBD'}</span>
                    </div>
                    <div className="flex items-center gap-sm text-body-sm">
                      <Clock className="h-icon-xs w-icon-xs color-muted" />
                      <span className="form-label">Duration:</span>
                      <span>{selectedRecord.duration_minutes ? `${selectedRecord.duration_minutes} min` : 'TBD'}</span>
                    </div>
                  </div>

                  {/* Status and Level Badges */}
                  <div className="flex items-center gap-md">
                    <div className="flex items-center gap-sm">
                      <span className="form-label text-body-sm">Status:</span>
                      <Badge className={getStatusColor(selectedRecord.status)}>
                        {selectedRecord.status?.replace('_', ' ') || 'Planning'}
                      </Badge>
                    </div>
                    <div className="flex items-center gap-sm">
                      <span className="form-label text-body-sm">Level:</span>
                      <Badge className={getSkillLevelColor(selectedRecord.skill_level)}>
                        {selectedRecord.skill_level?.replace('_', ' ') || 'All Levels'}
                      </Badge>
                    </div>
                  </div>

                  {/* Participation Metrics */}
                  <div className="grid grid-cols-2 gap-md pt-md border-t">
                    <div className="text-center">
                      <div className="text-heading-3 text-heading-3 color-accent">
                        {selectedRecord.current_participants || 0}
                      </div>
                      <div className="text-body-sm color-muted flex items-center justify-center gap-xs">
                        <Users className="h-3 w-3" />
                        Current Participants
                      </div>
                    </div>
                    <div className="text-center">
                      <div className="text-heading-3 text-heading-3 color-success">
                        {selectedRecord.max_participants || 20}
                      </div>
                      <div className="text-body-sm color-muted flex items-center justify-center gap-xs">
                        <Target className="h-3 w-3" />
                        Max Capacity
                      </div>
                    </div>
                  </div>

                  {/* Workshop Description */}
                  {selectedRecord.description && (
                    <div className="pt-md border-t">
                      <h4 className="form-label text-body-sm mb-sm flex items-center gap-sm">
                        <BookOpen className="h-icon-xs w-icon-xs" />
                        Workshop Description
                      </h4>
                      <p className="text-body-sm color-muted bg-secondary/50 p-sm rounded-lg">
                        {selectedRecord.description}
                      </p>
                    </div>
                  )}

                  {/* Quick Actions */}
                  <div className="flex gap-sm pt-md border-t">
                    <Button>
                      <Users className="h-icon-xs w-icon-xs mr-sm" />
                      Manage Participants
                    </Button>
                    <Button>
                      <BookOpen className="h-icon-xs w-icon-xs mr-sm" />
                      Course Materials
                    </Button>
                    <Button>
                      <MapPin className="h-icon-xs w-icon-xs mr-sm" />
                      Room Assignment
                    </Button>
                  </div>
                </div>
              )}
            </Drawer>

            {/* Empty State */}
            {!loading && data.length === 0 && (
              <Card className="p-xl text-center">
                <BookOpen className="h-icon-2xl w-icon-2xl mx-auto mb-md color-muted" />
                <h3 className="text-body text-heading-4 mb-sm">No Workshops Yet</h3>
                <p className="color-muted mb-md">
                  Start creating educational workshops and training sessions for your events.
                </p>
                <Button onClick={handleCreateWorkshop}>
                  <Plus className="h-icon-xs w-icon-xs mr-sm" />
                  Create First Workshop
                </Button>
              </Card>
            )}
          </div>
        </StateManagerProvider>
      </DataViewProvider>
    </div>
  );
}

"use client";

import { Grid3x3, LayoutGrid, Calendar, List, Search, Filter, Download, Upload, MoreVertical, Plus, Edit, Trash2, Eye, Copy, CheckCircle, Clock, Play, Rocket, DollarSign, Building, MapPin, ArrowUpDown, Columns } from "lucide-react";
import { useState, useCallback, useMemo, useEffect } from 'react';
import { useRouter } from "next/navigation";
import { createBrowserClient } from "@ghxstship/auth";
import {
 Card,
 Button,
 Badge,
 Input,
 Select,
 Checkbox,
 Tabs,
 Dropdown,

 DropdownItem,

 toast,
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator
} from "@ghxstship/ui";
import { format, parseISO } from "date-fns";
import CreateActivationDrawer from "./drawers/CreateActivationDrawer";
import EditActivationDrawer from "./drawers/EditActivationDrawer";
import ViewActivationDrawer from "./drawers/ViewActivationDrawer";
import ActivationGridView from "./views/ActivationGridView";
import ActivationKanbanView from "./views/ActivationKanbanView";
import ActivationCalendarView from "./views/ActivationCalendarView";
import ActivationListView from "./views/ActivationListView";

// Types
export interface Activation {
 id: string;
 name: string;
 description?: string;
 status: "planning" | "ready" | "active" | "completed" | "cancelled";
 activation_type: "soft_launch" | "beta" | "full_launch" | "pilot" | "rollout";
 project_id?: string;
 project?: {
 id: string;
 name: string;
 status: string;
 };
 scheduled_date?: string;
 actual_date?: string;
 completion_date?: string;
 location?: string;
 budget?: number;
 actual_cost?: number;
 success_metrics?: Record<string, unknown>;
 stakeholders?: string[];
 dependencies?: string[];
 risks?: string[];
 notes?: string;
 created_at: string;
 updated_at: string;
 organization_id: string;
}

interface ActivationsClientProps {
 orgId: string;
 initialActivations?: Activation[];
 projects?: Array<{ id: string; name: string }>;
}

// View configurations
const VIEW_TYPES = [
 { id: "grid", label: "Grid", icon: Grid3x3 },
 { id: "kanban", label: "Kanban", icon: LayoutGrid },
 { id: "calendar", label: "Calendar", icon: Calendar },
 { id: "list", label: "List", icon: List },
] as const;

type ViewType = typeof VIEW_TYPES[number]["id"];

// Field configuration
export const FIELD_CONFIG = [
 { id: "name", label: "Name", visible: true, sortable: true },
 { id: "status", label: "Status", visible: true, sortable: true },
 { id: "activation_type", label: "Type", visible: true, sortable: true },
 { id: "project", label: "Project", visible: true, sortable: true },
 { id: "scheduled_date", label: "Scheduled Date", visible: true, sortable: true },
 { id: "actual_date", label: "Actual Date", visible: false, sortable: true },
 { id: "completion_date", label: "Completion Date", visible: false, sortable: true },
 { id: "location", label: "Location", visible: true, sortable: false },
 { id: "budget", label: "Budget", visible: true, sortable: true },
 { id: "actual_cost", label: "Actual Cost", visible: false, sortable: true },
 { id: "created_at", label: "Created", visible: false, sortable: true },
 { id: "updated_at", label: "Updated", visible: false, sortable: true },
];

export default function ActivationsClient({
 orgId,
 initialActivations = [],
 projects = []
}: ActivationsClientProps) {
 const router = useRouter();
 const supabase = createBrowserClient();

 // State
 const [activations, setActivations] = useState<Activation[]>(initialActivations);
 const [loading, setLoading] = useState(false);
 const [viewType, setViewType] = useState<ViewType>("grid");
 const [searchQuery, setSearchQuery] = useState("");
 const [selectedStatus, setSelectedStatus] = useState<string>("all");
 const [selectedType, setSelectedType] = useState<string>("all");
 const [selectedProject, setSelectedProject] = useState<string>("all");
 const [sortField, setSortField] = useState<string>("scheduled_date");
 const [sortDirection, setSortDirection] = useState<"asc" | "desc">("desc");
 const [selectedActivations, setSelectedActivations] = useState<Set<string>>(new Set());
 const [fieldVisibility, setFieldVisibility] = useState(FIELD_CONFIG);

 // Drawers
 const [createDrawerOpen, setCreateDrawerOpen] = useState(false);
 const [editDrawerOpen, setEditDrawerOpen] = useState(false);
 const [viewDrawerOpen, setViewDrawerOpen] = useState(false);
 const [selectedActivation, setSelectedActivation] = useState<Activation | null>(null);

 // Load activations from Supabase
 const loadActivations = useCallback(async () => {
 setLoading(true);
 try {
 const query = supabase
 .from("project_activations")
 .select(`
 *,
 project:projects(id, name, status)
 `)
 .eq("organization_id", orgId)
 .order(sortField, { ascending: sortDirection === "asc" });

 // Apply filters
 if (selectedStatus !== "all") {
 query.eq("status", selectedStatus);
 }
 if (selectedType !== "all") {
 query.eq("activation_type", selectedType);
 }
 if (selectedProject !== "all") {
 query.eq("project_id", selectedProject);
 }

 const { data, error } = await query;

 if (error) throw error;
 setActivations(data || []);
 } catch (error) {
 console.error("Error loading activations:", error);
 toast.error("Failed to load activations");
 } finally {
 setLoading(false);
 }
 }, [supabase, orgId, selectedStatus, selectedType, selectedProject, sortField, sortDirection]);

 // Load activations on mount and when filters change
 useEffect(() => {
 loadActivations();
 }, [loadActivations]);

 // Real-time subscription
 useEffect(() => {
 const channel = supabase
 .channel("activations-changes")
 .on(
 "postgres_changes",
 {
 event: "*",
 schema: "public",
 table: "project_activations",
 filter: `organization_id=eq.${orgId}`
 },
 (payload) => {
 loadActivations();
 }
 )
 .subscribe();

 return () => {
 supabase.removeChannel(channel);
 };
 }, [supabase, orgId, loadActivations]);

 // Filter activations based on search
 const filteredActivations = useMemo(() => {
 return activations.filter((activation) => {
 if (searchQuery) {
 const query = searchQuery.toLowerCase();
 return (
 activation.name.toLowerCase().includes(query) ||
 activation.description?.toLowerCase().includes(query) ||
 activation.location?.toLowerCase().includes(query) ||
 activation.project?.name.toLowerCase().includes(query)
 );
 }
 return true;
 });
 }, [activations, searchQuery]);

 // Sort activations
 const sortedActivations = useMemo(() => {
 return [...filteredActivations].sort((a, b) => {
 let aVal: unknown = a[sortField as keyof Activation];
 let bVal: unknown = b[sortField as keyof Activation];

 // Handle nested project field
 if (sortField === "project") {
 aVal = a.project?.name || "";
 bVal = b.project?.name || "";
 }

 // Handle dates
 if (sortField.includes("date") || sortField.includes("_at")) {
 aVal = aVal ? new Date(aVal).getTime() : 0;
 bVal = bVal ? new Date(bVal).getTime() : 0;
 }

 // Handle numbers
 if (typeof aVal === "number" && typeof bVal === "number") {
 return sortDirection === "asc" ? aVal - bVal : bVal - aVal;
 }

 // Handle strings
 const aStr = String(aVal || "").toLowerCase();
 const bStr = String(bVal || "").toLowerCase();
 return sortDirection === "asc" 
 ? aStr.localeCompare(bStr)
 : bStr.localeCompare(aStr);
 });
 }, [filteredActivations, sortField, sortDirection]);

 // Handle selection
 const handleSelectAll = useCallback(() => {
 if (selectedActivations.size === sortedActivations.length) {
 setSelectedActivations(new Set());
 } else {
 setSelectedActivations(new Set(sortedActivations.map((a) => a.id)));
 }
  }, [selectedActivations, sortedActivations, setSelectedActivations]);

 const handleSelectActivation = useCallback((id: string) => {
 setSelectedActivations((prev: Set<string>) => {
 const next = new Set(prev);
 if (next.has(id)) {
 next.delete(id);
 } else {
 next.add(id);
 }
});
}, [selectedActivations, setSelectedActivations]);

// Handle actions
const handleView = useCallback(
  (activation: Activation) => {
    setSelectedActivation(activation);
    setViewDrawerOpen(true);
  },
  [setSelectedActivation, setViewDrawerOpen]
);

const handleEdit = useCallback(
  (activation: Activation) => {
    setSelectedActivation(activation);
    setEditDrawerOpen(true);
  },
  [setSelectedActivation, setEditDrawerOpen]
);

 const handleDelete = useCallback(async (activation: Activation) => {
 if (!confirm(`Are you sure you want to delete "${activation.name}"?`)) return;

 try {
 const { error } = await supabase
 .from("project_activations")
 .delete()
 .eq("id", activation.id);

 if (error) throw error;

 toast.success("Activation deleted successfully");
 loadActivations();
 } catch (error) {
 console.error("Error deleting activation:", error);
 toast.error("Failed to delete activation");
 }
 }, [supabase, loadActivations]);

 const handleDuplicate = useCallback(async (activation: Activation) => {
 try {
 const { id, created_at, updated_at, ...activationData } = activation;
 const { data, error } = await supabase
 .from("project_activations")
 .insert({
 ...activationData,
 name: `${activation.name} (Copy)`,
 status: "planning"
 })
 .select()
 .single();

 if (error) throw error;

 toast.success("Activation duplicated successfully");
 loadActivations();
 } catch (error) {
 console.error("Error duplicating activation:", error);
 toast.error("Failed to duplicate activation");
 }
 }, [supabase, loadActivations]);

 const handleStatusChange = useCallback(async (activation: Activation, newStatus: string) => {
 try {
 const updates: unknown = { status: newStatus };

 // Set actual_date when activation goes active
 if (newStatus === "active" && !activation.actual_date) {
 updates.actual_date = new Date().toISOString();
 }

 // Set completion_date when activation is completed
 if (newStatus === "completed" && !activation.completion_date) {
 updates.completion_date = new Date().toISOString();
 }

 const { error } = await supabase
 .from("project_activations")
 .update(updates)
 .eq("id", activation.id);

 if (error) throw error;

 toast.success(`Activation status updated to ${newStatus}`);
 loadActivations();
 } catch (error) {
 console.error("Error updating activation status:", error);
 toast.error("Failed to update activation status");
 }
 }, [supabase, loadActivations]);

 return (
 <div className="space-y-md">
 {/* Header */}
 <Card className="p-lg">
 <div className="flex items-center justify-between mb-md">
 <div>
 <h1 className="text-2xl font-bold">Project Activations</h1>
 <p className="text-muted-foreground">
 Manage project launches, go-live processes, and activations
 </p>
 </div>
 <Button onClick={() => setCreateDrawerOpen(true)}>
 <Plus className="mr-2 h-icon-xs w-icon-xs" />
 New Activation
 </Button>
 </div>

 {/* Filters and View Switcher */}
 <div className="flex flex-col lg:flex-row gap-md">
 <div className="flex-1 flex flex-wrap gap-sm">
 <div className="relative flex-1 min-w-content-narrow">
 <Search className="absolute left-3 top-xs/2 transform -translate-y-1/2 h-icon-xs w-icon-xs text-muted-foreground" />
 <Input
 placeholder="Search activations..."
 value={searchQuery}
 onChange={(e) => setSearchQuery(e.target.value)}
 className="pl-9"
 />
 </div>

 <Select value={selectedStatus} onValueChange={setSelectedStatus}>
 <option value="all">All Status</option>
 <option value="planning">Planning</option>
 <option value="ready">Ready</option>
 <option value="active">Active</option>
 <option value="completed">Completed</option>
 <option value="cancelled">Cancelled</option>
 </Select>

 <Select value={selectedType} onValueChange={setSelectedType}>
 <option value="all">All Types</option>
 <option value="soft_launch">Soft Launch</option>
 <option value="beta">Beta</option>
 <option value="full_launch">Full Launch</option>
 <option value="pilot">Pilot</option>
 <option value="rollout">Rollout</option>
 </Select>

 {projects.length > 0 && (
 <Select value={selectedProject} onValueChange={setSelectedProject}>
 <option value="all">All Projects</option>
 {projects.map((project) => (
 <option key={project.id} value={project.id}>
 {project.name}
 </option>
 ))}
 </Select>
 )}
 </div>

 {/* View Switcher */}
 <div className="flex items-center gap-sm">
 <DropdownMenu>
 <DropdownMenuTrigger asChild>
 <Button variant="outline" size="sm">
 <Columns className="mr-2 h-icon-xs w-icon-xs" />
 Columns
 </Button>
 </DropdownMenuTrigger>
 <DropdownMenuContent align="end" className="w-content-narrow">
 {fieldVisibility.map((field) => (
 <DropdownMenuItem key={field.id} asChild>
 <label className="flex items-center gap-xs cursor-pointer">
 <Checkbox
 checked={field.visible}
 onCheckedChange={(checked) => {
 setFieldVisibility((prev: unknown) =>
 prev.map((f) =>
 f.id === field.id ? { ...f, visible: !!checked } : f
 )
 );
 }}
 />
 {field.label}
 </label>
 </DropdownMenuItem>
 ))}
 </DropdownMenuContent>
 </DropdownMenu>

 <div className="flex rounded-md shadow-sm">
 {VIEW_TYPES.map((view) => {
 const Icon = view.icon;
 return (
 <Button
 key={view.id}
 variant={viewType === view.id ? "secondary" : "outline"}
 size="sm"
 onClick={() => setViewType(view.id as ViewType)}
 className="rounded-none first:rounded-l-md last:rounded-r-md"
 >
 <Icon className="h-icon-xs w-icon-xs" />
 </Button>
 );
 })}
 </div>
 </div>
 </div>
 </Card>

 {/* Bulk Actions Bar */}
 {selectedActivations.size > 0 && (
 <Card className="p-sm">
 <div className="flex items-center justify-between">
 <div className="flex items-center gap-sm">
 <Checkbox
 checked={selectedActivations.size === sortedActivations.length}
 onCheckedChange={handleSelectAll}
 />
 <span className="text-sm text-muted-foreground">
 {selectedActivations.size} selected
 </span>
 </div>
 <div className="flex items-center gap-sm">
 <Button
 variant="outline"
 size="sm"
 onClick={() => handleStatusChange(Array.from(selectedActivations)[0] as unknown, "active")}
 >
 Set Active
 </Button>
 <Button
 variant="outline"
 size="sm"
 onClick={() => handleStatusChange(Array.from(selectedActivations)[0] as unknown, "completed")}
 >
 Set Completed
 </Button>
 <Button
 variant="destructive"
 size="sm"
 onClick={async () => {
 if (confirm(`Delete ${selectedActivations.size} activations?`)) {
 for (const id of selectedActivations) {
 await supabase.from("project_activations").delete().eq("id", id);
 }
 loadActivations();
 setSelectedActivations(new Set());
 }
 }}
 >
 Delete
 </Button>
 </div>
 </div>
 </Card>
 )}

 {/* Content Views */}
 <Card className="p-lg">
 {loading ? (
 <div className="flex items-center justify-center py-xl">
 <div className="animate-spin rounded-full h-icon-lg w-icon-lg border-b-2 border-primary"></div>
 </div>
 ) : sortedActivations.length === 0 ? (
 <div className="text-center py-xl">
 <Rocket className="mx-auto h-icon-2xl w-icon-2xl text-muted-foreground mb-md" />
 <h3 className="text-lg font-semibold mb-sm">No activations found</h3>
 <p className="text-muted-foreground mb-md">
 {searchQuery || selectedStatus !== "all" || selectedType !== "all"
 ? "Try adjusting your filters"
 : "Get started by creating your first activation"}
 </p>
 {!searchQuery && selectedStatus === "all" && selectedType === "all" && (
 <Button onClick={() => setCreateDrawerOpen(true)}>
 <Plus className="mr-2 h-icon-xs w-icon-xs" />
 Create Activation
 </Button>
 )}
 </div>
 ) : (
 <>
 {viewType === "grid" && (
 <ActivationGridView
 activations={sortedActivations}
 selectedActivations={selectedActivations}
 onSelectActivation={handleSelectActivation}
 onView={handleView}
 onEdit={handleEdit}
 onDelete={handleDelete}
 onDuplicate={handleDuplicate}
 onStatusChange={handleStatusChange}
 />
 )}
 {viewType === "kanban" && (
 <ActivationKanbanView
 activations={sortedActivations}
 selectedActivations={selectedActivations}
 onSelectActivation={handleSelectActivation}
 onView={handleView}
 onEdit={handleEdit}
 onDelete={handleDelete}
 onStatusChange={handleStatusChange}
 />
 )}
 {viewType === "calendar" && (
 <ActivationCalendarView
 activations={sortedActivations}
 onView={handleView}
 />
 )}
 {viewType === "list" && (
 <ActivationListView
 activations={sortedActivations}
 selectedActivations={selectedActivations}
 fieldVisibility={fieldVisibility}
 sortField={sortField}
 sortDirection={sortDirection}
 onSelectAll={handleSelectAll}
 onSelectActivation={handleSelectActivation}
 onSort={(field) => {
 if (sortField === field) {
 setSortDirection(sortDirection === "asc" ? "desc" : "asc");
 } else {
 setSortField(field);
 setSortDirection("asc");
 }
 }}
 onView={handleView}
 onEdit={handleEdit}
 onDelete={handleDelete}
 onDuplicate={handleDuplicate}
 onStatusChange={handleStatusChange}
 />
 )}
 </>
 )}
 </Card>

 {/* Drawers */}
 <CreateActivationDrawer
 open={createDrawerOpen}
 onClose={() => setCreateDrawerOpen(false)}
 orgId={orgId}
 projects={projects}
 onSuccess={loadActivations}
 />

 {selectedActivation && (
 <>
 <EditActivationDrawer
 open={editDrawerOpen}
 onClose={() => setEditDrawerOpen(false)}
 activation={selectedActivation}
 projects={projects}
 onSuccess={loadActivations}
 />

 <ViewActivationDrawer
 open={viewDrawerOpen}
 onClose={() => setViewDrawerOpen(false)}
 activation={selectedActivation}
 onEdit={() => {
 setViewDrawerOpen(false);
 setEditDrawerOpen(true);
 }}
 />
 </>
 )}
 </div>
 );
}

"use client";

import { Grid3x3, Map, List, LayoutGrid, Search, Filter, Download, Upload, MoreVertical, Plus, Edit, Trash2, Eye, Copy, Archive, MapPin, Building, Users, Navigation, Globe, Home, Warehouse, Store, Building2, TreePine, Waves, Mountain, ChevronDown, ArrowUpDown, ArrowUp, ArrowDown, Columns, ExternalLink } from "lucide-react";
import { useState, useCallback, useMemo, useEffect } from 'react';
import { useRouter } from "next/navigation";
import { createBrowserClient } from "@ghxstship/auth";
import {
 Card,
 Button,
 Badge,
 Input,
 Select,
 Checkbox,
 Tabs,
 toast
} from "@ghxstship/ui";
import { format, parseISO } from "date-fns";
import CreateLocationDrawer from "./CreateLocationDrawer";
import EditLocationDrawer from "./EditLocationDrawer";
import ViewLocationDrawer from "./ViewLocationDrawer";
import LocationGridView from "./views/LocationGridView";
import LocationMapView from "./views/LocationMapView";
import LocationListView from "./views/LocationListView";
import LocationGalleryView from "./views/LocationGalleryView";

// Types
export interface Location {
 id: string;
 organization_id: string;
 project_id?: string;
 project?: {
 id: string;
 name: string;
 status: string;
 };
 name: string;
 type?: "venue" | "office" | "warehouse" | "retail" | "outdoor" | "studio" | "residential" | "other";
 address?: string;
 city?: string;
 state?: string;
 country?: string;
 postal_code?: string;
 coordinates?: { x: number; y: number } | null;
 capacity?: number;
 size?: number; // square feet/meters
 amenities?: string[];
 accessibility_features?: string[];
 parking_available?: boolean;
 parking_capacity?: number;
 public_transport?: string;
 contact_name?: string;
 contact_phone?: string;
 contact_email?: string;
 operating_hours?: string;
 rental_rate?: number;
 currency?: string;
 availability_status?: "available" | "booked" | "maintenance" | "unavailable";
 images?: string[];
 floor_plans?: string[];
 notes?: string;
 tags?: string[];
 is_featured?: boolean;
 is_demo?: boolean;
 created_at: string;
 updated_at: string;
 created_by?: string;
}

interface LocationsClientProps {
 orgId: string;
 projectId?: string;
 initialLocations?: Location[];
 projects?: Array<{ id: string; name: string }>;
}

// View configurations
const VIEW_TYPES = [
 { id: "grid", label: "Grid", icon: Grid3x3 },
 { id: "map", label: "Map", icon: Map },
 { id: "list", label: "List", icon: List },
 { id: "gallery", label: "Gallery", icon: LayoutGrid },
] as const;

type ViewType = typeof VIEW_TYPES[number]["id"];

// Field configuration for visibility and ordering
const FIELD_CONFIG = [
 { id: "name", label: "Name", visible: true, sortable: true },
 { id: "type", label: "Type", visible: true, sortable: true },
 { id: "address", label: "Address", visible: true, sortable: false },
 { id: "city", label: "City", visible: true, sortable: true },
 { id: "state", label: "State", visible: false, sortable: true },
 { id: "country", label: "Country", visible: false, sortable: true },
 { id: "capacity", label: "Capacity", visible: true, sortable: true },
 { id: "size", label: "Size", visible: false, sortable: true },
 { id: "availability_status", label: "Availability", visible: true, sortable: true },
 { id: "rental_rate", label: "Rate", visible: false, sortable: true },
 { id: "project", label: "Project", visible: true, sortable: true },
 { id: "contact_name", label: "Contact", visible: false, sortable: false },
 { id: "created_at", label: "Created", visible: false, sortable: true },
 { id: "updated_at", label: "Updated", visible: false, sortable: true },
];

export default function LocationsClient({
 orgId,
 projectId,
 initialLocations = [],
 projects = []
}: LocationsClientProps) {
 const router = useRouter();
 const supabase = createBrowserClient();

 // State
 const [locations, setLocations] = useState<Location[]>(initialLocations);
 const [loading, setLoading] = useState(false);
 const [viewType, setViewType] = useState<ViewType>("grid");
 const [searchQuery, setSearchQuery] = useState("");
 const [selectedType, setSelectedType] = useState<string>("all");
 const [selectedAvailability, setSelectedAvailability] = useState<string>("all");
 const [selectedProject, setSelectedProject] = useState<string>(projectId || "all");
 const [sortField, setSortField] = useState<string>("created_at");
 const [sortDirection, setSortDirection] = useState<"asc" | "desc">("desc");
 const [selectedLocations, setSelectedLocations] = useState<Set<string>(new Set());
 const [fieldVisibility, setFieldVisibility] = useState(FIELD_CONFIG);

 // Drawers
 const [createDrawerOpen, setCreateDrawerOpen] = useState(false);
 const [editDrawerOpen, setEditDrawerOpen] = useState(false);
 const [viewDrawerOpen, setViewDrawerOpen] = useState(false);
 const [selectedLocation, setSelectedLocation] = useState<Location | null>(null);

 // Load locations from Supabase
 const loadLocations = useCallback(async () => {
 setLoading(true);
 try {
 let query = supabase
 .from("locations")
 .select(`
 *,
 project:projects(id, name, status)
 `)
 .eq("organization_id", orgId)
 .order(sortField, { ascending: sortDirection === "asc" });

 // Apply filters
 if (projectId) {
 query = query.eq("project_id", projectId);
 } else if (selectedProject !== "all") {
 query = query.eq("project_id", selectedProject);
 }

 const { data, error } = await query;

 if (error) throw error;

 // Transform data to match our interface
 const transformedData = (data || []).map(loc => ({
 ...loc,
 type: loc.type || "other",
 availability_status: loc.availability_status || "available",
 amenities: loc.amenities || [],
 accessibility_features: loc.accessibility_features || [],
 images: loc.images || [],
 floor_plans: loc.floor_plans || [],
 tags: loc.tags || []
 }));

 setLocations(transformedData);
 } catch (error) {
 console.error("Error loading locations:", error);
 toast.error("Failed to load locations");
 } finally {
 setLoading(false);
 }
 }, [supabase, orgId, projectId, selectedProject, sortField, sortDirection]);

 // Load locations on mount and when filters change
 useEffect(() => {
 loadLocations();
 }, [loadLocations]);

 // Real-time subscription
 useEffect(() => {
 const channel = supabase
 .channel("locations-changes")
 .on(
 "postgres_changes",
 {
 event: "*",
 schema: "public",
 table: "locations",
 filter: `organization_id=eq.${orgId}`
 },
 (payload) => {
 loadLocations();
 }
 )
 .subscribe();

 return () => {
 supabase.removeChannel(channel);
 };
 }, [supabase, orgId, loadLocations]);

 // Filter locations based on search
 const filteredLocations = useMemo(() => {
 return locations.filter((location) => {
 if (searchQuery) {
 const query = searchQuery.toLowerCase();
 return (
 location.name.toLowerCase().includes(query) ||
 location.address?.toLowerCase().includes(query) ||
 location.city?.toLowerCase().includes(query) ||
 location.state?.toLowerCase().includes(query) ||
 location.country?.toLowerCase().includes(query) ||
 location.notes?.toLowerCase().includes(query) ||
 location.contact_name?.toLowerCase().includes(query) ||
 location.tags?.some(tag => tag.toLowerCase().includes(query))
 );
 }
 return true;
 });
 }, [locations, searchQuery]);

 // Apply type and availability filters
 const filteredAndTypedLocations = useMemo(() => {
 return filteredLocations.filter((location) => {
 if (selectedType !== "all" && location.type !== selectedType) {
 return false;
 }
 if (selectedAvailability !== "all" && location.availability_status !== selectedAvailability) {
 return false;
 }
 return true;
 });
 }, [filteredLocations, selectedType, selectedAvailability]);

 // Sort locations
 const sortedLocations = useMemo(() => {
 return [...filteredAndTypedLocations].sort((a, b) => {
 let aVal: unknown = a[sortField as keyof Location];
 let bVal: unknown = b[sortField as keyof Location];

 // Handle nested fields
 if (sortField === "project") {
 aVal = a.project?.name || "";
 bVal = b.project?.name || "";
 }

 // Handle dates
 if (sortField.includes("_at")) {
 aVal = aVal ? new Date(aVal).getTime() : 0;
 bVal = bVal ? new Date(bVal).getTime() : 0;
 }

 // Handle numbers
 if (typeof aVal === "number" && typeof bVal === "number") {
 return sortDirection === "asc" ? aVal - bVal : bVal - aVal;
 }

 // Handle strings
 const aStr = String(aVal || "").toLowerCase();
 const bStr = String(bVal || "").toLowerCase();
 return sortDirection === "asc" 
 ? aStr.localeCompare(bStr)
 : bStr.localeCompare(aStr);
 });
 }, [filteredAndTypedLocations, sortField, sortDirection]);

 // Handle selection
 const handleSelectAll = useCallback(() => {
 if (selectedLocations.size === sortedLocations.length) {
 setSelectedLocations(new Set());
 } else {
 setSelectedLocations(new Set(sortedLocations.map((l) => l.id)));
 }
 }, [selectedLocations, sortedLocations]);

 const handleSelectLocation = useCallback((id: string) => {
 setSelectedLocations((prev: unknown) => {
 const next = new Set(prev);
 if (next.has(id)) {
 next.delete(id);
 } else {
 next.add(id);
 }
 return next;
 });
 }, []);

 // Handle actions
 const handleView = useCallback((location: Location) => {
 setSelectedLocation(location);
 setViewDrawerOpen(true);
 }, []);

 const handleEdit = useCallback((location: Location) => {
 setSelectedLocation(location);
 setEditDrawerOpen(true);
 }, []);

 const handleDelete = useCallback(async (location: Location) => {
 if (!confirm(`Are you sure you want to delete "${location.name}"?`)) return;

 try {
 const { error } = await supabase
 .from("locations")
 .delete()
 .eq("id", location.id);

 if (error) throw error;

 toast.success("Location deleted successfully");
 loadLocations();
 } catch (error) {
 console.error("Error deleting location:", error);
 toast.error("Failed to delete location");
 }
 }, [supabase, loadLocations]);

 const handleDuplicate = useCallback(async (location: Location) => {
 try {
 const { id, created_at, updated_at, ...locationData } = location;
 const { data, error } = await supabase
 .from("locations")
 .insert({
 ...locationData,
 name: `${location.name} (Copy)`
 })
 .select()
 .single();

 if (error) throw error;

 toast.success("Location duplicated successfully");
 loadLocations();
 } catch (error) {
 console.error("Error duplicating location:", error);
 toast.error("Failed to duplicate location");
 }
 }, [supabase, loadLocations]);

 const handleNavigate = useCallback((location: Location) => {
 if (location.coordinates) {
 const { x: lng, y: lat } = location.coordinates;
 window.open(`https://maps.google.com/?q=${lat},${lng}`, "_blank");
 } else if (location.address) {
 const query = encodeURIComponent(`${location.address}, ${location.city}, ${location.state} ${location.postal_code}`);
 window.open(`https://maps.google.com/?q=${query}`, "_blank");
 }
 }, []);

 // Bulk actions
 const handleBulkDelete = useCallback(async () => {
 if (!confirm(`Are you sure you want to delete ${selectedLocations.size} locations?`)) return;

 try {
 const { error } = await supabase
 .from("locations")
 .delete()
 .in("id", Array.from(selectedLocations));

 if (error) throw error;

 toast.success(`${selectedLocations.size} locations deleted successfully`);
 setSelectedLocations(new Set());
 loadLocations();
 } catch (error) {
 console.error("Error deleting locations:", error);
 toast.error("Failed to delete locations");
 }
 }, [supabase, selectedLocations, loadLocations]);

 const handleBulkExport = useCallback(() => {
 const selectedData = sortedLocations.filter(l => selectedLocations.has(l.id));
 const csv = convertToCSV(selectedData.length > 0 ? selectedData : sortedLocations);
 downloadCSV(csv, "locations.csv");
 }, [sortedLocations, selectedLocations]);

 // Helper functions for export
 const convertToCSV = (data: Location[]) => {
 const headers = ["Name", "Type", "Address", "City", "State", "Country", "Capacity", "Size", "Availability", "Project"];
 const rows = data.map(l => [
 l.name,
 l.type || "",
 l.address || "",
 l.city || "",
 l.state || "",
 l.country || "",
 l.capacity?.toString() || "",
 l.size?.toString() || "",
 l.availability_status || "",
 l.project?.name || "",
 ]);
 return [headers, ...rows].map(row => row.join(",")).join("\n");
 };

 const downloadCSV = (csv: string, filename: string) => {
 const blob = new Blob([csv], { type: "text/csv" });
 const url = window.URL.createObjectURL(blob);
 const a = document.createElement("a");
 a.href = url;
 a.download = filename;
 a.click();
 window.URL.revokeObjectURL(url);
 };

 // Get type icon
 const getTypeIcon = (type: string) => {
 switch (type) {
 case "venue":
 return Building2;
 case "office":
 return Building;
 case "warehouse":
 return Warehouse;
 case "retail":
 return Store;
 case "outdoor":
 return TreePine;
 case "studio":
 return Home;
 case "residential":
 return Home;
 default:
 return MapPin;
 }
 };

 // Get availability badge variant
 const getAvailabilityBadgeVariant = (status: string) => {
 switch (status) {
 case "available":
 return "success";
 case "booked":
 return "warning";
 case "maintenance":
 return "secondary";
 case "unavailable":
 return "destructive";
 default:
 return "default";
 }
 };

 // Get type badge variant
 const getTypeBadgeVariant = (type: string) => {
 switch (type) {
 case "venue":
 return "info";
 case "office":
 return "secondary";
 case "warehouse":
 return "warning";
 case "retail":
 return "success";
 case "outdoor":
 return "default";
 default:
 return "outline";
 }
 };

 return (
 <div className="space-y-md">
