 return (
 <div className="space-y-lg">
 {/* Gallery Grid */}
 <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-md">
 {locationsWithImages.map((location) => {
 const TypeIcon = getTypeIcon(location.type || "other");

 return (
