'use client';


import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { useTranslations } from 'next-intl';
import { createBrowserClient } from '@ghxstship/auth';
import { Card, Badge, Button } from '@ghxstship/ui';
import { 
  BarChart3, 
  Calendar, 
  Clock, 
  DollarSign, 
  TrendingUp, 
  Users, 
  AlertTriangle,
  CheckCircle,
  Plus,
  ArrowRight
} from 'lucide-react';

interface ProjectStats {
  totalProjects: number;
  activeProjects: number;
  completedProjects: number;
  totalBudget: number;
  totalTasks: number;
  completedTasks: number;
  overdueTasks: number;
  totalRisks: number;
  highRisks: number;
}

interface RecentProject {
  id: string;
  name: string;
  status: string;
  budget: number;
  created_at: string;
  tasks_count?: number;
  completion_percentage?: number;
}

export default function ProjectsOverviewClient({ orgId }: { orgId: string }) {
  const t = useTranslations('projects');
  const router = useRouter();
  const [loading, setLoading] = useState(true);
  const [seedLoading, setSeedLoading] = useState(false);
  const [message, setMessage] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [stats, setStats] = useState<ProjectStats | null>(null);
  const [recentProjects, setRecentProjects] = useState<RecentProject[]>([]);
  const sb = createBrowserClient();

  useEffect(() => {
    loadOverviewData();
  }, [orgId]);

  async function loadOverviewData() {
    setLoading(true);
    try {
      // Load projects
      const { data: projects } = await sb
        .from('projects')
        .select('*')
        .eq('organization_id', orgId);

      // Load tasks
      const { data: tasks } = await sb
        .from('tasks')
        .select('*')
        .eq('organization_id', orgId);

      // Load risks
      const { data: risks } = await sb
        .from('risks')
        .select('*')
        .eq('organization_id', orgId);

      if (projects) {
        const totalBudget = projects.reduce((sum, p) => sum + (p.budget || 0), 0);
        const activeProjects = projects.filter(p => p.status === 'active').length;
        const completedProjects = projects.filter(p => p.status === 'completed').length;

        const completedTasks = tasks?.filter(t => t.status === 'done').length || 0;
        const overdueTasks = tasks?.filter(t => 
          t.due_at && new Date(t.due_at) < new Date() && t.status !== 'done'
        ).length || 0;

        const highRisks = risks?.filter(r => 
          r.impact === 'high' || r.impact === 'very_high'
        ).length || 0;

        setStats({
          totalProjects: projects.length,
          activeProjects,
          completedProjects,
          totalBudget,
          totalTasks: tasks?.length || 0,
          completedTasks,
          overdueTasks,
          totalRisks: risks?.length || 0,
          highRisks
        });

        // Set recent projects (last 5)
        setRecentProjects(
          projects
            .sort((a, b) => new Date(b.created_at).getTime() - new Date(a.created_at).getTime())
            .slice(0, 5)
        );
      }
    } catch (error) {
      console.error('Error loading overview data:', error);
      setError('Failed to load overview data');
    } finally {
      setLoading(false);
    }
  }

  async function onSeedDemo() {
    setSeedLoading(true);
    setMessage(null);
    setError(null);
    try {
      const res = await fetch(`/api/organizations/${orgId}/demo`, { method: 'POST' });
      const json = await res.json();
      if (!res.ok) throw new Error(json?.error || 'Request failed');
      setMessage(t('empty.seeded'));
      if (typeof window !== 'undefined' && (window as any).posthog) {
        (window as any).posthog.capture('demo.seeded', { organization_id: orgId });
      }
      await loadOverviewData(); // Refresh data after seeding
      router.refresh();
    } catch (e) {
      setError(e?.message || t('empty.error'));
    } finally {
      setSeedLoading(false);
    }
  }

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'bg-success/10 color-success';
      case 'completed': return 'bg-accent/10 color-accent';
      case 'on_hold': return 'bg-warning/10 color-warning';
      case 'cancelled': return 'bg-destructive/10 color-destructive';
      default: return 'bg-secondary/50 color-muted';
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(amount);
  };

  if (loading) {
    return (
      <div className="stack-lg">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-md">
          {[...Array(4)].map((_, i) => (
            <Card key={i} className="animate-pulse">
              <div className="h-component-lg bg-secondary rounded"></div>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  if (!stats || stats.totalProjects === 0) {
    return (
      <div className="text-center py-xsxl">
        <div className="mx-auto w-component-lg h-component-lg bg-secondary/50 rounded-full flex items-center justify-center mb-md">
          <BarChart3 className="w-icon-2xl h-icon-2xl color-muted" />
        </div>
        <h3 className="text-body text-heading-4 mb-sm">No Projects Yet</h3>
        <p className="color-muted mb-lg">Get started by creating your first project or seeding demo data.</p>
        <div className="flex items-center justify-center gap-md">
          <Button onClick={() => router.push('/projects?create=true')}>
            <Plus className="w-icon-xs h-icon-xs mr-sm" />
            Create Project
          </Button>
          <Button 
            variant="outline" 
            onClick={onSeedDemo}
            disabled={seedLoading}
          >
            {seedLoading ? 'Seeding...' : 'Seed Demo Data'}
          </Button>
        </div>
        {message && (
          <div className="mt-md color-success text-body-sm">{message}</div>
        )}
        {error && (
          <div className="mt-md color-destructive text-body-sm">{error}</div>
        )}
      </div>
    );
  }

  return (
    <div className="stack-lg">
      {/* Key Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-md">
        <Card className="p-lg">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-muted">Total Projects</p>
              <p className="text-heading-3">{stats.totalProjects}</p>
            </div>
            <BarChart3 className="w-icon-lg h-icon-lg color-accent" />
          </div>
          <div className="mt-sm flex items-center text-body-sm">
            <span className="color-success">{stats.activeProjects} active</span>
            <span className="mx-sm"></span>
            <span className="color-muted">{stats.completedProjects} completed</span>
          </div>
        </Card>

        <Card className="p-lg">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-muted">Total Budget</p>
              <p className="text-heading-3">{formatCurrency(stats.totalBudget)}</p>
            </div>
            <DollarSign className="w-icon-lg h-icon-lg color-success" />
          </div>
          <div className="mt-sm text-body-sm color-muted">
            Across all projects
          </div>
        </Card>

        <Card className="p-lg">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-muted">Tasks Progress</p>
              <p className="text-heading-3">
                {stats.totalTasks > 0 ? Math.round((stats.completedTasks / stats.totalTasks) * 100) : 0}%
              </p>
            </div>
            <CheckCircle className="w-icon-lg h-icon-lg color-accent" />
          </div>
          <div className="mt-sm flex items-center text-body-sm">
            <span className="color-success">{stats.completedTasks} done</span>
            <span className="mx-sm"></span>
            <span className="color-destructive">{stats.overdueTasks} overdue</span>
          </div>
        </Card>

        <Card className="p-lg">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-body-sm form-label color-muted">Risk Status</p>
              <p className="text-heading-3">{stats.totalRisks}</p>
            </div>
            <AlertTriangle className="w-icon-lg h-icon-lg color-warning" />
          </div>
          <div className="mt-sm text-body-sm">
            <span className="color-destructive">{stats.highRisks} high priority</span>
          </div>
        </Card>
      </div>

      {/* Recent Projects */}
      <Card className="p-lg">
        <div className="flex items-center justify-between mb-md">
          <h3 className="text-body text-heading-4">Recent Projects</h3>
          <Button 
            variant="outline" 

            onClick={() => router.push('/projects')}
          >
            View All
            <ArrowRight className="w-icon-xs h-icon-xs ml-sm" />
          </Button>
        </div>
        <div className="stack-sm">
          {recentProjects.map((project: any) => (
            <div 
              key={project.id}
              className="flex items-center justify-between p-sm border rounded-lg hover:bg-secondary/50 cursor-pointer"
              onClick={() => router.push('/projects/' + project.id as any)}
            >
              <div className="flex-1">
                <div className="flex items-center gap-sm">
                  <h4 className="form-label">{project.name}</h4>
                  <Badge className={getStatusColor(project.status)}>
                    {project.status}
                  </Badge>
                </div>
                <div className="flex items-center gap-md mt-xs text-body-sm color-muted">
                  <span className="flex items-center gap-xs">
                    <DollarSign className="w-3 h-3" />
                    {formatCurrency(project.budget || 0)}
                  </span>
                  <span className="flex items-center gap-xs">
                    <Calendar className="w-3 h-3" />
                    {new Date(project.created_at).toLocaleDateString()}
                  </span>
                </div>
              </div>
              <ArrowRight className="w-icon-xs h-icon-xs color-muted" />
            </div>
          ))}
        </div>
      </Card>

      {/* Quick Actions */}
      <Card className="p-lg">
        <h3 className="text-body text-heading-4 mb-md">Quick Actions</h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-md">
          <Button 
            variant="outline" 
            className="h-auto p-md flex flex-col items-center gap-sm"
            onClick={() => router.push('/projects?create=true')}
          >
            <Plus className="w-icon-md h-icon-md" />
            <span>Create Project</span>
          </Button>
          <Button 
            variant="outline" 
            className="h-auto p-md flex flex-col items-center gap-sm"
            onClick={() => router.push('/projects/tasks')}
          >
            <Clock className="w-icon-md h-icon-md" />
            <span>View Tasks</span>
          </Button>
          <Button 
            variant="outline" 
            className="h-auto p-md flex flex-col items-center gap-sm"
            onClick={() => router.push('/projects/risks')}
          >
            <AlertTriangle className="w-icon-md h-icon-md" />
            <span>Manage Risks</span>
          </Button>
        </div>
      </Card>
    </div>
  );
}

"use client";

import { Grid3x3, LayoutGrid, List, BarChart3, Search, Filter, Download, Plus, AlertTriangle, Shield, TrendingUp, Activity, Target, Zap, AlertCircle, CheckCircle, Clock, ArrowUpDown } from "lucide-react";
import { useState, useCallback, useMemo, useEffect } from 'react';
import { useRouter } from "next/navigation";
import { createBrowserClient } from "@ghxstship/auth";
import {
 Card,
 Button,
 Badge,
 Input,
 Select,
 Checkbox,
 toast
} from "@ghxstship/ui";
import { format, parseISO, isBefore } from "date-fns";
import CreateRiskDrawer from "./CreateRiskDrawer";
import EditRiskDrawer from "./EditRiskDrawer";
import ViewRiskDrawer from "./ViewRiskDrawer";
import RiskGridView from "./views/RiskGridView";
import RiskMatrixView from "./views/RiskMatrixView";
import RiskListView from "./views/RiskListView";
import RiskHeatmapView from "./views/RiskHeatmapView";

// Types
export interface Risk {
 id: string;
 project_id: string;
 organization_id: string;
 project?: {
 id: string;
 name: string;
 status: string;
 };
 title: string;
 description: string;
 category: "technical" | "financial" | "operational" | "legal" | "environmental" | "safety";
 probability: "very_low" | "low" | "medium" | "high" | "very_high";
 impact: "very_low" | "low" | "medium" | "high" | "very_high";
 risk_score: number;
 status: "identified" | "assessed" | "mitigated" | "closed";
 owner_id?: string;
 owner?: {
 id: string;
 email: string;
 full_name?: string;
 };
 mitigation_plan?: string;
 contingency_plan?: string;
 identified_date: string;
 review_date?: string;
 closed_date?: string;
 created_at: string;
 updated_at: string;
 created_by?: string;
 updated_by?: string;
}

interface RisksClientProps {
 orgId: string;
 projectId?: string;
 initialRisks?: Risk[];
 projects?: Array<{ id: string; name: string }>;
 users?: Array<{ id: string; email: string; full_name?: string }>;
}

const VIEW_TYPES = [
 { id: "grid", label: "Grid", icon: Grid3x3 },
 { id: "matrix", label: "Matrix", icon: LayoutGrid },
 { id: "heatmap", label: "Heatmap", icon: BarChart3 },
 { id: "list", label: "List", icon: List },
] as const;

type ViewType = typeof VIEW_TYPES[number]["id"];

const FIELD_CONFIG = [
 { id: "title", label: "Title", visible: true, sortable: true },
 { id: "category", label: "Category", visible: true, sortable: true },
 { id: "probability", label: "Probability", visible: true, sortable: true },
 { id: "impact", label: "Impact", visible: true, sortable: true },
 { id: "risk_score", label: "Risk Score", visible: true, sortable: true },
 { id: "status", label: "Status", visible: true, sortable: true },
 { id: "owner", label: "Owner", visible: true, sortable: true },
 { id: "project", label: "Project", visible: true, sortable: true },
 { id: "identified_date", label: "Identified", visible: false, sortable: true },
 { id: "review_date", label: "Review Date", visible: false, sortable: true },
 { id: "created_at", label: "Created", visible: false, sortable: true },
];

export default function RisksClient({
 orgId,
 projectId,
 initialRisks = [],
 projects = [],
 users = []
}: RisksClientProps) {
 const router = useRouter();
 const supabase = createBrowserClient();

 // State
 const [risks, setRisks] = useState<Risk[]>(initialRisks);
 const [loading, setLoading] = useState(false);
 const [viewType, setViewType] = useState<ViewType>("grid");
 const [searchQuery, setSearchQuery] = useState("");
 const [selectedCategory, setSelectedCategory] = useState<string>("all");
 const [selectedStatus, setSelectedStatus] = useState<string>("all");
 const [selectedProbability, setSelectedProbability] = useState<string>("all");
 const [selectedImpact, setSelectedImpact] = useState<string>("all");
 const [selectedProject, setSelectedProject] = useState<string>(projectId || "all");
 const [sortField, setSortField] = useState<string>("risk_score");
 const [sortDirection, setSortDirection] = useState<"asc" | "desc">("desc");
 const [selectedRisks, setSelectedRisks] = useState<Set<string>(new Set());
 const [fieldVisibility, setFieldVisibility] = useState(FIELD_CONFIG);

 // Drawers
 const [createDrawerOpen, setCreateDrawerOpen] = useState(false);
 const [editDrawerOpen, setEditDrawerOpen] = useState(false);
 const [viewDrawerOpen, setViewDrawerOpen] = useState(false);
 const [selectedRisk, setSelectedRisk] = useState<Risk | null>(null);

 // Calculate risk score
 const calculateRiskScore = (probability: string, impact: string): number => {
 const probMap: Record<string, number> = {
 very_low: 1,
 low: 2,
 medium: 3,
 high: 4,
 very_high: 5
 };
 return probMap[probability] * probMap[impact];
 };

 // Load risks from Supabase
 const loadRisks = useCallback(async () => {
 setLoading(true);
 try {
 let query = supabase
 .from("project_risks")
 .select(`
 *,
 project:projects(id, name, status),
 owner:users!project_risks_owner_id_fkey(id, email, full_name)
 `)
 .eq("organization_id", orgId)
 .order(sortField, { ascending: sortDirection === "asc" });

 if (projectId) {
 query = query.eq("project_id", projectId);
 } else if (selectedProject !== "all") {
 query = query.eq("project_id", selectedProject);
 }

 const { data, error } = await query;
 if (error) throw error;
 setRisks(data || []);
 } catch (error) {
 console.error("Error loading risks:", error);
 toast.error("Failed to load risks");
 } finally {
 setLoading(false);
 }
 }, [supabase, orgId, projectId, selectedProject, sortField, sortDirection]);

 // Load risks on mount
 useEffect(() => {
 loadRisks();
 }, [loadRisks]);

 // Real-time subscription
 useEffect(() => {
 const channel = supabase
 .channel("risks-changes")
 .on(
 "postgres_changes",
 {
 event: "*",
 schema: "public",
 table: "project_risks",
 filter: `organization_id=eq.${orgId}`
 },
 () => {
 loadRisks();
 }
 )
 .subscribe();

 return () => {
 supabase.removeChannel(channel);
 };
 }, [supabase, orgId, loadRisks]);

 // Filter risks
 const filteredRisks = useMemo(() => {
 return risks.filter((risk) => {
 if (searchQuery) {
 const query = searchQuery.toLowerCase();
 return (
 risk.title.toLowerCase().includes(query) ||
 risk.description.toLowerCase().includes(query) ||
 risk.mitigation_plan?.toLowerCase().includes(query) ||
 risk.project?.name.toLowerCase().includes(query)
 );
 }
 return true;
 });
 }, [risks, searchQuery]);

 // Apply filters
 const filteredAndTypedRisks = useMemo(() => {
 return filteredRisks.filter((risk) => {
 if (selectedCategory !== "all" && risk.category !== selectedCategory) return false;
 if (selectedStatus !== "all" && risk.status !== selectedStatus) return false;
 if (selectedProbability !== "all" && risk.probability !== selectedProbability) return false;
 if (selectedImpact !== "all" && risk.impact !== selectedImpact) return false;
 return true;
 });
 }, [filteredRisks, selectedCategory, selectedStatus, selectedProbability, selectedImpact]);

 // Sort risks
 const sortedRisks = useMemo(() => {
 return [...filteredAndTypedRisks].sort((a, b) => {
 let aVal: unknown = a[sortField as keyof Risk];
 let bVal: unknown = b[sortField as keyof Risk];

 if (sortField === "owner") {
 aVal = a.owner?.full_name || a.owner?.email || "";
 bVal = b.owner?.full_name || b.owner?.email || "";
 } else if (sortField === "project") {
 aVal = a.project?.name || "";
 bVal = b.project?.name || "";
 }

 if (sortField.includes("date") || sortField.includes("_at")) {
 aVal = aVal ? new Date(aVal).getTime() : 0;
 bVal = bVal ? new Date(bVal).getTime() : 0;
 }

 if (typeof aVal === "number" && typeof bVal === "number") {
 return sortDirection === "asc" ? aVal - bVal : bVal - aVal;
 }

 const aStr = String(aVal || "").toLowerCase();
 const bStr = String(bVal || "").toLowerCase();
 return sortDirection === "asc" 
 ? aStr.localeCompare(bStr)
 : bStr.localeCompare(aStr);
 });
}, [filteredAndTypedRisks, sortField, sortDirection]);

// Group risks for matrix view
const riskMatrix = useMemo(() => {
 const matrix: Record<string, Record<string, Risk[]>> = {};
 const probabilities = ["very_high", "high", "medium", "low", "very_low"];
 const impacts = ["very_low", "low", "medium", "high", "very_high"];

 probabilities.forEach(prob => {
 matrix[prob] = {};
 impacts.forEach(imp => {
 matrix[prob][imp] = [];
 });
 });

 sortedRisks.forEach(risk => {
 if (matrix[risk.probability] && matrix[risk.probability][risk.impact]) {
 matrix[risk.probability][risk.impact].push(risk);
 }
 });

 return matrix;
 }, [sortedRisks]);

 // Calculate statistics
 const statistics = useMemo(() => {
 const stats = {
 total: risks.length,
 identified: risks.filter(r => r.status === "identified").length,
 assessed: risks.filter(r => r.status === "assessed").length,
 mitigated: risks.filter(r => r.status === "mitigated").length,
 closed: risks.filter(r => r.status === "closed").length,
 critical: risks.filter(r => r.risk_score >= 20).length,
 high: risks.filter(r => r.risk_score >= 12 && r.risk_score < 20).length,
 medium: risks.filter(r => r.risk_score >= 6 && r.risk_score < 12).length,
 low: risks.filter(r => r.risk_score < 6).length,
 overdue: risks.filter(r => 
 r.review_date && 
 r.status !== "closed" && 
 isBefore(parseISO(r.review_date), new Date())
 ).length
 };
 return stats;
 }, [risks]);

 // Handle selection
 const handleSelectAll = useCallback(() => {
 if (selectedRisks.size === sortedRisks.length) {
 setSelectedRisks(new Set());
 } else {
 setSelectedRisks(new Set(sortedRisks.map((r) => r.id)));
 }
 }, [selectedRisks, sortedRisks]);

 const handleSelectRisk = useCallback((id: string) => {
 setSelectedRisks((prev: unknown) => {
 const next = new Set(prev);
 if (next.has(id)) {
 next.delete(id);
 } else {
 next.add(id);
 }
 return next;
 });
 }, []);

 // Handle actions
 const handleView = useCallback((risk: Risk) => {
 setSelectedRisk(risk);
 setViewDrawerOpen(true);
 }, []);

 const handleEdit = useCallback((risk: Risk) => {
 setSelectedRisk(risk);
 setEditDrawerOpen(true);
 }, []);

 const handleDelete = useCallback(async (risk: Risk) => {
 if (!confirm(`Are you sure you want to delete "${risk.title}"?`)) return;

 try {
 const { error } = await supabase
 .from("project_risks")
 .delete()
 .eq("id", risk.id);

 if (error) throw error;
 toast.success("Risk deleted successfully");
 loadRisks();
 } catch (error) {
 console.error("Error deleting risk:", error);
 toast.error("Failed to delete risk");
 }
 }, [supabase, loadRisks]);

 const handleDuplicate = useCallback(async (risk: Risk) => {
 try {
 const { id, created_at, updated_at, ...riskData } = risk;
 const { data, error } = await supabase
 .from("project_risks")
 .insert({
 ...riskData,
 title: `${risk.title} (Copy)`,
 status: "identified",
 identified_date: new Date().toISOString().split("T")[0]
 })
 .select()
 .single();

 if (error) throw error;
 toast.success("Risk duplicated successfully");
 loadRisks();
 } catch (error) {
 console.error("Error duplicating risk:", error);
 toast.error("Failed to duplicate risk");
 }
 }, [supabase, loadRisks]);

 // Bulk actions
 const handleBulkExport = useCallback(() => {
 const selectedData = sortedRisks.filter(r => selectedRisks.has(r.id));
 const csv = convertToCSV(selectedData.length > 0 ? selectedData : sortedRisks);
 downloadCSV(csv, "risks.csv");
 }, [sortedRisks, selectedRisks]);

 const convertToCSV = (data: Risk[]) => {
 const headers = ["Title", "Category", "Probability", "Impact", "Score", "Status", "Owner", "Project"];
 const rows = data.map(r => [
 r.title,
 r.category,
 r.probability,
 r.impact,
 r.risk_score.toString(),
 r.status,
 r.owner?.full_name || r.owner?.email || "",
 r.project?.name || "",
 ]);
 return [headers, ...rows].map(row => row.join(",")).join("\n");
 };

 const downloadCSV = (csv: string, filename: string) => {
 const blob = new Blob([csv], { type: "text/csv" });
 const url = window.URL.createObjectURL(blob);
 const a = document.createElement("a");
 a.href = url;
 a.download = filename;
 a.click();
 window.URL.revokeObjectURL(url);
 };

 // Get helper functions
 const getCategoryIcon = (category: string) => {
 switch (category) {
 case "technical": return Activity;
 case "financial": return TrendingUp;
 case "operational": return Target;
 case "legal": return Shield;
 case "environmental": return AlertCircle;
 case "safety": return AlertTriangle;
 default: return AlertTriangle;
 }
 };

 const getRiskLevelColor = (score: number) => {
 if (score >= 20) return "text-destructive";
 if (score >= 12) return "text-warning";
 if (score >= 6) return "text-yellow-500";
 return "text-success";
 };

 const getRiskLevelBadgeVariant = (score: number) => {
 if (score >= 20) return "destructive";
 if (score >= 12) return "warning";
 if (score >= 6) return "secondary";
 return "success";
 };

 const getStatusBadgeVariant = (status: string) => {
 switch (status) {
 case "identified": return "secondary";
 case "assessed": return "warning";
 case "mitigated": return "success";
 case "closed": return "default";
 default: return "outline";
 }
 };

 const getCategoryBadgeVariant = (category: string) => {
 switch (category) {
 case "technical": return "info";
 case "financial": return "warning";
 case "operational": return "secondary";
 case "legal": return "destructive";
 case "environmental": return "success";
 case "safety": return "destructive";
 default: return "default";
 }
 };

 return (
 <div className="space-y-md">
 {/* Header */}
 <Card className="p-lg">
 <div className="flex items-center justify-between mb-md">
 <div>
 <h1 className="text-2xl font-bold">Risk Management</h1>
 <p className="text-muted-foreground">
 Identify, assess, and mitigate project risks
 </p>
 </div>
 <Button onClick={() => setCreateDrawerOpen(true)}>
 <Plus className="mr-2 h-icon-xs w-icon-xs" />
 Add Risk
 </Button>
 </div>

 {/* Statistics Cards */}
 <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-md mb-md">
 <Card className="p-sm">
 <div className="flex items-center justify-between">
 <div>
 <p className="text-sm text-muted-foreground">Total</p>
 <p className="text-2xl font-bold">{statistics.total}</p>
 </div>
 <AlertTriangle className="h-icon-lg w-icon-lg text-muted-foreground" />
 </div>
 </Card>
 <Card className="p-sm">
 <div className="flex items-center justify-between">
 <div>
 <p className="text-sm text-muted-foreground">Critical</p>
 <p className="text-2xl font-bold text-destructive">{statistics.critical}</p>
 </div>
 <Zap className="h-icon-lg w-icon-lg text-destructive" />
 </div>
 </Card>
 <Card className="p-sm">
 <div className="flex items-center justify-between">
 <div>
 <p className="text-sm text-muted-foreground">High</p>
 <p className="text-2xl font-bold text-warning">{statistics.high}</p>
 </div>
 <TrendingUp className="h-icon-lg w-icon-lg text-warning" />
 </div>
 </Card>
 <Card className="p-sm">
 <div className="flex items-center justify-between">
 <div>
 <p className="text-sm text-muted-foreground">Mitigated</p>
 <p className="text-2xl font-bold text-success">{statistics.mitigated}</p>
 </div>
 <Shield className="h-icon-lg w-icon-lg text-success" />
 </div>
 </Card>
 <Card className="p-sm">
 <div className="flex items-center justify-between">
 <div>
 <p className="text-sm text-muted-foreground">Closed</p>
 <p className="text-2xl font-bold text-info">{statistics.closed}</p>
 </div>
 <CheckCircle className="h-icon-lg w-icon-lg text-info" />
 </div>
 </Card>
 <Card className="p-sm">
 <div className="flex items-center justify-between">
 <div>
 <p className="text-sm text-muted-foreground">Overdue</p>
 <p className="text-2xl font-bold text-destructive">{statistics.overdue}</p>
 </div>
 <Clock className="h-icon-lg w-icon-lg text-destructive" />
 </div>
 </Card>
 </div>

 {/* Filters and View Switcher */}
 <div className="flex flex-col lg:flex-row gap-md">
 <div className="flex-1 flex flex-wrap gap-sm">
 <div className="relative flex-1 min-w-content-narrow">
 <Search className="absolute left-3 top-xs/2 transform -translate-y-1/2 h-icon-xs w-icon-xs text-muted-foreground" />
 <Input
 placeholder="Search risks..."
 value={searchQuery}
 onChange={(e) => setSearchQuery(e.target.value)}
 className="pl-9"
 />
 </div>

 <Select value={selectedCategory} onValueChange={setSelectedCategory}>
 <option value="all">All Categories</option>
 <option value="technical">Technical</option>
 <option value="financial">Financial</option>
 <option value="operational">Operational</option>
 <option value="legal">Legal</option>
 <option value="environmental">Environmental</option>
 <option value="safety">Safety</option>
 </Select>

 <Select value={selectedStatus} onValueChange={setSelectedStatus}>
 <option value="all">All Status</option>
 <option value="identified">Identified</option>
 <option value="assessed">Assessed</option>
 <option value="mitigated">Mitigated</option>
 <option value="closed">Closed</option>
 </Select>

 <Select value={selectedProbability} onValueChange={setSelectedProbability}>
 <option value="all">All Probability</option>
 <option value="very_low">Very Low</option>
 <option value="low">Low</option>
 <option value="medium">Medium</option>
 <option value="high">High</option>
 <option value="very_high">Very High</option>
 </Select>

 {!projectId && projects.length > 0 && (
 <Select value={selectedProject} onValueChange={setSelectedProject}>
 <option value="all">All Projects</option>
 {projects.map((project) => (
 <option key={project.id} value={project.id}>
 {project.name}
 </option>
 ))}
 </Select>
 )}
 </div>

 {/* View Switcher */}
 <div className="flex items-center gap-sm">
 <Button variant="outline" size="sm" onClick={handleBulkExport}>
 <Download className="mr-2 h-icon-xs w-icon-xs" />
 Export
 </Button>

 <div className="flex rounded-md shadow-sm">
 {VIEW_TYPES.map((view) => {
 const Icon = view.icon;
 return (
 <Button
 key={view.id}
 variant={viewType === view.id ? "default" : "outline"}
 size="sm"
 onClick={() => setViewType(view.id as ViewType)}
 className="rounded-none first:rounded-l-md last:rounded-r-md"
 >
 <Icon className="h-icon-xs w-icon-xs" />
 </Button>
 );
 })}
 </div>
 </div>
 </div>
 </Card>

 {/* Bulk Actions Bar */}
 {selectedRisks.size > 0 && (
 <Card className="p-sm">
 <div className="flex items-center justify-between">
 <div className="flex items-center gap-sm">
 <Checkbox
 checked={selectedRisks.size === sortedRisks.length}
 onChange={handleSelectAll}
 />
 <span className="text-sm text-muted-foreground">
 {selectedRisks.size} selected
 </span>
 </div>
 <div className="flex items-center gap-sm">
 <Button variant="outline" size="sm" onClick={handleBulkExport}>
 Export Selected
 </Button>
 <Button
 variant="destructive"
 size="sm"
 onClick={async () => {
 if (!confirm(`Delete ${selectedRisks.size} risks?`)) return;
 const { error } = await supabase
 .from("project_risks")
 .delete()
 .in("id", Array.from(selectedRisks));
 if (!error) {
 toast.success("Risks deleted");
 setSelectedRisks(new Set());
 loadRisks();
 }
 }}
 >
 Delete Selected
 </Button>
 </div>
 </div>
 </Card>
 )}

 {/* Content Views */}
 <Card className="p-lg">
 {loading ? (
 <div className="flex items-center justify-center py-xl">
 <div className="animate-spin rounded-full h-icon-lg w-icon-lg border-b-2 border-primary"></div>
 </div>
 ) : sortedRisks.length === 0 ? (
 <div className="text-center py-xl">
 <AlertTriangle className="mx-auto h-icon-2xl w-icon-2xl text-muted-foreground mb-md" />
 <h3 className="text-lg font-semibold mb-sm">No risks found</h3>
 <p className="text-muted-foreground mb-md">
 {searchQuery || selectedCategory !== "all" || selectedStatus !== "all"
 ? "Try adjusting your filters"
 : "Get started by identifying your first risk"}
 </p>
 {!searchQuery && selectedCategory === "all" && selectedStatus === "all" && (
 <Button onClick={() => setCreateDrawerOpen(true)}>
 <Plus className="mr-2 h-icon-xs w-icon-xs" />
 Add Risk
 </Button>
 )}
 </div>
 ) : (
 <>
 {viewType === "grid" && (
 <RiskGridView
 risks={sortedRisks}
 selectedRisks={selectedRisks}
 onSelectRisk={handleSelectRisk}
 onView={handleView}
 onEdit={handleEdit}
 onDelete={handleDelete}
 onDuplicate={handleDuplicate}
 getCategoryIcon={getCategoryIcon}
 getRiskLevelColor={getRiskLevelColor}
 getRiskLevelBadgeVariant={getRiskLevelBadgeVariant}
 getStatusBadgeVariant={getStatusBadgeVariant}
 getCategoryBadgeVariant={getCategoryBadgeVariant}
 />
 )}
 {viewType === "matrix" && (
 <RiskMatrixView
 riskMatrix={riskMatrix}
 onView={handleView}
 getRiskLevelColor={getRiskLevelColor}
 />
 )}
 {viewType === "heatmap" && (
 <RiskHeatmapView
 risks={sortedRisks}
 onView={handleView}
 />
 )}
 {viewType === "list" && (
 <RiskListView
 risks={sortedRisks}
 selectedRisks={selectedRisks}
 fieldVisibility={fieldVisibility}
 sortField={sortField}
 sortDirection={sortDirection}
 onSelectAll={handleSelectAll}
 onSelectRisk={handleSelectRisk}
 onSort={(field) => {
 if (sortField === field) {
 setSortDirection(sortDirection === "asc" ? "desc" : "asc");
 } else {
 setSortField(field);
 setSortDirection("asc");
 }
 }}
 onView={handleView}
 onEdit={handleEdit}
 onDelete={handleDelete}
 onDuplicate={handleDuplicate}
 getRiskLevelBadgeVariant={getRiskLevelBadgeVariant}
 getStatusBadgeVariant={getStatusBadgeVariant}
 getCategoryBadgeVariant={getCategoryBadgeVariant}
 />
 )}
 </>
 )}
 </Card>

 {/* Drawers */}
 <CreateRiskDrawer
 open={createDrawerOpen}
 onClose={() => setCreateDrawerOpen(false)}
 orgId={orgId}
 projectId={projectId}
 projects={projects}
 users={users}
 onSuccess={loadRisks}
 />

 {selectedRisk && (
 <>
 <EditRiskDrawer
 open={editDrawerOpen}
 onClose={() => setEditDrawerOpen(false)}
 risk={selectedRisk}
 projects={projects}
 users={users}
 onSuccess={loadRisks}
 />

 <ViewRiskDrawer
 open={viewDrawerOpen}
 onClose={() => setViewDrawerOpen(false)}
 risk={selectedRisk}
 onEdit={() => {
 setViewDrawerOpen(false);
 setEditDrawerOpen(true);
 }}
 getCategoryIcon={getCategoryIcon}
 getRiskLevelColor={getRiskLevelColor}
 getRiskLevelBadgeVariant={getRiskLevelBadgeVariant}
 getStatusBadgeVariant={getStatusBadgeVariant}
 getCategoryBadgeVariant={getCategoryBadgeVariant}
 />
 </>
 )}
 </div>
 );
}

"use client";

import { ListTodo, LayoutGrid, Calendar, Activity, Search, Filter, Download, Upload, Plus, Edit, Trash2, Eye, Copy, Clock, CheckCircle, AlertCircle, Users, MoreVertical, ArrowUpDown, ChevronDown, Tag, TrendingUp, AlertTriangle, Briefcase, Target } from "lucide-react";
import { useState, useCallback, useMemo, useEffect } from 'react';
import { useRouter } from "next/navigation";
import { createBrowserClient } from "@ghxstship/auth";
import {
 Card,
 Button,
 Badge,
 Input,
 Select,
 Checkbox,
 Progress,
 Dropdown,

 DropdownItem,

 toast,
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator
} from "@ghxstship/ui";
import { format, parseISO, differenceInDays, isOverdue, startOfWeek, endOfWeek } from "date-fns";
import CreateTaskDrawer from "./drawers/CreateTaskDrawer";
import EditTaskDrawer from "./drawers/EditTaskDrawer";
import ViewTaskDrawer from "./drawers/ViewTaskDrawer";
import TaskBoardView from "./views/TaskBoardView";
import TaskListView from "./views/TaskListView";
import TaskCalendarView from "./views/TaskCalendarView";
import TaskTimelineView from "./views/TaskTimelineView";

// Types
export interface Project {
 id: string;
 name: string;
 status: string;
}

export interface User {
 id: string;
 email: string;
 full_name?: string;
 avatar_url?: string;
}

export interface Task {
 id: string;
 project_id: string;
 organization_id: string;
 project?: Project;
 title: string;
 description?: string;
 status: "todo" | "in_progress" | "review" | "done" | "blocked";
 priority: "low" | "medium" | "high" | "critical";
 assignee_id?: string;
 assignee?: User;
 reporter_id?: string;
 reporter?: User;
 parent_task_id?: string;
 parent_task?: Task;
 subtasks?: Task[];
 estimated_hours?: number;
 actual_hours?: number;
 start_date?: string;
 due_date?: string;
 completed_at?: string;
 tags?: string[];
 dependencies?: string[];
 attachments?: string[];
 position: number;
 created_at: string;
 updated_at: string;
 created_by?: string;
 updated_by?: string;
}

interface TasksClientProps {
 orgId: string;
 projectId?: string;
 initialTasks?: Task[];
 projects?: Project[];
 users?: User[];
}

// View configurations
const VIEW_TYPES = [
 { id: "board", label: "Board", icon: LayoutGrid },
 { id: "list", label: "List", icon: ListTodo },
 { id: "calendar", label: "Calendar", icon: Calendar },
 { id: "timeline", label: "Timeline", icon: Activity },
] as const;

type ViewType = typeof VIEW_TYPES[number]["id"];

export default function TasksClient({
 orgId,
 projectId,
 initialTasks = [],
 projects = [],
 users = []
}: TasksClientProps) {
 const router = useRouter();
 const supabase = createBrowserClient();

 // State
 const [tasks, setTasks] = useState<Task[]>(initialTasks);
 const [loading, setLoading] = useState(false);
 const [viewType, setViewType] = useState<ViewType>("board");
 const [searchQuery, setSearchQuery] = useState("");
 const [selectedProject, setSelectedProject] = useState<string>(projectId || "all");
 const [selectedStatus, setSelectedStatus] = useState<string>("all");
 const [selectedPriority, setSelectedPriority] = useState<string>("all");
 const [selectedAssignee, setSelectedAssignee] = useState<string>("all");
 const [selectedTags, setSelectedTags] = useState<string[]>([]);
 const [selectedItems, setSelectedItems] = useState<Set<string>(new Set());
 const [showSubtasks, setShowSubtasks] = useState(true);
 const [showCompleted, setShowCompleted] = useState(true);

 // Drawers
 const [createDrawerOpen, setCreateDrawerOpen] = useState(false);
 const [editDrawerOpen, setEditDrawerOpen] = useState(false);
 const [viewDrawerOpen, setViewDrawerOpen] = useState(false);
 const [selectedTask, setSelectedTask] = useState<Task | null>(null);

 // Load tasks from Supabase
 const loadTasks = useCallback(async () => {
 setLoading(true);
 try {
 let query = supabase
 .from("project_tasks")
 .select(`
 *,
 project:projects(id, name, status),
 assignee:users!project_tasks_assignee_id_fkey(id, email, full_name, avatar_url),
 reporter:users!project_tasks_reporter_id_fkey(id, email, full_name, avatar_url),
 parent_task:project_tasks!parent_task_id(id, title, status),
 subtasks:project_tasks!parent_task_id(
 id, title, status, priority, assignee_id, due_date
 )
 `)
 .eq("organization_id", orgId);

 // Apply project filter
 if (projectId) {
 query = query.eq("project_id", projectId);
 } else if (selectedProject !== "all") {
 query = query.eq("project_id", selectedProject);
 }

 // Apply status filter
 if (!showCompleted) {
 query = query.neq("status", "done");
 }

 // Order by position and creation date
 query = query.order("position", { ascending: true })
 .order("created_at", { ascending: false });

 const { data, error } = await query;

 if (error) throw error;

 setTasks(data || []);
 } catch (error) {
 console.error("Error loading tasks:", error);
 toast.error("Failed to load tasks");
 } finally {
 setLoading(false);
 }
 }, [supabase, orgId, projectId, selectedProject, showCompleted]);

 // Load tasks on mount and when filters change
 useEffect(() => {
 loadTasks();
 }, [loadTasks]);

 // Real-time subscriptions
 useEffect(() => {
 const channel = supabase
 .channel("tasks-changes")
 .on(
 "postgres_changes",
 {
 event: "*",
 schema: "public",
 table: "project_tasks",
 filter: `organization_id=eq.${orgId}`
 },
 () => {
 loadTasks();
 }
 )
 .subscribe();

 return () => {
 supabase.removeChannel(channel);
 };
 }, [supabase, orgId, loadTasks]);

 // Filter tasks
 const filteredTasks = useMemo(() => {
 return tasks.filter(task => {
 // Don't show subtasks at root level if showSubtasks is false
 if (!showSubtasks && task.parent_task_id) {
 return false;
 }

 // Search filter
 if (searchQuery) {
 const query = searchQuery.toLowerCase();
 const matchesSearch = 
 task.title.toLowerCase().includes(query) ||
 task.description?.toLowerCase().includes(query) ||
 task.tags?.some(tag => tag.toLowerCase().includes(query));
 if (!matchesSearch) return false;
 }

 // Status filter
 if (selectedStatus !== "all" && task.status !== selectedStatus) {
 return false;
 }

 // Priority filter
 if (selectedPriority !== "all" && task.priority !== selectedPriority) {
 return false;
 }

 // Assignee filter
 if (selectedAssignee !== "all") {
 if (selectedAssignee === "unassigned" && task.assignee_id) return false;
 if (selectedAssignee !== "unassigned" && task.assignee_id !== selectedAssignee) return false;
 }

 // Tag filter
 if (selectedTags.length > 0) {
 if (!task.tags || !selectedTags.every(tag => task.tags!.includes(tag))) {
 return false;
 }
 }

 return true;
 });
 }, [tasks, searchQuery, selectedStatus, selectedPriority, selectedAssignee, selectedTags, showSubtasks]);

 // Get all unique tags
 const allTags = useMemo(() => {
 const tags = new Set<string>();
 tasks.forEach(task => {
 task.tags?.forEach(tag => tags.add(tag));
 });
 return Array.from(tags).sort();
 }, [tasks]);

 // Calculate statistics
 const statistics = useMemo(() => {
 const stats = {
 total: filteredTasks.length,
 todo: 0,
 inProgress: 0,
 review: 0,
 done: 0,
 blocked: 0,
 overdue: 0,
 dueThisWeek: 0,
 critical: 0,
 high: 0,
 unassigned: 0,
 withSubtasks: 0
 };

 const today = new Date();
 const weekStart = startOfWeek(today);
 const weekEnd = endOfWeek(today);

 filteredTasks.forEach(task => {
 // Status counts
 switch (task.status) {
 case "todo": stats.todo++; break;
 case "in_progress": stats.inProgress++; break;
 case "review": stats.review++; break;
 case "done": stats.done++; break;
 case "blocked": stats.blocked++; break;
 }

 // Priority counts
 if (task.priority === "critical") stats.critical++;
 if (task.priority === "high") stats.high++;

 // Assignment
 if (!task.assignee_id) stats.unassigned++;

 // Subtasks
 if (task.subtasks && task.subtasks.length > 0) stats.withSubtasks++;

 // Due dates
 if (task.due_date) {
 const dueDate = parseISO(task.due_date);
 if (dueDate < today && task.status !== "done") {
 stats.overdue++;
 }
 if (dueDate >= weekStart && dueDate <= weekEnd) {
 stats.dueThisWeek++;
 }
 }
 });

 return stats;
 }, [filteredTasks]);

 // Handle actions
 const handleCreateTask = useCallback(() => {
 setSelectedTask(null);
 setCreateDrawerOpen(true);
 }, []);

 const handleViewTask = useCallback((task: Task) => {
 setSelectedTask(task);
 setViewDrawerOpen(true);
 }, []);

 const handleEditTask = useCallback((task: Task) => {
 setSelectedTask(task);
 setEditDrawerOpen(true);
 }, []);

 const handleDuplicateTask = useCallback(async (task: Task) => {
 try {
 const { data, error } = await supabase
 .from("project_tasks")
 .insert({
 organization_id: task.organization_id,
 project_id: task.project_id,
 title: `${task.title} (Copy)`,
 description: task.description,
 status: "todo",
 priority: task.priority,
 assignee_id: task.assignee_id,
 estimated_hours: task.estimated_hours,
 tags: task.tags,
 position: task.position + 1
 })
 .select()
 .single();

 if (error) throw error;

 toast.success("Task duplicated successfully");
 loadTasks();
 } catch (error) {
 console.error("Error duplicating task:", error);
 toast.error("Failed to duplicate task");
 }
 }, [supabase, loadTasks]);

 const handleDeleteTask = useCallback(async (task: Task) => {
 if (!confirm(`Are you sure you want to delete "${task.title}"?`)) return;

 try {
 const { error } = await supabase
 .from("project_tasks")
 .delete()
 .eq("id", task.id);

 if (error) throw error;

 toast.success("Task deleted successfully");
 loadTasks();
 } catch (error) {
 console.error("Error deleting task:", error);
 toast.error("Failed to delete task");
 }
 }, [supabase, loadTasks]);

 // Bulk actions
 const handleSelectAll = useCallback(() => {
 if (selectedItems.size === filteredTasks.length) {
 setSelectedItems(new Set());
 } else {
 setSelectedItems(new Set(filteredTasks.map(t => t.id)));
 }
 }, [filteredTasks, selectedItems]);

 const handleBulkDelete = useCallback(async () => {
 if (selectedItems.size === 0) return;

 if (!confirm(`Are you sure you want to delete ${selectedItems.size} tasks?`)) return;

 try {
 const { error } = await supabase
 .from("project_tasks")
 .delete()
 .in("id", Array.from(selectedItems));

 if (error) throw error;

 toast.success(`${selectedItems.size} tasks deleted successfully`);
 setSelectedItems(new Set());
 loadTasks();
 } catch (error) {
 console.error("Error deleting tasks:", error);
 toast.error("Failed to delete tasks");
 }
 }, [supabase, selectedItems, loadTasks]);

 const handleBulkExport = useCallback(() => {
 const selectedTasks = filteredTasks.filter(t => selectedItems.has(t.id));
 const dataToExport = selectedItems.size > 0 ? selectedTasks : filteredTasks;

 const csv = convertToCSV(dataToExport);
 downloadCSV(csv, "tasks.csv");
 }, [filteredTasks, selectedItems]);

 // Helper functions for export
 const convertToCSV = (tasks: Task[]) => {
 const headers = ["Title", "Status", "Priority", "Assignee", "Project", "Due Date", "Tags"];
 const rows = tasks.map(task => [
 task.title,
 task.status,
 task.priority,
 task.assignee?.full_name || task.assignee?.email || "Unassigned",
 task.project?.name || "",
 task.due_date ? format(parseISO(task.due_date), "yyyy-MM-dd") : "",
 task.tags?.join(", ") || "",
 ]);
 return [headers, ...rows].map(row => row.join(",")).join("\n");
 };

 const downloadCSV = (csv: string, filename: string) => {
 const blob = new Blob([csv], { type: "text/csv" });
 const url = window.URL.createObjectURL(blob);
 const a = document.createElement("a");
 a.href = url;
 a.download = filename;
 a.click();
 window.URL.revokeObjectURL(url);
 };

 // Get status color
 const getStatusColor = (status: string) => {
 switch (status) {
 case "done": return "text-success";
 case "in_progress": return "text-info";
 case "review": return "text-warning";
 case "blocked": return "text-destructive";
 default: return "text-muted-foreground";
 }
 };

 // Get priority color
 const getPriorityColor = (priority: string) => {
 switch (priority) {
 case "critical": return "text-destructive";
 case "high": return "text-warning";
 case "medium": return "text-info";
 default: return "text-muted-foreground";
 }
 };

 return (
 <div className="space-y-md">
 {/* Header */}
 <Card className="p-lg">
 <div className="flex items-center justify-between mb-md">
 <div>
 <h1 className="text-2xl font-bold">Tasks</h1>
 <p className="text-muted-foreground">
 Manage and track project tasks
 </p>
 </div>
 <Button onClick={handleCreateTask}>
 <Plus className="mr-2 h-icon-xs w-icon-xs" />
 Create Task
 </Button>
 </div>

 {/* Statistics Cards */}
 <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-md mb-md">
 <Card className="p-sm">
 <div className="flex items-center justify-between">
 <div>
 <p className="text-sm text-muted-foreground">Total</p>
 <p className="text-2xl font-bold">{statistics.total}</p>
 </div>
 <ListTodo className="h-icon-lg w-icon-lg text-muted-foreground" />
 </div>
 </Card>
 <Card className="p-sm">
 <div className="flex items-center justify-between">
 <div>
 <p className="text-sm text-muted-foreground">In Progress</p>
 <p className="text-2xl font-bold text-info">{statistics.inProgress}</p>
 </div>
 <Clock className="h-icon-lg w-icon-lg text-info" />
 </div>
 </Card>
 <Card className="p-sm">
 <div className="flex items-center justify-between">
 <div>
 <p className="text-sm text-muted-foreground">Review</p>
 <p className="text-2xl font-bold text-warning">{statistics.review}</p>
 </div>
 <Eye className="h-icon-lg w-icon-lg text-warning" />
