"use client";

import React, { useState } from 'react';
import { Image, Video, File, Eye, Download, MoreHorizontal } from 'lucide-react';
import {
  Badge,
  Button,
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger
} from "@ghxstship/ui";
import { Badge } from '@ghxstship/ui';
import { Card, CardContent } from '@ghxstship/ui';
import { Dropdown,  DropdownItem, DropdownMenuTrigger } from '@ghxstship/ui';

export interface ImageViewProps {
  data: unknown[];
  onItemClick?: (item: unknown) => void;
  onDownload?: (item: unknown) => void;
  onView?: (item: unknown) => void;
  loading?: boolean;
  emptyMessage?: string;
  className?: string;
  layout?: 'grid' | 'masonry';
  imageField?: string;
  titleField?: string;
  subtitleField?: string;
}

export default function ImageView({
  data,
  onItemClick,
  onDownload,
  onView,
  loading = false,
  emptyMessage = "No media available",
  className = "",
  layout = 'grid',
  imageField = 'file_url',
  titleField = 'name',
  subtitleField = 'description'
}: ImageViewProps) {
  const [selectedItem, setSelectedItem] = useState<(null);

  const getFileIcon = (fileType: string) => {
    if (fileType?.startsWith('image/')) return <Image className="h-icon-lg w-icon-lg" />;
    if (fileType?.startsWith('video/')) return <Video className="h-icon-lg w-icon-lg" />;
    return <File className="h-icon-lg w-icon-lg" />;
  };

  const getFileTypeColor = (fileType: string) => {
    if (fileType?.startsWith('image/')) return 'text-green-500';
    if (fileType?.startsWith('video/')) return 'text-blue-500';
    return 'text-gray-500';
  };

  if (loading) {
    return (
      <div className={`${layout === 'grid' ? 'grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-md' : 'columns-2 md:columns-3 lg:columns-4 gap-md'} ${className}`}>
        {Array.from({ length: 12 }).map((_, i) => (
          <div key={i} className="animate-pulse break-inside-avoid mb-4">
            <div className="bg-muted rounded-lg aspect-square mb-2"></div>
            <div className="h-3 bg-muted rounded w-3/4 mb-1"></div>
            <div className="h-2 bg-muted rounded w-1/2"></div>
          </div>
        ))}
      </div>
    );
  }

  if (data.length === 0) {
    return (
      <div className={`flex items-center justify-center p-xl ${className}`}>
        <div className="text-center text-muted-foreground">
          {emptyMessage}
        </div>
      </div>
    );
  }

  return (
    <>
      <div className={`${layout === 'grid' ? 'grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-md' : 'columns-2 md:columns-3 lg:columns-4 gap-md'} ${className}`}>
        {data.map((item) => (
          <Card
            key={item.id}
            className="break-inside-avoid cursor-pointer hover:shadow-md transition-shadow group"
            onClick={() => onItemClick?.(item)}
          >
            <CardContent className="p-sm">
              {/* Media Preview */}
              <div className="aspect-square bg-muted rounded-lg mb-3 flex items-center justify-center overflow-hidden">
                {item[imageField] ? (
                  item.file_type?.startsWith('image/') ? (
                    <img
                      src={item[imageField]}
                      alt={item[titleField]}
                      className="w-full h-full object-cover group-hover:scale-105 transition-transform"
                    />
                  ) : (
                    <div className={`${getFileTypeColor(item.file_type)}`}>
                      {getFileIcon(item.file_type)}
                    </div>
                  )
                ) : (
                  <div className="text-muted-foreground">
                    <File className="h-icon-lg w-icon-lg" />
                  </div>
                )}
              </div>

              {/* Content */}
              <div className="space-y-xs">
                <h3 className="font-medium text-sm line-clamp-xs">
                  {item[titleField] || item.name || 'Untitled'}
                </h3>

                {item[subtitleField] && (
                  <p className="text-xs text-muted-foreground line-clamp-xs">
                    {item[subtitleField]}
                  </p>
                )}

                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-xs">
                    <Badge variant="outline" className="text-xs">
                      {item.category || 'File'}
                    </Badge>
                    {item.file_size && (
                      <span className="text-xs text-muted-foreground">
                        {(item.file_size / 1024 / 1024).toFixed(1)}MB
                      </span>
                    )}
                  </div>

                  <DropdownMenu>
                    <DropdownMenuTrigger asChild>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={(e) => e.stopPropagation()}
                        className="h-icon-md w-icon-md p-0 opacity-60 hover:opacity-100"
                      >
                        <MoreHorizontal className="h-3 w-3" />
                      </Button>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent align="end">
                      {onView && (
                        <DropdownMenuItem onClick={() => onView(item)}>
                          <Eye className="mr-2 h-3 w-3" />
                          View
                        </DropdownMenuItem>
                      )}
                      {onDownload && (
                        <DropdownMenuItem onClick={() => onDownload(item)}>
                          <Download className="mr-2 h-3 w-3" />
                          Download
                        </DropdownMenuItem>
                      )}
                    </DropdownMenuContent>
                  </DropdownMenu>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Lightbox for full-size viewing */}
      {selectedItem && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
          <div className="max-w-4xl max-h-[90vh] p-md">
            {selectedItem[imageField] && selectedItem.file_type?.startsWith('image/') && (
              <img
                src={selectedItem[imageField]}
                alt={selectedItem[titleField]}
                className="max-w-full max-h-full object-contain"
              />
            )}
            <Button
              variant="secondary"
              onClick={() => setSelectedItem(null)}
              className="mt-4"
            >
              Close
            </Button>
          </div>
        </div>
      )}
    </>
  );
}

"use client";

import React from 'react';
import { Clock, CheckCircle, Circle, AlertCircle } from 'lucide-react';
import { Badge } from '@ghxstship/ui';
import { format, isAfter, isBefore, isEqual } from 'date-fns';

export interface TimelineViewProps {
  data: unknown[];
  dateField?: string;
  titleField?: string;
  descriptionField?: string;
  statusField?: string;
  onItemClick?: (item: unknown) => void;
  loading?: boolean;
  emptyMessage?: string;
  className?: string;
  groupBy?: 'month' | 'week' | 'day' | 'none';
}

export default function TimelineView({
  data,
  dateField = 'created_at',
  titleField = 'title',
  descriptionField = 'description',
  statusField = 'status',
  onItemClick,
  loading = false,
  emptyMessage = "No timeline events",
  className = "",
  groupBy = 'month'
}: TimelineViewProps) {
  // Sort data by date
  const sortedData = React.useMemo(() => {
    return [...data].sort((a, b) => {
      const dateA = new Date(a[dateField]).getTime();
      const dateB = new Date(b[dateField]).getTime();
      return dateB - dateA; // Most recent first
    });
  }, [data, dateField]);

  // Group data by time period
  const groupedData = React.useMemo(() => {
    if (groupBy === 'none') return { 'All Events': sortedData };

    const groups: Record<string, any[]> = {};

    sortedData.forEach(item => {
      const date = new Date(item[dateField]);
      let groupKey: string;

      switch (groupBy) {
        case 'month':
          groupKey = format(date, 'MMMM yyyy');
          break;
        case 'week':
          const weekStart = new Date(date);
          weekStart.setDate(date.getDate() - date.getDay());
          groupKey = `Week of ${format(weekStart, 'MMM d, yyyy')}`;
          break;
        case 'day':
          groupKey = format(date, 'MMMM d, yyyy');
          break;
        default:
          groupKey = 'All Events';
      }

      if (!groups[groupKey]) groups[groupKey] = [];
      groups[groupKey].push(item);
    });

    return groups;
  }, [sortedData, groupBy]);

  const getStatusIcon = (status: string) => {
    switch (status?.toLowerCase()) {
      case 'completed':
      case 'done':
        return <CheckCircle className="h-icon-xs w-icon-xs text-green-500" />;
      case 'in_progress':
      case 'active':
        return <Clock className="h-icon-xs w-icon-xs text-blue-500" />;
      case 'pending':
        return <Circle className="h-icon-xs w-icon-xs text-gray-500" />;
      case 'blocked':
      case 'failed':
        return <AlertCircle className="h-icon-xs w-icon-xs text-red-500" />;
      default:
        return <Circle className="h-icon-xs w-icon-xs text-gray-400" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status?.toLowerCase()) {
      case 'completed':
      case 'done':
        return 'border-green-500';
      case 'in_progress':
      case 'active':
        return 'border-blue-500';
      case 'pending':
        return 'border-gray-500';
      case 'blocked':
      case 'failed':
        return 'border-red-500';
      default:
        return 'border-gray-400';
    }
  };

  if (loading) {
    return (
      <div className={`space-y-lg ${className}`}>
        {Array.from({ length: 3 }).map((_, i) => (
          <div key={i} className="animate-pulse">
            <div className="h-icon-md bg-muted rounded w-container-xs mb-4"></div>
            <div className="space-y-sm">
              {Array.from({ length: 2 }).map((_, j) => (
                <div key={j} className="flex gap-md">
                  <div className="w-icon-xs h-icon-xs bg-muted rounded-full mt-1"></div>
                  <div className="flex-1 space-y-xs">
                    <div className="h-icon-xs bg-muted rounded w-3/4"></div>
                    <div className="h-3 bg-muted rounded w-1/2"></div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    );
  }

  if (data.length === 0) {
    return (
      <div className={`flex items-center justify-center p-xl ${className}`}>
        <div className="text-center text-muted-foreground">
          {emptyMessage}
        </div>
      </div>
    );
  }

  return (
    <div className={`space-y-xl ${className}`}>
      {Object.entries(groupedData).map(([groupName, items]) => (
        <div key={groupName}>
          {groupBy !== 'none' && (
            <h3 className="text-lg font-semibold mb-4 text-muted-foreground border-b pb-2">
              {groupName}
            </h3>
          )}

          <div className="space-y-md">
            {items.map((item, index) => (
              <div
                key={item.id}
                className="flex gap-md cursor-pointer hover:bg-muted/30 p-sm rounded-lg transition-colors"
                onClick={() => onItemClick?.(item)}
              >
                {/* Timeline connector */}
                <div className="flex flex-col items-center">
                  <div className={`w-icon-xs h-icon-xs rounded-full border-2 ${getStatusColor(item[statusField])} bg-background flex items-center justify-center`}>
                    {getStatusIcon(item[statusField])}
                  </div>
                  {index < items.length - 1 && (
                    <div className="w-px h-icon-lg bg-border mt-2"></div>
                  )}
                </div>

                {/* Content */}
                <div className="flex-1 min-w-0">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <h4 className="font-medium text-sm">
                        {item[titleField] || item.name || item.title}
                      </h4>
                      {item[descriptionField] && (
                        <p className="text-sm text-muted-foreground mt-1 line-clamp-xs">
                          {item[descriptionField]}
                        </p>
                      )}
                    </div>

                    <div className="flex items-center gap-xs ml-4">
                      <Badge variant="outline" className="text-xs">
                        {item[statusField]}
                      </Badge>
                      <span className="text-xs text-muted-foreground">
                        {format(new Date(item[dateField]), 'MMM d, yyyy')}
                      </span>
                    </div>
                  </div>

                  {/* Additional metadata */}
                  <div className="flex items-center gap-md mt-2 text-xs text-muted-foreground">
                    {item.priority && (
                      <span>Priority: {item.priority}</span>
                    )}
                    {item.assignee && (
                      <span>Assignee: {item.assignee.full_name || item.assignee.email}</span>
                    )}
                    {item.due_date && (
                      <span>Due: {format(new Date(item.due_date), 'MMM d, yyyy')}</span>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}

'use client';
import { Activity, Award, Calendar, Clock, Edit, FileText, Play, Plus, Search, Settings, Trash2, TrendingUp, User } from "lucide-react";
import { useEffect, useMemo, useState } from 'react';
import {
 Card,
 Button,
 Badge,
 Loader,
 Textarea,
 Input,
 useToastContext
} from '@ghxstship/ui';
import {
 fetchAutomationRules,
 createAutomationRule,
 updateAutomationRule,
 deleteAutomationRule,
 type AutomationRuleSummary,
 type AutomationAction,
 type AutomationCondition
} from '@/lib/services/settingsAutomationsClient';

interface AutomationFormState {
 name: string;
 description: string;
 triggerType: string;
 triggerConfig: string;
 conditions: string;
 actions: string;
 isActive: boolean;
}

const emptyForm: AutomationFormState = {
 name: '',
 description: '',
 triggerType: '',
 triggerConfig: '{\n "event": ""\n}',
 conditions: '[]',
 actions: '[\n {\n "type": "",\n "config": {},\n "order": 1\n }\n]',
 isActive: true
};

function parseJsonOrThrow<T>(value: string, label: string): T {
 try {
 return JSON.parse(value) as T;
 } catch (error) {
 throw new Error(`Invalid ${label}. Please provide valid JSON.`);
 }
}

function formatJson(value: unknown): string {
 return JSON.stringify(value, null, 2);
}

export default function AutomationsSettingsClient() {
 const { toast } = useToastContext();
 const [automationRules, setAutomationRules] = useState<AutomationRuleSummary[]>([]);
 const [loading, setLoading] = useState(true);
 const [saving, setSaving] = useState(false);
 const [showForm, setShowForm] = useState(false);
 const [editingRuleId, setEditingRuleId] = useState<string | null>(null);
 const [form, setForm] = useState<AutomationFormState>(emptyForm);

 const isEditing = useMemo(() => editingRuleId !== null, [editingRuleId]);

 useEffect(() => {
 void loadAutomationRules();
 }, [toast]);

 const loadAutomationRules = async () => {
 try {
 setLoading(true);
 const rules = await fetchAutomationRules();
 setAutomationRules(rules);
 } catch (error) {
 console.error('Error loading automation rules:', error);
 toast.error('Failed to load automation rules');
 } finally {
 setLoading(false);
 }
 };

 const resetForm = () => {
 setForm(emptyForm);
 setEditingRuleId(null);
 };

 const handleCreateNew = () => {
 resetForm();
 setShowForm(true);
 };

 const handleEditRule = (rule: AutomationRuleSummary) => {
 setForm({
 name: rule.name,
 description: rule.description ?? '',
 triggerType: rule.triggerType,
 triggerConfig: formatJson(rule.triggerConfig),
 conditions: formatJson(rule.conditions ?? []),
 actions: formatJson(rule.actions),
 isActive: rule.isActive
 });
 setEditingRuleId(rule.id);
 setShowForm(true);
 };

 const handleDeleteRule = async (rule: AutomationRuleSummary) => {
 const confirmed = window.confirm(`Delete automation rule "${rule.name}"?`);
 if (!confirmed) return;

 try {
 await deleteAutomationRule(rule.id);
 toast.success('Automation rule deleted');
 void loadAutomationRules();
 } catch (error) {
 console.error('Error deleting automation rule:', error);
 toast.error('Failed to delete automation rule');
 }
 };

 const handleToggleActive = async (rule: AutomationRuleSummary) => {
 try {
 await updateAutomationRule(rule.id, { isActive: !rule.isActive });
 toast.success(`Automation ${rule.isActive ? 'paused' : 'activated'}`);
 void loadAutomationRules();
 } catch (error) {
 console.error('Error toggling automation rule:', error);
 toast.error('Failed to update automation status');
 }
 };

 const handleSubmit = async () => {
 try {
 setSaving(true);

 if (!form.name.trim()) {
 throw new Error('Name is required.');
 }
 if (!form.triggerType.trim()) {
 throw new Error('Trigger type is required.');
 }

 const triggerConfig = parseJsonOrThrow<Record<string, unknown>(form.triggerConfig, 'trigger configuration');
 const actions = parseJsonOrThrow<AutomationAction[]>(form.actions, 'actions');
 const conditions = form.conditions.trim()
 ? parseJsonOrThrow<AutomationCondition[]>(form.conditions, 'conditions')
 : [];

 if (!Array.isArray(actions) || actions.length === 0) {
 throw new Error('At least one action is required.');
 }

 const payload = {
 name: form.name.trim(),
 description: form.description.trim() || undefined,
 triggerType: form.triggerType.trim(),
 triggerConfig,
 conditions,
 actions,
 isActive: form.isActive
 };

 if (isEditing && editingRuleId) {
 await updateAutomationRule(editingRuleId, payload);
 toast.success('Automation rule updated');
 } else {
 await createAutomationRule(payload);
 toast.success('Automation rule created');
 }

 resetForm();
 setShowForm(false);
 void loadAutomationRules();
 } catch (error) {
 console.error('Error saving automation rule:', error);
 toast.error(error instanceof Error ? error.message : 'Failed to save automation rule');
 } finally {
 setSaving(false);
 }
 };

 const renderForm = () => (
 <Card>
 <div className="p-lg space-y-md">
 <div className="flex items-center justify-between">
 <h2 className="text-xl font-semibold">
 {isEditing ? 'Edit Automation Rule' : 'Create Automation Rule'}
 </h2>
 <div className="flex gap-xs">
 <Button variant="outline" onClick={() => { resetForm(); setShowForm(false); }} disabled={saving}>
 Cancel
 </Button>
 <Button onClick={handleSubmit} disabled={saving}>
 {saving ? 'Saving...' : isEditing ? 'Update Rule' : 'Create Rule'}
 </Button>
 </div>
 </div>

 <div className="grid grid-cols-1 md:grid-cols-2 gap-md">
 <div className="space-y-xs">
 <label className="text-sm font-medium">Name</label>
 <Input
 value={form.name}
 onChange={(event) => setForm((prev: unknown) => ({ ...prev, name: event.target.value }))}
 placeholder="Automation name"
 />
 </div>
 <div className="space-y-xs">
 <label className="text-sm font-medium">Trigger Type</label>
 <Input
 value={form.triggerType}
 onChange={(event) => setForm((prev: unknown) => ({ ...prev, triggerType: event.target.value }))}
 placeholder="e.g. lead.created"
 />
 </div>
 </div>

 <div className="space-y-xs">
 <label className="text-sm font-medium">Description</label>
 <Textarea
 value={form.description}
 onChange={(event) => setForm((prev: unknown) => ({ ...prev, description: event.target.value }))}
 placeholder="Optional description"
 rows={2}
 />
 </div>

 <div className="space-y-xs">
 <label className="text-sm font-medium">Trigger Configuration (JSON)</label>
 <Textarea
 value={form.triggerConfig}
 onChange={(event) => setForm((prev: unknown) => ({ ...prev, triggerConfig: event.target.value }))}
 rows={6}
 />
 </div>

 <div className="space-y-xs">
 <label className="text-sm font-medium">Conditions (JSON array)</label>
 <Textarea
 value={form.conditions}
 onChange={(event) => setForm((prev: unknown) => ({ ...prev, conditions: event.target.value }))}
 rows={6}
 placeholder="[]"
 />
 </div>

 <div className="space-y-xs">
 <label className="text-sm font-medium">Actions (JSON array)</label>
 <Textarea
 value={form.actions}
 onChange={(event) => setForm((prev: unknown) => ({ ...prev, actions: event.target.value }))}
 rows={6}
 />
 </div>

 <div className="flex items-center justify-between">
 <div className="space-y-xs">
 <p className="text-sm font-medium">Status</p>
 <p className="text-xs text-muted-foreground">
 Active automations will run automatically when triggers are satisfied.
 </p>
 </div>
 <Button
 variant={form.isActive ? 'success' : 'outline'}
 onClick={() => setForm((prev: unknown) => ({ ...prev, isActive: !prev.isActive }))}
 type="button"
 >
 {form.isActive ? 'Active' : 'Inactive'}
 </Button>
 </div>
 </div>
 </Card>
 );

 const renderRule = (rule: AutomationRuleSummary) => (
 <Card key={rule.id}>
 <div className="p-lg space-y-md">
 <div className="flex flex-wrap items-start justify-between gap-md">
 <div>
 <div className="flex items-center gap-sm">
 <h3 className="text-lg font-semibold">{rule.name}</h3>
 <Badge variant={rule.isActive ? 'success' : 'secondary'}>
 {rule.isActive ? 'Active' : 'Paused'}
 </Badge>
 </div>
 {rule.description && (
 <p className="text-sm text-muted-foreground mt-1">{rule.description}</p>
 )}
 </div>
 <div className="flex gap-xs">
 <Button variant="outline" size="sm" onClick={() => handleToggleActive(rule)}>
 {rule.isActive ? 'Pause' : 'Activate'}
 </Button>
 <Button variant="outline" size="sm" onClick={() => handleEditRule(rule)}>
 Edit
 </Button>
 <Button variant="destructive" size="sm" onClick={() => handleDeleteRule(rule)}>
 Delete
 </Button>
 </div>
 </div>

 <div className="grid grid-cols-1 md:grid-cols-2 gap-md text-sm">
 <div className="space-y-sm">
 <div>
 <p className="font-medium text-muted-foreground">Trigger</p>
 <p className="mt-1">Type: <span className="font-mono">{rule.triggerType}</span></p>
 <pre className="mt-2 bg-muted/50 rounded-md p-sm text-xs overflow-x-auto">
 {formatJson(rule.triggerConfig)}
 </pre>
 </div>

 <div>
 <p className="font-medium text-muted-foreground">Conditions</p>
 {rule.conditions && rule.conditions.length > 0 ? (
 <pre className="mt-2 bg-muted/50 rounded-md p-sm text-xs overflow-x-auto">
 {formatJson(rule.conditions)}
 </pre>
 ) : (
 <p className="mt-1 text-muted-foreground">No additional conditions</p>
 )}
 </div>
 </div>
 <div className="space-y-sm">
 <div>
 <p className="font-medium text-muted-foreground">Actions</p>
 <pre className="mt-2 bg-muted/50 rounded-md p-sm text-xs overflow-x-auto">
 {formatJson(rule.actions)}
 </pre>
 </div>
 <div className="flex items-center gap-md text-xs text-muted-foreground">
 <span>Runs: {rule.runCount ?? 0}</span>
 {rule.lastRunAt && (
 <span>Last run: {new Date(rule.lastRunAt).toLocaleString()}</span>
 )}
 <span>Created: {new Date(rule.createdAt).toLocaleString()}</span>
 {rule.updatedAt && (
 <span>Updated: {new Date(rule.updatedAt).toLocaleString()}</span>
 )}
 </div>
 </div>
 </div>
 </div>
 </Card>
 );

 return (
 <div className="space-y-lg">
 <div className="flex flex-wrap items-center justify-between gap-sm">
 <div>
 <h1 className="text-3xl font-bold">Automations</h1>
 <p className="text-muted-foreground mt-1">
 Build rules that react to platform events and orchestrate downstream actions.
 </p>
 </div>
 <Button onClick={handleCreateNew}>
 Create Automation
 </Button>
 </div>

 {showForm && renderForm()}

 <Card>
 <div className="p-lg">
 {loading ? (
 <div className="flex items-center justify-center h-container-xs">
 <Loader className="h-icon-lg w-icon-lg animate-spin" />
 </div>
 ) : automationRules.length === 0 ? (
 <div className="py-16 text-center space-y-sm">
 <p className="text-lg font-medium">No automation rules yet</p>
 <p className="text-sm text-muted-foreground">
 Automations will appear here once you create them.
 </p>
 <Button variant="outline" onClick={handleCreateNew}>
 Create your first rule
 </Button>
 </div>
 ) : (
 <div className="space-y-md">
 {automationRules.map(renderRule)}
 </div>
 )}
 </div>
 </Card>
 </div>
 );
}

