source:
'use client';

import { Users, UserPlus, Mail, Shield, RefreshCw, Download, Settings } from "lucide-react";
import { useState, useEffect, useCallback, useMemo } from 'react';
import {
  Badge,
  Button,
  Card,
  CardContent,
  DataViewProvider,
  StateManagerProvider,
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
  ViewSwitcher,
  useToastContext
} from "@ghxstship/ui";
import type { 
 TeamRecord, 
 TeamViewConfig, 
 TeamFieldConfig,
 TeamStatistics,
 TeamSearchParams,
 TeamsClientProps,
 ROLE_CONFIGS
} from './types';
import { teamsService } from './lib/teams-service';

// Field configuration for ATLVS DataViews
const fieldConfig: TeamFieldConfig[] = [
 {
 key: 'id',
 label: 'ID',
 type: 'text',
 width: 100,
 sortable: true,
 filterable: false
 },
 {
 key: 'name',
 label: 'Name',
 type: 'text',
 width: 200,
 sortable: true,
 filterable: true,
 required: true
 },
 {
 key: 'email',
 label: 'Email',
 type: 'text',
 width: 250,
 sortable: true,
 filterable: true,
 required: true
 },
 {
 key: 'role',
 label: 'Role',
 type: 'select',
 width: 120,
 sortable: true,
 filterable: true,
 options: [
 { value: 'owner', label: 'Owner' },
 { value: 'admin', label: 'Admin' },
 { value: 'manager', label: 'Manager' },
 { value: 'member', label: 'Member' },
 { value: 'viewer', label: 'Viewer' }
 ]
 },
 {
 key: 'status',
 label: 'Status',
 type: 'select',
 width: 100,
 sortable: true,
 filterable: true,
 options: [
 { value: 'active', label: 'Active' },
 { value: 'inactive', label: 'Inactive' },
 { value: 'suspended', label: 'Suspended' },
 { value: 'pending', label: 'Pending' }
 ]
 },
 {
 key: 'description',
 label: 'Description',
 type: 'textarea',
 width: 300,
 sortable: false,
 filterable: true
 },
 {
 key: 'category',
 label: 'Category',
 type: 'select',
 width: 120,
 sortable: true,
 filterable: true,
 options: [
 { value: 'members', label: 'Members' },
 { value: 'invitations', label: 'Invitations' },
 { value: 'roles', label: 'Roles' },
 { value: 'permissions', label: 'Permissions' }
 ]
 },
 {
 key: 'created_at',
 label: 'Created',
 type: 'date',
 width: 150,
 sortable: true,
 filterable: true
 },
 {
 key: 'updated_at',
 label: 'Updated',
 type: 'date',
 width: 150,
 sortable: true,
 filterable: true
 }
];

export default function TeamsClient({ userId, orgId }: TeamsClientProps) {
 const { toast } = useToastContext();

 // State management
 const [records, setRecords] = useState<TeamRecord[]>([]);
 const [loading, setLoading] = useState(true);
 const [error, setError] = useState<string | null>(null);
 const [selectedRecords, setSelectedRecords] = useState<string[]>([]);
 const [statistics, setStatistics] = useState<TeamStatistics | null>(null);

 // View state
 const [currentView, setCurrentView] = useState<'grid' | 'list'>('grid');
 const [searchParams, setSearchParams] = useState<TeamSearchParams>({});
 const [activeTab, setActiveTab] = useState('overview');

 // Load team records
 const loadRecords = useCallback(async (params?: TeamSearchParams) => {
 try {
 setLoading(true);
 setError(null);
 const data = await teamsService.getTeamRecords(params);
 setRecords(data);
 } catch (err) {
 const message = err instanceof Error ? err.message : 'Failed to load team records';
 setError(message);
 toast.error(message);
 } finally {
 setLoading(false);
 }
 }, [toast]);

 // Load statistics
 const loadStatistics = useCallback(async () => {
 try {
 const stats = await teamsService.getStatistics();
 setStatistics(stats);
 } catch (err) {
 console.error('Failed to load statistics:', err);
 }
 }, []);

 // Initial data load
 useEffect(() => {
 loadRecords();
 loadStatistics();
 }, [loadRecords, loadStatistics, handleExport]);

 // ATLVS DataViews configuration
 const dataViewConfig: TeamViewConfig = useMemo(() => ({
 id: 'teams',
 name: 'Team Management',
 viewType: currentView,
 defaultView: 'grid',
 fields: fieldConfig,
 data: records,
 onSearch: async (query: string) => {
 const params = { ...searchParams, query };
 setSearchParams(params);
 await loadRecords(params);
 },
 onFilter: async (filters: unknown) => {
 const params = { ...searchParams, ...filters };
 setSearchParams(params);
 await loadRecords(params);
 },
 onSort: async (sorts: unknown) => {
 },
 onRefresh: async () => {
 await loadRecords(searchParams);
 await loadStatistics();
 return records;
 },
 onExport: (data: unknown, format: unknown) => {
 handleExport(format);
 }
 }), [currentView, records, searchParams, loadRecords, loadStatistics, handleExport]);

 // Event handlers
 const handleExport = async (format: 'csv' | 'json') => {
 try {
 const blob = await teamsService.exportRecords({
 format,
 includeMetadata: true
 });

 const url = URL.createObjectURL(blob);
 const a = document.createElement('a');
 a.href = url;
 a.download = `team-records.${format}`;
 document.body.appendChild(a);
 a.click();
 document.body.removeChild(a);
 URL.revokeObjectURL(url);

 toast.success(`Records exported as ${format.toUpperCase()}`);
 } catch (err) {
 const message = err instanceof Error ? err.message : 'Failed to export records';
 toast.error(message);
 }
 };

 const handleRefresh = async () => {
 await loadRecords(searchParams);
 await loadStatistics();
 };

 // View switcher component
 const ViewSwitcher = () => (
 <div className="flex items-center gap-xs">
 <Button
 variant={currentView === 'grid' ? 'default' : 'outline'}
 size="sm"
 onClick={() => setCurrentView('grid')}
 >
 Grid
 </Button>
 <Button
 variant={currentView === 'list' ? 'default' : 'outline'}
 size="sm"
 onClick={() => setCurrentView('list')}
 >
 List
 </Button>
 </div>
 );

 // Simple grid view for teams (since we're focusing on architecture completion)
 const renderTeamGrid = () => {
 if (loading) {
 return (
 <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-md">
 {[...Array(6)].map((_, i) => (
 <Card key={i} className="animate-pulse">
 <CardContent className="p-lg">
 <div className="h-icon-xs bg-muted rounded w-3/4 mb-2"></div>
 <div className="h-3 bg-muted rounded w-1/2"></div>
 </CardContent>
 </Card>
 ))}
 </div>
 );
 }

 const memberRecords = records.filter(r => r.type === 'member');
 const inviteRecords = records.filter(r => r.type === 'invite');

 return (
 <div className="space-y-lg">
 {/* Members Section */}
 <div>
 <h3 className="text-lg font-semibold mb-4">Team Members ({memberRecords.length})</h3>
 <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-md">
 {memberRecords.map((record) => (
 <Card key={record.id} className="hover:shadow-md transition-shadow">
 <CardContent className="p-lg">
 <div className="flex items-start justify-between mb-3">
 <div>
 <h4 className="font-medium">{record.name}</h4>
 <p className="text-sm text-muted-foreground">{record.email}</p>
 </div>
 <Badge variant="outline">{record.role}</Badge>
 </div>
 <div className="space-y-xs">
 <div className="flex items-center justify-between text-sm">
 <span>Status:</span>
 <Badge variant={record.status === 'active' ? 'default' : 'secondary'}>
 {record.status}
 </Badge>
 </div>
 <p className="text-xs text-muted-foreground">{record.description}</p>
 </div>
 </CardContent>
 </Card>
 ))}
 </div>
 </div>

 {/* Invitations Section */}
 {inviteRecords.length > 0 && (
 <div>
 <h3 className="text-lg font-semibold mb-4">Pending Invitations ({inviteRecords.length})</h3>
 <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-md">
 {inviteRecords.map((record) => (
 <Card key={record.id} className="hover:shadow-md transition-shadow border-dashed">
 <CardContent className="p-lg">
 <div className="flex items-start justify-between mb-3">
 <div>
 <h4 className="font-medium">{record.email}</h4>
 <p className="text-sm text-muted-foreground">Invited as {record.role}</p>
 </div>
 <Badge variant="outline">{record.status}</Badge>
 </div>
 <p className="text-xs text-muted-foreground">{record.description}</p>
 </CardContent>
 </Card>
 ))}
 </div>
 </div>
 )}
 </div>
 );
 };

 if (error) {
 return (
 <div className="flex items-center justify-center h-container-lg">
 <div className="text-center">
 <p className="text-destructive mb-4">{error}</p>
 <Button onClick={() => loadRecords()}>
 <RefreshCw className="h-icon-xs w-icon-xs mr-2" />
 Retry
 </Button>
 </div>
 </div>
 );
 }

 return (
 <DataViewProvider config={dataViewConfig}>
 <StateManagerProvider>
 <div className="space-y-lg">
 {/* Header */}
 <div className="flex items-center justify-between">
 <div>
 <h1 className="text-3xl font-bold">Team Management</h1>
 <p className="text-muted-foreground mt-1">
 Manage team members, invitations, and roles
 </p>
 </div>
 <div className="flex items-center gap-sm">
 <Button variant="outline" onClick={handleRefresh} disabled={loading}>
 <RefreshCw className="h-icon-xs w-icon-xs mr-2" />
 Refresh
 </Button>
 <Button variant="outline" onClick={() => handleExport('csv')}>
 <Download className="h-icon-xs w-icon-xs mr-2" />
 Export
 </Button>
 <Button>
 <UserPlus className="h-icon-xs w-icon-xs mr-2" />
 Invite Member
 </Button>
 </div>
 </div>

 {/* Statistics */}
 {statistics && (
 <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-md">
 <div className="bg-card p-md rounded-lg border">
 <div className="flex items-center gap-xs mb-2">
 <Users className="h-icon-sm w-icon-sm text-blue-600" />
 <div className="text-sm font-medium">Total Members</div>
 </div>
 <div className="text-2xl font-bold">{statistics.totalMembers}</div>
 <div className="text-xs text-muted-foreground">
 {statistics.activeMembers} active
 </div>
 </div>
 <div className="bg-card p-md rounded-lg border">
 <div className="flex items-center gap-xs mb-2">
 <Mail className="h-icon-sm w-icon-sm text-green-600" />
 <div className="text-sm font-medium">Pending Invites</div>
 </div>
 <div className="text-2xl font-bold">{statistics.pendingInvites}</div>
 <div className="text-xs text-muted-foreground">
 awaiting response
 </div>
 </div>
 <div className="bg-card p-md rounded-lg border">
 <div className="flex items-center gap-xs mb-2">
 <UserPlus className="h-icon-sm w-icon-sm text-purple-600" />
 <div className="text-sm font-medium">Recent Joins</div>
 </div>
 <div className="text-2xl font-bold">{statistics.recentJoins}</div>
 <div className="text-xs text-muted-foreground">
 last 30 days
 </div>
 </div>
 <div className="bg-card p-md rounded-lg border">
 <div className="flex items-center gap-xs mb-2">
 <Shield className="h-icon-sm w-icon-sm text-orange-600" />
 <div className="text-sm font-medium">Avg Response</div>
 </div>
 <div className="text-2xl font-bold">{statistics.averageResponseTime}h</div>
 <div className="text-xs text-muted-foreground">
 invite acceptance
 </div>
 </div>
 </div>
 )}

 {/* Tab Navigation */}
 <Tabs value={activeTab} onValueChange={setActiveTab}>
 <TabsList className="grid w-full grid-cols-4">
 <TabsTrigger value="overview">
 <Users className="h-icon-xs w-icon-xs mr-2" />
 Overview
 </TabsTrigger>
 <TabsTrigger value="members">
 <Users className="h-icon-xs w-icon-xs mr-2" />
 Members
 </TabsTrigger>
 <TabsTrigger value="invitations">
 <Mail className="h-icon-xs w-icon-xs mr-2" />
 Invitations
 </TabsTrigger>
 <TabsTrigger value="settings">
 <Settings className="h-icon-xs w-icon-xs mr-2" />
 Settings
 </TabsTrigger>
 </TabsList>

 <TabsContent value="overview" className="space-y-md">
 {/* View Controls */}
 <div className="flex items-center justify-between">
 <ViewSwitcher />
 <div className="flex items-center gap-xs">
 {selectedRecords.length > 0 && (
 <Button variant="destructive" size="sm">
 Delete ({selectedRecords.length})
 </Button>
 )}
 </div>
 </div>

 {/* Main Content */}
 {renderTeamGrid()}
 </TabsContent>

 <TabsContent value="members" className="space-y-md">
 <div className="bg-card p-xl rounded-lg border text-center">
 <Users className="h-icon-2xl w-icon-2xl mx-auto mb-4 text-muted-foreground" />
 <h3 className="text-lg font-semibold mb-2">Team Members</h3>
 <p className="text-muted-foreground mb-4">
 Member management is available through the overview interface above.
 </p>
 <Button onClick={() => setActiveTab('overview')}>
 Go to Overview
 </Button>
 </div>
 </TabsContent>

 <TabsContent value="invitations" className="space-y-md">
 <div className="bg-card p-xl rounded-lg border text-center">
 <Mail className="h-icon-2xl w-icon-2xl mx-auto mb-4 text-muted-foreground" />
 <h3 className="text-lg font-semibold mb-2">Team Invitations</h3>
 <p className="text-muted-foreground mb-4">
 Invitation management is available through the overview interface above.
 </p>
 <Button onClick={() => setActiveTab('overview')}>
 Go to Overview
 </Button>
 </div>
 </TabsContent>

 <TabsContent value="settings" className="space-y-md">
 <div className="bg-card p-xl rounded-lg border text-center">
 <Settings className="h-icon-2xl w-icon-2xl mx-auto mb-4 text-muted-foreground" />
 <h3 className="text-lg font-semibold mb-2">Team Settings</h3>
 <p className="text-muted-foreground mb-4">
 Team settings configuration will be available in a future update.
 </p>
 <Button onClick={() => setActiveTab('overview')}>
 Go to Overview
 </Button>
 </div>
 </TabsContent>
 </Tabs>
 </div>
 </StateManagerProvider>
 </DataViewProvider>
 );
}

'use client';
import { User, FileText, Settings, Award, Calendar, TrendingUp, Activity, Clock, Plus, Search, Play, Trash2 } from "lucide-react";
import { useEffect, useMemo, useState } from 'react';
import {
 Badge,
 Button,
 Card,
 CardContent,
 CardHeader,
 CardTitle,
 Input,
 Label,
 Modal,
 Select,
 SelectContent,
 SelectItem,
 SelectTrigger,
 SelectValue,
 Skeleton,
 Textarea,
 useToastContext
} from '@ghxstship/ui';
import {
 addExistingMember,
 bulkInviteMembers,
 fetchTeamsSettings,
 InviteInput,
 InviteRecord,
 inviteMember,
 resendInvite,
 revokeInvite,
 SeatUsage,
 TeamsSettingsResponse
} from '@/lib/services/settingsTeamsClient';

const ROLE_OPTIONS = [
 { value: 'viewer', label: 'Viewer' },
 { value: 'contributor', label: 'Contributor' },
 { value: 'manager', label: 'Manager' },
 { value: 'admin', label: 'Admin' },
 ] as const;

const EMAIL_REGEX = /.+@.+\..+/i;

type RoleValue = (typeof ROLE_OPTIONS)[number]['value'];

function parseEmailList(text: string): string[] {
 return Array.from(
 new Set(
 text
 .split(/[\s,;]+/)
 .map((s) => s.trim())
 .filter((s) => EMAIL_REGEX.test(s))
 )
 );
}

function extractDomain(email: string): string | null {
 const match = email.toLowerCase().match(/@([a-z0-9.-]+\.[a-z]{2})$/i);
 return match ? match[1] : null;
}

export default function TeamsSettingsClient() {
 const { toast } = useToastContext();
 const [data, setData] = useState<TeamsSettingsResponse | null>(null);
 const [loading, setLoading] = useState<boolean>(true);
 const [saving, setSaving] = useState<boolean>(false);
 const [email, setEmail] = useState<string>('');
 const [role, setRole] = useState<RoleValue>('contributor');
 const [bulkText, setBulkText] = useState<string>('');
 const [bulkRole, setBulkRole] = useState<RoleValue>('contributor');
 const [bulkModalOpen, setBulkModalOpen] = useState<boolean>(false);

 const load = async () => {
 try {
 setLoading(true);
 const response = await fetchTeamsSettings();
 setData(response);
 } catch (error) {
 const message = error instanceof Error ? error.message : 'Unable to load teams settings';
 toast.error('Failed to load teams', undefined, { description: message });
 } finally {
 setLoading(false);
 }
 };

 useEffect(() => {
 void load();
 }, []);

 const emailDomain = useMemo(() => extractDomain(email), [email]);
 const domainRecord = useMemo(() => {
 if (!emailDomain || !data?.domains) return null;
 return data.domains.find((d) => d.domain === emailDomain);
 }, [emailDomain, data?.domains]);

 const bulkEmails = useMemo(() => parseEmailList(bulkText), [bulkText]);

 const handleInvite = async () => {
 if (!email || !EMAIL_REGEX.test(email)) return;
 try {
 setSaving(true);
 await inviteMember({ email, role });
 toast.success('Invite sent');
 setEmail('');
 await load();
 } catch (error) {
 const message = error instanceof Error ? error.message : 'Unable to send invite';
 toast.error('Invite failed', undefined, { description: message });
 } finally {
 setSaving(false);
 }
 };

 const handleAddExisting = async () => {
 if (!email || !EMAIL_REGEX.test(email)) return;
 try {
 setSaving(true);
 await addExistingMember({ email, role });
 toast.success('Member added');
 setEmail('');
 await load();
 } catch (error) {
 const message = error instanceof Error ? error.message : 'Unable to add member';
 toast.error('Add failed', undefined, { description: message });
 } finally {
 setSaving(false);
 }
 };

 const handleBulkInvite = async () => {
 if (bulkEmails.length === 0) return;
 try {
 setSaving(true);
 const result = await bulkInviteMembers({ emails: bulkEmails, role: bulkRole });
 toast.success(`Bulk invite complete: ${result.results.successes} sent, ${result.results.failures} failed`);
 setBulkText('');
 setBulkModalOpen(false);
 await load();
 } catch (error) {
 const message = error instanceof Error ? error.message : 'Unable to send bulk invites';
 toast.error('Bulk invite failed', undefined, { description: message });
 } finally {
 setSaving(false);
 }
 };

 const handleResend = async (inviteId: string) => {
 try {
 setSaving(true);
 await resendInvite(inviteId);
 toast.success('Invite resent');
 await load();
 } catch (error) {
 const message = error instanceof Error ? error.message : 'Unable to resend invite';
 toast.error('Resend failed', undefined, { description: message });
 } finally {
 setSaving(false);
 }
 };

 const handleRevoke = async (inviteId: string) => {
 try {
 setSaving(true);
 await revokeInvite(inviteId);
 toast.success('Invite revoked');
 await load();
 } catch (error) {
 const message = error instanceof Error ? error.message : 'Unable to revoke invite';
 toast.error('Revoke failed', undefined, { description: message });
 } finally {
 setSaving(false);
 }
 };

 const renderSeatUsage = (usage: SeatUsage) => {
 if (usage.seatPolicy === 'domain-unlimited') {
 return (
 <div className="flex items-center justify-between">
 <div className="space-y-xs">
 <p className="text-body-sm font-medium">Domain Unlimited</p>
 <p className="text-body-xs text-muted-foreground">
 Members with verified organization domains don&rsquo;t count toward seat limits.
 </p>
 </div>
 <Badge variant="outline">{usage.activeCount} active</Badge>
 </div>
 );
 }

 return (
 <div className="flex items-center justify-between">
 <div className="space-y-xs">
 <p className="text-body-sm font-medium">Per-User Seats</p>
 <p className="text-body-xs text-muted-foreground">
 Each member counts toward your organization&rsquo;s seat limit.
 </p>
 </div>
 <div className="text-right">
 <p className="text-body-sm font-medium">
 {usage.activeCount}
 {usage.seatsLimit != null ? ` / ${usage.seatsLimit}` : ''}
 </p>
 {usage.remainingSeats != null ? (
 <p className="text-body-xs text-muted-foreground">
 {usage.remainingSeats} remaining
 </p>
 ) : null}
 </div>
 </div>
 );
 };

 const renderInviteRow = (invite: InviteRecord) => {
 const statusVariant = 
 invite.status === 'pending' ? 'secondary' :
 invite.status === 'accepted' ? 'default' :
 invite.status === 'revoked' ? 'outline' : 'outline';

 return (
 <tr key={invite.id} className="border-t">
 <td className="px-md py-sm">{invite.email}</td>
 <td className="px-md py-sm capitalize">{invite.role}</td>
 <td className="px-md py-sm">
 <Badge variant={statusVariant}>{invite.status}</Badge>
 </td>
 <td className="px-md py-sm text-right">
 <div className="flex justify-end gap-xs">
 {invite.status === 'pending' && data?.canManage ? (
 <>
 <Button
 variant="outline"
 size="sm"
 onClick={() => handleResend(invite.id)}
 disabled={saving}
 >
 Resend
 </Button>
 <Button
 variant="outline"
 size="sm"
 onClick={() => handleRevoke(invite.id)}
 disabled={saving}
 >
 Revoke
 </Button>
 </>
 ) : null}
 </div>
 </td>
 </tr>
 );
 };

 if (loading) {
 return (
 <div className="space-y-md">
 <Skeleton className="h-icon-2xl w-container-xs" />
 <div className="space-y-sm">
 <Skeleton className="h-component-xl w-full" />
 <Skeleton className="h-40 w-full" />
 </div>
 </div>
 );
 }

 if (!data) {
 return (
 <Card>
 <CardContent className="py-lg text-center text-body-sm text-muted-foreground">
 Unable to load teams data. Please try refreshing the page.
 </CardContent>
 </Card>
 );
 }

 return (
 <div className="space-y-lg">
 <Card>
 <CardHeader className="flex flex-col gap-sm md:flex-row md:items-center md:justify-between">
 <div>
 <CardTitle>Team Management</CardTitle>
 <p className="text-body-sm text-muted-foreground">
 Invite teammates, manage roles, and monitor seat usage across your organization.
 </p>
 </div>
 <div className="flex gap-sm">
 <Button variant="outline" onClick={() => setBulkModalOpen(true)} disabled={!data.canManage}>
 Bulk Invite
 </Button>
 <Button variant="outline" onClick={() => void load()}>
 Refresh
 </Button>
 </div>
 </CardHeader>
 <CardContent>
 {renderSeatUsage(data.seatUsage)}
 </CardContent>
 </Card>

 {data.canManage ? (
 <Card>
 <CardHeader>
 <CardTitle>Invite New Member</CardTitle>
 </CardHeader>
 <CardContent className="space-y-md">
 <div className="grid gap-md md:grid-cols-3">
 <div className="space-y-sm md:col-span-2">
 <Label htmlFor="invite-email">Email Address</Label>
 <Input

 type="email"
 value={email}
 onChange={(event) => setEmail(event.target.value)}
 placeholder="teammate@company.com"
 />
 {emailDomain && domainRecord ? (
 <p className="text-body-xs text-muted-foreground">
 {domainRecord.status === 'active' ? (
 <span className="text-green-600"> Domain matches active organization domain</span>
 ) : (
 <span className="text-yellow-600"> Domain is pending verification</span>
 )}
 </p>
 ) : emailDomain ? (
 <p className="text-body-xs text-muted-foreground">
 External domain  will count against seat limit if enforced.
 </p>
 ) : null}
 </div>
 <div className="space-y-sm">
 <Label htmlFor="invite-role">Role</Label>
 <Select value={role} onChange={(e) => setRole(e.target.value as RoleValue)}>
 <SelectTrigger >
 <SelectValue />
 </SelectTrigger>
 <SelectContent>
 {ROLE_OPTIONS.map((option) => (
 <SelectItem key={option.value} value={option.value}>
 {option.label}
 </SelectItem>
 ))}
 </SelectContent>
 </Select>
 </div>
 </div>
 <div className="flex gap-sm">
 <Button
 onClick={handleInvite}
 disabled={saving || !EMAIL_REGEX.test(email)}
 >
 Send Invite
 </Button>
 <Button
 variant="outline"
 onClick={handleAddExisting}
 disabled={saving || !EMAIL_REGEX.test(email)}
 >
 Add Existing User
 </Button>
 </div>
 </CardContent>
 </Card>
 ) : (
 <Card>
 <CardContent className="py-lg text-center text-body-sm text-muted-foreground">
 Only organization owners and admins can invite new members.
 </CardContent>
 </Card>
 )}

 <Card>
 <CardHeader>
 <CardTitle>Pending Invitations</CardTitle>
 </CardHeader>
 <CardContent>
 <div className="overflow-x-auto">
 <table className="min-w-full text-left text-body-sm">
 <thead>
 <tr className="border-b">
 <th className="px-md py-sm font-medium">Email</th>
 <th className="px-md py-sm font-medium">Role</th>
 <th className="px-md py-sm font-medium">Status</th>
 <th className="px-md py-sm font-medium text-right">Actions</th>
 </tr>
 </thead>
 <tbody>
 {data.invites.map(renderInviteRow)}
 {data.invites.length === 0 ? (
 <tr>
 <td colSpan={4} className="px-md py-lg text-center text-muted-foreground">
 No pending invitations.
 </td>
 </tr>
 ) : null}
 </tbody>
 </table>
 </div>
 </CardContent>
 </Card>

 <Modal
 open={bulkModalOpen}
 onClose={() => setBulkModalOpen(false)}
 title="Bulk Invite Members"
 description="Paste a list of email addresses to invite multiple members at once."
 >
 <div className="space-y-md">
 <div className="space-y-sm">
 <Label htmlFor="bulk-emails">Email Addresses</Label>
 <Textarea

 rows={6}
 value={bulkText}
 onChange={(event) => setBulkText(event.target.value)}
 placeholder="user1@company.com, user2@company.com&#10;user3@other.com&#10;user4@example.org"
 />
 <p className="text-body-xs text-muted-foreground">
 Detected: {bulkEmails.length} valid email{bulkEmails.length !== 1 ? 's' : ''}
 </p>
 </div>
 <div className="space-y-sm">
 <Label htmlFor="bulk-role">Role for All</Label>
 <Select value={bulkRole} onChange={(e) => setBulkRole(e.target.value as RoleValue)}>
 <SelectTrigger >
 <SelectValue />
 </SelectTrigger>
 <SelectContent>
 {ROLE_OPTIONS.map((option) => (
 <SelectItem key={option.value} value={option.value}>
 {option.label}
 </SelectItem>
 ))}
 </SelectContent>
 </Select>
 </div>
 <div className="flex justify-end gap-sm">
 <Button variant="outline" onClick={() => setBulkModalOpen(false)} disabled={saving}>
 Cancel
 </Button>
 <Button onClick={handleBulkInvite} disabled={saving || bulkEmails.length === 0}>
 {saving ? 'Sending' : `Invite ${bulkEmails.length} Member${bulkEmails.length !== 1 ? 's' : ''}`}
 </Button>
 </div>
 </div>
 </Modal>
 </div>
 );
}
